  [3100] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $n. Es scheint sich um einen versiegelten Brief mit den Insignien von Llane, unserem örtlichen Kriegerlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den einfachen Brief und sprecht mit Llane Beshere in der Abtei von Nordhain.",
    ["T"] = "Einfacher Brief",
  },
  [3101] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $n. Es scheint sich um einen versiegelten Brief mit den Insignien von Bruder Sammuel, unserem örtlichen Paladinlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den geweihten Brief und sprecht mit Bruder Sammuel in der Abtei von Nordhain.",
    ["T"] = "Geweihter Brief",
  },
  [3102] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $n. Es scheint sich um einen versiegelten Brief mit den Insignien von Jorik, einem unserer örtlichen Ausbilder, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den verschlüsselten Brief und sprecht mit Jorik Kerridan im Stall hinter der Abtei von Nordhain.",
    ["T"] = "Verschlüsselter Brief",
  },
  [3103] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $n. Es scheint sich um einen versiegelten Brief mit den Insignien von Priesterin Anetta, unserer örtlichen Priesterlehrerin, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den geheiligten Brief und sprecht mit Priesterin Anetta in der Abtei von Nordhain.",
    ["T"] = "Geheiligter Brief",
  },
  [3104] = {
    ["D"] = "Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, $n. Es scheint sich um einen versiegelten Brief mit den Insignien von Khelden, unserem örtlichen Magierlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.",
    ["O"] = "Lest den mit Glyphen versehenen Brief und sprecht mit Khelden Bremen in der Abtei von Nordhain.",
