  [7925] = {
    ["D"] = "You have proven your value to our efforts in Warsong Gulch. Continue to rage against the Silverwing Sentinels! Bring me more marks of honor!",
    ["O"] = "Du hast ein Warsong-Ehrenabzeichen für deine letzte Aufgabe erhalten. Sprich noch einmal mit mir, und du bekommst deine Belohnung.",
    ["T"] = "Ehrenabzeichen",
  },
  [7926] = {
    ["D"] = "Der Dunkelmond-Jahrmarkt kommt! Nehmt an spannenden Spielen teil, gewinnt fabulöse Preise, lasst Euch Eure Zukunft deuten!$B$BZögert nicht und Ihr werdet diesen Gutschein gegen kostenfreie Gewinnlose und einen der weltberühmten Abenteuerführer des Dunkelmond-Jahrmarkts einlösen können! Mit ihm werdet Ihr die Schätze, die Ihr auf Euren Reisen findet, leicht identifizieren können.$B$BBringt Euren Gutschein einfach zu Gelvas Rußgatter, der sich innerhalb des Jahrmarkts aufhält.$B$BMomentan befindet sich der Zugang zum Jahrmarkt außerhalb von Donnerfels in Mulgore.",
    ["O"] = "Bringt den Gutschein zu Gelvas Rußgatter, der sich innerhalb des Dunkelmond-Jahrmarkts aufhält.",
    ["T"] = "Der Dunkelmond-Jahrmarkt",
  },
  [7927] = {
    ["D"] = "Jetzt, da Ihr alle Karten des Portalkartensets in einem Set gesammelt habt, erscheint auf geheimnisvolle Weise eine neunte Karte, die dieselbe Rückseite hat, wie die ersten acht. Auf dieser Karte steht: \"Bringt diese Karten zum Dunkelmond-Jahrmarkt und Ihr werdet belohnt werden.\" Ihr habt gehört, dass der Zugang zu diesem Jahrmarkt jeden Monat in der Nähe von Mulgore oder Goldhain erscheint.",
    ["O"] = "Gebt das Portalkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist.",
    ["T"] = "Portalkartenset des Dunkelmond-Jahrmarkts",
  },
  [7928] = {
    ["D"] = "Jetzt, da Ihr alle Karten des Kriegsfürstenkartensets in einem Set gesammelt habt, erscheint auf geheimnisvolle Weise eine neunte Karte, die dieselbe Rückseite hat, wie die ersten acht. Auf dieser Karte steht: \"Bringt diese Karten zum Dunkelmond-Jahrmarkt und Ihr werdet belohnt werden.\" Ihr habt gehört, dass der Zugang zu diesem Jahrmarkt jeden Monat in der Nähe von Mulgore oder Goldhain erscheint.",
    ["O"] = "Gebt das Kriegsfürstenkartenset auf dem Dunkelmond-Jahrmarkt ab, sobald dieser wieder in der Stadt ist.",
