  [8860] = {
    ["D"] = "In Sturmwind wird das neue Jahr ganz groß gefeiert! Den ganzen Tag lang gibt es kostenlosen Hochprozentigen und nachts ein fantastisches Feuerwerk! Das wird sicher ein Riesenspaß... allerdings findet heute auch in Beutebucht eine Neujahrsfeier statt. Ich wette, dort ist sogar noch mehr los!$B$BEgal! Jedenfalls braucht Gastwirtin Allison in Sturmwind eine Ladung Kokelwälder Festtagsspeisen, um alle Partylöwen satt in das neue Jahr zu bringen. Würdet Ihr mir den Gefallen tun und die Vorräte für mich abliefern?$B$BDanke, $n! Ihr findet Gastwirtin Allison in der Güldenen Rose, drüben im Handelsdistrikt von Sturmwind.",
    ["O"] = "Bringt die Vorräte von Kokelwälder zu Gastwirtin Allison in Stormwind.",
    ["T"] = "Neujahrsfeier!",
  },
  [8861] = {
    ["D"] = "In Donnerfels wird das neue Jahr ganz groß gefeiert! Den ganzen Tag lang gibt es kostenlosen Hochprozentigen und nachts ein fantastisches Feuerwerk!$B$BDas wird sicher ein Riesenspaß... allerdings findet auch in Beutebucht eine Neujahrsfeier statt. Ich wette, dort ist sogar noch mehr los!$B$BEgal! Jedenfalls braucht Gastwirtin Pala eine Ladung Kokelwälder Festtagsspeisen, um alle Partylöwen satt in das neue Jahr zu bringen. Würdet Ihr mir den Gefallen tun und die Vorräte für mich abliefern?$B$BDanke, $n! Ihr findet Gastwirtin Pala in der Taverne nahe der Brücke zur Anhöhe der Jäger.",
    ["O"] = "Bringt die Vorräte von Kokelwälder zu Gastwirtin Pala in Donnerfels.",
    ["T"] = "Neujahrsfeier!",
  },
  [8866] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Urahne Bronzebart",
  },
  [8867] = {
    ["D"] = "Die Druiden der Mondlichtung feiern jedes Jahr den großen Triumph unserer Stadt über ein uraltes Übel.$B$BWir ehren die Weisheit unserer Urahnen, nehmen an herrlichen Festgelagen teil und... schießen natürlich Feuerwerk ab!$B$BFür eine kleine Spende könnt Ihr bei unserem Verkäufer farbenfrohe Raketen erwerben. Versucht doch mal einen der hier herumstehenden Zünder, $n.",
    ["O"] = "Zündet 8 Raketen und 2 Raketenbündel und kehrt dann zu einer Botin des Mondfests in irgendeiner Hauptstadt zurück.",
    ["T"] = "Mondfeuerwerk",
  },
  [8869] = "_",
  [8871] = {
    ["D"] = "Es ist mal wieder soweit, $n! Der Zirkel des Cenarius lädt alle Bewohner Azeroths dazu ein, gemeinsam mit uns, das Mondfest zu feiern. Der Ort, an dem Ihr die örtliche Botin des Mondfests finden könnt, wird durch einen Kreis aus heiligem Mondlicht markiert. Sie vermag Euch ausführlicher über dieses Fest zu berichten.",
    ["O"] = "Sprecht mit der Botin des Mondfests in Stormwind.",
    ["T"] = "Das Mondfest",
  },
  [8874] = {
    ["D"] = "Es ist mal wieder soweit, $n! Der Zirkel des Cenarius lädt alle Bewohner Azeroths dazu ein, gemeinsam mit uns, das Mondfest zu feiern. Der Ort, an dem Ihr die örtliche Botin des Mondfests finden könnt, wird durch einen Kreis aus heiligem Mondlicht markiert. Sie vermag Euch ausführlicher über dieses Fest zu berichten.",
    ["O"] = "Sprecht mit der Botin des Mondfests am Eingang zur Unterstadt. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen.",
    ["T"] = "Das Mondfest",
  },
  [8875] = {
    ["D"] = "Es ist mal wieder soweit, $n! Der Zirkel des Cenarius lädt alle Bewohner Azeroths dazu ein, gemeinsam mit uns, das Mondfest zu feiern. Der Ort, an dem Ihr die örtliche Botin des Mondfests finden könnt, wird durch einen Kreis aus heiligem Mondlicht markiert. Sie vermag Euch ausführlicher über dieses Fest zu berichten.",
    ["O"] = "Sprecht mit der Botin des Mondfests auf der Anhöhe der Ältesten in Donnerfels. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen.",
    ["T"] = "Das Mondfest",
  },
  [8897] = {
    ["D"] = "Wenn ich das Tor bewache muss ich ständig an meine geliebte Colara denken. Ich kann sie immer nur für ein paar kurze Stunden sehen, wenn ich Glück habe. Ach, seht mich doch an. Ich erhalte nur einen niederen Offizierssold und jeden Tag könnte ich an die Front abberufen werden.$B$BWürde sie mich dann einfach vergessen? Ich meine, es gibt so viele wohlhabende Männer in der Stadt.$B$BHier, ich habe einige Dinge aufgeschrieben... Nicht viel, aber vielleicht könntet Ihr diese Nachricht meiner Liebsten überbringen. Mein Dank wäre Euch gewiss.",
    ["O"] = "Bringt Leutnant Heldrics sorgfältig verfasste Notiz zu Colara Dean außerhalb der Bank von Stormwind.",
    ["T"] = "Liebste Colara",
  },
  [8898] = {
    ["D"] = "Als ich geschäftlich mit der Bank in Sturmwind zu tun hatte, traf ich ein Mädchen. Sie hat mir auf Anhieb mein Herz gestohlen. Ich kann einfach nicht mehr aufhören, an sie zu denken! Ich würde Ihr gerne diese Nachricht schicken. Ganze Zwölfmal musste ich sie neu schreiben!$B$BJetzt möchte ich sicherstellen, dass sie meinen Brief auch erhält.$B$BVielleicht reist Ihr ja nach Sturmwind und könnt mir weiterhelfen?",
    ["O"] = "Bringt den zerknitterten Brief zu Colara Dean außerhalb der Bank in Stormwind.",
    ["T"] = "Liebste Colara",
  },
  [8899] = {
    ["D"] = "Wie soll ich ihre Schönheit nur beschreiben? Tausende Jahre voll der Poesie und mir fehlen die Worte.$B$BDiese Sterblichen verwässern den Geist und am Ende bleiben wir als plappernde Narren zurück.$B$BZu lange habe ich diese Worte immer und immer wieder in mich aufgenommen. Es ist mir unmöglich, sie weiter zu verfeinern. Nein, sie muss diese Nachricht noch heute erhalten, oder ein anderer Verehrer wird sie für sich einnehmen.",
    ["O"] = "Bringt den makellosen Brief zu Colara Dean außerhalb der Bank von Stormwind.",
    ["T"] = "Liebste Colara",
  },
  [8900] = {
    ["D"] = "Sie war so traurig. Ich habe es genau gespürt, trotz Ihrer Versuche es zu verbergen. Ich meine, sie wirkte so traurig, versteht Ihr?$B$BDa war diese Verbindung zwischen uns beiden, als ich mit ihr sprach. Niemand hatte mich bisher verstanden, doch sie tat es auf Anhieb.$B$BAls ich es den anderen erzählte, lachten sie nur über mich und so habe ich nie wieder mit ihr gesprochen. Doch ich vermisse sie. Würdet Ihr mir einen Gefallen tun und ihr diesen Brief von mir geben? Ich will sicher sein, dass sie ihn auch erhält.",
    ["O"] = "Bringt die leicht zerknitterte Notiz zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8901] = {
    ["D"] = "Habt Ihr jemals wahre Liebe erfahren, $g junger Herr:junge Dame;? Noch vor Kurzem war dies ein Fremdwort für mich, doch dann traf ich dieses junge Mädchen aus Unterstadt, Elenia. Es ist selten für einen Angehörigen meiner Art, sich mit den anderen Völker einzulassen, ganz besonders mit den Verlassenen.$B$BDoch jetzt sind meine Gedanken Tag und Nacht von ihr erfüllt.$B$BWäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich möchte, dass sie hierher nach Mulgore kommt. Hoffentlich sagt sie ja.",
    ["O"] = "Bringt den sorgfältig verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8902] = {
    ["D"] = "Zu Lebzeiten hatte ich geschworen, Elenia, meine Jugendliebe, zu meiner Frau zu nehmen. Es ist schon Jahre her, seit ich das letzte Mal an sie denken musste. Doch seit einiger Zeit beherrschen mehr und mehr Gedanken an sie meinen Verstand.$B$BObwohl es eine Ewigkeit her ist, sollte es in unser beider Leben noch etwas Raum für unsere Liebe geben.$B$BIch bin sicher, auch sie wird sich verändert haben. Sei es drum! Wäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich selbst bin zu nervös, um ihr selbst gegenüberzutreten.",
    ["O"] = "Bringt den liebevoll verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.",
    ["T"] = "Liebste Elenia",
  },
  [8903] = {
    ["D"] = "Diese Liebesepidemie hat etwas unnatürliches an sich. Die ganze Situation ist ungebührlich, ja ein Schandfleck, und im schlimmsten Fall eine Bedrohung für die Sicherheit der gesamten Allianz.$B$BDenkt doch nur: Was passiert, wenn sämtliche Wachen von dieser seltsamen Krankheit betroffen werden? Irgendetwas muss geschehen.$B$BGeht zu einer der Wachen hier in Sturmwind und seht, ob sie von dieser törichten Epidemie befallen wurde.",
    ["O"] = "Besorgt die Karte einer Wache von Stormwind und bringt sie zu Aristan Mottar in Stormwind.",
    ["T"] = "Gefährliche Liebschaft",
  },
  [8904] = {
    ["D"] = "Diese Liebesepidemie hat etwas unnatürliches an sich. Die ganze Situation ist abscheulich, und wenn sie erst einmal unsere Verteidigung schwächt, könnte dies zu einer ernsten Bedrohung für uns alle werden.$B$BSchlimm genug, dass bereits ein Großteil unserer Bevölkerung dieses lächerlich säuselnde Verhalten an den Tag legt. Ich denke, selbst unsere Wächter hat es bereits erwischt, dabei sollten sie gegen derartige Einflüsse immun sein.$B$BGeht zu einem der Wächter und seht, ob er von dieser törichten Epidemie befallen wurde.",
    ["O"] = "Besorgt eine modrige Karte des Wächters und bringt sie zu Fenstad Argyle in Unterstadt.",
    ["T"] = "Gefährliche Liebschaft",
  },
