pfDB["quests"]["esES-turtle"] = {
  [1] = "_",
  [2] = {
    ["D"] = "El poderoso hipogrifo Garrafilada ha sido derrotado, y la garra de la bestia abatida sirve como testimonio de tu victoria. Senani Corazón Atronador en el Puesto del Hachazo sin duda estará interesada en ver este trofeo como prueba de tus hazañas.",
    ["O"] = "Lleva la Garra de Garrafilada a Senani Corazón del Trueno en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "La garra de Garrafilada",
  },
  [5] = {
    ["D"] = "He estado escondido en este pueblo fantasma durante semanas, ¡y no he comido nada más que gusanos y malezas! Necesito algo de comida decente, y estoy dispuesto a pagar bien por ella.$B$BTráeme un festín y te pagaré generosamente.$B$BEscuché que el Chef Grual en la Taberna del Cuervo Escarlata en Villa Oscura hace unos estupendos pasteles de cangrejo oscuro...",
    ["O"] = "Habla con el Chef Grual.",
    ["T"] = "La hambruna de Alterio",
  },
  [6] = {
    ["D"] = "Garrick Piesuaves, es un maleante que lleva semanas acosando a granjeros y mercaderes. Ha sido visto en una choza cerca de los viñedos, cruzando el puente al este de la abadía. Tráeme la cabeza de ese villano y podrás quedarte con su botín.$B$BPero ten cuidado, $N. Garrick anda con una pandilla de matones. No será fácil llegar a él.",
    ["O"] = "Mata a Garrick Piesuaves y lleva su cabeza al Ayudante de alguacil Willem en Villanorte.",
    ["T"] = "La recompensa por Garrick Piesuaves",
  },
  [7] = {
    ["D"] = "Tu primera tarea es una limpieza, $N. Un clan de kóbolds ha invadido los bosques del norte. Ve y liquida a todos los kóbolds que encuentres; si reducimos sus filas podremos echarlos de Villanorte..",
    ["O"] = "Mata 10 Alimaña Kóbold, luego regresa con el Alguacil McBride.",
    ["T"] = "La limpieza del campamento kóbold",
  },
  [8] = {
    ["D"] = "¡Oye, amigo! ¿Le harías un favor a un joven hombre que ha combatido contra más zombis y arañas de los que puede contar?$B$BTengo esta carta que necesita llegar a Rémol... a un posadero llamado Renee algo. Realmente no importa cuál sea su apellido.$B$BDe todos modos, es un lugar acogedor lleno de víctimas de la plaga tratando de abrirse camino en el mundo. Y es un gran lugar para que descanses también si surge la necesidad. Deberías echarle un vistazo... si lo haces, te pagaré bien.",
    ["O"] = "Entrega la Carta Indescifrable a la Tabernera Renee en los Claros de Tirisfal.",
    ["T"] = "Un trato de Pícaro",
  },
  [9] = {
    ["D"] = "¡Mira lo que ha pasado con este lugar! Estas tierras alguna vez fueron ocupadas por buenos granjeros. Pero los malditos ladrones los han echado a todos. ¡Pero no a mi! Eso sí, parece que algunos vigías de la cosecha han tomado los campos.$B$BSi estás dispuesto a trabajar, me gustaría que salieras y mataras a veinte de ellos. Regresa cuando hayas terminado para recibir tu pago. Si terminas con los de mi campo, puedes limpiar también los campos vecinos.",
    ["O"] = "El Granjero Saldean quiere que mates a 20 Vigías de la Cosecha.",
    ["T"] = "Campos de la muerte",
  },
  [10] = {
    ["D"] = "¡Oh, esto es simplemente terrible! Envié a mi principal asistente, el perito adjunto Patagolpe, en el desierto situado al sudeste de aquí. Esa es un área que llamamos la Sima Abierta. Bueno, ha desaparecido, y su último informe mencionaba más de esos bichos extraños donde estaba. ¿Podría ser que ahora hayan infestado dos regiones grandes del desierto?$B$B¡Necesito que te dirijas a la Sima Abierta y busques a Patagolpe, por favor! O... al menos encuentra su equipo de análisis. Temo que eso sea todo lo que quede del pobre chico.",
    ["O"] = "Descubre el destino del perito adjunto Patagolpe y tráelo a él o su equipo de análisis al perito jefe Picoflojo en Gadgetzan.",
    ["T"] = "La Redención de Patagolpe",
  },
  [11] = {
    ["D"] = "Los gnolls, criaturas brutales sin ningún negocio decente en estas tierras, han sido vistos a lo largo de los bordes del Bosque de Elwynn. Una gran manada de ellos, muchos más de los que podemos manejar solos, ha infestado los bosques al sur de la torre de vigilancia. Otro grupo ha infestado las áreas cerca del Lago del Hito al este.$B$BEl Ejército de Ventormenta elogiará a quienquiera que ayude a matarlos. Tráeme sus brazaletes pintados de gnoll como prueba de tu hazaña.",
    ["O"] = "Lleva 8 Brazales de Gnoll Pintados al Alguacil Rainer en el Cuartel.",
    ["T"] = "Recompensa por los Gnolls de Patarroyo",
  },
  [12] = {
    ["D"] = "La Milicia del Pueblo tiene un solo objetivo: defender las tierras de Páramos de Poniente y devolver la paz a nuestros alrededores. Desafortunadamente, el precio de la paz suele ser la sangre.$B$BUno de mis exploradores ha traído noticias de una banda de Tramperos Defias causando estragos cerca. Tengo informes de avistamientos de Tramperos Defias cerca de la Mina de Jango al noroeste, así como en la Granja de Molsen y la Granja de Calabazas de Furlbrow. Si deseas unirte a nuestras filas, mata a 15 Tramperos Defias y 15 Contrabandistas Defias y luego regresa conmigo.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 Tramperos Defias y 15 Contrabandistas Defias y luego regreses con él en la Colina del Centinela.",
    ["T"] = "La Milicia del Pueblo",
  },
  [13] = {
    ["D"] = "Una banda de saqueadores Defias ha sido vista saqueando la Mina de la Costa de Oro, Arroyo de la Luna y la Granja Alexston. La Milicia del Pueblo no tolerará tal comportamiento. Despacha inmediatamente, $n, y haz que la Luz se haga presente en Páramos de Poniente.$B$BLa Mina de la Costa de Oro está cerca de la orilla, al oeste de la torre. Como siguiente paso de tu entrenamiento, quiero que mates a 15 de esos repugnantes Saqueadores Defias y a 15 Despojadores Defias.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 Saqueadores Defias y 15 Despojadores Defias y regreses con él en la Colina del Centinela.",
    ["T"] = "La Milicia del Pueblo",
  },
  [14] = {
    ["D"] = "Algunos Defias han eludido nuestra captura. Mi explorador más confiable informa que estos Defias han estado saqueando y robando en el campo, adentrándose incluso en el Oeste de Páramos de Poniente. Creemos que se esconden en las Colinas Dagas, planeando su próximo movimiento. Mata a esos miserables en nombre de la Milicia del Pueblo.",
    ["O"] = "Gryan Mantorrecio quiere que mates 15 Bandoleros Defias, 5 Acechacaminos Defias y 5 Nudillos Defias y luego regreses con él en la Colina del Centinela.",
    ["T"] = "La Milicia del Pueblo",
  },
  [15] = {
    ["D"] = "$N, mis exploradores me dicen que la infestación de kóbolds es más grande de lo que pensábamos. Un grupo de trabajadores kóbolds ha acampado cerca de la Mina del Eco, al norte.$B$BVe a la mina y elimínalos. Sabemos que hay al menos 10. Mátalos, comprueba si hay más, y luego regresa a informarme.",
    ["O"] = "Mata 10 Trabajadores Kóbolds, luego informa al Alguacil McBride.",
    ["T"] = "Investigar la Mina del Eco",
  },
  [16] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Dale una Bebida a Gerard",
  },
  [17] = {
    ["D"] = "He oído hablar de una planta recién descubierta llamada hongos magenta. Crecen en racimos, en lo profundo del sitio de excavación de Uldaman, pueden estar en otros lugares, pero por ahora ese es el único lugar donde sé que están. Quiero estudiar su uso potencial en la alquimia, y ahí es donde entras tú.$B$BEncuentra los racimos de setas magenta y tráeme una docena. Ten cuidado, es posible que los cúmulos lancen esporas venenosas si los molestas.$B$BHaz esto por mí, y te prepararé un lote de uno de mis famosos elixires restauradores.",
    ["O"] = "Lleva 12 Setas Magenta a Ghak Sanadón en Thelsamar.",
    ["T"] = "Componentes de Uldaman",
  },
  [18] = {
    ["D"] = "Hace poco llegó una banda de maleantes a Villanorte. Se hacen llamar la Hermandad Defias; se les ha visto cruzando el río hacia el este.$B$BNo sé qué están haciendo, ¡pero seguro que no es nada bueno! Tráeme sus pañuelos y te daré un arma como recompensa.",
    ["O"] = "Lleva 12 Pañuelos de Arpillera Roja al ayudante de alguacil Willem fuera de la Abadía de Villanorte.",
    ["T"] = "Hermandad de Ladrones",
  },
  [19] = {
    ["D"] = "Los exploradores y renegados de Roca Negra están tendiendo emboscadas entre aquí y el Fuerte de Petravista. El líder de los exploradores es un orco llamado Tharil\'zun, ¡y queremos a este orco! Tráeme la cabeza de Tharil\'zun.",
    ["O"] = "Lleva la Cabeza de Tharil\'zun a Marshal Marris en Crestagrana.",
    ["T"] = "Tharil\'zun",
  },
  [20] = {
    ["D"] = "Los orcos Roca Negra han viajado desde las Estepas Ardientes y han reclamado el Fuerte de Petravista al norte. Desde allí, asolan las áreas al norte de Villa del Lago y mantienen a sus ciudadanos en el pueblo, encerrados como ganado.$B$BAyuda a liberar Villa del Lago de esta amenaza. Mata a estos orcos Roca Negra y tráeme diez de sus Hachas de Batalla Gastadas como prueba.$B$BY aunque los Roca Negra nos amenazan desde el norte, hemos recibido informes de más de ellos en el sureste de Crestagrana. Si encuentras alguno allí, también deberían ser cazados.",
    ["O"] = "Lleva 10 Hachas de Batalla Gastadas al Mariscal Marris en Villa del Lago.",
    ["T"] = "Amenaza de Roca Negra",
  },
  [21] = {
    ["D"] = "Tus investigaciones anteriores son prueba de que la Mina del Eco necesita ser purgada. Regresa a la mina y ayuda a limpiarla de kóbolds.$B$BNo pierdas tiempo, $N. Cuanto más tiempo se deje a los kóbolds sin ser molestados en la mina, más profunda será su presencia en Villanorte.",
    ["O"] = "Mata 12 Kóbolds Obreros, luego regresa con el Mariscal McBride en la Abadía de Villanorte.",
    ["T"] = "Escaramuza en la Mina del Eco",
  },
  [22] = {
    ["D"] = "Las cebollas están peladas. El ajo está picado. El romero está triturado. La corteza ha sido horneada. El eneldo está picado. La salsa está hirviendo. ¡Ahora todo lo que necesito para mi famoso pastel de carne son 8 hígados de Jabalí de Collado!",
    ["O"] = "Salma Saldean necesita 8 hígados de Jaba de Tuscos para hacer un Pastel de Hígado de Jaba de Tuscos.",
    ["T"] = "Pastel de Hígado de Javierto",
  },
  [23] = {
    ["D"] = "La garra del poderoso oso Ursangous es tu trofeo por esta parte de la Cacería de Ashenvale. En la garra hay un pequeño emblema en forma de cuarto creciente, el significado del emblema sigue siendo un misterio.$B$BLa garra debe llevarse a Senani Truhacor en el Puesto de Hachazo para demostrar que has vencido a la poderosa criatura y completado esta parte de la cacería.",
    ["O"] = "Lleva la Garra de Ursangous a Senani Thunderheart en el Campamento de la Trama Esmeralda, Vallefresno.",
    ["T"] = "La Garra de Ursangous",
  },
  [24] = {
    ["D"] = "La una vez ágil Shadumbra ahora yace inmóvil mientras reclamás la victoria sobre ella. La cabeza del sable de la noche abatido es un trofeo adecuado para esta cacería de Ashenvale. Senani Corazón Atronador en el Puesto de Hachazo querrá ver tal trofeo para otorgarte crédito por esta parte de la Cacería de Ashenvale.",
    ["O"] = "Lleva la Cabeza de Shadumbra a Senani Corazón Atronador en el Puesto del Hachazo, Vallefresno.",
    ["T"] = "La Cabeza de Shadumbra",
  },
  [25] = {
    ["D"] = "Si quieres ser útil, ¿por qué no haces algo sobre la situación cerca del Sendero Talondeep?$B$B¡Los transportes entre Vallefresno y Stonetalon están paralizados porque el lago cobró vida y ataca todo lo que se acerca... lo que significa que cualquiera en el sendero. Sería incluso más útil que simplemente poner el martillo sobre los elementales de agua allí que alguien explorara el mirador en el Lago Mystral—el que tiene una vista sólida del puesto de la Alianza allí.$B$B¿Estás listo para ser útil?",
    ["O"] = "Elimina 12 Elementales de Agua Corruptos en el Lago Mystral, al este del Sendero Talondeep y al suroeste del Puesto de Hachazo.$B$BExplora la glorieta en el Lago Mystral que mira hacia el puesto de la Alianza cercano.$B$BRegresa con Mastok Wrilehiss en el Puesto de Hachazo, en Ashenvale.",
    ["T"] = "Punto Muerto en Stonetalon",
  },
  [26] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en un valioso protector de la naturaleza.$B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenarius nos ha bendecido. Una forma que podemos adoptar y que nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrite Rafagaestelar del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Habla con Dendrite Rafagaestelar en la aldea de Amparo de la Luna, Claro de la Luna.",
    ["T"] = "Una Lección Que Aprender",
  },
  [27] = {
    ["D"] = "$N, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en un valioso protector de la naturaleza.$B$BHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenarius nos ha bendecido. Una forma que podemos adoptar y que nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrite Rafagaestelar del Círculo Cenarion. Él será tu guía.",
    ["O"] = "Habla con Dendrite Rafagaestelar en la aldea de Nocheterna, Claro de la Luna.",
    ["T"] = "Una Lección Que Aprender",
  },
  [28] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar un Baratija de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Remulos. Coloca el Baratija en el altar y habla con Tajarri; es una de las celadoras más apreciadas de Claro de la Luna.$B$BTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$B$B¡Buena suerte, $N.",
    ["O"] = "Encuentra una Baratija del Santuario en el Lago Elune\'ara, y llévalo al Santuario de Remulos en el noroeste de Claro de la Luna. Una vez allí, usa la Baratija del Santuario.$B$BDebes hablar con Tajarri en el santuario después para completar la prueba.",
    ["T"] = "Prueba del Lago",
  },
  [29] = {
    ["D"] = "En tu primera prueba, debes ir a las profundidades del Lago Elune\'ara y encontrar una Baratija de Santuario. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Remulos. Coloca el Baratija en el altar y habla con Tajarri; es una de las celadoras más apreciadas de Claro de la Luna.$B$BTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.$B$B¡Buena suerte, $N.",
    ["O"] = "Encuentra una Baratija de Santuario en el Lago Elune\'ara, y llévalo al Santuario de Remulos en el noroeste de Claro de la Luna. Una vez allí, usa el Baratija del Santuario.$B$BDebes hablar con Tajarri en el santuario después para completar la prueba.",
    ["T"] = "Prueba del Lago",
  },
  [30] = {
    ["D"] = "Para la segunda prueba, necesitarás las dos mitades que forman el Colgante del León Marino. Una mitad obtiene su poder de la agilidad del aspecto del león marino, deslizándose a través del agua, la otra obtiene su poder de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.$B$BHabla con los locales de Claro de la Luna para saber dónde pueden estar las partes, y tráelas aquí para unirlas. Una vez formado, lleva el colgante a Dendrite Rafagaestelar.",
    ["O"] = "Encuentra el Medio Colgante de Agilidad Acuática y el Medio Colgante de Aguante Acuático. Habla con los residentes de Claro de la Luna para obtener pistas sobre dónde pueden estar estos objetos.$B$BForma el Colgante del León Marino a partir de las dos mitades. Debes estar cerca del Santuario de Remulos para hacer esto.$B$BLleva el colgante unido a Dendrite Rafagaestelar en la aldea de Puesto de la Guardia Nocturna, Claro de la Luna.",
    ["T"] = "Prueba del León Marino",
  },
  [31] = {
    ["D"] = "Has completado las lecciones necesarias que todos los jóvenes druidas deben aprender antes de poder adoptar un aspecto acuático. Ahora regresa con tu instructor, Turak Runatótem, en Cima del Trueno. Muéstrale tu colgante y demuéstrale que estás listo para aprender lo que él tiene para enseñarte. Él completará tu entrenamiento, permitiéndote convertirte en uno con el agua.$B$BAdiós, joven druida. Hablaremos de nuevo.",
    ["O"] = "Regresa a Cima del Trueno y muéstrale a Turak Runatótem el Colgante del León Marino.",
    ["T"] = "Forma Acuática",
  },
  [32] = {
    ["D"] = "Aquí está tu informe. Te insto a que se lo lleves a alguien que tenga conocimientos prácticos en este tipo de cosas.$B$BConozco a un erudito troll llamado Zilzibin Drumlore. Ha pasado mucho tiempo estudiando formas de vida únicas en la ecología de Azeroth; ¡también es historiador! Si quieres que alguien te ayude a descubrir exactamente cuál es esta amenaza y cómo detenerla, entrégale este informe. Vamos a necesitar toda la ayuda posible.$B$BZil vive en una casa en el segundo nivel del Callejón en Orgrimmar.",
    ["O"] = "Lleva el informe a Zilzibin Drumlore en Orgrimmar.",
    ["T"] = "El Ascenso de los Silítidos",
  },
  [33] = {
    ["D"] = "¡Detesto a esos asquerosos lobos grises! Pero los filetes de lobo son tan deliciosos... Tráeme carne de lobo dura y te la cambiaré por algo que te será útil.$B$BPuedes cazar a los lobos grises y los lobos jóvenes que merodean por la región de Villanorte.",
    ["O"] = "Lleva 8 trozos de Carne de Lobo Dura a Eagan Desollapelambres fuera de la Abadía de Villanorte.",
    ["T"] = "Lobos en la Frontera",
  },
  [34] = {
    ["D"] = "Han vuelto a arrasar mi jardín, y sé quién es el responsable. Es ese maldito jabalí, ese al que el alguacil Marris llama Panzallena.$b$bYa es demasiado tarde para replantar narcisos. Ese puerco ha devastado mis flores. ¡Acaba con ese bicharraco! Si me traes un colmillo suyo, te recompensaré. Dicen que anda husmeando por los campos que hay al suroeste de Villa del Lago, pero de vez en cuando viene a mi jardín.",
    ["O"] = "Martie Jainrose de Villa del Lago quiere que mates a Panzallena. Llévale su colmillo como prueba.",
    ["T"] = "Una Visita Poco Grata",
  },
  [35] = {
    ["D"] = "Si te preocupa que los rumores sobre los múrlocs sean ciertos, hay algo que puedes hacer: viaja al puente oriental de Elwynn y habla con el guardia Thomas. Ha estado emplazado en el puente durante la última semana, así que debe saber cuál es el estado de la situación.$B$BTráeme su informe.",
    ["O"] = "El alguacil Dughan quiere que hables con el Guardia Thomas.",
    ["T"] = "Más Preocupaciones",
  },
  [36] = {
    ["D"] = "Nunca pensé que llegaría el día en que dejaría la granja. Pero los campos están invadidos de ladrones y es demasiado peligroso para nosotros aquí ahora. Tan pronto como el granjero Furlbrow repare el carro, nos iremos. $B$B¿Tal vez podrías hacerme un favor? Déjame anotar mi receta para el estofado de los Páramos de Poniente. Por favor, llévasela a Salma Saldean en la granja de allá. La granja de los Saldean está justo más allá de la bifurcación en el camino.",
    ["O"] = "Verna Cejade quiere que entregues su receta de Estofado de los Páramos de Poniente a Salma Saldean.",
    ["T"] = "Estofado de Poniente",
  },
  [37] = {
    ["D"] = "Hace unos días enviamos a dos guardias, Rolf y Malakai, a investigar a lo largo del río, y aún no han regresado. Para completar mi informe, debo saber qué les sucedió a esos hombres.$B$BViaja hacia el norte a lo largo del río y encuentra a los guardias... o sus restos.",
    ["O"] = "El Guardia Thomas quiere que viajes al norte por el río y busques a los dos guardias perdidos, Rolf y Malakai.",
    ["T"] = "Encuentra a los Guardias Perdidos",
  },
  [38] = {
    ["D"] = "¡Ayúdame a hacer un estofado de Páramo de Poniente! Regresa con los siguientes ingredientes: $b $b 3 trozos de carne de buitre Fibrosa $b 3 Hocicos de Dentosangre $b 3 Ojos de Múrloc $b 3 Okra",
    ["O"] = "Salma Saldean quiere 3 Trozos de Carne de Buitre Fibrosa, 3 Hocicos de Dentosangre, 3 Ojos de Múrloc y 3 Okra.",
    ["T"] = "Estofado de Poniente",
  },
  [39] = {
    ["D"] = "Dile al Mariscal Dughan sobre las muertes de Malakai y Rolf, e infórmale que los múrlocs en el este de Elwynn no pueden ser contenidos con nuestra presencia actual de tropas.$B$BSé que no tenemos muchas tropas para enviar, pero espero que Dughan pueda encontrar a alguien.",
    ["O"] = "Preséntate ante el Mariscal Dughan en Villadorada.",
    ["T"] = "Entregar el Informe de Thomas",
  },
  [40] = {
    ["D"] = "$N, ¡hay una nueva amenaza en el Bosque de Elwynn! ¡Los múrluks están nadando por los arroyos del este de Elwynn, asustando a los peces y atacando a la gente amable!$B$B¡Le advertí al Mariscal Dughan, pero está más preocupado por los gnolls y los bandidos. No está convencido de que los múrluks sean un peligro.$B$B¡Por favor, $N, habla con Dughan y convéncelo de que envíe más tropas al este!",
    ["O"] = "Remy \"Dos Veces\" quiere que hables con el Mariscal Dughan en Villadorada.",
    ["T"] = "Un Peligro Escamoso",
  },
  [45] = {
    ["D"] = "Tras una búsqueda más detallada del área, encuentras huellas palmeadas que se dirigen hacia el Este a lo largo de la orilla del Lago del Hito. En la distancia hacia el este, apenas puedes ver una aldea Murloc.$B$BQuizás el destino de Rolf terminó allí...",
    ["O"] = "Busca en la aldea múrloc a Rolf, o señales de su muerte.",
    ["T"] = "Descubrir el destino de Rolf",
  },
  [46] = {
    ["D"] = "El Ejército de Ventormenta ha puesto una recompensa por los merodeadores y recolectores múrloc en Elwynn. Mátalos y tráeme sus aletas de múrloc desgarradas, y el Ejército de Ventormenta te recompensará generosamente.$B$BLos múrlocs han construido una aldea en el Lago del Hito al norte de aquí, y otra al sur donde el arroyo se bifurca.",
    ["O"] = "Lleva 8 Aletas de Múrloc Rasgadas al Guardia Thomas en el puente Este de Elwynn.",
    ["T"] = "Recompensa por los Murlocs",
  },
  [47] = {
    ["D"] = "Los kóbolds de esta zona a veces llevan Polvo de Oro. Me vendría muy bien tenerlo, tráeme una buena cantidad y te daré el mejor precio de la ciudad... ¡el mejor precio de la ciudad!$B$BPuedes encontrar kóbolds en la Mina Abisal al sur, y alrededor de la Cantera de Jaspe al noreste.",
    ["O"] = "Lleva 10 Polvo de Oro a Remy \"Dos Veces\" en Villadorada. El Polvo de Oro se obtiene de los Kóbolds en el Bosque de Elwynn.",
    ["T"] = "Intercambio de Polvo de Oro",
  },
  [48] = {
    ["D"] = "En la tierra al sur donde las enredaderas se retuercen y se arrastran$bHay un pozo oculto donde el agua corre profunda$bPura como la sagrada Hija de la Luz$bTráeme ahora un poco de Agua de Manantial Sagrado.",
    ["O"] = "Lleva Agua Bendita de Manantial a Tragoamargo Cebatruenos.",
    ["T"] = "Ámbar Dulce",
  },
  [49] = {
    ["D"] = "El ámbar es el color del amor más duradero de mi vida$BComo las últimas brasas del crepúsculo en el cielo$BRecupera para mí, para que mi amor nazca$bUn saco de cada uno: cebada, centeno y maíz.",
    ["O"] = "Tragoamargo Cebatruenos quiere un Saco de Cebada, un Saco de Centeno y un Saco de Maíz.",
    ["T"] = "Ámbar Dulce",
  },
  [50] = {
    ["D"] = "Mezclaré y mezclaré hasta que tengamos un puré$bPara que este sea nuestro propio alijo privado$bUn alambique debe ser hecho de metal fuerte$bTráeme algo de Veraplata para que pueda terminar esta canción.",
    ["O"] = "Tragoamargo Cebatruenos quiere Veraplata.",
    ["T"] = "Ámbar Dulce",
  },
  [51] = {
    ["D"] = "Aún no filtrado pero recién destilado$bComo tierra sin sembrar esperando ser labrada.$bLo siguiente será preparar nuestra pila de carbón$bEntrega una rama de sicomoro, ese es tu próximo objetivo.",
    ["O"] = "Lleva una Rama de Sicómoro a Tragoamargo Cebatruenos.",
    ["T"] = "Ámbar Dulce",
  },
  [52] = {
    ["D"] = "Saludos, $N. Los animales salvajes se vuelven cada vez más agresivos a medida que nos alejamos de Villadorada, ¡y el Aserradero de la Vega del Este sufre ataques casi constantes de lobos y osos!$B$B¡Nos vendría bien tu ayuda aquí!",
    ["O"] = "Mata 8 Merodeadores y 5 Osos Jóvenes del Bosque, y luego regresa con el Guardia Thomas en el puente este de Elwynn.",
    ["T"] = "Proteger la Frontera",
  },
  [53] = {
    ["D"] = "Líquido dulce como la miel, claro como las lágrimas de un bebé$bSe vuelve de un ámbar encantador con los años.$bPero antes de ponernos a beber hasta hartarnos$bDebemos construir un barril de roble carbonizado.",
    ["O"] = "Lleva un fardo de Roble Carbonizado a Tragoamargo Cebatruenos .",
    ["T"] = "Ámbar Dulce",
  },
  [54] = {
    ["D"] = "$N, eres un $c con un interés demostrado en la seguridad de Villanorte. Ahora estás encargado de la protección del Bosque de Elwynn que nos rodea.$B$BSi aceptas este deber, entonces tengo preparados unos documentos que deben ser entregados al Mariscal Dughan en Villa del Lago. Villa del Lago se encuentra al sur, más allá de las puertas fronterizas.",
    ["O"] = "Lleva los Documentos del Mariscal McBride al Mariscal Dughan en Villadorada.",
    ["T"] = "Informar a Villadorada",
  },
  [55] = {
    ["D"] = "Morbent Vil se esconde en su guarida, una casa ubicada en la cima de la montaña que hay al este, da al Cementerio de Colina del Cuervo. Su tiempo en esta tierra está llegando a su fin...$B$BUsa la Pesadilla de Morbent contra él. Esto anulará sus magias protectoras.$B$BMátalo. Mátalo y sálvanos de su maldad. ¡Sé el instrumento de mi venganza y un héroe de Bosque del Ocaso!",
    ["O"] = "Usa la Pesadilla de Morbent en Morbent Vil, mata a Morbent Vil, luego devuelve la Pesadilla de Morbent a Sven en su campamento.",
    ["T"] = "Morbent Vil",
  },
  [56] = {
    ["D"] = "Como Comandante de la Guardia Nocturna, es mi deber jurado proteger a los ciudadanos de Villa Oscura. Ventormenta ha retirado a los guardias que estaban aquí destacados. Ahora tenemos que arreglárnoslas solos.$b$b¡Si te sientes digno de la tarea, necesitamos tu ayuda! Asiste a la Guardia Nocturna aventurándote en el Jardín Tranquilo, justo al sur de Villa Oscura, mata a 8 Guerreros Esqueléticos y 6 Magos Esqueléticos. ¡La amenaza de los no-muertos debe ser detenida!",
    ["O"] = "La Comandante Althea Cerranegro de Villa Oscura quiere que mates a 8 Guerreros Esqueléticos y 6 Magos Esqueléticos.",
    ["T"] = "La Guardia Nocturna",
  },
  [57] = {
    ["D"] = "$N, estoy recibiendo informes de una infestación de no-muertos en el bosque. Si estás dispuesto, viaja al cementerio de la Colina del Cuervo y mata a 15 de esos Malignos Esqueléticos y 15 Horrores Esqueléticos. También hay informes de esqueletos al sur de Villa Oscura, en el Cementerio de los Jardines Tranquilos. ¡Los no-muertos deben ser expulsados de Bosque del Ocaso!",
    ["O"] = "La Comandante Althea Cerranegra de Villa Oscura quiere que mates a 15 Malignos Esqueléticos y 15 Horrores Esqueléticos.",
    ["T"] = "La Guardia Nocturna",
  },
  [58] = {
    ["D"] = "La situación en Colina del Cuervo sigue siendo desalentadora. La seguridad de Villa Oscura está en tus manos, $N. No tengo suficientes Vigilantes para mantener el pueblo a salvo. ¡Necesitamos que vayas a Colina del Cuervo una vez más y elimines a 20 Propagadores de la Peste del mausoleo este!",
    ["O"] = "La Comandante Althea Cerranegro de Villa Oscura quiere que mates a 20 Propagadores de la Peste en el mausoleo este de Colina del Cuervo.",
    ["T"] = "La Guardia Nocturna",
  },
  [59] = {
    ["D"] = "Por tu astucia y valentía, tengo aquí una marca que es válida para una pieza de armadura. Quiero que la lleves a Sara Timberlain en el Aserradero de la Vega del Este. Dale la marca y ella te confeccionará la armadura. Y después de recibirla, $N, úsala en la defensa de Elwynn.$B$BEl Aserradero de la Vega del Este está está pasando el puesto del guardia Thomas en el este.",
    ["O"] = "Entrega a Sara Timberlain el marcador de armaduras de Ventormenta.",
    ["T"] = "Armaduras de Paño y Cuero",
  },
  [60] = {
    ["D"] = "¡Hola, $gbuen señor:mi señora;! ¿Tienes un momento?$B$BMi hermano y yo somos boticarios en Ventormenta, y estoy aquí para recolectar velas grandes por su cera. ¿Puedes ayudarme?$B$BPuedes obtener velas grandes de los kóbolds, he escuchado rumores de que los kóbolds están infestando las minas de Elwynn... la Mina Abisal al sur y la Cantera de Jaspe al este. Te sugiero buscar velas en uno de esos lugares.",
    ["O"] = "Lleva 8 Velas Grandes a William Mortero en Villadorada.",
    ["T"] = "Las velas de los kóbolds",
  },
  [61] = {
    ["D"] = "Mi hermano Morgan está esperando en Ventormenta mi envío de velas. No tengo suficiente tiempo para hacer el viaje yo mismo, pero si quieres llevarle el envío, te pagaría bien.$B$BHe empacado las velas, y puedes encontrar a Morgan en nuestra tienda, Herbolario Mortero, en el Distrito de Mercaderes de Ventormenta.",
    ["O"] = "Lleva el Envío de William a Morgan Mortero en el Distrito de Mercaderes de Ventormenta.",
    ["T"] = "Un envío para Ventormenta",
  },
  [62] = {
    ["D"] = "¡La mina en Villanorte no es la única con problemas! Tengo informes de que la Mina Abisal en Elwynn también se ha convertido en un refugio para los kóbolds.$B$BExplora la mina y confirma estos informes, luego regresa conmigo. La mina está hacia el sur de Villadorada, entre la Granja Pedregosa y la Granja Maclure.",
    ["O"] = "Explora la Mina Abisal y regresa con el alguacil Dughan en Villadorada.",
    ["T"] = "La Mina de Abisal",
  },
  [63] = {
    ["D"] = "Por la costa de Bosque de Argénteos, al oeste de El Sepulcro, verás un altar infestado por la peste. Los espíritus allí ya no hablan con nuestra gente, y será tu tarea derrotar a la manifestación y limpiar el pozo.$B$BLlévate los brazales de elemental, junto con las gotas restantes de la poción de salmuera y colócalas en el brasero. El ritual debería ser suficiente para invocar un espíritu puro con el que puedas comunicarte.$B$BTráeme una prueba de que la tarea está hecha, y te recompensaré con un tótem.",
    ["O"] = "Derrota a la manifestación de agua corrupta y coloca los brazales de manifestación corrupta con las gotas restantes del agua más pura en el blandón de Siemprefuente en el Bosque de Argénteos.",
    ["T"] = "La Llamada del Agua",
  },
  [64] = {
    ["D"] = "¡Fue horrible! Verna me despertó cuando escuchó un alboroto en los campos. Los campos estaban llenos de gamberros. Salimos apresurados y olvidé tomar mi reloj de bolsillo. El padre de Verna me dio ese reloj el día de nuestra boda y es horrible saber que está en manos de esos ladrones. Dejé el reloj de bolsillo en el armario de la granja. Busca el campo de calabazas al Oeste, no puedes perderlo. Si me lo traes de vuelta, te estaré muy agradecido.",
    ["O"] = "El Granjero Cejade quiere que recuperes su reloj de bolsillo del armario en su granja de calabazas al Oeste.",
    ["T"] = "La Reliquia Olvidada",
  },
  [65] = {
    ["D"] = "La banda de desgraciados responsable de que la gente de los Páramos de Poniente abandonara sus tierras se llama a sí misma la Hermandad Defias. Necesito que te infiltres en este clan de matones. Necesitamos saber quién dirige el cártel y dónde se esconden. En Villa del Lago, en la posada, hay un pícaro llamado Wiley que me debe un favor.$B$BViaja a Villa del Lago en las Montañas Crestagrana, al este de Elwynn, y descubre lo que puedas.",
    ["O"] = "Gryan Mantorrecio quiere que hables con Wiley en Villa del Lago.",
    ["T"] = "La Hermandad de los Defias",
  },
  [66] = {
    ["D"] = "Anoche, una horrible perturbación recorrió mis venas. Sentí que mi nieta, Alyssa, estaba en gran peligro. Consulté las cartas y la Muerte me miró desde la mesa.$B$BBusqué la luz en un largo y oscuro trance... intentaba encontrar algo concreto que me ayudara a calmar el espíritu. Un nombre vino a mí, el nombre de Stalvan.$B$BAcude al secretario del Concejo e intenta averiguar algo más sobre ese nombre. Temo por todos nosotros.",
    ["O"] = "Busca al secretario Daltry en el Concejo de Villa Oscura.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [67] = {
    ["D"] = "¿Stalvan, eh? Déjame revisar el registro de la ciudad.$b$bStalvan... Stalvan... veamos. ¡Ah, aquí está! Tengo un registro de un tal Sr. Stalvan Mantoniebla. La última dirección registrada es la Escuela de Arroyo de la Luna. ¡Pero este dato es de hace mucho tiempo!$b$bHazme un favor, ¿quieres, amigo? Si por casualidad vas a Arroyo de la Luna, hazme saber si consigues información más reciente sobre este tipo. Me gusta llevar los registros al día.",
    ["O"] = "Viaja a la Escuela de Arroyo de la Luna y lleva cualquier información que descubras sobre Stalvan al secretario Daltry.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [68] = {
    ["D"] = "Encuentras una Carta Polvorienta Sin Enviar en el viejo baúl.",
    ["O"] = "Lleva la Carta Polvorienta Sin Enviar al secretario Daltry.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [69] = {
    ["D"] = "¡Ay, ay! Esto se me debió de pasar la primera vez. En el registro, justo debajo de la primera dirección de Stalvan, hay otra dirección. Está tachada... Parece que se dirigía a la Posada Orgullo de León, en Villadorada. Quizás quieras comprobarlo, $N.",
    ["O"] = "Ve a la Posada Orgullo de León a ver si alguien ha oído el nombre de Stalvan.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [70] = {
    ["D"] = "Stalvan... ese nombre me suena de algo. ¡Ya sé de qué!$B$BHace muchos años, en una noche tormentosa, un mensajero llegó buscando refugio por la noche. Cerca de la medianoche, el hombre bajó las escaleras gritando, su rostro pálido de miedo. Todavía con su ropa de dormir, desapareció en la tormenta.$B$BCon las prisas, olvidó sus cartas en el cofre de arriba. Nunca regresó por ellas. Una de ellas es de ese tal Stalvan, destinada al Distrito de Los Canales de Ventormenta. Puedes quedártela.",
    ["O"] = "Recupera la Carta Sin Enviar de Stalvan del cofre y entrégala en el distrito de Los Canales de Ventormenta.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [71] = {
    ["D"] = "Ahora que tienes ambos medallones, entrégaselos al Guardia Thomas en el puente para que pueda conocer el destino de sus guardias asesinados.",
    ["O"] = "Entrega los medallones de Rolf y Malakai al guardia Thomas en el puente oriental de Elwynn.",
    ["T"] = "Informar a Thomas",
  },
  [72] = {
    ["D"] = "Mi padre era el custodio de la propiedad mucho antes que yo. Tuvo que limpiar la sangre después de la masacre. Pero eso no viene al caso.$B$BEl fondo de Cresta Sílex se ha agotado. Ahora las últimas posesiones de la familia están destinadas a la subasta. Es culpa de esos buitres de los recaudadores de impuestos. Como veo que te mueres por saber más, creo que puedes mirar entre toda esta basura. Quién sabe qué podrías encontrar.",
    ["O"] = "Busca una pista entre los objetos empaquetados.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [73] = "_",
  [74] = {
    ["D"] = "Dentro de la caja encuentras varios objetos: reliquias familiares mohosas, unos trofeos de caza, un retrato de familia y algunos libros antiguos. Al fondo, debajo de un jarrón de cerámica, descubres una página arrancada de un diario.",
    ["O"] = "La página arrancada del diario que descubriste es tu siguiente pista.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [75] = {
    ["D"] = "Había un montón de pergaminos en el cofre de arriba cuando me mudé a este lugar. Empecé a examinarlos, pero tuve que dejarlo cuando la niebla empezó a nublarme la vista.$b$bHazle un favor a un viejo casi ciego y revisa el cofre de arriba por cualquier cosa que pueda ayudarte en tu búsqueda para descubrir más sobre este tal Stalvan. Sé que hay una página de un diario que, aunque está algo descolorida, podría interesarte mucho. Tráemela y te ayudaré en todo lo que pueda.",
    ["O"] = "El alguacil Haggard quiere que revises el cofre de arriba en busca de la Página de Diario Descolorida.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [76] = {
    ["D"] = "Gracias a ti, sabemos que la Mina Abisal está infestada de kóbolds. Ahora necesitamos un explorador que investigue la Cantera de Jaspe, que está más lejos.$B$BExplora la Cantera de Jaspe y confirma la presencia de kóbolds. Para llegar a la mina, viaja hacia el este por el camino hasta que llegues a la Torre de Azora. Cuando llegues allí, dirígete hacia el norte. Encontrarás la mina al pie de las colinas.",
    ["O"] = "Explora la Cantera de Jaspe y vuelve a informar al alguacil Dughan a Villadorada.",
    ["T"] = "La Cantera de Jaspe",
  },
  [77] = {
    ["D"] = "Tengo material de empaquetado original para el calimocho que quiere Dran Droffers. Te ayudaré a prepararlo para enviárselo a ese gruñón del padre de Malton... a cambio de dos botellas del mismo. Están en unas cajas en la base abandonada de la Horda cerca de Roca Oculta.$B$BSerá muy dificultoso; habrá babosas y mocos repugnantes por las ruinas. Dicen que su líder es una especie de jefe moco. Pero da igual; consigue el calimocho y vuelve aquí para empaquetarlo.",
    ["O"] = "Recupera 10 botellas de calimocho dulce de las Tierras del Interior de las cajas ubicadas alrededor de la base abandonada de la Horda en Roca Oculta.",
    ["T"] = "Una Situación Peliaguda",
  },
  [78] = {
    ["D"] = "Conozco a alguien que podría ayudarte. Cuando yo lideraba la Guardia de Ventormenta, solíamos tomar tragos en El la Taberna del Cuervo Escarlata en Villa Oscura. El tabernero de allí, Smitts, era todo un experto en la historia local. Muéstrale esta página y mira qué tiene que decir al respecto.",
    ["O"] = "Lleva la Página del Diario Descolorida al Tabernero Smitts.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [79] = {
    ["D"] = "Seguí la leyenda de ese tal Stalvan durante años. Cuando esos nobles visitantes fueron masacrados hace unos años, fui con Haggard a investigar. Encontré estas páginas embarradas, pero nunca pudimos vincular la escritura con ese loco del bosque. Tu rastro de evidencia prueba su culpabilidad. Lleva esto a la comandante Cerranegro de inmediato y ponla al tanto de lo que has descubierto.",
    ["O"] = "Lleva la página enlodada a la comandante Cerranegro.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [80] = {
    ["D"] = "He estado vigilando a ese tipo, Stalvan, durante bastante tiempo. Pero si esta página fue escrita por la misma mano, prueba su culpabilidad más allá de toda duda.$b$bEn todos sus días llevando el Registro de la Ciudad, el secretario Daltry se ha convertido en un experto en identificar la escritura. Muéstrale esta página y verifica si la escritura coincide con la firma del registro.",
    ["O"] = "Muéstrale al Secretario Daltry la Página del Diario Manchada de Sangre.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [81] = {
    ["D"] = "He puesto la caja de calimocho y las otras botellas que se lleva Dran en el barril para que no se muevan. Él no me cae muy bien, pero la verdad es que no hay nada peor que el calimocho agitado.$B$BAhora, entrega ese barril a Dran Droffers a su local en Orgrimmar. Y transmite mi afectuoso saludo a Malton; dile que si alguna vez tiene sed de bebida y de buena compañía, nos honraría mucho su visita.",
    ["O"] = "Entrega el barril a Dran Droffers en Orgrimmar.",
    ["T"] = "Entrega el calimocho",
  },
  [82] = {
    ["D"] = "A ver, en cuanto a estos informes sobre los bichos... lo que sabemos es que están drenando las pozas de agua que antes estaban donde se ubica la Guarida Ponzoñosa. Lo que NO sabemos es... bueno, todo lo demás.$B$B¡Descubramos en primer lugar a quién nos enfrentamos! Quiero que vayas a la Guarida Ponzoñosa y que obtengas algunos trozos de esos bichos. Consigue 5 trozos de centipaar y llévaselos al alquimista Morterozugg para que se encargue de ellos. Con su ayuda podremos descubrir qué son.",
    ["O"] = "Lleva cinco Partes de Insecto Centipaar de la Guarida Ponsoñoza al Alquimista Morterozugg en Gadgetzan.",
    ["T"] = "Investigación en la Guarida Ponzoñosa",
  },
  [83] = {
    ["D"] = "La banda Defias en Villanorte usa máscaras de arpillera, pero los Defias en Elwynn usan lino que puedo usar para hacer buenos productos de lino.$B$BTráeme bandanas de lino rojo y las usaré para hacerte algo.$B$BLos miembros de la banda Defias tienen campamentos esparcidos por todo Elwynn.",
    ["O"] = "Lleva 6 Pañuelos de Lino Rojo a Sara Timberlain en el Aserradero de la Vega del Este.",
    ["T"] = "Artículos rojos de lino",
  },
  [84] = {
    ["D"] = "Aquí tienes. Y cuando le des este pastel a ese Billy, dile que espero que se atragante con él.",
    ["O"] = "Lleva el pastel de tripa de cerdo a Billy Maclure en los Viñedos Maclure.",
    ["T"] = "Vuelve con Billy",
  },
  [85] = {
    ["D"] = "He perdido mi collar... creo que me lo robó ese granuja de Billy Maclure. Normalmente pasa el día escondido como una rata en Los Viñedos de Maclure, al este de aquí.$B$BRecupera mi collar y le darás una alegría al corazón de una anciana viuda.",
    ["O"] = "Habla con Billy Maclure.",
    ["T"] = "El Collar Perdido",
  },
  [86] = {
    ["D"] = "Si me das un pastel, puede que te diga quién tiene el collar. Bernice, esa anciana de la otra granja, hace unos pasteles de tripa de cerdo increíbles... $B$BQuizás te cocine un pastel si le llevas algunos trozos de carne de los jabalíes que rondan por nuestras granjas y le dices para qué son.",
    ["O"] = "Lleva 4 Trozos de Carne de Jabalí a la Tía Bernice Pedregosa en La Granja Pedregosa.",
    ["T"] = "Pastel para Billy",
  },
  [87] = {
    ["D"] = "Estaba jugando cerca de la Mina Abisal y creo que se me cayó... quiero decir, creo que vi el collar de la señora. No me preguntes cómo llegó allí... ¡Yo no fui!$B$BDe todas formas, vi a un enorme kóbold con dientes de oro que lo recogía y se lo llevaba a la mina. Si encuentras a ese kóbold, encontrarás el collar, ¡lo juro!",
    ["O"] = "Lleva el Collar de Bernice a tía Bernice Pedregosa a La Granja Pedregosa..",
    ["T"] = "Dientes de Oro",
  },
  [88] = {
    ["D"] = "Los Brackwell tienen una cerda que ganaría cualquier concurso. Se llama Princesa. Está ENORME, ¡y es gracias a mí! ¡Porque se mete en mis plantaciones y se come mis verduras!$B$B¡Así que antes de que venga a nuestros campos... la Princesa debe morir! ¡Tráeme su collar como prueba del hecho y te daré algo por tu tiempo!$B$BLa Princesa suele estar en el Campo de Calabazas de los Brackwell, al este y más allá de la granja de los Maclure. ¡Atrapala antes de que tenga hambre y vuelva aquí!",
    ["O"] = "Mata a Princesa, agarra su collera y llévasela a la vieja Pedregosa a La Granja Pedregosa.",
    ["T"] = "¡Princesa debe Morir!",
  },
  [89] = {
    ["D"] = "Durante la última invasión orca, nos vimos obligados a fundir nuestro hierro para hacer balas, espadas y armaduras. Enviamos un mensaje a Ventormenta para un nuevo envío de materiales, pero una banda de gnolls de Crestagrana secuestró la caravana y huyó a las colinas detrás de Villa del Lago.$B$BAhora nos estamos quedando cortos de suministros para reconstruir este puente. Si puedes traerme 5 picas de hierro y 5 remaches de hierro, $N, me aseguraré de que seas recompensado.$B$B¡Pongámonos a trabajar!",
    ["O"] = "Lleva 5 Picas de Hierro y 5 Remaches de Hierro al supervisor Oslow en Villa del Lago.",
    ["T"] = "El puente Sempiterno",
  },
  [90] = {
    ["D"] = "¡El kebab de lobo sazonado es el especial de la casa! ¡Claro que te puedo preparar un poco! Pero necesitaré algunos productos. Tráeme 10 costillares de lobo magro y algunas hierbas aromatizantes de Ventormenta. Hay tantos lobos aquí en el bosque que estoy seguro de que encontrar los costillares no será un problema. Para las hierbas, busca a Felicia Gump en su tienda de flores del distrito del canal en Ventormenta.",
    ["O"] = "Reúne 10 costillares de lobo magro y hierbas aromatizantes de Ventormenta luego regresa con el Chef Grual en Villa Oscura.",
    ["T"] = "Kebab de Lobo Sazonado",
  },
  [91] = {
    ["D"] = "Se ha instalado un enemigo de Ventormenta en las laderas orientales de Crestagrana. Este villano usa lo arcano para extender el terror entre nuestra gente. Se oculta bajo el nombre de Morganth.$B$BMorganth ahora controla al Clan Pielsombra para realizar sus órdenes. Según la Ley de Villa del Lago, ayudar a un criminal del Reino es castigable con la muerte.$B$B¡Estos viles gnolls deben ser asesinados!$B$BEntrégame 10 Colgantes Pielsombra y serás recompensado.",
    ["O"] = "Lleva 10 Colgantes Pielsombra al Alguacil Conacher en el Ayuntamiento de Villa del Lago.",
    ["T"] = "La Ley de Solomon",
  },
  [92] = {
    ["D"] = "Nada me complacería más que prepararte un poco de Estofado Crestagrana. Pero hay un pequeño problema. Durante el reciente levantamiento orco, alimenté a toda la brigada de tropas de Ventormenta. Ahora mi despensa está vacía. Pero si puedes proporcionarme los ingredientes, estaré encantado de complacerte.$b$bSolo tráeme cinco piezas de Carne de Cóndor Dura, cinco Hocicos de gran Dentosangre y cinco porciones de Carne de Araña Crujiente.",
    ["O"] = "La Chef Breanna de Villa del Lago quiere cinco trozos de Carne de Cóndor Dura, cinco Hocicos de Gran Dentosangre y cinco raciones de Carne de Araña Crujiente.",
    ["T"] = "Estofado Crestagrana",
  },
  [93] = {
    ["D"] = "Te contaré un pequeño secreto: ¡los Pasteles de Cangrejo Oscuro en realidad están hechos de patas de araña! Sé que es un poco asqueroso, pero los pasteles tienen un sabor agradable y picante, ¡y son un gran bocadillo! Tráeme Patas de Araña Viscosas y te prepararé algunos.$B$BHe oído que las Telarácnidas venenosas son una buena fuente: anidan al norte, entre las laderas de las colinas y el río.",
    ["O"] = "Reúne 6 Patas de Araña Pegajosas y llévaselas al Chef Grual en Villa Oscura.",
    ["T"] = "Pasteles de Cangrejo Oscuro",
  },
  [94] = {
    ["D"] = "Mi rival, Morganth, es un malvado mago de gran poder que vive en la Torre de Ilgalar, en Crestagrana. He creado un dispositivo que me permitirá espiar sus movimientos: el Ojo de Azora. Ahora puedo verlo desde lejos.$B$BPero para estudiar sus pensamientos... necesito más ayuda.$B$BSi estás dispuesto, lleva mi Glifo de Azora a la Torre de Ilgalar. Colócalo sobre la Estatua del León cerca de la Torre.$B$BCuando esto esté hecho, el poder de Azora te otorgará visiones de los planes futuros de Morganth.",
    ["O"] = "Lleva el Glifo de Azora a la Estatua del León cerca de la Torre de Ilgalar en Crestagrana.",
    ["T"] = "Un Ojo Vigilante",
  },
  [95] = {
    ["D"] = "Solía trabajar en una de las granjas al sureste... ¡hasta que los Jinetes Oscuros del Paso de la Muerte descendieron sobre mi granja y masacraron a mi familia cuando yo estaba fuera!$B$BCuando regresé, vi una figura sombría merodeando cerca de mi granero, enterrando algo. Huyó antes de que pudiera atraparlo, y no pude quedarme porque estaba siguiendo de cerca a los Jinetes Oscuros. Así que nunca descubrí lo que había escondido.$B$BSi puedes encontrar lo que esa figura sombría enterró, te estaría agradecido. El escondite está detrás del viejo tocón cerca de mi granero.",
    ["O"] = "Ve a la Granja de Sven y encuentra lo que estaba enterrado detrás del viejo tocón.",
    ["T"] = "La Venganza de Sven",
  },
  [96] = {
    ["D"] = "Toma el fragmento. Toma el fragmento y da vida.$B$BDa vida y comprende. Comprende que el agua es vida... la corrupción detiene la vida.$B$BTú proteges y das vida con el poder del agua.",
    ["O"] = "Lleva el Fragmento de Agua a Islen Vidente de las Aguas en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [97] = {
    ["D"] = "¡Informa inmediatamente a la comandante Cerranegro de que la letra coincide, $N! ¡Sus sospechas eran correctas!",
    ["O"] = "Informa las noticias a la Comandante Cerranegro.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [98] = {
    ["D"] = "Stalvan Mantoniebla llevó una vida de depravación. Víctimas inocentes murieron en sus manos. Sin duda, es culpable de innumerables crímenes. Ahora el lunático amenaza a Villa Oscura. Solo la Luz sabe qué actos sórdidos está planeando. Viaja a su cabaña, justo al norte del pueblo, $C, y ejecuta a Stalvan de una vez por todas.$b$bCuando la tarea esté hecha, ve a la casa de Madame Eva y muéstrale el anillo familiar. Después de todo, fue su premonición la que llevó a este macabro descubrimiento. Pero Villa Oscura está más segura gracias a ella.",
    ["O"] = "Mata a Stalvan Mantoniebla y muéstrale a Madame Eva su anillo familiar.",
    ["T"] = "La Leyenda de Stalvan",
  },
  [99] = {
    ["D"] = "A medida que aumenta mi comprensión de la magia de Arugal, también lo hace mi desprecio por ese desventurado necio. Estoy a punto de completar mi investigación sobre su supuesto remedio.$b$bMi conocimiento estará completo cuando descubra qué encantamiento está causando el extraño comportamiento en el Pueblo Piroleña. Durante el día, los campesinos parecen humanos. Pero cuando el sol se pone, los habitantes del pueblo se convierten en Huargen Furia Lunar.$b$bNecesito extraer energía de los grilletes encantados que Arugal les ha impuesto. Tráeme seis grilletes encantados de Piroleña, $N.",
    ["O"] = "Lleva 6 Grilletes de Piroleña a Dalar Tejealba en el Sepulcro.",
    ["T"] = "La Locura de Arugal",
  },
  [100] = {
    ["D"] = "La luz explota mientras terminas el conjuro. Los sonidos de las gotas de agua golpeando la llama se han apagado y tus sentidos se ven abrumados al ver cómo los espíritus corruptos que te rodean son destruidos.",
    ["O"] = "Habla con la Manifestación Menor de Agua en el Bosque de Argénteos.",
    ["T"] = "La Llamada del Agua",
  },
  [101] = {
    ["D"] = "A medida que la mancha mística se extiende por el bosque, la necesidad de autoprotección es innegable, $N. Los vientos me susurran y hablan de un gran peligro que te espera pacientemente en un futuro cercano.$b$bSi deseas protegerte, noble $c, tráeme 10 colmillos de necrófago, 10 dedos de esqueleto y 5 frascos de veneno de araña. Para ti, encantaré un tótem de castigo que dañará a aquellos que intenten actos violentos contra ti.",
    ["O"] = "Lleva 10 Colmillos de Necrófago, 10 Dedos de Esqueleto y 5 Viales de Veneno de Araña a Lady Eva en Villa Oscura.",
    ["T"] = "El Tótem de castigo",
  },
  [102] = {
    ["D"] = "Ventormenta nos ha abandonado. Un viento fétido de depravación sopla a través de las llanuras de Páramos de Poniente. Esta era mi tierra natal y no le daré la espalda a los ciudadanos que eligen quedarse aquí. Nosotros, los antiguos granjeros, haremos nuestra resistencia.$b$bTu tarea, si decides aceptarla, es patrullar las praderas de Páramos de Poniente. Rastrea y mata a los vilísimos gnolls que parecen estar trabajando en conjunto con los ladrones de las Minas de la Muerte. Tráeme ocho Patas de Gnoll y recompensaré tu valentía.",
    ["O"] = "Lleva 8 Patas de Gnoll al Capitán Danuvin en la Colina del Centinela.",
    ["T"] = "Patrullando Páramos de Poniente",
  },
  [103] = {
    ["D"] = "La noche en que murió la familia del guardián del Faro fue horrible. Contemplé, impotente, cómo el Viejo Ojosombrío lideraba el ataque. Pero lo hecho, hecho está, y ahora mi preocupación es por las vidas de los marineros en el Gran Mar cuyos barcos se acercan a las peligrosas rocas de la costa. Sin nadie que vigile la llama, la responsabilidad ha recaído en mí.$B$B¡Ayúdame a mantener la antorcha encendida trayéndome 5 frascos de aceite de los Monstruos de la Cosecha!",
    ["O"] = "Lleva 5 Frascos de Aceite al Capitán Grisillo en el Faro de Poniente.",
    ["T"] = "Guardián de la Llama",
  },
  [104] = {
    ["D"] = "Cuando mi vida terminó sobre las rocas, no tenía idea de lo que me deparaba el más allá. El faro estaba apagado esa noche porque el Viejo Ojosombrío había asustado a la familia del guardián del faro. Regresaron y volvieron a encender la llama, pero el Viejo Ojosombrío convenció a los murlocs de mente débil para que asaltaran el faro con él una vez más. La segunda vez, la familia no tuvo tanta suerte y ante mis ojos perecieron indefensos.$B$BMata al Viejo Ojosombrío si lo ves a lo largo de la costa y tráeme una de sus escamas, te recompensaré.",
    ["O"] = "Llevale una escama del Viejo Ojosombrío al Capitán Grisillo en el Faro de Poniente.",
    ["T"] = "La Amenaza Costera",
  },
  [105] = {
    ["D"] = "¡Por desgracia, ha llegado el momento de atacar Andorhal y expulsar al exánime que lo controla!$B$BDentro de las ruinas de la ciudad es donde el exánime, Araj el Invocador, tiene su dominio. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes unidos directamente a él es formidable. Necesitarás numerosos aliados para siquiera tener una posibilidad de enfrentarlo con éxito.$B$B¡Destruye a Araj, $N, y tráeme un fragmento de su filacteria como prueba!",
    ["O"] = "Lleva el Fragmento de Filacteria de Araj al Sumo Ejecutor Derrington en El Baluarte, Tierras de la Peste del Oeste.",
    ["T"] = "Ah, Andorhal",
  },
  [106] = {
    ["D"] = "¡Oh, estoy maldita! He entregado mi corazón a Tommy Joe Pedregosa, pero nuestras familias se odian a muerte. Así que no puedo verlo, ¡aunque me duelan los ojos por no poder mirar su hermoso rostro!$B$BPor favor, toma esta carta y entrégasela a Tommy Joe. Suele estar en el río que está al oeste de la Granja Pedregosa, al oeste de aquí.",
    ["O"] = "Dale la Carta de Amor de Maybell a Tommy Joe Pedregosa.",
    ["T"] = "Jóvenes enamorados",
  },
  [107] = {
    ["D"] = "Seguro que William Mortero tiene una poción que reúna a los dos jóvenes amantes!$B$BTen, llévale esta nota a William. Está en la Posada Orgullo de León, en Villadorada.",
    ["O"] = "Lleva la Nota de la Abuela Pedregosa a William Mortero.",
    ["T"] = "Nota para William",
  },
  [108] = "_",
  [109] = {
    ["D"] = "Parece que has visto bastante combate en tu vida, $c. Si aún no los has hecho, deberías reportarte con Gryan Mantorrecio. Él dirige la Milicia del Pueblo, destinada a proteger las tierras de cultivo de Páramos de Poniente. Apuesto a que podría usar tu ayuda. Normalmente lo encuentras en la torre de piedra en la Colina del Centinela, justo al lado del camino en el medio de Páramos de Poniente.",
    ["O"] = "Habla con Gryan Mantorrecio. Normalmente se le puede encontrar en la torre de piedra en la Colina del Centinela, justo al lado del camino, en el medio de los Páramos de Poniente.",
    ["T"] = "Informar a Gryan Mantorrecio",
  },
  [110] = {
    ["D"] = "Voy a pasar un tiempo revisando el equipo de topografía de Patagolpe... y a poner en orden sus efectos personales.Tengo la inquietante sensación de que esta no será la última pérdida que sufriremos a causa de estos insectos.$B$BMe gustaría que revisaras con el Alquimista Morterozugg para ver si ha tenido la oportunidad de procesar las partes de insectos que le enviamos. Si están listas, tráemelas.",
    ["O"] = "Consulta en Gadgetzan por el Alquimista Morterozugg y su análisis de las partes de insecto.",
    ["T"] = "Análisis de Trozo de Insecto",
  },
  [111] = {
    ["D"] = "Por favor, $N, habla con mi abuela. Si alguien puede encontrar una manera de unirme con Maybell, es ella.$B$BElla está dentro de nuestra casa, al este de aquí.",
    ["O"] = "Habla con la Abuela Pedregosa.",
    ["T"] = "Hablar con la Abuela",
  },
  [112] = {
    ["D"] = "Puedo hacer un licor de invisibilidad para Maybell, para que pueda escapar de los Viñedos Maclure e ir con Tommy Joe. Pero para preparar el licor, necesito un poco de alga cristalina.$B$BAunque el alga generalmente crece en el océano... a veces los múrlocs la recolectan. Ve a ver si los múrlocs cerca del Lago Cristal tienen algo. El Lago Cristal está justo al este de Villadorada.",
    ["O"] = "Lleva 4 Frondas de Alga Cristalina a William Mortero en Villadorada.",
    ["T"] = "Recolectando Algas",
  },
  [113] = {
    ["D"] = "El Perito jefe Picoflojo necesita ver este informe de inmediato. En realidad... todos los que puedan leer necesitan ver este informe. Nos enfrentamos a una amenaza grave y seria no solo para Tanaris y Gadgetzan, sino que, si no se controla, para todo el sur de Kalimdor. Diablos, incluso es posible que para todo Azeroth, esta cosa no ha dado indicios de que vaya a disminuir.$B$B¡Haz que Picoflojo haga una copia del informe para que lo distribuyas libremente a tu aliados! ¡Todos deben saber sobre esta amenaza!",
    ["O"] = "Entrega el informe al Perito jefe Picoflojo en Gadgetzan.  Asegúrate de que te dé una copia del informe, como lo ha solicitado el Boticario Morterozugg.",
    ["T"] = "Análisis de Partes de Insecto",
  },
  [114] = {
    ["D"] = "Llévale este licor de invisibilidad a la joven Maybell. Debería durar lo suficiente para que visite a Tommy Joe.",
    ["O"] = "Lleva el Licor de Invisibilidad a Maybell Maclure.",
    ["T"] = "La Escape",
  },
  [115] = {
    ["D"] = "Los orcos Roca Negra reclutaron Taumaturgos Umbríos para ayudarles en sus ataques en Crestagrana, y han traído consigo artefactos de poder oscuro: orbes de medianoche. Estos orbes han asestado golpes contundentes contra los defensores de Crestagrana, y es imperativo que eliminemos los objetos mancillados por demonios del conflicto.$B$BEncuentra y entrégame orbes de medianoche de los Taumaturgos Umbríos Roca Negra asesinados. Luego los desecharé, ¡porque este mundo sería un lugar mejor sin ellos!",
    ["O"] = "Lleva 3 Orbes de Medianoche al Mariscal Marris en Villa del Lago.",
    ["T"] = "Magia de las Sombras",
  },
  [116] = {
    ["D"] = "Me encuentro en un aprieto aquí, $N. Las botellas están a punto de agotarse. El último envío de alcohol está muy retrasado. La invasión de los orcos ha sido un infierno.$b$b¿Quizás puedas trabajar para mí?$b$bNecesito que recojas un barril de Cerveza Cebatruenos de Tragoamargo Cebatruenos en las colinas de Páramos de Poniente, un tonel de Merlot de Ventormenta, una botella de licor de luna de Villa Oscura y un odre de Ron Dulce de Villadorada. Tráemelos y me encargaré de que recibas tu recompensa.",
    ["O"] = "El tabernero Daniels de Villa del Lago necesita un barril de Cerveza Cebatruenos, un tonel de Merlot, una botella de licor de luna y un odre de Ron Dulce.",
    ["T"] = "Tiempos Secos",
  },
  [117] = {
    ["D"] = "Date prisa, amigo mío, muévete con rapidez.$B$BPara que nuestra rica cerveza sepa$B$B Más a cerveza y no a estofado,$B$Bse necesitan lúpulos para la cerveza.",
    ["O"] = "Lleva a Tragoamargo Cebatruenos 5 lúpulos para completar su cerveza especial.",
    ["T"] = "Cerveza de Trueno",
  },
  [118] = {
    ["D"] = "He estado usando tanto hierro para equipar a los guardias de Ventormenta que no me queda suficiente para herrar a los caballos del establo.$B$BLleva esta nota al herrero Argus en Villadorada. En ella le explico mi problema y le pido un envío de herraduras.",
    ["O"] = "Lleva la Nota de Verner al Herrero Argus en Villadorada.",
    ["T"] = "El Precio de los Zapatos",
  },
  [119] = {
    ["D"] = "Vale, le daré sus 50 herraduras...$B$B¡Aquí están, todas empaquetadas y listas para llevar! Dile a Verner que fue un placer hacer negocios con él, ¡y asegúrate de que lea la nota adjunta a este cajón!",
    ["O"] = "Regresa con Verner Osgood en Crestagrana. Entrégale la caja de Herraduras.",
    ["T"] = "Regreso con Verner",
  },
  [120] = {
    ["D"] = "Estos son tiempos difíciles, $c. El pueblo está bajo asedio constante. Sin refuerzos, seguramente enfrentaremos la derrota. El mensaje que ahora te confío es de suma importancia. Lleva este informe al General Marcus Jonathan de Ventormenta de inmediato. Una vez que hayas lo hayas entregado, regresa a mí de inmediato con cualquier noticia, sea buena o mala.$b$b¡Ahora date prisa!",
    ["O"] = "El Magistrado Solomon te ha dado un informe que debe ser entregado al General Marcus Jonathan en Ventormenta. El juez quiere que regreses con él tan pronto como se haya realizado la entrega.",
    ["T"] = "Mensajero a Ventormenta",
  },
  [121] = {
    ["D"] = "Mientras espero una respuesta del Rey, quiero que lleves esta carta al Magistrado Solomon. $b$b¡Retírate, $c!",
    ["O"] = "Lleva la carta de respuesta del General Marcus Jonathan al Magistrado Solomon en Villa del Lago.",
    ["T"] = "Mensajero a Ventormenta",
  },
  [122] = {
    ["D"] = "Necesito algunas escamas del vientre de las crías de dragón negro para pagar los zapatos que Argus me envió desde Villadorada. Si puedes conseguir 6 de ellas para mí, entonces puedo pagarle a Argus con algunas... y tener suficientes para hacer algo para ti.$B$BLas crías de dragón negro a menudo vuelan al sur de Villa del Lago, pero les gusta vagar. Puede que tengas que estar atento y cazarlos cuando los veas.",
    ["O"] = "Reúne 6 escamas de cría de dragón negro y llévaselas a Verner Osgood en Crestagrana.",
    ["T"] = "Escamas del Vientre",
  },
  [123] = {
    ["D"] = "Esta nota es un horario con una lista de días y horas en que una persona, descrita solo como \"El Coleccionista\", recibirá un cargamento de oro de las minas en el Bosque de Elwynn.$B$BSegún el horario, parece que el Coleccionista reside cerca del Campo de Calabazas de Brackwell en el este de Elwynn.$B$BEsto suena importante. Deberías informárselo al Mariscal Dughan en Villa del Lago.",
    ["O"] = "Ve con el Mariscal Dughan en Villadorada y entrégale El Horario del Coleccionista.",
    ["T"] = "El Coleccionista",
  },
  [124] = {
    ["D"] = "¡Como si los orcos atacantes no fueran suficiente! Ahora tengo a los brutos y místicos gnolls merodeando por la cresta al norte de mi establo, llevándose mis caballos cuando se alejan...$B$BSi puedes deshacerte de esos gnolls, mis caballos y yo estaríamos muy agradecidos.",
    ["O"] = "Mata 10 Brutos Crestagraña y 8 Místicos Crestagraña, luego regresa con Verner Osgood.",
    ["T"] = "El Aullido de los Gnolls",
  },
  [125] = {
    ["D"] = "Realmente podría usar una mano aquí, $N. Con el pueblo bajo asedio, ha sido difícil conseguir suministros. Mis herramientas venían desde Villadorada en carro, pero el puente fue destruido. Pusimos las herramientas en un bote, pero los orcos lo golpearon con una catapulta. ¡Que mala suerte, mi caja de herramientas se hundió hasta el fondo del lago!$B$BRecupera mi caja de herramientas, $N, y te recompensaré generosamente.",
    ["O"] = "El Supervisor Olento de Villa del Lago quiere que recuperes su caja de herramientas del fondo del Lago Sempiterno.",
    ["T"] = "Las Herramientas Perdidas",
  },
  [126] = {
    ["D"] = "$N, los gnolls todavía están ahí afuera. Puedo escuchar sus aullidos mientras cazan en las colinas sobre Villa del Lago. Y hay un grito que más fuerte que los demás: Ululante, su líder.$B$BMata a Aullador y dispersa a su manada. Tráeme su garra como prueba.$B$BY ten cuidado, este gnoll es su líder por una razón. No es un mestizo cualquiera...",
    ["O"] = "Lleva la Garra de Ululante a Verner Osgood en Villa del Lago.",
    ["T"] = "Aullidos en las Colinas",
  },
  [127] = {
    ["D"] = "El Lago Sempiterno es famoso por sus peces luna moteados. ¡Siempre hay demanda para ellos... y me estoy quedando sin existencias!$B$BTráeme un lote de 10 y negociaré bien por ellos. Si no sabes pescar, entonces... ¡quizás puedas conseguirlos de los múrlocs!",
    ["O"] = "Lleva 10 Peces luna moteados al Maestro de Embarcadero Baren en Villa del Lago.",
    ["T"] = "Venta de Pescado",
  },
  [128] = {
    ["D"] = "El Clan Roca Negra debe ser destruido. El Enemigo ha invadido nuestras tierras. Estas montañas están bajo la soberanía del rey de Ventormenta. Nuestras fuentes nos dicen que los líderes de cada unidad dentro del Clan son conocidos como Campeones. Los encontrarás en su campamento al norte, justo al oeste del camino a las Estepas Ardientes.$B$B El magistrado Solomon quiere ver muertos a esos brutales monstruos. Mata a 15 Campeones Roca Negra y regresa conmigo.",
    ["O"] = "Mata 15 Campeones de Roca Negra y el Guardia Howe en Villa del Lago te recompensará.",
    ["T"] = "Recompensa de Roca Negra",
  },
  [129] = {
    ["D"] = "¿Puedes hacerme un favor? He preparado un almuerzo para el Guardia Parker, pero está de patrulla... es un guardia de Ventormenta grande y fuerte que puede defenderse solo, pero es demasiado peligroso ahí fuera para una ciudadana como yo.$B$B¡Así que si le entregas su almuerzo y vuelves aquí, te daré un almuerzo gratis! Parker patrulla el tramo de camino que lleva a Bosque del Ocaso.",
    ["O"] = "Lleva el almuerzo de Parker al Guardia Parker. Patrulla el camino que lleva a Villa Oscura.",
    ["T"] = "Un Almuerzo Gratis",
  },
  [130] = {
    ["D"] = "Antes de que regreses con Darcy, ¿podrías llevarle algunas flores de mi parte? Puedes conseguirlas en la herboristería en Villa del Lago, en el extremo oeste del pueblo.$B$BPero cuando consigas las flores... no le digas a la herbolaria, Martie, de quién son o para quién son...",
    ["O"] = "Habla con la Herbolaria de Crestagraña, Martie Jainrose.",
    ["T"] = "Visita al Herborista",
  },
  [131] = {
    ["D"] = "Aquí tienes tu ramo. Elegí Narcisos para ti. ¡Son mis favoritos!",
    ["O"] = "Dale el Ramo de Narcisos a Darcy.",
    ["T"] = "Entrega de Narcisos",
  },
  [132] = {
    ["D"] = "Lo que estoy a punto de contarte podría costarme la vida. La banda Defias está tramando algo grande. Lo último que escuché es que estaban colaborando con varios gnolls, kóbolds e incluso goblins.$B$BLleva esta nota a Mantorrecio. Explica todo lo que sé sobre el tema.",
    ["O"] = "Lleva la Nota de Wiley a Gryan Mantorrecio en los Páramos de Poniente.",
    ["T"] = "La Hermandad Defias",
  },
  [133] = {
    ["D"] = "Vivo tan lejos de la protección de la ciudad, que es un milagro que los necrófagos y los muertos vivientes no me hayan comido. De hecho, ¡ayer mismo una manada de Masticahuesos estaba golpeando las paredes de mi casa!$B$BQuiero hacer un muñeco, una especie de \"espantapájaros\" para necrófagos. Para eso necesito algunas costillas de necrófago. Puedes conseguirlas de Comecarnes, Mascahuesos, Comesesos, Podridos y Propagadores de la peste.$B$BConsígueme las costillas y te recompensaré con algo de dinero.",
    ["O"] = "Reúne 7 Costillas de Necrófago y llévaselas a Abercrombie en su choza.",
    ["T"] = "Un Espantapájaros para Necrófagos",
  },
  [134] = {
    ["D"] = "Hace unas semanas estaba recolectando hierbas lejos de mi casa, y una banda de ogros me atacó. ¡Huí y me vi obligado a dejar atrás una caja de herramientas y hierbas preciosas!$B$BDespués de ahuyentarme, los ogros regresaron pavoneándose al montículo de los ogros, en el sur de Bosque del Ocaso. Estoy seguro de que mi caja está cerca del montículo.$B$BPor favor, $N, recupera esta caja para mí, la extraño mucho.",
    ["O"] = "Devuelve la caja de Abercrombie a Abercrombie.",
    ["T"] = "Ladrones Ogros",
  },
  [135] = {
    ["D"] = "Me pregunto qué quiso decir Wiley cuando mencionó a los Albañiles. Tal vez fue un desliz. ¿Podría la banda Defias estar relacionada con los albañiles? Solo un hombre lo sabría con certeza: Mathias Shaw, jefe de SI:7. Muéstrale la Nota de Wiley y ve si tiene algo que agregar a este misterio cada vez mayor. Si tienes problemas para encontrar a Shaw, revisa los Cuarteles en el Casco Antiguo.",
    ["O"] = "Lleva la Nota de Wiley a Mathias Shaw en Ventormenta.",
    ["T"] = "La Hermandad Defias",
  },
  [136] = {
    ["D"] = "Si estás leyendo esto, significa que el viejo Capitán Sanders está en una tumba acuática. Así que mi tesoro es tuyo ahora, solo necesitas seguir las pistas. $b$bPrimero necesitas encontrar mi baúl. Probablemente esté medio enterrado en la arena por ahora, a lo largo de la Costa Occidental de Páramos de Poniente cerca del naufragio. Hay muchos naufragios, pero solo un ancla oxidada en la costa. ¡Encuentra ese ancla y encontrarás mi baúl! Allí encontrarás también la siguiente pista",
    ["O"] = "Encuentra el baúl del Capitán Sanders y regístralo para encontrar la siguiente pista.",
    ["T"] = "El Tesoro Escondido del Capitán Sanders",
  },
  [137] = "_",
  [138] = {
    ["D"] = "La pista del tesoro dice: ¡Buen trabajo, amigo! Ahora debes dirigirte hacia el Este. Al Este hacia los riscos, al Este hacia el camino. Busca las ruinas de chimeneas a orillas del camino. Allí encontrarás un viejo barril con la siguiente pista.",
    ["O"] = "Encuentra el viejo barril cerca de la chimenea en ruinas y búscalo para encontrar tu próxima pista.",
    ["T"] = "El Tesoro Escondido del Capitán Sanders",
  },
  [139] = {
    ["D"] = "Al buscar en el barril, descubres otro trozo de pergamino. En este se puede leer: Desde este barril, ve hacia el Norte. Tan recto como vuela el cuervo, anda hasta que veas una jarra vacía junto al solitario molino de viento sobre los riscos del mar. Si buscas alrededor de la jarra encontrarás lo que estás buscando.",
    ["O"] = "Busca la siguiente pista en la jarra vacía que hay junto al molino de viento.",
    ["T"] = "El Tesoro Escondido del Capitán Sanders",
  },
  [140] = {
    ["D"] = "Seguro que dentro de la Vieja Jarra hay otra pista del tesoro de Sander. La tinta se ha corrido en algunos lugares y el papel huele a whisky, pero puedes distinguir parte del texto: ¡Ahora que has encontrado mi vieja jarra de whisky, estás cerca del tesoro! Solo mira hacia el oeste desde la botella y camina hacia la orilla. Una vez que llegues al agua, ¡sigue adelante! Nada directamente hacia el oeste hasta que encuentres la isla con mi cofre del tesoro!",
    ["O"] = "Localiza el cofre del Capitán Sanders y ábrelo para obtener tu recompensa.",
    ["T"] = "El Tesoro Escondido del Capitán Sanders",
  },
  [141] = {
    ["D"] = "El Gremio de Canteros estaba dirigido por un hombre llamado Edwin VanCleef. VanCleef fue responsable de reconstruir Ventormenta después de que los orcos la arrasaran en la Primera Guerra. Aparentemente, VanCleef y sus hombres estaban descontentos con el trato que recibieron del Rey después de que se completó la reconstrucción. Eso podría explicar una cosa o dos.$B$BHe escrito un relato más detallado para tu Maestro en Westfall. ¡Llévaselo de inmediato!",
    ["O"] = "Lleva el informe de Shaw a Gryan Mantorcia en los Páramos de Poniente.",
    ["T"] = "La Hermandad Defias",
  },
  [142] = {
    ["D"] = "Necesitamos descubrir la ubicación del escondite de los Defias. $N, mi explorador informa que se ha visto a un Mensajero Defias en los caminos entre Molino Ámbar, la Cantera de la Costa del Oro y la Mina de Jangolode. Quiero que lo captures. Si se resiste, mátalo y tráeme lo que lleve.",
    ["O"] = "Rastrea al Mensajero de los Defias en los Páramos de Poniente y lleva su mensaje a Mantorrecio.",
    ["T"] = "La Hermandad Defias",
  },
  [143] = {
    ["D"] = "Algo extraño está pasando con el Ejército de Ventormenta. Deberían haber estado aquí en masa para ahora. Sin embargo, el tiempo es un lujo que no tenemos. No me quedaré de brazos cruzados y veré cómo la gente de Villa del Lago da sus vidas sin intentar reclutar más ayuda. He oído rumores de que se está formando una milicia popular en el Páramo Occidental.$b$bLleva esta súplica a su líder, Gryan Stoutmantle. Quizás los hombres de Stoutmantle puedan brindar algún apoyo aquí hasta que el Rey envíe refuerzos.",
    ["O"] = "El Magistrado Solomon quiere que lleves su súplica escrita a Gryan Mantorrecio en los Páramos de Poniente.",
    ["T"] = "Mensajero a Poniente",
  },
  [144] = {
    ["D"] = "La nota del Magistrado Solomon me duele leerla. Pero es obvio que no conoce la guerra que se libra en Poniente, o sabría que es mejor no esperar ayuda de la Milicia. Si Ventormenta no nos hubiera abandonado también, no tendríamos la necesidad de la Milicia.$B$BLleva esta respuesta a tu maestro en Crestagrana, $c. Y hazle saber que mi corazón está pesado por la pérdida de buenos hombres.",
    ["O"] = "Regresa con el Magistrado Solomon con la respuesta de Gryan Mantorrecio.",
    ["T"] = "Mensajero a Poniente",
  },
  [145] = {
    ["D"] = "Necesito tu ayuda una vez más. He escrito a Lord Cerranegro para pedir que mande a su Guardia Nocturna como refuerzo de la defensa de Villa del Lago. Es imprescindible que reciba este mensaje cuanto antes, pero debes ser extremadamente prudente. Cerranegro es el alcalde de Villa Oscura, en pleno corazón del Bosque del Ocaso. No te salgas de los caminos.",
    ["O"] = "El Magistrado Solomon quiere que lleves una carta a Lord Cerranegro en Villa Oscura.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [146] = {
    ["D"] = "Si no conociera al Magistrado Solomon, diría que se ha vuelto loco. ¿Que envíe a La Guardia Nocturna a Villa del Lago porque Ventormenta no envía ayuda? La Guardia Nocturna se creó porque Ventormenta nos abandonó cuando nos infestó esta magia inmunda. Si envío a la Guardia, esta ciudad sería invadida antes de que mis guardias lleguen al Linde del Bosque.$b$bLlévale la respuesta a tu señor en Villa del Lago.",
    ["O"] = "Regresa con el Magistrado Solomon con la carta de respuesta de Lord Cerranegro.",
    ["T"] = "Mensajero a Villa Oscura",
  },
  [147] = {
    ["D"] = "Si el \"Recolector\" está tomando oro de nuestras minas, ¡entonces está robando al reino! Trae al Recolector ante la justicia y tráeme el anillo mencionado en el programa de recolección que me diste. Puede decirnos para quién trabaja el Recolector...$B$BEse Programa de Recolección dice que el Recolector se esconde en el Campo de Calabazas de Brackwell. Deberías buscarlo allí.",
    ["O"] = "Encuentra y mata a \"El Coleccionista\", luego regresa con el Mariscal Dughan con el Anillo del Coleccionista.",
    ["T"] = "Cacería Humana",
  },
  [148] = {
    ["D"] = "Oh, ¿qué puede hacer un pobre anciano? Necesito algunos suministros de Villa Oscura, pero el pueblo está tan lejos y yo soy tan viejo y débil... Nunca podría hacer el viaje.$B$B¿Podrías ir a Villa Oscura y traerme un poco de Hilo de Cabello Fantasma? Puedes conseguirlo de Madame Eva en su casa en la plaza de Villa Oscura.",
    ["O"] = "Habla con Madame Eva.",
    ["T"] = "Suministros de Villa Oscura",
  },
  [149] = {
    ["D"] = "Hay un espíritu pobre y triste, la Ciega Mary, que acecha una vieja casa de campo en las colinas al sur de Villa del Lago.$B$BAquí, toma este Peine Espectral y pídele a la Ciega Mary que se peine el cabello. Luego tráeme el peine y obtendré el Cabello Fantasma que necesitaré para el hilo.$B$BY ten cuidado, porque cosas oscuras ahora acechan cerca de esa casa...",
    ["O"] = "Lleva el Peine Espectral a Mary la Ciega.",
    ["T"] = "Hilo de Cabello Fantasma",
  },
  [150] = {
    ["D"] = "Los murlocs se están convirtiendo en un problema. Grupos más grandes de estos hombres pez están anclando a lo largo de las costas del Lago Sempiterno y ¡pescando una tormenta! ¡Necesitamos que desaparezcan antes de que se coman todos los peces del lago!$B$B¡Caza murlocs! Tráeme ocho de sus aletas y podría tener algo para ti...",
    ["O"] = "Lleva 8 Aletas de Múrloc a Jefe del Muelle Baren en Villa del Lago.",
    ["T"] = "Furtivos Murlocs",
  },
  [151] = {
    ["D"] = "¡Pobre Blanchy! Es una bestia tan cansada después de todo el trabajo que le hemos hecho. La alimenté antes de salir de la granja, pero no esperábamos que el carro se rompiera. Si pudieras traerle unos puñados de avena de los campos, te lo agradecería.$B$BApuesto a que podrías encontrar algo alrededor de todas las granjas en el Páramo Occidental, si puedes evitar esas horribles máquinas que han tomado el control. Hay varias granjas al suroeste de aquí.",
    ["O"] = "Verna Furlbrow en Páramos de Poniente quiere que le lleves 8 Puñados de Avena.",
    ["T"] = "El Pobre Viejo Blanchy",
  },
  [152] = {
    ["D"] = "Probablemente hayas notado todos los naufragios a lo largo de la costa. El Gran Mar es traicionero, de hecho. La costa de Poniente debe mantenerse despejada, para que si los marineros encuentran su camino a nuestras playas, estén a salvo. Los múrlocs son un problema, sin embargo.$b$bMata a 7 Cazadores de la marea, 7 Guerreros, 7 Oráculos y 7 Correcostas y me aseguraré de que seas recompensado.",
    ["O"] = "Mata 7 Cazadores de Mareas, 7 Guerreros, 7 Oráculos y 7 Costeros y regresa con el Capitán Grayson en el Faro de Poniente.",
    ["T"] = "La Costa no está Clara",
  },
  [153] = {
    ["D"] = "El frente de los Defias está en constante cambio. He estado siguiendo sus movimientos durante bastante tiempo. Como nota al margen, he determinado que muchos miembros de la banda pueden ser rastreados por las Bandanas de Cuero Rojo que usan.$B$BTráeme 15 de estas bandanas y me aseguraré de que seas recompensado.",
    ["O"] = "Lleva 15 Pañuelos de Cuero Rojo al Explorador Galiaan en Cerro del Centinela.",
    ["T"] = "Pañuelos de Cuero Rojo",
  },
  [154] = {
    ["D"] = "¡NO!!!!!$B$B¡Tómalo, quítame este peine!$B$B¡Soy un monstruo, y nadie volverá a encontrarme hermoso!",
    ["O"] = "Devuelve el Peine de Cabello Fantasmal a Madame Eva en Villa Oscura.",
    ["T"] = "Devolver el Peine",
  },
  [155] = {
    ["D"] = "¿Así que Stoutmantle envía a un $r enclenque como tú para protegerme? Supongo que tendrás que servir. Mejor trae algunos amigos también. $b$bConoces el trato, ¿verdad? Vigilas mi espalda y yo te llevaré al escondite de los Defias. Pero mejor mantente cerca de mí. La banda Defias quiere mi cabeza ahora. Si me ven contigo, intentarán matarme.$b$bAvísame cuando tú y cualquier amigo que puedas reunir estén listos para partir.",
    ["O"] = "Escolta al Traidor Defias hasta el escondite secreto de la Hermandad Defias. Una vez que el Traidor Defias te muestre dónde se esconde VanCleef y sus hombres, regresa con Gryan Mantorrecio con la información.",
    ["T"] = "La Hermandad Defias",
  },
  [156] = {
    ["D"] = "Si me traes algunas flores de pútrido, puedo prepararte un lote de jugo de zombi.$B$BLas flores de pútrido crecen en lugares extraños. En particular, escuché que puedes encontrarlas dentro de los cráneos de los Horrores Esqueléticos y los Fiendes Esqueléticos... ¡no que me haya acercado lo suficiente para mirar!$B$BEl Cementerio de la Colina del Cuervo y los Jardines Tranquilos están llenos de esos esqueletos. Puedes deshacerte de ellos mientras buscas flores de pútrido. El pueblo de Villa Oscura estaría agradecido.",
    ["O"] = "Reúne 8 Flores Pútridas y llévaselas al Tabernero Smitts en Villa Oscura.",
    ["T"] = "Recoger Flores Putrefactas",
  },
  [157] = {
    ["D"] = "Aquí tienes tu hilo, $N. Tiene mil usos, desde atar espíritus malignos hasta dar vida a objetos inanimados.",
    ["O"] = "Lleva el hilo de cabello fantasma a Abercrombie, a su choza al norte del Cementerio del Colina del Cuervo.",
    ["T"] = "Entregar el Hilo",
  },
  [158] = {
    ["D"] = "Necesito una botella de un licor en particular: Jugo de Zombi. El tabernero Smitts en la Taberna del Cuervo Escarlata en Villa Oscura debería tener algo.$B$B¡Y por favor, date prisa, $N! Estas noches se ponen frías, y necesito ese trago para volver a encender el fuego en mis venas.",
    ["O"] = "Habla con el Tabernero Smitts.",
    ["T"] = "Jugo de Zombi",
  },
  [159] = {
    ["D"] = "Aquí está tu Jugo de Zombi...$B$B...y ten cuidado con esto. ¡Es lo suficientemente fuerte como para resucitar a los muertos!",
    ["O"] = "Lleva el Jugo de Zombi a Abercrombie en su choza.",
    ["T"] = "Entrega de Jugo",
  },
  [160] = {
    ["D"] = "Llévale esta nota a Ello Cerranegro, el Alcalde de Villa Oscura.$B$BÉl y yo somos viejos amigos... y cuando la lea y descubra lo que hiciste por mí... ¡estoy seguro de que estará tan feliz como yo! ¡Jajajaja!",
    ["O"] = "Lleva la Carta de Abercrombie a Ello Cerranegro, Alcalde de Villa Oscura.",
    ["T"] = "Nota para el Alcalde",
  },
  [161] = {
    ["D"] = "Si mis sospechas son correctas, esto es algún tipo de polvo explosivo. Esto debe ser analizado por un experto en explosivos para que sepamos con qué estamos lidiando aquí. Ashlan Risapétrea fue asignado al regimiento que custodia Dun Modr y el Puente Thandol. Necesito que hagas un viaje grave y peligroso, $N.$b$bViaja a través de la Puerta de Algaz, sigue el camino a través de los Humedales y busca a Risapétrea en Dun Modr. Sigue mi consejo: quédate en los caminos y no te detengas por nada.",
    ["O"] = "El Ingeniero Jefe Hinderweir quiere que lleves el polvo de olor extraño a Ashlan Risapétrea, el experto en explosivos en Dun Modr.",
    ["T"] = "Una amenaza oscura se cierne",
  },
  [162] = {
    ["D"] = "Te insto a llevar este informe a alguien que tenga conocimientos prácticos en este tipo de cosas.$B$BConozco a una erudita elfa de la noche llamada Gracina Poderespíritu. ¡Ha pasado mucho tiempo estudiando formas de vida únicas en la ecología de Azeroth, además, es historiadora! Si quieres que alguien te ayude a descubrir exactamente cuál es esta amenaza y, lo más importante, cómo detenerla, entrégale el informe. Necesitaremos la ayuda.$B$BLa última vez que supe, residía en el Templo de la Luna, ubicado en Darnassus.",
    ["O"] = "Lleva el Informe de Análisis de Insectos a Gracina Poderespíritu en Darnassus.",
    ["T"] = "El Ascenso de los Silítidos",
  },
  [163] = {
    ["D"] = "Algo anda mal en Colina del Cuervo, $n. Calor jura que alguien, o algo, está acechando los edificios allí. Durante dos noches seguidas ha notado que las sombras se mueven, y la tercera noche antes de eso dice que vio luz en una de las ventanas.$b$bSi, como dices, te importa la seguridad de Bosque del Ocaso, descubre qué monstruo acecha en Colina del Cuervo.",
    ["O"] = "Averigua qué está acechando en la Colina del Cuervo.",
    ["T"] = "Colina del Cuervo",
  },
  [164] = {
    ["D"] = "¿Conoces a Sven? ¡Ahora, ahí hay un hombre que hace el trabajo de la Luz! Él y sus seguidores mantienen un campamento en el borde noroeste de Bosque del Ocaso.$B$BDesafortunadamente, como son tan pocos, ni siquiera pueden permitirse enviar a alguien a buscar suministros en la ciudad. ¿Podrías llevar estos suministros a él?",
    ["O"] = "Entrega los suministros a Sven.",
    ["T"] = "Entregas para Sven",
  },
  [165] = {
    ["D"] = "Había un anciano que solía venir al pueblo a comprar suministros con bastante frecuencia, pero no lo he visto desde hace bastante tiempo. Vive en una cabaña con vista al cementerio de Colina del Cuervo, si no recuerdo mal. Quizás deberías ir a ver si algo anda mal.",
    ["O"] = "Revisa al anciano en la choza cerca del cementerio de Colina del Cuervo.",
    ["T"] = "El Ermitaño",
  },
  [166] = {
    ["D"] = "Hay una tarea más que debes completar. Edwin VanCleef debe ser asesinado. Aunque me entristece condenar a cualquier hombre a una sentencia de muerte, es por el bien mayor de la gente de Páramos de Poniente que VanCleef sea puesto a descansar de una vez por todas. Tráeme la cabeza del villano una vez que se haya cumplido la tarea.",
    ["O"] = "Mata a Edwin VanCleef y lleva su cabeza a Gryan Mantorrecio.",
    ["T"] = "La Hermandad Defias",
  },
  [167] = {
    ["D"] = "Estábamos en las profundidades de una gran mina en los Páramos de Poniente, oculta tras un granero en el Arroyo de la Luna. No tenemos ni idea de dónde salieron esos ladrones. En fin, el túnel se derrumbó sobre nosotros. Conseguí salir, pero los otros... bueno... de los otros no sé nada.$b$bPareces tener más suerte que yo allí. Si pudieras buscar a mi hermano, te lo agradecería. Siempre llevaba su Insignia de la Liga de Expedicionarios. Si no puedes encontrarlo, al menos esa insignia me daría un poco de paz mental.",
    ["O"] = "Lleva la Insignia de la Liga de Expedicionarios de Capataz Cardortiga a Wilder Cardortiga en Ventormenta.",
    ["T"] = "Oh, Hermano...",
  },
  [168] = {
    ["D"] = "Muchos de mis amigos perecieron ese horrible día cuando el túnel de la mina se derrumbó. Si tus aventuras te llevan a esa parte de la mina en Arroyo de la Luna, por favor mantén los ojos abiertos en busca de cualquier señal que pueda identificarlos. Si encuentras alguna de sus Tarjetas del Sindicato de Mineros, tráelas de vuelta a mí y me aseguraré de que sus familias obtengan algún cierre de este horrible accidente.",
    ["O"] = "Recupera 4 tarjetas del Sindicato de Mineros y devuélveselos a Wilder Cardortiga en Ventormenta.",
    ["T"] = "Recolectando Recuerdos",
  },
  [169] = {
    ["D"] = "¡Se busca! Gath\'Ilzogg, líder de los ataques de Roca Negra sobre el Torreón de la Guardia del Orden y las incursiones contra el pueblo de Villa del Lago. $b$bGath\'Ilzogg es extremadamente peligroso. Se informa que está escondido en el torreón recientemente conquistado, planeando más crímenes del clan Roca Negra contra la humanidad. La infestación orca debe ser sofocada a toda costa. Se ofrecerá una generosa recompensa a cualquier alma valiente que pueda entregar la cabeza de Gath\'Ilzogg al Magistrado Solomon.",
    ["O"] = "Mata a Gath\'Ilzogg y lleva su cabeza al Magistrado Solomon en Villa del Lago para recibir la recompensa.",
    ["T"] = "Se Busca: Gath\'Ilzogg",
  },
  [170] = {
    ["D"] = "Espero que estés aquí para echarnos una mano, $c. Después del último ataque de los troggs, podríamos usar toda la ayuda posible.$b$bEscuché que los bichos han estado apareciendo por todas partes, y parece que el Valle de Crestanevada no es una excepción. Han sido vistos por todas las colinas al sureste y cerca del lago helado.$b$b¡Y eso no es todo, hace unas noches atacaron y abrumaron nuestro campamento al oeste! $b$bEstamos un poco escasos de personal aquí, $g chico:chica;, y necesitamos brazos fuertes para ayudar a repeler a los troggs.",
    ["O"] = "Balir Martilloescarcha quiere que mates a 6 Troggs Comepiedra y 6 Troggs Comepiedra fornidos.",
    ["T"] = "Una Nueva Amenaza",
  },
  [171] = {
    ["D"] = "Has sido muy amable conmigo, $N. Nunca soñé que me divertiría tanto durante la Semana de los Niños. Gracias. No quiero ponerme triste por lo que me pasó a mí y a todos nosotros, mis amigos siempre me dicen que debo sobreponerme a todo lo malo. Pero ya sabes, cuando crezca seré un héroe... como tú.",
    ["O"] = "Devuelve a tu pupilo al orfanato de Ventormenta entregando el Silbato de Huérfano Humano a la Supervisora de Huérfanos Ruiseñor en el Distrito de la Catedral.",
    ["T"] = "Alcaide de la Alianza",
  },
  [172] = {
    ["D"] = "¿Estás dispuesto a ayudarnos aquí en el orfanato? Bendito seas, amigo, tu ayuda durante la Semana de los Niños será invaluable.$B$BToma este silbato. Solo podrás usarlo por un corto período de tiempo, generalmente no más que la propia Semana de los Niños. Cuando lo uses, podrás hablar con el niño que has aceptado cuidar. Usarlo nuevamente despedirá al niño.$B$B¡Por todos los medios, $N, usa el silbato y conoce a tu pupilo! Estoy seguro de que el niño estará ansioso por conocerte.",
    ["O"] = "Usa el Silbato de Huérfano Orco para hablar con el niño que cuidarás durante la Semana de los Niños.",
    ["T"] = "Semana de los Niños",
  },
  [173] = {
    ["D"] = "La oscuridad parece atraída inexorablemente a Bosque del Ocaso. La misión del Maestro Carevin es la expulsión del mal y la herejía. A través de nuestros esfuerzos, la gente de Villa Oscura se mantiene a salvo.$b$b¿Crees que eres digno de unirte a nosotros?$b$bUna vez pensé como tú. Desilusionado por la complacencia de la Guardia, me uní al Maestro Carevin. Si deseas probarte, no será a través de palabras.$b$bPon a prueba tus habilidades contra los huargen Tejesombras Penumbria de la Arboleda del Destello—¡brillante, ja!—y El Vergel Pútrido.",
    ["O"] = "Mata 6 Tejesombras Penumbria para Calor en Villa Oscura.",
    ["T"] = "Huargen en el Bosque",
  },
  [174] = {
    ["D"] = "Mi amigo gnomo, Cog, ha prometido ayudarme a construir un dispositivo que me permita mirar hacia las estrellas. Existe una verdad, más allá de la magia, que puede explicar la extraña transformación en el bosque.$b$bTráeme un tubo de bronce hecho por un ingeniero hábil. Ese es el primer paso, según el plano de Cog.",
    ["O"] = "Viktori Prism\'Antras de Villa Oscura quiere que le lleves un tubo de bronce.",
    ["T"] = "Mirar a las Estrellas",
  },
  [175] = {
    ["D"] = "Según los planos de Cog, lo que necesito ahora es una forma de reflejar la luz. Recuerdo a una mujer llamada Mary que se hizo conocida por estos lugares por llevar siempre un espejo. Quizás puedas rastrear a Mary y ver si podría prestarnos una superficie reflectante para este proyecto. Creo que una vez vivió al sur de Bosque del Ocaso.",
    ["O"] = "Localiza a Mary y convéncela de que te dé una superficie reflectante para el proyecto de Viktori.",
    ["T"] = "Mirar a las Estrellas",
  },
  [176] = {
    ["D"] = "¡Se busca: Hogger!$B$BUn enorme gnoll, Hogger, está merodeando por los bosques al suroeste de Elwynn. Ha superado todos los intentos de captura.$B$BEl Ejército de Ventormenta ha puesto una generosa recompensa por el gnoll. Para ganar la recompensa, los cazadores de recompensas deben traer prueba de la muerte de Hogger al Mariscal Dughan en Villadorada.",
    ["O"] = "Mata al gnoll Hogger y lleva su Garra de Gnoll Enorme al Mariscal Dughan.",
    ["T"] = "Se Busca: \"Hogger\"",
  },
  [177] = {
    ["D"] = "El espejo era uno de mis tesoros más preciados hasta... el incidente. Después de eso, lo deseché detrás del granero. Había un ghoul demente que solía merodear por allí. Quizás lo recogió. Se rumorea que la bestia diabólica reside en el Cementerio de los Jardines Tranquilos.",
    ["O"] = "Recupera el Espejo de la Ciega Mary del Ghoul Demente y entrégaselo a Viktori en Villa Oscura.",
    ["T"] = "Mirar a las Estrellas",
  },
  [178] = {
    ["D"] = "<Escuchas una voz incorpórea...>$B$BNo te alarmes. Soy Theocritus, Alto Mago de la Torre Azora en el Bosque de Elwynn. El colgante que sostienes es un método de comunicación entre los Gnolls Pieloscura y su maestro, Morganth.$B$BTras meses de investigación, creo que yo también puedo comunicarme a través de estos colgantes. Si puedes escuchar este mensaje, entonces mi hechizo fue un éxito.$B$BTráeme este colgante y te recompensaré por el servicio.",
    ["O"] = "Lleva el Colgante Sombrío Descolorido a Theocritus el Mago.",
    ["T"] = "Recuperación de Theocritus",
  },
  [179] = {
    ["D"] = "¿Qué tenemos aquí? Parece que podrías necesitar algo para mantener tus manos calientes, ¿eh?$B$BTe diré qué ayudaría: un par de guantes bonitos y cálidos. Y, siendo el alma amable que soy, estaría más que feliz de proporcionarte un par adecuado. Sin embargo, tengo una condición.$B$BNecesito que vayas a conseguirme un poco de carne de lobo. Buen trato, ¿eh? Me traes un poco de carne de lobo, y me aseguraré de que no pierdas ningún dedo por congelación. Bueno, ¿qué dices?",
    ["O"] = "Sten Brazorrecio quiere 8 trozos de Carne de lobo resistente.",
    ["T"] = "Proveedores Enanos",
  },
  [180] = {
    ["D"] = "¡Se busca: Teniente Fangore!$b$bLíder conocido del clan de los gnolls Piel de Sombra, trabajando bajo el enemigo del Municipio y el Reino, Morganth. Por mandato del Magistrado Solomon, este vil traidor de Ventormenta es buscado muerto. Muestra su garra al Magistrado como prueba de su muerte.",
    ["O"] = "Mata al Teniente Fangore y regresa con el Magistrado Solomon en Villa del Lago con su garra.",
    ["T"] = "Se Busca: Teniente Fangore",
  },
  [181] = {
    ["D"] = "Ahora solo falta un artículo más para completar este dispositivo. El plano de Cog requiere una lente de algún tipo. La única lente que conozco que sería lo suficientemente grande será muy difícil de adquirir.$b$bHay un ogro llamado Zzarc\'Vul que reside en el montículo al sur de Bosque del Ocaso. Si puedes, tráeme su monóculo y lo usaré como nuestra lente.",
    ["O"] = "Localiza a Zzarc\'Vul en el túmulo ogro del sur en Bosque del Ocaso y devuelve su monóculo a Viktori en Villa Oscura.",
    ["T"] = "Mirar a las Estrellas",
  },
  [182] = {
    ["D"] = "Mi hermano Senir y yo fuimos enviados a diferentes partes de Dun Morogh para investigar la amenaza que representan los trolls. El Senado tiene las manos ocupadas con los troggs, así que no necesitan más molestias.$B$BPor lo que he visto, los trolls no están bien situados aquí en el Valle de Crestanevada, principalmente en la cueva del sur. Diría que el ejército no será necesario. Unos cuantos brazos fuertes deberían ser más que suficientes.$B$B¿Quizás te gustaría ayudar en este esfuerzo? Tengo la autoridad para ofrecerte una compensación por tu ayuda.",
    ["O"] = "Grelin Barbablanca quiere que mates 14 Crías de Troll Pellejofrío.",
    ["T"] = "La Cueva de los Trolls",
  },
  [183] = {
    ["D"] = "Nada como un día de caza de jabalíes, ¿eh?$b$bAunque aquí en el Valle de Crestanevada, hay tantos jabalíes que casi le quita la diversión. No hay necesidad de hacer que carguen. Ya están todos enojados y listos sin ninguna ayuda. De hecho, recientemente ha habido tantos jabalíes en el área que se ha vuelto peligroso para mí hacer mi caza diaria.$b$bEn resumen, si pudieras ayudarme a matar algunos de los jabalíes, te lo agradecería.",
    ["O"] = "Talin Ojoagudo quiere que mates a 12 Jabalíes de risco pequeños.",
    ["T"] = "El Cazador de Jabalíes",
  },
  [184] = {
    ["D"] = "Esta es la escritura de una extensión de tierras de cultivo dentro de Páramos de Poniente. Está firmada por un tal Theodore Furlbrow y refrendada por su esposa, Verna. Y en el reverso de la escritura hay palabras garabateadas apresuradamente:$B$B\"Presionamos a Furlbrow y obtuvimos su escritura. Pensamos que podría ser útil si quisieras falsificar una de estas para tu propio lugar.$B$BLos Furlbrow no nos darán problemas. La última vez que los vi, estaban saliendo de Páramos de Poniente, atascados con un carro roto.\"$B$BCrees que los Furlbrow podrían querer recuperar su escritura...",
    ["O"] = "Lleva el Título de Propiedad de Cejadelejo a Granjero Cejadelejo.",
    ["T"] = "La Escritura de Furlbrow",
  },
  [185] = {
    ["D"] = "Cuando era solo una niña, mi padre me entrenó en el arte de la caza de tigres.$B$BEncontrarás a los felinos jóvenes mucho más fáciles de rastrear y matar. Por esta razón, comenzaremos contigo fácilmente. A menudo se pueden encontrar tigres jóvenes cerca del campamento de la expedición.$B$BPon a prueba tus habilidades de rastreo y ve si puedes cazar algunas de estas bestias.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates 10 Tigres de Tuercespinos jóvenes.",
    ["T"] = "Maestría en Tigres",
  },
  [186] = {
    ["D"] = "Estás aprendiendo, $c. Probemos tus habilidades con los gatos maduros ahora. Mata a 10 Tigres de Tuercespina esta vez.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates 10 Tigres de Tuercespinos.",
    ["T"] = "Maestría en Tigres",
  },
  [187] = {
    ["D"] = "Ahora voy a subir la apuesta y realmente poner a prueba tu habilidad. Demuéstrame que puedes matar a 10 Tigres de Tuercespina Mayores. Si puedes hacer eso, estarás listo para tu desafío final antes de que te declare un maestro cazador de tigres en presencia del Maestro Nesingwary.$b$bLos Tigres de Tuercespina Mayores no solo son los más difíciles de encontrar, sino también los más feroces de derrotar.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates 10 Tigres de Tuercespinos mayores.",
    ["T"] = "Maestría en Tigres",
  },
  [188] = {
    ["D"] = "Aquí está el desafío final que te pondré. Hemos estado rastreando a un escurridizo tigre durante semanas. Lo llamamos Sin\'Dall. A ver si puedes hacer lo que ningún $r ha hecho antes: cazar y matar a Sin\'Dall. Tráeme su garra como prueba de tu hazaña.$b$bRastrearla no será tarea fácil.",
    ["O"] = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates a Sin\'Dall y regreses con su garra.",
    ["T"] = "Maestría en Tigres",
  },
  [189] = {
    ["D"] = "¡Malditos trolls! ¡Han estado matando a nuestros agentes en la jungla! Ya he perdido a un buen número de mis mejores guardias en escaramuzas con ellos. ¡Los costos se están acumulando! ¿Cómo voy a explicar esto al Barón Revilgaz?$b$bAcción decisiva... Piensa, Kebok... no te asignaron al puesto que tienes hoy por quedarte sentado... ¡Lo tengo! Tú... $n, ¿verdad? ¿Me ayudarás, no? ¡Hay dinero que ganar!$b$bAquí está el trato... tráeme las orejas de quince trolls Sangrapellejo, y te compensaré bien.",
    ["O"] = "Consigue 15 Orejas Sangrapellejo y llévaselas a Kebok en Bahía del Botín.",
    ["T"] = "Orejas Sangrapellejo",
  },
  [190] = {
    ["D"] = "Si quieres ser parte de la cacería con este grupo selecto que Hemet ha reunido, tendrás que demostrar que eres un cazador de panteras capaz. Te empezaremos fácil—no te preocupes. Veamos si puedes matar a 10 panteras jóvenes para empezar.$b$bRastrearlas es solo la mitad del desafío...",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary quiere que mates 10 Panteras Jóvenes.",
    ["T"] = "Maestría en Panteras",
  },
  [191] = {
    ["D"] = "Ahora estás listo para subir el nivel. Si quieres demostrar que eres digno de socializar con cazadores de caza mayor como estos, tendrás que demostrar que puedes matar a 10 panteras.$b$bEsos tipos grandes son más duros. No son tan fáciles como los jóvenes que estabas matando antes.",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary quiere que mates 10 Panteras.",
    ["T"] = "Maestría en Panteras",
  },
  [192] = {
    ["D"] = "Ahora la parte difícil. Un verdadero cazador de panteras puede demostrar su habilidad derribando a las Panteras Faucesombra, las más mortíferas de Tuercespina. Demuéstranos que puedes matar a 10 de estas bestias.$b$bSi crees que son difíciles de rastrear, espera a que intentes matar a una...",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary quiere que mates 10 Panteras Sombría.",
    ["T"] = "Maestría en Panteras",
  },
  [193] = {
    ["D"] = "Ya casi has demostrado ser un maestro cazador de panteras. Una pantera llamada Bhag\'thera ha estado merodeando por la jungla. Hasta ahora ha eludido a nuestro grupo. A ver si puedes usar tus habilidades para matar al gran Bhag\'thera.$b$bTráeme el Colmillo de Bhag\'thera y te habrás ganado mi respeto!",
    ["O"] = "Sir S. J. Erlgadin de la Expedición de Nesingwary quiere que le lleves el Colmillo de Bhag\'thera.",
    ["T"] = "Maestría en Panteras",
  },
  [194] = {
    ["D"] = "¿Así que crees que tus habilidades de caza están en plena forma? Puedo ponerlas a prueba en un santiamén. Sal a esa jungla y mata a 10 Raptores de Tuercespina. Veremos cuán buen cazador de caza mayor eres.$b$bY no, ¡no te diré dónde puedes encontrarlos! Localizar a las bestias es la mitad del desafío.",
    ["O"] = "Hemet Nesingwary quiere que mates 10 Raptores de Tuercespina.",
    ["T"] = "Maestría en Raptores",
  },
  [195] = {
    ["D"] = "Ahora vamos a subir el desafío y ver si estás a la altura. Aventúrate en la jungla y caza a 10 de esos malditos Raptores Colazote. ¡Veamos de qué estás hecho!",
    ["O"] = "Hemet Nesingwary quiere que mates 10 Raptores Colazote.",
    ["T"] = "Maestría en Raptores",
  },
  [196] = {
    ["D"] = "Veamos cómo manejas este próximo desafío. Hay una raza astuta de raptores por ahí. Los llamamos Acechadores de la Jungla. Son mucho más difíciles de rastrear y matar que las otras razas con las que has estado probando suerte. Mata a 10 Acechadores de la Jungla y te daré una pista sobre la mejor caza.",
    ["O"] = "Hemet Nesingwary quiere que mates 10 Acechadores de la Jungla.",
    ["T"] = "Maestría en Raptores",
  },
  [197] = {
    ["D"] = "Porque demostraste ser tan emprendedor en la jungla, déjame contarte sobre un feroz raptor que incluso yo he fallado en matar. Ajeck aquí llama a este astuto raptor Tethis.$b$bSi quieres demostrar que eres un verdadero maestro, mata a Tethis y tráeme su garra. Sería una hazaña que ningún otro cazador de caza mayor ha logrado.",
    ["O"] = "Hemet Nesingwary quiere que mates a Tethis, un raptor escurridizo y peligroso en Tuercespina.",
    ["T"] = "Maestría en Raptores",
  },
  [198] = {
    ["D"] = "Hmm. Sí, de hecho tengo algo que podrías hacer. El Teniente Doren y sus seguidores en el norte necesitan sus suministros regulares de nuestra parte.$B$BNo han sido entregados, y con los trolls atacando, necesitaré a alguien que lo haga. El campamento de Doren se encuentra al noroeste del camino en la entrada a Vega de Tuercespina desde el Bosque del Ocaso. Entrega los suministros al Soldado Thorsen, si no está en el campamento, estará patrullando al sur de él. Él se encargará de ellos.$B$BTen en cuenta que, si fallas, me deberás una compensación.",
    ["O"] = "Entrega los Suministros Goblin Varios al Soldado Thorsen.",
    ["T"] = "Suministros para el Soldado Thorsen",
  },
  [199] = {
    ["D"] = "Tomas una muestra del polvo de olor extraño.",
    ["O"] = "Regresa con el Ingeniero Jefe Hinderweir y muéstrale la pista que has descubierto.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [200] = {
    ["D"] = "Si Kurzen conoce el secreto de Thorsen, entonces tenemos un espía entre nosotros. ¡Debemos determinar cómo se filtró el secreto!$B$BEl contacto de Thorsen dentro del campamento de Kurzen era Bookie Herod. Herod es el oficial de logística de Kurzen y sabe todo sobre el funcionamiento del Compuesto de Kurzen. Si puedes obtener las notas o registros de Herod, tal vez podamos descubrir cómo Thorsen fue descubierto.$B$BCuando todavía estábamos con Kurzen, la oficina de Bookie estaba en el piso superior de la única casa de dos pisos del campamento. Sus registros probablemente todavía estén allí.",
    ["O"] = "Encuentra los Registros de Bookie Herod.",
    ["T"] = "Herod, el Apuesta",
  },
  [201] = {
    ["D"] = "He recibido informes de un grupo de cazadores que han establecido un campamento río arriba del campamento minero de la Compañía Científica en el Lago Nazferiti. Ahora, normalmente no me importaría, pero increíblemente, escuché que Hemet Nesingwary era uno de los cazadores allí. Está bien conectado, si entiendes lo que quiero decir, y nunca se puede ser demasiado cuidadoso.$b$b¿Por qué no vas a revisar el campamento, $n, a ver de qué se trata?",
    ["O"] = "Encuentra el campamento de los cazadores e informa su ubicación a Krazek en Bahía del Botín.",
    ["T"] = "Investigar el Campamento",
  },
  [202] = {
    ["D"] = "Has demostrado tu valía una y otra vez, y tu ingenio parece rivalizar incluso con el del propio Kurzen. ¡Puedes ser la salvación de nuestra banda de rebeldes... si puedes enfrentarte a Kurzen y prevalecer!$B$BÉl comanda a sus hombres desde lo profundo del Almacén. Debes entrar en esa cueva, abrirte paso entre sus defensores y enfrentarte a Kurzen y sus líderes.$B$BAdiós, $N. Mi esperanza, y el destino de mis hombres, van contigo.",
    ["O"] = "Mata 6 Élites de Kurzen, 4 Subjefes de Kurzen, y lleva la Cabeza de Kurzen al Teniente Doren en el Campamento Rebelde.",
    ["T"] = "Coronel Kurzen",
  },
  [203] = {
    ["D"] = "Estamos perdiendo la batalla contra el Coronel Kurzen. No solo es un táctico brillante, ¡sino que nos supera en número al menos 10 a 1! Para sobrevivir, necesitamos usar sus propias tácticas encubiertas contra él.$B$BEntra en su campamento al este y mata a sus hombres. Muchos de ellos. Y sal antes de que los refuerzos puedan alcanzarte.",
    ["O"] = "Mata 15 Luchadores de la Jungla de Kurzen.$B$BRegresa con el Sargento Yohwa en el Campamento Rebelde.",
    ["T"] = "La Segunda Rebelión",
  },
  [204] = {
    ["D"] = "Para tener una oportunidad contra Kurzen, necesitamos neutralizar su apoyo de sanación. Ha reunido a su alrededor una gran cantidad de Hombres Medicina que curan a sus fuerzas con Remedios de la Jungla y envenenan a nuestros luchadores con Extractos de Helecho Venenoso.$B$BNecesitamos esos remedios para curar a nuestra propia gente, y necesitamos una muestra de esos extractos para estudiarlos y poder hacer nuestros propios remedios. Busca en el Complejo de Kurzen ambos: sus Hombres Medicina tendrán los remedios, y los extractos probablemente estén guardados en algunas de las cajas de suministros del campamento.",
    ["O"] = "Lleva 7 Remedios de la Jungla y 1 Extracto de Helecho Venenoso al Sargento Yohwa en el Campamento Rebelde.",
    ["T"] = "Mala Medicina",
  },
  [205] = {
    ["D"] = "Mientras estudio esas leyendas que adquiriste, reúne para mí implementos de magia maligna de los trolls. Entra en el territorio de los trolls Skullsplitter, una segunda tribu al sur, y obtén fetiches Skullsplitter de sus Brujos y Místicos.",
    ["O"] = "Lleva 4 Fetiches Machacacráneos al Hermano Nimetz en el Campamento Rebelde.",
    ["T"] = "Brujería Troll",
  },
  [206] = {
    ["D"] = "Kurzen puede estar muerto, pero... ¡lo que lo llevó a la maldad todavía vive! A través de mis estudios he descubierto esto: Un mago ogro - Mai\'Zoth - habita en un montículo ogro dentro de las montañas orientales de Tuercespina. Se rumorea que emplea magia de control mental, y estoy seguro de que él corrompió a Kurzen.$B$BSi se permite que Mai\'Zoth viva, entonces su maldad puede influir en otros, e incluso puede hacer que otro Kurzen aparezca.$B$BMátalo y tráeme su Ojo de la Mente, el artefacto que usa para corromper a otros.",
    ["O"] = "Lleva el Ojo de la Mente al Hermano Nimetz en el Campamento Rebelde.",
    ["T"] = "Mai\'Zoth",
  },
  [207] = {
    ["D"] = "El Coronel Kurzen fue una vez un hombre grande y noble; ¡debo encontrar qué causó su caída en la depravación! Puede estar bajo la influencia de la magia troll. Para confirmar esto, debo estudiar algunas de sus leyendas.$B$BHay 4 tablillas escondidas dentro de las ruinas troll de Tuercespina. ¡Debo ver qué está escrito en estas tablillas! Transcribe sus marcas y tráeme las transcripciones.$B$BLa primera tablilla está en las Ruinas de Bal\'lal, la segunda en el Arrecife Vil, y la tercera y cuarta en las Ruinas de Zul\'Kunda.",
    ["O"] = "Encuentra las 4 tablillas y lleva sus leyendas de vuelta al Hermano Nimetz.",
    ["T"] = "El Misterio de Kurzen",
  },
  [208] = {
    ["D"] = "$N, eres un cazador digno de nuestra compañía. Desde los campos de batalla de Lordaeron hasta la jungla más profunda, rara vez he sido testigo de tal destreza con las armas como la que has demostrado.$B$BHas matado al feroz Sin\'Dall, al escurridizo Bag\'thera y al traicionero Tethis. Pero el verdadero premio es la cabeza del Rey Bangalash. Ese tigre blanco es la razón por la que estoy aquí. Lo he tenido en mi mira durante bastante tiempo. Mata al Rey Bangalash y tu destreza como cazador será probada como insuperable.",
    ["O"] = "Hemet Nesingwary quiere que le traigas la cabeza de Rey Bangalash, el gran tigre blanco.",
    ["T"] = "Cazador de Juego Mayor",
  },
  [209] = {
    ["D"] = "Es costoso y difícil matar suficientes tigres para apoyar la exportación de colmillos de tigre a las islas de los Mares del Sur. Afortunadamente, hemos desarrollado una técnica que nos permite tomar objetos comunes parecidos a cuernos y convertirlos en falsificaciones indetectables.$b$bEl parecido más cercano que hemos encontrado, sorprendentemente, son los colmillos de los trolls Rajacráneos.$b$bOye, antes de que digas algo, lo que el comprador no sabe no le hace daño, ¿verdad? Tráeme una gran cantidad de ellos para que podamos trabajar en el envío mensual.",
    ["O"] = "Consigue 18 Colmillos Machacacráneos y llévaselas a Kebok.",
    ["T"] = "Colmillos de los Machacacráneos",
  },
  [210] = {
    ["D"] = "Hago la mayor parte de la cocina en el campamento. Y solía ser mucho trabajo, hasta que compré una olla de cocina mecánica a un mercader goblin en Bahía del Botín. ¡La olla es una maravilla! Me ahorró innumerables horas... ¡hasta que se rompió!$B$B ¿Puedes llevarle la olla al goblin para que la arregle? Su nombre es Krazek.",
    ["O"] = "Lleva la Olla de Krazek a Krazek el goblin.",
    ["T"] = "Cocina de Krazek",
  },
  [211] = {
    ["D"] = "¡Por desgracia, ha llegado el momento de atacar Andorhal y expulsar al nigromante que la controla!$B$BDentro de las ruinas de la ciudad es donde el nigromante, Araj el Invocador, tiene su dominio. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes unidos directamente a él es formidable. Necesitarás numerosos aliados para siquiera tener una posibilidad de enfrentarlo con éxito.$B$B¡Destruye a Araj, $N, y tráeme un fragmento de su filacteria como prueba!",
    ["O"] = "Lleva el Fragmento de Filacteria de Araj al Comandante Ashlam Puñovalor en el Campamento del Orvallo, Tierras de la Peste del Oeste.",
    ["T"] = "¡Ay, Andorhal!",
  },
  [212] = {
    ["D"] = "Uno de mis platos más famosos es el Basilisco Frío en Vinagreta de Lima. Pero el problema es... la carne es imposible de mantener fresca, y debo servir el plato esta noche para el cumpleaños de un noble.$B$BAsí que si puedes traerme un Muslo de Basilisco Frío de un Basilisco Ojo Frío recién muerto, rápidamente, ¡te deberé un festín digno de un rey!$B$BLos Basilisco Ojo Frío son conocidos por merodear por las dunas de la playa al noroeste de Tuercespina, así que tienes mucho que viajar. ¡Y sé rápido!",
    ["O"] = "Mata un Basilisco Ojo Frío, consigue un Muslo de Basilisco Frío y llévaselo a Angus Stern en el Recluse Azul.$B$B<No debes liberar tu espíritu para completar esta misión.>",
    ["T"] = "Una Comida Fría",
  },
  [213] = {
    ["D"] = "¡Esta vez lo ha hecho! Ya era bastante malo que Gelriz estuviera desplazando a los magnates nombrados por los príncipes mercantes, ¡ahora intenta meterse con el pirata más notorio!$b$bRevilgaz no lo permitirá, y me ha dicho que me encargue del problema a mi manera.$b$b¿Mi manera? El robo. Los geólogos de Venture Co. cerca del Lago Nazferiti están muy interesados en esos extraños cristales azules que han estado encontrando en las minas. Tráeme muestras de la piedra de sus geólogos, no me importa lo que tengas que hacer para conseguirlas.",
    ["O"] = "Recupera 8 Cristales Pulidos y devuélveselos a Kebok en Bahía del Botín.",
    ["T"] = "Toma Hostil",
  },
  [214] = {
    ["D"] = "No es ningún secreto que la marca distintiva de la Pandilla Defias son sus Pañuelos Rojos. Pero hemos aprendido que el material del que están hechos estos pañuelos indica el rango del miembro.$b$bDicho esto, quiero que elimines a tantos miembros de alto rango de los Defias como puedas. Los miembros de mayor rango sin duda se encontrarán en el escondite secreto de VanCleef. Cuando termines, tráeme 10 Pañuelos de Seda Roja como prueba de sus muertes y te recompensaré. Buena suerte, $N.",
    ["O"] = "La Exploradora Riell en la Torre de la Colina del Centinela quiere que le lleves 10 Pañuelos de Seda Roja.",
    ["T"] = "Pañuelos de Seda Roja",
  },
  [215] = {
    ["D"] = "Gracias por salvarme, $N. Sé cómo se ve esto, pero... permíteme explicar.$B$BEl Teniente Doren me encargó reenlistarme con los hombres de Kurzen, para poder espiarlos y traer información crucial a los rebeldes. Viste aquí lo que pensé que sería mi oportunidad de infiltrarme en el Complejo de Kurzen. Me equivoqué, y de nuevo, gracias por salvarme.$B$BEste era un misión de alto secreto, así que no debes decirle a nadie. Pero si hablas con Doren, él te lo explicará.",
    ["O"] = "Habla con el Teniente Doren en el Campamento Rebelde.",
    ["T"] = "Secretos de la Jungla",
  },
  [216] = {
    ["D"] = "¡Los furbolgs Piel de Cardo están amenazando nuestra expansión! La Aldea Piel de Cardo al este bloquea el camino entre nosotros aquí y el Puesto del Hachazo. Es un camino que evitaría la aldea de Astranaar, sin él, le damos a la Alianza una ruta de expansión adicional.$B$B¡$N, no debemos permitir... una brecha en la ruta de expansión!$B$BProcede a la Aldea Piel de Cardo y reduce el número de furbolgs. Se te dará una compensación por esta tarea, pero lo más importante es que estarás cumpliendo con tu deber para la Horda!",
    ["O"] = "Elimina a 12 Vengadores Pelocardo y 12 Chamán Pelocardo, la mayoría se encuentran al este de la Ensenada de Zoram en la Aldea Pelocardo. Una vez completado, regresa con Karang Amakkar en el Puesto de Zoram\'gar, Vallefresno.",
    ["T"] = "Entre una Roca y los Pieles de Cardo",
  },
  [217] = {
    ["D"] = "$N, nuestros exploradores han identificado al líder de esta reciente incursión Trogg. Parece que estas bestias en Loch Modan siguen el liderazgo de un jefe llamado Grawmug. Sin embargo, Grawmug está fuertemente custodiado. Dos de sus guardias más élite, Mascador y Peleador, nunca lo abandonan.$B$BSi te sientes digno, $N, quiero que lideres la misión de asesinato contra Grawmug y sus dos matones. Con el liderazgo Trogg muerto, podríamos tener una oportunidad de empujar a estas bestias de vuelta al suelo.",
    ["O"] = "Mata al líder trogg, Grawmug, y a sus dos guardias, Masticador y Peleador, luego informa al Capitán Rugelfuss en la torre de guardia del sur.",
    ["T"] = "En Defensa de las Tierras del Rey",
  },
  [218] = {
    ["D"] = "¡Mi diario! Se lo llevaron a la cueva. El que lo tenía... Era un bruto grande con algunas marcas extrañas en su piel y cara. No pude ver mucho más que eso.$b$bHas tenido suerte con los trolls, ¿quizás podrías ir a recuperarlo por mí?",
    ["O"] = "Grelin Barbablanca quiere que mates a Grik\'nir el Frío y recuperes su diario.",
    ["T"] = "El Diario Robado",
  },
  [219] = {
    ["D"] = "¡Por fin, amigos de la Alianza!$b$bAyuda... por favor... $b$bLo último que recuerdo es que estos orcos despreciables superaron a nuestro regimiento. La mayoría de los hombres fueron asesinados. Recibí un fuerte golpe en la cabeza y todo se volvió negro. Ahora estoy cautivo en esta cueva. Ayúdame, estoy gravemente herido.$b$bNecesitaré ayuda para volver al pueblo... por favor... eres mi última esperanza...",
    ["O"] = "Protege al Cabo Keeshan en el viaje de regreso a Crestagrana.",
    ["T"] = "Desaparecido en Acción",
  },
  [220] = {
    ["D"] = "Islen tendrá una tarea más para ti antes de recompensarte con tu próximo tótem. El frasco de agua que te he dado es necesario para lo que sea que esa tarea sea.$B$BVe ahora y encuéntrala. Nos volveremos a ver algún día.",
    ["O"] = "Lleva el Frasco de Agua Más Pura a Islen Waterseer en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [221] = {
    ["D"] = "¿Te has fijado en algunos de los huargos más grandes que deambulan con los Tejedores de las Sombras en el bosque? Por lo que podemos decir, estos Corredores Oscuros constituyen la mayor parte de los números de los huargos.$b$bEn mis recorridos, también he notado que han invadido el Huerto Putrefacto al suroeste de la ciudad. Estos huargos son un poco más duros que los que enfrentaste la última vez.$b$bMantente alerta.",
    ["O"] = "Mata 12 Huargens Corredor Nocturno para Calor en Villa Oscura.",
    ["T"] = "Huargos en el Bosque",
  },
  [222] = {
    ["D"] = "Tus logros anteriores me han convencido de que estás listo para enfrentarte a los huargos más peligrosos que infestan el bosque.$b$bDe los huargos que han hecho de este lugar su nuevo hogar, los Colmillos Viles y los Corrompidos han demostrado ser los más peligrosos. Se han establecido cerca de algunas de las cuevas y en la mina al sur.$b$bDesde lejos, incluso puedes ver la luz de sus hogueras...",
    ["O"] = "Mata 8 Colmillo Vil Noctívago y 8 Tiznados Noctívago para Calor en Villa Oscura.",
    ["T"] = "Huargos en el Bosque",
  },
  [223] = {
    ["D"] = "Aquí tienes, $n. Lleva este mensaje al Maestro Carevin.$b$b<Él rápidamente saca un pedazo de pergamino descolorido y te lo ofrece.>$b$bCon unos pocos más como tú, superaremos en número a la Guardia Nocturna! Quizás entonces podamos completar el trabajo que nosotros pocos llevamos a cabo hoy.",
    ["O"] = "Lleva la nota de Calor a Jonathan Carevin.",
    ["T"] = "Huargos en el Bosque",
  },
  [224] = {
    ["D"] = "¡Necesitamos proteger el Loch, $N! Con tantos soldados del Rey luchando valientemente en campos de batalla remotos, nos hemos visto abrumados en el frente interno. ¡Los troggs están saliendo de cada grieta! La infestación de troggs representa la mayor amenaza para Forjaz. Estos repugnantes mutantes deben ser destruidos.$b$b¡Te necesitamos, valiente aventurero, para que salgas y acabes con la amenaza de los troggs! Mata a 10 Troggs Rompepiedras y a 10 Exploradores Rompepiedras e informa de vuelta.",
    ["O"] = "El Montaraz Pedernal de la torre de guardia del sur quiere que mates 10 Trogues Rompepedras y 10 Exploradores Rompepedras.",
    ["T"] = "En Defensa de las Tierras del Rey",
  },
  [225] = {
    ["D"] = "La lápida desgastada dice simplemente:$B$BMORGAN LADIMORE$B$B<Al examinarla más de cerca, notas que la tierra sobre la tumba ha sido perturbada recientemente, y que una buena cantidad de tierra parece haber sido desplazada...>",
    ["O"] = "Habla con Sirra Von\'Indi en Villa Oscura.",
    ["T"] = "La Tumba Erosionada",
  },
  [226] = {
    ["D"] = "Sven y yo tenemos días peligrosos por delante, con el Nigromante al este y todo. Y aquí, solos como estamos, tenemos que cazar nuestra propia comida. Parece que cada vez que regreso al campamento con algo de carne, lobos hambrientos o rabiosos salen del bosque queriendo un bocado. No hace falta decir que vivir aquí es un trabajo peligroso.$B$BPero si puedes deshacerte de algunos de esos lobos, lo tendríamos más fácil. Principalmente merodean al norte y al este de aquí, cerca del río.",
    ["O"] = "Mata 12 Lobos Huargos Hambrientos y 8 Lobos Huargos Rabiosos, luego regresa con Lars en el Campamento de Sven en la frontera occidental de Bosque del Ocaso.",
    ["T"] = "Lobos en Nuestros Talones",
  },
  [227] = {
    ["D"] = "<Busca entre los estantes y saca un libro encuadernado en cuero.>$b$bSi quieres saber más, podrías preguntarle a Althea, ella ha sido la que ha manejado los... problemas... con él últimamente.",
    ["O"] = "Habla con la Comandante Althea Cerro Negro en Villa Oscura.",
    ["T"] = "Morgan Ladimore",
  },
  [228] = {
    ["D"] = "Por lo que me dicen mis exploradores, Mor\'Ladim vaga por el Bosque del Ocaso, siguiendo un camino extraño y sinuoso a través del cementerio. Lo enterramos más allá de la casa en la colina, ¿sabes dónde está?$b$bNo hay nada que pueda hacer para ayudarte, pero te deseo buena suerte.",
    ["O"] = "Mata a Mor\'Ladim, luego lleva su cráneo a la Comandante Althea Cerranegra en Villa Oscura.",
    ["T"] = "Mor\'Ladim",
  },
  [229] = {
    ["D"] = "Debería haberte dicho esto antes...$B$B¿Cómo lo digo...?$B$BMorgan puede haber creído que su familia estaba muerta, pero, de hecho, su hija Sarah Ladimore ahora es una Vigía. Siempre ha estado preocupada por las... circunstancias... que rodearon la muerte de su padre. Quizás podrías ir a llevarle las noticias.",
    ["O"] = "Habla con la Vigía Sarah Ladimore en Villa Oscura.",
    ["T"] = "La Hija que Sobrevivió",
  },
  [230] = {
    ["D"] = "Al cavar en el montículo, encuentras un pequeño libro manchado de tierra.$B$BEl libro no tiene título, pero... contiene información que a Sven le gustaría saber.",
    ["O"] = "Lleva el Libro de la Granja de Sven a Sven.",
    ["T"] = "El Campamento de Sven",
  },
  [231] = {
    ["D"] = "Aquí, toma esto y colócalo en su tumba. Quizás... de alguna manera, él sabrá que estoy bien, y que ninguno de nosotros lo responsabiliza por lo que sucedió.$b$b<Hay lágrimas en sus ojos ahora, mientras se quita un anillo de su mano derecha y te lo entrega.>$b$bY... gracias, $n.",
    ["O"] = "Lleva el anillo de Sarah Ladimore a la tumba de Morgan Ladimore.",
    ["T"] = "El Amor de una Hija",
  },
  [232] = {
    ["D"] = "La Sociedad Real de Boticarios necesita tu ayuda. Escucha atentamente, y ambos podemos beneficiarnos de mi plan.$B$BEl Boticario Keever en la siguiente habitación puede estar... tocado... pero su investigación potencialmente da frutos si fuera perseguida por un investigador astuto. Afortunadamente, yo soy tal investigador. Quiero que lleves esta orden de compra a Alessandro Luca, el propietario de Cosas y Rarezas de la Luna Azul, él te dará un kit de prueba. Cuando lo tengas, tráemelo y te describiré tu tarea en detalle.",
    ["O"] = "Lleva la orden de compra a Alessandro Luca en Entrañas.",
    ["T"] = "Recado para el Boticario Zinge",
  },
  [233] = {
    ["D"] = "Hmm, no supongo que estarías dispuesto a hacerme un favor, ¿verdad, $g chico:chica;? Hoy llegó una pila de cartas a través del paso, pero no tengo tiempo para enviarlas.$B$BTodas están dirigidas a Talin Keeneye. Puedes encontrarlo al oeste, siguiendo el camino. Ha establecido un campamento junto al lago helado.$B$B¿Qué dices?",
    ["O"] = "Entrega el montón de cartas a Talin Ojo Agudo.",
    ["T"] = "Entrega de Correo del Valle de Crestanevada",
  },
  [234] = {
    ["D"] = "Si recuerdo correctamente, el campamento de Grelin está camino abajo hacia el sureste. Sin duda, estará ansioso por recibir su correo.",
    ["O"] = "Entrega la carta a Grelin Barbablanca.",
    ["T"] = "Entrega de Correo del Valle de Crestanevada",
  },
  [235] = {
    ["D"] = "¡Atención, jóvenes aventureros! ¡Las tierras salvajes de Vallefresno te esperan!$B$BLa Horda ha establecido una fuerte presencia en las tierras al norte de los Baldíos. Nuestros dos puestos avanzados, el Puesto del Hachazo y el Puesto de Zoram, luchan por traer gloria a la Horda. Aquellos que quieran demostrar su valía deben buscar orientación allí. ¡Notablemente, Senani Corazón de Trueno en el Puesto del Hachazo, directamente al norte de los Baldíos, busca aventureros dispuestos a participar en una gran cacería en Vallefresno!",
    ["O"] = "Habla con Senani Corazón de Trueno en el Puesto del Hachazo en Vallefresno.",
    ["T"] = "La Cacería de Vallefresno",
  },
  [236] = {
    ["D"] = "Hace unas semanas, envié al Vigía Callahan y a algunos otros a la frontera norte de Bosque del Ocaso para lidiar con la infestación de lobos. No he tenido noticias suyas desde hace bastante tiempo. Si siguió mis instrucciones, estará acampado en el camino al norte de Villa Oscura. $B$BSi está en tu camino, por favor, échale un vistazo e infórmale que estoy esperando un informe de progreso.",
    ["O"] = "Habla con el Vigía Callahan.",
    ["T"] = "Vigilante Callahan",
  },
  [237] = {
    ["D"] = "El montañero Cobbleflint no tuvo más que cosas buenas que decir sobre ti, $r. Por esa razón voy a confiarte una misión de suma importancia. Necesitamos mantener la presión sobre las fuerzas invasoras de los Troggs hasta que nuestros hermanos enanos regresen del frente de la Alianza.$B$BAdéntrate en las colinas del sur y mata a 10 Machacacráneos Rajapiedras y a 10 Videntes Rajapiedras. Tus ataques nos darán algo de tiempo. Informa cuando hayas completado tu misión.",
    ["O"] = "El Montaraz Gravelgaw en la torre de guardia del sur quiere que mates 10 Machacacráneos Rompepedras y 10 Videntes Rompepedras y le informes.",
    ["T"] = "En Defensa de las Tierras del Rey",
  },
  [238] = {
    ["D"] = "El kit está listo, $n. Sin duda, la Boticaria Zinge querrá esto lo antes posible. Llévaselo sin demora y hazle saber que agregaré el costo del kit a la cuenta de la Sociedad. Además, por favor, extiéndale mis más cálidos saludos.$B$BBueno, no te quedes ahí parado. Así es la vida de un simple mensajero, ¿no?",
    ["O"] = "Lleva el Kit de Muestras de Campo al Boticario Zinge en Entrañas.",
    ["T"] = "Recado para el Boticario Zinge",
  },
  [239] = {
    ["D"] = "La Guarnición en nuestra frontera occidental envía noticias de una creciente actividad de gnolls y ladrones. Están solicitando que enviemos más soldados de Ventormenta... ¡pero simplemente no tenemos ninguno para enviar!$B$BSi puedes ayudar, lo agradeceríamos. Ve y habla con el Teniente Rainer en la Guarnición de Westbrook y mira qué necesita hacer.$B$BLa Guarnición está al oeste por el camino. Después de cruzar el puente sobre el pequeño arroyo, estará a la derecha.",
    ["O"] = "Ve al Cuartel de Arroyo del Oeste y habla con el Alguacil Rainer.",
    ["T"] = "¡El Fuerte de Arroyo Oeste Necesita Ayuda!",
  },
  [240] = {
    ["D"] = "Y aquí estamos: ¡Pasteles de \"Cangrejo\" al estilo de Grual!",
    ["O"] = "Regresa con Jitters con los Pasteles de Cangrejo Oscuro.",
    ["T"] = "Regreso con Jitters",
  },
  [241] = "_",
  [242] = "_",
  [243] = {
    ["D"] = "Te dirigirás al desierto de Tanaris, probando la consistencia del tejido de los basiliscos, escórpidos y hienas nativos de allí. Obtén muestras de tejido y úsalas con el kit de prueba que llevas, espera que una parte de las muestras falle. Necesito ocho muestras aceptables de cada tipo de criatura.$B$BAdemás, los goblins de Gadgetzan tienen una fuente de energía que debe usarse con este tipo de kit modificado. Habla con el Ingeniero Jefe Chispeje y consigue una para el kit.$B$BRegresa conmigo solo cuando tengas éxito.",
    ["O"] = "Habla con el Ingeniero Jefe Silbapellizco en Gadgetzan sobre conseguir una fuente de energía para el kit de prueba.",
    ["T"] = "Al Campo",
  },
  [244] = {
    ["D"] = "Saludos, $c. Estoy encargado de patrullar este tramo de camino. Aunque el camino es seguro por ahora, he visto campamentos de gnolls al norte y al este de aquí.$B$B¡Villa del Lago debe saber de la fuerza gnoll que se está reuniendo! Informa al Diputado Feldon en Villa del Lago y cuéntale sobre los gnolls. Haz esto, y estoy seguro de que Feldon te ofrecerá un salario de explorador.$B$BFeldon suele inspeccionar el área al sur del puente hacia Villa del Lago.",
    ["O"] = "Preséntate ante el Alguacil Feldon.",
    ["T"] = "Gnolls Invadiendo",
  },
  [245] = {
    ["D"] = "Saludos, $c. ¿Quizás has visto esas arañas que han invadido la frontera occidental? Esas amenazas de ocho patas son demasiado para nosotros, y el Comandante no tiene suficiente mano de obra para desplegar tan lejos de Villa Oscura.$b$bOdio pedirte esto, pero pareces capaz de manejarlas. No puedo prometerte nada de gran valor, pero puedo recompensarte si puedes ayudar a erradicar a esos bichos asquerosos.$b$bAh, y un consejo, intenta evitar su veneno si puedes.",
    ["O"] = "Mata 15 Arañas Venenosas Pigmeas, y luego informa al Vigía Dodds en Villa Oscura.",
    ["T"] = "Amenazas de Ocho Patas",
  },
  [246] = {
    ["D"] = "No tenemos hombres que desperdiciar, pero algo debe hacerse con esos gnolls que me reportaste.$B$BExplora el sur de Crestagrana en busca de gnolls. Acósalos, mata a los que puedas y regresa conmigo con un recuento de su número y una evaluación de la amenaza que representan. Puedes encontrar esos gnolls acampados a lo largo del camino sur de Crestagrana.$B$BHaz esto por el Ejército de Ventormenta, $N, y serás recompensado.",
    ["O"] = "Mata 10 Canes Crestagrana y 6 Furtivos Crestagrana, luego informa al Diputado Feldon en Villa del Lago.",
    ["T"] = "Evaluando la Amenaza",
  },
  [247] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Cacería Completada",
  },
  [248] = {
    ["D"] = "He estado observando la Torre de Ilgalar y puedo detectar que Morganth está en conflicto con los Orcos en el Bastión de Petravista. No sé la razón detrás de su contienda, pero puedes ayudarme a desentrañar este misterio.$B$BToma otro Glifo de Azora, luego ve a la cima de la torre de vigilancia intacta junto al Bastión de Petravista.$B$BEncuentra un objeto adecuado en el que colocar el Glifo. Un cofre o una jarra, algún lugar donde el glifo no sea notado, servirá para mi propósito.",
    ["O"] = "Escala la torre de vigilancia intacta junto a la Fortaleza de Petravista.$B$BEncuentra un contenedor adecuado para colocar el Glifo de Azora.",
    ["T"] = "Mirando Más Allá",
  },
  [249] = {
    ["D"] = "Después de mucha observación, he descubierto los planes de Morganth.$B$B¡Está buscando la Guadaña de Elune... y puede que la haya encontrado! Si es así, entonces las Montañas Crestagrana y el reino de Ventormenta pronto sentirán su poder oscuro.$B$BNo estoy preparado para enfrentarme a Morganth, pero temo que debe ser tratado ahora. Así que debes entrar en la Torre de Ilgalar y enfrentarte a Morganth.$B$BDerrota a Morganth y tráeme su Colgante de las Sombras. Haz esto, y el reino te deberá mucho.",
    ["O"] = "Derrota a Morganth.$B$BLleva su Colgante de las Sombras a Theocritus en la Torre de Azora en el Bosque de Elwynn.",
    ["T"] = "Morganth",
  },
  [250] = {
    ["D"] = "Gracias a las estrellas, alguien se preocupa por el bienestar de la Presa. Primero la destrucción del Puente Thandol y luego el saqueo de Dun Modr. No tengo dudas de que la Presa será el próximo objetivo de la amenaza Hierro Negro. La mayor parte del personal de seguridad aquí fue reasignado al Frente de la Alianza, incluido mi inspector principal.$b$bPor eso te necesito, $N. Vi a algunos zapadores Hierro Negro merodeando por la rampa este de la Presa. Investiga el área y trae una pista.",
    ["O"] = "Investiga el área cerca de la rampa oriental de la Presa y lleva al Ingeniero Jefe Hinderweir una pista que pueda revelar lo que están tramando los Enanos Hierro Negro.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [251] = {
    ["D"] = "Llévale la nota que me diste a Sirra Von\'Indi. Es muy versado en idiomas y artes antiguas, si alguien aquí puede traducir este mensaje, es él.",
    ["O"] = "Lleva la Carta a Ello a Sirra Von\'Indi.",
    ["T"] = "Traducir la Nota de Abercrombie",
  },
  [252] = {
    ["D"] = "¡Oh, cielos! Si lo que está escrito en esta nota es cierto, entonces Abercrombie ha hecho algo... indescriptible.$B$BEs mejor que lleves la nota traducida de vuelta a Ello.$B$BY hazlo rápidamente, porque temo que una condena ya se acerca a nuestro pueblo...",
    ["O"] = "Lleva la Carta Traducida a Lord Ello Ebonlocke.",
    ["T"] = "Traducción para Ello",
  },
  [253] = {
    ["D"] = "El Embalsamador es un nombre mencionado en una de nuestras historias infantiles. Era un alquimista amable, enloquecido por la muerte de su esposa, Eliza. Para restaurarla, usó magia oscura para colocar su propio corazón en el pecho de su esposa muerta.$B$BEsto revivió a Eliza... pero la maldijo con un hambre de carne humana, obligando al Embalsamador a mantenerla enterrada.$B$BSu corazón es su poder. Encuentra la tumba de su esposa, recupera su corazón y tráemelo mientras aún late. Haz esto y su poder se romperá.",
    ["O"] = "Encuentra la tumba de Eliza. Recupera el Corazón del Embalsamador de ella, luego regresa con Ello Candil Negro.",
    ["T"] = "La Novia del Embalsamador",
  },
  [254] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Cavando en la Tierra",
  },
  [255] = {
    ["D"] = "Dos meses solo para escuchar de Forjaz que nos enviarán un puñado de soldados para nuestra propia defensa, ¡y otros dos meses hasta que lleguen! Ya es bastante malo que nuestro pueblo esté desprotegido, pero la excavación y la presa—¡la presa!—también están abiertas a ataques.$b$bNo tengo otra opción entonces, parece. Necesitaré brazos contratados.$b$b¿Qué tal tú? Pareces un tipo probable, $c. Un grupo de ogros ha establecido un campamento al noreste del lago. Quizás podrías reclutar a otros y ayudar a eliminar la amenaza para nuestro pueblo.",
    ["O"] = "El Magistrado Nariz Chata de Thelsamar te ha contratado para matar a 4 Ogros Mo\'grosh, 4 Brutos Mo\'grosh y 4 Aplicadores Mo\'grosh.",
    ["T"] = "Mercenarios",
  },
  [256] = {
    ["D"] = "Por orden del Magistrado de Thelsamar:$b$bChok\'sul, el ogro que se presume es el líder y coordinador de los ataques contra la ciudad de Thelsamar, la Presa de Forjapiedra y el sitio de excavación, es buscado muerto por cualquier medio necesario.$b$bSe proporcionará compensación monetaria cuando se traiga prueba de la muerte de Chok\'sul.$b$bChok\'sul fue visto por última vez en el campamento ogro en la parte noreste de Loch Modan.",
    ["O"] = "Mata a Chok\'sul y lleva su cabeza al Magistrado Bluntnose de Thelsamar.",
    ["T"] = "SE BUSCA: Chok\'sul",
  },
  [257] = {
    ["D"] = "¿Crees que puedes igualar el ingenio de Daryl el Audaz, eh? ¡Debería pensar que no! Por supuesto, eres más que bienvenido a intentarlo. Aquí tienes un desafío que debería estar por encima de tus habilidades, así que no te sientas mal si no puedes enfrentarlo.$b$bUna bandada de buitres ha anidado aquí en Loch Modan. ¿Por qué no intentas derribar a algunas de las bestias? Te diré qué, si cumples mi desafío en quince minutos, te daré mi arco o mi arma.$b$b<Te mira con desdén.>$b$bParece que no tienes mucho que perder, de todos modos.",
    ["O"] = "Mata 6 Buitres de Montaña y regresa con Daryl el Joven en el Refugio de los Exploradores Lejanos en 15 minutos.",
    ["T"] = "La Fanfarronería de un Cazador",
  },
  [258] = {
    ["D"] = "$nath, ¿verdad? Puedo ver que estás lleno de orgullo por completar la primera prueba, ¿eh? Como te dije antes, no es gran cosa.$B$BDeberías intentar cazar jabalíes. Créeme, esta no es una cacería de jabalíes de Vallefresno, así que será mejor que tengas cuidado con ellos. Te daré solo doce minutos esta vez.$B$BNo te sientas mal si fallas. ¡Te daría la camisa de mi espalda si tuvieras éxito!$B$B¿Te he contado alguna vez la historia de cómo recibí mi famosa cicatriz? ¿No? Fue hace dos años...",
    ["O"] = "Mata 5 Jabalíes Montés Ancianos y regresa con Daryl el Joven en el Refugio de los Exploradores Lejanos en 12 minutos.",
    ["T"] = "El Desafío de un Cazador",
  },
  [259] = "_",
  [260] = "_",
  [261] = {
    ["D"] = "Seré franco. Estamos en guerra con la Plaga. Es un mal que corrompe a nuestra gente e infecta nuestra tierra. Debe ser detenido antes de que nos abrume y arrastre nuestro mundo a la sombra. Nosotros, de la Cruzada Escarlata, hemos jurado luchar contra la Plaga con cuerpo y alma.$B$BSi estás con nosotros, reúne tu coraje y demuestra tu lealtad: destruye a los no muertos devastadores de Desolace, en el Valle de los Huesos al sur.$B$BHaz esto, y la Cruzada te abrazará.",
    ["O"] = "Destruye 30 Devastadores No-muertos y luego regresa con el Hermano Anton en el Puesto de Nijel.",
    ["T"] = "Por el Sendero Escarlata",
  },
  [262] = {
    ["D"] = "¡Debo saber quién era ese merodeador en las sombras que vi la noche en que mataron a mi familia!$B$BPor favor, $N, has demostrado tu ingenio. ¡Por favor, encuentra a esa figura sombría! Debo saber qué secretos guarda.$B$BNo tengo pistas que tú no conozcas, pero aquí, toma el libro que encontraste en mi granja. Quizás algunos de los aldeanos puedan vincular este libro con su dueño.",
    ["O"] = "Lleva el libro que encontraste en la granja de Sven y muéstralo a los aldeanos de Villa Oscura.",
    ["T"] = "La Figura Sombría",
  },
  [263] = {
    ["D"] = "Los montañeros me dicen que eres muy valiente y capaz, $N. Necesitamos un $r como tú por aquí. El problema de los troggs no está mejorando. Las reservas han sido llamadas al frente y estamos solos aquí. Pero ahora que tenemos un $c experimentado aquí, veamos qué puedes hacer.$B$BSal y mata a 10 chamán Rompecantos y 10 Quebrantahuesos Rompecantos. Veamos si estás a la altura de tu reputación, Asesino de Troggs.",
    ["O"] = "El Montaraz Wallbang en la torre de guardia del sur quiere que mates 10 Chamanes Rompepedras y 10 Rompehuesos Rompepedras.",
    ["T"] = "En Defensa de las Tierras del Rey",
  },
  [264] = {
    ["D"] = "¡Escoria inmunda! Me deja por su maldita cruzada. Porque \"la Luz es lo más importante que tenemos contra las amenazas de los no-muertos.\"$B$B¿Y qué hay de sus hijos? ¿Y qué hay de MÍ? ¡Noche tras noche me senté pacientemente esperándolo... siempre en segundo lugar ante su maldito deber!$B$B¡Bueno, atestigua lo que la \"justicia\" te trae, $N! ¡Está muerto y yo estoy cargando con lo mismo que él intentaba detener!$B$BToma esta basura y ponla en su tumba en el Sepulcro. No quiero tener nada más que ver con eso... ¡o con él!",
    ["O"] = "Coloca el Colgante de Clarice en la Tumba de Yuriv en el Bosque de Argénteos.",
    ["T"] = "Hasta que la Muerte nos Separe",
  },
  [265] = {
    ["D"] = "Aquí tienes tu libro de vuelta, $N, y te deseo suerte en tu búsqueda. Si preguntas a otros en el pueblo, quizás puedan ofrecerte más pistas.$B$BY aquí tienes una más: aunque el autor de este libro es un extraño para Villa Oscura, el libro en sí fue comprado aquí.",
    ["O"] = "Pregunta a otros aldeanos si tienen pistas sobre la figura sombría.",
    ["T"] = "La Búsqueda Sombría Continúa",
  },
  [266] = {
    ["D"] = "Aquí tienes tu libro, y buena suerte con tus indagaciones.$B$BPodrías ampliar tu búsqueda a la posada. Si la persona que buscas pasó tiempo en el pueblo, es probable que alguien la haya visto allí.",
    ["O"] = "Busca en la posada pistas sobre la figura sombría.",
    ["T"] = "Pregunta en la Posada",
  },
  [267] = {
    ["D"] = "$C, puede que no estés al tanto de la amenaza Trogg que se cierne sobre las tierras Enanas. Con la Reserva de Forjaz llamada al frente de la Alianza, nos quedamos con una fracción de las fuerzas de defensa necesarias para mantener estas tierras seguras. Mi regimiento está asignado a vigilar la Puerta aquí y no podemos abandonar nuestro puesto por miedo a una invasión.$B$BPero necesitamos ejercer presión sobre esos malditos Troggs que acechan en las colinas. Si estás dispuesto a la tarea, lanza un asalto contra los Troggs. Tráeme 8 Dientes de Piedra de Trogg como prueba.",
    ["O"] = "Lleva 8 Dientes de Trogg de Piedra al Capitán Rugelfuss en la torre de vigilancia del sur.",
    ["T"] = "La Amenaza Trogg",
  },
  [268] = {
    ["D"] = "El libro que encontraste estaba solo a medio terminar. Tuve que dejarlo en la granja de Sven cuando huí allí...$B$BAquí. Lleva mi diario completo a Sven. Describe más completamente lo que sucedió en su granja.$B$BAunque este diario ha sido mi única compañía estas últimas semanas, si entregarlo me evita tener que enfrentarme a Sven y su ira, entonces lo hago de buena gana.",
    ["O"] = "Regresa con Sven con el diario completado de Jitters.",
    ["T"] = "Regreso con Sven",
  },
  [269] = {
    ["D"] = "Ve a la ciudad de Ventormenta y habla con el Obispo Farthing en la Catedral de la Luz. Entrégale el diario de Jitters y dile que pretendes enfrentarte a Morbent Vil.$B$BAunque Ventormenta ha abandonado Villa Oscura, Farthing es un alma amable y muy sabia. Él puede ayudarte en tu misión para salvarnos.$B$BTu misión es peligrosa, pero date prisa, $N. Con cada hora que pasa, las sombras de Bosque del Ocaso se vuelven más oscuras.",
    ["O"] = "Dale el Diario Completo de Jitters al Obispo Farthing en Ventormenta.",
    ["T"] = "Buscando Sabiduría",
  },
  [270] = {
    ["D"] = "Hace años, la Tercera Flota de Kul\'Tiras se hundió en las costas de Los Humedales, una extensión de marisma al norte del reino enano de Forjaz. En uno de los barcos había un cargamento de Hierro Forjaluz.$B$BEste metal es precioso para nosotros, ya que los objetos creados con él golpean con verdad sagrada. Si vas a enfrentarte a Morbent Vil, necesitarás un arma así.$B$BVe al Puerto de Menethil y habla con mi colega enano Glorin Ceja de Acero. Si ese metal perdido puede ser encontrado, él sabrá dónde buscar.",
    ["O"] = "Ve al Puerto de Menethil en los Humedales, y habla con Glorin Frentedeacero.",
    ["T"] = "La Flota Condenada",
  },
  [271] = {
    ["D"] = "No supongo que estarías interesado en un poco de venganza contra Su Osadía, ¿verdad? Daryl tiene algo de habilidad, me duele decirlo.$B$BHace un año, un gran oso había sido el objetivo de la Cabaña del Errante, pero ninguno de los cazadores tuvo suerte con él. Entonces, Daryl pensó que intentaría suerte contra él. Todo lo que tuvo que mostrar por su esfuerzo fue esa fea cicatriz sobre su ojo.$B$BMata al oso, y estoy seguro de que incluso Daryl se quedaría sin palabras por una vez.$B$BEl oso, Viejo Hollín, se puede encontrar alrededor de su guarida en las colinas sobre Thelsamar.",
    ["O"] = "Mata al Viejo Hollín y luego muestra tu trabajo a Daryl el Joven en el Refugio de los Exploradores Lejanos.",
    ["T"] = "La Venganza de Vyrin",
  },
  [272] = {
    ["D"] = "Para la segunda prueba, necesitarás las dos mitades que forman el Colgante del León Marino. Una mitad obtiene su poder de la agilidad del aspecto del león marino, deslizándose a través del agua, la otra obtiene su poder de la increíble resistencia del aspecto del león marino. Los druidas se basan en ambos rasgos por igual para encarnar el aspecto de su forma acuática.$B$BHabla con los locales de Claro de la Luna para saber dónde pueden estar las partes, y tráelas aquí para unirlas. Una vez formado, lleva el colgante a Dendrite Starblaze.",
    ["O"] = "Encuentra la Mitad del Colgante de Agilidad Acuática y la Mitad del Colgante de Aguante Acuático. Habla con los residentes de Claro de la Luna para obtener pistas sobre dónde pueden estar estos objetos.$B$BForma el Colgante del León Marino a partir de las dos mitades. Debes estar cerca del Santuario de Remulos para hacer esto.$B$BLleva el colgante unido a Dendrite Destello Estelar en la aldea de Puesto de la Guardia Nocturna, Claro de la Luna.",
    ["T"] = "Prueba del León Marino",
  },
  [273] = {
    ["D"] = "Aún no es demasiado tarde, eres bienvenido a ir a buscar a Huldar. No puede haber llegado muy lejos, fuertes como son, Miran y Saean solo pueden moverse tan rápido cargados con esos barriles.$b$bEstán tomando la ruta normal al sitio, saliendo al sur del pueblo y siguiendo la curva del Loch.$b$bNo deberías tener problemas para encontrarlos a menos que ya hayan llegado.",
    ["O"] = "Habla con Huldar.",
    ["T"] = "Reabasteciendo la Excavación",
  },
  [274] = {
    ["D"] = "Este no es un polvo explosivo ordinario. Mira los pequeños cristales plateados. ¡Y el olor distintivo! Es tan claro como el día que esto es Polvo de Seaforio. El Seaforio es bastante inofensivo. Pero una vez que se moja, podría volar a Forjaz fuera de la montaña.$b$bLa reacción química puede ser desactivada mezclando cuatro componentes: veneno de acechador, cristal de Mo\'Grosh triturado, una lágrima de crocolisco y este coloide desactivador. ¡Ahora dile a Hinderweir antes de que sea demasiado tarde!",
    ["O"] = "Ashlan Stonesmirk quiere que regreses con el Jefe Ingeniero Hinderweir y le informes del nuevo descubrimiento.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [275] = {
    ["D"] = "No sé por qué, pero una infección crece dentro de los Humedales. Los Trepadores del Pantano han surgido. Son ampollas enfermas y febriles en la tierra, y para que la fiebre se rompa, deben ser despachados y permitir que se asienten de nuevo en los pantanos.$B$BLos Trepadores del Pantano deambulan entre los cursos de agua de los Humedales.",
    ["O"] = "Mata 12 Trepadores de las Ciénagas, luego regresa con Rethiel el Guardaverde en los Humedales.",
    ["T"] = "Ampollas en la Tierra",
  },
  [276] = {
    ["D"] = "Los Gnolls Mantoscuro han vivido en los Humedales pacíficamente durante muchos años, pero ahora están creciendo en número. Sus pies aplastan los arbustos y flores del pantano, y sus hachas cortan demasiada madera para alimentar sus fuegos.$B$BYa no están en equilibrio con la tierra. Para que los Humedales sobrevivan, los Gnolls deben ser reducidos.$B$BMata a 15 Gnolls Mantoscuro y 10 Canes Mantoscuro. Cázalos al sur, cerca de la puerta enana de Algaz. Después de hacer esto, regresa conmigo.",
    ["O"] = "Mata 15 Gnolls Pellejomusgo y 10 Canes Pellejomusgo, luego regresa con Rethiel el Guardaverde en los Humedales.",
    ["T"] = "Pisadas en el Suelo",
  },
  [277] = {
    ["D"] = "Los Mantoscuro están cortando los árboles de los Humedales, usando la carne de los árboles para calentarse con fuego. ¡No sufriré este dolor por más tiempo!$B$BDeshazte de los pedernales toscos de los Mantoscuro, las herramientas que usan para hacer fuego. Los pedernales son llevados por todos los Mantoscuro excepto los gnolls y canes más pequeños.",
    ["O"] = "Lleva a Rethiel el Guardaverde 9 Pedernales Crudos.",
    ["T"] = "Tabú de Fuego",
  },
  [278] = {
    ["D"] = "Comienza a recolectar los materiales de desarme de inmediato, $N. El veneno de Rondador se puede encontrar en las arañas indígenas de Loch Modan. Las lágrimas de crocolisco se encuentran aquí en el Loch también. Pero un cristal Mo\'Grosh será muy difícil de conseguir. Los ogros al noreste los extraen, pero los cristales son una mercancía muy rara.$B$B¡Necesitamos estar preparados para desactivar un ataque de los Hierro Negro! Regresa conmigo una vez que hayas recolectado los artículos necesarios y prepararé la mezcla.",
    ["O"] = "El Ingeniero Jefe Hinderweir quiere que reúnas Veneno de Rondador, un Cristal de Mo\'grosh y una Lágrima de Crocolisco.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [279] = {
    ["D"] = "Los murlocs están saliendo de las aguas profundas y construyendo sus aldeas en la costa. Están acosando a nuestros pescadores y comerciantes y deben ser detenidos.$B$BUno de estos murlocs, Gobbler, merodea con otros murlocs branquiazules y acosa a los comerciantes a lo largo del camino, siempre retirándose a la seguridad de las chozas murloc cercanas. Nuestros comerciantes están en peligro, y pagaremos para asegurarlos.$B$BGobbler merodea por la entrada del río al norte de aquí. Encuéntralo, mata a sus parientes y tráeme su cabeza.",
    ["O"] = "Mata 12 Múrlocs Branquiazules.$B$BMata a Glotón y llévale su cabeza a Karl Boran en el Puerto de Menethil.",
    ["T"] = "Garras de las Profundidades",
  },
  [280] = {
    ["D"] = "Hoy temprano vi a algunos insurgentes Hierro Negro nadando hacia la Presa con un gran barril. ¡No había montañeros para detenerlos! Me temo que han plantado el Seaforium en la base de la Presa y podría explotar en cualquier momento. Déjame combinar todos estos ingredientes en la Mezcla Desarmadora. Aquí vamos.$b$b¡Ahora necesito que tomes esta mezcla y la revuelvas en el barril antes de que sea demasiado tarde! ¡Date prisa!",
    ["O"] = "El Ingeniero Jefe Hinderweir quiere que nades hasta la base de la presa, localices el barril de pólvora y mezcles la Mezcla Desactivadora para evitar una explosión.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [281] = {
    ["D"] = "Hace no mucho tiempo, un barco mercante, la Princesa Ciega, fue atacado por Murlocs. Aunque el barco escapó intacto, gran parte de su carga fue arrastrada por las criaturas, incluyendo una valiosa estatuilla de Terenas Menethil. Queremos recuperar esa estatuilla.$B$BBusca en las chozas de los Murlocs costeros cajas, barriles y otras evidencias de carga perdida. Encuentra la Estatuilla de Menethil y devuélvemela.",
    ["O"] = "Busca en las cajas y barriles de los campamentos múrloc costeros.",
    ["T"] = "Recuperando Bienes",
  },
  [282] = {
    ["D"] = "Estaba planeando enviar a mi aprendiz con mi hermano Senir para entregar mi informe, pero me sentiría mucho mejor si lo pusiera en manos más... confiables. Eso es, por supuesto, si no te importa.$B$BHmm... tendrás que tomar el túnel para llegar a Kharanos.$B$BHabla con el Montaraz Thalos antes de pasar por el túnel. Ahora está completamente infestado de troggs.$B$BSigue el camino de regreso a Yunquemar, luego continúa hacia el este hasta el túnel. Thalos está estacionado cerca.",
    ["O"] = "Habla con el Montaraz Thalos.",
    ["T"] = "Observaciones de Senir",
  },
  [283] = {
    ["D"] = "El barril chisporrotea ligeramente.",
    ["O"] = "La Mezcla Desarmadora parece haber surtido efecto. Regresa con el Ingeniero Jefe Hinderweir para informar las buenas noticias.",
    ["T"] = "Una Amenaza Oscura se Cierne",
  },
  [284] = {
    ["D"] = "Revisas a fondo este grupo de chozas de múrlocs y no encuentras rastro de la Estatuilla de Menethil.$B$BTal vez una de las dos chozas cercanas al norte y al noreste tenga más pistas.",
    ["O"] = "Busca la Estatuilla de Menethil.",
    ["T"] = "La Búsqueda Continúa",
  },
  [285] = {
    ["D"] = "Este grupo de chozas Murloc, tras una inspección exhaustiva, no revela nada.$B$B... pero estás seguro de que la próxima pista que encuentres será fructífera.$B$B¡Una de las chozas al norte debe tener la Estatuilla de Menethil!",
    ["O"] = "Continúa buscando en las chozas de los murlocs la Estatuilla de Menethil.",
    ["T"] = "Buscar más Cabañas",
  },
  [286] = {
    ["D"] = "Después de sacar el barril del lodo y abrirlo, se encuentra una figurilla plateada empaquetada en paja fina. Representa a un joven paladín de cabello largo con un martillo levantado.$B$B¡Esta debe ser la Estatuilla de Menethil! Ahora debes devolverla a Karl Boran en el Puerto de Menethil.",
    ["O"] = "Lleva la Estatuilla de Menethil a Karl Boran.",
    ["T"] = "Devolver la Estatuilla",
  },
  [287] = {
    ["D"] = "Estoy atrasado con mi informe y podría usar tu ayuda, $n. Acabo de encontrar dónde se esconden los trolls, pero vi tantos que tuve miedo de entrar.$B$BAquí está lo que necesito... Ve a la cueva, mira adentro, mata a algunos de los trolls y luego regresa aquí.$B$BToma el camino al norte de Kharanos, cuando llegues al puente, sigue el río congelado hacia el oeste hasta llegar al Lago Flujo de Hielo. Encontrarás la Aldea Brewnall en la orilla oeste. La Fortaleza está al suroeste de la aldea.",
    ["O"] = "Explora la Guarida de los Pellejocéanos y mata a 5 Cazacabezas Pellejocéanos para Barbablanca Senir en Kharanos.",
    ["T"] = "Dominios Pielhelada",
  },
  [288] = {
    ["D"] = "Una vez fui el Primer Oficial del Señor de las Olas, el buque insignia de la Tercera Flota de Kul Tiras. La Flota Condenada. Condenada, porque en su último viaje la flota fue atacada por dragones rojos. ¡Dragones controlados por orcos!$B$BNo tuvimos ninguna posibilidad contra ese fuego de dragón... y nuestra flota fue quemada y hundida frente a esta costa. Soy el único superviviente de esos pobres barcos...$B$B...¡Oh, mi cabeza martilla y late! Necesitaré una bebida fresca si quiero controlar esta resaca y continuar mi historia.",
    ["O"] = "Compra un Jarro de Hidromiel al Primer Oficial Fitzsimmons.",
    ["T"] = "La Tercera Flota",
  },
  [289] = {
    ["D"] = "<Fortalecido, Fitzsimmons continúa su relato...>$B$BSoy el único superviviente de la desafortunada Tercera Flota de Kul Tiras, naufragada en la costa al norte. Pero aunque soy el único miembro de la flota que sigue respirando, ¡no soy el único que sigue caminando! En uno de nuestros barcos se escondía un gran y terrible tesoro, y el poder inquietante de este mantiene las almas de mis hermanos atrapadas dentro de los cascos podridos de los barcos.$B$BPero si puedes liberar a algunos de mis hermanos, quizás puedas romper su maldición.",
    ["O"] = "Mata 13 Marineros Malditos, 5 Marines Malditos y al Primer Oficial Snellig. Llévale el Tabaquera de Snellig al Primer Oficial Fitzsimmons en el Puerto de Menethil.",
    ["T"] = "La Tripulación Maldita",
  },
  [290] = {
    ["D"] = "¡Para romper la maldición y liberar a todos mis hermanos, la... cosa... escondida en mi barco debe ser recuperada y purificada! No sé qué era, pero sí sé que estaba almacenada de manera segura en la caja fuerte del barco, de la cual el Capitán Halyndor era el dueño.$B$BMi viejo capitán todavía está en mis sueños. Sé que camina por mi barco condenado, el Intrépido. Consigue la llave de él y úsala para abrir la caja fuerte del Intrépido.$B$BLuego, después de que tengas la cosa maldita, llévasela al sacerdote Glorin Steelbrow en el pueblo.",
    ["O"] = "Consigue la Llave del Cofre Fuerte del Intrépido del Capitán Halyndor.$B$BUsa la llave para abrir el Cofre Fuerte del Intrépido.",
    ["T"] = "Levantando la Maldición",
  },
  [291] = {
    ["D"] = "¡Excelente! Lleva mi informe al Senador Barin Redstone. Es un tipo amargado, así que no dejes que su disposición menos que soleada te afecte. Está en Forjaz, en la cámara donde el Rey Magni celebra la corte.$b$b¿No sabes el camino a Forjaz? Toma el camino que sale de Kharanos hacia el norte, cruza el puente y luego sigue el camino hacia el este—hay grandes banderas flanqueando el camino—hacia la montaña.$b$bPor cierto, si pudieras evitar mencionar cómo me ayudaste... No podemos dejar que piensen que no estoy trabajando duro aquí, ¿sabes?",
    ["O"] = "Entrega el informe de Senir al Senador Barin Redstone en Forjaz.",
    ["T"] = "Los Informes",
  },
  [292] = {
    ["D"] = "Al abrir el cofre, revelas un orbe ennegrecido, pulsando con una luz inquietante. Es el tesoro del que habló Fitzsimmons: un Ojo Maldito de Paleth.$B$BLleva el Ojo Maldito de Paleth al sacerdote enano Glorin Steelbrow, en el Puerto de Menethil. Pregúntale cómo eliminar la maldición del Ojo.",
    ["O"] = "Lleva el Ojo Maldito de Paleth a Glorin Steelbrow.",
    ["T"] = "El Ojo de Paleth",
  },
  [293] = {
    ["D"] = "No puedo levantar la maldición del Ojo de Paleth. Los Ojos fueron creados por Sacerdotes Humanos, así que tendrás que ir a Ventormenta para encontrar a alguien que pueda ayudarte. Habla con el Arzobispo Benedictus. Él es el jefe de la Catedral de la Luz en Ventormenta. Él puede ayudarte.$B$BEspero, por tu bien.",
    ["O"] = "Lleva el Ojo Maldito de Paleth al Arzobispo Benedictus. Benedictus está en la Catedral de la Luz, en la ciudad de Ventormenta.",
    ["T"] = "Purificando el Ojo",
  },
  [294] = {
    ["D"] = "La situación es grave, eso es seguro. Cuando descubrimos estos huesos, atrajo a los Raptores. Estas bestias asquerosas mataron a mis hermanos y me atraparon a mí, a Merrin y al pobre Prospectador aquí arriba.$B$BAyuda a limpiar los Humedales de estos Raptores, $N. Los Raptores moteados y los Chilladores moteados están justo al oeste del acantilado. Mata a 10 de cada uno, si puedes.$B$BEse será un buen comienzo para la venganza que tengo planeada para ellos.",
    ["O"] = "Ormer Ferrobrazo en el Sitio de Excavación de Whelgar quiere que mates a 10 Chillones Jaspeados y 10 Raptores Jaspeados.",
    ["T"] = "La Venganza de Ormer",
  },
  [295] = {
    ["D"] = "Ahora es el momento de hacer que esos temidos Raptores se arrepientan de su sed de sangre. Justo abajo hay montones de Raptores Filonudos y Raptores Filonudos Jaspeados. ¡Haz que esas criaturas podridas paguen matando a 10 de cada uno!",
    ["O"] = "Ormer Ferrobrazo quiere que mates a 10 Raptores Garrafilada Jaspeados y 10 Raptores Bocarraptor Jaspeados y luego regreses con él en el Sitio de Excavación de Whelgar.",
    ["T"] = "La Venganza de Ormer",
  },
  [296] = {
    ["D"] = "Mientras estabas allí abajo, noté que una de esas bestias se destacaba del resto. Era más grande y más amenazante. Apuesto a que es el que llevó a los demás aquí para causar la interrupción en el sitio de excavación.$b$bTe pido ahora una última tarea, $N. Asegúrate de que Sarltooth sea llevado ante la justicia. Y considerando la gravedad de sus crímenes, ¡la justicia en este caso significa la muerte! Tráeme una de sus garras como prueba de su muerte.",
    ["O"] = "Ormer Ferrobrazo en el Sitio de Excavación de Whelgar quiere que mates a Colmillo de Sarl y regreses con él con una de sus garras una vez cumplida la tarea.",
    ["T"] = "La Venganza de Ormer",
  },
  [297] = {
    ["D"] = "Recientemente, justo antes de que los troggs aparecieran en el sitio, habíamos desenterrado una gran cantidad de extraños ídolos tallados. Pero no tuvimos la oportunidad de estudiarlos, pues poco después de su descubrimiento los troggs nos expulsaron de las ruinas. ¡Y esos ídolos tienen un efecto extraño en los troggs! ¡Los vuelven locos!$B$BTráeme 8 ídolos, quiero estudiarlos y quiero que estén fuera de las manos de los troggs. Puedes encontrar los ídolos en los troggs que infestan el sitio.",
    ["O"] = "Lleva a Magmar Hachacarniza 8 Ídolos de Piedra Tallados.",
    ["T"] = "Reuniendo Ídolos",
  },
  [298] = {
    ["D"] = "Necesito enviar un informe de progreso a Thelsamar: un informe de progreso y una reiteración de mi solicitud de más pólvora. Aquí está el informe. Llévalo a Jern Hornhelm. Él es nuestro contacto local en Thelsamar.$B$BNo es una tarea difícil, pero debe hacerse.",
    ["O"] = "Lleva el Informe de Progreso de Vetaferro a Jern Cuerno de Helm en Thelsamar.",
    ["T"] = "Informe de Progreso de la Excavación",
  },
  [299] = {
    ["D"] = "Justo antes de la invasión, descubrí una gran tableta llamada la Piedra de Goaz. El texto traducido se interrumpe en 4 lugares. El texto habla de un \"plan divino\" y una \"profecía condenada\".$b$bTemo que se nos acaba el tiempo. Soy demasiado viejo para bajar y enfrentarme a esas bestias. Pero tú eres fuerte.$b$bRegistra el sitio de excavación y descubre los 4 fragmentos de tableta que faltan: Ados, Modr, Golm y Neru. Búscalos en artefactos antiguos o donde la tierra esté suelta. Tráemelos para que pueda comenzar a desbloquear el misterio.",
    ["O"] = "El Prospector Whelgar quiere que examines el yacimiento en busca de los 4 fragmentos de tablilla faltantes: Ados, Modr, Golm y Neru.",
    ["T"] = "Descubriendo el Pasado",
  },
  [301] = {
    ["D"] = "Necesitaremos aprobación para conseguir más pólvora para Ironband. Tendrás que llevar su informe a la sede de la Liga de Expedicionarios en Forjaz.$B$BAquí está el informe. Entrégaselo al Prospector Stormpike, y no olvides agacharte después de entregarlo. Stormpike es temperamental, y no estará contento de escuchar sobre el lento progreso de Ironband en el sitio.$B$BEncontrarás a Stormpike en la Asamblea de Expedicionarios.",
    ["O"] = "Lleva el Informe de Progreso de Vetaferro al Prospector Pico Tormenta.",
    ["T"] = "Informe a Forjaz",
  },
  [302] = {
    ["D"] = "Si Ironband todavía necesita pólvora, ¡por Magni que la tendrá! Envié la aprobación por adelantado con un mensajero a Jern Hornhelm en Thelsamar. Él preparará la pólvora y otros suministros para llevarlos a Ironband.$B$BY una cosa más: quiero que te asegures personalmente de que el envío de pólvora llegue a su destino. El último no lo hizo, así que mantén la mente despejada y los ojos abiertos.",
    ["O"] = "Habla con Jern Yelmocerno en Thelsamar.",
    ["T"] = "Polvo para Vetaferro",
  },
  [303] = {
    ["D"] = "La guerra arrecia tanto en casa como en el extranjero. Mientras los valientes soldados enanos dan sus vidas en territorios lejanos en nombre de la Alianza, nuestras propias tierras caen presa de los malvados actos de los Enanos Hierro Negro. Destruyeron el Puente Thandol. Dun Modr cayó poco después.$B$B¡Necesitamos que reúnas fuerzas y empujes el frente hacia atrás! Si puedes matar a 15 Enanos Hierro Negro, 5 Tuneladores, 5 Saboteadores y 5 Demolicionistas, la reserva podría llegar a tiempo para terminar el empuje.$B$B¡Date prisa!",
    ["O"] = "Motley Garmason en Dun Modr quiere que mates 15 Enanos Hierro Negro, 5 Tuneladores Hierro Negro, 5 Saboteadores Hierro Negro y 5 Demoledores Hierro Negro.",
    ["T"] = "La Guerra Hierro Negro",
  },
  [304] = {
    ["D"] = "Muchos de mis soldados murieron en la batalla de Dun Modr. Mi propio hermano pereció cuando el Puente Oeste sobre el Vado Thandol fue destruido. Sí, los enanos Hierro Negro son una raza diabólica. Por lo que pudo recopilar Roggo, el líder de esta banda de matones en los Humedales es un brujo llamado Balgaras el Fétido. Sin embargo, es bastante cobarde. Envía a sus soldados a hacer su trabajo, pero se mantiene elusivo, evitando el peligro.$b$bSi puedes encontrar a ese cobarde, mátalo y tráeme su oreja. Te recompensaré, $c.",
    ["O"] = "Mata a Balgaras el Fétido y lleva su oreja a Barbalarga el Severo fuera de Dun Modr.",
    ["T"] = "Una Tarea Sombría",
  },
  [305] = {
    ["D"] = "¡Oh, cielos, debes ser el explorador del que habló Longbraid! No puedes imaginar lo preocupado que he estado.$b$bMi esposa, Merrin, me ha estado enviando cartas cada semana durante el último mes. Ella es parte del equipo de excavación de Whelgar. Pero ha pasado casi dos semanas desde su última correspondencia. ¡Seguro que algo anda mal!$b$bLongbraid dijo que enviaría un explorador para ver cómo estaban las cosas si no recibíamos noticias del equipo pronto. Supongo que ese explorador eres tú, $N. ¡Encuentra a Merrin en el sitio de excavación al este!",
    ["O"] = "Tarrel Tejerroca quiere que viajes al Sitio de Excavación y contactes a Merrin Tejerroca.",
    ["T"] = "En Busca del Equipo de Excavación",
  },
  [306] = {
    ["D"] = "¡Fue horrible! Estábamos descubriendo algunos artefactos raros y comenzamos a encontrar estos grandes huesos.$b$bAntes de que supiéramos lo que estaba pasando, estos raptores moteados abrumaron el sitio de excavación. Muchas vidas se perdieron.$b$bOrmer aquí, tan valiente, salvó al Prospector y a mí. Nos defendió mientras corríamos hacia un refugio. Ahora estamos atrapados. Y nuestro arduo trabajo yace en ruinas.$b$bAquí, lleva esta nota de vuelta a mi dulce Tarrel. Le explicará la situación.",
    ["O"] = "Regresa con Tarrel Tejerroca con la nota de Merrin.",
    ["T"] = "En Busca del Equipo de Excavación",
  },
  [307] = {
    ["D"] = "La Mina Arroyo de Plata al este se secó hace mucho tiempo. Fue convertida por la Liga de Mineros en un depósito de almacenamiento, pero ahora los kóbolds se han mudado, ¡poniendo sus sucias patas en buenas herramientas Enanas!$B$B¡Pronto echaremos a esos bichos, pero la Liga quiere que alguien saque su equipo de la mina antes de que nosotros, los guerreros, entremos allí y rompamos cosas! Será una inmersión difícil, quizás quieras compañeros a tu lado.$B$BEl equipo está almacenado en Cajas de la Liga de Mineros por toda la mina. ¡Buena suerte!",
    ["O"] = "Ve a la Mina de la Veta de Plata y recolecta 4 cargas de Equipo de Minero.$B$BRegresa con el Montañero Pico Tormenta.",
    ["T"] = "Garras Sucias",
  },
  [308] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Distrayendo a Jarven",
  },
  [309] = {
    ["D"] = "¿Listo para partir, $n?$b$bPrimero, necesitamos llevar esta pólvora a Ironband. Será mucho para mí cargar, y estas partes pueden ser peligrosas, y quién sabe qué más podrían tener los Hierro Negro preparado para mí.$b$bMe sentiré mucho mejor si vienes conmigo.",
    ["O"] = "Asegúrate de que Miran y el cargamento lleguen al sitio de excavación, luego informa al Prospector Ironband.",
    ["T"] = "Protegiendo el Cargamento",
  },
  [310] = {
    ["D"] = "Debo admitir que esos Thunderbrew hacen buenas bebidas. ¡Pero necesitan aprender que la suya no es la única bebida decente! Quizás puedas ayudarme a enseñarles esa lección...$B$BAquí, toma este barril de Cebadizador Cebada. Escóndete en el sótano de la Destilería Thunderbrew en Kharanos y cámbialo por uno de sus barriles de Cerveza Trueno. ¡Entonces veremos cómo les gusta a sus clientes mi bebida sobre la suya!$B$BY si los barriles están vigilados, entonces tendrás que distraer al guardia ...",
    ["O"] = "En el sótano de la Destilería Cebatruenos en Kharanos, reemplaza un barril de Cerveza Trueno por un Barril de Escaldador Cebada.",
    ["T"] = "Rivales Amargos",
  },
  [311] = {
    ["D"] = "Después de insertar un barril de Escaldador de Marleth en la bodega de la Destilería de Kharanos, agarras un barril de Cerveza Trueno como prueba del hecho...",
    ["O"] = "Lleva el barril de Cerveza de Trueno a Marleth Barleybrew en la Aldea Brewnall.",
    ["T"] = "Regreso con Marleth",
  },
  [312] = {
    ["D"] = "Tenía un mes de carne seca salada y guardada bajo llave para la temporada fría. ¡La mantuve cerrada para que los osos no la consiguieran! Pero mientras estaba cazando ciervos, esa bestia, Viejo Barbagelada, se llevó el armario de la carne. No podrá romper ese torio, pero me moriré de hambre si no recupero mi carne.$b$bPareces más sigiloso que yo. ¿Quizás puedas recuperar mi reserva? Nunca podría entrar y salir de esa cueva lo suficientemente rápido con esta pierna maltrecha.",
    ["O"] = "Recupera las carnes secas de Tundra MacGrann del armario de carne robado en la cueva de Viejo Barbagélida.",
    ["T"] = "El Alijo Robado de Tundra MacGrann",
  },
  [313] = {
    ["D"] = "Conducir un Motor de Asedio no es para todos. Se necesita un agarre de hierro y nervios de acero... ¡afortunadamente, yo tengo ambos! ¿Y tú? ¿Cómo está tu temple? ¿Quieres demostrármelo?$B$BBueno... estoy buscando hacer que mi motor, Arado Troll, sea un poco más acogedor. ¿Qué tal si me consigues un montón de crines de Wendigo de la Guarida Gris, al oeste de Kharanos? Esas crines harían una gran alfombra para la cubierta interior de Arado Troll.",
    ["O"] = "Reúne 8 Melenas de Wendigo y llévaselas al Piloto Engranaje de Piedra.",
    ["T"] = "La Guarida Gris",
  },
  [314] = {
    ["D"] = "Escuchamos los gritos en medio de la noche. Luego, esta mañana, efectivamente, la manada había perdido dos carneros. Esa bestia infame conocida como Vagash ha estado atacando nuestro sustento. Con la mayor parte del ejército del Rey Magni en tierras lejanas luchando con la Alianza, no hay nadie que mantenga a Vagash a raya.$b$bQuizás seas lo suficientemente valiente como para buscar a la bestia y matarla. Tráeme uno de sus colmillos y te recompensaré. Vagash merodea justo arriba del rancho aquí, pero ten cuidado, es mortal.",
    ["O"] = "Rudra Amberstill quiere que mates a Vagash y le lleves su colmillo al Rancho de Carneros.",
    ["T"] = "Protegiendo la Manada",
  },
  [315] = {
    ["D"] = "Estoy en una misión, una misión para hacer la cerveza negra perfecta. Sé que puedo hacerlo, la cerveza corre por mis venas. Solo necesito encontrar la receta correcta...$B$BLos trolls Peloescarcha cultivan una planta, la hierba brillante, en lo alto de las colinas al este. La usan en sus extraños rituales tribales. Los enanos no le hemos encontrado mucho uso, pero tiene un sabor único... ¡y quiero experimentar con ella en mis cervezas!$B$B¡Consígueme algo de hierba brillante de los videntes Peloescarcha o tómala de las cestas de hierba brillante de los trolls!",
    ["O"] = "Lleva 6 Hierbaluz a Rejold Cebada en la Aldea Cebadiz.",
    ["T"] = "La Cerveza Perfecta",
  },
  [316] = "_",
  [317] = {
    ["D"] = "Me estoy preparando para comenzar una misión para la Brigada de Asedio. Es una misión larga, y necesito abastecer a Jetsteam con suministros para un mes. Así que, mientras Steelgrill trabaja en mi tanque, ¿podrías cazar algo por mí?$B$BNecesitaré algo de piel para la cama y carne de jabalí para la comida. Puedes conseguir carne de los jabalíes y piel de los osos... puedes encontrarlos a ambos en los campos de nieve al sur de la Guarida Gris.",
    ["O"] = "Reúne 4 Trozos de Carne de Jabalí y 2 Pieles de Oso Gruesas y entrégaselas al Piloto Burbuceo en el Depósito de Grillacerado.",
    ["T"] = "Abasteciendo a Vaporino",
  },
  [318] = {
    ["D"] = "Rejold Barleybrew experimenta con sus brebajes. Algunos saben bien, otros mal, y algunos... bueno, algunos dejarán en el suelo a todos menos al enano más robusto.$B$BÉl vive en la Aldea Brewnall, al oeste de Kharanos, y una de sus bebidas, Evershine, es lo que quiero. Me ayudará a mantenerme caliente mientras estoy en el frío camino.$B$BY... ¡el bendito líquido arde tan caliente que puedo tirarlo en el horno de Jetsteam para un estallido de poder! Esa pequeña táctica me ha sacado de más de un aprieto.",
    ["O"] = "Consigue un barril de Siemprebrillo de Rejold Cebadiz en Villa Cebadiz.",
    ["T"] = "Siemprebrillo",
  },
  [319] = {
    ["D"] = "Tengo un barril de Siemprebrillo a mano, pero... ¿puedes hacerme un favor? El pueblo de Brewnall podría usar una mano contra los animales salvajes que deambulan por las nieves cercanas. A veces se acercan.$B$BNo tenemos miedo de estos osos, gatos y jabalíes, ningún enano que se respete lo tendría, pero si puedes encargarte de esos animales, tendríamos más tiempo para nuestra artesanía y elaboración de cerveza.",
    ["O"] = "Mata 6 Osos Garra Helada, 8 Jabalíes del Risco Ancianos y 8 Leopardos de las Nieves, y luego regresa con Rejold Cebadiz en Villa Cebadiz.",
    ["T"] = "Un Favor para Siemprebrillo",
  },
  [320] = {
    ["D"] = "Aquí está tu barril de Siemprebrillo, $N. Y de nuevo, gracias por la caza anterior.",
    ["O"] = "Dale el barril de Siemprebrillo al Piloto Burbuceo en el Depósito de Hierroforjado.",
    ["T"] = "Regreso con Burbuceo",
  },
  [321] = {
    ["D"] = "Los naufragios frente a la costa aquí son los restos de la Tercera Flota de Kul\'Tiras, quemados y hundidos por los Dragones Rojos durante la última Gran Guerra.$B$BLa Flota provenía de Lordaeron, pero un barco, un Destructor Élfico: Gavilán Volador, estaba con ellos. Estaba transportando un cargamento de Hierro Forjaluz y se unió a la Tercera Flota para protección. Irónico que se hundiera por los mismos dragones...$B$BBasta de historia. El Gavilán Volador está naufragado al sur del Puerto de Menethil. Si encuentras el Hierro Forjaluz allí, tráemelo.",
    ["O"] = "Busca en los restos del El Halcón Marino en Los Humedales.",
    ["T"] = "Hierro Forjaluz",
  },
  [322] = {
    ["D"] = "Hay un herrero enano en el Distrito de los Enanos de Ventormenta que puede trabajar con hierro forjaluz. Su nombre es Grimand Elmore.$B$BHe empaquetado el hierro que encontraste en una caja. Llévasela a Grimand y cuéntale tu historia. Estoy seguro de que complacerá a alguien en una misión como la tuya, y te forjará un arma, ¡un arma que incluso le daría preocupación a Morbent Fel!",
    ["O"] = "Lleva el Crate de Lingotes Forjaluz a Grimand Elmore en Ventormenta.",
    ["T"] = "Brazo Bendito",
  },
  [323] = {
    ["D"] = "Morbent Fel es un nigromante y un aliado de los jinetes oscuros. Te enfrentaría al poder de Morbent Fel, pero no te enviaré contra ese vil demonio sin antes conocer tu fuerza.$B$BYa has demostrado tu valentía ante mí, pero si realmente deseas enfrentarte al nigromante, entonces ahora debes demostrar tu habilidad contra sus esbirros.",
    ["O"] = "Mata a 15 Asaltantes Esqueléticos, 3 Sanadores Esqueléticos y 3 Guardias Esqueléticos, y luego regresa con Sven.",
    ["T"] = "Demostrando tu Valor",
  },
  [324] = {
    ["D"] = "Después de agarrar el único lingote de forjaluz del cofre anegado, está claro que los múrlocs han robado el resto. Caza a los asaltantes múrlocs cerca del naufragio del Halcón Marino en busca del hierro de forjaluz perdido.",
    ["O"] = "Reúne 5 Lingotes Forjaluz y luego regresa con Glorin Frenteférrea en los Humedales.",
    ["T"] = "Los Lingotes Perdidos",
  },
  [325] = {
    ["D"] = "Aquí tienes el arma contra el demonio del que hablaste. Estás listo, $N. ¡Te deseo la mejor de las suertes y espero escuchar tu historia de gloria!",
    ["O"] = "Lleva el Flagelo de Morbent a Sven en Bosque del Ocaso.",
    ["T"] = "Armado y Listo",
  },
  [328] = {
    ["D"] = "Aunque no se encuentra mención del Soldado Thorsen en estos registros, dentro del lomo de uno de los libros de contabilidad de Herod hay una pequeña llave de latón.$B$BY después de un estudio más detallado de sus notas, se encuentran algunas referencias a una \"caja fuerte\" entre varios papeles. La caja está en algún lugar dentro del Almacén, el complejo de cavernas en la parte trasera del Complejo de Kurzen.$B$BSi se encuentra esta caja fuerte, y la pequeña llave de latón puede abrirla, entonces quizás se revele el misterio detrás de la detección de Thorsen.",
    ["O"] = "Usa la Llave Pequeña de Latón para abrir el Cofre Fuerte de Bookie Herod.",
    ["T"] = "La Llave Oculta",
  },
  [329] = {
    ["D"] = "Entre los objetos de valor de la caja fuerte hay una nota sucia y garabateada. Aunque apenas legible, parece ser un itinerario para el Soldado Thorsen. Detalla sus rutas de patrulla, con quién habla y cómo pasa su tiempo con los rebeldes. Está firmada con una sola letra: -M.$B$BPero quien escribió esta nota debió haber estado muy cerca de Thorsen. Quien escribió esta nota es el traidor dentro del campamento rebelde.",
    ["O"] = "Lleva la Nota Enlodada al Teniente Doren en el Campamento Rebelde.",
    ["T"] = "¡El Espía Revelado!",
  },
  [330] = {
    ["D"] = "La nota que me diste está firmada con una \"-M\"... No sé qué podría significar.$B$BPero... la nota también dice que esta persona compartió rutas de patrulla con Thorsen la semana pasada. ¡Así que quien compartió patrulla con Thorsen la semana pasada debe ser el traidor!$B$BVe con el Cabo Sethman. Él maneja los horarios de patrulla y puede decirte quién estaba con Thorsen.",
    ["O"] = "Habla con el Cabo Sethman.",
    ["T"] = "Horarios de Patrulla",
  },
  [331] = {
    ["D"] = "¿El Teniente Doren quería que le informaras con esa información de patrulla?",
    ["O"] = "Habla con el Teniente Doren en el Campamento Rebelde.",
    ["T"] = "Informe a Doren",
  },
  [332] = {
    ["D"] = "Si hablas con alguien sobre vino, entonces sabes que los Gallinas vendemos el mejor vino de Ventormenta. Y no estamos lejos, todavía en el Distrito de Mercaderes a lo largo de los encantadores canales de la ciudad.$B$BAquí, toma este folleto. Llévaselo a mi hermana Suzetta en nuestra tienda para una botella complementaria de nuestro famoso pinot noir. No te arrepentirás.",
    ["O"] = "Ve a la Viña Gallina, y llévale a Suzetta Gallina el Cupón de Vino para una botella de vino gratis.",
    ["T"] = "Publicidad de la Tienda de Vinos",
  },
  [333] = {
    ["D"] = "Hemos estado haciendo muchos negocios últimamente. Parece que todos están comprando armaduras y ropa resistente. Casi como si esperaran una temporada fría y dura por delante...$B$BPero esas son preocupaciones futuras. Mi preocupación hoy es que me estoy quedando sin ropa tejida para vender. Necesito otro cargamento de nuestro proveedor.$B$BSi puedes llevar esta solicitud a Rema Schneider en la Tienda de Sastrería y Ajuste del Canal, te lo agradecería mucho.",
    ["O"] = "Ve a la Sastrería del Canal y llévale a Rema Schneider la Solicitud de Tela de Harlan Bagley.",
    ["T"] = "Harlan Necesita Reabastecerse",
  },
  [334] = {
    ["D"] = "Mi hijo Thurman es un aprendiz en los Sastres Larson, en el Barrio de los Magos. Tenía prisa hoy y olvidó sus tijeras y agujas. Sé que un gran $C como tú debe tener tareas importantes en mano, pero sin sus herramientas Thurman no puede hacer su trabajo de aprendiz.$B$BPor favor, $N. ¿Puedes llevar el kit de costura de mi hijo a él? Los Sastres Larson es una de las dos tiendas de ropa en el Barrio de los Magos, es la más adentro, cerca de la Torre de los Magos.",
    ["O"] = "Ve a la Sastrería Larson en el Barrio de los Magos de Ventormenta y entrégale a Thurman Schneider su Kit de Costura.",
    ["T"] = "Paquete para Thurman",
  },
  [335] = {
    ["D"] = "El Lord Wishock está causando bastante revuelo entre la Casa de los Nobles. Se enteró de nuestro pequeño \"establecimiento\" aquí abajo y está presionando para una investigación completa. Tengo un plan para mantener a Wishock \"ocupado\", pero necesitaré tu ayuda.$B$BEn el Jardín de Stalvan en las afueras de Villa Oscura crece una flor. Esta pequeña flor blanca es conocida como la Lágrima de Tilloa. También necesitaré una Raíz de Musquash, que solo se encuentra en la base de las cataratas debajo de la Presa Forjada en los Humedales.",
    ["O"] = "Zardeth la Garra Negra quiere que le lleves una Lágrima de Tilloa de Villa Oscura y una Raíz de Musquash de los Humedales.",
    ["T"] = "Una Cerveza Noble",
  },
  [336] = {
    ["D"] = "El Lord Wishock, como todos los hombres, tiene sus debilidades. La suya es su afición por el licor. La hipocresía es asombrosa. El tonto trivial pasa sus días tratando de cerrar nuestra pequeña \"taberna\" mientras que sus noches las pasa arriba, acumulando una cuenta que nunca paga. Bueno, veamos cómo le gusta a Wishock esta cerveza especial que he preparado para él: la cerveza negra Garra Negra. Tiene bastante fuerza, $n.$B$BLlévasela y ve cómo le gusta. Lo más probable es que el viejo tonto esté merodeando por el Salón de los Peticionarios.",
    ["O"] = "Lleva la Cerveza Irisada de Garra Negra a Lord Wishock en el Salón de los Suplicantes.",
    ["T"] = "Una Cerveza Noble",
  },
  [337] = {
    ["D"] = "Aunque la portada de este libro está dañada por rayones y cubierta de barro, las palabras \"La Historia de Ventormenta\" aún pueden leerse a lo largo de su lomo. Al abrir el libro, el sello de la Biblioteca de Ventormenta está claramente estampado en la primera página.$B$BParece que alguien sacó este libro y nunca lo devolvió...",
    ["O"] = "Devuelve el libro: La Historia de Ventormenta, a la Biblioteca de Ventormenta.",
    ["T"] = "Un Viejo Libro de Historia",
  },
  [338] = {
    ["D"] = "¡El Maestro Nesingwary estará tan molesto si se entera!$b$bÉl confió en mí para corregir el manuscrito de su novela, Las Colinas Verdes de Tuercespina. Todo iba bien, pero una gran ráfaga de viento pasó por el campamento y esparció las páginas por todas partes.$b$bPude reunir la mayoría de las páginas, pero muchas todavía están esparcidas por la jungla. Recoge las páginas para completar cada uno de los cuatro capítulos.$b$bTe ayudaré a organizarlas. Devuélveme los capítulos una vez que los tengas todos.",
    ["O"] = "Recolecta las páginas faltantes del manuscrito Las Verdes Colinas de Tuercespina. Una vez que los cuatro capítulos estén completos, devuélveselos a Barnil.",
    ["T"] = "Las Verdes Colinas de Tuercespina",
  },
  [339] = {
    ["D"] = "¡El Capítulo I fue bastante emocionante! El Maestro Nesingwary establece el trasfondo de la historia y menciona a sus compañeros de caza, ¡incluyéndome a mí!$b$bPero eso no es el punto. Déjame ver qué páginas todavía faltan. Parece que las páginas 1, 4, 6 y 8 todavía están por ahí en alguna parte de la jungla!",
    ["O"] = "Lleva las páginas 1, 4, 6 y 8 de Las Verdes Colinas de Tuercespina de Nesingwary a Barnil Marmolada para completar el Capítulo I.",
    ["T"] = "Capítulo I",
  },
  [340] = {
    ["D"] = "El Capítulo II estaba lleno de emocionantes historias de caza de panteras y tigres. ¡Podría patearme por dejar que las páginas se volaran!$b$b Parece que pude recuperar todas las páginas excepto las páginas 10, 11, 14 y 16.",
    ["O"] = "Lleva las páginas 10, 11, 14 y 16 de Las Verdes Colinas de Tuercespina de Nesingwary a Barnil Marmolada para completar el Capítulo II.",
    ["T"] = "Capítulo II",
  },
  [341] = {
    ["D"] = "El Capítulo III era mi favorito. Habla de la primera matanza de Sir Erlgadin y Lady Rouack con un rifle enano. ¡Y el final del Capítulo III, vaya, qué cliffhanger!$b$bParece que todavía necesitamos las páginas 18, 20, 21 y 24 para terminar el capítulo. Esas páginas están por ahí en alguna parte...",
    ["O"] = "Lleva las páginas 18, 20, 21 y 24 de Las Verdes Colinas de Tuercespina de Nesingwary a Barnil Marmolada para completar el Capítulo III.",
    ["T"] = "Capítulo III",
  },
  [342] = {
    ["D"] = "Ah, el Capítulo IV; ¡el gran final! ¡Y qué final fue!$B$BSolo nos faltan tres páginas para este capítulo. Necesitamos las páginas 25, 26 y 27 para reconstruir el Capítulo IV.",
    ["O"] = "Lleva las páginas 25, 26 y 27 de Las Verdes Colinas de Tuercespina de Nesingwary a Barnil Marmolada para completar el Capítulo IV.",
    ["T"] = "Capítulo IV",
  },
  [343] = {
    ["D"] = "Estoy dando un discurso sobre la Fortaleza del Espíritu, y me gustaría recitar pasajes de un libro en particular sobre metalurgia: \"Las Tensiones del Hierro\". Una copia de este libro debería estar en la Biblioteca Real, dentro del Castillo de Ventormenta. ¿Puedes recuperarlo para mí? Si es así, ¡que las bendiciones de la Luz estén contigo!$B$BLa Biblioteca Real es muy grande, pero el bibliotecario jefe, Milton Sheaf, conoce cada estante como si fuera su propio hijo. Habla con él, él te traerá el libro.",
    ["O"] = "Ve a la Biblioteca Real en la Fortaleza de Ventormenta y habla con Milton Sheaf. Él puede encontrar para ti el libro sobre metalurgia que el Hermano Kristoff necesita para su discurso.",
    ["T"] = "Hablando de Fortaleza",
  },
  [344] = {
    ["D"] = "¿Las Tensiones del Hierro? Sí, conozco el libro. Uno bastante bueno, escrito por el minero enano Margulf Blaggon hace unas décadas. Pasó la mayor parte de su tiempo en las profundidades de las montañas de Khaz Modan, extrayendo mineral. ¡Un tipo inteligente!$B$B¿Pero dónde estaba? ¡Ah sí, Las Tensiones del Hierro! Me temo que el libro fue trasladado a la Abadía de Villanorte. El cuidador de la biblioteca de la Abadía, el Hermano Paxton, quería hacer una copia del libro para sus propios estantes.$B$BPero eso fue hace meses. Me pregunto por qué le está tomando tanto tiempo...",
    ["O"] = "Habla con el Hermano Paxton en la Abadía de Villanorte.",
    ["T"] = "Hermano Paxton",
  },
  [345] = {
    ["D"] = "He estado queriendo terminar mi copia de Los Estrés del Hierro, pero...$B$BBueno, verás, hago mi propia tinta para los libros que copio. Es mi propia receta, no se corre ni mancha, y se seca muy rápidamente. Desafortunadamente, ¡se seca demasiado rápido! Mi último lote se secó antes de que pudiera terminar el trabajo de copia, y no he tenido la oportunidad de hacer más tinta.$B$BPara hacer más, necesito mineral de hierro Rethban de las Montañas Crestagrana. El capataz Oslow en Villa del Lago es mi proveedor habitual.",
    ["O"] = "Habla con el Capataz Oslow en Villa del Lago y pídele Mineral de Hierro de Rethban.",
    ["T"] = "Suministros de Tinta",
  },
  [346] = {
    ["D"] = "Ahora que he terminado de copiar el libro, aquí está el original de Las Tensiones del Hierro. Gracias por tu paciencia, y si ves a Milton, dile que lamento la larga demora.",
    ["O"] = "Regresa con el Hermano Kristoff en la Plaza de la Catedral. Entrégale el libro Las Tensiones del Hierro.",
    ["T"] = "Regreso con Kristoff",
  },
  [347] = {
    ["D"] = "El Mineral de Rethban lleva el nombre del hombre que lo encontró por primera vez, Bart Rethban. Cuando se funde, no es tan fuerte como el hierro, pero su mineral tiene un color oscuro y plano que algunos escribas usan en sus tintes. Se puede encontrar en las Cavernas de Rethban, al norte de aquí.$B$BNormalmente tenemos algo en stock, pero estamos demasiado ocupados preparándonos para los ataques orcos para recolectar más. Y además... los gnolls de Crestagrana ahora viven en las Cavernas de Rethban y sus Esclavos están recolectando el mineral ellos mismos.$B$BAsí que si necesitas algo, tendrás que conseguirlo tú mismo.",
    ["O"] = "Reúne 5 cargas de Mineral de Rethban y llévaselas al Hermano Paxton en la Biblioteca de Villanorte.",
    ["T"] = "Mena de Rethban",
  },
  [348] = {
    ["D"] = "Enfermo... tan... muy enfermo....$b$b...he contraído la Fiebre de Tuercespina... un virus mortal...$b$b...la única cura... es... es... comer el corazón de M-m-m-mokk el Salvaje... una bestia blanca escurridiza...$b$b...el Médico Brujo Unbagwa... es el único que puede invocarlo....$b$b...busca a Unbagwa en la cueva oculta en el cabo sur...$b$b...date prisa... estoy muriendo... muriendo....",
    ["O"] = "Busca al Médico Brujo Unbagwa y haz que invoque a Mokk el Salvaje. Lleva el Corazón de Mokk a Fin Fizracket.",
    ["T"] = "Fiebre de Tuercespina",
  },
  [349] = {
    ["D"] = "Texto temporal 02 - descripción",
    ["O"] = "Texto temporal 02 - registro",
    ["T"] = "Fiebre de Tuercespina",
  },
  [350] = {
    ["D"] = "Que Marzon estuviera contactando a un miembro de los Defias y tuviera lazos con Lescovar...$B$BAl haber matado a VanCleef y a Thredd, sería difícil llevar a Lescovar ante la justicia, ya que tu evidencia no es nada sin sus testimonios. Eso sin considerar que Lescovar es un noble y está bien conectado. Básicamente, está por encima de la ley.$B$BVas a necesitar otra ayuda. Odio hacerlo, pero hay alguien que conozco que nos ayudará. Encontrarás a Trias en el Distrito de Mercaderes de la ciudad.",
    ["O"] = "Habla con Elling Trias en Ventormenta.",
    ["T"] = "Mira a un Viejo Amigo",
  },
  [351] = {
    ["D"] = "Has descubierto una especie de dispositivo extraño en forma de huevo hecho de metal. Jugando con una de sus perillas, el huevo cobra vida y se abre en una especie de artilugio robótico gnomo. Una voz desde dentro del huevo robótico cobra vida.$B$B\"¡Mi nombre es Oglethorpe, y mi robot de localización ha desaparecido! ¡Te recompensaré por su recuperación! Por favor, lleva este faro al robot. ¡Excelente, ahora he calculado las coordenadas para ti! ¡Se estrelló cerca del Abismo Abierto en Tanaris!\"",
    ["O"] = "Lleva la baliza de socorro al robot de guiado de Oglethorpe.",
    ["T"] = "¡Encuentra a OOX-17/TN!",
  },
  [352] = "_",
  [353] = {
    ["D"] = "Los Pico Tormenta son un clan enano respetado y son bien conocidos por sus gustos finos y exigentes. Así que no es de extrañar que Gringer Pico Tormenta, un montañero de Forjaz, me encargara fabricarle un arma.$B$BEl arma está terminada, pero... el Montañero Pico Tormenta está muy lejos, en el lejano Loch Modan. Si planeas viajar al norte, ¿puedes entregarle este paquete?$B$BMi último mensaje del Montañero Pico Tormenta decía que está estacionado en la torre de vigilancia del norte en Loch Modan.",
    ["O"] = "Entrega el Paquete para Pico Tormenta al Montaraz Pico Tormenta en Loch Modan.",
    ["T"] = "La Entrega de Pico Tormenta",
  },
  [354] = {
    ["D"] = "La familia Agamand era la familia más próspera en los Claros de Tirisfal. Yo solía trabajar en sus molinos... antes de la Plaga.$B$BCuando el Azote llegó por primera vez, los Agamand fortificaron su hogar y convencieron a aquellos bajo su empleo de quedarse y ayudarlos a defenderse. Fuimos tontos, pero al menos éramos tontos leales.$B$BLos Agamand, en su orgullo, nos condenaron a la no-muerte. ¡Y ahora son siervos del Azote!$B$BSirve a los No-Muertos derrotando a los Agamand que cayeron ante la Plaga. Sírveme trayéndome sus restos.",
    ["O"] = "Lleva los Restos de Gregor, los Restos de Nissa y los Restos de Thurman a Coleman Farthing en Rémol.",
    ["T"] = "Muertes en la Familia",
  },
  [355] = {
    ["D"] = "Me has dado los restos de los Agamand, y has satisfecho mi deseo de venganza. Pero los Molinos Agamand representan una amenaza para todos los No-Muertos, no solo para mí.$B$BHe hablado con el Magistrado Sevren sobre tus hazañas contra los Agamand, y desea hablar contigo.",
    ["O"] = "Habla con el Magistrado Sevren en Rémol.",
    ["T"] = "Habla con Sevren",
  },
  [356] = {
    ["D"] = "Los defensores en El Baluarte protegiendo Tirisfal de las Tierras de la Peste están en constante alerta. Pero a veces el Azote se les escapa.$B$BNuestro éxito en El Baluarte depende de una batalla en un solo frente: no podemos permitir un ataque por la retaguardia, ni que se corte la línea de suministros de nuestros defensores.$B$BAyuda a El Baluarte. Patrulla hacia el este y mata a cualquier miembro del Azote que encuentres. Dedica un esfuerzo especial en la Granja Balnir al este. Se ha convertido en un refugio para el Azote intruso.",
    ["O"] = "Mata 8 Horrores Sangrantes y 8 Espíritus Errantes, luego informa a Linnea en su campamento.",
    ["T"] = "Patrulla de Retaguardia",
  },
  [357] = {
    ["D"] = "Hay un nigromante que habita en la isla del Lago Aguasclaras al norte. Aunque es un No-Muerto de libre albedrío, cree que todos los demás no-muertos son esclavos de la Plaga y tiene a sus esbirros atacando a cualquiera que se le acerque.$B$BPero como es un No-Muerto, la Reina lo querrá. Y si es hábil en la nigromancia, entonces su conocimiento sería... bastante útil para nosotros.$B$BPara descubrir su identidad y sus talentos, debo ver su libro de hechizos. Entra sigilosamente en su campamento y adquiérelo, luego regresa conmigo.",
    ["O"] = "Lleva el Libro de Hechizos del Exánime a Bethor Iceshard en Entrañas.",
    ["T"] = "La Identidad del Exánime",
  },
  [358] = {
    ["D"] = "Las Tumbas Masivas, al suroeste de la Mansión de Garren al norte, fueron hechas para acomodar el... impresionante... número de muertes que Tirisfal sufrió cuando llegó la Plaga por primera vez.$B$BLos cuerpos en estas tumbas hasta ahora han sido librados de la no-muerte, pero la Plaga ahora envía a los Gnolls Pielpodrida a recolectar los cadáveres y usarlos para reforzar sus ejércitos. ¡Esto no puede permitirse!$B$BTu tarea es doble: mata a los Pielpodrida en las Tumbas Masivas y la Mansión de Garren, y recolecta de ellos el Ichor de Embalsamamiento que les da vida.",
    ["O"] = "Mata a los Saqueadores Pellejopútrida y a los Canes Pellejopútrida. $B$BLleva 8 Icor de Embalsamamiento al Magistrado Sevren en Rémol.",
    ["T"] = "Saqueadores de Tumbas",
  },
  [359] = {
    ["D"] = "Necesito un informe fresco de nuestra estación de la Guardia de la Muerte al este.$B$BDebo saber si más fuerzas de la Plaga han pasado por El Baluarte y han entrado en Tirisfal. ¡Nuestra vigilancia en la frontera con las Tierras de la Peste debe mantenerse, no podemos permitir que la Plaga gane más terreno aquí!$B$BViaja hacia el sur por el camino, luego hacia el este en la bifurcación hacia la estación. Habla con la Guardia de la Muerte Linnea. Ella tiene la información que necesito.",
    ["O"] = "Habla con el Exterminador Linnea.",
    ["T"] = "Deberes de los No-Muertos",
  },
  [360] = {
    ["D"] = "Ahora regresa con el Magistrado Sevren con la información que te di. La querrá lo antes posible, así que sé rápido.$B$BY si ves algún Azote en tu camino de regreso, acaba con ellos, no podemos dejarlos vagar por nuestras tierras sin desafiar.",
    ["O"] = "Regresa con el Magistrado Sevren en Rémol.",
    ["T"] = "Regreso con el Magistrado",
  },
  [361] = {
    ["D"] = "Esta es una carta escrita por Thurman, el hijo mayor de Gregor Agamand. Está dirigida a una tal Yvette Farthing, aunque parece que no llegó a sus manos...",
    ["O"] = "Encuentra a Yvette Farthing y entrégale la carta de Thurman Agamand.",
    ["T"] = "Una Carta Sin Entregar",
  },
  [362] = {
    ["D"] = "Devlin Agamand era el menor de dos hijos en la familia Agamand, y en vida los dos no podían ser más diferentes. Thurman era alto y gentil, mientras que su hermano menor era débil y de lengua afilada.$B$BCuando los Molinos Agamand cayeron ante la Plaga, no me sorprendió escuchar que Devlin sucumbió rápidamente. Sus locos parloteos aún se pueden escuchar cerca del camino que conduce a los Molinos Agamand.$B$BEstoy recolectando los restos de los Agamand, y quiero al pobre Devlin. Encuéntralo, destrúyelo y tráeme sus huesos.",
    ["O"] = "Mata a Devlin Agamand y lleva los Restos de Devlin a Coleman Farthing en Rémol.",
    ["T"] = "Los Molinos Encantados",
  },
  [363] = {
    ["D"] = "Ya era hora de que despertaras. Estábamos a punto de lanzarte al fuego con los demás, pero parece que lo lograste.$B$BSoy Mordo, el cuidador de la cripta de Villa del Lamento. Y ya no eres esclavo del Rey Exánime.$B$BHabla con el Sacerdote de las Sombras Sarvis en la capilla al pie de la colina, él te dirá más de lo que debes saber.",
    ["O"] = "Habla con el Sacerdote de las Sombras Sarvis.",
    ["T"] = "Un Despertar Brusco",
  },
  [364] = {
    ["D"] = "Los No-Muertos estamos en guerra con el ejército del Rey Exánime: los ejércitos no muertos resucitados necrománticamente, bestias repugnantes del norte y espectros atormentados.$B$BLa parte norte del pueblo ha sido invadida por los Desdichados, y deben ser destruidos. Destrúyelos, no les muestres piedad, aunque fueran nuestros hermanos y hermanas. Los Caídos no son más que esclavos del Rey Exánime.",
    ["O"] = "El Sacerdote de las Sombras Sarvis quiere que mates 8 Zombis Sin Mente y 8 Zombis Desdichados.",
    ["T"] = "Los Despiadados",
  },
  [365] = {
    ["D"] = "¿Qué tenemos aquí? Pareces un $c novato. Si esperas demostrar tu valía ante la Dama Oscura, necesitas aprender las formas de los No-Muertos.$B$BAl oeste encontrarás una granja. Los humanos infestan la tierra como el moho en un cadáver en descomposición. Y lo que es peor, la Cruzada Escarlata patrulla cerca desde su torre. Enséñales una lección a esa escoria y roba 10 de sus preciadas calabazas.$B$BUna vez que tengas 10, llévaselas al Boticario Johaan en Rémol.",
    ["O"] = "Roba 10 calabazas de la granja al oeste, justo al norte de Cerro del Cuervo, y llévaselas al Boticario Johaan en Rémol.",
    ["T"] = "Campos de Pena",
  },
  [366] = {
    ["D"] = "Hace mucho tiempo, Gunther y yo, junto con el ahora nigromante del Azote Thule Ravenclaw, éramos amigos y estudiantes dentro del Kirin Tor, una sociedad de magos en Dalaran.$B$BGunther y yo caímos ante la Plaga, pero pensé que él había permanecido muerto o había sido levantado en las filas del Rey Exánime. Es bueno saber que está libre.$B$BDebemos tenerlo. He colocado un hechizo en su libro que, cuando lo vea, le mostrará que yo también estoy libre del Rey Exánime. Después de saber esto, espero que se una a nosotros.",
    ["O"] = "Devuelve el Libro de Hechizos de Gunther a él, en la isla del Retiro de Gunther.",
    ["T"] = "Devuelve el Libro",
  },
  [367] = {
    ["D"] = "Lady Sylvanas ha convocado a la Sociedad Real de Boticarios. La Dama Oscura cree que nuestro conocimiento, junto con la magia recién descubierta, proporcionará la clave para la caída de Arthas. Nos ha desafiado a crear una nueva plaga, una plaga más mortífera que cualquier enfermedad en Azeroth. Esta nueva enfermedad arruinará al Ejército de la Plaga de Arthas.$B$BMis estudios muestran que la sangre de las bestias podría ser la clave. Tráeme 5 viales de sangre de can oscuro para probar mi teoría.",
    ["O"] = "El Boticario Johaan en el pueblo de Rémol quiere que recolectes 5 Viales de Sangre de Can Oscuro.",
    ["T"] = "Una Nueva Plaga",
  },
  [368] = {
    ["D"] = "Mientras recolectabas muestras para mí, mis experimentos me llevaron a darme cuenta de que se necesitarán más reactivos para que esta nueva enfermedad se propague adecuadamente. Envenenar a una víctima desafortunada es cosa de niños. Plagar un mundo entero resulta ser un poco más complicado.$b$bNecesitaré 5 escamas de Aleta Vil de los múrlocs en los alrededores. Encontrarás a las criaturas a lo largo de la costa al norte o al oeste.",
    ["O"] = "El Boticario Johaan del pueblo de Rémol necesita 5 Escamas de Aleta Vil de los Murlocs en los Claros de Tirisfal.",
    ["T"] = "Una Nueva Plaga",
  },
  [369] = {
    ["D"] = "Mientras estabas fuera recolectando, descubrí un texto antiguo en uno de mis tomos que indica que una antigua plaga acabó con miles de víctimas inocentes. Más tarde se descubrió que el agente mortal de la plaga se conservaba a través del veneno de las arañas Noctirred.$b$bTráeme un poco de veneno de una Araña Noctirred Viciosa para completar este experimento. Quiero ver si el elemento contagioso del veneno funcionará con mi nuevo brebaje. Se rumorea que las arañas se pueden encontrar en el este de los Claros de Tirisfal.",
    ["O"] = "El Boticario Johaan en el pueblo de Rémol quiere que le traigas 4 muestras de veneno de una Araña Nocturna Vil.",
    ["T"] = "Una Nueva Plaga",
  },
  [370] = {
    ["D"] = "Los documentos estratégicos del Ejecutor Arren contienen todos los detalles que necesitamos para eliminar la infestación humana de las tierras. Según la información, La Cruzada Escarlata ha asignado al Capitán Perrine y una brigada a la torre en ruinas al suroeste de Rémol.$B$BMata a Perrine junto con 3 Fanáticos y 3 Misioneros e infórmame.",
    ["O"] = "El Ejecutor Zygand en Rémol quiere que mates al Capitán Perrine, a 3 Fanáticos Escarlata y a 3 Misioneros Escarlata.",
    ["T"] = "En Guerra con la Cruzada Escarlata",
  },
  [371] = {
    ["D"] = "Todavía estamos en guerra y la Cruzada Escarlata crece en fuerza. El informe que me envió el Ejecutor Arren indica que los Cruzados Escarlata han estado asaltando desde la torre en ruinas en el sureste de Tirisfal, cerca de la Granja Balnir, bajo el mando del Capitán Vachon.$B$BMata a Vachon junto con 5 Frailes Escarlata. Debería ser un golpe devastador para la Cruzada.",
    ["O"] = "El Ejecutor Zygand en Rémol te ha encargado matar al Capitán Vachon y a 5 Frailes Escarlata.",
    ["T"] = "En Guerra con la Cruzada Escarlata",
  },
  [372] = {
    ["D"] = "Los Cruzados Escarlata han estado atacando desde la torre en ruinas en el norte de Tirisfal, más allá del Reposo de Faol. Según la información que tenemos, un despiadado comandante llamado Capitán Melrache está a cargo de este malvado grupo.$b$bTe estoy confiando ahora una misión especial y peligrosa. Mata a Melrache y a sus dos guardaespaldas, en nombre de la Dama Oscura.",
    ["O"] = "El Ejecutor Zygand en el pueblo de Rémol quiere que asesines al Capitán Melrache y a sus dos guardaespaldas.",
    ["T"] = "En Guerra con la Cruzada Escarlata",
  },
  [373] = {
    ["D"] = "Al registrar el cuerpo de Edwin VanCleef, descubres, entre otras cosas, una carta sin enviar. Está dirigida a Baros Alexston, el Arquitecto de la Ciudad de Ventormenta, en el Ayuntamiento, Plaza de la Catedral.$B$BParece estar recién escrita y sellada.",
    ["O"] = "Entrega la Carta al Arquitecto de la Ciudad a Baros Alexston en Ventormenta.",
    ["T"] = "La Carta No Enviada",
  },
  [374] = {
    ["D"] = "El ejecutor Zygand me dice que has estado matando Cruzados Escarlata. Los Claros de Tirisfal están llenos de ellos.$b$bVarimathras, teniente de la Dama Oscura, se está asegurando personalmente de que la escoria de Azeroth, la raza humana, sea borrada de la faz de la tierra. Aquí en los Claros de Tirisfal nos enfrentamos a una raza particularmente molesta de humanos: la Cruzada Escarlata.$b$bAdelántate y lanza una ofensiva contra la Cruzada Escarlata, $c. Tráeme 10 anillos de insignia escarlata como prueba de tu lealtad a Varimathras y a la Dama Oscura.",
    ["O"] = "Lleva 10 Anillos de Insignia Escarlata al Guardia de la Muerte Burgess en Rémol.",
    ["T"] = "Prueba de Fallecimiento",
  },
  [375] = {
    ["D"] = "Hace tanto frío ahora. La Plaga de la No Muerte corre por mis venas como una serpiente helada. El Estado de Inconsciencia estará sobre mí pronto. Pero ningún destino condenado me impedirá servir a nuestra Dama Oscura. Cuando surgió el llamado, cosí bolsas para cadáveres para los soldados caídos del poderoso ejército de Sylvanas.$b$bAhora mis manos tiemblan por el frío. Si pudieras traerme cinco Pieles de Murciélago del Ocaso y un poco de Hilo Grueso, podría coserme una manta. Ayúdame, $N, para que pueda seguir sirviendo a la causa.",
    ["O"] = "Lleva cinco Pieles de Murciélago del Ocaso e Hilo Grueso a Gretchen Dedmar en Rémol.",
    ["T"] = "El Frío de la Muerte",
  },
  [376] = {
    ["D"] = "Mis deberes incluyen atender a nuestros guerreros heridos, confeccionar armaduras y ropa, y asistir al Sacerdote de las Sombras Sarvis en lo que necesite.$B$BPor lo que parece, tú también serás enlistado en su servicio... cazando a los Despiadados, si conozco su mente. Bueno, si quieres mantenerte en una pieza, y no dudo que así sea, quizás pueda ayudarte. Me estoy quedando sin patas y alas, y si me traes algunas, encontraré algo de armadura para ti. Encontrarás una buena cantidad de lobos y murciélagos al sur.",
    ["O"] = "El Novicio Elreth requiere 6 Garras de Carroñero y 6 Alas de Murciélago del Ocaso.",
    ["T"] = "Los Malditos",
  },
  [377] = {
    ["D"] = "Como si el descuido hacia los residentes de Bosque del Ocaso no fuera suficiente, ahora la Casa de los Nobles escupe en la cara del Consejo de Villa Oscura con su decisión de encarcelar a Dextren Ward en Ventormenta en lugar de decapitar al villano según la sentencia de Lord Ebonlocke.$b$bWard fue capturado vendiendo cuerpos del cementerio a Morbent Vil, un crimen castigable con la muerte. Sin embargo, Ventormenta reclama a Ward como su prisionero.$b$bAsesina a Ward en Las Mazmorras de Ventormenta: tráeme su mano y te recompensaré.",
    ["O"] = "El Concejal Millstipe de Villa Oscura quiere que le lleves la mano de Dextren Ward.",
    ["T"] = "Crimen y Castigo",
  },
  [378] = {
    ["D"] = "Has demostrado tu lealtad al Rey Magni, $c. Y tu odio por la escoria Hierro Negro es tan grande como el mío.$b$bHay una tarea que deseo completar yo mismo, pero estoy obligado por honor a quedarme con Barbalarga. Pero Roggo ha reunido inteligencia que prueba que Kam Furiatierra fue un conspirador en el ataque al Puente Thandol. Fue por la planificación de Furiatierra que Barbalarga perdió a su familia.$b$bFuriatierra está siendo retenido por razones políticas en las Mazmorras de Ventormenta. ¡Quiero que esté muerto, $N! ¡Por Barbalarga! ¡Tráeme su cabeza!",
    ["O"] = "Motley Garmason quiere que le lleves la cabeza de Kam Furiahonda a Dun Modr.",
    ["T"] = "La Furia es Profunda",
  },
  [379] = {
    ["D"] = "Quieres una fuente de poder. Puedo ayudarte con eso. ¿Sabes lo que quiero? ¡Quiero algo de beber! Tráeme cinco Odres de Agua Merodeadora y te daré el 4711-FTZ.$B$BAhora... ¡adivina dónde puedes encontrar Odres de Agua Merodeadora! ¿Por qué no caminas hasta uno de los nómadas Merodeadores al este de aquí y les pides uno? ¡Hazme saber cómo te va!",
    ["O"] = "Lleva 5 Odres de Agua de los Merodeadores de las Dunas al Jefe Ingeniero Bilgewhizzle en Gadgetzan.",
    ["T"] = "Saciar esa Sed",
  },
  [380] = {
    ["D"] = "Una de nuestras mayores luchas es obtener los recursos naturales que necesitamos para sobrevivir. El oro era escaso incluso en el apogeo del poder de la Alianza.$b$bHay una mina de oro al noroeste que ha sido invadida por arañas. Necesitamos oro de la mina, pero no podemos conseguirlo mientras las arañas estén merodeando por allí. No tengo mucha mano de obra para dedicar a la tarea, así que tendremos que hacerlo poco a poco.$B$BSube allí y ve qué puedes hacer por nosotros, $n.",
    ["O"] = "El Ejecutor Arren quiere que mates a 10 Arañas Nocturnas Jóvenes y 8 Arañas Nocturnas.",
    ["T"] = "El Hueco de Nocturácnida",
  },
  [381] = {
    ["D"] = "Te alegrará saber que parece que estamos progresando en la mina, gracias en gran parte a tus esfuerzos. Ahora podemos volver nuestra atención a otras preocupaciones.$b$bMis exploradores han informado que un destacamento de la Cruzada Escarlata está estableciendo un campamento al sureste de aquí. La Cruzada Escarlata es una organización despreciable que nos caza, y no descansarán hasta que todos los no-muertos, ya sean del Azote del Rey Exánime o no, sean destruidos. ¡Debemos atacar primero!$b$BTen cuidado, su celo impío los hace adversarios peligrosos.",
    ["O"] = "Lleva al Ejecutor Arren 12 Brazales Escarlata de Conversos Escarlata e Iniciados Escarlata.",
    ["T"] = "La Cruzada Escarlata",
  },
  [382] = {
    ["D"] = "Los informes de mis superiores en Rémol indican que un agente de la Cruzada Escarlata fue enviado desde el Monasterio Escarlata y llegó aquí a Cerro del Cuervo. Según los informes, el mensajero lleva información importante consigo. No tengo más detalles, pero sea lo que sea, ¡debemos tenerlo!$b$bEres la persona más calificada que tengo para enviar, $n. Encuentra a ese mensajero y tráeme cualquier información que puedas encontrar.",
    ["O"] = "Mata a Meven Korgal, el mensajero, en el campamento de los Cruzados, luego lleva cualquier información que encuentres al Ejecutor Arren.",
    ["T"] = "El Mensajero Escarlata",
  },
  [383] = {
    ["D"] = "Estos deberán ser llevados a mi superior, el Ejecutor Zygand, en Rémol. Con esta información, podremos asestar un golpe decisivo a la Cruzada Escarlata. Tus servicios han sido útiles aquí en Aldea del Ocaso, pero necesito que alguien entregue esto a Zygand, y creo que él podrá encontrarte un trabajo más apropiado.$b$bToma el camino al norte de Aldea del Ocaso. Poco después de entrar en Tirisfal, llegarás a una encrucijada. Toma el camino del este y sigue hacia el este. Pasarás por la Mansión Hearthfría en el camino.",
    ["O"] = "Entrega los Documentos de la Cruzada Escarlata al Ejecutor Zygand en Rémol.",
    ["T"] = "Inteligencia Vital",
  },
  [384] = {
    ["D"] = "¡Nada disfrutan más los clientes de mi taberna que unas Costillas de Jabalí al Vino! El único problema es que el cazador local que solía traerme suministros se alistó en el Ejército del Rey para ayudar en la guerra en el frente de la Alianza. $b$b¿Quizás puedas ayudarme? Si me traes seis costillas de jabalí de crag y un Malta Rhapsody de la taberna aquí, te daré la receta familiar de mis famosas Costillas de Jabalí al Vino, ¡y además una muestra gratis! ¡El secreto está en la Malta!",
    ["O"] = "Ragnar Cebatruenos en Kharanos quiere 6 Costillas de Jabalí del Risco y una jarra de Malta de Rapsodia.",
    ["T"] = "Costillas de Jabalí a la Cerveza",
  },
  [385] = {
    ["D"] = "Muchos cazadores son atraídos a Loch Modan para cazar a nuestros famosos crocoliscos. Siempre hay mercaderes que buscan pieles de crocolisco para prendas de vestir o armaduras, y también hay algunos que disfrutan del sabor de su carne.$B$BHacemos algo de comercio con esto, pero no mucho, ya que los crocoliscos son feroces y se han atrincherado en las islas del Loch. Pero no dejes que te disuada, es toda una experiencia luchar con las mandíbulas de estas bestias.$B$B¿Por qué, una vez...",
    ["O"] = "Consigue 5 trozos de Carne de Crocolisco y 6 Pieles de Crocolisco para Marek Corazón de Hierro en el Refugio de los Exploradores Lejanos.",
    ["T"] = "Cacería de Crocoliscos",
  },
  [386] = {
    ["D"] = "Targorr el Temible sirvió bajo Gath\'Ilzogg como Ejecutor Supremo. Sus métodos eran despiadados, incluso para los estándares de los sucios orcos. Hombres que lucharon valientemente para defender el Reino fueron torturados por su capricho. Ahora está retenido en las Mazmorras de Ventormenta, sentenciado a morir. Sin embargo, algo anda mal. Uno de los nobles burocráticos ha detenido su ejecución. Estoy seguro de que hay juego sucio en marcha.$b$bPonle fin a Targorr el Temible, $N. Viaja a las Mazmorras y decápitalo antes de que la traición nos alcance.",
    ["O"] = "Lleva la cabeza de Targorr el Temible al Guardia Berton en Villa del Lago.",
    ["T"] = "Lo Que Va, Viene...",
  },
  [387] = {
    ["D"] = "¡El levantamiento debe ser sofocado! Necesito tu ayuda. Si se corre la voz de que la prisión está invadida, ¡perderé mi trabajo! Este es un caso de juego sucio si alguna vez he visto uno. Pero lo importante ahora es someter a estos prisioneros por la seguridad de Ventormenta.$B$BEl castigo por la insurgencia es la muerte. ¡Mata a algunos de estos desviados! Eso debería poner las cosas bajo control.",
    ["O"] = "El Celador Thelwater de Ventormenta quiere que mates a 10 Prisioneros Defias, 8 Reos Defias y 8 Insurgentes Defias en Las Mazmorras.",
    ["T"] = "Calmar el Levantamiento",
  },
  [388] = {
    ["D"] = "La amargura fluye por mis venas como un río frío. Mi nieto, Mac, era un buen chico. Un mero niño de 14 años. Cuando el ejército fue enviado al frente, Mac tomó las armas para defender la ciudad. Era un guardia en Las Mazmorras, donde la banda de los Defias está encerrada. Mac reprendió a algunos de los prisioneros por rasgar sus mantas de lana para hacer bufandas, un símbolo de su pandilla. Por eso, Mac fue apuñalado por la espalda.$B$BQuiero venganza. Tráeme 10 de sus preciadas Bandanas de Lana. Son del color de la sangre.",
    ["O"] = "Nikova Raskol de Ventormenta quiere que recolectes 10 Pañuelos de Lana Roja.",
    ["T"] = "El Color de la Sangre",
  },
  [389] = {
    ["D"] = "VanCleef y yo éramos miembros del Gremio de Canteros. Nuestro principal proyecto era reconstruir Ventormenta después de la Guerra.$B$BCuando terminamos nuestros deberes, nos engañaron. Los nobles se negaron a pagarnos por nuestro trabajo. A algunos de los canteros más veteranos se les ofrecieron trabajos gubernamentales, pero VanCleef lo rechazó por lealtad a todos los canteros. Lideró un motín y abandonó la ciudad, jurando venganza.$B$BSu lugarteniente, Bazil Thredd, podría saber más sobre los planes de VanCleef. Está prisionero en Las Mazmorras.",
    ["O"] = "Habla con el Alcaide Thelwater en Las Mazmorras.",
    ["T"] = "Bazil Thredd",
  },
  [390] = "_",
  [391] = {
    ["D"] = "Dejemos una cosa clara, $N: no confío en ti. Pero, con la situación como está, probablemente no marcarás la diferencia.$B$BAsí que así es como será: baja allí y habla con Thredd. Si realmente estás de nuestro lado, entonces mátalo y tráeme su cabeza cuando el trabajo esté hecho. Y si resulta que estás de su lado y no sales... cuando te encontremos, morirás junto con el resto de los gusanos.",
    ["O"] = "Mata a Bazil Thredd y lleva su cabeza de vuelta al Alcaide Thelwater en Las Mazmorras.",
    ["T"] = "Los Motines de Las Mazmorras",
  },
  [392] = {
    ["D"] = "Durante los primeros años del encarcelamiento de Thredd, nunca tuvo visitantes. Supuse que ya no era útil para la Hermandad, así que lo abandonaron.$B$BDe todos modos, hace unos meses, eso cambió, comenzó a recibir visitas regulares... una, quizás dos veces por semana. Era un hombre extraño, del tipo callado. Tenía mis sospechas, pero todos sus papeles estaban en orden.$B$BSus documentos decían que su nombre era Maelik, aquí está su descripción. No creo que vuelva a visitar a Thredd.",
    ["O"] = "Lleva la Descripción Sellada del Visitante de Thredd a Baros Alexston en Ventormenta.",
    ["T"] = "El Visitante Curioso",
  },
  [393] = {
    ["D"] = "Realmente no tengo ni idea, $N. Hay alguien más que podría ayudarte, y resulta que me debe un favor por—bueno, no entremos en eso. El Maestro Mathias Shaw de SI:7 puede ser encontrado en los cuarteles del Casco Antiguo. Shaw tiene su dedo en la mayoría de los tratos turbios o deshonestos de la ciudad.$B$BSi alguien sabe, sería él.",
    ["O"] = "Lleva la Descripción Sellada del Visitante de Thredd al Maestro Mathias Shaw en Ventormenta.",
    ["T"] = "Sombra del Pasado",
  },
  [394] = {
    ["D"] = "Encuentra a Shaw en el Casco Antiguo. Hazle saber que la amenaza de Lescovar ha sido tratada y sobre tu participación en los planes del Lord contra la ciudad. Estoy seguro de que estará orgulloso de tener otro héroe entre los ciudadanos de Ventormenta.$B$BEs probable que te envíe a reunirte con el Príncipe o algún otro noble. No dejes que se te suba a la cabeza, $N. Los nobles pueden pensar que gobiernan la ciudad todo lo que quieran, pero algunas cosas siguen fuera de su alcance.",
    ["O"] = "Habla con Mathias Shaw en Ventormenta.",
    ["T"] = "La Cabeza de la Bestia",
  },
  [395] = {
    ["D"] = "Ah, casi lo olvido. Alexston vino mientras estabas fuera, deseaba hablar contigo. Sin duda quiere informar sobre las actividades de la Hermandad Defias a los nobles.$B$BUn consejo, $N: si alguien pregunta, proporciona todos los detalles, pero que fue tu mano la que mató a Lescovar. Créeme. Estoy... familiarizado... con las formas de estas cosas.$B$BY en cuanto a Trias, bueno, no hables de él con nadie... nunca... si puedes evitarlo.",
    ["O"] = "Habla con Baros Alexston en Ventormenta.",
    ["T"] = "El Fin de la Hermandad",
  },
  [396] = {
    ["D"] = "He preparado un informe para el Rey. Creo que deberías ser tú quien se lo entregue, junto con un relato de tus hallazgos. No te preocupes, no hay duda de que te elogiará por cumplir con tu deber hacia el pueblo de Ventormenta.$B$BTu audiencia está programada pronto, así que date prisa, es un buen trecho hasta la Fortaleza de Ventormenta.",
    ["O"] = "Entrega el informe de Baros Alexston sobre la Hermandad Defias al Rey Varian Wrynn en el Castillo de Ventormenta.",
    ["T"] = "Una Audiencia con el Rey",
  },
  [397] = {
    ["D"] = "Escuché que a Lord Wishock le gustó su bebida.  $b$bNos has servido bien, $N.  Los brujos de Ventormenta podrán practicar nuestras formas sin perturbaciones, gracias a ti.  Por tus esfuerzos, he preparado un regalo especial para ti.  Habla con mi sirviente, Zggi, y reclama tu recompensa.",
    ["O"] = "Habla con Zggi, el sirviente de Zardeth el Garra Negra, para reclamar tu recompensa.",
    ["T"] = "Nos Has Servido Bien",
  },
  [398] = {
    ["D"] = "¡Se busca: Ojo de Gusano!$b$bUna bestia repugnante, incluso para los estándares de los gnolls, Ojo de Gusano ha sido sentenciado a muerte por sus crímenes contra los No-Muertos. Este villano despreciable ha estado robando cadáveres para usarlos en el ejército del Rey Exánime. Ojo de Gusano fue visto por última vez saqueando las tierras cerca de la Mansión de Garren, al norte de Rémol.$b$bEl pueblo de Rémol ofrece una recompensa al alma capaz que pueda llevar a cabo la ejecución. Muéstrale al Ejecutor Zygand la garra del villano una vez que se haya hecho el trabajo.$b$bLa misericordia es para los débiles.",
    ["O"] = "Mata a Ojo de Gusano y regresa con el Ejecutor Zygand en Rémol con su garra para recibir una recompensa.",
    ["T"] = "Se Busca: Ojo de Gusano",
  },
  [399] = {
    ["D"] = "Parece que ha pasado una eternidad desde que era un niño trabajando en la granja en Poniente. Dicen que nunca puedes volver, y es cierto. Doblemente cierto en mi caso, el hogar de mi familia ha sido quemado y tomado por ladrones.$b$bHe hablado con mi padre sobre el destino de algunas de mis posesiones, incluida mi primera brújula. No pudo salvarlas. Sin embargo, también dice que deberían estar escondidas en la granja.$b$bEncontrarás la Granja Alexston al oeste de la Colina del Centinela. ¿Quizás podrías ir a recuperarla por mí?",
    ["O"] = "Ve a la casa de Baros Alexston en los Páramos del Oeste y busca su brújula, luego devuélvesela a él en la Plaza de la Catedral de Ventormenta.",
    ["T"] = "Comienzos Humildes",
  },
  [400] = {
    ["D"] = "Beldin Steelgrill es el dueño de la tienda de mecánica local, ¡y es el mejor técnico de motores de asedio que existe! Pero no es muy cuidadoso con sus herramientas. ¡Juro que casi rompe más llaves de arco que las que podemos suministrarle!$B$BAcabamos de completar su último pedido de herramientas. Si se las entregas, estoy seguro de que te lo agradecerá.$B$BSu tienda, el Depósito de Steelgrill, está justo al noreste de Kharanos. Y es un lugar frecuentado por veteranos pilotos de motores de asedio, así que mantén los oídos abiertos por oportunidades mientras estés allí.",
    ["O"] = "Entrega las Herramientas de Steelgrill a Beldin Steelgrill.",
    ["T"] = "Herramientas para Aceror",
  },
  [401] = {
    ["D"] = "Casi he terminado de traducir esa nota. Si vuelves en unos momentos, debería estar lista.",
    ["O"] = "Espera un momento, luego habla con Sirra Von\'Indi nuevamente.",
    ["T"] = "Espera a que Sirra Termine",
  },
  [403] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Barril de Cebatruenos Protegido",
  },
  [404] = {
    ["D"] = "El Azote se ha filtrado en las Tierras de la Peste y ha infestado el área al oeste de Rémol, cerca del viejo puente.$B$BVe allí y repele a los muertos podridos y cadáveres desgarrados que encuentres. Esparce sus huesos y tráeme sus garras putrefactas, y los Guardias de la Muerte te recompensarán.",
    ["O"] = "Lleva 7 Garras Pútridas a Guardia de la Muerte Dillinger en Rémol.",
    ["T"] = "Una Tarea Pútrida",
  },
  [405] = {
    ["D"] = "Bethor Iceshard, un mago de alto rango y poderoso en Entrañas al sur, me ordena enviarle un agente con méritos probados contra la Plaga. Tú, $N, serás ese agente.$B$BPresenta estas órdenes a Bethor. Él te instruirá sobre tu misión. No sé sus detalles, pero trata de reclutar a un Lich descarriado. Y será peligroso.$B$BAsí que prepárate, $C. No debes fallar.$B$BPuedes encontrar a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Preséntale las Órdenes de Sevren a Bethor Hielocortante en Entrañas.",
    ["T"] = "El Exánime Pródigo",
  },
  [406] = "_",
  [407] = {
    ["D"] = "Calabazas inofensivas, ¿verdad? O eso parecería. Si vamos a derrotar los avances de Arthas desde el norte y la infestación humana desde el sur, necesitamos comenzar a darnos cuenta del potencial total de nuestro don de no-muerte.$b$bCon un poco de ingenio, una simple calabaza se convierte en un agente para nuestra Dama Oscura. Esta calabaza, impregnada con mi última fórmula, resultará ser todo un manjar.$b$bOtro fanático Escarlata ha sido capturado y está siendo retenido en el sótano de la Taberna del Fin del Cadalso. Lleva esta calabaza al tonto.",
    ["O"] = "Lleva la Calabaza Atada al Fanático Escarlata Capturado que está retenido en el sótano de la Taberna Gallows End.",
    ["T"] = "Campos de Pena",
  },
  [408] = {
    ["D"] = "El área de los Molinos Agamand se ha convertido en un bastión del Azote. Su punto más fuerte es la Cripta de la Familia Agamand, donde su líder militar local, un esqueleto llamado Capitán Dargol, ha establecido su cuartel general. Con Nigromancia, ha resucitado a los ancestros de los Agamand y planea usarlos contra nosotros.$B$BEsto no puede pasar, y debes detenerlo. Viaja a la Cripta de la Familia Agamand y derrota a los ancestros resucitados de los Agamand.$B$BY tráeme el cráneo del Capitán Dargol.",
    ["O"] = "Mata 8 Ancestros Lamentables y 8 Ancestros Pútridos.$B$BMata al Capitán Dargol y lleva su cráneo al Magistrado Sevren en Rémol.",
    ["T"] = "La Cripta Familiar",
  },
  [409] = {
    ["D"] = "Dices que no estás atado por la voluntad del Rey Exánime, y el encantamiento que Bethor colocó en mi libro corrobora esto. Pero antes de creerte, debo tener pruebas de que eres enemigo de la Plaga.$B$BHay un viejo y sangriento altar justo en la costa sur de esta isla. Es utilizado por un poderoso agente de la Plaga, Lillith Nefara.$B$BToma una Vela de Llamado de este cajón aquí. Enciéndela en el altar y Lillith despertará. Destruye a Lillith, y creeré que eres enemigo del Rey Exánime.",
    ["O"] = "Consigue una Vela de Llamado.$B$BInvoca a Lillith Nefara y mátala.$B$BRegresa con Gunther en su isla.",
    ["T"] = "Probando Lealtad",
  },
  [410] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Sombra Durmiente",
  },
  [411] = {
    ["D"] = "Llévale esta Gema Abisal a mi viejo camarada Bethor. Nos permitirá comunicarnos... y tengo mucho que contarle.",
    ["O"] = "Lleva la Gema Abisal a Bethor Iceshard en Entrañas.",
    ["T"] = "El Regreso del Exánime Pródigo",
  },
  [412] = {
    ["D"] = "Tras un análisis más detallado de la situación en Gnomeregan, parece que no solo no logramos erradicar a los troggs, sino que también convertimos a la mayoría de la raza gnoma en horribles, dementes y malvados gnomos leprosos.$b$bOzzie y yo planeamos revertir el horrible efecto leproso con nuestro último invento: El Recombinador. La máquina está casi completa, pero estamos en una necesidad desesperada de algunos Engranajes de Restabilización y Engranajes Giromecánicos. Recupera algunos de los gnomos leprosos afuera de Gnomeregan para nosotros.",
    ["O"] = "Lleva a Razzle Sprysprocket en Kharanos 8 Engranajes de Restabilización y 8 Engranajes Girotécnicos.",
    ["T"] = "Operación Recombobulación",
  },
  [413] = {
    ["D"] = "Me gustaría que mi hermano Wellart probara mi nueva Cerveza Brillante. Es un montaraz estacionado en la torre de vigilancia del sur, una de las dos torres en la frontera con Loch Modan.$B$BPuede que no sea un cervecero como el resto de los Cebadafuerte, pero le encantan sus bebidas y sé que le gustará esta.$B$B¿Podrías llevarle este Barril de Cerveza Brillante por mí?",
    ["O"] = "Lleva el Barril de Cerveza Irisada al Montañero Cebadafervida.",
    ["T"] = "Cerveza Brillante",
  },
  [414] = {
    ["D"] = "He estado bebiendo más de esa cerveza negra Shimmer que hizo mi hermano—¡es genial! Quiero que un compañero Montaraz la pruebe. Se llama Kadrell, y usualmente patrulla el camino a través de Thelsamar en Loch Modan.$B$BPara llegar a Thelsamar, ve al sureste a través del túnel, luego gira al noreste cuando el camino se divida en una carretera. Sigue esa carretera directamente a Thelsamar.$B$BEntrega este barril de cerveza negra Shimmer al Montaraz Kadrell y verás qué piensa.",
    ["O"] = "Lleva el Barril de Cerveza Irisada al Montañero Kadrell.",
    ["T"] = "Cerveza para Kadrell",
  },
  [415] = {
    ["D"] = "Rejold Barleybrew en la Aldea Brewnall ha estado preguntando por ti.$B$BQuiere hablar contigo sobre una nueva bebida que ha estado preparando... dijo algo sobre que tú podrías ayudarlo con eso. Quizás quieras regresar a la Aldea Brewnall y ver qué tiene que decir.$B$BPodría haber algo de alcohol gratis para ti...",
    ["O"] = "Habla con Rejold Cebadiz.",
    ["T"] = "La Nueva Cerveza de Rejold",
  },
  [416] = {
    ["D"] = "Tenemos un verdadero problema de infestación al oeste. ¡Los kóbolds rata de túnel se han mudado a las estribaciones, estableciendo guaridas y ensuciando nuestra tierra con su suciedad!$B$BQueremos eliminarlos. Cázalos, tráeme sus orejas y gana su recompensa.$B$BPuedes encontrar kóbolds rata de túnel y sus guaridas al oeste de Thelsamar, esparcidos por la ladera de la montaña.",
    ["O"] = "Lleva 12 Orejas de Rata de Túnel al Montaraz Kadrell en Thelsamar.",
    ["T"] = "Caza de Ratas",
  },
  [417] = {
    ["D"] = "Hildelve escribió en su diario que fue atacado por Mangeclaw, un \"enorme oso de garra de hielo\". Logró ahuyentar a la bestia, pero no antes de que lo mutilara salvajemente.$B$BJuzgando por el estado del cuerpo de Hildelve, Mangeclaw debe haber regresado más tarde y matado.$B$BSin embargo, Hildelve escribió en su diario su último deseo:$B$BVenganza contra Mangeclaw.",
    ["O"] = "Mata a Garraguja.$B$BLleva su Garra Sarnosa y el Diario de Hildelve al Piloto Piemartillo.",
    ["T"] = "La Venganza de un Piloto",
  },
  [418] = {
    ["D"] = "Nunca hay escasez de estómagos vacíos aquí en Thelsamar, niños corriendo de un lado a otro, trabajadores de la excavación entrando después de un día de trabajo duro. Somos famosos por nuestras salchichas de sangre, ¿no creo que las hayas probado alguna vez?$b$b¿No? Bueno, por aquí tienes que trabajar para tus comidas, y no pienses que solo porque eres un $c elegante, serás una excepción.$b$bNecesitaré carne de oso, intestinos de jabalí para las tripas e icor de araña para especiar. Consígueme algo de eso y deja la cocina a Vidra!",
    ["O"] = "Lleva 3 Piezas de Carne de Oso, 3 Intestinos de Jabalí y 3 Ichor de Araña a Vidra Hearthstove en Thelsamar.",
    ["T"] = "Morcillas de Thelsamar",
  },
  [419] = {
    ["D"] = "Mi amigo y compañero piloto de máquinas de asedio, Mori Hildelve, está perdido en las colinas. Estábamos buscando un mineral raro que se necesita para una pólvora de alto grado, y durante nuestra búsqueda condujo su máquina por una colina empinada y la rompió.$B$BAún convencido de que el mineral estaba en estas montañas, Hildelve me encargó vigilar nuestras máquinas mientras él continuaba a pie, buscando.$B$BHan pasado días, y he escuchado algunos rugidos bestiales en las colinas por la noche. Mori es duro como una roca, pero estoy preocupado.$B$BPor favor, $N. Encuéntralo.",
    ["O"] = "Encuentra a la Piloto Hildelve.",
    ["T"] = "El Piloto Perdido",
  },
  [420] = {
    ["D"] = "Los troggs en el túnel son extremadamente hostiles y no han mostrado ninguna vacilación en atacar a los viajeros que pasan por el túnel. Pareces lo suficientemente fuerte, así que probablemente estarás a salvo.$B$B¿Direcciones? Si vas a hablar con Senir, lo encontrarás en Kharanos.$B$BAbre camino a través del túnel, y después de salir al otro lado, simplemente sigue el camino, te llevará directamente a Kharanos.",
    ["O"] = "Entrega el informe de Grelin a Senir Barbablanca en Kharanos.",
    ["T"] = "Observaciones de Senir",
  },
  [421] = {
    ["D"] = "Lady Sylvanas ha encargado a Varimathras la conquista de las tierras humanas y enanas del sur.$B$BPero ese tonto, Arugal, charlatán de Dalaran y ahora bestia maldita de la Fortaleza de Colmillo Oscuro, dejó que su magia imprudente causara estragos en el bastión estratégico del Bosque de Argénteos.$B$BNecesito a alguien hábil en el combate para ayudar a limpiar el desastre de Arugal. Demuéstrame tu valía matando a 5 Crines Blancas de Lunadaga. Esas bestias miserables suelen encontrarse cerca del camino, colina abajo.",
    ["O"] = "Dalar Tejeamanecer en El Sepulcro quiere que mates a 5 Calva Lunafuria.",
    ["T"] = "Demuestra tu Valor",
  },
  [422] = {
    ["D"] = "Cómo Arugal ganó aceptación dentro del Kirin Tor es un misterio para mí. Su conocimiento en hechizos parecía tan transparente como una burbuja de vidrio soplado.$B$BLo que es importante ahora es aprender exactamente qué magia usó Arugal para que pueda usarla en su contra y asegurar el Bosque de Argente para la Dama Oscura.$B$BArugal se quedó por primera vez en la granja de trigo justo al norte del puente. Uno de los Acechadores de la Muerte informó haber visto algunos libros de hechizos, pero no pudo asegurarlos. Recupera para mí el hechizo etiquetado como Remedio de Arugal de esos libros.",
    ["O"] = "Recupera el Remedio de Arugal para Dalar Tejealba en el Sepulcro.",
    ["T"] = "La Locura de Arugal",
  },
  [423] = {
    ["D"] = "Después de examinar el trabajo de Arugal, mis peores sospechas se confirmaron. El viejo inepto no estaba calificado para limpiar orinales en Dalaran, y mucho menos para representar al Kirin Tor en su hora más oscura. ¡Tontos!$B$BArugal usó objetos encantados para reforzar su magia débil. Necesito examinar estos objetos de primera mano. Viaja y mata a Glotones Lunadaga y Almas Oscuras Lunadaga hasta que hayas recolectado suficientes grilletes encantados para mi investigación. Esas criaturas despreciables han sido vistas al norte y al este.",
    ["O"] = "Lleva 6 Grilletes de Glotón y 3 Grilletes de Almanegra a Dalar Dawnweaver en El Sepulcro.",
    ["T"] = "La Locura de Arugal",
  },
  [424] = {
    ["D"] = "De hecho, me tomará más tiempo del que pensaba descubrir los oscuros secretos detrás de los encantamientos que Arugal estaba usando.$b$bPero mientras tanto, necesito que te encargues de un pequeño problema que nuestros Acechadores Oscuros han descubierto. Parece que Arugal dejó que su magia se extendiera a la Mina Elem de las colinas al sureste.$b$bLa mina resultaría ser un recurso bastante útil para el avance de Varimathras.$b$bQuiero que decapites al capataz contaminado de la mina, Grimson el Pálido. Con su muerte, la mina será nuestra.",
    ["O"] = "Mata a Grimson el Pálido y lleva su cabeza a Dalar Tejealba en el Sepulcro.",
    ["T"] = "La Locura de Arugal",
  },
  [425] = {
    ["D"] = "Mi hermano Quinn y yo fuimos asignados para reconocer el Bosque de Argénteos e informar al Alto Ejecutor Hadrec. Pero no llegamos muy lejos: cuando pasamos por esta granja, fuimos atacados por el necrófago Ivar el Fétido.$B$BNos escapamos de Ivar, pero él hirió gravemente a Quinn. Se está recuperando de la poción que le diste, pero Ivar puede regresar antes de que Quinn se recupere.$B$BMata a Ivar para que Quinn tenga tiempo de sanar. Ivar suele estar en el granero de allá, esperando a que baje la guardia.",
    ["O"] = "Mata a Ivar el Fétido y lleva la Cabeza de Ivar a Rane Yorick en el Campo de Ivar.",
    ["T"] = "Ivar el Fétido",
  },
  [426] = {
    ["D"] = "La Plaga está tratando de formar una base en los Molinos Agamand. Si lo hacen, entonces pueden organizar más ataques dentro de Tirisfal.$B$BSe han dado las órdenes. La Plaga en los Molinos debe ser destruida.$B$BVe a los Molinos. Recolecta Costillas Muescadas de los Soldados Espinazo y Cráneos Ennegrecidos de los Invocahuesos Ojosombríos.$B$BPara llegar a los Molinos, sigue el camino hacia el oeste. Después de cruzar el puente, toma el siguiente desvío hacia el norte y sigue hacia el norte. Cuando veas los molinos, la batalla comenzará.",
    ["O"] = "Reúne 5 Costillas Muescadas y 3 Cráneos Ennegrecidos, y luego regresa con el Guardia de la Muerte Dillinger en Rémol.",
    ["T"] = "Los Molinos Invadidos",
  },
  [427] = {
    ["D"] = "Los documentos que proporcionó el Ejecutor Arren son justo lo que necesitábamos en nuestra batalla contra la miserable Cruzada Escarlata. Ahora sabemos sus ubicaciones exactas en los Claros de Tirisfal.$B$BPero la Guardia de la Muerte tiene preocupaciones mayores. El ejército del Rey Exánime crece en número cada noche. Necesitamos a alguien con \"iniciativa\" como tú para llevar a la Cruzada Escarlata a la tumba.$B$BDemuéstrame que eres capaz de servir a la Dama Oscura viajando al oeste hacia la torre más allá de la Granja Solliden y matando a 10 Guerreros Escarlata.",
    ["O"] = "El Ejecutor Zygand de Rémol quiere que mates a 10 Guerreros Escarlata.",
    ["T"] = "En Guerra con la Cruzada Escarlata",
  },
  [428] = {
    ["D"] = "Hace un par de semanas, dos de nuestros Acechadores de la Muerte, Rane y Quinn Yorick, fueron enviados en una misión de reconocimiento por el norte de Bosque de Argénteos. No hemos tenido noticias de ellos.$B$BPuede que hayan sido víctimas de la Plaga, aunque si eso es cierto, quiero que se confirme. Encuentra a estos Acechadores de la Muerte.$B$BSu primer objetivo era explorar las granjas en el norte de Bosque de Argénteos. Deberías comenzar tu búsqueda allí.",
    ["O"] = "Encuentra a los Acechamuerte Quinn y Rane Yorick.",
    ["T"] = "Mortacechadores Perdidos",
  },
  [429] = {
    ["D"] = "Mi hermano Quinn fue gravemente herido por el necrófago Ivar el Fétido, y no sé si sanará adecuadamente sin ayuda mágica. Aunque no soy un boticario, sé que nuestros boticarios pueden hacer pociones curativas con los corazones descoloridos de los huargos.$B$BReúne esos corazones y llévaselos al Boticario Renferrel en el Sepulcro, al sur. Luego regresa aquí con la poción.$B$BPuedes encontrar muchos huargos entre aquí y el Huerto de Malden, al este.",
    ["O"] = "Reúne 6 corazones de huargo descoloridos y llévaselos al Boticario Renferrel en el Sepulcro.",
    ["T"] = "Corazones Salvajes",
  },
  [430] = {
    ["D"] = "Aquí tienes la poción para ayudar en la recuperación de Quinn Yorick.$B$BAhora, si me disculpas, tengo un trabajo muy importante que continuar...",
    ["O"] = "Lleva la poción de Quinn a Quinn Yorick en el Campo de Ivar, al norte del Sepulcro.",
    ["T"] = "Regreso con Quinn",
  },
  [431] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Velas de Invocación",
  },
  [432] = {
    ["D"] = "¡Esos malditos troggs, convirtiendo mi sitio de trabajo en un desastre! Mira eso, ¡ahí va otro barril de pólvora!$b$b¡Ohhh, desearía poder bajar allí y retorcer cada uno de sus escuálidos cuellos! ¡Quiero matarlos a todos!$b$bEsto no puede ser bueno para mi salud, todo este estrés y enojo... ¿Qué me han dejado? ¡Nada!$b$b¡Los mataría yo mismo, pero mi puntería ya no es buena! ¡Ve a la cantera y mata a algunos de esos sucios bichos! ¡Te pagaré! ¡Solo hiérelos, mátalos!",
    ["O"] = "Mata 6 Machacacráneos Quijapiedra para el Capataz Ceja Pétrea en la cantera de Gol\'Bolar.",
    ["T"] = "¡Esos Malditos Troggs!",
  },
  [433] = {
    ["D"] = "Como miembro del Senado y de la Liga de Expedicionarios, me he tomado la responsabilidad de encargarme de esta parte de la infestación de troggs que ha afectado nuestras tierras.$b$bCiertamente han hecho un desastre en la cantera de Gol\'Bolar, y sin razón. Mientras cavábamos profundamente en la tierra, ellos salieron, destruyendo nuestro equipo y expulsando a los mineros. No nos queda mucho por hacer más que exterminarlos, reconstruir y luego volver al trabajo.$b$bSi me ayudas con los troggs, con gusto te compensaré por tu tiempo.",
    ["O"] = "Mata 10 Rompehuesos Quijapiedra para el Senador Mehr Petrahonda en la cantera de Gol\'Bolar.",
    ["T"] = "El Servidor Público",
  },
  [434] = {
    ["D"] = "Después de ponerme el disfraz de Spybot, él entrará a hablar con Lescovar. Los guardias deberían dejarlo pasar sin problemas.$B$BMientras conseguías materiales para mí, me encargué de informarle a Marzon que era hora de que él y Lescovar se reunieran nuevamente. Incluso solté información falsa.$B$BLescovar despedirá a sus guardias y esperará a Marzon aquí en el jardín. Después de que escuches hablar de los Defias, actúa. ¡Debes matarlos a ambos antes de que regresen los guardias! Cuando hayas terminado, habla con Trias.",
    ["O"] = "Permanece en el jardín hasta que Lord Gregor Lescovar y Marzon la Hoja Silenciosa hayan tenido su conversación. Después, mátalos a ambos antes de regresar con Elling Trias en Ventormenta.",
    ["T"] = "¡El Ataque!",
  },
  [435] = {
    ["D"] = "Los lobos afuera no se irán, y me miran malévolamente cada vez que pongo un pie fuera de esa puerta. Deben estar increíblemente hambrientos para pensar que valgo la pena comer.$B$BTengo que informar a mi compañero, el Acechador de la Muerte Rane Yorick. Y para llegar a Rane, necesitaré ayuda contra esos lobos.$B$B¡Por favor, acompáñame! Y hay muchos lobos afuera, así que tendremos una mejor oportunidad si tienes amigos que puedan ayudarnos...",
    ["O"] = "Escolta a Erland a través de los lobos, hasta Rane Yorick.",
    ["T"] = "Escoltando a Erland",
  },
  [436] = {
    ["D"] = "El Prospector Ironband está dirigiendo una excavación de ruinas antiguas al este del lago. Su progreso ha sido lento últimamente, especialmente considerando todos los suministros que le hemos enviado.$B$BIronband es un enano robusto y honesto que valora los resultados, lo que me tiene preocupado de que haya fuerzas trabajando en su contra.$B$BVe a la Excavación de Ironband y habla con Magmar Fellhew. Él maneja los detalles del sitio y sabrá por qué hay una desaceleración.$B$BPara llegar al sitio de excavación, rodea la punta sur del lago, luego dirígete al este.",
    ["O"] = "Habla con el Explorador Fellhew.",
    ["T"] = "Excavación de Vetaferro",
  },
  [437] = {
    ["D"] = "Nuestros informes iniciales de exploración de Bosque de Argénteos muestran que los ocultistas de la podredumbre tienen una base en una vieja granja al norte, los Campos Muertos. Hemos enviado pequeños asaltos contra ellos, pero cada vez son reforzados por la banshee Latigonocturno, quien usa su magia para derrotar a nuestras fuerzas.$B$BQueremos que Latigonocturno sea destruida.$B$BVe a los Campos Muertos y asalta a los ocultistas de la podredumbre. Si no ves a Latigonocturno, entonces mata a los ocultistas hasta que aparezca, luego derrótala.$B$BSu esencia se convertirá en polvo. Tráeme ese polvo como prueba.",
    ["O"] = "Mata a los gnolls Pellejopútrida en los Campos Muertos.$B$BCuando aparezca Latigazo Nocturno, mátala.$B$BLleva la Esencia de Latigazo Nocturno al Alto Ejecutor Hadrec en el Sepulcro.",
    ["T"] = "Los Campos de la Muerte",
  },
  [438] = {
    ["D"] = "$N, hemos descubierto una nueva colmena para la Plaga: el Ferry Decrepito. El ferry se encuentra al este del Sepulcro y está fuertemente patrullado por pieles podridas y otros no muertos.$B$BDebe haber una razón para la ocupación de la Plaga en el área. Ve al Ferry Decrepito y descubre por qué la Plaga está allí.",
    ["O"] = "Ve al Transbordador Decrépito.$B$BBusca la razón por la que la Plaga está allí.",
    ["T"] = "El Ferry Decrépito",
  },
  [439] = {
    ["D"] = "Entre los cadáveres sobresale una mano, sin envolver. Es la mano de una mujer, marcada por la plaga pero relativamente fresca.$B$BEn la mano hay un anillo. Al quitarlo e inspeccionarlo, se revela un grabado:$B$B\"Para Deliah\"",
    ["O"] = "Lleva el Anillo de Deliah al Alto Ejecutor Hadrec en el Sepulcro.",
    ["T"] = "Pistas de Piel Putrefacta",
  },
  [440] = {
    ["D"] = "Si quieres encontrar al esposo o amado que le dio a Deliah su anillo... entonces habla con el Magistrado Sevren en Rémol. Debería tener registros de muertes recientes y, con suerte, puede descubrir quién es Deliah.$B$B¡Pero no esperaría mucho! Quien le dio el anillo probablemente esté muerto. O si es no muerto, probablemente ya no le importe Deliah.",
    ["O"] = "Lleva el Anillo de Deliah al Magistrado Sevren en Rémol.",
    ["T"] = "El Anillo Grabado",
  },
  [441] = {
    ["D"] = "Deliah tenía un esposo, Raleigh, que ahora reside en Entrañas. Si la Deliah que vivía en Rémol es la dueña de este anillo, entonces Raleigh lo sabrá.$B$BRaleigh está en el Barrio Comercial de Entrañas.",
    ["O"] = "Lleva el Anillo de Deliah a Raleigh.",
    ["T"] = "Raleigh y Entrañas",
  },
  [442] = {
    ["D"] = "Ahora es el momento de asaltar el lugar de fuerza del Azote en Bosque de Argénteos: la Isla Fenris.$B$BDebes ir nuevamente a la isla y destruir a Thule Ravenclaw. ¡Quiero su cabeza!$B$BEsta misión es tan peligrosa como vital, $N. No debes fallar.",
    ["O"] = "Lleva la Cabeza de Thule al Alto Ejecutor Hadrec en el Sepulcro.",
    ["T"] = "Asalto a la Isla de Fenris",
  },
  [443] = {
    ["D"] = "Antes de lanzar un asalto a la Isla Fenris, debemos determinar quién está detrás de los gnolls Piel de Podredumbre. Nuestros boticarios creen que su origen se encuentra dentro del extraño fluido que fluye a través de sus cuerpos.$B$BVe a la Isla Fenris, al este del Ferry Decrépito, y recolecta ichor de Piel de Podredumbre de los gnolls que encuentres allí. Tráelo al Boticario Renferrel, estacionado fuera del Sepulcro.",
    ["O"] = "Lleva 8 botellas de icor de piel podrida al Boticario Renferrel en el Sepulcro.",
    ["T"] = "Icor de Piel Putrefacta",
  },
  [444] = {
    ["D"] = "He reducido el icor que me trajiste y lo he separado en sus componentes básicos. Mis hallazgos no son concluyentes, pero he determinado que se usaron magias de brujería y nigromancia en la creación del icor.$B$BMientras continúo estudiando el icor, quiero que lleves una muestra a Bethor Iceshard en Entrañas. Bethor no es un boticario, pero su conocimiento de la magia puede ayudar a desentrañar el misterio de los pieles podridas.$B$BVe. Encontrarás a Bethor en el Barrio de la Magia de Entrañas.",
    ["O"] = "Lleva la Muestra de Ichor a Bethor Iceshard en Entrañas.",
    ["T"] = "Orígenes de Piel Putrefacta",
  },
  [445] = {
    ["D"] = "El tiempo es un lujo que no se les concede a aquellos de nosotros bajo el empleo de Lady Sylvanas. Seguramente ya lo sabes.$b$bComo miembro de la Sociedad Real de Boticarios, es mi deber compartir mi conocimiento con mis colegas para que nuestros esfuerzos colectivos puedan algún día proporcionarle a la Dama Oscura la Nueva Plaga que tanto desea.$b$bLleva estos hallazgos al Boticario Renferrel, quien está estacionado en El Sepulcro en el Bosque de Argente. Sigue los caminos hacia el sur desde Brill. Desde Entrañas, ve al suroeste.",
    ["O"] = "Lleva los hallazgos del Boticario Johaan al Boticario Renferrel en el Bosque de Argénteos.",
    ["T"] = "Entrega a Bosque de Argénteos",
  },
  [446] = {
    ["D"] = "Este Ichor fue creado por un antiguo colega mío: Thule Cuervozarpa. Era un joven mago prometedor antes de la gran guerra, pero cuando el Rey Exánime ascendió al poder, traicionó a sus hermanos y se alió con la Plaga.$B$B¡No me di cuenta de que todavía vivía, ni de que lideraba a los Podredumbre en sus incursiones contra nosotros!$B$BConozco muchos de sus hechizos y puedo ayudar a preparar una poción para contrarrestarlos. Lleva este pergamino de vuelta al Boticario Renferrel. Contiene los encantamientos necesarios para la poción.",
    ["O"] = "Lleva el Pergamino de Bethor al Boticario Renferrel en el Sepulcro de Argénteos.",
    ["T"] = "Thule Cuerval",
  },
  [447] = {
    ["D"] = "Los números de Arthas son abrumadores. Pero con una Nueva Plaga podríamos erradicar tanto al Ejército de la Plaga como a la infestación humana de una vez por todas.$b$bMis estudios han demostrado que la sangre de araña combinada con una toxina derivada del corazón de un oso gris resulta en un elixir mortal. Recolecta muestras de las arañas en la Oscuridad Escurridiza al noroeste y de los osos que vagan por el Bosque de Argente. Entrega los reactivos al Maestro Boticario Faranell de la Sociedad Real de Boticarios en Entrañas.",
    ["O"] = "Recolecta 6 Corazones de Oso Canoso y 6 muestras de Sangre Rastrera y entrégaselas al Maestro Boticario Faranell en Entrañas.",
    ["T"] = "Una Receta para la Muerte",
  },
  [448] = {
    ["D"] = "Antes de lanzar un asalto a la Isla Fenris, informa al Alto Ejecutor Hadrec. Él puede tener información adicional o equipo que necesitarás.",
    ["O"] = "Preséntate ante el Alto Ejecutor Hadrec en El Sepulcro.",
    ["T"] = "Informe para Hadrec",
  },
  [449] = {
    ["D"] = "Aunque las heridas de Quinn están sanando, quiero que nuestro informe llegue al Alto Ejecutor Hadrec lo más rápido posible. Contiene información vital para la ocupación de los No-Muertos en el Bosque del Ocaso.",
    ["O"] = "Lleva el Informe de los Acechadores a Alto Ejecutor Hadrec en el Sepulcro.",
    ["T"] = "El Informe de los Mortacechadores",
  },
  [450] = {
    ["D"] = "Puedes ser de más servicio para la Sociedad Real de Boticarios, $N.$B$BCuando Arugal lanzó su maldita magia sobre el bosque, maldijo a uno de nuestros miembros más prometedores, el Boticario Berard. Ahora un sirviente sin mente de Arugal, Berard es inútil para nosotros. Pero sus hallazgos podrían ser de valor. Estaba estudiando una antigua plaga que mató a toda la vida acuática en el Lago Lordamere hace siglos.$B$BRecupera el Diario de Berard de su estudio en la Posada de Villa del Lago y llévaselo al Boticario Renferrel.",
    ["O"] = "Recupera el diario del Boticario Berard de Aldea Piroleña y llévaselo al Boticario Renferrel en El Sepulcro.",
    ["T"] = "Una Receta para la Muerte",
  },
  [451] = {
    ["D"] = "Según el diario de Berard, el musgo de los Rondadores y Acechadores del Lago podría ser útil para nuestra misión de elaborar una nueva plaga. Esas bestias habitan en las islas del Lago Lordamere, al este.$B$BTambién menciona un tumor endurecido muy raro que descubrió en un múrloc Aleta de Vile. Aparentemente, el tumor es tan raro que Berard solo pudo obtener uno.$B$BRecoge muestras del musgo, así como el tumor endurecido raro, y llévaselas al Maestro Boticario Faranell en Entrañas.",
    ["O"] = "Lleva 6 muestras de Musgo del Merodeador del Lago, 6 muestras de Musgo del Acechador del Lago y un Tumor Endurecido al Maestro Boticario Faranell en Entrañas.",
    ["T"] = "Una Receta para la Muerte",
  },
  [452] = {
    ["D"] = "¡Llegaste justo a tiempo, $N!$b$bEl Consejo de Pirosco será de vuelta en cualquier momento. Arugal tiene otros asuntos que atender en el torreón. Él usa al Consejo como meros títeres para mantener a los esbirros sin mente de Pirosco bajo su control. Pero creo que sin la guía del Consejo de Pirosco, los hijos de Arugal se volverán contra su maestro.$b$bNo puedo lidiar con todos ellos solo. Necesito que me ayudes. Juntos podemos acabar con el Consejo por Lady Sylvanas.",
    ["O"] = "Ayuda a la Acechamuerte Faerleia a matar al Consejo de Vega del Bosque.",
    ["T"] = "Emboscada en Piñaluna",
  },
  [453] = {
    ["D"] = "Buena suerte, $N. Espero que encuentres al tipo, y espero que lo encuentres vivo.$B$BSi ha dejado la seguridad de Villa Oscura, entonces podría ser presa de las bestias de los bosques.",
    ["O"] = "Encuentra a la Figura Sombría. Tus pistas:$B$BNo es nativo de Villa Oscura.$B$BEs una persona nerviosa e inquieta.$B$BSalió de Villa Oscura y se dirigió al oeste.",
    ["T"] = "Encontrando a la Figura Sombría",
  },
  [454] = {
    ["D"] = "Habíamos estado transportando los barriles de pólvora de un lado a otro en viajes antes de este... asunto. Ahora tenemos que dividir el trabajo, Miran está llevando los barriles y yo estoy vigilando los otros.$b$bNo me siento seguro dejando que Miran lleve el resto del cargamento solo. Cuando esté listo para partir, ¿quizás podrías escoltarlo en su próximo viaje?",
    ["O"] = "Habla con Miran.",
    ["T"] = "Después de la Emboscada",
  },
  [455] = {
    ["D"] = "Los orcos del clan Faucedraco tienen un refugio en los Humedales. ¡Y ahora se han expandido a Dun Algaz! No podemos permitir que mantengan su posición aquí o Loch Modan quedará aislado de Puerto Menethil, nuestro puerto y fortaleza en los Humedales.$B$B¡Abre paso a través de Dun Algaz hacia los Humedales más allá, y mata a algunos orcos por si acaso! Informa a Valstag Ironjaw en Puerto Menethil.$B$BPuerto Menethil está a través de Dun Algaz, luego hacia el norte por el camino, luego al oeste en la bifurcación.$B$BContamos contigo, $N. No falles.",
    ["O"] = "Atraviesa Dun Algaz.$B$BMata a 8 Exploradores Faucedraco y 6 Soldados Faucedraco.$B$BInforma a Valstag Quijadahierro en el Puerto de Menethil.",
    ["T"] = "El Desafío de Algaz",
  },
  [456] = {
    ["D"] = "Saludos, $N. Soy el Conservador Ilthalaine. Mi propósito en Cañada Umbría es asegurar que se mantenga el equilibrio de la naturaleza.$b$bLas lluvias primaverales fueron particularmente fuertes este año, haciendo que algunas de las bestias del bosque prosperaran mientras que otras sufrieran.$b$bDesafortunadamente, las poblaciones de sable de la noche y jabalí cardo crecieron demasiado. Cañada Umbría solo puede producir cierta cantidad de comida para las bestias. Sal, joven $c, y reduce las poblaciones de jabalíes y sables para que se preserve la armonía de la naturaleza.",
    ["O"] = "Mata 7 Sable de la Noche Jóvenes y 4 Jabalíes Cardo Jóvenes y regresa con el Conservador Ilthalaine.",
    ["T"] = "El Equilibrio de la Naturaleza",
  },
  [457] = {
    ["D"] = "Reducir la población más joven de criaturas aquí en Vallefresno fue un buen comienzo, $N, pero todavía hay trabajo por hacer.$b$bLos recursos del bosque se agotarán demasiado rápido si el problema no se aborda. Matar a las bestias de la naturaleza es un mal necesario por el bien de todos los que comparten la tierra. Adéntrate en el bosque y mata a los sable de la noche mangosos y a los jabalíes cardo en nombre del equilibrio.",
    ["O"] = "El Conservador Ilthalaine necesita que mates a 7 Sable de la Noche Sarnosos y 7 Jabalíes Cardo.",
    ["T"] = "El Equilibrio de la Naturaleza",
  },
  [458] = {
    ["D"] = "Gracias a los cielos que estás aquí, $c. Noticias extrañas me han llegado a través de los susurros de los espíritus del bosque.$b$bLa misteriosa protectora del bosque, Tarindrella, ha regresado a la Arboleda de la Umbría. La presencia de la dríade no se había sentido en los bosques de Kalimdor en años. Algo anda mal si ha regresado a estas tierras.$b$bBusca a Tarindrella y descubre qué asuntos atiende en nuestra arboleda. Una de las Centinelas informó haberla visto al suroeste de Aldrassil.",
    ["O"] = "Busca a la dríade conocida como Tarindrella.",
    ["T"] = "El Protector del Bosque",
  },
  [459] = {
    ["D"] = "Algo malvado se está gestando en los bosques de Teldrassil. Mira a lo largo de las colinas hacia donde solían habitar los pacíficos furbolgs. Han abandonado sus hogares y se están reuniendo bajo el nombre de la tribu Gnarlpine.$b$bSolo la corrupción del malvado Musgo Vil podría causar tal transformación. Los grells y grellkin han infestado el área y están amenazando a los residentes de Valle de la Bruma. $b$bEnfrenta a estos grells y grellkin, $N, y comprueba si están realmente bajo el encantamiento del malvado Musgo Vil.",
    ["O"] = "Recolecta 8 Musgos Vil y llévaselos a Tarindrella.",
    ["T"] = "El Protector del Bosque",
  },
  [460] = {
    ["D"] = "No pareces un descarnado. Bien. Había perdido la esperanza...$B$BMi nombre es Alaric, y una vez fui el sirviente principal y guardaespaldas de Thule. Él usó su magia para \"protegerme\" de la Plaga de los No Muertos, pero en su lugar, su hechizo me dio una no-vida de la que no puedo escapar. Y después de crear a los malditos gnolls descarnados, Thule me decapitó... ¡y le dio mi cabeza a los gnolls como juguete!$B$BPor favor, llévame a mi cuerpo. Creo que los gnolls lo enterraron aquí en el Castillo de Fenris, cerca del establo, en una tumba sin marcar.",
    ["O"] = "Lleva la Cabeza de Alaric a su tumba.",
    ["T"] = "Descansando en Pedazos",
  },
  [461] = {
    ["D"] = "Conozco un nicho oculto dentro del fuerte que guarda un objeto querido por Thule y sus antiguos colegas. También guarda algo que podrías encontrar útil. ¡Llévame allí!$B$BEl nicho está en la torre suroeste del fuerte, detrás de una estantería polvorienta.$B$BToma mi cuerpo, pero deja mis botas. Nunca me quedaron bien de todos modos...",
    ["O"] = "Lleva a Alaric al rincón escondido.",
    ["T"] = "El Nicho Oculto",
  },
  [462] = "_",
  [463] = {
    ["D"] = "¿Has oído hablar del Guardián Verde? ¿No lo estarás buscando, verdad? Bueno, digo que estás loco si lo haces, pero ¿quién soy yo para evitar que un tonto encuentre $gsu:muerte;?$B$BSi buscas a esa bestia, entonces escuché que está en el pantano, al este del camino donde se bifurca hacia Dun Modr. Está al acecho allí entre los cocodrilos... ¡y cosas peores!$B$BY deja tu dinero aquí. No lo necesitarás donde vas... y no querrás que el viejo Greenie se rompa un diente con tu oro cuando te muerda por la mitad, ¿verdad?",
    ["O"] = "Encuentra al Guardaverde.",
    ["T"] = "El Guardián del Bosque",
  },
  [464] = {
    ["D"] = "El clan orco Faucedraco fue prácticamente destruido al final de la Segunda Guerra, después de que perdieron el control de sus dragones rojos. Los dragones no estaban muy contentos con sus antiguos amos y cazaron ferozmente a los Faucedraco. Los pocos que quedaron huyeron de Grim Batol y se adentraron en las montañas al este de la Excavación de Whelgar.$B$BAhora han ganado fuerza y tienen un campamento al noreste de Menethil.$B$BQueremos que ataques ese campamento. Reúne y tráeme sus estandartes de guerra como prueba.",
    ["O"] = "Lleva 8 Estandartes de Guerra Faucedraco al Capitán Puño Fuerte.",
    ["T"] = "Estandartes de Guerra",
  },
  [465] = {
    ["D"] = "Mientras luchabas contra los Faucedraco, descubrimos más de sus planes.$B$BNek\'rosh ha construido catapultas y planea sitiar a Menethil. ¡Y con la lucha en Dun Algaz, estamos efectivamente bloqueados de los refuerzos!$B$BNecesitas destruir esas catapultas. Están en el campamento Faucedraco.$B$BToma, lleva esta yesca. Lucha hasta llegar a las catapultas y enciéndelas con la yesca.$B$BY después de que hayas hecho eso, encuentra y mata a Nek\'rosh. Necesitamos cortar esta bestia Faucedraco... por el cuello.",
    ["O"] = "Destruye las catapultas Faucedraco.",
    ["T"] = "La Apuesta de Nek\'rosh",
  },
  [466] = {
    ["D"] = "Escuché que se encontró un mineral raro, la incendicita, en los Humedales, en una cueva al noroeste de la Puerta de Algaz. La incendicita es un material volátil y difícil de usar sin volarse uno mismo, ¡pero estoy seguro de que si se funde correctamente podría hacer proyectiles de cañón que den un gran golpe!$B$B¡Consígueme mineral de incendicita y encontraré a alguien lo suficientemente valiente como para experimentar con él!$B$BOh, y para extraer ese mineral de una veta necesitarás habilidad en minería. Si no la tienes, entonces... lleva a un amigo que sí la tenga a esa cueva.",
    ["O"] = "Lleva 6 cargamentos de Mineral de Incendicita al Piloto Stonegear en Dun Morogh.",
    ["T"] = "Búsqueda de Incendicita",
  },
  [467] = {
    ["D"] = "¡Al piloto Stonegear le encanta su máquina de asedio, como debería ser con todo piloto! Y siempre está ansioso por encontrar nuevas formas de mejorar el poder de fuego de su tanque.$B$BHa escuchado rumores de un mineral raro y está buscando a alguien que lo cace por él. Si tus botas están empezando a picarte, entonces encuentra a Stonegear en el Depósito de Steelgrill en Dun Morogh y ve qué quiere.",
    ["O"] = "Habla con el Piloto Stonegear.",
    ["T"] = "La Búsqueda de Piedravil",
  },
  [468] = {
    ["D"] = "El montañero Rockgar está buscando voluntarios para una misión a través de Dun Algaz y hacia los Humedales. $B$BSi quieres mojarte las botas en ese país empapado al norte, habla con Rockgar en Dun Algaz. Dun Algaz está al norte por el camino, más allá de la torre de vigilancia.",
    ["O"] = "Habla con el Montaraz Rockgar.",
    ["T"] = "Informe para el Montañero Roccagar",
  },
  [469] = {
    ["D"] = "Saludos, viajero.$b$b¿No estarás yendo en dirección a Menethil, verdad? Si no es así, te sugiero que pases por allí, es un buen lugar para comprar bienes e incluso tomar una pinta y relajarte.$b$bAhora, si resulta que estás yendo en esa dirección, ¿no te importaría llevar este paquete de pieles de crocolisco a James Halloran, el curtidor?$b$bLo haría yo mismo, pero mi esposa ha estado de bastante mal humor últimamente, y me gustaría retrasar enfrentarme a ella tanto como sea posible.",
    ["O"] = "Entrega el Fardo de Pieles de Crocolisco a James Halloran, el peletero, en Puerto de Menethil.",
    ["T"] = "Entrega Diaria",
  },
  [470] = {
    ["D"] = "En mi visita semanal a la Tumba de Ironbeard fui atacado por horribles y goteantes oozes. Naturalmente, entré en pánico.$b$b¡Le arrojé mi bolsa a uno, pero no hizo nada! El ooze simplemente la absorbió... Afortunadamente, eso me dio suficiente tiempo para escapar.$b$bLa mala noticia es que realmente necesito recuperar el contenido de mi bolsa.$b$b¡No sé a qué ooze le arrojé mi bolsa, todos se veían iguales! La Tumba de Ironbeard está al noreste de aquí, cerca de Dun Modr. ¡Muchas gracias!",
    ["O"] = "Uno de los mocos en la Tumba de Barbaférrea tiene la bolsa de Sida, recupérala y llévasela a ella en el Puerto de Menethil.",
    ["T"] = "Excavando en el Lodo",
  },
  [471] = {
    ["D"] = "Envié a mi aprendiz a los pantanos para desollar a algunos de nuestros famosos crocoliscos gigantes de los Humedales. Cuando no regresó, salí a buscarlo... Digamos que no fue una vista agradable.$B$BLa partera dice que podría recuperarse, pero mientras tanto, ¡todavía necesito mis pieles!$B$B... Si te gustaría matar a algunos más de esos brutos, no me opondría, después de lo que hicieron. Encontrarás a los crocos al norte, siguiendo la costa, más allá de las chozas de los múrlocs.",
    ["O"] = "Recolecta 6 Pieles de Crocolisco Gigante y llévaselas a James Halloran en Puerto de Menethil.",
    ["T"] = "Deberes de Aprendiz",
  },
  [472] = {
    ["D"] = "¡Dun Modr ha caído en manos de los Enanos Hierro Negro!$B$BMis heridas son graves, $c. ¡La mayoría del regimiento fue asesinado! Los matones Hierro Negro nos atacaron antes de que pudiéramos reagruparnos después de la emboscada del Puente Thandol.$B$BNuestro líder, Longbraid, hizo sonar el cuerno de retirada. Mientras abandonábamos la ciudad, me golpeó un hachazo perdido en la espalda. Todo se volvió negro.$B$BDesperté aquí en Menethil, pero temo por mis compañeros soldados. La esperanza aún arde en mí. ¡Quizás Longbraid todavía esté vivo! ¡Ve si puedes encontrarlo cerca de Dun Modr, $r!",
    ["O"] = "Busca a Trenzado Largo cerca del pueblo de Dun Modr.",
    ["T"] = "La Caída de Dun Modr",
  },
  [473] = {
    ["D"] = "$N. El clan Faucedraco está mostrando su fuerza en más que solo Algaz. El Capitán Puño Férreo ha pedido hablar con aquellos dispuestos a ofrecer ayuda contra estos orcos. Está dentro del castillo, en la sala de guerra.",
    ["O"] = "Habla con el Capitán Stoutfist.",
    ["T"] = "Informe para el Capitán Puño Fuerte",
  },
  [474] = {
    ["D"] = "Ahora que las catapultas han sido quemadas, encuentra y mata al Jefe Nek\'rosh.",
    ["O"] = "Mata al Jefe Nek\'rosh$B$BLleva la Cabeza de Nek\'rosh al Capitán Puño Férreo.",
    ["T"] = "Derrota a Nek\'rosh",
  },
  [475] = {
    ["D"] = "Una brisa inquietante sopla a través del bosque.$B$BGaerolas Talvethren sirve como Gran Guardián de los Druidas del Sueño en la Cueva Ban\'ethil. Su deber como protector elegido de los Durmientes es asegurar su seguridad para que su pacto con Ysera se mantenga.$B$BPero las noticias de Gaerolas ahora están retrasadas y me pongo nervioso. Viaja al este a la Aldea Brisa Estelar y trae un informe de Gaerolas para que pueda poner fin a mis preocupaciones, sabiendo que mis hermanos soñadores duermen a salvo.",
    ["O"] = "Busca a Gaerolas Talvethren en la Aldea Estrella Fugaz.",
    ["T"] = "Una Brisa Preocupante",
  },
  [476] = {
    ["D"] = "¡La tribu Gnarlpine ha sido corrompida!$b$bLos furbolgs, una vez pacíficos, se han vuelto contra los protectores del bosque. Me emboscaron cuando salía hacia la Guarida de Ban\'ethil y procedieron a saquear la Aldea Brisa Estelar. Ursal el Maullador, su jefe, está usando los poderes malignos del Musgo Vil para volverlos locos.$b$bEstoy demasiado herido para regresar con Athridas y llevarle esta grave noticia. La tarea recae en ti, joven $c. Solo podemos esperar que los Gnarlpine enloquecidos no hayan llegado a Ban\'ethil todavía.",
    ["O"] = "Regresa con Athridas Mantospino en Dolanaar.",
    ["T"] = "Corrupción de los Tuercepinos",
  },
  [477] = {
    ["D"] = "Como sabrás, el pueblo de Molino Ámbar sigue siendo una fuente de oposición humana, principalmente a través del apoyo que reciben de los magos de Dalaran.$b$bNo sé cuál es su plan, pero que muestren interés en un pueblo remoto como Molino Ámbar indica que debe tener una mayor importancia.$b$bHan estado enviando cajas en carros desde Trabalomas. Muchos de esos suministros terminan en un pequeño campamento al norte de la Aldea Piroleña. Recupera el contenido de una caja y tráemelo.",
    ["O"] = "Recupera el contenido de una de las cajas de los magos de Dalaran. Encontrarás la Aldea Pirosurco al sur.",
    ["T"] = "Cruces de Frontera",
  },
  [478] = {
    ["D"] = "<Rápidamente quitas el colgante del cajón y reemplazas la tapa.>",
    ["O"] = "Lleva el extraño colgante al Sacerdote de las Sombras Allister en el Sepulcro.",
    ["T"] = "Mapas y Runas",
  },
  [479] = {
    ["D"] = "Dalar está intentando localizar la fuente de los hechizos de los magos. Por ahora, tendremos que ralentizar su progreso de cualquier manera posible...$b$bLos conjuradores, magos y protectores sin duda llevan los colgantes. Quítalos y recógelos.$b$bToma el camino principal al sur y el desvío hacia el este hasta Ambermill.",
    ["O"] = "Consigue 8 Colgantes de Dalaran para el Sacerdote de las Sombras Allister en el Sepulcro.",
    ["T"] = "Investigaciones en Molino Ámbar",
  },
  [480] = {
    ["D"] = "Un archimago de gran poder está supervisando la reactivación del nodo de energía ley. Con el progreso que han hecho, nuestra única esperanza de detenerlos es matándolo. Será difícil, sin duda está protegido por muchos otros magos y guardianes.$B$BDebes ir, y debes despacharlo rápidamente. Cuando esté muerto, recupera su bastón para mí. Ve ahora.",
    ["O"] = "Mata al archimago de Dalaran, luego recupera su bastón para el Sacerdote de las Sombras Allister en el Sepulcro.",
    ["T"] = "El Tejedor",
  },
  [481] = {
    ["D"] = "Llévale el colgante a Dalar Tejeamaná, tal vez él pueda arrojar algo de luz sobre los planes de los magos.",
    ["O"] = "Lleva el colgante con runas a Dalar Tejeamanecer en el Sepulcro.",
    ["T"] = "Análisis de Dalar",
  },
  [482] = {
    ["D"] = "¡Por supuesto! ¿Cómo podría haberlo olvidado?$b$bAmbermill fue uno de los sitios señalados por el Kirin Tor, notado porque alberga un nodo de energía ley inactivo. Los magos deben estar planeando reactivar el nodo y usar sus energías para algún propósito mayor.$b$bNo podemos permitir que esto suceda...$b$bSe necesitará una gran cantidad de poder para activar las energías ley del área, podemos retrasarlos quitándoles los colgantes a los magos.$b$bTransmite esta información a Allister, él decidirá un curso de acción.",
    ["O"] = "Habla con el Sacerdote de las Sombras Allister en El Sepulcro.",
    ["T"] = "Las Intenciones de Dalaran",
  },
  [483] = {
    ["D"] = "Se han visto invasores Gnarlpine devastando la Guarida Ban\'ethil al oeste.$B$BLos druidas en letargo estarán atrapados en el Sueño Esmeralda por la eternidad, inconscientes de su destino, a menos que les ayudemos. El delicado ritual de hibernación no puede romperse sin las Reliquias del Despertar.$B$BViaja a la Guarida y recupera el Talismán de Garra de Cuervo, la Pluma de Pluma Negra, el Zafiro del Cielo y la Runa de Anidación. Los druidas los guardan en cofres sagrados. Devuélvemelos y prepararé el ritual de despertar.",
    ["O"] = "Recupera las Reliquias del Despertar y llévaselas a Athridas Mantospino en Dolanaar.",
    ["T"] = "Las Reliquias del Despertar",
  },
  [484] = {
    ["D"] = "Soy James Halloran, el curtidor. Mis pieles de crocolisco curadas son muy solicitadas en todo el mundo, con envíos que salen en barco a puertos de todo el mundo.$b$b¿Conoces a Einar? Es uno de los cazadores que me trae pieles de crocolisco.$b$bMe estoy quedando un poco corto en las pieles más suaves que provienen de los crocoliscos jóvenes.$b$bComo me estoy quedando sin existencias, te pagaré bien por un montón de pieles. Puedes encontrar a los crocoliscos jóvenes en los pantanos justo afuera del pueblo.",
    ["O"] = "Consigue 4 Pieles de Crocolisco Joven para James Halloran en el Puerto de Menethil.",
    ["T"] = "Pieles de Crocolisco Joven",
  },
  [485] = {
    ["D"] = "Has descubierto una especie de dispositivo extraño en forma de huevo hecho de metal. Jugando con una de sus perillas, el huevo cobra vida y se abre en una especie de artilugio robótico gnomo. Una voz desde dentro del huevo robótico cobra vida.$B$B\"¡Mi nombre es Oglethorpe Obnoticus, y mi robot de localización se ha estrellado! Te recompensaré por su recuperación; ¡por favor lleva este faro al robot!\"$B$B\"¡Ahora he calculado las coordenadas del robot para ti, se estrelló en la cabecera del río cerca de Roca Skulk!\"",
    ["O"] = "Lleva el Distress Beacon al robot guía de Oglethorpe en la cabecera del río en las Tierras del Interior, cerca de Roca Skulk.",
    ["T"] = "¡Encuentra a OOX-09/HL!",
  },
  [486] = {
    ["D"] = "Ahora ha llegado el momento de salvar a los Druidas de las Garras. Si fallamos ahora, $N, estarán perdidos para siempre en el sueño.$b$bPrepararé las Reliquias del Despertar y realizaré el ritual. Para que mi trabajo surta efecto, la bestia maldita responsable de esta horrible situación debe ser asesinada. Solo entonces el ritual estará completo.$b$bFue Ursal el Aplastador quien interfirió con el llamado de nuestros hermanos y es Ursal el Aplastador quien debe pagar ahora para que puedan ser liberados. Viaja a la Guarida de los Gnarlpine en el suroeste y mátalo.",
    ["O"] = "Mata a Ursal el Aplastador y regresa con Athridas Mantososo en Dolanaar.",
    ["T"] = "Ursal el Aplastador",
  },
  [487] = {
    ["D"] = "El camino a Darnassus debe mantenerse seguro. Los viajeros que se dirigen desde Dolanaar a Darnassus han informado de ataques despiadados por parte de los furbolgs corruptos de la tribu Peloescarcha.$B$BNoticias importantes y comercio viajan hacia y desde Darnassus a través de este camino a diario. No podemos permitir que una banda de paganos descontrolados aterrorice a la gente.$B$BToma las armas en nombre del bosque sagrado, $c. Su guarida está en algún lugar debajo de este punto de observación. Mata a 6 de estos emboscadores Peloescarcha e infórmame.",
    ["O"] = "Mata 6 Emboscadores Nudopino y regresa con la Centinela Amara Caminante Nocturno afuera de Dolanaar.",
    ["T"] = "El Camino a Darnassus",
  },
  [488] = {
    ["D"] = "Ansioso por trabajar, ya veo. Por suerte para ti, no pasa un día sin que desee tener un $c novato para que haga mis mandados.$b$bVerás, $N, puedo hacerte muy feliz y proporcionarte cosas que nunca soñaste tener. Pero para que eso suceda, debes traerme ciertos artículos.$b$bMi negocio en el bosque a menudo requiere ciertos... ingredientes. Tráeme 3 colmillos de Sable de la Noche, 3 plumas de búho estrígido y 3 trozos de seda de araña del Bosque del Ocaso.$b$bMantengamos esto en secreto, $r.",
    ["O"] = "Lleva a Zenn Pezuña Podrida fuera de Dolanaar 3 Colmillos de Sable de la Noche, 3 Plumas de Lechuza Strigid y 3 Trozos de Seda de Araña del Bosque.",
    ["T"] = "La Misión de Zenn",
  },
  [489] = {
    ["D"] = "El Consejo del Bosque tiene noticias de que ayudaste a Zenn Pezuña Inmunda. El sátiro es un enemigo del bosque. Como $r, deberías saber que es mejor no profanar el bosque matando a las criaturas de la Naturaleza.$B$BDebes redimirte ante los ojos del Consejo si deseas seguir siendo un amigo de Teldrassil.$B$BEnseña una lección a Pezuña Inmunda y serás redimido. Los Conos Vil son semillas corrompidas que caen de los árboles. Despiden humo verde.$B$BDale algunos a Pezuña Inmunda. Pensará que le has traído un bocadillo inofensivo.",
    ["O"] = "Recolecta 3 Piñas de Fuego Abisal y entrégaselas a Zenn Pezuña Podrida fuera de Dolanaar.",
    ["T"] = "¡Busca Redención!",
  },
  [490] = "_",
  [491] = {
    ["D"] = "Thule una vez me dijo que, hace mucho tiempo, él y dos colegas crearon una varita mágica, tejida a partir de las varitas de aprendiz que cada mago poseía. Esta varita era un símbolo de su amistad, y aunque Thule llegó a despreciarla, no podía deshacerse de ella, ni dejar que los otros la tuvieran. Así que la robó y la escondió en el Castillo de Fenris.$B$BAquí. Lleva la varita a Bethor Iceshard, uno de los amigos de Thule de años atrás. Estoy seguro de que la querrá.",
    ["O"] = "Lleva la Varita Tejida a Bethor Hielocero en el Barrio de la Magia de Entrañas.",
    ["T"] = "Varita para Bethor",
  },
  [492] = {
    ["D"] = "Según los guardias de la muerte, otro de esos tontos montañeros enanos acaba de ser capturado. A los guardias de la muerte les gusta usar el sótano de la Taberna del Fin del Patíbulo como celda de retención hasta que los prisioneros puedan ser \"tratados adecuadamente\".$B$B¿Por qué no vas a ver cómo disfruta el Montañero Capturado esta bebida especial que hice para él? Contiene un toque sutil de lo que la Dama Oscura tiene planeado para el resto de Azeroth.",
    ["O"] = "Lleva la Bebida Especial de Johaan al Montañero Capturado.",
    ["T"] = "Una Nueva Plaga",
  },
  [493] = {
    ["D"] = "La Sociedad Real de Boticarios está bajo una presión extrema por parte de la Dama Oscura para desarrollar una Nueva Plaga. Estamos trabajando diligentemente y hemos hecho mucho progreso. Creemos que el éxito se logrará mucho más rápido si compartimos información dentro de la Sociedad.$B$BPor esa razón quiero que entregues mis últimos hallazgos al Boticario Lydon en Molino Tarren, un pequeño pueblo enclavado en las Tierras Altas de Trabalomas. El viaje será largo.$B$B¡Dirígete al sur y sigue los caminos! ¡Sigue las señales de cerca!",
    ["O"] = "Entrega los hallazgos del Boticario Renferrel al Boticario Lydon en el pueblo de Molino Tarren en las Foothills de Trabalomas.",
    ["T"] = "Viaje a las Tierras del Páramo",
  },
  [494] = {
    ["D"] = "Sé silencioso y presta atención, $c.$b$bMe enviaron a esta posición para inspeccionar la ciudad de Trabalomas. Mi misión es de reconocimiento. Es imperativo que envíes un mensaje a la Alta Ejecutora Darthalia en Molino Tarren de inmediato. Hazle saber que yo, el Acechador de la Muerte Lesh, envío el siguiente mensaje:$b$b\"El grito del cuervo desde el oeste nos llama.\"$b$BSigue el camino hacia el este y presta mucha atención a las señales. Ahora, ¡apresúrate! Ve a Molino Tarren, y con urgencia, podría agregar.",
    ["O"] = "Viaja a Molino Tarren para entregar el mensaje del Acechador de la Muerte Lesh al Alto Ejecutor Darthalia.",
    ["T"] = "Hora de Atacar",
  },
  [495] = {
    ["D"] = "Mug\'Thol y su banda de ogros fueron enviados a Alterac para recuperar la Corona de Voluntad para Lady Sylvanas. Desafortunadamente, en lugar de devolverla, el idiota decidió probársela... Supongo que los ogros no pueden resistirse a nada brillante y reluciente.$B$BSus torpes acciones se han convertido en un problema, sin embargo. La Corona de Voluntad le permitió resistir la posesión que se le impuso para servir a Lady Sylvanas.$B$BLa Dama Oscura quiere que se haga un ejemplo, viaja a Molino Tarren en Trabalomas e informa a Melisara, mi teniente.",
    ["O"] = "Preséntate ante Melisara en Molino Tarren en las Tierras Altas de los Páramos.",
    ["T"] = "La Corona de la Voluntad",
  },
  [496] = {
    ["D"] = "Ah, otro día miserable en Molino Tarren. Todo este aire limpio me pone de tan mal humor, $N.$B$BCuanto antes podamos plagiar a los humanos aquí, mejor. He estado realizando estudios intensivos sobre posibles agentes mortales para usar en mis pociones, pero no tengo tiempo para recolectarlos todos.$B$BSi quieres hacerte útil, consígueme los siguientes artículos: 10 lenguas de oso gris y el muy raro y difícil de encontrar, Ichor de Rastrero. Encontrarás tanto osos como rastreros justo fuera de Molino Tarren.",
    ["O"] = "El Boticario Lydon de Molino Tarren quiere 10 Lenguas de Oso Gris y algo de Ichor de Trepador.",
    ["T"] = "Elixir de Sufrimiento",
  },
  [497] = "_",
  [498] = {
    ["D"] = "Yo y algunos otros guerreros fuimos enviados por el Jefe de Guerra para recuperar un objeto de Durnholde e investigar este llamado Sindicato.$b$bFuimos a Durnholde para echar un vistazo, pero fuimos sorprendidos por un gran número de humanos. Luchamos valientemente, pero su gran número fue suficiente para derrotarnos. Tomaron a algunos de nosotros como prisioneros, pero yo pude escapar.$b$bDurnholde está justo al sureste de aquí, al otro lado del río. Drull y Tog\'thar están siendo retenidos allí, debes ir a rescatarlos.",
    ["O"] = "Krusk en Molino Tarren necesita que liberes a Drull y Tog\'thar del Castillo de Durnholde.",
    ["T"] = "El Rescate",
  },
  [499] = {
    ["D"] = "Solo tomará un momento para que mezcle estos componentes en lo que he denominado un Elixir de Sufrimiento.$b$b¡Ahí vamos! Ah, nada alegra un día sombrío en estas miserables estribaciones como traer un poco de sufrimiento al mundo.$b$bVeamos si nuestro pequeño brebaje ha logrado su efecto deseado.$b$bDale a mi asistente Umpi una probada de lo que la Dama Oscura tiene planeado tanto para los humanos como para la Plaga.",
    ["O"] = "Dale el Elixir de Sufrimiento a Umpi.",
    ["T"] = "Elixir de Sufrimiento",
  },
  [500] = {
    ["D"] = "Los ogros Machacacresta han cavado un montículo ogro en las Montañas de Alterac cerca de la ciudad en ruinas de Alterac. Y mis exploradores me dicen que también han tomado esas ruinas.$B$BNo podemos dejar que se sientan cómodos allí arriba, si piensan que están seguros donde están, entonces su próximo paso será bajar a las estribaciones, lo que los pondrá justo en nuestra puerta.$B$BVe al norte a las Montañas de Alterac y caza ogros. Tráeme los Huesos Sucios que llevan y ganarás una buena recompensa.",
    ["O"] = "Reúne 9 Nudillos Sucios de los ogros Aterra Cresta en las Montañas de Alterac. Llévaselos al Mariscal Rutagrana en Costasur.",
    ["T"] = "Recompensa de los Aplastacresta",
  },
  [501] = {
    ["D"] = "Oh, $N. . .las flores están floreciendo en Trabalomas y el aire es tan fresco y vigorizante. No puedo evitar sumergirme en la miseria de todo. Miro por mi ventana sucia y anhelo el día en que nuestra Nueva Plaga traiga la muerte que este mundo merece.$b$bPor mucho que me encantaría sentarme y lamentarme todo el día, mi deber con la Dama Oscura me llama. ¿Me ayudarías? Mi investigación me lleva a creer que puedo hacer la más espléndida poción de muerte con la sangre de un León de Montaña. Tráeme algo y nos divertiremos un poco.",
    ["O"] = "Lleva 10 viales de Sangre de León de Montaña al Boticario Lydon en Molino Tarren.",
    ["T"] = "Elixir de Dolor",
  },
  [502] = {
    ["D"] = "Ah, ¡nada como el olor a sangre fresca en las estribaciones!$B$BAhora mezclaremos un poco de esto y un poco de aquello, ¡y que comience la verdadera diversión! Lo que he creado con esta sangre, me gusta llamarlo Elixir del Dolor. Si mis cálculos son correctos, esta poción podría ser el comienzo de algo muy hermoso para la Lady Sylvanas. Pero lo que necesitamos es una prueba.$B$BLleva este elixir a la granja del norte en los Campos de Trabalomas, al suroeste. Veamos cómo le gusta a Stanley, el pequeño perro del granjero Ray, este \"regalo\".",
    ["O"] = "Alimenta a Stanley con la Poción de Dolor.",
    ["T"] = "Elixir de Dolor",
  },
  [503] = {
    ["D"] = "Por lo que puedo deducir de los documentos que conseguiste, Gol\'dir está siendo retenido en una gran base del Sindicato al norte, muy probablemente en las Montañas de Alterac. Ha estado cautivo demasiado tiempo y debe ser liberado.",
    ["O"] = "Libera a Gol\'dir y luego regresa con Krusk en Molino Tarren.",
    ["T"] = "Gol\'dir",
  },
  [504] = {
    ["D"] = "Ahora que has probado a los ogros de Crestafuria, quiero que realmente les des un golpe...$B$BEntra en las Ruinas de Alterac y busca a los Belicosos de Crestafuria. Quiero que mates a un buen número de ellos, esa es la única manera en que esos brutos aprenderán a mantenerse alejados del territorio de la Alianza.",
    ["O"] = "Mata 15 Belicistas Aplastacresta, luego regresa con el Mariscal Rutagrana en Costasur.",
    ["T"] = "Belicistas de los Aplastacresta",
  },
  [505] = {
    ["D"] = "Soy el nuevo magistrado de Costasur, asignado recientemente después del asesinato del magistrado anterior. Los asesinos nunca fueron encontrados, pero a través de nuestras investigaciones estamos casi seguros de que fueron contratados por el Sindicato, un grupo de ladrones liderado por villanos nobles del ahora caído reino de Alterac.$B$BEl Sindicato tiene un campamento al norte del Molino Tarren, ocupado por la Horda, y nuestras últimas pistas llevaron a ese campamento. Mata a los miembros del Sindicato que encuentres allí.",
    ["O"] = "Mata 12 Rondadores de la Hermandad y 8 Ladrones de la Hermandad, luego regresa con el Magistrado Henry Maleb en Costasur.",
    ["T"] = "Asesinos de la Hermandad",
  },
  [506] = {
    ["D"] = "Hace un día o dos, fui visitado por el líder de este Sindicato. Su nombre era Aliden Perenolde, y se hacía llamar señor de estas tierras. Me confió sus planes de tomar a los orcos que se le opusieran como esclavos, tal como lo habían planeado su padre y su mentor, el despreciado Blackmoore.$B$BMás importante fue que trajo a su amante, una chica frágil llamada Elysa. Alrededor de su cuello colgaba el colgante que desea el Jefe de Guerra.$B$BAhora vete, yo me las arreglaré, después de que haya resuelto mis asuntos aquí.",
    ["O"] = "Devuelve la información recopilada por Gol\'dir a Krusk en Molino Tarren.",
    ["T"] = "El Legado de Negrálito",
  },
  [507] = {
    ["D"] = "Está claro que Perenolde debe ser asesinado, su maldad eliminada del mundo. Su mansión se encuentra un poco al norte de aquí, en las orillas del Lago Lordamere. Cumple con tu deber, $n, luego recupera el colgante que el Jefe de Guerra desea y regresa aquí.",
    ["O"] = "Mata a Lord Aliden Perenolde y pregunta a su amante, Elysa, sobre el colgante de Taretha.",
    ["T"] = "Lord Aliden Perenolde",
  },
  [508] = {
    ["D"] = "El colgante? Por supuesto que puedes tenerlo... ¡Tómalo! No quiero ningún recuerdo de él... solo por favor, déjame en paz...",
    ["O"] = "Devuelve el Colgante de Taretha a Krusk en Molino Tarren.",
    ["T"] = "El Regalo de Taretha",
  },
  [509] = {
    ["D"] = "Otro día, otro elixir por hacer. Si dependiera de mí, dejaría que los patéticos humanos y la Plaga se mataran entre sí. Parecen empeñados en hacerlo de todos modos.$B$BDesafortunadamente, nuestra Dama Oscura quiere acelerar un poco el proceso, así que ¿por qué no pasar estos días mórbidos esparciendo un poco de muerte y enfermedad por la tierra?$B$BAquí está el plan, $N: ve a la Granja Nethander, al sur de Molino Tarren y al este del río y Costasur. Recoge para mí 6 Flores de Hocicofango del campo para mi próximo elixir.",
    ["O"] = "Lleva 6 Flores de Hocico de Barro al Boticario Lydon en Molino Tarren.",
    ["T"] = "Elixir de Agonía",
  },
  [510] = {
    ["D"] = "Entre estos documentos hay una lista de suministros solicitados. Estos incluyen artículos mundanos (comida, armas, ropa) así como equipo más exótico (máscaras, aceite, veneno).$B$BPero lo más notable es la urgencia de la solicitud: estos suministros eran necesarios pronto para emprender lo que se describe en el documento como una \"misión importante contra Costasur\".",
    ["O"] = "Lleva los Planes Siniestros al Magistrado Maleb en Costasur.",
    ["T"] = "Planes Siniestros",
  },
  [511] = {
    ["D"] = "Una de las cartas en esta mesa está cifrada y es indescifrable.",
    ["O"] = "Lleva la Carta Cifrada al Maestro de Historia Dibbs en Costasur.",
    ["T"] = "Carta Cifrada",
  },
  [512] = {
    ["D"] = "Temo que el liderazgo detrás del Sindicato está decidido a arruinar Costasur. Debemos golpear duro a estos líderes antes de que sus planes se realicen.$B$BEntra en las ruinas de Strahnbrad, en las Montañas de Alterac al norte. Creemos que el Sindicato está usando el área como cuartel general.$B$BSabemos que esta banda de asesinos está dirigida por antiguos nobles de Alterac. Pueden estar escondidos como agentes normales del Sindicato, pero sabrás que has matado a un noble por el anillo con sello que lleva.$B$BTráeme esos anillos con sello.",
    ["O"] = "Lleva 7 Anillos de Sello de Alterac al Magistrado Maleb en Costasur.",
    ["T"] = "Muertes Nobles",
  },
  [513] = {
    ["D"] = "Justo cuando pensaba que otro día pasaría sin alegría, me trajiste esas maravillosas Flores Mudsnout.$b$bPrimero las mezclaré hasta convertirlas en un compuesto fino. Luego necesitaremos que el Maestro Boticario Faranell en Entrañas aplique su coloide de descomposición y luego estaremos listos para divertirnos.$b$bAquí, toma este Compuesto Mudsnout y llévaselo de inmediato al Maestro Faranell, $N.",
    ["O"] = "Lleva el Compuesto Barrofondo al Maestro Boticario Faranell en Entrañas.",
    ["T"] = "Elixir de Agonía",
  },
  [514] = {
    ["D"] = "Tengo un colega en Forjaz que podría ayudarte a descifrar esta carta. Ve a la Biblioteca en el Salón de los Exploradores en Forjaz y habla con el Prospector Pico Tormenta. Dile que te envié yo y muéstrale la carta. Si hay alguien al norte de la Montaña Roca Negra que pueda leer esto, es él.",
    ["O"] = "Lleva la Carta Ingeniosamente Encriptada al Prospector Pico Tormenta en Forjaz.",
    ["T"] = "Carta para Pico Tormenta",
  },
  [515] = {
    ["D"] = "¿Un compuesto de hocicofango? ¡Absolutamente brillante! ¿Por qué no se me ocurrió a mí?$B$BDeja que contribuya con mi coloide de descomposición a esta mezcla diabólica.$B$BPara activar los agentes contaminantes en esta mezcla de hocicofango, Lydon necesitará una poción de sangre de troll fuerte, así como algunas escamas de espinilla y ojos de aleta desgarrada de la costa sur de Trabalomas. Él sabrá qué hacer una vez que le hayas reunido todos los reactivos. ¡Y estoy ansioso por escuchar cómo le va en su experimento!",
    ["O"] = "Lleva la Mezcla de Hocico de Barro, una Poción Fuerte de Sangre de Troll, 5 Escamas de Daggerspine y 5 Ojos de Aleta Desgarrada al Boticario Lydon en Molino Tarren.",
    ["T"] = "Elixir de Agonía",
  },
  [516] = {
    ["D"] = "He recibido informes de que un grupo de no muertos se está preparando para atacarnos. Con esta información, podemos darles la vuelta y atacarlos primero, frustrando su pequeño plan.$b$bDesafortunadamente, mi información es incompleta, en el mejor de los casos. Se informa que se esconden en un lugar conocido como el Peril de Beren. La ubicación exacta es desconocida, pero parece ser una cueva en las colinas, cerca de un grupo de magos de Dalaran.$b$bConfío en tu ingenio. Encuéntralos y acaba con ellos.",
    ["O"] = "Localiza el Peligro de Beren, luego mata a 6 Peones Garvacurva y 6 Guardianes Garvacurva, luego regresa con el Sacerdote de las Sombras Allister en el Sepulcro.",
    ["T"] = "El Peligro de Beren",
  },
  [517] = {
    ["D"] = "Este nuevo elixir finalmente está listo para ser probado. Pero, ¿por qué dejar que una plaga altamente contagiosa se desperdicie en una simple rana o perro? Divirtámonos de verdad en nombre de Lady Sylvanas.$b$bDirígete a Dun Garok. Ya sabes, los barracones donde esos sucios enanos corretean. Robame un barril de su Cerveza Shindigger. Una vez que tenga ese barril, tú y yo tendremos una gran fiesta, $N.",
    ["O"] = "Lleva un barril de Cerveza Pisatripas al Boticario Lydon en Molino Tarren.",
    ["T"] = "Elixir de Agonía",
  },
  [518] = {
    ["D"] = "Mug\'Thol y sus ogros Crushridge han invadido las ruinas de Alterac. Será difícil infligir un castigo serio sobre él sin reducir el número de ogres.$B$BDespués de debilitarlos, procederemos.$B$B¿Entendido? Bien. Ahora ve.",
    ["O"] = "Mata 14 Aplastadores Machacacresta para Melisara en Molino Tarren.",
    ["T"] = "La Corona de la Voluntad",
  },
  [519] = {
    ["D"] = "Targ, Muckrake y Glommus son los lugartenientes de Mug\'Thol. Son leales a él, aunque quién sabe qué impulsa a un ogro a la lealtad.$b$bTráeme sus cabezas, $n, y luego todo lo que quedará será quitarle la cabeza a Mug\'Thol, corona y todo.",
    ["O"] = "Mata a Targ, Barrofango y Glommus y lleva sus cabezas a Melisara en Molino Tarren.",
    ["T"] = "La Corona de la Voluntad",
  },
  [520] = {
    ["D"] = "Con los lugartenientes de Mug\'Thol muertos, todo lo que queda es matar a Mug\'Thol y recuperar la Corona de la Voluntad. No tengo dudas de que será reacio a separarse de ella. Lo más probable es que te esté esperando, dado el... fallecimiento... de sus compañeros.$b$bEncuentra a Mug\'Thol y entrégame su cabeza y la Corona. La resistencia Machacacresta llegará a su fin.",
    ["O"] = "Mata a Mug\'Thol, y lleva su cabeza junto con la Corona de Voluntad a Melisara en Molino Tarren.",
    ["T"] = "La Corona de la Voluntad",
  },
  [521] = {
    ["D"] = "Todo lo que queda es entregar la Corona de Voluntad a Sharlindra en Entrañas. Ella se asegurará de que sea entregada a Lady Sylvanas. Te advertiría contra la idea de usarla para tus propios fines, $n, el destino de Mug\'Thol debería ser una ilustración suficiente.",
    ["O"] = "Entrega la Corona de Voluntad a Sharlindra en Entrañas.",
    ["T"] = "La Corona de la Voluntad",
  },
  [522] = {
    ["D"] = "Este es un contrato para el asesinato del Magistrado Henry Maleb. Fue sellado y estampado con cera roja, usando un anillo de sello distintivo.",
    ["O"] = "Lleva el Contrato del Asesino al Magistrado Maleb en Costasur.",
    ["T"] = "Contrato de Asesino",
  },
  [523] = {
    ["D"] = "El contrato que recuperaste fue firmado por el Barón Vardus, un noble del antiguo reino de Alterac, y uno conocido por su corazón frío y sus prácticas brutales. No me sorprende que alguien como el Barón esté detrás de esto.$B$BEncuentra a este hombre. Encuéntralo y haz que pague por sus crímenes. Debe estar entre los escondites de la Hermandad, tal vez en las ruinas de Strahnbrad, o tal vez dentro del valle al norte de ellas.",
    ["O"] = "Lleva la Cabeza del Barón Vardus al Magistrado Maleb en Costasur.",
    ["T"] = "La Muerte del Barón",
  },
  [524] = {
    ["D"] = "En la habitación de al lado, los Guardias de la Muerte están reteniendo a unos cuantos granjeros capturados. El Alto Ejecutor Darthalia sospechaba que uno de los estrategas militares de Ventormenta se estaba escondiendo entre ellos. Aparentemente, el tipo militar fue encontrado muerto en las Tierras de la Peste anoche. Darthalia, siempre pendiente de mí, decidió que los prisioneros quedarían a mi disposición.$b$bCreo que es hora de que les demos una fiesta a esos granjeros. ¡Suena como un gran momento!$b$bColoca este barril contaminado en la alfombra de su habitación. ¡Cerveza apestada para todos!",
    ["O"] = "Coloca el Barril Contaminado en la alfombra para los granjeros capturados.",
    ["T"] = "Elixir de Agonía",
  },
  [525] = {
    ["D"] = "Descifré la carta, pero no sé qué hacer con ella. ¿Encontraste esto entre ladrones en las estribaciones de Alterac? Muy extraño...$B$BDeberías llevársela al magistrado de Costasur. Aunque esta carta es críptica, su pueblo es el más cercano a Alterac, si hay una amenaza en ciernes, él debería saberlo.",
    ["O"] = "Lleva la Carta Descifrada al Magistrado Maleb en Costasur.",
    ["T"] = "Misterios Adicionales",
  },
  [526] = {
    ["D"] = "Puede que hayas encontrado una barra de lingote de forjaluz en ese cofre, ¡pero eso no es suficiente para crear algo decente!$B$BDeberías volver allí y buscar más. Quizás los múrlocs alrededor de ese barco naufragado tengan lingotes de forjaluz.",
    ["O"] = "Reúne 5 Lingotes Forjaluz y luego regresa con Glorin Frenteférrea en los Humedales.",
    ["T"] = "Lingotes Forjaluz",
  },
  [527] = {
    ["D"] = "¡Firme mientras me hablas, $c!$b$bEstoy bajo las órdenes directas de Varimathras. Debemos sofocar la infestación humana hasta que nuestros boticarios desarrollen la nueva plaga.$b$bNuestra información nos lleva a creer que la ciudad de Trabalomas es vulnerable a un ataque. Tu primera misión está dirigida a interrumpir su suministro de alimentos e infraestructura.$b$bViaja al oeste. Ataca las granjas más al norte de los Campos de Trabalomas. Asegúrate de que los terratenientes, Granjero Ray y Granjero Getz, sean ejecutados también.",
    ["O"] = "Mata 6 Peones de Trabalomas, 6 Granjeros de Trabalomas, al Granjero Ray y al Granjero Getz e informa a Darthalia en Molino Tarren.",
    ["T"] = "Batalla de Trabalomas",
  },
  [528] = {
    ["D"] = "La noticia del ataque a las granjas del norte ha puesto a los aldeanos en pánico. Muy bien.$B$BNuestros Acechadores de la Muerte informan que los campesinos huyen a los campos del sur de Trabalomas. Los tontos creen que pueden esconderse en los huertos y escapar de su destino.$B$BDemuestra que están equivocados, $N. Regresa a los Campos de Trabalomas y busca a los campesinos escondidos. Devástalos en nombre de nuestra Dama Oscura.$B$B¡Despedido!",
    ["O"] = "Mata 15 Campesinos de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "Batalla de Trabalomas",
  },
  [529] = {
    ["D"] = "Los humanos de Villa del Cerro han estado presentando una resistencia inesperada. El problema parece ser que los aldeanos están siendo armados por el herrero local. Necesitamos cortar todas las líneas de suministro.$b$bTu misión será ejecutar al Herrero Verringtan. Mata también a sus aprendices. Mientras estés allí, trae un cargamento de hierro de su stock. Los suministros serán útiles cuando terminemos con la conquista de Villa del Cerro y comencemos a arrasar Costasur.",
    ["O"] = "Mata al Herrero Verringtan y a 4 Aprendices de Herrero de Trabalomas.$b$bRecupera un cargamento de hierro e informa a Darthalia en Molino Tarren.",
    ["T"] = "Batalla de Trabalomas",
  },
  [530] = {
    ["D"] = "Valdred, una vez mi buen amigo, fue el asesino de mi esposa. Aunque soy incapaz de sentir dolor, anhelo venganza. Si matas a Valdred y me traes sus malditas manos, te recompensaré.$B$BLa última vez que supe de él, estaba en el Muro de Greymane, al sur de Bosque de Argénteos, intentando huir al reino de Gilneas.",
    ["O"] = "Mata a Valdred Moray.$B$BLleva sus manos a Raleigh Andrean en Entrañas.",
    ["T"] = "La Venganza de un Esposo",
  },
  [531] = {
    ["D"] = "¡Luz! No traigas cosas así a la Logia... ¡es antihigiénico, indescriptible, anti... anti... antihigiénico!$b$b¡Solo sácalo de aquí, por favor! ¡Nadie quiere ver tu pequeño trofeo sangriento, de verdad!",
    ["O"] = "Lleva la cabeza del Viejo Hollín a Vyrin Vientosveloz en el Refugio del Errante.",
    ["T"] = "La Venganza de Vyrin",
  },
  [532] = {
    ["D"] = "El pueblo de Trabalomas está en desorden y creemos que pronto caerá. Sin embargo, debemos persistir en nuestra crueldad hasta que la escoria humana sea eliminada de las estribaciones para siempre.$B$BA pesar de sus abrumadoras pérdidas, los humanos se reúnen alrededor de su liderazgo y siguen luchando. Debemos eliminar a su líder, el Magistrado Burnside, así como al consejo de la ciudad. Destruye también su venerado documento político, la Proclamación de Trabalomas. Y mientras estás en eso, roba el registro de la ciudad.",
    ["O"] = "Mata al Magistrado Burnside y a 5 Consejeros de Trabalomas. Destruye la Proclamación de Trabalomas. Roba el Registro de la Ciudad de Trabalomas. Informa a Darthalia en Molino Tarren después.",
    ["T"] = "Batalla de Trabalomas",
  },
  [533] = {
    ["D"] = "Por fin comenzamos a escuchar palabras y rumores, encontrar evidencia del paradero de Gol\'dir. Los humanos lo han estado moviendo, pero estamos reduciendo nuestra búsqueda y enfocándonos en el gran campamento del Sindicato justo al norte de aquí.$b$bVe allí, $n, y trae cualquier información que puedas conseguir.",
    ["O"] = "Recupera información sobre el paradero de Gol\'dir para Krusk en Molino Tarren.",
    ["T"] = "Infiltración",
  },
  [534] = "_",
  [535] = {
    ["D"] = "Urrgh... Herido y muriendo y enfrentado al enemigo.$b$bSin duda me matarás de todos modos, pero si me traes un poco de cerveza de Costasur—¿tu gente bebe, no?—te diré lo que quieres saber.",
    ["O"] = "Lleva una jarra de Cerveza Cerveza de Costasur a Valik en el campamento de la Hermandad.",
    ["T"] = "Valik",
  },
  [536] = {
    ["D"] = "Transferido de la guardia de la ciudad de Ventormenta a Costasur, un pueblo que debería estar abandonado por lo que parece... Olor a puerto día y noche.$b$bSin mencionar al viejo Redpath... Quiere que me ocupe de los múrlocs. ¡Ja! $b$bApenas puedo obligarme a salir a algunos de los grupos de esos peces sobredimensionados en la Orilla Occidental. Si fueras en mi lugar y te ocuparas de ellos, lo agradecería.",
    ["O"] = "Mata 10 Cazadores de Mareas Aleta Rasgada y 10 Oráculos Aleta Rasgada para el Teniente Farren Orinelle en Costasur.",
    ["T"] = "Costa Abajo",
  },
  [537] = {
    ["D"] = "La carta que me trajiste fue escrita por un ser que se hace llamar Nagaz, un miembro del Despertar de Argus. No sé qué es este Despertar de Argus, pero si están aliados con la Hermandad, deben ser una amenaza.$B$BQuiero que encuentres a Nagaz y a los Magos Sombríos de Argus mencionados en esa carta. Quiero que los encuentres y los mates.$B$BBusca en las Tierras Altas, donde la Hermandad es más fuerte. Está al norte de Strahnbrad.$B$BCon suerte, encontrarás a estos enviados del Despertar de Argus allí.",
    ["O"] = "Mata 4 Magos de las Sombras de Argus.$B$BLlévale la Cabeza de Nagaz al Magistrado Maleb en Costasur.",
    ["T"] = "Consejo Oscuro",
  },
  [538] = {
    ["D"] = "$N. Si no te importa un viaje largo, tengo una tarea para ti, para ayudar nuevamente a Ventormenta a llenar su biblioteca.$B$BEl antiguo reino de Alterac cayó después de la gran guerra, y tenía una gran colección de libros. ¡Es una tragedia que la ciudad esté en ruinas, pero aún más trágico es todo el conocimiento perdido! Debemos recuperar lo que podamos.$B$BCostasur es la ciudad más cercana a las Ruinas de Alterac. Ve allí y habla con el Maestro de Letras Dibbs. Él puede dirigirte a las ruinas y a los libros que se puedan encontrar allí.",
    ["O"] = "Habla con el Maestro de Historia Dibbs en Costasur.",
    ["T"] = "Costasur",
  },
  [539] = {
    ["D"] = "El Registro de Villa del Cerro indica que la Mina Azurelode está bajo control de la Alianza. Además, la mina es una fuente principal de mineral de hierro para las armerías de la Alianza.$b$bAunque hemos asestado un golpe decisivo a la ciudad de Villa del Cerro, la Alianza todavía apoya y protege fervientemente la Mina Azurelode.$b$bLa mina se encuentra al sur de Villa del Cerro. Ve allí y mata al capataz, un humano llamado Bonds. Mata también a sus mineros. Eso enviará un mensaje claro a la Alianza.",
    ["O"] = "Mata al Capataz Bonds y a 10 Mineros de Trabalomas e informa a Darthalia en Molino Tarren.",
    ["T"] = "Batalla de Trabalomas",
  },
  [540] = {
    ["D"] = "Esas bestiales ogros ahora residen dentro de las ruinas de Alterac, y me estremece pensar qué están haciendo con los preciosos libros que aún quedan allí. ¡Debes recuperar lo que puedas!$B$BEntra en las Ruinas de Alterac y busca los tomos saqueados por los ogros. Consigue lo que puedas encontrar y tráemelo, pero también busca un libro en particular: El Brazo de Gri\'lek. Contiene antigua sabiduría troll que debo aprender, y las Ruinas de Alterac fue la última ubicación conocida de una copia intacta de este libro.",
    ["O"] = "Lleva 5 Tomes Recuperados y el Libro de Cuero Gastado que contiene El Brazo de Gri\'lek al Maestro del Saber Dibbs en Costasur.",
    ["T"] = "Preservando el Conocimiento",
  },
  [541] = {
    ["D"] = "¿Enanos ayudando a los humanos? Malditas sean esas pequeñas bestias.$b$bPondremos a prueba la fuerza de la llamada Alianza. Veremos cuán ansiosos están esos pequeños golpeadores de tobillos por ayudar a los humanos una vez que sientan nuestra ira. Sabemos exactamente dónde está su fortaleza en este territorio.$b$bHan establecido un cuartel en el sureste de Trabalomas, junto al Muro de Thoradin, llamado Dun Garok. La misión final de esta batalla es asediar Dun Garok, $c. El Capitán Ironhill, su líder, debe morir.",
    ["O"] = "Viaja a Dun Garok y mata a 10 Montañeros, 8 Fusileros, 4 Sacerdotes y al Capitán Ironhill e informa a Darthalia en Molino Tarren.",
    ["T"] = "Batalla de Trabalomas",
  },
  [542] = {
    ["D"] = "Necesito pasar un tiempo estudiando el libro \"El Brazo de Gri\'lek\" que me trajiste, aunque estoy seguro de que Milton está ansioso por llevar los otros libros recuperados a la colección de Ventormenta.$B$BAquí están los libros que recogiste de las Ruinas de Alterac, empaquetados y catalogados. Llévalos de regreso a Milton Sheaf en Ventormenta y estoy seguro de que estará agradecido.",
    ["O"] = "Lleva los Tomos de Alterac a Milton Sheaf en Ventormenta.",
    ["T"] = "Regreso con Milton",
  },
  [543] = {
    ["D"] = "La Tiara de Perenolde es una antigua reliquia de los \"nobles\" Perenolde, la familia gobernante de Alterac antes de que el reino cayera en ruinas. Se dice que fue creada por maestros joyeros en Forjaz, y engastada con las más finas esmeraldas.$B$BCuando Alterac fue destruida, la Tiara se perdió, pero hay rumores de que un mago ogro, Grel\'borg el Avaro, encontró la tiara y ahora vaga por las Ruinas de Alterac en busca de más tesoros.$B$BNos gustaría que adquirieras la tiara para nosotros.",
    ["O"] = "Lleva la Tiara de Perenolde a Remington Ridgewell en Ventormenta.",
    ["T"] = "La Tiara de Perenolde",
  },
  [544] = {
    ["D"] = "Vine a Molino Tarren para investigar, pero ahora debo resolver una crisis. Verás, cuatro No-Muertos huyeron de Entrañas hace unos meses. Le dieron la espalda a sus hermanos, pero lo que es peor... le robaron a la Dama Oscura.$B$BEstos ladrones irrumpieron en una bóveda segura y robaron cuatro artefactos, objetos que nuestros boticarios necesitaban para ciertos estudios. Sylvanas quiere que se devuelvan estos artefactos.$B$BLos ladrones huyeron de Entrañas a Dalaran, y esos magos los pusieron en cuarentena en el Campo de Reclusión de Lordamere.",
    ["O"] = "Encuentra a los traidores y recupera sus artefactos, luego regresa con Magus Mirada Vacía en Molino Tarren.",
    ["T"] = "Fuga de la Prisión",
  },
  [545] = {
    ["D"] = "Los magos de Dalaran construyeron una vasta cúpula mágica alrededor del corazón de su ciudad. Algunos creen que fue para protegerse de la violencia de estos tiempos, mientras que otros dicen que simplemente querían soledad, para estudiar y conspirar.$B$BNo me importa cuál fue la razón. Lo que me importa es que las ruinas de las afueras de su ciudad, fuera de la cúpula, pueden esconder valiosos tesoros mágicos.$B$BPero el área está patrullada por los magos y sus esclavos elementales.$B$BAsí que cázalos y regresa cuando su número se haya reducido lo suficiente.",
    ["O"] = "Mata 6 Invocadores de Dalaran y 12 Esclavos Elementales, luego regresa con el Magus Mirada Vacía en Molino Tarren.",
    ["T"] = "Patrullas de Dalaran",
  },
  [546] = {
    ["D"] = "Escucho que fuiste reclutado por Darthalia para hacer la guerra a los humanos de Trabalomas. ¡Qué envidia tengo!$b$bMientras tú te diviertes matando humanos, saqueando el pueblo y aterrorizando a la gente inocente, yo estoy aquí de guardia en Molino Tarren.$b$b¿Quizás tengas piedad de un viejo Guardia de la Muerte como yo? Verás, estoy coleccionando cráneos humanos. Y estarás luchando contra humanos por bastante tiempo. En el transcurso de tu larga batalla, tráeme 30 cráneos y te recompensaré.",
    ["O"] = "Guardia de la Muerte Samsa de Molino Tarren quiere 30 Cráneos Humanos de Trabalomas.",
    ["T"] = "Souvenirs de Muerte",
  },
  [547] = {
    ["D"] = "El Alto Ejecutor Darthalia me envió a vigilar Dun Garok desde la distancia. Había establecido un campamento justo fuera de la vista. Pero cuando llegó la mañana y tuve que hervir agua, salí a recoger leña.$B$BCuando regresé al campamento, descubrí que mi equipo había desaparecido.$B$BAparentemente, algunos exploradores enanos de Dun Garok pasaron por allí y se lo llevaron. ¡Necesito mi espada! Si puedes recuperarla por mí, te estaré muy agradecido. Hay tantos enanos allí... será bastante difícil encontrar al pequeño ladrón sucio que la robó.",
    ["O"] = "Recupera la espada del Guardia de la Muerte Humbert de Dun Garok y devuélvesela a él en Molino Tarren.",
    ["T"] = "La Espada de Humbert",
  },
  [548] = "_",
  [549] = {
    ["D"] = "Por la autoridad de Lady Sylvanas, todos los miembros de la organización conocida como La Hermandad son buscados muertos. Aquellos que sean llevados bajo custodia o tomados prisioneros serán ejecutados sumariamente. Actualmente se sabe que se están reuniendo en las ruinas del Castillo de Durnholde, al sureste de Molino Tarren.$b$bSe ofrece una generosa recompensa a todos los que traigan pruebas de sus hazañas a la Alta Ejecutora Darthalia.",
    ["O"] = "Mata 10 Pícaros de la Hermandad y 10 Vigías de la Hermandad. Regresa con el Alto Ejecutor Darthalia en Molino Tarren para recibir tu recompensa.",
    ["T"] = "SE BUSCA: Personal del Sindicato",
  },
  [550] = {
    ["D"] = "Porque luchaste con tanto valor y perseverancia en la Batalla de Trabalomas, he escrito esta recomendación, elogiando tu heroísmo en combate para que el alto mando lo reconozca.$b$bLleva esta recomendación sellada a Varimathras en Entrañas. Ve con orgullo, $c.",
    ["O"] = "Lleva la Comendación Sellada de Darthalia a Varimathras en Entrañas.",
    ["T"] = "Batalla de Trabalomas",
  },
  [551] = {
    ["D"] = "Este pergamino está encantado con un hechizo, lo que lo hace indescifrable. Pero una palabra en la parte superior del papel se puede leer:$B$B$B     Nagaz$B$B",
    ["O"] = "Lleva el Pergamino Encantado al Maestro de Historia Dibbs en Costasur.",
    ["T"] = "El Pergamino Encantado",
  },
  [552] = {
    ["D"] = "El Maestro Helcular estaba casi completando su transformación en un Lich cuando una turba humana descubrió su guarida ritual en las colinas y lo mató brutalmente. Para empeorar las cosas, enterraron su cadáver en el cementerio de Costasur.$b$bTal falta de respeto hacia el gran mago es inaceptable. Como su aprendiz, me aseguraré de que las lluvias de la venganza caigan sobre el pueblo de Costasur.$b$bNecesitaremos primero la Vara de Helcular. Los yetis se apoderaron de su guarida ritual. Sin duda, una de las bestias la tiene.",
    ["O"] = "Recupera la Vara de Helcular del Yeti y devuélvesela al Novicio Thaivand en Molino Tarren.",
    ["T"] = "La Venganza de Helcular",
  },
  [553] = {
    ["D"] = "Aquí, toma la Vara de Helcular. La necesitarás.$b$bHelcular preparó 3 piras ceremoniales como parte de su hechizo más poderoso. La Llama de Azel y la Llama de Veraz están dentro de las Cavernas de las Colinas. La Llama de Uzel está en una cueva sobre las cavernas, más al norte en las montañas.$b$bLa Vara de Helcular debe ser cargada de cada una de las tres llamas. Una vez que el ritual esté completo, clava la vara en la tumba de Helcular en Costasur, el pueblo humano fuertemente vigilado al sur. ¡La venganza será nuestra!",
    ["O"] = "Carga la Vara de Helcular con los poderes de la Llama de Azel, la Llama de Veraz y la Llama de Uzel.$b$bClava la vara cargada en la tumba de Helcular en Costasur.",
    ["T"] = "La Venganza de Helcular",
  },
  [554] = {
    ["D"] = "El Prospector Stormpike es un maestro en descifrar y decodificar. Si llevas el pergamino que encontraste a él, entonces con suerte podrá darle sentido. Stormpike está actualmente estudiando en la sede del Prospector en Forjaz.$B$BY cuando lo veas, dile que hablaste conmigo, le he estado enviando bastante negocio últimamente.",
    ["O"] = "Lleva el Pergamino Encantado al Prospector Pico Tormenta en Forjaz.",
    ["T"] = "Descifrado de Pico Tormenta",
  },
  [555] = {
    ["D"] = "Si hay una cosa que aprenderás sobre Costasur es que tenemos una de las mejores cocinas al norte de Ventormenta!$b$bToma mi receta secreta de Sopa de Tortuga, por ejemplo. He conocido a gente que viaja desde lugares tan lejanos como Villa Oscura solo para disfrutar de un tazón. Hablando de eso, no he podido preparar ninguna últimamente. Solía ir más allá de Dalaran hasta el Lago Lordamere para cazar Tortugas Rápidas, pero ahora es demasiado peligroso. Si me traes un poco de Carne de Tortuga de las Tortugas Rápidas del norte y Especias Relajantes, prepararé un poco para ti.",
    ["O"] = "Lleva 10 trozos de Carne de Tortuga y algunas Especias Relajantes al Chef Jessen en Costasur.",
    ["T"] = "Sopa de Tortuga Calmante",
  },
  [556] = {
    ["D"] = "Un talento notable de los magos de Dalaran es su familiaridad con los elementales: criaturas poderosas de otras dimensiones. Los magos usan fichas mágicas como medio para controlar a estos elementales. El Magus Voidglare quisiera estudiar algunas de estas fichas.$B$BEntra en los territorios de los magos de Dalaran, al oeste de Trabalomas, y recoge las fichas.$B$BPodrías encontrar las fichas en cualquier ciudadano de Dalaran, pero sugiero cazar en el campo de internamiento. Es más seguro que Dalaran misma.",
    ["O"] = "Lleva 10 Amuletos de Piedra Gastados a Guardián Bel\'varil en Molino Tarren.",
    ["T"] = "Fichas de Piedra",
  },
  [557] = {
    ["D"] = "El Magus estará ocupado estudiando esos tokens de piedra que recolectaste, pero sé que eventualmente querrá ejemplos de su contraparte: brazales de vinculación. Estos se colocan alrededor de las muñecas de los elementales reales y los vinculan al servicio del mago.$B$BCaza elementales en las ruinas de Dalaran y recolecta sus Brazales de Vinculación de Tierra, y el Magus estará satisfecho.",
    ["O"] = "Lleva 4 Brazales de Atadura de Tierra a Guardián Bel\'varil en Molino Tarren.",
    ["T"] = "Brazales de Atadura",
  },
  [558] = {
    ["D"] = "Dicen que Lady Jaina Valiente en Theramore es una de las mayores heroínas que la Alianza ha tenido. ¡Cuando crezca, quiero ser un héroe de la Alianza también!$B$B¿Conoces a Jaina, $N? ¿Podrías hacerme un favor, por favor? ¿Podrías conseguirme su autógrafo? No sé cómo preguntarle, y probablemente estaría demasiado ocupada para alguien como yo... ¡pero tú! ¡Eres un aventurero como ella! ¡Apuesto a que te daría su autógrafo fácilmente!$B$B¿Por favor?",
    ["O"] = "Viaja a Theramore para conseguir el autógrafo de Lady Jaina Valiente para tu pupilo.",
    ["T"] = "El Autógrafo de Jaina",
  },
  [559] = {
    ["D"] = "¿Lo creerías, $n? Redpath no estaba satisfecho con todos los múrlocs que matamos, oh, no seas modesto, tuviste algo que ver. Ahora quiere que traiga pruebas de los muertos, cortando cabezas frías y viscosas de múrlocs para presentarlas para su aprobación.",
    ["O"] = "Recolecta 10 Cabezas de Murloc de los murlocs en la Playa Occidental para el Teniente Farren Orinelle en Costasur.",
    ["T"] = "La Prueba de Farren",
  },
  [560] = {
    ["D"] = "Sabes, $n, realmente preferiría no tomar estas cabezas... Ya que tú las tienes, ¿te importaría entregárselas a Redpath por mí?",
    ["O"] = "Entrega el saco de cabezas de múrlocs al Mariscal Redpath en Costasur.",
    ["T"] = "La Prueba de Farren",
  },
  [561] = {
    ["D"] = "Por favor, informa al Teniente Orinelle que me gustaría que reenfocara sus esfuerzos en los naga que infestan la Costa Oriental. Cuando tenga resultados, dile que me informe en persona.",
    ["O"] = "Habla con el Teniente Farren Orinelle en Costasur.",
    ["T"] = "La Prueba de Farren",
  },
  [562] = {
    ["D"] = "Mi antiguo mentor en la guardia de la ciudad de Ventormenta me envió un mensaje de que el Alto Mando de la Alianza está cada vez más preocupado por la amenaza que representan los naga. Por lo que ha escuchado, hay buenas posibilidades de promoción, o al menos reasignación, para cualquiera que demuestre ser valioso en este sentido.$b$bSolo hay un pequeño problema... No puedo manejar a los naga, $n. Oh, claro, podría romper una pelea de borrachos en un bar, o llevarme a un mendigo beligerante, pero los naga son demasiado para mí. ¡Tienes que ayudarme, $n!",
    ["O"] = "Mata 10 Costero Dagapinna y 10 Sirenas Dagapinna para el Teniente Farren Orinelle en Costasur.",
    ["T"] = "¡A Ventormenta!",
  },
  [563] = {
    ["D"] = "No debería ser un problema que mi informe sea visto. El nuevo capitán de la guardia, el Mayor Samuelson, fue mi mentor cuando estaba estacionado en Ventormenta. Debería darme más que suficiente excusa para que me transfieran de vuelta a la ciudad. Necesitaré que lleves mi informe a él, sin embargo... no querría que el viejo Redpath se entere.",
    ["O"] = "Entrega el informe de Farren al Mayor Samuelson en el Castillo de Ventormenta.",
    ["T"] = "Reasignación",
  },
  [564] = {
    ["D"] = "No hay nada que me moleste más que perder estos hermosos caballos. Y créeme, hemos perdido bastantes entre esos molestos no-muertos, la pandilla del Syndicate y todos los demás sinvergüenzas que merodean por aquí.$B$B¡Pero juro por la tumba de mi abuelo que no hay mayor amenaza para nuestros caballos que esos leones de montaña en Alterac! ¡Siempre bajan de las colinas y me complican la vida!$B$B¿Te crees un héroe? ¡Entonces haz algo al respecto!",
    ["O"] = "Darren Malvew de Costasur quiere que mates a 8 Leones de Montaña y 10 Leones de Montaña Descomunales.",
    ["T"] = "Amenaza Costosa",
  },
  [565] = {
    ["D"] = "Yo, el gran Bartolo, ¡haré para ti la famosa capa de piel de yeti, conocida en todo el mundo y renombrada en la Alianza!$b$b¡Viaja por el planeta y encuentra una capa mejor, te reto! Oh, espera, ¿qué es eso, pequeño $r? \"¡Bartolo, no hay una capa más fina!\" Sí, ¡sí! Eso lo he escuchado antes.$b$bBartolo necesita una Bobina de Lana, una Capa de Colina hecha por un peletero experto, Hilo Fino y, por supuesto, ¡Piel de Yeti! Tráeme estos artículos y serás un cliente feliz.",
    ["O"] = "Lleva a Bartolo Ginsetti de Costasur una Madeja de Lana, una Capa de Montañés, Hilo Fino y 10 mechones de Piel de Yeti.",
    ["T"] = "La Capa de Piel de Yeti de Bartolo",
  },
  [566] = {
    ["D"] = "Por orden de la Alta Ejecutora Darthalia, el humano conocido como Barón Vardus es buscado por crímenes contra los No-Muertos. Vardus es conocido por ser un miembro de alto rango de la organización conocida como La Hermandad.$b$bFue visto por última vez en las Tierras Altas en las Montañas de Alterac, al norte de Molino Tarren.$b$bSe le busca muerto. Trae pruebas de su muerte a la Alta Ejecutora Darthalia para reclamar la recompensa.",
    ["O"] = "Mata al Barón Vardus y entrega su cabeza al Alto Ejecutor Darthalia en Molino Tarren.",
    ["T"] = "SE BUSCA: Barón Vardus",
  },
  [567] = {
    ["D"] = "¡Peligroso!$b$bLos siguientes humanos de Trabalomas han sido considerados peligrosos y están marcados para recompensa por la Alta Ejecutora Darthalia:$b$bEl escribano Horrace Whitesteed. Buscado por el asesinato del Guardia de la Muerte Toma.$b$bEl ciudadano Wilkes. Buscado por el asesinato del Boticario Eli.$b$bEl minero Hackett. Buscado por el asesinato del Acechador de la Muerte Fry.$b$bEl granjero Kalaba. Buscado por la emboscada de suministros de Entrañas.$b$bTodos estos enemigos están escondidos y serán difíciles de encontrar. Se otorgará una recompensa al notificar su muerte.",
    ["O"] = "El Alto Ejecutor Darthalia de Molino Tarren ofrece una recompensa por el Oficial Horrace Blancocorcel, el Ciudadano Wilkes, el Minero Hackett y el Granjero Kalaba.",
    ["T"] = "¡Peligroso!",
  },
  [568] = {
    ["D"] = "¡Throm\'ka, $c!$b$bTu llegada a Grom\'gol es oportuna, de hecho. Como comandante del campamento base del Jefe de Guerra aquí en la jungla, estoy obligado por el honor a garantizar la seguridad de todos los miembros de la Horda. Nuestra misión de proporcionar una cadena de suministro segura a Rocal está siendo obstaculizada por algunos de los habitantes locales.$b$bTe pongo a cargo de reducir la población de raptores fuera de Grom\'gol. Una vez que hayas hecho un progreso significativo, repórtame para reasignarte.",
    ["O"] = "El Comandante Aggro\'gosh del campamento base de Grom\'gol quiere que mates a 15 Raptores Colazote.",
    ["T"] = "La Defensa de Grom\'gol",
  },
  [569] = {
    ["D"] = "Cuando llegamos por primera vez a esta jungla no teníamos la intención de hacer la guerra a las tribus locales. Nuestra misión de abastecer a Rocal es de la más alta prioridad. Pero han surgido complicaciones. Una banda local de ogros ha ocupado las Ruinas de Mizjah al sureste de Grom\'gol. A pesar de las advertencias, continúan emboscando nuestras caravanas de suministros.$B$BTe pongo a cargo de lidiar con estos ogros poco cooperativos.$B$BViaja a las ruinas e intenta expulsarlos del área. Al menos enviará un mensaje firme.",
    ["O"] = "El Comandante Aggro\'gosh del campamento base de Grom\'gol quiere que mates a 10 Brutos Ogros y 5 Médicos Brujos Ogros.",
    ["T"] = "La Defensa de Grom\'gol",
  },
  [570] = {
    ["D"] = "El poder de la magia de la jungla debe ser comprendido y luego aprovechado por la Horde. Mis rituales han demostrado ser exitosos hasta ahora. Puedo lanzar encantamientos muy poderosos. Para ti, valiente $c, haré un objeto especial.$B$BTráeme algunas garras de pantera Sombrarbusto junto con un colmillo de tigresa. No cualquier colmillo servirá. Debe estar en perfectas condiciones.$B$BLas bestias merodean por toda la jungla, pero encontrarás algunas cerca, justo al otro lado del río desde las Ruinas de Mizjah hacia el sureste.",
    ["O"] = "El Clarividente Mok\'thardin en el campamento base de Grom\'gol quiere 8 Garras Sombramatorral y un Colmillo de Tigresa Pristino.",
    ["T"] = "El Encantamiento de Mok\'thardin",
  },
  [571] = {
    ["D"] = "Lo que necesito de ti ahora será muy difícil de obtener.$B$BPara sostener todas estas plumas, garras y el colmillo prístino en su lugar, necesito un amarre mágico. Solo un tendón de gorila anciano funcionará.$B$BSolo podrás encontrar uno en un Gorila del Valle de la Niebla Anciano. Se sabe que forrajean al este de Bahía del Botín.$B$BEl tendón debe ser perfecto. Podría tomarte bastantes muertes para encontrar exactamente lo que necesito, pero valdrá la pena la caza.",
    ["O"] = "Lleva un Tendón de Gorila Añejo al Profeta Lejano Mok\'thardin en Grom\'gol.",
    ["T"] = "El Encantamiento de Mok\'thardin",
  },
  [572] = {
    ["D"] = "Los artículos que recolectaste de las bestias felinas del Valle son solo el primer requisito para un objeto encantado poderoso. Estos objetos de la jungla combinados con mi ritual chamánico te proporcionarán una herramienta muy útil para ayudar a la Horda y al Jefe de Guerra.$b$bPero ahora debes reunir para mí algunas Plumas de Acechador de la Jungla. Las plumas son tradicionalmente usadas por las bestias en sus brazaletes. Encontrarás una tribu de los raptores directamente al sur de Grom\'gol, no muy lejos de la gran arena.",
    ["O"] = "Lleva 10 Plumas de Acechador de la Jungla al Profeta Mok\'thardin en el Campamento Grom\'gol.",
    ["T"] = "El Encantamiento de Mok\'thardin",
  },
  [573] = {
    ["D"] = "Hay un último reactivo necesario para completar el encantamiento.$B$BEn el sur, hacia la costa occidental, muy por encima de los acantilados, se encuentra un Manantial Sagrado. Este manantial es objeto de gran conflicto entre eruditos religiosos y místicos. Cada raza en Azeroth afirma que las aguas del manantial fueron hechas divinas para su pueblo.$B$BMi última petición para ti: tráeme Agua del Manantial Sagrado. Pero ten cuidado. Una expedición de exploradores naga ha avanzado sobre el manantial. También tendrás que lidiar con ellos.",
    ["O"] = "El Clarividente Mok\'thardin de Grom\'gol necesita Agua de Manantial Sagrado. También quiere que mates a 10 Exploradores Naga.",
    ["T"] = "El Encantamiento de Mok\'thardin",
  },
  [574] = {
    ["D"] = "Ahora que muchos de sus tropas regulares han sido reducidas, debes enfrentarte a sus fuerzas altamente entrenadas. Cuando no están en el desierto masacrando a nuestros soldados rebeldes, se encuentran en lo profundo de la parte trasera del Complejo de Kurzen, en una cueva que llaman el Almacén.$B$BConozco personalmente a muchas de las tropas de Kurzen, y sé que son hábiles, leales y crueles. No es una tarea fácil la que te pongo, $N.$B$BDespués de derrotarlos, informa al Teniente Doren, mi oficial al mando. Él querrá tu informe de primera mano.",
    ["O"] = "Mata 10 Comandos de Kurzen y 6 Rezagadores de Kurzen.$B$BInforma al Teniente Doren en el Campamento Rebelde.",
    ["T"] = "Fuerzas Especiales",
  },
  [575] = {
    ["D"] = "\"El mejor artesano de cuero de los Mares del Sur y de los mundos antiguos y nuevos.\" Así me llaman. ¡A mí! ¡Drizzlik! El mejor artesano de cuero... Ejem. Bueno.$B$BMis productos de cuero son conocidos en todas partes como los más exquisitos, ¡la elección de un conocedor!$B$BAcabo de recibir un pedido del Director Riddlevox de la Unión de Manitas para una docena de sus botas favoritas de la línea Excelsior. Nuestro producto más vendido, de hecho.$B$BPara comenzar con la forma básica, necesitaré pieles de crocolisco de los cocodrilos a lo largo del río en el norte de Tuercespina.",
    ["O"] = "Lleva 2 Pieles Grandes de Crocolisco de Río a Drizzlik en Bahía del Botín.",
    ["T"] = "Oferta y Demanda",
  },
  [576] = {
    ["D"] = "¡Alto ahí! ¿Quién va?$b$b¡Arrgh, soy inútil sin mi ojo mágico especial! Ya es bastante malo haber perdido mis ojos en batalla en primer lugar. Pero por un golpe de suerte, atracamos en Menethil y un mago viajero encantó un ojo de vidrio especial que me permitió ver de nuevo.$b$bAnoche me asaltaron unos Bucaneros Velasangre cuando salía. ¡Se llevaron mi ojo! Parece que esos tipos han encontrado su camino a Bahía del Botín.$b$b¿Ayudarías a un viejo lobo de mar a recuperar mi ojo?",
    ["O"] = "Dizzy el Tuerto en Bahía del Botín quiere que recuperes su ojo de los Bucaneros Velasangre.",
    ["T"] = "Mantén un Ojo Abierto",
  },
  [577] = {
    ["D"] = "Bueno, tengo la forma básica de las botas resuelta, y están saliendo bien, pero las pieles que trajiste no serán suficientes para las capas internas.$B$B\"¿Qué vamos a hacer, Drizzlik?\" Sin duda la pregunta en tus labios, y bueno, ¿no es afortunado que estés hablando con Drizzlik, porque él te va a decir lo que vamos a hacer!$B$BLas capas internas nunca se verán realmente, así que solo conseguiremos algunas de las pieles más gruesas y baratas de los cocodrilos alrededor del Lago Nazferiti para rellenar las botas.",
    ["O"] = "Lleva 5 Pieles de Crocolisco Quijatroz a Drizzlik en Bahía del Botín.",
    ["T"] = "Se Requiere Algo de Ensamblaje",
  },
  [578] = {
    ["D"] = "Si realmente hay un mago goblin y elementales de agua acechando la isla, ¡podría significar que la Piedra de las Mareas puede ser mía!$b$bQuiero que localices esta isla encantada... averigua qué está pasando. Oh, y si quizás quieres saber más sobre la Piedra de las Mareas, lleva este escrito a la Biblioteca de Ventormenta, deberían poder encontrarte una copia del libro en el que encontré la leyenda por primera vez.$b$b¿Podría ser que Gazban realmente descubrió la Piedra...?",
    ["O"] = "Encuentra la isla encantada para el Barón Revilgaz en Bahía del Botín.",
    ["T"] = "La Piedra de las Mareas",
  },
  [579] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Biblioteca de Ventormenta",
  },
  [580] = {
    ["D"] = "Estábamos ajustando el aparejo principal con fuerza frente a los Acantilados del Mirador, con las Tierras del Interior a la distancia. Pensamos que los mares nos tragarían enteros.$b$b¡El viejo Slim sabe todo sobre el valor líquido! El capitán tenía una caja de Vino Pupellyverbos que habíamos confiscado a un corsario en ruta a Ventormenta.$b$bAsí que cuando esa tormenta frente a las Tierras del Interior golpeó, me metí en el grog. Una gran ola golpeó y arrojó esas botellas por la borda. ¡El capitán me dará un gusto del gato si no me ayudas a recuperarlas!",
    ["O"] = "Whiskey Slim en Bahía del Botín quiere que le lleves las botellas de Vino Pupellyverbos que perdió.",
    ["T"] = "El Grog Perdido de Whiskey Slim",
  },
  [581] = {
    ["D"] = "Una maldición hay. Una maldición sobre el hijo menor de mi jefe, Yenniku. Como es costumbre entre nuestra gente, fue entregado a los trolls Gurubashi de Zul\'Gurub, pero después de que mi tribu, la tribu Lanzanegra, se fuera con el orco Thrall, el pobre Yenniku se perdió para nosotros. Fui enviado desde la lejana Orgrimmar, de regreso a la Vega de Tuercespina, para buscar al hijo de mi jefe.$B$BY creo que la tribu Sangrapellejo lo tiene.$B$BSi quieres ayudarme, entonces caza a los Sangrapellejo. Puedes encontrarlos al norte. Recoge sus colmillos y regresa a mí.",
    ["O"] = "Lleva 9 Colmillos de Sangrapellejo a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza de Yenniku",
  },
  [582] = {
    ["D"] = "Debemos averiguar si Yenniku está muerto.$B$BMata a los Cazacabezas Sangrapellejo y toma de ellos sus Cabezas Reducidas. Debes recolectar muchas, muchas cabezas, y las inspeccionaré para asegurarme de que la cabeza de Yenniku no esté entre ellas.$B$BY si Yenniku fue asesinado por los Sangrapellejo, ¡que una maldición de 600 años caiga sobre el espíritu de su jefe, Gan\'zulah!$B$BVe, $N. Puedes encontrar muchos Cazacabezas en las ruinas de Zuuldaia y Zul\'Kunda, a lo largo de la costa hacia el norte.",
    ["O"] = "Lleva 20 Cabezas Reducidas a Nimboya en el Campamento Grom\'gol.",
    ["T"] = "Caza de Cabezas",
  },
  [583] = {
    ["D"] = "¡Bienvenido a Tuercespina!$b$bQuizás no estés al tanto de esto, pero ese enano de allí es el único e incomparable Hemet Nesingwary, renombrado héroe de guerra de la Alianza y maestro cazador de grandes bestias. No es muy dado a dar la bienvenida a extraños en su campamento, pero pareces haber visto bastante acción en tu día, $c.$b$bSiendo un veterano de muchas batallas, el Maestro Nesingwary tiene un punto débil por los compañeros héroes.$b$bVe y habla con él. Quizás pueda darte algunos consejos de caza.",
    ["O"] = "Habla con Hemet Nesingwary.",
    ["T"] = "Bienvenido a la Jungla",
  },
  [584] = {
    ["D"] = "Yenniku no estaba entre las cabezas, y le doy gracias al Gran Serpiente por eso. Pero no estamos más cerca de encontrarlo.$B$BDebemos saber la verdad, y me temo que solo el jefe de los Sangrapellejo y su brujo la conocen. Son enemigos de la tribu Lanzanegra y no hablarán libremente, ¡así que debemos sacarles la verdad de sus labios muertos!$B$BTráeme las cabezas de Gan\'zulah y Nezzliok el Temible y colócalas dentro de este caldero.$B$BEstán en lo profundo de Zul\'Kunda. Tráeme sus cabezas... luego las haremos hablar.",
    ["O"] = "Lleva la Cabeza de Gan\'zulah y la Cabeza de Nezzliok al Caldero Burbujeante en el Campamento Grom\'gol.",
    ["T"] = "Cabezas del Clan Sangrapellejo",
  },
  [585] = {
    ["D"] = "<La cabeza de Nezzliok habla.>$B$BEs cierto. Sé dónde fue llevado el hijo de los Lanza Negra. Me has vencido y has atado mi espíritu, pero no diré nada sin apaciguamiento.$B$BLos Sangrapellejos están en guerra con los Machacacráneos. Para ganar mi favor, roba a los Machacacráneos 3 de sus cráneos trofeo y colócalos aquí con nosotros, para que mi espíritu tenga esclavos en el Vacío. Un cráneo debe ser de su pila de trofeos en Balia\'mah, otro de Ziata\'jai y el último de Zul\'Mamwe.",
    ["O"] = "Lleva un Trofeo de Balia\'mah, un Trofeo de Ziata\'jai y un Trofeo de Zul\'Mamwe al Caldero Burbujeante en el Campamento Grom\'gol.",
    ["T"] = "Hablando con Nezzliok",
  },
  [586] = {
    ["D"] = "<La cabeza de Gan\'zulah habla.>$B$B¡Maldito seas! ¡Y malditos sean tus ancestros!$B$BSolo la sangre y la venganza pueden enfriar mi ira, así que si ese es tu deseo, entonces... haz esto:$B$BLánzate al corazón de los Machacacráneos al este. Mata al más feroz entre ellos, y si sobrevives... entonces arrójalos contra su jefe, Ana\'thek el Cruel. ¡Ja! ¡Tráeme su armadura destrozada si puedes!$B$B¡Que te arranque las extremidades y te deje pudrir para que te coman los carroñeros!",
    ["O"] = "Mata 8 Cazadores Machacacráneos, 6 Cazacabezas Machacacráneos y 4 Rabiosos Machacacráneos.$B$BLleva la Armadura Rota de Ana\'thek al Caldero Burbujeante en el Campamento Grom\'gol.",
    ["T"] = "Hablando con Gan\'zulah",
  },
  [587] = {
    ["D"] = "Ah, la vida en el mar. No voy a decir que sea la buena vida, pero es mi vida.$B$BCuando llego a puerto, me gusta encontrar una buena botella de grog y un poco de rapé de alta calidad. Malos hábitos, pero ¿qué es un pirata sin sus vicios, verdad?$B$BAsí que puedes imaginar lo desconsolado que estaba al descubrir que el herbolario aquí en la ciudad se ha quedado sin el rapé. Dice que su envío fue secuestrado por esos malditos Bucaneros Velasangre.$B$BApuesto a que un valiente $c como tú podría convencerlos de compartir los bienes, ¿eh?",
    ["O"] = "Deeg en Bahía del Botín quiere que le lleves 15 acciones de Rapé.",
    ["T"] = "A la Altura",
  },
  [588] = {
    ["D"] = "<Nezzliok habla.>$B$BHas apaciguado a mí y a mi jefe, así que te diré lo que buscas descubrir...$B$BEl joven Yenniku está perdido para su clan.$B$BFue tomado por el brujo Zanzil el Desterrado. Ahora Zanzil lo controla, cuerpo y alma, y solo la magia más potente podría liberarlo.$B$BUna magia que tú no posees, $c.$B$BTal vez tus aliados Lanza Negra puedan ofrecer una esperanza de salvación para Yenniku. Pero de eso, lo dudo.",
    ["O"] = "Habla con Kin\'weelay.",
    ["T"] = "El Destino de Yenniku",
  },
  [589] = {
    ["D"] = "Las leyendas hablan de una joya, escondida en lo profundo de la Vega de Tuercespina. La llamamos el Ojo de la Mente, y tiene grandes poderes. Si obtienes este Ojo, entonces, quizás, podamos usarlo para traer de vuelta a Yenniku perdido de la oscuridad.$B$BConsultaré con los espíritus de la jungla para encontrar el Ojo de la Mente. Mientras hago esto, entra en la Mina Vetacristal, al sureste de aquí, y recolecta Fragmentos Azules Pulsantes de los Basilisco Fauzahierro. Los fragmentos son raros, pero necesitaremos su magia para doblegar el Ojo de la Mente a nuestra voluntad.",
    ["O"] = "Lleva 3 Fragmentos Azules Pulsantes a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Los Cristales Cantarines",
  },
  [590] = {
    ["D"] = "¿Qué?$B$B¡¿Qué?!$B$B¿Quieres dinero? No te debo nada de dinero.$B$B¿Carta? ¿Qué carta? Oh, ¿quieres pelear entonces? Está bien, compañero... ¡hagámoslo! ¿Te debo dinero?",
    ["O"] = "Derrota a Calvin Montague en El Cementerio.",
    ["T"] = "Un Trato de Pícaro",
  },
  [591] = {
    ["D"] = "El Ojo de la Mente está en posesión de un mago ogro, al este de aquí y en lo profundo del montículo ogro Mosh\'Ogg. Su nombre es Mai\'Zoth. Lo conozco desde que los Lanzanegra habitaban en Tuercespina: es vicioso y su magia es fuerte.$B$BY si tiene el Ojo de la Mente, entonces su magia será aún más fuerte.$B$BDebes enfrentarte a Mai\'Zoth y arrebatarle el Ojo de la Mente. Es una tarea peligrosa, pero es la única esperanza que tenemos de salvar a Yenniku.",
    ["O"] = "Lleva el Ojo de la Mente a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Ojo de la Mente",
  },
  [592] = {
    ["D"] = "He reformado el Ojo de la Mente en una Gema de Alma. Su propósito es doble: debes usarla para aturdir a Yenniku, y una vez aturdido, colocarla sobre su cabeza para atrapar su alma.$B$BLuego, lleva la gema a Nimboya.$B$BYenniku vive con Zanzil el Desterrado, en las Ruinas de Aboraz, a lo largo de la Costa de Cristal en el lejano Cabo de Tuercespina.",
    ["O"] = "Lleva la Gema de Alma Llena a Nimboya.",
    ["T"] = "Salvando a Yenniku",
  },
  [593] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Llenando la Gema del Alma",
  },
  [594] = {
    ["D"] = "La nota dice, con una escritura bastante buena:$B$BA cualquiera que pueda ayudarme-$B$BPor favor, mi necesidad de ayuda es desesperada. Un gran Crines de Cielo me tiene cautivo en una isla en los Mares del Sur.$B$BLas estrellas por la noche me son ajenas y están oscurecidas por los árboles de la jungla, pero en los días más claros veo Costa Salvaje al noroeste, y lo que parecen ser grandes barcos de vela al suroeste.$B$BTe lo ruego, mi salvador potencial, por favor ayúdame.",
    ["O"] = "Encuentra al autor del mensaje en una botella.",
    ["T"] = "Mensaje en una Botella",
  },
  [595] = {
    ["D"] = "Pareces ansioso por trabajar, amigo. No estoy seguro de por qué algún $r $c amante de la tierra está ofreciendo ayuda a los Capitanes Negros, pero no voy a rechazarte.$b$bNuestro barco acaba de atracar aquí en Bahía del Botín y se dice que nuestros rivales, esos malditos y sucios Bucaneros Velasangre, han sido vistos por todo el sur de la Vega de Tuercespina.$b$b¡Todos los muchachos están demasiado ocupados bebiendo y buscando mujeres! Lo que necesito es un explorador.$b$bRevisa la cala justo al norte de Bahía del Botín a lo largo de la Costa Salvaje y mira qué encuentras.",
    ["O"] = "El Primer Oficial Crazz quiere que investigues la caleta al norte de Bahía del Botín.",
    ["T"] = "Los Bucaneros Velasangre",
  },
  [596] = {
    ["D"] = "Fui enviado a Grom\'gol para ayudar a Nimboya en su misión, pero... yo también tengo razones para estar aquí.$B$BLa tribu Sangrapellejo es enemiga de nuestra tribu. Recolectar trofeos de nuestros enemigos nos da poder sobre ellos. Puedes ayudar a nuestra tribu cazando a nuestros enemigos.$B$BHabitan al norte. Mata a los trolls Sangrapellejo y toma de ellos sus collares de hueso ensangrentado. Recolecta muchos, y tu recompensa será grande.",
    ["O"] = "Lleva 25 Collares de Hueso Ensangrentado a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Collares de Hueso Sangriento",
  },
  [597] = {
    ["D"] = "El mapa muestra Bahía del Botín con una \"X\" roja dibujada precisamente en el lugar donde estás parado.$B$BEl texto garabateado dice:$B$B\"Vosotros, hombres, debéis asegurar un campamento en esta ubicación y manteneros en silencio. Esperad la señal del puesto de mando del sur antes de atacar. Si alguien se acerca, matadlo.\"",
    ["O"] = "Informa de tus hallazgos al Primer Oficial Crazz en Bahía del Botín.",
    ["T"] = "Los Bucaneros Velasangre",
  },
  [598] = {
    ["D"] = "Ahora que has recolectado trofeos Sangrapellejo, tengo otra tarea para ti. La tribu de los Rajacráneo vive al sureste, y durante años hemos estado en guerra con ellos. Aunque los Lanzanegra ya no habitan en Tuercespina, estamos ligados a nuestro conflicto con los Rajacráneo.$B$BAsí que, cázalos. Recolecta sus Collares de Hueso Partido y regresa a mí. Ayúdanos en nuestra guerra y ganarás la amistad de los Lanzanegra.",
    ["O"] = "Lleva 25 Collares de Hueso Partido a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "Collar de Hueso Partido",
  },
  [599] = {
    ["D"] = "¡Me tiemblan las tablas! ¿Así que los Bucaneros Velasangre están planeando algún tipo de ataque contra Bahía del Botín? Vaya, el descaro de esos granujas. ¡Bahía del Botín siempre será dirigida por los Corsarios Negros!$b$bEsto es más grande de lo que pensaba. ¡Informa de lo que has aprendido al Maestre de Flota Seahorn de inmediato! El viejo lobo de mar está por ahí en algún lugar...",
    ["O"] = "Informa del ataque planeado de los Velasangre al Maestre de Flota Cuernomarino.",
    ["T"] = "Los Bucaneros Velasangre",
  },
  [600] = {
    ["D"] = "La Venture Co. tiene una cadena de operaciones a través de Tuercespina que impide que goblins trabajadores como yo ganemos oro honesto. ¡Por favor, debes ayudarme!$B$BLa Venture Co. está minando cerca de la Mina Vetaescarcha al norte. No pueden entrar en la mina debido a todos los basilisco, pero aún pueden desenterrar Cristales Cantarinos de las colinas circundantes.$B$B¡Toma sus cristales y muéstrales que no tienen el control de la jungla!$B$BY... um... tráeme esos cristales como prueba.",
    ["O"] = "Lleva 10 Cristales Azules Cantarinos a Crank Fizzlebub en Bahía del Botín.",
    ["T"] = "Minería de Venture Co.",
  },
  [601] = {
    ["D"] = "Los elementales de agua fueron sin duda convocados por Gazban, el mago goblin del que se rumorea que descubrió la Piedra de las Mareas. Como sabrás, la Piedra de las Mareas maldice a su portador, causando que desaparezca y reaparezca...$B$BTal vez podamos encontrar una manera de contrarrestar la maldición. Podemos hacer que un mago de Dalaran analice el residuo mágico de una de las brazaletes de los elementales de agua. Necesito que regreses a la isla y recuperes algunos especímenes.",
    ["O"] = "Consigue 6 Brazales de Elemental de Agua para el Barón Revilgaz en Bahía del Botín.",
    ["T"] = "Elementales de Agua",
  },
  [602] = {
    ["D"] = "Por lo que he oído, los magos de Dalaran están ocupados con la reconstrucción de su ciudad destruida, estoy seguro de que puedes encontrar a uno de ellos que tenga algo de tiempo para examinar las brazales. Si hay algo que sé sobre los magos, es que no pueden resistirse a un acertijo mágico.$b$bLleva la bolsa de brazales de elemental de agua y ve si pueden arrojar algo de luz sobre la maldición de la Piedra. Busca en particular al Archimago Ansirem Runeweaver. He tratado con él antes, y su conocimiento es vasto.",
    ["O"] = "Lleva las Brazales de Elemental de Agua al Archimago Ansirem Runeweaver en Dalaran.",
    ["T"] = "Análisis Mágico",
  },
  [603] = {
    ["D"] = "El mago que posee la Piedra está amplificando su poder a través de un punto focal... un altar de algún tipo. Deberías poder interrumpirlo sobrecargándolo.$B$BResulta que conozco justo lo que necesitas: una daga antigua, otra reliquia troll. Solo hay un... pequeño problema.$B$BVerás, encontré la daga mientras investigaba en la Vega de Tuercespina, pero se la di a mi testaruda hija, Catelyn, quien se escapó a Bahía del Botín para convertirse en pirata.",
    ["O"] = "Habla con Catelyn en Bahía del Botín.",
    ["T"] = "La Llave de Ansirem",
  },
  [604] = {
    ["D"] = "Crazz habla muy bien de tu dedicación a los Capitanes Negros. Te voy a confiar una misión de extrema importancia para la flota.$b$bInfiltrate en el llamado puesto de mando sur que los Bucaneros Velasangre han establecido en la Costa Salvaje. Asalta el campamento. Tráeme sus cartas junto con sus órdenes escritas. Veremos exactamente qué están tramando esos escoria.",
    ["O"] = "El Maestre de Flota Seahorn en Bahía del Botín quiere que mates a 10 Espadachines Velasangre y recuperes las Cartas Velasangre y las Órdenes Velasangre.",
    ["T"] = "Los Bucaneros Velasangre",
  },
  [605] = {
    ["D"] = "Los Cristales Cantarines son únicos en Tuercespina y son muy valiosos para ciertas partes. Puedo mover esos cristales, ¡pero la maldita Venture Co. hace que sea difícil para un emprendedor honesto como yo reunir alguno!$B$BMe gustaría contratarte.$B$BLos basiliscos en Tuercespina se comen el cristal. Esto les da su piel endurecida, y a veces se puede obtener cristal de buena calidad de ella.$B$BPuedes obtenerlo de cualquier basilisco, pero los menos desagradables están a lo largo de las costas al sur de Zul\'Kunda, hacia el norte.",
    ["O"] = "Lleva 10 Fragmentos de Cristal Cantarinos a Crank Fizzlebub.",
    ["T"] = "Fragmentos Azules Cantarines",
  },
  [606] = {
    ["D"] = "Si hay una cosa que no soporto, ¡es la gente que no paga sus deudas de juego! Y hay mucha gente que me debe. ¿Crees que puedes ayudarme a cobrar?$B$BPrimero, está \"Tembloroso\" Phillipe en los muelles. ¡Me debe mucho después del juego de la semana pasada y quiero lo que me corresponde! Pero para conseguirlo, tendremos que asustarlo.$B$BSal de la ciudad y caza Gorilas del Valle Brumoso Ancianos. Encuentra una buena selección de menudencias, luego llévaselas a Tembloroso. No dejes que sepa que son de un animal; ¡deja que piense lo peor!",
    ["O"] = "Lleva 5 Menudillos de Vallebruma a \"Tembloroso\" Phillipe en Bahía del Botín.",
    ["T"] = "Asustando a Shaky",
  },
  [607] = {
    ["D"] = "Aquí, toma esta bolsa y entrégasela a Lobo Marino. ¡Y dile que tenía la intención de pagarle! ¡Lo juro!!$B$B¡Solo me emborraché un poco y perdí el rumbo, y... ya sabes cómo son las cosas! Él entenderá, no tienes que ponerte violento.",
    ["O"] = "Lleva el Pago de Shaky a \"Lobo de Mar\" MacKinley.",
    ["T"] = "Regreso con MacKinley",
  },
  [608] = {
    ["D"] = "¡Maldita sea la audacia de Firallon! No podría navegar un bote a través del Lago Cristalino, y mucho menos liderar una flota entera contra nosotros.$B$BLos Bucaneros Velasangre pagarán por su descuido. Ahora que conocemos su plan de ataque, podemos contraatacar antes de que sepan qué los golpeó.$B$B¿Pero por qué enviar a toda la flota a hacer el trabajo de un valiente grupo de aventureros? $n, toma a algunos de tus compañeros más hábiles y ve al punto de desembarco de Firallon y asesina a los líderes de los Velasangre. Su flota se hundirá poco después....",
    ["O"] = "El Maestre de Flota Seahorn en Bahía del Botín quiere que mates al Capitán Aguasmansas, al Capitán Arrastraquilla y al Maestre de Flota Firallon.",
    ["T"] = "Los Bucaneros Velasangre",
  },
  [609] = {
    ["D"] = "Mi lista se está acortando, pero todavía hay personas que me deben.$B$BA continuación, tenemos a Maury \"Pie de Palo\" Wilkins, Jon-Jon el Cuervo y Chucky \"Diez Dedos\".$B$B¡Estos malditos perros tienen deudas pendientes desde hace meses y pensé que habían huido del pueblo para evitar pagar! Más tarde, escuché que están malditos y hechizados y ahora deambulan por las ruinas de la jungla. ¡Pero no me importa cuál sea su destino, quiero lo que es mío!$B$BEstán en las Ruinas de Aboraz y las Ruinas de Jubuwal, al noreste de aquí. Encuéntralos y cobra.",
    ["O"] = "Lleva el Pie de Palo de Maury, el Catalejo Dorado de Jon-Jon y el Anillo Enorme de Chucky a \"Lobo de Mar\" MacKinley en Bahía del Botín.",
    ["T"] = "Deudas de Vudú",
  },
  [610] = {
    ["D"] = "¿Mi PADRE te envió? Hubiera pensado que ese viejo cascarrabias ya se habría rendido conmigo.$B$BNo fue realmente su culpa que me escapara, simplemente no debería haber esperado que estuviera dispuesto a vivir una vida aburrida, leyendo libros polvorientos y asociándome con magos canosos, tiesos y estirados.$B$BSi necesitas mi daga, tendrás que recuperarla de \"Pretty Boy\" Duncan, uno de los Bucaneros Velasangre. Pon algo entre sus hombros y estoy seguro de que la soltará.",
    ["O"] = "Consigue la daga de Catelyn de \"Guaperas\" Duncan.",
    ["T"] = "\"Chico Lindo\" Duncan",
  },
  [611] = {
    ["D"] = "¿Así que buscas un altar de algún tipo? Bueno, no soy un experto en ruinas troll como mi padre, pero he oído hablar de una ciudad troll hundida cerca de esa isla que estás buscando. Tal vez deberías intentar allí.$b$bRecuperaste mi cuchillo, así que supongo que puedes tomarlo prestado... ¡pero lo quiero de vuelta cuando termines!",
    ["O"] = "Destruye el Altar de las Mareas con la Espada de Catelyn, mata a Gazban y lleva la Piedra de las Mareas y la Espada de Catelyn de vuelta al Barón Revilgaz.",
    ["T"] = "La Maldición de las Mareas",
  },
  [613] = {
    ["D"] = "¡Maldición! Ese bribón Maury puso un candado en su pie, ¡y no puedo abrirlo! Su botín está dentro del pie, ¡y eso es lo que me debe!$B$BNecesitamos encontrar la llave para abrir el candado. Maury solía apostar con los ogros Mosh\'Ogg, en su montículo al noreste. Y me dijo, cuando todavía estaba vivo, que la última vez que fue al montículo lo echaron después de ganar demasiado. Quizás los ogros allí tienen su llave.$B$BPero si la tienen, podrías tener que matar a un montón de ellos para encontrar la correcta...",
    ["O"] = "Lleva la Llave de Maury a \"Lobo de Mar\" MacKinley en Bahía del Botín.",
    ["T"] = "Rompiendo el Pie de Maury",
  },
  [614] = {
    ["D"] = "Solía navegar un barco desde aquí hasta Ratchet, en Kalimdor. Era un capitán exitoso con una reputación impecable.$B$BHasta que...$B$B¡Esos malditos gigantes marinos me arruinaron! Destrozaron mi barco, mataron a mi tripulación y me pusieron en un bote salvavidas hacia Bahía del Botín. Y uno de los gigantes, Gorlash, robó mi Cofre del Capitán. Dijo que lo quería para un tabaquera. ¡Qué descaro!$B$BHe oído que Gorlash vaga por la costa al este de aquí. ¡Encuéntralo y recupera mi cofre! Si lo haces, te habrás ganado la gratitud de un capitán.",
    ["O"] = "Lleva el Cofre de Smotts a Hecklebury Smotts en Bahía del Botín.",
    ["T"] = "El Cofre del Capitán",
  },
  [615] = {
    ["D"] = "Después de perder mi primer barco ante esos gigantes, compré otro. Lo llamé La Venganza de Smotts, lo llené de suministros y tripulación, y partí para encontrar a los villanos. Los encontré, pero... me vencieron. Destrozaron La Venganza de Smotts, mataron a mi segunda tripulación y me pusieron en otro bote salvavidas.$B$BEsta vez, otro de los gigantes, Negolash, robó mi alfanje.$B$B¡Enfrenta a Negolash y tráeme mi alfanje!$B$BHabla con Sprogger. Él era mi cocinero en La Venganza de Smotts y sobrevivió al último ataque. Él puede ayudarte a encontrar al gigante.",
    ["O"] = "Habla con Sprogger.",
    ["T"] = "El Alfanje del Capitán",
  },
  [616] = {
    ["D"] = "El Barón Revilgaz me ha tenido al tanto de los rumores de lo extraño y fuera de lugar en Bahía del Botín y Tuercespina. Normalmente no le doy mucha importancia a las historias de fantasmas, pero escuché a unos marineros en la taberna hablando de una isla encantada frente a la costa. Dicen que hay elementales de agua y un fantasma de un goblin delirante.$b$bSi vas a hablar con Revilgaz, ¿podrías contarle esa historia?",
    ["O"] = "Habla con el Barón Revilgaz en Bahía del Botín.",
    ["T"] = "La Isla Embrujada",
  },
  [617] = {
    ["D"] = "¿Buscas trabajo, $N? Podría tener algo si estás dispuesto a un pequeño desafío.$B$BTengo un amigo llamado Groy que se hace de oro vendiendo lo que los naga llaman caña de akiris: es una especie de caña oceánica valiosa. No estoy muy seguro de para qué sirve, pero paga bien por los manojos de eso.$B$BSi me traes diez tallos de eso, tendremos negocios que hacer. Los naga fueron vistos por última vez a lo largo de la playa al norte de aquí.",
    ["O"] = "Lleva 10 Manojos de Caña Akiris al Corsario Bloads en Bahía del Botín.",
    ["T"] = "Akiris en Paquete",
  },
  [618] = {
    ["D"] = "Negolash es un gigante hambriento.$B$BCuando los gigantes destruyeron nuestro barco, Negolash fue directo a mi cocina. No tenía hambre de tripulación como los otros gigantes. Negolash quería vino... y mis Alas de Buitre a la Barbacoa. Son mi especialidad, ¡y tuve que ir hasta las Tierras Inhóspitas por la receta!$B$BSi quieres atraer al gigante, consigue un poco de vino y un montón de alas de buitre. Ponlos en nuestro viejo bote salvavidas, al sureste de aquí, a lo largo de la costa. Cuando Negolash huela toda esa comida, ¡seguro que vendrá!",
    ["O"] = "Lleva 10 Alas de Buitre a la Barbacoa y 5 botellas de Vino de Junglevino al Bote Salvavidas del Capitán Smotts.$B$BMata a Negolash y lleva la Espada de Smotts al Capitán Smotts en Tuercespino.",
    ["T"] = "Enfrentando a Negolash",
  },
  [619] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Atrayendo a Negolash",
  },
  [620] = "_",
  [621] = {
    ["D"] = "Zanzil el Desterrado habita con sus seguidores en las Ruinas de Jubuwai y Aboraz, al noreste de Bahía del Botín. Está exiliado de su tribu. ¿Por qué? Bueno...$B$BZanzil tiene métodos para doblegar las mentes de los demás, y sus líderes le temen. Usa una mezcla de plantas de la jungla en sus seguidores para suprimir su voluntad y fortalecer sus cuerpos.$B$BSu mezcla valdría mucho para aquellos que desean controlar a otros.$B$BTráeme muestras de su mezcla, y encontraré un uso para ellas.",
    ["O"] = "Lleva 12 muestras de la Mezcla de Zanzil a Crank Fizzlebub en Bahía del Botín.",
    ["T"] = "El Secreto de Zanzil",
  },
  [622] = {
    ["D"] = "Mientras estabas fuera, arreglé la olla que me trajiste. ¡Incluso le añadí un silbato de vapor extra, y mira! ¡Hay un nuevo cortador en el fondo que cortará huesos de pierna!$B$B¡Sí, sí, la Olla de Krazek es un 100% satisfactoria para cocinar! ¡Estoy seguro de que su dueño estará satisfecho!",
    ["O"] = "Lleva la Olla Reparada de Krazek al Cabo Kaleb en el Campamento Rebelde.",
    ["T"] = "Regreso con el Cabo Kaleb",
  },
  [623] = {
    ["D"] = "$N, desde que estás aquí, recibí un mensaje de Groy mientras estabas fuera. Mencionó estar satisfecho con el último envío que llegó y tiene un cliente que quiere más del material de inmediato.$B$BNo estaré yendo a Theramore por una semana, pero si estás dispuesto a hacer el viaje, no hay razón por la que no puedas llevar las cosas por mí.$B$B¿Te sientes aventurero? Sabes dónde está Marjal Revolcafango, ¿verdad? Sería cuidadoso, Theramore puede ser peligroso si no eres del tipo adecuado.",
    ["O"] = "Lleva un Haz de Juncos de Akiris al Corsario Groy en Theramore.",
    ["T"] = "Akiris en Paquete",
  },
  [624] = {
    ["D"] = "Hay un puente ornamentado y muy transitado$BAl este de Mortal y al sur de Crestagrana$BBajo él espera algo para ti$BAlgunos lo llaman una pista, otros una clave$BAllí yacerá por incontables mañanas$BAguardando su tiempo en los Pantanos de las Penas",
    ["O"] = "¡Resuelve el acertijo!",
    ["T"] = "El Acertijo de Cortello",
  },
  [625] = {
    ["D"] = "Una tierra lejana en Kalimdor espera$BDonde el peligro ha destrozado esperanzas y destinos$BAl este de Los Baldíos el clima es duro$BLa penumbra y el peligro salpican Marjal Revolcafango$BAllí yace una pista para aquellos que estén a la altura$BEsperando ser encontrada en una cueva en el suroeste",
    ["O"] = "¡Resuelve el acertijo!",
    ["T"] = "El Acertijo de Cortello",
  },
  [626] = {
    ["D"] = "¡A Lordaeron! La gran tierra de la agitación$b$bDonde los valientes soldados con la muerte se enfrentan$b$bEncuentra tu camino a través del gran desasosiego$b$bAllí encontrarás el final de esta búsqueda$b$bA lo largo de la costa de las Tierras del Interior, al pie de las grandes cataratas$b$b¡Debajo del agua allí tu tesoro llama!",
    ["O"] = "¡Resuelve el acertijo!",
    ["T"] = "El Acertijo de Cortello",
  },
  [627] = {
    ["D"] = "Hay una cueva en la montaña en el Cañón Secobarbo, muy al norte en las Tierras Altas de Arathi. En esa cueva hay un mineral raro: la piedra de sangre. La piedra de sangre en esta cueva no es de la más alta calidad, pero incluso la piedra de sangre de baja calidad es valiosa... para aquellos con el conocimiento para usarla.$B$BMe gustaría un poco.$B$BArreglaré tu olla, pero mientras trabajo en ella... tráeme muestras de piedra de sangre. Tráeme suficiente, y hasta te daré una bonificación.$B$BOh, y si no tienes la habilidad para minarla... ¡encuentra a alguien que sí la tenga!",
    ["O"] = "Lleva 4 cargamentos de Mineral de Sangrita Inferior a Krazek en Bahía del Botín.",
    ["T"] = "Un Favor para Krazek",
  },
  [628] = {
    ["D"] = "Con el interior terminado, todo lo que queda es agregar la capa exterior. Para esto, solo uso las mejores pieles de crocolisco anciano. Son más duraderas y un mejor lienzo, por así decirlo, para agregar los diseños y acentos que hacen que la línea Excelsior de Drizzlik sea única.$B$BSin embargo, es difícil encontrar esa piel perfecta, la única que se ajusta a mi plan es la piel de un crocolisco de agua salada anciano. Los ancianos solo salen para defender a los otros crocoliscos de agua salada, por lo que podrías necesitar matar a algunos para atraer a uno.",
    ["O"] = "Lleva una Piel de Crocolisco Anciano a Drizzlik en Bahía del Botín.",
    ["T"] = "Excelsior",
  },
  [629] = {
    ["D"] = "Hace mucho tiempo, un gran temblor de tierra hundió una antigua ciudad troll bajo las aguas de la Costa Salvaje, al noroeste. Llamamos a ese lugar el Arrecife Vil, porque los múrloc ahora residen en las ruinas de la ciudad, atacando a cualquiera que se acerque.$B$BHay una antigua tableta entre esas ruinas que cuenta la antigua historia de Gri\'lek, un héroe de las leyendas troll. La historia es sagrada para la tribu Lanza Negra, y aunque la tableta es demasiado grande para moverla, quiero un fragmento de ella para consagrarla en nuestro nuevo hogar en Orgrimmar.",
    ["O"] = "Lleva un Fragmento de Tablilla a Kin\'weelay en el Campamento Grom\'gol.",
    ["T"] = "El Arrecife Vil",
  },
  [630] = {
    ["D"] = "$N, pongo mi fe en ti y en cualquiera que puedas reunir para ayudarme a liberarme. El gran Rey Crin del Cielo Mukla es mi captor. Él comanda a las bestias de esta isla, y me mantienen aquí contra mi voluntad con la ayuda de esta cadena. Si puedes matarlo y encontrar la llave de este grillete, estaré eternamente en deuda contigo.$B$BA menudo lo escucho en el lado sureste de la isla, pero ten cuidado: no es alguien con quien se deba jugar.",
    ["O"] = "Mata al Rey Mukla y regresa con la Princesa Poobah con la Llave para liberarla.",
    ["T"] = "Mensaje en una Botella",
  },
  [631] = {
    ["D"] = "Una tremenda explosión rugió a través de las colinas. ¡Fue horrible, te lo digo!$B$BBuenos soldados murieron defendiendo el Puente Thandol. Longbraid dice que nos vengaremos de los Hierro Negro antes de que todo termine.$B$BEstamos en un mundo de problemas y no veo refuerzos a la vista. Longbraid envió al Viejo Rustlocke a explorar el puente oriental para ver si podíamos asegurarlo.$B$BEso fue hace días. No tenemos los recursos para buscarlo con Dun Modr bajo asedio.$B$BTal vez puedas encontrarlo...",
    ["O"] = "Rhag Garmason quiere que localices a Viejo Cerrojo.",
    ["T"] = "El Puente Thandol",
  },
  [632] = {
    ["D"] = "Parece que el pobre Rustlocke se topó con una emboscada de los Hierro Negro. Notas un pergamino junto a su puño cerrado.$b$bInforma las tristes noticias a Rhag Garmason y muéstrale lo que encontraste.",
    ["O"] = "Informa a Rhag Garmason y muéstrale el pergamino que encontraste en el cadáver de Rustlocke.",
    ["T"] = "El Puente Thandol",
  },
  [633] = {
    ["D"] = "¡Cielos, no! Pobre Rustlocke. Es una verdadera tragedia.$b$bNo debemos dejar que su muerte pase en vano. Este pergamino que descubriste indica que Kam Deepfury, el cerebro detrás del ataque original, organizó que un alijo de explosivos fuera escondido justo al otro lado del Puente Thandol, en las Tierras Altas de Arathi.$b$b¡Si esos explosivos llegan al puente, nuestra principal línea de suministro al norte será cortada! ¡El alijo debe ser destruido, $n! Regresa a mí cuando hayas completado tu misión.$b$bEres nuestra única esperanza.",
    ["O"] = "Destruye el alijo de explosivos.",
    ["T"] = "El Puente Thandol",
  },
  [634] = {
    ["D"] = "Te pido un pequeño favor más, $c. Estamos esperando a las reservas de Forjaz. Sin embargo, Roggo tiene conocimiento de un campamento de la Alianza al norte en las Tierras Altas de Arathi, en Refugio de la Zaga.$B$BViaja allí y busca al Capitán Nials. Pregúntale al buen Capitán si la Alianza puede perdonar algunos soldados para ayudarnos a recuperar Dun Modr y el Puente Thandol.",
    ["O"] = "Busca al Capitán Nials en Refugio de la Zaga.",
    ["T"] = "Súplica a la Alianza",
  },
  [635] = {
    ["D"] = "Este fragmento pulsa mientras lo sostienes...$B$BSientes una sensación de súplica proveniente del fragmento, como si alguien intentara comunicarse, alguien que desesperadamente necesita tu ayuda.$B$BUna visión aparece en tu mente: de las montañas del norte de Arathi, y de un cristal brillante que sobresale de la tierra allí.$B$BTe sientes atraído hacia ese lugar.",
    ["O"] = "Encuentra el cristal de tu visión.",
    ["T"] = "Cristal en las Montañas",
  },
  [636] = {
    ["D"] = "Este libro cuenta la historia de una princesa elemental, Myzrael, y de su encarcelamiento en las profundidades de las Tierras Altas de Arathi.$B$BTambién habla de un afloramiento secreto de cristales en las Tierras Altas. Estos cristales, los Fragmentos de Myzrael, son una ventana a la prisión de la princesa.$B$BSi un alma valiente encontrara los Fragmentos de Myzrael, entonces quizás $gél:ella, podría hablar con ella...",
    ["O"] = "Encuentra los Fragmentos de Myzrael.",
    ["T"] = "Leyendas de la Tierra <NYI>",
  },
  [637] = {
    ["D"] = "El sobre empapado y destrozado por la guerra se disuelve en tus manos, dejándote con unas pocas páginas de una carta en tu poder, escrita con esmero.$B$BLa carta está dirigida a la Sra. Sara Balloo de Forjaz y comienza, \"Mi querida Sara...\"",
    ["O"] = "Entrega la Carta de Sully Balloo a Sara Balloo.",
    ["T"] = "Carta de Sully Balloo",
  },
  [638] = {
    ["D"] = "Con Thoras Trolbane muerto y Stromgarde en ruinas, Vol\'jin ha ordenado que hagamos todo lo posible para arrebatar la legendaria espada Trol\'kalar de las manos de los humanos, para que podamos usarla en nuestras próximas batallas contra los trolls de Zul\'Gurub.$B$BZengu, uno de nuestros cazadores más experimentados, fue enviado a Caída del Halcón en las Tierras Altas de Arathi para supervisar su adquisición.",
    ["O"] = "Viaja a Trono del Trueno en las Tierras Altas de Arathi y habla con Zengu.",
    ["T"] = "Trolbane",
  },
  [639] = {
    ["D"] = "En tiempos antiguos, los trolls lucharon contra el Imperio Arathoriano y nuestro adversario más capaz, Lord Ignaeus de Strom, llamado Matatrols. Cabalgó hacia la batalla empuñando Trol\'kalar—asesino de trolls, en su lengua antigua.$b$bPero ahora los hijos de Matatrols están debilitados, su fortaleza reducida a ruinas.$b$BLa espada está protegida por varios sigilos que fueron divididos entre los sobrevivientes de Stromgarde, y debemos recuperarlos.$b$BEl primero fue tomado por el Sindicato, uno de sus hombres en Stromgarde debería tenerlo.",
    ["O"] = "Consigue el Sigilo de Strom para Zengu en Martillo Honor.",
    ["T"] = "Sigilo de Strom",
  },
  [640] = {
    ["D"] = "La tumba de Thoras Trollbane está protegida por tres sigilos: el primero de los cuales recuperaste del Sindicato. Los otros dos serán más difíciles de obtener, ya que los tiene la milicia de Stromgarde firmemente atrincherada.$b$bEl sigilo de Thoradin fue dividido en once piezas por el hijo de Thoras, Galen, y dispersado entre los defensores de Stromgarde. Recupera las piezas y tráeselas a Tor\'gan, el chamán troll, él podrá unirlas en el sigilo completo.",
    ["O"] = "Recupera los 11 Fragmentos de Sigilo de los defensores en Stromgarde y llévaselos a Tor\'gan en Martillo de Honor.",
    ["T"] = "El Sigilo Roto",
  },
  [641] = {
    ["D"] = "La reconstrucción funcionó a la perfección. El sigilo ahora puede usarse para desbloquear el sello de la tumba de Trolbane. Devuélveselo a Zengu, sin duda tiene tareas adicionales para ti.",
    ["O"] = "Lleva el Sello de Thoradin restaurado a Zengu en Martillo Honorario.",
    ["T"] = "Sigilo de Thoradin",
  },
  [642] = {
    ["D"] = "Mi nombre es Myzrael. Soy una princesa de la tierra, y mis captores, los gigantes, me han atrapado en lo profundo de las Tierras Altas de Arathi. Estos fragmentos de cristal son la única forma en que puedo comunicarme con el mundo de la superficie.$B$BPor favor, ayúdame. Los aliados de los gigantes, los kóbolds Mechapelaje, tienen un fragmento como este en su Garganta Mechapelaje, al este. Para activar el grupo, debes recolectar Motas de Myzrael de los kóbolds y aplicarlas a él.$B$BTe lo ruego, $N, ¡ayúdame!",
    ["O"] = "Reúne 12 Partículas de Myzrael y luego llévalas a los Fragmentos Iridiscentes en la Garganta Secacresta.",
    ["T"] = "La Princesa Atrapada",
  },
  [643] = {
    ["D"] = "Aunque apenas se puede decir que controlan Stromgarde, y mucho menos todo el territorio de las Tierras Altas de Arathi, el príncipe humano pasea los restos de su caballería por el campo. No puedo entender por qué lo hace, pero ¿quién entiende la forma en que piensan los humanos, eh?$b$bCualquiera que sea su razonamiento, más importante aún, le ha dado uno de los sigilos al Teniente Valorcall, su líder de caballería. Localízalo y tráeme su sigilo.",
    ["O"] = "Recupera el Sigilo de Arathor y devuélveselo a Zengu en Caída del Martillo.",
    ["T"] = "Sigilo de Arathor",
  },
  [644] = {
    ["D"] = "El último de los sigilos necesarios para desbloquear la tumba de Thoras Trollbane lo tiene su hijo personalmente. Reside en lo profundo de Stromgarde, en una capilla en los distritos controlados por la milicia. El Príncipe Galen está bien protegido y no es ningún guerrero débil, por lo que esta tarea podría resultar muy difícil.",
    ["O"] = "Mata al Príncipe Galen Aterratrols y lleva el Sigilo de Aterratrols a Zengu en Grito de Guerra.",
    ["T"] = "Sigilo de Trolbane",
  },
  [645] = {
    ["D"] = "¿Qué es esto? Con los cuatro sigilos reunidos, se han unido para formar un solo sigilo. No hay duda de que esto podrá romper los hechizos de protección colocados en la tumba de Trolbane.$B$BLos restos de Trolbane están enterrados en el Santuario detrás de la capilla en Stromgarde. Recupera Trol\'kalar.",
    ["O"] = "Recupera Trol\'kalar de la tumba de Trolbane en Stromgarde.",
    ["T"] = "Trol\'kalar",
  },
  [646] = {
    ["D"] = "La luz se desvanece lentamente, la espada Trol\'kalar, sostenida dentro de una vaina de piedra, ha sido liberada.",
    ["O"] = "Entrega Trol\'kalar a Zengu en Bastión del Honor.",
    ["T"] = "Trol\'kalar",
  },
  [647] = {
    ["D"] = "¡Anoche bebí bastante del viejo Moonshine! Longbraid me cortaría la cabeza si supiera que me desmayé durante mi guardia.$b$bMe duele mucho la cabeza. Sentí como si toda una brigada de máquinas de asedio pasara por encima, todo ese estruendo.$b$b¡Oh, mira la hora! Le prometí al Maestro Cervecero Bilger en Costasur que le pagaría mi deuda enviándole un poco de Moonshine.$b$b¡Pero solo quedan 15 minutos antes de que me retrase! Llévale este lote, ¿te importaría? ¡Y date prisa!",
    ["O"] = "Lleva el Alcohol de MacKreel al Maestro Cervecero Bilger en Costasur.",
    ["T"] = "El Aguardiente de MacKreel",
  },
  [648] = {
    ["D"] = "La voz del gnomo resuena nuevamente desde el robot:$B$B\"Necesito mover a OOX-17/TN a un lugar abierto y seguro para que pueda comenzar un largo procedimiento de despegue. Tiene camuflaje incorporado, pero necesito tiempo de mi parte para volver a hacerlo operativo. Necesito que acompañes al robot desde su ubicación actual hasta, digamos, el Puerto Bonvapor. ¡Ese debería ser un lugar perfecto, y lo suficientemente largo, para poner las cosas en marcha!\"$B$B\"¡Acompaña al robot de manera segura hasta el puerto y luego ven a hablar conmigo en Bahía del Botín! Oglethorpe Obnoticus, fuera.\"",
    ["O"] = "Escolta a OOX-17/TN hasta el Puerto Bonvapor, luego informa a Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "¡Rescata a OOX-17/TN!",
  },
  [649] = {
    ["D"] = "Tengo un trabajo de salvamento para ti, y si eres un aficionado al buen vino como yo, te encantará esta tarea. ¡Vino!$B$BEs una bebida, tonto.$B$BUn puesto de la Horda abandonado tiene un alijo de Vino de Miel de las Tierras del Interior de cuando estaban en funcionamiento. ¡No se consigue un vino tan fino como ese hoy en día! Uno de los asociados tontos de Malton sabe dónde está, así que ve allí y tráeme una caja de vino empaquetado, y dos para mi propia colección. Habla con Malton, él te dará los detalles.",
    ["O"] = "Habla con Malton Droffers en Orgrimmar.",
    ["T"] = "Recuperación de Ondulación",
  },
  [650] = {
    ["D"] = "El elfo con el que necesitarás hablar es Gilveradin Sunchaser. ¡Puede nombrar cualquier vino, añada y todo, solo por el olor! Puede empaquetar el ripple que necesitamos, así que tendrás que hablar con él. Necesitamos seis botellas para empaquetar, dos para papá, y las que él quiera... y créeme, querrá algunas. Cuando hayas empaquetado el ripple y tengas las dos botellas de papá, ¡vuelve aquí!$B$BEl campamento de Gilveradin está cerca de un refugio de altos elfos en las Tierras del Interior, y esos altos elfos no son buenos. Créeme.",
    ["O"] = "Habla con Gilveradin Cazasol, su campamento se encuentra en las Tierras del Interior.",
    ["T"] = "Recuperación de Ondulación",
  },
  [651] = {
    ["D"] = "Cuatro Piedras de Vinculación están esparcidas por Arathi. Estas piedras me mantienen atrapado bajo la tierra.$B$BPara desbloquearlas, debes desbloquear la más fuerte: la Piedra de Vinculación Interna. Y para hacer eso, debes recolectar llaves de las otras tres.$B$BEstas piedras están vigiladas por guardianes, pero si eres astuto o tienes suerte, puedes encontrar formas de desterrarlos.",
    ["O"] = "Reúne la Llave Ardiente, la Llave Crestada y la Llave Atronadora de la Piedra de Vínculo Oeste, la Piedra de Vínculo Este y la Piedra de Vínculo Exterior.$B$BLlévalas a la Piedra de Vínculo Interior.",
    ["T"] = "Piedras de Atadura",
  },
  [652] = {
    ["D"] = "Abrir las Piedras de Vinculo aflojó gran parte de mi confinamiento, pero un candado todavía me mantiene encadenada. Es la Piedra Angular que ves entre este círculo de piedras. Y la llave la tiene uno de mis captores, el vigía de piedra Fozruk.$B$BDebes obtener esa llave, que los vigilantes de piedra llaman la Vara del Orden, de Fozruk y usarla para abrir la Piedra Angular.$B$B$N, has hecho mucho para ayudarme. ¡Solo un poco más... y mi gratitud sacudirá los cimientos de la tierra!",
    ["O"] = "Encuentra y mata a Fozruk. Lleva la Vara de Orden a la Piedra Angular en las Tierras Altas de Arathi.",
    ["T"] = "Rompiendo la Piedra Angular",
  },
  [653] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Soy libre para reunir fuerzas, escondida de mis captores. Porque si me enfrentaran ahora, seguramente me superarían y me encarcelarían de nuevo.$B$B¡Pero con el tiempo enfrentaré a los gigantes, y se arrepentirán de haberme custodiado!$B$BEres un noble aliado, $N. Necesitaré tu ayuda de nuevo en el futuro. Cuando esté lista, necesitaré que me invoques en la superficie.$B$BHabla con Gerrig Bonegrip. Está en Forjaz, en una tienda de las Cavernas de los Lamentos. Él sabe cómo puedo ser invocada.",
    ["O"] = "Habla con Gerrig Puño Férreo en la Caverna Abisal de Forjaz.",
    ["T"] = "Aliados de Myzrael",
  },
  [654] = {
    ["D"] = "Activar la fuente de energía hace que el kit de prueba de campo comience a brillar y vibrar. Parece estar funcionando según lo planeado.$B$BSegún el Jefe Ingeniero Bilgewhizzle, ahora tienes dos horas para recolectar los datos que necesitas en el desierto de Tanaris. Si no los recolectas dentro de dos horas, todos los datos se perderán y tendrás que comprar otra fuente de energía de él.$B$BRegresa con él cuando hayas completado la tarea.",
    ["O"] = "Consigue muestras aceptables de 8 basilisco, 8 hienas y 8 escorpiones. Devuelve el kit de pruebas al Jefe Ingeniero Bilgewhizzle en Gadgetzan antes de que se agote la fuente de energía.",
    ["T"] = "Muestreo de Campo de Tanaris",
  },
  [655] = {
    ["D"] = "Todavía lo tengo claro, como si fuera ayer... Tantos años sin esperanza, con madera y acero para atarme a este lugar terrible. Entonces, finalmente, ¡finalmente! Estaba el Jefe de Guerra. Martillo Maldito en su armadura de medianoche, su martillo de guerra en alto.$B$BFue... demasiado terrible... Que el golpe de un cobarde derribara a nuestro mayor héroe. Desde atrás cargó en su caballo, y luego su lanza lo atravesó... Caí de rodillas. ¿Qué esperanza había? ¿Qué esperanza hay...",
    ["O"] = "Habla con Tor\'gan en Bastión de Hammersong.",
    ["T"] = "Martillo del Honor",
  },
  [656] = {
    ["D"] = "Debes derrotar a Myzrael antes de que gane fuerza. Si no lo haces, una vez que haya reunido suficiente poder, ¡desafiará a sus captores! Hay quienes piensan que pueden controlarla, pero si puede ser controlada, ¿por qué los gigantes la encadenaron en primer lugar??$B$B¿Lógica loca, eh?$B$BPara derrotarla, invócala en los Fragmentos de Myzrael donde hablaste con ella por primera vez. Derrota a Myzrael y recoge sus Grilletes Arcanos, luego únelos a los Fragmentos de Myzrael.$B$BHaz eso, y volverá a estar atrapada.",
    ["O"] = "Ve a los Fragmentos de Myzrael, invócala y derrótala.$B$BReúne sus Grilletes Arcanos y únelos a los Fragmentos de Myzrael.",
    ["T"] = "Invocando a la Princesa",
  },
  [657] = {
    ["D"] = "<Quae comienza a revisar la carpeta.>$B$BEsto ES interesante. Parece que el Boticario Jorell ha estado trabajando en algunos experimentos mortales aquí en Go\'Shek. No estoy segura de qué tiene que ver eso con Trabalomas, pero a Phin no le gustaría que lo dejáramos continuar.$B$BTuvimos un encuentro con algunos orcos mientras estabas fuera, y no estoy en condiciones de ayudar a Kinelory. Si estás dispuesto, ella necesitará algo de protección cuando vaya a la granja a robar cualquier investigación que Jorell aún no haya enviado a Molino Tarren.",
    ["O"] = "Habla con Kinelory.",
    ["T"] = "¿Indicios de una Nueva Plaga?",
  },
  [658] = {
    ["D"] = "¿Así que Phin te envió a ver cómo estamos? No me sorprende, se preocupa demasiado.$B$BHemos estado vigilando la granja durante días, y Kin dice que vio a un mensajero salir de esa pequeña casa al sur hace poco.$B$BElla habla mucho, pero siempre puedo confiar en sus sentidos cuando estamos en áreas peligrosas.$B$B¿No serás un rastreador habilidoso, verdad? Creemos que el mensajero podría llevar notas a Molino Tarren. Si pudiéramos obtener esos documentos, podría arrojar algo de luz sobre lo que está sucediendo en Trabalomas.",
    ["O"] = "Encuentra al Mensajero No-Muerto y trae su Carpeta Sellada de vuelta a Quae cerca de la Granja Go\'Shek.",
    ["T"] = "¿Indicios de una Nueva Plaga?",
  },
  [659] = {
    ["D"] = "Saludos y bienvenido, $N. Lidero un grupo de aventureros investigando sucesos extraños en esta área.$B$BMi primo en Trabalomas recientemente tuvo que sacrificar a su perro Stanley porque el animal creció mágicamente a un tamaño inmenso y atacó salvajemente a cualquiera que se le acercara.$B$BCreo que los no-muertos están involucrados, y estamos buscando señales para probarlo.$B$BDos de mis compañeros, Quae y Kinelory, se dirigieron a la Granja Go\'Shek en las Tierras Altas de Arathi, y no han dado señales de vida en días. ¿Podrías encontrarlos? Me preocupa.",
    ["O"] = "Encuentra a Quae y Kinelory cerca de la Granja Go\'Shek en las Tierras Altas de Arathi.",
    ["T"] = "¿Indicios de una Nueva Plaga?",
  },
  [660] = {
    ["D"] = "Muy bien, $N. Estoy listo si tú lo estás.$B$B¿Vamos?",
    ["O"] = "Protege a Kinelory hasta que regrese con Quae.",
    ["T"] = "¿Indicios de una Nueva Plaga?",
  },
  [661] = {
    ["D"] = "He visto a Kinelory mucho peor después de una noche en la taberna de lo que está ahora. Obviamente hiciste un buen trabajo protegiéndola.$B$BEstos objetos que trajo de la granja deberían al menos darle a Phin una idea de lo que está pasando aquí en Arathi. Solo para asegurarnos de que no haya represalias después de tu ataque, nos quedaremos aquí un poco más. ¿Estarías dispuesto a regresar a Costasur y hablar con Phin?",
    ["O"] = "Encuentra a Phin Odelic en Costasur.",
    ["T"] = "¿Indicios de una Nueva Plaga?",
  },
  [662] = {
    ["D"] = "O\'Breen lideró los barcos hacia la cala. Estábamos seguros de que aquí se encontraría el tesoro. El Harbinger atracó de manera segura, pero cuando el Maiden\'s Folly y el Spirit of Silverpine se acercaban, las mareas comenzaron a bajar. ¡Lo más extraño que he visto!$b$bEsos dos barcos encallaron en algo... una especie de templo en el arrecife. Se hundieron tan rápido que fue toda una tragedia.$b$bNecesitamos recuperar sus Registros y sus Cartas antes de irnos. ¡No podemos dejar esa información atrás para ojos curiosos!",
    ["O"] = "El Primer Oficial Nilzlix quiere que recuperes las Cartas y los Registros de la Doncella de la Locura y el Espíritu de los Pinos de Plata.",
    ["T"] = "Salvamento en Alta Mar",
  },
  [663] = {
    ["D"] = "¡Lolo ve un pequeño $r! ¡Siempre alerta, Lolo está!$b$bBienvenido a la Cala de Faldir. El Capitán O\'Breen dijo que solo estaríamos aquí unas horas. Solo el tiempo suficiente para reunir el tesoro y volver a Bahía del Botín.$b$bPero Lolo cree que vamos a estar aquí mucho más tiempo. Perdimos los otros dos barcos de nuestra formación. Pobres Spirit of Silverpine y Maiden\'s Folly. ¡Ahora descansan en el fondo del mar!$b$bLolo sugiere que hables con el Capitán O\'Breen si planeas quedarte aquí.",
    ["O"] = "Habla con el Capitán O\'Breen en la Cala de Faldir.",
    ["T"] = "¡Tierra a la Vista!",
  },
  [664] = {
    ["D"] = "Argh, es una vida horrible. Debería estar en una tumba acuática en este momento, no sentado aquí junto al fuego, respirando aire fresco.$B$BLa tripulación intentó tan duro salvar mi hermoso Folly de la Doncella. Benditos sean los chicos, benditos sean sus corazones.$B$BO\'Breen llegó a la orilla sano y salvo, pero nosotros no tuvimos tanta suerte. Los mares simplemente se hundieron bajo nosotros, lo juro.$B$BUn segundo estábamos a flote y al siguiente el agua entraba a raudales. ¡Y esas bestias! Mataron a todos los sobrevivientes. Escoria naga horrible. Me desmayé y terminé aquí, salvado.$B$BQuiero que esas bestias mueran.",
    ["O"] = "Capitán Tripahierro en la Ensenada de Faldir quiere que mates a 10 Asaltantes Filoespina y 3 Hechiceras Filoespina.",
    ["T"] = "Penas Ahogadas",
  },
  [665] = {
    ["D"] = "Ahora que somos miembros de pleno derecho de los Blackwater Raiders, es nuestro trabajo ayudar al Sr. O\'Breen a localizar el tesoro élfico perdido.$b$bEs casi imposible encontrar las gemas en el mar oscuro sin ayuda. El doctor ha construido unas gafas que ayudarán. Necesita que las gafas se carguen con la energía derivada de la piedra encantada en la cueva justo arriba de la colina.$b$b¡Pero la cueva está maldita! Cuando nos acercamos, nos emboscan. Defiéndeme y puedo canalizar la energía de la piedra en las gafas.",
    ["O"] = "Escolta al Profesor Phizzlethorpe hasta la cueva y de regreso.",
    ["T"] = "Tesoro Hundido",
  },
  [666] = {
    ["D"] = "El tesoro ha estado en el fondo del mar tanto tiempo que las gemas se han calcificado en una gruesa piedra. Pero el poder contenido en estas gafas te permitirá localizarlas fácilmente.$B$B¡Un poco de ingenio gnómico llega lejos!$B$BAsí que toma prestadas las Gafas de Caza de Gemas, $n, y ve si puedes recolectar algo del tesoro perdido para el Capitán O\'Breen.$B$BYo nadaría hasta allí, pero... um... bueno, tengo importantes asuntos científicos que atender aquí en la tierra firme y seca... eh, sí.",
    ["O"] = "El Doctor Draxlegauge en la Ensenada de Faldir quiere que recolectes 10 Gemas Élficas y devuelvas las Gafas de Caza de Gemas una vez que hayas terminado.",
    ["T"] = "Tesoro Hundido",
  },
  [667] = {
    ["D"] = "Esto es malo. Parece que los Daggerspine se están preparando para un ataque.$b$bLos Naga son conocidos por su crueldad en la batalla. Seguirán atacando hasta que yo esté muerto.$b$bNo valoro mi propia vida por encima de la de los miembros de mi tripulación, pero debes ayudarme a defenderme. La tripulación no podrá regresar a Bahía del Botín sin mi conocimiento de los mares.$b$bLos Naga vendrán desde el mar. Necesito que te quedes aquí conmigo para repeler el ataque. Maneja el cañón y hazlos retroceder.$b$b¿Estás listo, $n?",
    ["O"] = "Protege a Shakes O\'Breen durante el ataque.",
    ["T"] = "Muerte desde las Profundidades",
  },
  [668] = {
    ["D"] = "No dejemos esperando al Capitán O\'Breen. Querrá ver estas gemas de primera mano. Después de todo, ¡es por eso que estamos aquí!$b$bY después de unas semanas de tratar con estos piratas, el profesor y yo nos hemos dado cuenta de que lo último que es bueno para nuestra salud es que nos sorprendan colgando de su tesoro.$b$bAquí, $n, llévaselas a O\'Breen.",
    ["O"] = "Lleva las Gemas Élficas al Capitán O\'Breen.",
    ["T"] = "Tesoro Hundido",
  },
  [669] = {
    ["D"] = "El Maestro de la Flota Seahorn querrá saber sobre nuestro hallazgo de inmediato.$b$bPero como puedes ver, no estamos en posición de salir de aquí. No solo la marea está demasiado baja, sino que esas malditas criaturas que perturbamos están vigilando de cerca nuestros movimientos.$b$bPuedes ser de gran servicio para los Corsarios Negraaguas si puedes llevar la noticia al Maestro de la Flota Seahorn en Bahía del Botín de que descubrimos el tesoro y estamos trabajando en extraer más. Llévale esta muestra como prueba.",
    ["O"] = "Lleva la Muestra de Gema Élfica al Maestre de Flota Cuerno Marino en Bahía del Botín.",
    ["T"] = "Tesoro Hundido",
  },
  [670] = {
    ["D"] = "Oye, $n, pareces ser del tipo aventurero. Mi flota está atascada en Bahía del Botín hasta que podamos reabastecernos.$b$bHay cosas importantes que se están gestando aquí y en el extranjero, y necesito enviar una correspondencia secreta a Shakes O\'Breen, que está atracado en la costa de Arathi.$b$b¿Puedo confiar en que entregarás este mensaje de manera segura y confidencial?",
    ["O"] = "Entrega la Carta de Seahorn a Shakes O\'Breen en las Tierras Altas de Arathi.",
    ["T"] = "Tesoro Hundido",
  },
  [671] = {
    ["D"] = "He sentido una extrañeza en el aire... una sensación que hierve en mi sangre. Tenía mis sospechas. Me dirigí a encontrar la fuente de mi inquietud y descubrí, al oeste, en la Mansión Northfold, los rastros residuales de una invocación demoníaca. Cuando me acerqué, mi sangre comenzó a hervir y la ira dentro de mí creció.$b$bEn el suelo vi un amuleto de piedra de sangre, utilizado para ayudar en la invocación demoníaca. Sin embargo, el que encontré estaba agotado. Necesitaré otro para examinarlo, pero no me atrevo a regresar a la mansión. ¿Puedes traerme algunos?",
    ["O"] = "Consigue 10 Amuletos de Sangrapiedra para Tor\'gan en Martillo Honor.",
    ["T"] = "Magias Impuras",
  },
  [672] = {
    ["D"] = "Intentamos acomodarlo, animarlo, contándole historias de nuestra poderosa ciudad en Orgrimmar en Kalimdor, pero nada lo conmovía.$b$bHistorias de nuestro joven Jefe de Guerra, Thrall, que lleva la armadura y el martillo de Orgrim... pero, tal vez... ¡Tal vez! Una demostración de nuestra nueva magia chamánica le daría nueva esperanza. ¡Vale la pena intentarlo!$b$bUn pequeño objeto que podríamos hacer para él, sí... Tráeme algunos ojos de raptor, y veré qué puedo hacer.",
    ["O"] = "Consigue 10 Ojos de Raptor de las Tierras Altas de Acechadores y Trituradores de las Tierras Altas para Tor\'gan en Caída del Martillo.",
    ["T"] = "Levantando Espíritus",
  },
  [673] = {
    ["D"] = "No hay duda en mi mente de que un poderoso brujo reside dentro de los muros de Stromgarde. De vez en cuando, usando una reliquia mágica arcana, este brujo ha convocado terribles demonios en grandes cantidades. Debemos eliminar la fuente de su energía.$B$BEncuentra al brujo y mátalo. Tráeme cualquier objeto mágico que encuentres en su persona, y tomaré medidas para destruirlo y asegurarme de que su poder no sea usado para corromper esta tierra más.",
    ["O"] = "Recupera el Orbe de Sangre de Marez Cowl y llévaselo a Tor\'gan en Martillo de Honor.",
    ["T"] = "Magias Impuras",
  },
  [674] = {
    ["D"] = "Aquí, toma este amuleto y llévaselo a Gor\'mul, a ver si la fuerza de los raptores puede despertar la llama del guerrero que una vez ardió en sus venas.",
    ["O"] = "Dale el Amuleto de Garra de Raptor a Gor\'mul.",
    ["T"] = "Levantando Espíritus",
  },
  [675] = {
    ["D"] = "<Gor\'mul arroja el amuleto al suelo.>$b$bTor\'gan te envió, ¿no? ¡Pah! No entiendo por qué siente algo de piedad hacia mí. La piedad me fue negada cuando no se me permitió morir en batalla con mi Jefe de Guerra. Un golpe cruel del destino...",
    ["O"] = "Habla con Tor\'gan en Bastión de Hammersong.",
    ["T"] = "Levantando Espíritus",
  },
  [676] = {
    ["D"] = "Débiles... todos ellos. Tú, $c, ¿qué estás mirando? ¿Crees que eres especial, eh? ¿Por qué no lo pruebas?$B$BEl puesto de Caída del Martillo en Arathi está bajo ataque. A menos que les enviemos ayuda pronto, su perdición es inminente.$B$BPero antes de que salgas corriendo, quizás quieras probar tus habilidades primero. Al noreste de Stromgarde en Arathi hay un montículo de ogros habitado por la tribu Puño de Roca. Cuando puedas manejar a los ogros y a los ejecutores allí, entonces informa a Drum Fel en Caída del Martillo.",
    ["O"] = "Mata 8 Ogros Puño de Roca y 10 Déspotas Puño de Roca, luego encuentra a Drum Fel en el puesto de Martillo Honor en las Tierras Altas de Arathi.",
    ["T"] = "El Martillo Puede Caer",
  },
  [677] = {
    ["D"] = "Los trolls Secacorteza y los ogros Puño de Roca de Arathi están trabajando juntos para deshacerse de nosotros de esta tierra, pero no encontraremos nuestro destino a manos de ellos, o de nadie más. Los mataremos y les mostraremos que nosotros también tenemos un hogar aquí. $B$BLos trolls nos dieron la espalda, los ogros nos usarían como comida y ropa de cama si se lo permitiéramos, estas cosas no serán.$B$BComenzaremos nuestro ataque contra los trolls Secacorteza al sur de aquí. Mátalos en masa y solo regresa cuando sus campamentos estén empapados de sangre.",
    ["O"] = "Mata 10 Lanzahachas Secacorteza, 10 Cazacabezas y 8 Médicos Brujos, y regresa con Tambor Vil en el Puesto Martillo de Honor.",
    ["T"] = "Llamado a las Armas",
  },
  [678] = {
    ["D"] = "Tu fuerza es evidente, $N. Ahora veamos cómo te va contra un enemigo real.$B$BLos Machacapiedras tienen una guarida al suroeste de aquí hacia el puente enano, y, si no me equivoco, los magos y los magos allí te presentarán un desafío mayor: hasta ahora nos han presentado un desafío.$B$BMata a tantos como puedas y ven a mí en cuerpo cuando tengas éxito, o ven a mí en espíritu cuando fallen. Alabaré tu tenacidad en la batalla de cualquier manera.",
    ["O"] = "Mata 10 Brutos Puño de Roca y 4 Magos Puño de Roca, y regresa con Drum Fel en el puesto de Martillo Honor.",
    ["T"] = "Llamado a las Armas",
  },
  [679] = {
    ["D"] = "La cabeza de la bestia está lista para ser cortada... con gran fuerza, $N. Has mostrado todas las señales de que puedes ayudarnos con este último y poderoso golpe. Los líderes de estos ataques han hecho su guarida dentro de las ruinas de Stromgarde, un símbolo flagrante de la fragilidad humana.$B$BEncuentra y mata a esos señores Puño de Roca y a sus lacayos chamanes que intentarían expulsarnos de estas tierras.",
    ["O"] = "Mata 15 Chamán Puño de Roca y 10 Señores Puño de Roca y regresa con Drum Fel en el puesto de Martillo Honor.",
    ["T"] = "Llamado a las Armas",
  },
  [680] = {
    ["D"] = "Sirves bien a mi esposo, $c, pero él no sabe por qué Sentencia está bajo ataque.$B$BOr\'Kalar, el peón de Mug\'thol en Stromgarde, tiene un intenso odio hacia mi esposo y no desea nada más que la muerte de mi esposo por haberlo derrotado hace años.$B$BMis augurios me dicen que Or\'Kalar ha tendido una trampa para mi esposo, así que he ocultado los signos de la presencia del ogro de él.$B$BMi esposo saldrá corriendo tontamente si se entera de la ubicación del ogro y te pediría que me ayudaras a matar a la criatura.",
    ["O"] = "Mata a Or\'Kalar y lleva su Cabeza a Korin Fel en el puesto de Martillo Honor.",
    ["T"] = "La Amenaza Real",
  },
  [681] = {
    ["D"] = "Estamos librando una guerra por Stromgarde, y la estamos perdiendo. Los ogros, el Syndicato y nosotros hemos dividido nuestra gran ciudad, y cada facción lucha contra la otra en un sangriento punto muerto.$B$BPero nuestras fuerzas disminuyen mientras nuestro enemigo crece.$B$BNecesitamos ayuda del pueblo de Costasur, pero los suministros son difíciles de recibir mientras el Syndicato controla el territorio entre nosotros.$B$BVe a la base del Syndicato en la Mansión Northfold y mátalos. Entonces, quizás, la presión sobre nuestras líneas de suministro disminuirá y más llegará.",
    ["O"] = "Mata 10 Bandidos de la Hermandad y 6 Mercenarios de la Hermandad.$B$BRegresa con el Capitán Nials en Refugio de la Zaga.",
    ["T"] = "Mansión de Valfondo",
  },
  [682] = {
    ["D"] = "El Sindicato en Stromgarde lucha contra nuestro contingente de milicia allí. Para instigar a la chusma a luchar contra nuestras tropas más disciplinadas, los líderes del Sindicato han puesto una recompensa por nuestros soldados, otorgando oro por cada Insignia de Stromgarde recolectada de los milicianos caídos.$B$B¡Queremos que esas insignias sean recuperadas, no podemos permitir que los asesinos tomen trofeos de nuestros valientes caídos!$B$BEntra en el área controlada por el Sindicato en Stromgarde y cázalos por nuestras insignias.",
    ["O"] = "Lleva las Insignias de Stromgarde al Capitán Nials en Refugio de la Zaga.",
    ["T"] = "Insignias de Stromgarde",
  },
  [683] = {
    ["D"] = "$n, tengo una pequeña solicitud que hacerte.$B$BLleva esta nota que he preparado a nuestro gran monarca de Forjaz, el Rey Magni Barbabronce. Es una tarea pequeña para alguien como tú, alguien que ha demostrado gran valentía en la causa de la Alianza hasta ahora.$B$BQue yo vaya ante el Rey en mi estado de duelo no serviría al propósito de mi causa. Seguramente lo entiendes.",
    ["O"] = "Lleva la nota de Sara Balloo al Rey Magni Barbabronce de Forjaz.",
    ["T"] = "La Súplica de Sara Balloo",
  },
  [684] = {
    ["D"] = "Se ha puesto una recompensa por la cabeza de Marez Cowl, una consejera y operativa de alto nivel del Sindicato. Fue vista por última vez dentro del territorio controlado por el Sindicato en Stromgarde.$B$BLa recompensa puede ser cobrada del Capitán Nials.$B$BAdvertencia: Marez es una bruja hábil y astuta, conocida por su tráfico con demonios. Se recomienda extremar la precaución al cazarla.",
    ["O"] = "Lleva la Cabeza de Marez al Capitán Nials en Refugio de la Zaga.",
    ["T"] = "¡Se Busca! Capucha de Marez",
  },
  [685] = {
    ["D"] = "La Milicia de Stromgarde ha puesto recompensas por las cabezas de Lord Cresta de Halcón y su guardaespaldas Otto. Cresta de Halcón dirige los esfuerzos del Sindicato en las Tierras Altas de Arathi, y su muerte causaría una gran interrupción en esos esfuerzos.$B$BSu guardaespaldas Otto, aunque no es un objetivo estratégico, es un oponente feroz y ha matado a docenas de nuestros defensores.$B$BSus recompensas pueden ser cobradas del Capitán Nials.",
    ["O"] = "Lleva la Cabeza de Otto y la Cabeza de Cresta de Halcón al Capitán Nials en Refugio de la Zaga.",
    ["T"] = "¡Se Busca! Otto y Cresta de Halcón",
  },
  [686] = {
    ["D"] = "No espero que los buenos enanos como la Sra. Balloo entiendan por qué las tragedias de la guerra deben ser soportadas por el bien mayor de nuestro pueblo. La pérdida de su esposo, y la pérdida de cada enano que lucha en nombre de la Alianza, pesa mucho en mi alma.$B$BAunque no puedo traer a su esposo de vuelta del Vacío Abisal, puedo rendirle homenaje.$B$BVoy a encargar un memorial para Sully Balloo que será construido por el Gran Mason Mablesten.$B$BEnvíale un mensaje ahora, $c. Serás el custodio de mi mandato.",
    ["O"] = "El Rey Magni Barbabronce quiere que hables con el Gran Mason Marblesten.",
    ["T"] = "Tributo de un Rey",
  },
  [687] = {
    ["D"] = "Theldurin el Perdido fue una vez miembro de nuestra orden, el Martillo Crepuscular. Pero perdió la cabeza y despreció nuestras enseñanzas. Y antes de huir de nosotros, robó el pergamino de Myzrael. No pudimos atraparlo, pero los rumores dicen que se esconde en las Tierras Inhóspitas, balbuceando y loco.$B$BPara traer a la Dama de vuelta a nosotros, debes encontrar el pergamino. Y para hacer eso, debes encontrar a Theldurin.",
    ["O"] = "Encuentra a Theldurin el Perdido.",
    ["T"] = "Theldurin el Perdido",
  },
  [688] = {
    ["D"] = "<Myzrael habla.>$B$B¡Libre! ¡SOY LIBRE! Soy libre para reunir fuerzas, escondida de mis captores. Porque si me enfrentaran ahora, seguramente me superarían y me encarcelarían de nuevo.$B$B¡Pero con el tiempo enfrentaré a los gigantes, y se arrepentirán de haberme custodiado!$B$BEres un noble aliado, $N. Necesitaré tu ayuda de nuevo en el futuro. Cuando esté lista, necesitaré que me invoques en la superficie.$B$BHabla con Zaruk en Sentencia. Él sabe cómo puedo ser invocada.",
    ["O"] = "Habla con Zaruk en Bastión de Hammersong.",
    ["T"] = "Aliados de Myzrael",
  },
  [689] = {
    ["D"] = "¿Así que nuestro buen Rey quiere que se construya un memorial en honor a un soldado llamado Sully Balloo? ¿Y dices que el pobre murió durante la tragedia del Puente Thandol?$b$bBah, ¿habrá alguna vez paz en Azeroth?$b$bSin embargo, cualquier encargo del Rey es de suma importancia. Para construir una obra verdaderamente épica y duradera, quiero trabajar con una de las piedras más finas disponibles. Viaja a la Colina de Darrow en Trabalomas y recoge para mí un poco de Granito de Alterac.",
    ["O"] = "El Gran Mason Marblesten de Forjaz quiere 5 piezas de Granito de Alterac.",
    ["T"] = "Tributo de un Rey",
  },
  [690] = {
    ["D"] = "Saludos, $N. Tienes el aspecto de un aventurero, y tengo una tarea que necesita atención.$B$BEl Archimago Trelane, mi maestro original, huyó de Stromgarde en las Tierras Altas de Arathi no hace mucho, y me ha pedido que lo ayude a recuperar algunos de los artículos que dejó atrás.$B$BLa ciudad cayó bajo asedio, y él intentó defender la Torre de Arathor, pero sin éxito. Ya tengo agentes en las Tierras Altas, pero han solicitado que envíe más ayuda. ¿Estás interesado?",
    ["O"] = "Encuentra a Skuerto en Refugio de la Zaga en las Tierras Altas de Arathi.",
    ["T"] = "La Petición de Malin",
  },
  [691] = {
    ["D"] = "El Archimago Malin envió a Skuerto y a mí aquí para recolectar reactivos para él. Estaba perdido sobre cómo iba a recolectar los artículos, pero ahora que estás aquí, creo que mi problema está resuelto.$B$BNecesito lo siguiente: colmillos de Secacorteza, bolsas de medicina de Secacorteza y un cuchillo de cazador de sombras.$B$B¿Podrías encontrarlos para mí? Los Secacorteza viven al sureste de Arathi.",
    ["O"] = "Encuentra 10 Colmillos Secacorteza, 4 Fardos de Medicina Secacorteza y un Cuchillo de Cazador de las Sombras para el Aprendiz Kryten en Refugio de la Zaga.",
    ["T"] = "Vale su Peso en Oro",
  },
  [692] = {
    ["D"] = "¡El Pergamino de Myzrael... el Pergamino de Myzrael?! ¡No quieres eso! ¡Su magia puede atraer a Myzrael al mundo de la superficie y si haces eso, estamos en problemas!$B$BO... tal vez ya sea demasiado tarde. Tal vez ya esté libre. Tal vez solo esté reuniendo fuerzas antes de desatarse sobre nosotros. Si es así, ¡entonces NECESITARÁS el pergamino!$B$B...lástima que esté destruido. Los elementales enfurecidos lo robaron y lo rompieron, y ahora luchan por los fragmentos.$B$BPero si puedes reunir esos fragmentos, tal vez podamos arreglarlo...",
    ["O"] = "Lleva el Fragmento de Pergamino Rasgado, el Fragmento de Pergamino Arrugado y el Fragmento de Pergamino Chamuscado a Theldurin el Perdido.",
    ["T"] = "Los Fragmentos Perdidos",
  },
  [693] = {
    ["D"] = "Oye, $N. Lamento haberte tratado tan bruscamente antes... y no, eso no es un juego de palabras.$B$BDescubrí dónde terminó el primer objeto de Trelane: Kor\'gresh lo tiene en el Salón Puño de Roca. Y por la forma en que lanzaba ráfagas de hielo a sus parientes para mantenerlos en línea, debe estar cerca de descubrir cómo usar todos sus poderes.$B$BEl Salón Puño de Roca está al sur de aquí y luego al este del Puente Thandol. Encuentra al ogro y recupera esa vara.$B$BMientras estás fuera, descubriré qué vamos a hacer con Stromgarde.",
    ["O"] = "Encuentra la Vara de Invocación de Trelane y devuélvesela a Skuerto en Refugio de la Zaga.",
    ["T"] = "Varita sobre Puño",
  },
  [694] = {
    ["D"] = "¿Todos descansados?$B$BAquí está nuestro siguiente paso: hay ogros por todas partes alrededor de la Torre de Arathor en Stromgarde. Vamos a necesitar activar las defensas de la torre si quieres tener alguna posibilidad contra ellos y aún así llegar a los objetos con vida. Para hacer eso, vamos a necesitar algunas gemas que los chamanes Boulderfist han tomado como suyas. Las tomaron de la torre, pero no se quedarán en su posesión por mucho tiempo.$B$BConsigue una de esas gemas y tráeselas a Kryten, él conoce el hechizo para encantarlas y que funcionen como una llave.",
    ["O"] = "Encuentra una Ágata Azur y llévasela al Aprendiz Kryten en Refugio de la Zaga.",
    ["T"] = "Defensas de Trelane",
  },
  [695] = {
    ["D"] = "Aquí tienes, $N. La gema solo necesita ser presionada contra uno de los pedestales cerca de la base de la torre para activar sus defensas. Úsala cuando estés listo para entrar en la torre y encontrar los objetos restantes de Trelane. $B$BTe deseo buena suerte. Te estaré esperando aquí en el campamento. Oh, y habla con Skuerto antes de irte. Él te dará los detalles que necesitarás.",
    ["O"] = "Habla con Skuerto.",
    ["T"] = "Encantamiento de un Aprendiz",
  },
  [696] = {
    ["D"] = "Aquí está lo que buscas: el filacteria de Trelane, el orbe de Trelane y la ágata de Trelane.$B$BLos tres deberían estar en cofres dentro de la torre y no deberías tener problemas para abrir los cofres por tu cuenta, solo tienes que tener cuidado con los ogros.$B$BLas defensas de la torre no se encargarán de ellos por sí solas, necesitarás tener tus armas listas. Si esos ogros descubren que estás allí y que les estás robando, ¡seguro que te harán daño!$B$BTen cuidado y buena suerte.",
    ["O"] = "Encuentra el Filacteria de Trelane, el Orbe de Trelane y la Ágata Ardiente de Trelane, y llévaselas a Skuerto en Refugio de la Zaga.",
    ["T"] = "Ataque a la Torre",
  },
  [697] = {
    ["D"] = "Llévale esta carta de vuelta a Ventormenta y hazle saber a Malin que Kryten y yo estaremos en casa pronto. Te recompensará bien por ayudarnos aquí, $N, de eso no tengo duda.$B$BViaja con cuidado.",
    ["O"] = "Lleva la Carta Sellada al Archimago Malin en el Barrio de los Magos de Ventormenta.",
    ["T"] = "La Petición de Malin",
  },
  [698] = {
    ["D"] = "Estoy trabajando en conseguir algunos suministros para nuestro puesto avanzado cerca de la playa al este—Tok\'kar hace lo que puede, pero entre los turnos de guardia y cocinar para sus soldados, no le queda mucho tiempo en el día.$B$BLes gusta cocinar algo de carne de sierra cuando pueden, pero no he tenido tiempo de enviarles otro cargamento. ¿Te importaría ayudarlos?$B$BLos sierradientes jóvenes no son tan sabrosos como los mayores, así que evítalos, dales un año o dos para que alcancen su tamaño completo.",
    ["O"] = "Lleva 8 Flancos de Dientes de Sierra sin Preparar a Tok\'Kar en los Pantanos de las Penas.",
    ["T"] = "Falta de Excedentes",
  },
  [699] = {
    ["D"] = "Con la amenaza de los múrlocs a lo largo de la costa, estamos estacionados aquí para proteger, o advertir si la fuerza es demasiado grande, de cualquier amenaza que se acerque a Rocal. No nos da mucho tiempo para relajarnos, y podríamos usar más ayuda si estás dispuesto.$B$BLas garras de los rompedientes son excelentes armas cuando se preparan adecuadamente. Tráeme algunas de sus garras y te recompensaré con una de nuestras armas a cambio de la carne fresca y la buena compañía.",
    ["O"] = "Lleva 6 Garras de Tortuga Dientes de Sierra a Tok\'Kar en los Pantanos de las Penas.",
    ["T"] = "Falta de Excedentes",
  },
  [700] = {
    ["D"] = "Ah, qué fina piedra viene de Lordaeron. Qué lástima que la tierra esté en ruinas.$B$B¡Pero el Memorial quedó bastante bien! Debería pensar que Su Majestad estará muy complacido con él.$B$BAhora, si puedes, por favor envía un mensaje a Su Majestad que el Memorial está completo como se solicitó.",
    ["O"] = "Preséntate ante el Rey Magni Barbabronce.",
    ["T"] = "Tributo de un Rey",
  },
  [701] = {
    ["D"] = "Con algunos objetos adicionales, podré crear un objeto que imbuirá a Gor\'mul con la astucia del raptor. ¡Con el poder del raptor fluyendo a través de él, Gor\'mul conocerá nuestro nuevo poder y redescubrirá al guerrero que yace dormido!$b$bNecesitaré corazones de raptor para confeccionar el objeto y el hechizo.",
    ["O"] = "Consigue 12 Corazones de Raptor de Devoradores de Carne de las Tierras Altas para Tor\'gan.",
    ["T"] = "Astucia del Raptor",
  },
  [702] = {
    ["D"] = "Toma este orbe y usa su energía para darle a Gor\'mul la esencia del raptor. Seguramente esto vigorizará su espíritu y le dará un nuevo propósito en la vida. Con él de vuelta a la normalidad, podremos explicarle el estado actual de la Horda, y tendremos la ayuda de un gran guerrero en nuestras batallas incesantes.",
    ["O"] = "Dale el Orbe de Tor\'gan a Gor\'mul.",
    ["T"] = "Astucia del Raptor",
  },
  [703] = {
    ["D"] = "¡Oh sí, mis alas de buitre a la barbacoa son un favorito! La salsa es rica y la carne es ácida. ¡Y el olor atraerá a los hambrientos desde millas de distancia!$B$BTengo todo lo que necesito para cocinar un lote. Todo... ¡excepto las alas de buitre!$B$BSi quieres algunas, consígueme las alas de buitre. ¡Tráeme suficientes y tal vez incluso te comparta la receta!",
    ["O"] = "Lleva 4 Alas de Buitre a Rigglefuzz.",
    ["T"] = "Alas de Buitre a la Barbacoa",
  },
  [704] = {
    ["D"] = "El último informe que recibí de Agmond es preocupante, más aún ahora que está muerto. Mencionó que sus excavadores descubrieron \"urnas antiguas\" en el sitio de excavación fuera de Uldaman. Temo que las urnas que encontró en ese sitio incitaron a los troggs, tal como lo hicieron los ídolos que encontré aquí.$B$BNecesito que consigas esas urnas para nuestros estudios. Ve al sitio de Uldaman, recoge las urnas y luego regresa conmigo.",
    ["O"] = "Lleva 4 Urnas de Piedra Talladas al Prospector Ironband en Loch Modan.",
    ["T"] = "El Destino de Agmond",
  },
  [705] = {
    ["D"] = "Los Baldíos son un lugar duro, lleno de depredadores viciosos y carroñeros audaces. ¡Da miedo, especialmente para un pequeño goblin! Para sobrevivir, tengo que ser astuto.$B$BConozco la receta para hacer bombas cegadoras. Las uso para ahuyentar a la vida salvaje. Pero me estoy quedando sin uno de los ingredientes: polvo de perla azul triturada. Consígueme algo y haré que valga la pena tu esfuerzo.$B$BJe, y espero que tengas buenas botas puestas. Las Perlas Azules que necesito se encuentran en almejas del Arrecife Maldito.$B$BSí, ¡el Arrecife Maldito en Tuercespina!",
    ["O"] = "Lleva 9 Perlas Azules a Rigglefuzz en las Tierras Inhóspitas.",
    ["T"] = "Buceo de Perlas",
  },
  [706] = {
    ["D"] = "Como aprendiz de Feranor Steeltoe, aprendí su técnica especial para imbuir armas con encantamientos ígneos. La última vez que supe de él, estaba trabajando en una herrería con un grupo de cazadores en Lordaeron.$b$bNo puedo contarte muchos detalles sobre el proceso, pero puedo decirte que requiere sangre del corazón de un raro y aún latiente cría de draco negro. No tengo mucho que hacer mientras espero noticias de Forjaz. Si me traes un corazón de las crías en la Quebrada Lethlor, ¡te mostraré mi habilidad!",
    ["O"] = "Consigue un Corazón de Draco Negro para Sigrun Ironhew.",
    ["T"] = "Encantamientos de Llamas Ardientes",
  },
  [707] = {
    ["D"] = "$N. Recibí informes del Prospector Vetaferro en su sitio de excavación en Loch Modan: necesita que se haga algo de trabajo en otra de nuestras excavaciones.$B$BNo tengo más detalles que eso, pero si quieres estirar las piernas, ve a hablar con Vetaferro.",
    ["O"] = "Habla con el Prospector Ironband en la Excavación de Ironband en Loch Modan.",
    ["T"] = "¡Vetaferro te Quiere!",
  },
  [708] = {
    ["D"] = "La superficie de esta caja metálica negra está muy picada, como si hubiera estado sumergida en ácido, debe haber estado en el estómago del cóndor gigante durante mucho tiempo.$B$BAl girar la caja, se encuentra un símbolo estampado en su parte inferior. Debajo del símbolo están las palabras:$B$B\"Motor #19 - Brigada de Asedio de Forjaz\"",
    ["O"] = "Lleva la Caja Negra Corroída al Salón de las Armas en Forjaz.",
    ["T"] = "La Caja Negra",
  },
  [709] = {
    ["D"] = "¡Oh no! ¡Oh no! Se acerca una fatalidad. ¡Escúchame! ¡Una fatalidad!$B$BNo, no puedo hablar de ello... ¡no puedo pronunciar sus nombres o podrían oírme! Pero si aprendemos, sí, si aprendemos más de ellos... ¡entonces tal vez podamos aprender cómo frustrarlos!$B$BTal vez.$B$BEntra en Uldaman. ¡No! ¡Lo dije! Debes encontrar... encontrar la tableta de... Ryun\'eh. ¡Sí, encuéntrala! ¡Y no me hagas pronunciar ese nombre de nuevo!$B$BEstá en lo profundo de ese lugar, en un cofre antiguo. Ahí es donde se esconde... en un cofre hecho antes de las obras de cualquier enano.",
    ["O"] = "Lleva la Tablilla de Ryun\'eh a Theldurin el Perdido.",
    ["T"] = "Solución para la Perdición",
  },
  [710] = {
    ["D"] = "Aquí en las Tierras Inhóspitas me he detenido para trabajar en mi último proyecto: mis grilletes de vinculación elemental.$B$BCuando estén completos, permitirán a los magos invocar elementales aún más poderosos para usarlos en capacidades aún mayores. Para comenzar, necesito fragmentos de piedra pequeños para probar algunos de mis hechizos ofensivos más nuevos, junto con algunos artilugios que he ensamblado.$B$BLos fragmentos se recolectan fácilmente de los elementales de roca menores al oeste de aquí. ¿Podrías traerme algunos?",
    ["O"] = "Encuentra 10 Fragmentos de Piedra Pequeños y llévaselos a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los Elementos: Roca",
  },
  [711] = {
    ["D"] = "$N, ya has demostrado ser más capaz que todos mis aprendices juntos.$B$BLas siguientes partes que necesito requieren un poco más de fuerza. Se pueden encontrar grandes losas de piedra en los elementales de roca si tienes suficiente cuidado. Sus cuerpos son más fuertes y grandes, y por lo tanto más propensos a tener piezas lo suficientemente grandes para mis pruebas. Puedes encontrarlos más al oeste de aquí o al sureste a lo largo de los bordes de las montañas.$B$BTómate tu tiempo, necesitaré un rato para probar estas piezas más pequeñas que me trajiste.",
    ["O"] = "Lleva 3 Lápidas Grandes a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los Elementos: Roca",
  },
  [712] = {
    ["D"] = "$N, necesito una parte más de los elementales aquí en las Tierras Inhóspitas: las brazales de unión de roca de los elementales de roca superiores. Según mis cálculos, esas brazales deberían ser suficientes para medir la fuerza de mis propias cadenas.$B$BMis intentos de escudriñar a los elementales de roca superiores mostraron un grupo de ellos al suroeste de aquí, cerca de algunos ogros, si no recuerdo mal.$B$BTráeme 5 de sus brazales para tener extras. Servo es conocido por perder mis reactivos e investigaciones.",
    ["O"] = "Lleva 5 Brazales de Atadura de Roca a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Estudio de los Elementos: Roca",
  },
  [713] = {
    ["D"] = "Déjame contarte algo sobre el Aceite de Escarcha: nada puede evitar que un motor o engranajes se sobrecalienten como él.$B$BSus propiedades refrigerantes son extraordinarias, y su longevidad como lubricante no tiene igual. Eso no es todo: también ayuda a retener los encantamientos en los objetos mágicos.$B$BNecesito un vial de él para aumentar la capacidad mágica de mis grilletes y hacerlos lo suficientemente poderosos como para evitar que nuestro amigo invocado nos mate a todos.$B$BBro\'kin, un amigo goblin mío, puede enseñarte a hacerlo. Está en Alterac.",
    ["O"] = "Encuentra Aceite Gélido y llévaselo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Los Refrigerantes Prevalece",
  },
  [714] = {
    ["D"] = "$N, has hecho tu trabajo más allá de todos los estándares.$B$B<Lotwil está obviamente distraído por su trabajo.>$B$BSi alguna vez nos encontramos en Kharanos, no dudes en venir a solicitar trabajo.$B$B¡Ups! Rápido, agarra el... sí, eso. Gracias.$B$BAhora solo necesito una cosa más. Es una parte pequeña, pero bastante crucial, me temo: un girocronátomo.$B$BSi pudieras ir a buscar uno y traérmelo, debería estar listo para completar esto.",
    ["O"] = "Lleva un Girocronátomo a Lotwil Veriatus en las Tierras Inhóspitas.",
    ["T"] = "Giro... ¿Qué?",
  },
  [715] = {
    ["D"] = "Creo que esos objetos que conseguiste para Lotwil van a ser lo menos preocupante, $N. Por otro lado, has demostrado ser más que competente en las ciencias que Lotwil considera sagradas, así que, ¿qué tal si me ayudas?$B$BNecesito una poción de invisibilidad y una poción de curación, ambas podrían ser útiles muy pronto si él logra esto.$B$BComo recompensa, te daré esta receta para una poción que he estado desarrollando mientras aprendía de Lotwil. Está basada en algunas de las investigaciones que él ha estado haciendo aquí en las Tierras Inhóspitas.",
    ["O"] = "Lleva una Poción de Curación y una Poción de Invisibilidad Menor a Lucien Llaveinglesa en las Tierras Inhóspitas.",
    ["T"] = "Piedra Líquida",
  },
  [716] = {
    ["D"] = "Sé lo tacaño que puede ser Lotwil, $N, así que déjame hacerte algo por todos tus problemas. Tal vez incluso te ayude a protegerte de la próxima atrocidad que es el nuevo invento de Lotwil.$B$BHe estado trabajando con todas las piedras extra que recolectaste para él, y puedo hacer unas bonitas brazales que quizás quieras usar. Están encantadas, por supuesto.$B$BSolo necesitaré brazales de bronce con diseño fabricados por un herrero para poder completar las brazales que estoy haciendo para ti.",
    ["O"] = "Lleva unos Brazales de Bronce con Diseño a Lucien Llaveinglesa en las Tierras Inhóspitas.",
    ["T"] = "La Piedra es Mejor que la Tela",
  },
  [717] = {
    ["D"] = "Se ha vuelto obvio que no podemos asegurar que Blacklash y Hematus permanezcan encarcelados en Lethlor. Que los dragones negros hayan tomado residencia allí, y que los ogros hayan robado el Signo indica que se tendrán que tomar medidas más drásticas.$b$bLa prisión de los drakes es mantenida por tres piedras rúnicas que solo pueden ser recuperadas usando el Signo de la Tierra. Esas piedras rúnicas pueden entonces ser usadas para romper el sello que los aprisiona.$b$b$n... asegúrate de que sus sueños sean eternos.",
    ["O"] = "Usa el Signo de la Tierra para activar los Pilares de Amatista, Ópalo y Diamante y obtener las Piedras Rúnicas.$b$bColoca las piedras rúnicas en el Sello de la Tierra para liberar a Azotador y Hematus.$b$bMátalos y devuelve las Ataduras de Azotador, las Cadenas de Hematus y el Signo de la Tierra a Garek.",
    ["T"] = "Temblores de la Tierra",
  },
  [718] = {
    ["D"] = "En nuestro apuro por abandonar el sitio de excavación de Uldaman, nos vimos obligados a abandonar muchos carros y cajas de suministros en el camino. Los ogros locales del Campamento Kosh han estado sobre nuestro equipo abandonado como buitres sobre un cadáver podrido. Tampoco huelen mucho mejor. Que los cadáveres, quiero decir.$b$bDe todos modos, se llevaron el carro que llevaba todas nuestras armas y demás... así que—bueno, esto es un poco vergonzoso—no hemos podido conseguir comida.$b$bEstamos en un pequeño aprieto aquí, ¿supongo que podrías ayudarnos?",
    ["O"] = "Recupera la Caja de Suministros para Sigrun Ferroforja.",
    ["T"] = "Espejismos",
  },
  [719] = {
    ["D"] = "Oye, $N. Ten cuidado por aquí. Hay enanos Hierro Negro por todas partes, parecían del clan Forjatiniebla.$B$BAcaban de atacar el sitio de excavación en el que estaba trabajando, mataron a casi todos allí, incluido mi jefe Hammertoe. Apenas escapé con vida.$B$BEl sitio está justo al norte de aquí, y estoy tratando de planear una manera de recuperar algunos de nuestros suministros, especialmente mi pico de la suerte.$B$B¿Crees que podrías ayudarme? ¡Uno de esos malditos Hierro Negro debe tener mi pico! Podría ser cualquiera de ellos.",
    ["O"] = "Encuentra el Pico Afortunado de Ryedol y devuélveselo al Prospector Ryedol al sur de la Excavación de Hammertoe.",
    ["T"] = "Un Enano y sus Herramientas",
  },
  [720] = {
    ["D"] = "<Hay un texto escrito toscamente en tiza en el mapa.>$B$B\"Ryedol, $BMe llevan a Uldaman. Envía ayuda.$B-Hammertoe\"",
    ["O"] = "Encuentra al Prospector Ryedol y hazle saber que Hammertoe Grez está vivo.",
    ["T"] = "Una Señal de Esperanza",
  },
  [721] = {
    ["D"] = "$N, ¿puedes encontrarlo? ¿Puedes encontrar a Piemartillo en Uldaman?$B$BSi esos sucios Forjatiniebla lo tienen como rehén para descubrir lo que sabe sobre las excavaciones de los titanes, entonces todo Forjaz podría estar en un gran problema.$B$BDurante mucho tiempo, esos Hierro Negro han estado buscando magias para hacer golems más poderosos. Piemartillo estaba haciendo lo mismo, por supuesto, pero solo para detenerlos.$B$BProbablemente lo mantengan con vida hasta que aprendan lo que sabe. ¡Tienes que darte prisa si vas a salvarlo!",
    ["O"] = "Encuentra a Dedoduro Grez en Uldaman.",
    ["T"] = "Una Señal de Esperanza",
  },
  [722] = {
    ["D"] = "Eres valiente... <tos>... por venir a Uldaman por mí, $c.$B$BSi realmente estás aquí para ayudar... al viejo Hammertoe, entonces... <tos>... necesitarás encontrar... mi amuleto.$B$BDeepshadow lo tiene... Magregan Deepshadow. Está por aquí... en las ruinas, seguro. Ese amuleto lo llevará... <tos>... directamente a los pergaminos si no lo detenemos. Depende de ti, $N. Por favor... consigue ese amuleto.$B$BLos pergaminos seguramente ayudarán a los Hierro Negro a crear golems aún más poderosos. Lo sé. Lo siento en mis huesos.",
    ["O"] = "Encuentra el Amuleto de Dedoduro y devuélveselo en Uldaman.",
    ["T"] = "Amuleto de los Secretos",
  },
  [723] = {
    ["D"] = "<Hammertoe sostiene sus heridas y toma un momento para recuperar la compostura antes de hablar de nuevo.>$B$BEncuentra a Ryedol, $N. Llévale mi amuleto. <tose tose> Él... <tose> sabrá qué hacer. Sabe que estaba buscando los orígenes de nuestra gente, y él... <tose>... sabe que también era mi misión detener al clan Forjatiniebla. No se les debe permitir encontrar los pergaminos... <tose>",
    ["O"] = "Lleva el Amuleto de Dedomartillo al Prospector Ryedol en las Tierras Inhóspitas.",
    ["T"] = "Perspectiva de Fe",
  },
  [724] = {
    ["D"] = "Vale, vale... ¿quién sabría qué hacer?$B$B¡El historiador Karnik! ¡Eso es!$B$BEstá en Forjaz. Él podrá ayudar. Él y Hammertoe estaban trabajando juntos para encontrar los artefactos perdidos en Uldaman.$B$BSupongo que los Forjatiniebla se enteraron de que los dos sabían más de lo que decían.$B$BCuéntale a Karnik lo que le pasó a Hammertoe y dale el amuleto, él te dirá qué hacer a continuación.",
    ["O"] = "Lleva el Amuleto de Dedomartillo al Historiador Karnik en Forjaz.",
    ["T"] = "Perspectiva de Fe",
  },
  [725] = {
    ["D"] = "$N, corre y encuentra al Consejero Belgrum lo más rápido que puedas. Dile lo que has presenciado y pregúntale cómo quiere que procedamos. Esta nota le explicará más.$B$BMientras tanto, me tomaré el tiempo para hablar con el espíritu de Piemartillo. Quizás él sepa algo que nos ayude a detener las actividades de esos cretinos Forjatiniebla en las Tierras Inhóspitas. Solo podemos rezar.",
    ["O"] = "Encuentra al Consejero Belgrum y entrégale su nota.",
    ["T"] = "Transmitiendo Palabra de una Amenaza",
  },
  [726] = {
    ["D"] = "<El Consejero Belgrum llega al final de la nota y sus ojos se abren por un momento.>$B$BAy, estas son noticias malvadas.$B$B$N, por favor, regresa con el Historiador Karnik y dile, con la autoridad del rey, que actúe sobre esta información rápida y decisivamente. Debe detener las actividades de Forjatiniebla en las Tierras Inhóspitas.",
    ["O"] = "Habla con el Historiador Karnik.",
    ["T"] = "Transmitiendo Palabra de una Amenaza",
  },
  [727] = {
    ["D"] = "La tableta dice mucho. ¡Pero no lo suficiente! ¡No lo suficiente!$B$BHay un libro, un compendio que Yagyin escribió en épocas pasadas. La... gente de la que huí... guardan este libro en muchos lugares. Y sus más eruditos lo estudian. Contiene vastos conocimientos. ¡Conocimientos para protegernos!$B$BVe a Gerrig Apretahuesos. Está en una tienda en Forjaz, en la Caverna Abandonada. Muéstrale este sigilo y pensará que eres uno de ellos. Pídele el Compedio de Yagyin... y tráemelo.",
    ["O"] = "Dale el Sigilo del Martillo a Gerrig Agarrahuesos en la Caverna Abisal de Forjaz.",
    ["T"] = "A Forjaz por el Digest de Yagyin",
  },
  [728] = {
    ["D"] = "La tableta dice mucho. ¡Pero no lo suficiente! ¡No lo suficiente!$B$BHay un libro, un compendio que Yagyin escribió en épocas pasadas. La... gente de la que huí... guardan este libro en muchos lugares. Y sus más eruditos lo estudian. Contiene vastos conocimientos. ¡Conocimientos para protegernos!$B$BVe al Guardián Bel\'dugur en la Entrañas de Lordaeron. Muéstrale este sigilo y pensará que eres uno de ellos. Pídele el Compedio de Yagyin... y tráemelo.",
    ["O"] = "Lleva el Sello del Martillo al Guardián Bel\'dugur en Entrañas.",
    ["T"] = "A Entrañas por el Digest de Yagyin",
  },
  [729] = {
    ["D"] = "Qué honor fue cuando el Maestro Greywhisker me asignó trabajar bajo la guía del gran Prospector Remtravel. En la academia de Forjaz todos conocían los grandes descubrimientos de Remtravel.$B$BPero el prospector es bastante... um... despistado... con su entorno.$B$BDescubrimos evidencia de una gran sociedad. Horribles gólems surgieron del suelo y arrasaron el sitio. Remtravel nunca pareció darse cuenta. Corrí de vuelta a Auberdine en busca de ayuda.$B$BPor favor, viaja al sur y ve si el prospector está bien.",
    ["O"] = "Viaja al sur y comprueba el estado del Prospector Remtravel.",
    ["T"] = "El Prospector Distraído",
  },
  [730] = {
    ["D"] = "Qué agradable es ver a un $c interesado en las grandes maravillas arqueológicas de nuestro mundo.$B$BA menudo nuestro trabajo es descartado como un mero pasatiempo por nuestros amigos en la Alianza. Pero lo que muchos no se dan cuenta es que los descubrimientos recientes en Khaz Modan han demostrado que una fuerza grande y poderosa amenaza a todo Azeroth, desde Lordaeron hasta Kalimdor.$B$BEstoy muy preocupado por mi tripulación que envié a Costa Oscura. No han enviado noticias en semanas.$B$BViaja a Auberdine y busca una pista sobre su paradero.",
    ["O"] = "Viaja a Auberdine y busca señales del equipo de excavación enano.",
    ["T"] = "¿Problemas en Costa Oscura?",
  },
  [731] = {
    ["D"] = "Entonces, Hollee... oh, cierto, tú no eres Hollee. ¿Dónde está Hollee?$b$bBah, eso no importa. Debemos encontrar ese pincel de piedra... quiero decir, ¡el fósil misterioso! Debemos encontrar ese fósil. La Liga de Expedicionarios en Darnassus querrá alguna indicación de lo que he estado haciendo.$b$bAvísame cuando estés listo y cazaremos ese pincel... ¡quiero decir fósil! Encontraremos algo por escrito para satisfacer a la Liga.$b$b¿Así que estás listo?",
    ["O"] = "Protege al Prospector Viajero mientras busca el fósil misterioso, luego regresa con la Arqueóloga Hollee en Auberdine.",
    ["T"] = "El Prospector Distraído",
  },
  [732] = {
    ["D"] = "Escuché de Sigrun que has sido de gran ayuda para él. Quizás yo también pueda contar con tu ayuda. Fui enviado aquí por mi maestro, Krasus de los Kirin Tor, para poner fin a los problemas que están surgiendo en la Quebrada Lethlor.$b$bNo puedo revelar la naturaleza exacta de mi misión aquí o las tareas que debo realizar, pero te aseguro que es de vital importancia.$b$bUna banda de ogros errantes, a veces vistos cerca del Campamento Boff, robó un objeto llamado el Signo de la Tierra, debo tenerlo. Tráemelo y te contaré más.",
    ["O"] = "Recupera la Señal de la Tierra para Garek.",
    ["T"] = "Temblores de la Tierra",
  },
  [733] = {
    ["D"] = "Va a ser difícil seguir aquí si no consigo algunos materiales para construir defensas y otras necesidades. He notado que los ogros de la zona parecen estar bien equipados con chatarra. Tal vez las historias de máquinas de asedio en las Tierras Inhóspitas no estaban tan lejos de la realidad...$b$bDe todos modos, el Prospector se ha estado preocupando por el estado de nuestro pequeño campamento, así que me gustaría empezar a trabajar pronto si puedo.$b$bEl campamento principal de ogros está en la Gruta Polvorienta, un buen trecho al oeste de aquí.",
    ["O"] = "Consigue 7 piezas de Chatarra para Sigrun.",
    ["T"] = "Rebuscando",
  },
  [734] = {
    ["D"] = "Maravilloso, maravilloso.$B$B<Lotwil te mira por un momento antes de volver a sus grilletes.>$B$BVe a hablar con Lucien, $N. Necesito un engranaje #5. Tráemelo, ¿quieres?",
    ["O"] = "Habla con Lucien Llaveinglesa en las Tierras Inhóspitas.",
    ["T"] = "Esto Va a Ser Difícil",
  },
  [735] = {
    ["D"] = "¿El Digest de Yagyin, eh? ¿Estás empezando un nuevo capítulo de nosotros en algún lugar? No importa, no es mi lugar preguntar.$B$BTe ayudaré, porque tengo una copia extra. Debemos ayudarnos mutuamente, ¿no? Así es como prosperamos.$B$BNecesito tres objetos de poder. Tráemelos y te daré el digest.$B$BNo diré sus nombres. Puede haber oídos escuchándonos. Pero aquí, los escribiré.",
    ["O"] = "Devuelve los objetos de la Nota de Puño Férreo a Gerrig Puño Férreo en Forjaz.",
    ["T"] = "La Estrella, la Mano y el Corazón",
  },
  [736] = {
    ["D"] = "¿Necesitas el Digest de Yagyin? Entonces, tu plan es reunir otro rebaño. Por supuesto que lo es... y por supuesto que te ayudaré, $ghermano:hermana;.$B$BPero tú también debes ayudarme.$B$BNecesito tres objetos de poder. Tráeme estos y el digest será tuyo.$B$BNo puedo pronunciar los nombres de estos objetos, pero los escribiré.$B$BConsígueme los objetos de esta lista. Si puedes hacer eso, entonces eres verdaderamente digno de abrir tu propio capítulo de nuestra orden.",
    ["O"] = "Lleva los objetos en la Nota de Bel\'dugur al Guardián Bel\'dugur en Entrañas.",
    ["T"] = "La Estrella, la Mano y el Corazón",
  },
  [737] = {
    ["D"] = "Tengo el digesto para ti, $N. No necesito decirte que tengas mucho cuidado con él. Y que lo mantengas lejos de manos enemigas. $B$BEste libro contiene muchas verdades...$B$B...Y la verdad puede quemar.",
    ["O"] = "Lleva el Digestivo de Yagyin a Theldurin.",
    ["T"] = "Conocimiento Prohibido",
  },
  [738] = {
    ["D"] = "El Prospector Agmond estaba a cargo de nuestros sitios de excavación en las Tierras Inhóspitas, al suroeste. Digo \"estaba\" porque... bueno, no he sabido de él en semanas. Necesito que encuentres a Agmond para que pueda obtener su informe.$B$BLa última vez que supe de Agmond, estaba en el sitio de excavación más al sur de las Tierras Inhóspitas, una pequeña operación más allá de la Fortaleza de Angor. Comenzaría tu búsqueda por él allí.",
    ["O"] = "Encuentra a Agmond.",
    ["T"] = "Encuentra a Agmond",
  },
  [739] = {
    ["D"] = "Las huellas de Agmond provienen del oeste. Es fácil deducir por las manchas de sangre en las rocas y la arena que el enano arrastró una distancia.$B$BY aquí murió.$B$BTras una inspección cercana, la palabra \"Murdaloc\" está garabateada en un parche de arenisca de las Tierras Inhóspitas.$B$BGarabateado junto a la mano muerta de Agmond.",
    ["O"] = "Mata al asesino de Agmond, Murdaloc.$B$BMata 12 Quebrahuesos de las Cámaras.$B$BInforma al Prospector Vetaferro en Loch Modan.",
    ["T"] = "Murdaloc",
  },
  [741] = {
    ["D"] = "¿Así que el prospector quiere enviar el fósil misterioso a la Liga de Expedicionarios en Darnassus? No me siento bien dejando Costa Oscura sin él.$b$bAquí, $n, entrega el fósil misterioso al Arqueólogo Jefe Greywhisker en Darnassus.",
    ["O"] = "Lleva el fósil misterioso al Arqueólogo Jefe Bigotigris en Darnassus.",
    ["T"] = "El Prospector Distraído",
  },
  [742] = {
    ["D"] = "¡Atención, jóvenes aventureros! ¡Las tierras salvajes de Vallefresno te esperan!$B$BLa Horda ha establecido una fuerte presencia en las tierras al norte de los Baldíos. Nuestros dos puestos avanzados, el Puesto del Hachazo y el Puesto de Zoram, luchan por traer gloria a la Horda. Aquellos que quieran demostrar su valía deben buscar orientación allí. ¡Notablemente, Senani Corazón de Trueno en el Puesto del Hachazo, directamente al norte de los Baldíos, busca aventureros dispuestos a participar en una gran cacería en Vallefresno!",
    ["O"] = "Habla con Senani Corazón de Trueno en el Puesto del Hachazo en Vallefresno.",
    ["T"] = "La Cacería de Vallefresno",
  },
  [743] = {
    ["D"] = "Gracias por tomarte el tiempo de hablar conmigo, $N. Soy Ruul, guerrero y maestro.$B$BObviamente has alcanzado una edad en la que debes prepararte para tus pruebas si deseas aventurarte más allá de Mulgore.$B$BSi deseas una prueba de tu fuerza, comienza buscando a las arpías Viento Furioso al sureste. Anidan a lo largo de los bordes de la montaña, lejos del camino.$B$BSon uno de nuestros enemigos naturales aquí en Mulgore y serán una buena medida de tu habilidad.",
    ["O"] = "Lleva 8 Garras Viento Furioso a Ruul Aguila del Alba en Sangrapellejo.",
    ["T"] = "Peligros de los Furia del Viento",
  },
  [744] = {
    ["D"] = "$N, mi hermano pronto se presentará ante el Jefe Sangre de Buey, y es un honor para mí hacer su tocado.$B$BQuiero pedirte un favor mientras termino de curtir estas correas de cuero. No tengo tiempo para encontrar suficientes plumas, y me preguntaba, ¿podrías conseguir más para mí?$B$BPuedes encontrar plumas del tamaño correcto de las arpías que residen muy al norte y noroeste de Cima del Trueno. Solo necesito 6 plumas azules y 6 plumas bronce para completar el diseño.",
    ["O"] = "Recolecta 6 Plumas Azur y 6 Plumas Bronce, y llévaselas a Eyahn Garra de Águila en Cima del Trueno.",
    ["T"] = "Preparación para la Ceremonia",
  },
  [745] = {
    ["D"] = "$N, hay muchos conflictos que hieren esta tierra. Espero que no seas testigo de tantos como yo. Sin embargo, hay uno que me gustaría que buscaras. Te dará una idea de lo terrible que puede ser, si no se controla, incluso una pequeña amenaza para la tierra.$B$BLos gnolls Palemane se han establecido al sur de Bloodhoof y en una cueva al oeste. No respetan nuestros intentos de comunicarnos con ellos, y matan a la fauna de Mulgore sin control.$B$BLas palabras ya no son la respuesta.",
    ["O"] = "Mata 10 Desolladores Crines Pálidas, 8 Pellejeros Crines Pálidas y 5 Furtivos Crines Pálidas, luego regresa con Baine Pezuña de Sangre en la Aldea Pezuña de Sangre.",
    ["T"] = "Compartiendo la Tierra",
  },
  [746] = {
    ["D"] = "Hay enanos preparando un sitio de excavación al noroeste. Creen que los secretos yacen en la tierra, y eso es cierto, pero cavar y profanar la tierra no es la manera de ganar sus enseñanzas.$B$BRecoge las herramientas de excavación enanas, destrúyelas y regresa a mí con las herramientas rotas de los enanos.$B$BPuedes destruir las herramientas en una forja. Encontrarás una en Cima del Trueno, pero cualquier forja, incluso una forja en el campamento enano, servirá.",
    ["O"] = "Recolecta 5 Picos de Prospector.$B$BEn una forja, rompe los Picos de Prospector para crear Herramientas Rotas.$B$BLleva 5 Herramientas Rotas a Baine Pezuña de Sangre en Poblado Pezuña de Sangre.",
    ["T"] = "Excavación Enana",
  },
  [747] = {
    ["D"] = "Tienes un aire prometedor y te has ganado el respeto de la tribu. Quizás algún día pronto serás bienvenido en la gran ciudad de Cima del Trueno. Pero antes de que eso suceda, debes demostrarle tu valía a mi padre, el Jefe Viento de Halcón.$b$bAquí en la Meseta Nube Roja nos enorgullecemos de nuestras habilidades de caza. Los tauren cazan por necesidad y por deporte. Nuestro suministro de carnes se ha agotado y necesitamos plumas para la ropa. Caza a los zancudos cercanos y demuestra tu valía reabasteciendo la aldea.",
    ["O"] = "Grull Vientohalcón en Campamento Narache quiere que le traigas 7 Plumas de Correllanos y 7 trozos de Carne de Correllano.",
    ["T"] = "La Cacería Comienza",
  },
  [748] = {
    ["D"] = "¡Los goblins y sus sirvientes han contaminado nuestros pozos de agua sagrados! No podemos permitir esto.$B$BPara limpiar cada pozo, debo crear un tótem de purificación, luego debes llevar el tótem al pozo y realizar un ritual de purificación.$B$BPrimero crearemos un tótem de purificación para el Pozo Pezuña Invernal. Para hacer esto, la tierra debe ofrecer su ayuda. Caza lobos de la pradera por sus patas y avestruces adultos por sus garras. Están al suroeste.$B$BRegresa a mí con las patas y las garras, $N.",
    ["O"] = "Lleva 6 Patas de Lobo de la Pradera y 4 Garras de Zancudo de la Llanura a Mull Cuernoatronador en Sangrapellejo.",
    ["T"] = "Agua Envenenada",
  },
  [749] = {
    ["D"] = "Hace unos días, encontramos una caravana de Venture Co. viajando bajo custodia en la orilla norte del Lago Toro de Piedra. Habíamos perdido a dos de los nuestros en una batalla anterior, así que atacamos rápidamente y arrasamos con la caravana.$B$BDejamos sus cajas de suministros, pensando que nuestras antorchas destruirían cualquier cosa de valor, pero dado que los salvadores de Venture Co. han sido vistos cerca de los carromatos quemados, tal vez nos equivocamos.$B$BQuizás podrías ir a la caravana y examinar el contenido de las cajas.",
    ["O"] = "Morin Acechanubes quiere que examines el contenido de una caja de suministros en la Caravana Devastada.",
    ["T"] = "La Caravana Devastada",
  },
  [750] = {
    ["D"] = "Un tauren hábil en las formas de la caza sabe que su presa no es solo un trofeo. Las bestias de las llanuras nos proporcionan un medio de supervivencia. Dejarás una gran impresión en los ancianos si puedes traer algunas pieles de puma de montaña muy valoradas. Puedes encontrar a las bestias al acecho en las colinas al sur.$B$BNuestros hijos necesitan ropa y nuestras tiendas necesitan reparaciones.",
    ["O"] = "Grull Vientohalcón en Campamento Narache quiere que le traigas 10 Pieles de Puma de Montaña.",
    ["T"] = "La Cacería Continúa",
  },
  [751] = {
    ["D"] = "Al abrir la tapa, se revela una colección bien empaquetada de minerales y metales, más evidencia de la operación minera de la Compañía Cosechadora en Mulgore. Encima de los minerales y metales, retiras un fajo de papeles bien atado.$b$bEn las colinas al este, apenas puedes escuchar los sonidos del trabajo y ver el humo que se eleva desde un gran fuego.",
    ["O"] = "Devuelve los Documentos de Venture Co. a Morin Acechanubes cerca de Aldea Sangrante.",
    ["T"] = "La Caravana Devastada",
  },
  [752] = {
    ["D"] = "Todos los miembros de la tribu comparten la armonía de la vida. Vivimos juntos y trabajamos juntos.$B$BNuestro compromiso mutuo conlleva un alto grado de responsabilidad. Te pido ahora una tarea humilde.$B$BMi madre salió esta mañana a buscar agua del pozo al sureste de Narache. Ha pasado bastante tiempo. ¿Podrías comprobar cómo está mientras yo me ocupo de los asuntos tribales aquí?",
    ["O"] = "Jefe Halcón Viento quiere que busques a su madre cerca del pozo de agua al sureste de Campamento Narache.",
    ["T"] = "Una Tarea Humilde",
  },
  [753] = {
    ["D"] = "He recorrido muchos caminos en la vida y estas viejas piernas carecen del vigor que una vez tuvieron. Aún puedo cumplir con mis deberes para la tribu. A veces solo toma un poco más de tiempo a una anciana realizar la tarea.$b$bPero pareces un $c ansioso. Pongamos un poco de esa vitalidad juvenil a prueba. Toma una jarra de agua del pozo y llévasela a mi hijo, el Jefe, en el Campamento Narache.$b$bRecuerda que incluso la tarea más humilde puede ganar el reconocimiento de los ancianos.",
    ["O"] = "Toma una Jarra de Agua del pozo. Devuelve la jarra al Jefe Hawkwind en el Campamento Narache, que está al noroeste del pozo.",
    ["T"] = "Una Tarea Humilde",
  },
  [754] = {
    ["D"] = "He creado el tótem para purificar el Pozo Pezuña Invernal. Ahora, debes llevarlo al pozo y realizar un ritual de purificación. Lo encontrarás al sureste, ¡custodiado por malvados goblins!$B$BEsta no será una tarea fácil, pero debes hacerlo si alguna vez queremos usar sus aguas nuevamente.$B$BBuena suerte, $N.",
    ["O"] = "Usa el Tótem de Purificación de Pezuña Invernal en el Pozo de Agua de Pezuña Invernal, luego regresa con Mull Tronacuerno.",
    ["T"] = "Purificación de Pezuña Invernal",
  },
  [755] = {
    ["D"] = "Tu disposición a realizar una tarea humilde para los tauren de Narache y tu entusiasmo por aprender son rasgos nobles, $n. Creo que algún día serás aclamado en Cima del Trueno como un $c de grandeza.$b$bAntes de eso, debes embarcarte en los Ritos de la Madre Tierra, de los cuales hay tres.$b$bLa primera prueba es el Rito de la Fuerza. Viaja al Vidente Lenguagris y dile que el Jefe Vientó Halcón te ha enviado.$b$bEncontrarás la morada del vidente directamente al sur del Campamento Narache, escondida en las colinas.",
    ["O"] = "Viaja al Vidente Lenguagris que vive en las colinas directamente al sur del Campamento Narache.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [756] = {
    ["D"] = "Para crear el Tótem de Purificación de Cuernoatroz, debes recolectar las garras de los depredadores de las llanuras de Mulgore. Caza a los acechadores de las praderas por sus garras de acechador y a los pumas de las llanuras por sus garras de puma, luego regresa conmigo.$B$BEncontrarás a los acechadores de las praderas y a los pumas de las llanuras al este y al oeste.",
    ["O"] = "Lleva 6 Garras de Acechador y 6 Garras de Puma a Mull Cuernoatronador.",
    ["T"] = "Tótem de Cuerno de Trueno",
  },
  [757] = {
    ["D"] = "Los Ritos de la Madre Tierra son los pasos que un joven tauren debe seguir para ganar respeto en Cima del Trueno.$B$BPrimero debes pasar el Rito de la Fuerza. En esta prueba debes demostrar tu valentía matando enemigos de la tribu.$B$BLos Jabaespines de la Quebrada de las Espinas al este están invadiendo nuestras tierras tribales. Emboscan a nuestros grupos de caza y roban de la aldea bajo la oscuridad.$B$B¡Muestra tu valentía matando a estos villanos y regresa con el Jefe en el Campamento Narache con sus cinturones como prueba de tus hazañas!",
    ["O"] = "Mata a los Pezuña de Púa en la Hondonada Hoja de Zarza y lleva 12 Cinturones Pezuña de Púa al Jefe Hawkwind en Campamento Narache.",
    ["T"] = "Rito de Fuerza",
  },
  [758] = {
    ["D"] = "El tótem está listo, $N. Y de nuevo, es tu tarea realizar el ritual de purificación.$B$BVe al Pozo de los Cuernos de Trueno, al norte de la aldea, y usa el Tótem de Purificación de los Cuernos de Trueno en tu ritual. Después de hacer esto, regresa conmigo.$B$BQue los espíritus te guíen.",
    ["O"] = "Usa el Tótem de Purificación de Cuernoatronador en el Pozo de Agua de Cuernoatronador, luego regresa con Mull.",
    ["T"] = "Purificación de Cuerno de Trueno",
  },
  [759] = {
    ["D"] = "Solo queda un pozo de agua por purificar, nombrado en honor al clan Crin Salvaje. Para que este pozo sea puro, la tierra debe ofrecer los dientes de un feroz depredador, el lobo alfa de la pradera. Encuentra a los alfas al norte, luego regresa conmigo cuando tu caza haya terminado.$B$BLos alfas a menudo deambulan alrededor de la base de nuestra poderosa ciudad, Cima del Trueno.",
    ["O"] = "Lleva 8 Dientes de Alfa de la Pradera a Mull Cuernoatronador en Sangrapellejo.",
    ["T"] = "Tótem de Crin Salvaje",
  },
  [760] = {
    ["D"] = "Tu purificación de los pozos de los Pezuña Invernal y los Tronacuerno es hablada entre los espíritus, joven $N. El Tótem de Bravacrín está hecho, y tu última tarea está por delante.$B$BEl Pozo de Agua Bravacrín está al norte de Cima del Trueno. ¡Realiza el ritual! ¡Sana la tierra de los venenos de los goblins! ¡Deja que el agua limpia fluya una vez más!",
    ["O"] = "Usa el Tótem de Purificación de Bravacrín en el Pozo de Agua de Bravacrín, luego regresa con Mull Tronacuerno.",
    ["T"] = "La Purificación de Bravacrín",
  },
  [761] = {
    ["D"] = "El aguilón es un pájaro astuto y difícil de encontrar y cazar. Una colección de Plumas de Aguilón es una insignia de astucia y determinación para un cazador.$B$BSi estás dispuesto a asumir esta tarea, entonces entra en las llanuras y caza aguilones.$B$BTráeme sus plumas y trae honor a tu clan.$B$BLos aguilones pueden encontrarse en cualquier lugar de Mulgore, pero tus ojos deben ser agudos para detectarlos y tus pezuñas deben ser rápidas para atraparlos.",
    ["O"] = "Lleva 8 Plumas de Aguilón a Harken Tótem de Viento en Poblado Pezuña de Sangre.",
    ["T"] = "Caza de Aguilones",
  },
  [762] = {
    ["D"] = "El Embajador Infernus fue enviado a la Fortaleza de Angor por el propio Ragnaros para supervisar a los Forjatiniebla que buscan artefactos perdidos en Uldaman.$B$BYa ha significado tremendas bajas para nuestra gente, pero obviamente ha empeorado. Dudo en adivinar cuántos artefactos podrían encontrar y usar contra Forjaz.$B$BNuestro objetivo es simple, $c. Regresa a las Tierras Inhóspitas y destruye al Embajador Infernus. Una vez que la amenaza sea eliminada, regresa con el Consejero Belgrum con una prueba de tu victoria.$B$BEstá en tus manos ahora.",
    ["O"] = "Mata al Embajador Infernus en la Fortaleza de Angor y lleva prueba de su muerte al Consejero Belgrum en Forjaz.",
    ["T"] = "Un Embajador del Mal",
  },
  [763] = {
    ["D"] = "Para que continúes con los Ritos de la Madre Tierra, debes pasar dos pruebas más. Es hora de que amplíes tu experiencia, $n.$b$bViaja a la Aldea Sangre de Buey y busca al Jefe, Baine Pezuña de Sangre, hijo de Cairne. Allí podrás continuar tu viaje y ganar la aceptación de los ancianos de Cima del Trueno.$b$bLleva este tótem a Baine. Reconocerá mis tallas y te ayudará en tu camino.$b$bSigue el camino que sale del Campamento Narache y viaja con prisa. No te desvíes o perderás tu camino.",
    ["O"] = "Lleva el Tótem de Hawkwind a Baine Pezuña de Sangre en Aldea Sangre de Toro. Sigue el camino que sale del Campamento Narache.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [764] = {
    ["D"] = "Esto explica la acumulación de empleados y equipos de Venture Co. que hemos visto en Mulgore. Esos goblins... su compañía se está expandiendo demasiado rápido para su propio bien. Es como dicen, muestra a un goblin algo, y puedes escuchar las monedas en el fondo.$b$b Pequeñas cosas codiciosas, son. Desafortunadamente para su empresa, nosotros los tauren no podemos permitirles llevar a cabo sus operaciones en nuestras tierras. Ve a su mina al noreste de la caravana devastada y envíales un mensaje.",
    ["O"] = "Mata 14 Obreros de Venture Co. y 6 Supervisores de Venture Co. para Morin Acechanubes en la Aldea Pezuña de Sangre.",
    ["T"] = "La Venture Co.",
  },
  [765] = {
    ["D"] = "Según los documentos que rescataste de la caravana, Venture Co. creó muchos planes para intentar expulsarnos de nuestras tierras aquí, para que pudieran tener rienda suelta para saquear nuestras tierras.$B$BLas profundidades a las que se hundirían para lograr sus objetivos me disgustan y me horrorizan. Quiero que el cerebro detrás de sus planes sea asesinado. Su nombre es Supervisor Fizsprocket, lo encontrarás en la mina de Venture Co. al este de la caravana devastada. Tráeme su tablero, veremos si encontramos algo útil.",
    ["O"] = "Mata al Supervisor Fizsprocket y devuelve su portapapeles a Morin Acechacielos en el Poblado Pezuña de Sangre.",
    ["T"] = "Supervisor Fizsprocket",
  },
  [766] = {
    ["D"] = "Mi abuelo me contó historias sobre sus batallas con un acechador llamado Mazzranache. Me habló de sus ojos rojos demoníacos, garras afiladas como navajas y mordedura venenosa.$b$bPor fortuna, mientras viajaba por las llanuras, me lo encontré, y me dio un mordisco desagradable en el hombro. Era tan terrible como mi abuelo lo describió. Necesitaré algunas partes de animales difíciles de encontrar para limpiar la infección de su mordedura: un corazón de lobo, un fémur de puma, una escama de acechador y una molleja de aguilón.$b$bDate prisa, el tiempo es esencial.",
    ["O"] = "Lleva un Corazón de Lobo de la Pradera, un Fémur de Puma de la Llanura, una Escama de Zancudo de Llanura y una Molleja de Aguilón a Maur Clamalluvias en el Poblado Pezuña de Sangre.",
    ["T"] = "Mazzranache",
  },
  [767] = {
    ["D"] = "El Rito de la Visión, uno de los Ritos de la Madre Tierra, te ayudará a ganar el respeto de los ancianos de Cima del Trueno.$B$BNuestro pueblo ha aprendido que la tierra es nuestro proveedor más sagrado. Para participar en la visión ritual, debes hablar con el líder espiritual de la aldea, Zarlman Dos Lunas.",
    ["O"] = "Habla con Zarlan Dos Lunas en Aldea Sangre de Toro.",
    ["T"] = "Rito de Visión",
  },
  [768] = {
    ["D"] = "Saludos, joven. Tienes un brillo en tus ojos, puedo decir que estás ansioso por aventurarte de nuevo en las llanuras para la caza. ¡Que traigas honor a tu clan!$B$BTrabajo con las pieles de las bestias, convirtiéndolas en ropa y armadura para la gente de Cima del Trueno.$B$BSi cazas bestias y me traes sus pieles, entonces te haré algo.",
    ["O"] = "Lleva 12 Piezas de Cuero Ligero a Veren Zancaalta en Cima del Trueno.",
    ["T"] = "Recolección de Cuero",
  },
  [769] = {
    ["D"] = "Las bestias kodo de Mulgore son fuertes y resistentes, rasgos muy respetados entre nosotros. Si eres hábil en peletería y deseas el conocimiento para hacer una Bolsa de Piel de Kodo, entonces tráeme suministros para mi oficio.$B$BHaz esto y compartiré mi conocimiento contigo.",
    ["O"] = "Lleva 4 Cuero Ligero y 4 Hilos Gruesos a Veren Zancaalta en Cima del Trueno.",
    ["T"] = "Bolsa de Piel de Kodo",
  },
  [770] = {
    ["D"] = "El pelaje desgarrado de Aullido Fantasma todavía muestra una terrible herida, obtenida en las batallas del lobo contra la Legión Ardiente.$B$BQuizás alguien en el Pueblo de Sangre de Toro debería saber de la muerte de Aullido Fantasma....",
    ["O"] = "Encuentra a alguien que conozca a Aullido Fantasmal.",
    ["T"] = "La Capa Marcada por el Demonio",
  },
  [771] = {
    ["D"] = "Para pasar por el Rito de la Visión, debes reunir los reactivos necesarios para hacer el Agua de los Videntes.$b$bNecesitaré piedras de pozo, que se encuentran alrededor de los pozos de agua en Mulgore. Con ellas combinaré Bellotas de Ámbar, que caen bajo los poderosos árboles de nuestra tierra.$b$bEstos reactivos contienen elementos mágicos, y cuando se elaboran y se consumen antes del fuego tribal, te harán experimentar una visión. Síguela, y te llevará al siguiente paso de esta sagrada búsqueda.",
    ["O"] = "Recolecta 2 Piedras de Pozo y 2 Bellotas Ámbar y llévaselas a Zarlman Dos Lunas en Poblado Pezuña de Sangre.",
    ["T"] = "Rito de Visión",
  },
  [772] = {
    ["D"] = "Ahora te presento el Agua de los Videntes.$B$BCuando estés listo, consume las aguas cerca del fuego tribal. Una vez que hayas ingerido las aguas sagradas, tomará unos momentos antes de que la visión se materialice frente al fuego.$B$BEn ese momento, dependerá de ti seguirla hasta tu destino....",
    ["O"] = "Bebe el Agua de los Videntes frente al fuego tribal en Poblado Pezuña de Sangre y sigue la visión una vez que aparezca.",
    ["T"] = "Rito de Visión",
  },
  [773] = {
    ["D"] = "Para ganar aceptación entre los ancianos de Cima del Trueno, debes completar el Rito de la Sabiduría.$b$bAhora que has pasado el Rito de la Visión, los espíritus ancestrales de las Rocas Rojas te darán la bendición de nuestros ancestros. Solo aquellos que han bebido del Agua de los Videntes pueden obtener la bendición.$b$bViaja al este de Cima del Trueno, a las Rocas Rojas y busca al Espíritu Ancestral, $n.",
    ["O"] = "Viaja a las Rocas Rojas al este de Cima del Trueno y habla con el Espíritu Ancestral.",
    ["T"] = "Rito de Sabiduría",
  },
  [774] = "_",
  [775] = {
    ["D"] = "Ahora serás venerado dentro de nuestra gran ciudad. Cualquier $r digno de completar los Ritos de la Madre Tierra se gana ese honor.$b$bPara tu misión final, busca a nuestro noble líder, Cairne Pezuña de Sangre, que habita en la meseta más alta de Cima del Trueno.",
    ["O"] = "Habla con Cairne Pezuña de Sangre en Cima del Trueno.",
    ["T"] = "Viaje a Cima del Trueno",
  },
  [776] = {
    ["D"] = "Has pasado los Ritos de la Madre Tierra y has ganado tu lugar en Cima del Trueno.$b$bPero para mantener el lugar que tanto te costó conseguir, debes continuar demostrando tu valía ante tu gente.$b$bSomos cazadores, $n. Abajo en Mulgore hay un poderoso kodo llamado Arra\'chea. Muéstrame tus habilidades en el arte del rastreo y la caza trayéndome el Cuerno de Arra\'chea.",
    ["O"] = "Cairne Pezuña de Sangre en Cima del Trueno quiere que le lleves el Cuerno de Arra\'chea.",
    ["T"] = "Ritos de la Madre Tierra",
  },
  [777] = {
    ["D"] = "Vale, aquí tienes.$B$BVoy a estar por aquí... detrás de la cobertura.$B$BSiéntete libre de unirte a mí.",
    ["O"] = "Lleva el Engranaje #5 a Lotwil Veriatus.",
    ["T"] = "Esto Va a Ser Difícil",
  },
  [778] = {
    ["D"] = "¡Jaja! ¡Hecho! ¡Los grilletes están listos!$B$BEs el momento, Lucien. ¡Es el momento, Servo!$B$B¿Estás listo para presenciar la perfecta combinación de alquimia, ingeniería y lo arcano, $N?$B$BInvocaré a un poderoso elemental ahora. Este es un día trascendental para todos nosotros.",
    ["O"] = "Derrota al Guardián Fam\'retor y lleva los Grilletes de Lotwil de Atadura Elemental de vuelta a Lotwil Veriatus.",
    ["T"] = "Esto Va a Ser Difícil",
  },
  [779] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sello de la Tierra",
  },
  [780] = {
    ["D"] = "Los Jabalíes de Batalla del Barranco Brambleblade al este están invadiendo nuestros terrenos de caza tribal. Han sido entrenados para ser maliciosos por los Puyaespina Quillboar, con quienes estamos en guerra.$B$BVe y mata a estas criaturas viles y tráeme algunos hocicos y lomos para que podamos hacer un estofado para nuestros jóvenes.",
    ["O"] = "Grull Vientohalcón en Campamento Narache quiere que mates Jabalíes de Guerra y traigas 8 Hocicos de Jabalí de Guerra y 8 Lomos de Jabalí de Guerra.",
    ["T"] = "Los Jabalíes de Batalla",
  },
  [781] = {
    ["D"] = "Después de evaluar cuidadosamente el mapa, te das cuenta de que lo que descubriste son los planes hechos por el jefe de guerra de los Cerdospín para un asedio extendido en el Campamento Narache.$B$B¡Seguramente el Jefe Viento del Halcón podría usar esta información!",
    ["O"] = "Lleva los Planes de Ataque de los Crines de Púa al Jefe Hawkwind en Campamento Narache.",
    ["T"] = "Ataque al Campamento Narache",
  },
  [782] = {
    ["D"] = "De la alianza de la Horda con el dragón Deathwing durante la Segunda Guerra, sabemos que dos de sus lugartenientes, Blacklash y Hematus, fueron encarcelados en la Quebrada Lethlor, muy al este. ¡Debemos silenciar a nuestros depravados aliados de antaño para restaurar el honor de la Horda!$B$BContratamos a Tho\'grun y su banda de ogros mercenarios para ayudarnos a obtener la Señal de la Tierra, una llave necesaria para abrir la prisión de los dragones. Sin embargo, Tho\'grun nos traicionó, abrumándonos y tomándola para sí. ¡Ve a Campamento Boff y tómala de vuelta!",
    ["O"] = "Mata al Jefe Tho\'grun y lleva la Señal de la Tierra a Gorn en Kargath.",
    ["T"] = "Alianzas Rotas",
  },
  [783] = {
    ["D"] = "Espero que te hayas ajustado bien el cinturón, joven $c, porque hay trabajo por hacer aquí en Villanorte.$B$BNo me refiero a la agricultura.$B$BLos guardias de Ventormenta tienen dificultades para mantener la paz aquí, con tantos de nosotros en tierras lejanas y tantas amenazas acercándose. Y por eso estamos reclutando la ayuda de cualquiera que esté dispuesto a defender su hogar. Y su alianza.$B$BSi estás aquí para responder al llamado, habla con mi superior, el Mariscal McBride. Está dentro de la abadía detrás de mí.",
    ["O"] = "Habla con el Mariscal McBride.",
    ["T"] = "Una Amenaza Interna",
  },
  [784] = {
    ["D"] = "Liderados por el Almirante Proudmoore, los humanos de Kul Tiras invadieron Durotar, violando el pacto hecho por el Jefe de Guerra con Jaina Proudmoore para derrotar a Archimonde hace años.$B$BLa agresión humana fue repelida y el Bastión Tiragarde cayó. Pero recientemente, las reservas del Almirante, lideradas por el Teniente Benedict, han retomado el bastión y una vez más representan una amenaza para nuestra tierra. Estos humanos no respetan la diplomacia.$B$BDemuestra tu honor y viaja al sur al Bastión Tiragarde para eliminar a los invasores humanos.",
    ["O"] = "Mata 10 Marineros de Kul Tiras, 8 Marines de Kul Tiras y al Teniente Benedict y regresa con Gar\'Thok en Cerro del Cuervo.",
    ["T"] = "Vencer a los Traidores",
  },
  [785] = "_",
  [786] = {
    ["D"] = "Baja la voz, $c. Los centauros Kolkar están justo al otro lado de la cresta al oeste en el Despeñadero Kolkar.$B$BAnoche, mientras estaban asaltando, me colé en su aldea y descubrí que esas bestias sucias tienen un ataque de tres niveles planeado contra los trolls y orcos de Durotar.$B$BNo debemos permitir que su invasión se concrete. Tal vez puedas reunir la fuerza necesaria para infiltrarte en el Despeñadero Kolkar y destruir sus planes de ataque.$B$BLa última vez que vi, los habían dividido entre tres de sus líderes.",
    ["O"] = "Lar Colmillo Sigiloso fuera de la Aldea Sen\'jin quiere que destruyas los 3 conjuntos de Planes de Ataque guardados dentro de la Grieta Kolkar.",
    ["T"] = "Frustrando la Agresión Kolkar",
  },
  [787] = {
    ["D"] = "Throm\'ka, $c. Soy Eitrigg, encargado por Thrall de supervisar el entrenamiento de los reclutas.$b$bLa Horda ha cambiado de lo que una vez fue. Hubo un tiempo en que dejé la Horda, desilusionado por la creciente influencia de los peones sedientos de poder de la Legión Ardiente. Durante mi exilio, fui hecho prisionero por un grupo de humanos, pero fui rescatado por el Jefe de Guerra. Al escuchar su visión de la Horda libre de influencia demoníaca, guiada por los chamanes, regresé.$b$bGornek tendrá más instrucciones para ti.",
    ["O"] = "Preséntate ante Gornek en La Guardia.",
    ["T"] = "La Nueva Horda",
  },
  [788] = {
    ["D"] = "El primer orden del día será poner un poco de fuerza en tu columna vertebral. Podría enviarte a los Baldíos a cazar kodos, pero, bueno, para ser honesto, eres más útil para nosotros vivo que muerto.$b$bCreo que encontrarías un buen oponente en los jabalíes moteados que encontrarás al norte de aquí.",
    ["O"] = "Mata 10 Jabalíes Moteados y luego regresa con Gornek en el Cubil.",
    ["T"] = "Cortando Dientes",
  },
  [789] = {
    ["D"] = "Tanto el poderoso guerrero como el novato torpe han caído ante el aguijón venenoso del escórpido. Encontrarás una gran cantidad de escórpidos al noroeste de aquí. Tráeme diez de sus colas como prueba de tu destreza en la batalla.$b$bEl antídoto para su picadura en realidad se hace con veneno extraído de sus aguijones. Mantenemos grandes cantidades de antídoto para el veneno de escórpido a mano para curar a jóvenes como tú...$b$bPero estoy seguro de que no necesitarás nada de eso, ¿verdad?",
    ["O"] = "Consigue 10 Colas de Trabajador Escórpido para Gornek en el Cubil.",
    ["T"] = "Picadura del Escórpido",
  },
  [790] = {
    ["D"] = "$C! Pensé que moriría aquí sin que nadie lo supiera. Mientras cazaba a los escórpidos del Valle, me encontré con uno que parecía particularmente vicioso. Lanzándome sobre él, logré infligir un golpe masivo en su garra antes de que se cerrara alrededor de mi pierna.$B$BNo estaba preparado para su aguijón, que se deslizó hacia abajo y se clavó en mi pecho, cortando mi carne y dejando que mi sangre fluyera.$B$B¡Por favor, debes matar al escórpido por mí! ¡Mi honor debe ser restaurado! Lo combatí en la meseta al sur.",
    ["O"] = "Mata a Sarkoth y lleva su garra de vuelta a Hana\'zua.",
    ["T"] = "Sarkoth",
  },
  [791] = {
    ["D"] = "La edad me ha vuelto inútil en la batalla. Ahora me hago útil de otras maneras.$B$BDesde este punto de observación, vigilo a los invasores. A medida que nuestra fuerza aquí crece, me encuentro tocando el cuerno de señal cada vez menos.$B$BPara pasar el tiempo, fabrico bienes para ayudar a los guerreros más jóvenes y capaces a defender nuestra tierra natal.$B$BPara ti, puedo fabricarte una bolsa para tus pertenencias. Si un artículo así te sería útil, tráeme un poco de lona, un material común entre los humanos y los centauros.",
    ["O"] = "Furl Ceja Fruncida en la torre de vigilancia de Cerro Cortante quiere 8 Retales de Lona.",
    ["T"] = "Lleva tu Peso",
  },
  [792] = {
    ["D"] = "Confío en que el Valle de los Retos te enseñará mucho, joven $c.$B$BFui enviado al Valle para guiarte, pero he descubierto una creciente corrupción aquí...$B$BUn grupo que se hace llamar la Horda de la Llama tiene un aquelarre aquí en el Valle de los Retos. Están al acecho en una cueva al noreste, y sus Familiares Viles han salido de su boca para causar estragos.$B$BComo tu primera tarea contra la Horda de la Llama, te pido que derrotes a estos familiares. Mata a muchos y, si sobrevives, regresa conmigo.",
    ["O"] = "Mata 12 Familiares Viles.$B$BRegresa con Zureetha Miralejos fuera del Cubil.",
    ["T"] = "Familiares Viles",
  },
  [793] = {
    ["D"] = "El Sello de la Tierra está sostenido por tres piedras rúnicas, guardadas en los Pilares de Diamante, Ópalo y Amatista en la Quebrada Lethlor. Usando la Señal de la Tierra, podrás quitar las piedras rúnicas y desbloquear el Sello de la Tierra.$B$BSin embargo, ten cuidado, $n, los dragones negros Latigonegro y Hematus no son para tomarlos a la ligera. Mátalos y regresa con sus ataduras.$B$BPara llegar a la quebrada, viaja a la parte más oriental de las Tierras Inhóspitas. Sin embargo, ten cuidado, ya que la quebrada está llena de innumerables dragones.",
    ["O"] = "Usa el Signo de la Tierra para activar los Pilares de Diamante, Ópalo y Amatista y obtener las Piedras Rúnicas.$b$bColoca las piedras rúnicas en el Sello de la Tierra para liberar a Azotador y Hematus.$b$bMátalos y devuelve las Ataduras de Azotador, las Cadenas de Hematus y el Signo de la Tierra a Gorn.",
    ["T"] = "Alianzas Rotas",
  },
  [794] = {
    ["D"] = "A través de mis adivinaciones, veo que un objeto de poder se esconde en lo profundo del Coven de la Hojarrasa, custodiado por bestias y magia negra.$B$BSe llama el Medallón de la Hojarrasa, y tu próxima tarea es encontrarlo y sacarlo del coven.$B$BPero ten cuidado, porque el medallón puede estar poseído por un agente de la Hojarrasa, y si es así, entonces el poder del agente sería mayor que el de los familiares que ya has encontrado.$B$BVe, $N. Encontrarás el coven en una cueva, al norte y al oeste.",
    ["O"] = "Lleva el Medallón de la Hojarrasa a Zureetha Fargaze, fuera de La Guarida.",
    ["T"] = "Medallón de la Horda de Fuego",
  },
  [795] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sello de la Tierra",
  },
  [796] = "_",
  [797] = "_",
  [798] = "_",
  [799] = "_",
  [800] = "_",
  [801] = "_",
  [802] = "_",
  [803] = "_",
  [804] = {
    ["D"] = "Ver la hazaña que has hecho por mí fortalece mi corazón. ¡No puedo caer tan fácilmente! ¡Debo resistir!$b$bPero sigue siendo que no puedo hacer el viaje de regreso al Cubil sin ayuda. Por favor, $n, regresa al Cubil y dile a Gornek sobre mi situación. Quizás él pueda ayudarme.",
    ["O"] = "Lleva las noticias de la difícil situación de Hana\'zua a Gornek en La Guarida.",
    ["T"] = "Sarkoth",
  },
  [805] = {
    ["D"] = "Tus pruebas contra la Horda de Fuego han terminado... aquí en el Valle. Pero quiero que informes de tus hallazgos.$B$BVe al pueblo troll de Sen\'jin y busca al Maestro Gadrin. El pueblo de Sen\'jin está al este del valle, luego a la derecha en la bifurcación.$B$BCuéntale a Gadrin sobre la Horda de Fuego, y que han llegado al Valle de los Retos. Descubre de él si ya han llegado a Sen\'jin.$B$BVe, $N, y sé rápido. Temo que el mal encontrado en el Coven de la Horda de Fuego es solo el heraldo de una amenaza mayor...",
    ["O"] = "Habla con el Maestro Gadrin en la Aldea Sen\'jin.",
    ["T"] = "Informe a la Aldea Sen\'jin",
  },
  [806] = {
    ["D"] = "¡No podemos permitir que el Filo Ardiente tenga un punto de apoyo en Durotar! Debemos destruirlos antes de que su mal se extienda.$B$BHe realizado mis propias investigaciones y descubrí que un brujo del Filo Ardiente, el goblin Fizzle Darkstorm, ha acampado dentro de la Cresta del Trueno, al noroeste. Allí, él y sus secuaces cultistas esparcen el caos.$B$B¡Encuentra y derrota a Fizzle, y tráeme su garra muerta!",
    ["O"] = "Lleva la Garra de Fizzle a Orgnil Cicatriz del Alma en Cerro Cortante.",
    ["T"] = "Tormentas Oscuras",
  },
  [807] = {
    ["D"] = "Mis exploradores informan que algunos de los animales salvajes se han infectado con una corrupción demoníaca. Deambulan entre sus hermanos como bestias normales, pero el poder maligno fluye a través de ellos. ¡Ardiendo dentro de cada uno hay un corazón chamuscado por la magia oscura! ¡Estas bestias deben ser destruidas!$B$BSi encuentras a un animal así mientras exploras, mátalo y recoge su corazón chamuscado. Tráeme lo que encuentres en tus viajes para estudiarlo y luego los destruiré.",
    ["O"] = "Lleva 5 Corazones Carbonizados a Orgnil Soulscar en Cerro Cortado.",
    ["T"] = "[Deprecado] Limpiar Durotar",
  },
  [808] = {
    ["D"] = "Escucho la voz de mi hermano, Minshina, llamándome en mis sueños.$B$BFue tomado por Zalazane, el brujo en las Islas del Eco al este. Y está muerto.$B$BPero la muerte no es libertad para mi hermano. El espíritu de Minshina fue atrapado dentro de su propio cráneo por las magias de Zalazane. En mis sueños lo veo con otros cráneos, en un círculo de poder en la Isla del Eco más grande. Mientras permanezca allí, el alma de mi hermano está condenada.$B$BPor favor, $N. Encuentra el círculo y recupera el cráneo de Minshina. Tráemelo.$B$B¡Libéralo!",
    ["O"] = "Recupera el cráneo de Minshina del círculo de poder en las Islas del Eco.$B$BLlévaselo al Maestro Gadrin en la Aldea Sen\'jin.",
    ["T"] = "El Cráneo de Minshina",
  },
  [809] = {
    ["D"] = "Desde la última gran guerra, cuando la Legión Ardiente fue derrotada, he buscado fuentes de corrupción demoníaca en la sociedad orca. El collar que me trajiste confirma mis temores.$B$BPertenece a la Espada Ardiente, un culto que se reúne alrededor de un objeto de poder demoníaco. Se llama la Semilla Demoníaca, y reside en los Baldíos en la cima del Pico de la Niebla Temible. ¡Debe ser destruida!$B$BVe al Puesto del Otejo Lejano, en la frontera de los Baldíos al oeste, y habla con mi asistente, Ak\'Zeloth. Él te guiará más.",
    ["O"] = "Habla con Ak\'Zeloth en los Baldíos.",
    ["T"] = "Ak\'Zeloth",
  },
  [810] = {
    ["D"] = "Harías bien en aprender cuán peligrosa puede ser esta tierra, $c. La vida salvaje aquí puede enseñarnos cosas increíbles, si somos lo suficientemente sabios para escuchar sus lecciones.$B$BLos escórpidos son un ejemplo perfecto de supervivencia en Durotar. Mientras la tierra se ha vuelto más dura e implacable, sus caparazones endurecidos los protegen del sol brutal y las armas mortales de los cazadores. Seríamos sabios si adoptáramos algo de su fuerza, ya que ahora dominan el entorno.$B$BTráeme 6 Caparazones de Escórpido Pequeño de los Escórpidos Traqueteantes, $N, y te recompensaré.",
    ["O"] = "Lleva 6 Caparazones de Escórpido Pequeños a Kor\'ghan en la Aldea Sen\'jin.",
    ["T"] = "Estudiar para Sobrevivir",
  },
  [811] = {
    ["D"] = "Una de las otras características de los escórpidos es su feroz lealtad. Se protegerán unos a otros incluso a riesgo personal, probablemente ya hayas visto este comportamiento durante tu primera cacería. Harías bien en prestar atención a esa lección y aplicarla a ti mismo: somos más fuertes como uno, somos débiles cuando estamos divididos.$B$BTráeme 8 Caparazones de Escórpido Grande de Escórpidos Acorazados y te otorgaré otra recompensa.",
    ["O"] = "Lleva 8 Caparazones de Escórpido Grandes a Kor\'ghan en la Grieta de la Sombra.",
    ["T"] = "La Unidad es Fuerza",
  },
  [812] = {
    ["D"] = "$N... tu momento es perfecto. Solo espero poder elogiar tu prisa también.$B$BFui descuidado mientras luchaba contra algunos de los colaviles cercanos, y uno de ellos me picó profundamente. Puedo sentir su veneno debilitándome incluso mientras hablamos. A este ritmo, me quedará quizás una hora de vida. Pero necesitaré tu ayuda si quiero lograrlo...$B$BKor\'ghan en Orgrimmar sabe cómo hacer el antídoto. Encuéntralo... y date prisa, $N. No podré aguantar mucho más. Debería estar en la Grieta de la Sombra.",
    ["O"] = "Encuentra a Kor\'ghan en Orgrimmar y consigue el Antídoto Colaveneno. Luego lleva el antídoto a Rhinag cerca de la frontera noroeste de Durotar.",
    ["T"] = "Necesidad de una Cura",
  },
  [813] = {
    ["D"] = "Los Veneno Cola son algunos de los escórpidos más mortíferos de Durotar. Fue imprudente que Rhinag los cazara sin tener algo del antídoto, pero ahora no es momento de regañarlo.$B$BTráeme unos cuantos sacos de veneno de los Veneno Cola cerca de la entrada de Orgrimmar y haré un antídoto para él.$B$BDate prisa, $N. Uno de los nuestros necesita tu ayuda.",
    ["O"] = "Lleva 4 Sacos de Veneno de Colaveneno a Kor\'ghan en Orgrimmar.",
    ["T"] = "Encontrando el Antídoto",
  },
  [814] = {
    ["D"] = "¡Cocinar y limpiar, eso es todo lo que hago!$B$B¡Si quieres comer, consígueme más carne! No tengo todo el día para cazar y preparar comida para todos estos piojos. Tienes que aprender a hacer tu parte aquí si quieres ser tratado como un igual.$B$B¡Consígueme algunos Trozos de Carne de Jabalí si quieres hacerte útil... o si no quieres morir de hambre!",
    ["O"] = "Lleva 10 Trozos de Carne de Jabalí a la Cociner Torka en Cerro Cortado.",
    ["T"] = "Trabaja por Comida",
  },
  [815] = {
    ["D"] = "¡Bah! Casi olvido que necesito preparar el desayuno.$B$B$N, muévete y consígueme algunos huevos de colazote. Necesitaré al menos tres de ellos si voy a tener suficiente para la comida de mañana.$B$BLos colazotes Garragil pueden ser feroces y proteger sus huevos hasta la muerte más a menudo de lo que no. A veces entierran sus huevos demasiado profundo en la tierra para desenterrarlos de manera segura, pero si te diriges a las Islas del Eco al sureste de la Aldea Sen\'jin, generalmente los encontrarás por todas esas islas.",
    ["O"] = "Lleva 3 Huevos de Colazote a la Cociner Torka en Cerro Cortado.",
    ["T"] = "Rompe Algunos Huevos",
  },
  [816] = {
    ["D"] = "$N, por favor, ¿puedes ayudarme? Mi hijo Kron salió a cazar hace días y aún no ha regresado. Se fue hacia el oeste, hacia los Baldíos, para cazar crocoliscos a lo largo del Río Furia del Sur. Temo lo peor.$B$BAunque era fuerte, su orgullo obstinado siempre lo metía en problemas. Le advertí que los crocoliscos eran poderosos y viciosos. Se enojó y se marchó.$B$BSi no puedes encontrarlo, al menos tráeme una señal de su destino... incluso si tienes que abrir el estómago de cada crocolisco a lo largo del Río Furia del Sur.",
    ["O"] = "Lleva una señal del destino de Kron a Misha Tor\'kren en la granja al noroeste de Cerro Cortante.",
    ["T"] = "Perdido pero no Olvidado",
  },
  [817] = {
    ["D"] = "Muchas de las pieles que usamos provienen de los tigres de Durotar, $N. Mantas, armaduras, tiendas: hay muchas razones por las que cazamos a estas bestias, y muchas razones por las que las dejamos prosperar al mismo tiempo.$B$BHa llegado el momento de reducir la manada, por así decirlo. Nuestros números crecen, y nuestras necesidades están comenzando a abrumar nuestras reservas. Necesito más pieles si voy a preparar bienes adecuados para nuestra gente.$B$BTráeme 4 pieles de tigre de Durotar, y te recompensaré. Puedes encontrarlos en las islas al sur de aquí.",
    ["O"] = "Lleva 4 Pieles de Tigre de Durotar a Vel\'rin Fang en la Aldea Sen\'jin.",
    ["T"] = "Presa Práctica",
  },
  [818] = {
    ["D"] = "Aunque mis ojos fallan, todavía puedo ver lo suficientemente claro. Más a menudo debo confiar en mis habilidades alquímicas para ayudarme en magias que antes me resultaban fáciles. Pero me niego a tomar un aprendiz, ningún troll u orco lo suficientemente digno se ha presentado.$B$B¿Eres digno? Sí, por supuesto que lo eres... por supuesto que crees que lo eres.$B$BNecesito algunas cosas. ¿Las conseguirás para mí?$B$BNecesito ojos de makrura intactos y viales de moco de araña. Puedes encontrarlos en cualquier makrura o araña en Durotar. Hablaremos de nuevo pronto.",
    ["O"] = "Lleva 4 Ojos de Makrura Intactos y 8 viales de Moco de Rondador al Maestro Vornal en la Aldea Sen\'jin.",
    ["T"] = "Un Espíritu Solvente",
  },
  [819] = {
    ["D"] = "Una pequeña placa en uno de los extremos del barril dice:$B$B\"Chen Stormstout-$B$BQue tu espíritu se eleve y siempre eleve tus espíritus.\"",
    ["O"] = "Encuentra a alguien que conozca el Barril Vacío de Chen.",
    ["T"] = "El Barril Vacío de Chen",
  },
  [820] = {
    ["D"] = "Verdaderamente, un espíritu de fuerza reside en tu cuerpo, dispuesto a ayudar a un viejo hombre a preparar sus pociones.$B$BLa generosidad encontrada en nuestro nuevo hogar y la fuerza de confiar en nuestros aliados es lo que nos distingue de los demás, $c.$B$BNecesito una cosa más para comenzar a preparar un lote de mi poción, ocho hierba brillante.$B$BPuedes encontrar las hierbas al noroeste de Colina del Trueno en la Cresta del Trueno, y ten cuidado, $N.",
    ["O"] = "Lleva 8 Hierbaluz al Maestro Vornal en la Aldea Sen\'jin.",
    ["T"] = "¿En Qué Confías?",
  },
  [821] = {
    ["D"] = "¿Te gustaría probar el homónimo de Chen? Ah, la cerveza Trueno es una bebida poderosa. Chen le enseñó la receta a mi mentor, y mi mentor me la pasó a mí. Necesitaré que me consigas algunas cosas, pero te digo esto: la patada vale la pena.$B$BTráeme 5 colmillos de león de la sabana de cualquier león de la sabana, 5 riñones de zancudo de cualquier zancudo y 1 cuerno de lagarto del trueno de cualquier especie de lagarto del trueno. Eso debería funcionar muy bien.$B$BPuedes encontrar estos ingredientes por todo Los Baldíos.",
    ["O"] = "Lleva 5 Colmillos de León de la Sabana, 5 Riñones de Merodeador de las Llanuras y 1 Cuerno de Lagarto del Trueno al Maestro Cervecero Drohn en Ratchet.",
    ["T"] = "El Barril Vacío de Chen",
  },
  [822] = {
    ["D"] = "Buena cosa, esa cerveza Cebatruenos, ¿eh?$B$BTengo otra receta que aprendí de Chen. ¿Te interesaría? No tomará mucho tiempo hacerla, y podría ser útil si planeas seguir aventurándote.$B$BTráeme 5 glándulas de relámpago de cualquier piel de tormenta, 1 glándula de saliva de halcón de trueno de los halcones de trueno mayores y un hígado de kodo de cualquiera de los kodos de Los Baldíos.$B$BComo dije, esto tiene fuerza, es la cerveza trogg que te mencioné antes.",
    ["O"] = "Lleva 5 Glándulas de Relámpago, 1 Glándula de Saliva de Halcón del Trueno y 1 Hígado de Kodo al Maestro Cervecero Drohn en Ratchet.",
    ["T"] = "El Barril Vacío de Chen",
  },
  [823] = {
    ["D"] = "En el pueblo de Sen\'jin no estamos exentos de problemas, y te agradecemos tu ayuda. Pero las noticias que traes sobre el Filo Ardiente podrían ser un problema para todos.$B$BHay un orco estacionado en Cerro Cortado, Orgnil Cicatriz del Alma, que vigila Durotar de males como el Filo Ardiente. Ve a Orgnil y cuéntale de nuestra situación, así como de las noticias que traes del Valle de los Retos. Él querrá saber estas cosas.$B$BCerro Cortado está al norte.",
    ["O"] = "Habla con Orgnil Cicatriz de Alma en Cerro Cortado.",
    ["T"] = "Informe a Orgnil",
  },
  [824] = {
    ["D"] = "El Anillo de la Tierra es un grupo chamánico dedicado a estudiar y preservar los elementos. Han olvidado más sobre viejos dioses locos, leyendas antiguas y lore de lo que la mayoría de los eruditos jamás sabrán.$B$BAfortunadamente para ti, conozco a alguien en el Anillo de la Tierra aquí en Ashenvale. Dirígete al puesto de avanzada de Zoram\'gar en la costa de la Playa de Zoram, muéstrale a Je\'neu Sancrea lo que encontraste. Si alguien puede descifrar el significado de ese globo, sería él.",
    ["O"] = "Lleva el Globo de Agua Contaminado a Je\'neu Sancrea en el Campamento Zoram\'gar, Vallefresno.",
    ["T"] = "Je\'neu del Anillo de la Tierra",
  },
  [825] = {
    ["D"] = "Uno de mis exploradores más observadores trajo noticias de que los restos de la flota de Valiente todavía permanecen frente a la costa de Durotar, justo al este del Bastión de Tiragarde.$b$bNo es ningún secreto que los humanos, en alianza con esas criaturas repugnantes conocidas como gnomos, tienen un conocimiento avanzado de la mecánica. Debemos tener un conocimiento completo de todos nuestros enemigos potenciales. Y nuestra gente también se beneficiará de este nuevo conocimiento.$b$bNada por los restos, $n, y recupérame las herramientas de la Alianza.",
    ["O"] = "Gar\'Thok de Cerro Cortante quiere que recuperes 3 Herramientas Gnomish de los restos del naufragio en la costa.",
    ["T"] = "De los Escombros...",
  },
  [826] = {
    ["D"] = "El brujo Zalazane habita en las Islas del Eco al este. Son las islas que una vez llamamos hogar.$B$BDesde allí envía a sus trolls al continente, para hechizar a nuestra gente y arrastrar a más de ellos bajo su influencia.$B$BDebe ser detenido.$B$BDerrota a Zalazane y sus esbirros, antiguos trolls Lanza Negra, ahora perdidos para nosotros. Tráeme su cabeza y sabré que su reinado de maldad ha terminado.",
    ["O"] = "Derrota a Zalazane.$B$BMata a 8 Trols Vudú y 8 Trols Hechizados.$B$BLleva la Cabeza de Zalazane a Gadrin.",
    ["T"] = "Zalazane",
  },
  [827] = {
    ["D"] = "$N. La Horda de la Hoja Ardiente ha infestado la cueva al este de Orgrimmar conocida como Roca del Cráneo. Dentro de esa cueva realizan rituales viles y queman su propia carne con Collares Ardientes.$B$BAl usar estos collares, creo que los cultistas se sintonizan con el poder demoníaco. Pero para confirmar esto, debo tener una colección de los collares para estudiarlos.$B$BVe a Roca del Cráneo y recoge Collares Ardientes de los cultistas que encuentres allí. Tráemelos, y descubriré sus secretos.",
    ["O"] = "Reúne Collares Abrasadores de los cultistas en Roca del Cráneo.$B$BLlévaselos a Margoz en su campamento.",
    ["T"] = "Roca del Cráneo",
  },
  [828] = {
    ["D"] = "Uno de nuestros chamanes, Margoz, sabe más sobre la corrupción de la Horda. Habla de una cueva llamada Roca del Cráneo en las montañas, ¡justo fuera de Orgrimmar! que alberga a una gran banda de cultistas de la Horda.$B$BAntes de ir a Roca del Cráneo, habla con Margoz. Es sabio y su consejo es valorado.$B$BEstá acampado al noreste, entre la costa y la Barranca Seca.$B$BSigue su consejo, pero sea lo que sea que diga Margoz, $N, ¡aún quiero que aplastes a esos cultistas!",
    ["O"] = "Habla con Margoz.",
    ["T"] = "Margoz",
  },
  [829] = {
    ["D"] = "Los Collares Ardientes que me trajiste son poderosos implementos demoníacos. Descifrar su origen está, me temo, más allá de mis habilidades como chamán. Necesitaremos un brujo para estudiarlos.$B$BLleva un collar ardiente a Neeru Hojafuego. Aunque es un brujo hábil, afirma usar sus poderes para frustrar a los demonios y dice que su investigación en lo oculto es benigna. Sea cierto o no, podríamos necesitar su ayuda contra el culto demoníaco en Durotar.$B$BPuedes encontrar a Neeru en Orgrimmar en la Grieta de la Sombra.",
    ["O"] = "Lleva un Collar de Ejemplo a Neeru Hojafuego en Orgrimmar.",
    ["T"] = "Neeru Hojafuego",
  },
  [830] = {
    ["D"] = "Abres el sobre viejo y desgastado y descubres un documento de aspecto oficial. Reconoces el sello del Almirante Proudmoore.$b$bEsto parece importante. Quizás Gar\'Thok, el comandante de Colina Cortante, estaría interesado en tener esta información.",
    ["O"] = "Lleva las Órdenes del Almirante Proudmoore a Gar\'Thok en Cerro Cortado.",
    ["T"] = "Las Órdenes del Almirante",
  },
  [831] = {
    ["D"] = "Los humanos no pueden ser confiados. Luchamos junto a ellos con un corazón cansado, sabiendo que nos traicionarían algún día.$B$BLa muerte del Almirante Proudmoore no fue suficiente para detener su legado de engaño. La escoria humana tenía sus planes bien trazados antes de encontrar su fin.$B$BSu reinado ni siquiera morirá con Benedict, parece. ¿Quién sabe cuánto tiempo pasará antes de que los próximos hombres de Proudmoore lleguen a nuestras costas?$B$B¡Necesitamos llevar estas órdenes a Nazgrel en Orgrimmar de inmediato! Puedes encontrarlo en la cámara de Thrall.",
    ["O"] = "Entrega las Órdenes del Almirante Valiente a Nazgrel en la cámara de Thrall en Orgrimmar.",
    ["T"] = "Las Órdenes del Almirante",
  },
  [832] = {
    ["D"] = "<Las palabras emanan del colgante y entran en tu mente.>$B$B¿Gazz\'uz?$B$BGazz\'uz... ¡informa!$B$BHay noticias de tu descubrimiento en Roca del Cráneo. ¡Debes prepararte para un ataque!$B$BGazz\'uz, ¡te ordeno que hables!$B$B¡Habla o me aseguraré de que Neeru Fuegooscuro sepa de tu presencia, y descenderá sobre ti con brutalidad rápida!$B$BNo pongas a prueba tus habilidades contra Neeru...$B$B...Gazz\'uz, ¿estás ahí...?",
    ["O"] = "Lleva este ojo a Neeru Hojafuego.",
    ["T"] = "Sombras Ardientes",
  },
  [833] = {
    ["D"] = "Solo los tauren más valientes son enterrados en las Rocas Rojas, nuestro sagrado cementerio. Es un honor otorgado a los grandes guerreros que ayudaron a fundar y defender Cima del Trueno y a aquellos que han dado sus vidas por el bien mayor de sus tribus y jefes.$b$bPero parece que una amenaza vil ha llegado a nuestra tierra sagrada. Una banda de intrusos Cerdamora está devastando el lugar y yo soy demasiado viejo y estoy pasado de mi prime para ahuyentarlos.$b$bDeben ser expulsados por la fuerza, $n.",
    ["O"] = "El Guardián de la Sabiduría Raintotem quiere que mates a 8 Intrusos Cerdeespín en las Rocas Rojas.",
    ["T"] = "Un Entierro Sagrado",
  },
  [834] = {
    ["D"] = "Soy Rezlak, uno de los chicos de Gazlowe. El jefe me envió para ayudar a los orcos aquí en Durotar. Las cosas iban bien hasta que empezaron los problemas con las caravanas. ¡No podemos mantenerlas a salvo! Eso hace mi trabajo un poco más difícil, ¿sabes?$b$bEl último envío—¡prometieron que llegaría!—fue robado por las arpías Polvoviento de... ¿dónde era? ¿El Cañón Filoravén?$b$b¡Necesito esos suministros o nunca podré hacer nada! Sigue el gran cañón hacia el sur, encontrarás la garganta cortada en los lados este y oeste de las paredes.",
    ["O"] = "Recupera 5 Sacos de Suministros y devuélveselos a Rezlak cerca de Orgrimmar.",
    ["T"] = "Vientos en el Desierto",
  },
  [835] = {
    ["D"] = "Gazlowe me dijo una vez: \"Rezlak, hay una cosa que necesitas saber en la vida, y es que si quieres que algo se haga bien, hazlo tú mismo\".$b$bNo puedo lidiar con perder todas nuestras caravanas, así que voy a tomar el asunto en mis propias manos. El Barranco Seco se encuentra a través de la parte oriental del Cañón Razorwind. Si matamos a todas las arpías allí, las rutas estarán seguras.$b$bPor supuesto, había otra cosa que Gazlowe me enseñó, y es que \"Puedes conseguir que se haga cualquier cosa por un precio\". Entonces, ¿vas a ayudarme, $n?",
    ["O"] = "Mata 12 Salvajes Viento Seco y 8 Brujas de la Tormenta Viento Seco para Rezlak cerca del Barranco Seco.",
    ["T"] = "Asegurando las Líneas",
  },
  [836] = {
    ["D"] = "La voz del gnomo resuena nuevamente desde el robot:$B$B\"Necesito mover a OOX-09/HL a un lugar abierto y seguro para que pueda comenzar un largo procedimiento de despegue. Tiene camuflaje incorporado, pero necesito tiempo de mi parte para volver a hacerlo operativo. ¡Acompaña al robot desde su ubicación actual hasta, digamos, la costa más allá del Acantilado del Mirador! Ese debería ser un lugar perfecto, y lo suficientemente largo, para poner las cosas en marcha.\"$B$B\"¡Acompaña al robot de manera segura hasta la costa y luego ven a hablar conmigo en Bahía del Botín! Oglethorpe Obnoticus, fuera.\"",
    ["O"] = "Escolta a OOX-09/HL hasta la costa más allá del Acantilado del Mirador, luego informa a Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "¡Rescata a OOX-09/HL!",
  },
  [837] = {
    ["D"] = "Cuando llegamos, los quilboars Cortazar poseían gran parte de la tierra y resultaron ser una espina en nuestro costado. A través de nuestros esfuerzos hemos expulsado a la mayor parte de sus números, pero aún permanecen bien fortificados en algunas áreas.$B$BSin embargo, ya ha durado demasiado. Para nuestra propia protección, no podemos permitir que los Cortazar tengan ningún control en nuestras tierras. Sus campamentos se pueden encontrar al oeste de aquí. Busca las zarzas y los encontrarás. Hoy los expulsamos de Durotar, mañana, quizás de toda Kalimdor.",
    ["O"] = "Mata 4 Jaqualones Crin de Púa, 4 Exploradores Crin de Púa, 4 Polvoreros Crin de Púa y 4 Guardias de Batalla Crin de Púa para Gar\'Thok en Cerro del Cuervo.",
    ["T"] = "Invasión",
  },
  [838] = {
    ["D"] = "En la isla de Castel Darrow se encuentra Scholomance, una escuela que sirve como fortaleza para la Plaga. Nuestro enemigo reside dentro, permitido de manera segura para realizar investigaciones que fortalecen su poder. Una puerta bloquea el camino, y por más que lo intentamos, no se puede entrar sin una llave adecuada.$B$BEl boticario Dithers está bien versado en Scholomance y tiene una idea de cómo obtener una llave. Has demostrado tu valor para mí como alguien que puede hacer las cosas, ahora te confío esta tarea. Habla con él sobre este asunto.",
    ["O"] = "Habla con el Boticario Dithers en El Baluarte, en las Tierras de la Peste del Oeste.",
    ["T"] = "Scholomance",
  },
  [839] = "_",
  [840] = {
    ["D"] = "Hmm, pareces bastante fuerte.$B$BEscucha, mi buen amigo Kargal necesita algunos nuevos reclutas para el servicio en Los Baldíos. Sé que quieres hacer lo correcto por la Horda. Bueno, esta es tu oportunidad.$B$BLleva esta carta de reclutamiento a Kargal y mira si está dispuesto a firmarla.",
    ["O"] = "Sigue el camino occidental desde Cerro Cortante hasta Los Baldíos cruzando un puente.$B$BDetente en el puesto orco al otro lado del puente.$B$BEntrega tu carta de reclutamiento a Kargal Cicatriz de Batalla en el puesto de Los Baldíos.",
    ["T"] = "Recluta de la Horda",
  },
  [841] = {
    ["D"] = "Déjame adivinar: no lograste hacer esa malvada y apocalíptica tarea que estabas haciendo a tiempo. No, espera, mejor aún... ¡la dejaste caer en la arena! Bueno, cualquiera que sea la razón, tengo fuentes de energía adicionales que te venderé. Esta vez, sin embargo, te costará diez, ¡SÍ DIEZ, POCIMAS DE AGUA DE LOS MERODEROS DE LAS DUNAS!$B$BLos tiempos son difíciles para todos, $n, ¡y las fuentes de energía no crecen en los árboles! Hmm, pero ¿y si lo hicieran? Tal vez podríamos HACER un árbol de fuentes de energía...",
    ["O"] = "Lleva 10 Odres de Agua de los Merodeadores de las Dunas al Jefe Ingeniero Bilgewhizzle en Gadgetzan a cambio de otra fuente de energía.",
    ["T"] = "¿Otra Fuente de Poder?",
  },
  [842] = {
    ["D"] = "Llévale esta carta de reclutamiento firmada a El Cruce.$B$BSergra Espina Oscura, maldita sea, está a cargo allí. Es una chamán desagradable que te hará dar vueltas antes de dejarte hacer algo útil. Thrall está convencido de que es alguien especial, pero yo no estoy tan seguro.$B$BTendrás que decidir por ti mismo, cachorro...",
    ["O"] = "Sigue el camino occidental desde el Puesto de Vigilancia Lejana de Kargal.$B$BEn la intersección en T, gira a la izquierda y sigue el camino hacia el sur.$B$BEncuentra a Sergra Espina Oscura en el cruce de caminos dentro de El Cruce.",
    ["T"] = "Conscripción de El Cruce",
  },
  [843] = {
    ["D"] = "En soledad deambulo por estos caminos.$b$bLos enanos de Bael Modan no muestran respeto por mi tierra. Horribles explosiones abren agujeros profundos en el suelo mientras ruidosas máquinas destrozan las colinas. Tierras que una vez sirvieron como hogar y proveedor para mi tribu ahora están plagadas de enanos destructivos.$b$bLos intentos de llegar a una resolución diplomática han fallado. Ahora es el momento de tomar medidas decisivas.$b$bExpulsa a los enanos de la excavación de Bael Modan por la fuerza y tráeme el diario de su líder, el Prospector Khazgorm.",
    ["O"] = "Gann Picopiedra quiere que mates a 15 Excavadores Bael\'dun y 5 Capataces Bael\'dun.$B$BLleva el Diario de Khazgorm a Gann Picopiedra.",
    ["T"] = "Reclamación de Gann",
  },
  [844] = {
    ["D"] = "Tu primera presa será fácil.$B$BLos zancudos de las llanuras al este han estado acosando nuestros suministros de alimentos y se han convertido en una molestia.$B$BMata a los zancudos y regresa con sus picos.",
    ["O"] = "Recolecta 7 Picos de Acechacaminos y llévaselos a Sergra Espina Oscura en El Cruce.",
    ["T"] = "Amenaza de los Zancudos",
  },
  [845] = {
    ["D"] = "Las cebras, aunque no son las bestias más feroces en Los Baldíos, son un poco más duras que los correcaminos. No te preocupes, te enviaremos contra presas más grandes en su momento, pero por ahora tus dientes podrían usar un poco más de filo.$B$BCaza a las cebras al norte y al sur, y recolecta sus pezuñas. Tráemelas y quizás la próxima vez te enviemos contra algo más duro.",
    ["O"] = "Mata Corredores Zhevra para recolectar 4 Pezuñas de Zhevra para Sergra Espina Oscura en El Cruce.",
    ["T"] = "Los Zhevra",
  },
  [846] = {
    ["D"] = "Está claro que el prospector dependía de explosivos y esos vehículos ruidosos para destrozar el suelo. Podemos esperar que un nuevo capataz tome el mando pronto.$B$BQuiero darles a los enanos una probada de su propia medicina. Según estos planes, si combinamos nitroglicerina con pulpa de madera y nitrato de sodio, podemos crear una carga explosiva. Se sabe que el fusilero, los soldados y los oficiales en el Fuerte tienen los suministros.$B$BRecoge estos artículos y tráemelos. ¡Crearé una carga que los enanos no olvidarán!",
    ["O"] = "Gann Picopiedra quiere que le traigas 6 viales de Nitroglicerina, 6 fardos de Pulpa de Madera y 6 muestras de Nitrato de Sodio.",
    ["T"] = "Venganza de Gann",
  },
  [847] = {
    ["D"] = "Este... este poder... ¿Es este el poder de los chamanes que Tor\'gan intentó mostrarme? Debo decir que es interesante. Dile a Tor\'gan que me ha convencido... por el momento. Escucharé lo que tenga que decir.",
    ["O"] = "Habla con Tor\'gan en Bastión de Hammersong.",
    ["T"] = "Astucia del Raptor",
  },
  [848] = {
    ["D"] = "Los oasis de los Baldíos esconden un misterio. La energía vital fluye de sus aguas, vigorizando las plantas y bestias que la beben.$B$BVigorizante, y alterando.$B$BHay un hongo que crece cerca de estos oasis. Sus esporas tienen propiedades que nosotros, los boticarios de Lordaeron, encontramos útiles.$B$BTráeme estas esporas y ganarás nuestra gratitud.",
    ["O"] = "Lleva 4 Esporas de Hongo al Boticario Helbrim en El Cruce.",
    ["T"] = "Esporas Fungales",
  },
  [849] = {
    ["D"] = "Siguiendo los planes de Khazgorm, no debería haber problema en fabricar una carga explosiva$b$bAh, ahí está.$b$bAhora, $n, te voy a encomendar una tarea especial. Toma esta barra explosiva y úsala para volar la máquina voladora en Bael Modan.$b$bNo solo la máquina voladora es una parte clave de la infraestructura del sitio, también sirve como un arma de guerra. Más importante aún, es un ícono de poder y un insulto a la memoria de mi tribu.$b$bHazme sentir orgulloso completando esta tarea heroica y serás recompensado.",
    ["O"] = "Destruye la máquina voladora en Bael Modan y regresa con Gann Picopiedra.",
    ["T"] = "Venganza de Gann",
  },
  [850] = {
    ["D"] = "Los centauros han plagado a los Tauren durante años. Y recientemente, los centauros Kolkar de los Baldíos se han convertido en una verdadera amenaza. Normalmente estaban desorganizados en estas tierras, pero nuevos líderes han surgido entre ellos. Reuniéndolos.$B$BPara preservar nuestras posesiones aquí, estos líderes centauros deben ser destruidos.$B$BTráeme la cabeza de Barak Kodobane. Acampa cerca de las Charcas del Olvido al norte.$B$BDerrota a Barak, luego regresa conmigo.",
    ["O"] = "Lleva la Cabeza de Barak a Regthar Puerta de la Muerte, al oeste de El Cruce.",
    ["T"] = "Líderes Kolkar",
  },
  [851] = {
    ["D"] = "El centauro Verog el Derviche vaga por los Baldíos y será difícil de encontrar. Pero tiene su base en la tienda de mando de los centauros en el Oasis Estancado al sureste. Puede ser posible atraerlo hacia ti.$B$BViaja al Oasis Estancado al sureste y ataca a los centauros cerca de la tienda de mando. Será peligroso, pero si puedes matar suficientes centauros, deberían dar la alarma. Y Verog vendrá.$B$BTráeme su cabeza y la colocaré junto a la de Barak Kodobane.",
    ["O"] = "Lleva la Cabeza de Verog a Regthar Puerta de la Muerte, al oeste de El Cruce.",
    ["T"] = "Verog el Derviche",
  },
  [852] = {
    ["D"] = "Hezrul Bloodmark es el líder de los centauros Kolkar en Los Baldíos. Es feroz, brutal y astuto. Derrotarlo desestabilizaría y fracturaría a los Kolkar, reduciendo en gran medida su amenaza para nosotros.$B$BAsí que mátalo. Como con Barak y Verog, tráeme la cabeza de Hezrul.$B$BÉl lidera a su gente desde el Oasis Aguaverde, al sur.",
    ["O"] = "Lleva la Cabeza de Hezrul a Regthar Puerta de la Muerte, al oeste de El Cruce.",
    ["T"] = "Hezrul Marcasangre",
  },
  [853] = {
    ["D"] = "He convertido las esporas que me diste en una emulsión. Ahora debo enviársela a mi asociada, la Boticaria Zamah. Si la llevas tú, ella puede ofrecerte algo de nuestras reservas de bienes alquímicos.$B$BElla está en las Pozas de la Visión, una cueva debajo de los chamanes de Cima del Trueno. El camino a esta cueva está bien escondido, pero puedes encontrarlo en el risco del Ascenso de los Espíritus.$B$BDate prisa. Esta emulsión mantendrá su potencia solo por un corto tiempo, y debe ser resellada y procesada antes de entonces.",
    ["O"] = "Lleva las Esporas Procesadas al Boticario Zamah en Cima del Trueno, antes de que se acabe el tiempo límite.",
    ["T"] = "Boticario Zamah",
  },
  [854] = {
    ["D"] = "He previsto tu llegada, joven $c. Aunque quizás ya no seas tan joven.$B$BCreo que es hora de que te adentres más en esta tierra. Puedo sentir que estás destinado a grandes cosas.$B$BEs en El Cruce donde debes buscar, pues nuestras vidas con los orcos se han entrelazado y necesitan ayuda. Continúa por este camino hacia el este y síguelo hacia el norte hasta El Cruce. Encuentra a Thork dentro de sus muros, él tiene la bendición de la Madre Tierra.",
    ["O"] = "Habla con Thork en El Cruce en los Baldíos.",
    ["T"] = "Viaje a El Cruce",
  },
  [855] = {
    ["D"] = "Los Kolkar son una amenaza para las reclamaciones de la Horda en los Baldíos, debemos reducir el número de sus tropas. Y así, tengo una tarea de recompensa para ti.$B$BMientras estés en territorios Kolkar, mata a sus guerreros y recoge brazales de centauro. Regresa cuando tengas un montón de brazales y la Horda te recompensará bien.",
    ["O"] = "Lleva 15 Brazales de Centauro a Regthar Deathgate, al oeste de El Cruce.",
    ["T"] = "Brazales Centauro",
  },
  [856] = "_",
  [857] = {
    ["D"] = "¡El poder era mío! ¡En mis propias manos! ¡Y lo quiero de vuelta! ¡De vuelta, digo!$B$BEsos sucios excavadores lo desenterraron... era tan hermoso. Lo llamaron la Lágrima de las Lunas, fue el primer gran descubrimiento que hicimos en Bael Modan.$B$B¡Debería haber sido mío! ¡Mío, digo!$B$BEl General Twinbraid se puso celoso del poder que me daba. Lo encerró en la Fortaleza. ¡Dijo que estaba maldito, el tonto!$B$B¡Otros lo querían y tuvieron que ser detenidos! Ese bastardo Twinbraid me echó.$B$BAhora lo quiero de vuelta. ¡De vuelta, digo!",
    ["O"] = "Feegly el Exiliado quiere que recuperes para él la Lágrima de las Lunas.",
    ["T"] = "La Lágrima de las Lunas",
  },
  [858] = {
    ["D"] = "No supongo que Sputtervalve te haya enviado, ¿verdad? Estoy en un aprieto aquí. Me subí sin darme cuenta de que necesito una llave para desbloquear la columna de movimiento del triturador. Uno de los otros operadores de trituradoras me preguntó si todo estaba bien, ¡y entré en pánico! En lugar de decirle que me faltaba mi llave, le dije que había algún tipo de problema mecánico.$B$B ¡Necesitamos salir de aquí rápidamente! Sube a la sala de control en la parte superior de la torre, el supervisor debería tener una llave para este triturador.$B$B ¡Ayúdame aquí!",
    ["O"] = "Consigue la Llave de Encendido y llévasela a Palanqueta.",
    ["T"] = "Ignición",
  },
  [860] = {
    ["D"] = "Si deseas seguir el camino del cazador, entonces tu viaje te lleva a los Baldíos. Sus bestias son de voluntad fuerte y feroces. Aprenderás mucho cazando allí, y tu espíritu crecerá.$B$BHabla con Sergra Espina Oscura. Ella será tu primera guía en los Baldíos.$B$BSergra está en El Cruce. Para llegar a ella, viaja al este desde la Aldea Sangrante hacia los Baldíos, luego al norte donde el camino se bifurca. Y ten cuidado mientras viajas, los Baldíos contienen un gran peligro para los no preparados y los incautos.",
    ["O"] = "Habla con Sergra Espina Oscura en El Cruce.",
    ["T"] = "Sergra Espina Oscura",
  },
  [861] = {
    ["D"] = "Estás ansioso por explorar, lo noto. Yo también tuve el deseo de vagar, una vez...$B$BVagar, y cazar. Porque cazar es el mayor honor de un tauren.$B$BSi realmente deseas seguir los caminos del cazador, entonces Melor Pezuña Pétrea puede mostrarte el camino. Está en Cima del Trueno, en el Ascenso del Cazador.$B$BY para mostrarle tu habilidad y determinación, tráele las garras de los merodeadores de las llanuras de Mulgore. Son duros y astutos, presas adecuadas para un joven $c en el camino del cazador.",
    ["O"] = "Lleva 4 Garras de Merodeador de las Llanuras a Melor Stonehoof en Cima del Trueno.",
    ["T"] = "El Camino del Cazador",
  },
  [862] = {
    ["D"] = "Un $c como tú debe pasar mucha hambre aquí en Los Baldíos. Yo, me muero de hambre todo el día.$B$BTe diré algo, te prepararé un buen estofado. Solo necesito unas cuantas ratas de excavación.$B$BParece que esos sucios enanos de Bael Modan están infestados de lo que a todos les gusta comer. Ahora dirígete al sur hacia Bael Modan y tráeme algunas ratas de excavación.$B$B¡No puedes llamarte un aventurero experimentado hasta que hayas pasado un tiempo matando ratas! ¡Ja!",
    ["O"] = "Lleva 8 Ratas de Excavación a Grub al este de El Cruce.",
    ["T"] = "Estofado de Rata de Madriguera",
  },
  [863] = {
    ["D"] = "Supongo que aprenderé sobre la marcha... No puede ser tan difícil. Solo unos botones aquí, y una palanca o dos... Bueno, ¿estás listo para ir?",
    ["O"] = "Protege a Wizzlecrank y a la trituradora goblin robada en el camino a Valvulerre en Ratchet.",
    ["T"] = "El Escape",
  },
  [864] = {
    ["D"] = "Aquí tienes, la fuente de energía ha sido removida de manera segura y los datos han sido preservados. Bueno, buen viaje y todo eso. Espero que el experimento científico nefasto haya funcionado para ti y todo.",
    ["O"] = "Lleva el Kit de Pruebas de Campo Sellado al Boticario Zinge en Entrañas.",
    ["T"] = "Regreso con el Boticario Zinge",
  },
  [865] = {
    ["D"] = "Los raptores de los Baldíos son más inteligentes que los raptores de otras tierras. ¡Y creo que toda esa inteligencia está escondida en sus cuernos! Si es así, entonces podría moler sus cuernos hasta convertirlos en polvo y usarlo para hacer \"bebidas inteligentes\". ¡Podría venderlas por una fortuna!!!$B$BY tú puedes ayudarme. Encuéntrame cuernos de raptor intactos de los raptores Garfoclo. Merodean en el sur de los Baldíos, y en el norte de los Baldíos cerca de la frontera con el Bosque de Ashenvale.",
    ["O"] = "Reúne 5 Cuernos de Raptor Intactos de los Segadores Escamasol y llévaselos a Mebok Mizzyrix en Ratchet.",
    ["T"] = "Cuernos de Raptor",
  },
  [866] = {
    ["D"] = "Las hierbas de los Baldíos no son como las hierbas de otras tierras. Sus propiedades son en su mayoría las mismas, pero el agua del oasis aquí altera las plantas, son ligeramente diferentes.$B$BQuiero estudiar esas diferencias... ¡para ver si son explotables!$B$BSi tienes habilidades en herboristería, entonces recolecta muestras de raíces de las hierbas que encuentres en los Baldíos. Tráeme estas muestras para que pueda compararlas con hierbas de otras regiones.",
    ["O"] = "Lleva 8 Muestras de Raíz a Mebok Mizzyrix en Ratchet.",
    ["T"] = "Muestras de Raíces",
  },
  [867] = {
    ["D"] = "Las malditas arpías han estado devastando nuestros suministros de caravanas durante demasiado tiempo. Es hora de cortar algunas gargantas y reducir su número.$B$BHe estado husmeando alrededor de sus nidos y descubrí quién lidera a quién. El ataque más devastador destruirá una buena parte de su cadena de mando inferior y luego podremos avanzar hacia la cima.$B$BTe pondré en marcha lentamente, mata a las Arpías Bruja y a las Arpías Bruja Plumarroja en el noroeste y tráeme 8 Garras de Bruja. Ese será un buen comienzo...",
    ["O"] = "Recolecta 8 Garras de Alavil.$B$BLlévaselas a Darsok Daga Presta en El Cruce.",
    ["T"] = "Asaltantes Arpía",
  },
  [868] = {
    ["D"] = "Oye, $N. Me han enviado a los Cruces para vigilar la tierra y tomar nota de lo que sucede para mis maestros en Orgrimmar.$B$BUn objeto de mis estudios son las criaturas parecidas a insectos que se encuentran al sur en el Campo de los Gigantes. Sabemos poco sobre las criaturas, así que me propongo descubrir más. Parecen tener inteligencia, más que cualquier animal normal.$B$BToma esta garra de excavación y recolecta algunos huevos de las criaturas de sus montículos, pero ten cuidado: si te descubren, te atacarán.",
    ["O"] = "Lleva 12 Huevos de Silítido y la Garra de Excavación a Korran en El Cruce.",
    ["T"] = "Caza de Huevos",
  },
  [869] = {
    ["D"] = "Hace no mucho tiempo, un cargamento de plata fue robado de nuestra torre de vigilancia. Estaba destinado a ser el pago de los guardias de El Cruce, y queremos esa plata de vuelta.$B$BLa cosa extraña es... ¡capturamos a uno de los ladrones la noche del robo! Y... ¡era un raptor! ¡Increíble!$B$BNo sé qué querrían los raptores con la plata. Pero no me importa, ¡quiero que los raptores mueran para que no nos roben de nuevo!$B$BCaza raptores en Los Baldíos. ¡Recógeme sus cabezas!",
    ["O"] = "Lleva 12 Cabezas de Raptor a Gazrog en El Cruce.",
    ["T"] = "Ladrones de Raptores",
  },
  [870] = {
    ["D"] = "Hace mucho tiempo, Los Baldíos era un lugar exuberante, lleno de vida. Pero la guerra y el cataclismo arrasaron la tierra, quemándola y dejando un cascarón seco. Así es el curso de las cosas, y entristece mi corazón.$B$BPero en los últimos años, nuevos oasis se han formado en Los Baldíos, y la vida se agita. Y en lo profundo, los druidas sentimos una energía que se filtra hacia la superficie.$B$BDebemos encontrar su fuente. Viaja a las Charcas del Olvido, al noroeste de El Cruce. Busca en sus aguas una fuente de poder, luego regresa aquí.",
    ["O"] = "Informa a Tonga Tótem de Runa con tus hallazgos.",
    ["T"] = "Las Pozas Olvidadas",
  },
  [871] = {
    ["D"] = "Todos los quilboars son nuestros enemigos, $N. Algunos simplemente resultan ser más molestos que otros.$B$BLa tribu Colmillarrastro ha estado atacando nuestras líneas de suministro desde Durotar, causándonos innumerables molestias. Tengo exploradores buscando al líder de estas incursiones, pero hasta entonces, cualquier pérdida que puedas infligir a los sucios hombres cerdo nos ayudaría.$B$BComienza en el noreste hacia Durotar. Siempre puedes distinguir sus guaridas por las enormes enredaderas espinosas que salen de la tierra. Búscalos y mátalos.",
    ["O"] = "De la tribu Cerdaceña, mata a 8 Buscadores de Agua, 8 Tejespinas y 3 Cazadores, y luego regresa con Thork en El Cruce.",
    ["T"] = "Interrumpir los Ataques",
  },
  [872] = {
    ["D"] = "Uno de mis exploradores presenció un ataque a una caravana de Durotar, $N. Los culpables son, de hecho, la tribu Colmillarrío de los jabalíes. Uno de los jabalíes en particular está liderando las incursiones: Kreenig Gruñisnarl. Fue visto al noreste de aquí, justo al sur del camino de Durotar. Si la amenaza va a terminar, entonces Kreenig debe morir.$B$BPara asegurarnos de que los ataques cesen, sin embargo, digamos que agreguemos un insulto a su lesión. Mata a Kreenig junto con más de su tribu, y regresa a mí cuando tengas su colmillo.",
    ["O"] = "Mata 8 Geománticos Crin de Púa, 8 Defensores Crin de Púa y a Kreenig Gruñisnort.$B$BLuego lleva el Colmillo de Kreenig Gruñisnort a Thork en el Cruce.",
    ["T"] = "El Fin de la Interrupción",
  },
  [873] = {
    ["D"] = "El gran Isha Awak es el señor de estas aguas. Grande es su fuerza y solemne su orgullo. Los humanos en la costa lo temen, pues ha consumido a muchos de ellos.$B$BPero yo no lo temo. Estoy agradecido de que esté aquí. Es un desafío digno y una presa honorable.$B$BSi estás listo, entonces nada y busca a Isha Awak, la Perdición Profunda.$B$BSu espíritu habita en su corazón, y escuchar su latido es conocer tu destino.",
    ["O"] = "Lleva el Corazón de Isha Awak a Mahren Skyseer.",
    ["T"] = "Isha Awak",
  },
  [874] = {
    ["D"] = "Has cazado a las bestias de Los Baldíos. Tu espíritu es fuerte. Pero un cazador siempre debe estar preparado. Un verdadero cazador puede acechar a su presa por cualquier camino. Las montañas y los pantanos no lo detendrán.$B$BNi siquiera el mar.$B$BBusca a mi hermana, Mahren. Ella caza a las grandes bestias acuáticas a lo largo de la costa. Ella será tu maestra en sus formas.$B$BPero ten cuidado en tu búsqueda de ella, porque la costa de Los Baldíos está en manos de los humanos.",
    ["O"] = "Habla con Mahren Visteluna.",
    ["T"] = "Mahren Vidente del Cielo",
  },
  [875] = {
    ["D"] = "Jajaja, $n. ¿De vuelta por más? Bien, me gusta escuchar eso.$B$BEsta vez quiero que asesines a algunas de las tenientes arpías en las Colinas Áridas. Solo las Asesinas Alavil pueden ser tenientes, y puedes distinguirlas por los anillos que llevan. Son un grupo desagradable, pero lideran a algunos de los subordinados en esa área. Eliminar a 6 de ellas será un golpe decisivo contra su matriarcado.$B$BAsegúrate de que mueran dolorosamente, $n. Queremos que esas arpías sepan la idiotez de buscar peleas con la Horda.",
    ["O"] = "Recolecta 6 Anillos de Teniente Arpía de las Destripadoras Alavil y llévaselos a Darsok Daga Presta en El Cruce.",
    ["T"] = "Tenientes Arpía",
  },
  [876] = {
    ["D"] = "Lo hiciste tan bien con los tenientes y subordinados que me gustaría que hicieras una última cosa por mí.$B$BSerena Plumaje de Sangre es la hermana de una arpía llamada Plumaje de Sangre que fue asesinada por Rexxar hace bastante tiempo. Aparentemente, estos ataques a las caravanas de la Horda son venganza por la muerte de su hermana.$B$BNecesito que le cortes la garganta y me traigas su cabeza. Quiero colocarla en la próxima caravana que enviemos... Darles a esas arpías algo en qué pensar.",
    ["O"] = "Mata a Serena Plumavil y lleva su cabeza a Darsok Puñocéfalo en El Cruce.",
    ["T"] = "Serena Plumavil",
  },
  [877] = {
    ["D"] = "Como el que encontraste en las Charcas del Olvido, puede haber fisuras en los otros oasis de Los Baldíos. Si es así, entonces tal vez las fisuras sean la fuente de vida de los oasis.$B$BDebemos probar esto.$B$BAquí, toma estas semillas. Están muertas e infértiles, pero llévalas al Oasis Estancado al sureste. Si hay una fisura allí, entonces coloca las semillas dentro de ella... y observa.",
    ["O"] = "Regresa con Tonga en El Cruce, después de investigar el Oasis Estancado.",
    ["T"] = "El Oasis Estancado",
  },
  [878] = {
    ["D"] = "Debes golpear a mis enemigos. <snort> Debes ayudar a Mangletooth y a los Colmillarrata debilitando a los Cerahueso. Ambas tribus son muy espirituales, conectadas con la tierra y su poder. <snort> Nos da fuerza en todas las cosas... especialmente en la guerra.$B$BSi quieres descubrir quién realmente causa tanto sufrimiento a Thork <snort> y a la Horda, ¡entonces ayuda a Mangletooth! Atacas a los líderes espirituales de los Cerahueso al norte, debilítalos para que los Colmillarrata puedan derrotarlos y gobernar Los Baldíos.",
    ["O"] = "Mata 6 Buscadores de Agua Pezuña de Púa, 12 Tejeespinas Pezuña de Púa y 12 Geománticos Pezuña de Púa y regresa con Mangletooth en el Campamento Taurajo en los Baldíos.",
    ["T"] = "Tribus en Guerra",
  },
  [879] = {
    ["D"] = "Tres de mi tribu salieron del Kraul para liderar las incursiones contra la Horda, $r. <snort> Son despiadados y astutos, y si los derrotas, entonces tus Cruces y la mayor parte de los Baldíos conocerán la paz... <snort> al menos de la tribu Zarparrío. <snort>$B$BNak, Kuz y Lok Orcbane son a quienes buscas. Están muy al sur de los Baldíos. Uno es un lanzador de hechizos, otro un rastreador, y su líder, el llamado Orcbane, <snort> un guerrero. Mátalos, $r, como ellos me han matado a mí.",
    ["O"] = "Mata a Nak, Kuz y Lok Mataorcos y lleva sus cráneos a Dientetorcido en Campamento Taurajo en los Baldíos.",
    ["T"] = "Traición desde Dentro",
  },
  [880] = {
    ["D"] = "Tus hallazgos son increíbles, $N. Estos oasis tienen propiedades que deben provenir de una fuente externa. O quizás de una interna.$B$BQuiero saber cómo estas fisuras están afectando a las bestias que beben del agua de los oasis.$B$BCaza a los fauces de oasis en los Oasis Aguaverde y Estancado. Tráeme sus caparazones para que pueda examinarlos.",
    ["O"] = "Lleva 8 Caparazones de Quijaforte Alterados a Tonga Tótem de Runa en El Cruce.",
    ["T"] = "Seres Alterados",
  },
  [881] = {
    ["D"] = "Brumablanca, Echeyakee en la lengua tauren, es el rey de los gatos de la sabana. Caza con tal sigilo que dicen que es como una fina niebla blanca sobre la tierra. Y mata tan rápido que sus presas no tienen tiempo para el miedo o el dolor.$B$BLos tauren dicen que es tanto misericordia como muerte.$B$BTú descubrirás si eso es cierto, porque ahora te pongo en el camino de cazar a Echeyakee. Su guarida está al noreste de El Cruce, entre los huesos de los grandes kodos.$B$BVe. Toca este cuerno cuando llegues a su guarida. Toca el cuerno, y él vendrá.",
    ["O"] = "Lleva la Piel de Echeyakee a Sergra Espina Oscura en El Cruce.",
    ["T"] = "Echeyakee",
  },
  [882] = {
    ["D"] = "Ishamuhale, Colmillo de Lanza, es el raptor solescama más feroz de Los Baldíos. No caza por deporte, ni por comida. Caza porque cazar es su pasión. Mata porque es su naturaleza matar.$B$BY tú aprenderás sobre su naturaleza, $N, porque tu camino ahora sigue las huellas de Ishamuhale.$B$BComienza la caza. Mata a su presa favorita, una cebra, y lleva su cadáver al árbol muerto al noroeste de Trinquete. Ishamuhale olerá el cadáver y será atraído hacia él.$B$B¡Prepárate cuando llegue!",
    ["O"] = "Lleva el Colmillo de Ishamuhale a Jorn en Campamento Taurajo.",
    ["T"] = "Ishamuhale",
  },
  [883] = {
    ["D"] = "El Pezuña de Lakota\'mani es un símbolo de la fuerza del gran kodo.",
    ["O"] = "Lleva la Pezuña de Lakota\'mani a Jorn en Campamento Taurajo.",
    ["T"] = "Lakota\'mani",
  },
  [884] = {
    ["D"] = "Cuando se toca, chispas vuelan de la púa de la cola de Owatanka.",
    ["O"] = "Lleva el Pico de Cola de Owatanka a Jorn en Campamento Taurajo.",
    ["T"] = "Owatanka",
  },
  [885] = {
    ["D"] = "La pluma de Washte Pawne es dolorosa al tacto.",
    ["O"] = "Lleva la Pluma de Washte Pawne a Jorn Vidente del Cielo en Campamento Taurajo.",
    ["T"] = "Washte Pawne",
  },
  [886] = {
    ["D"] = "Los druidas de Cima del Trueno perciben un poder extraño que se filtra en los Baldíos, al este de Mulgore.$B$BTonga Runetotem fue enviado para descubrir la fuente de este poder, pero tememos que necesitará ayuda.$B$BViaja a los Baldíos y habla con Tonga. Estará en El Cruce. Para llegar allí, toma el camino hacia el este desde la Aldea Pezuña de Sangre. Entra en los Baldíos, luego gira hacia el norte cuando el camino se divida. Continúa hacia el norte y encontrarás El Cruce.$B$BY no te salgas del camino, $N, porque los Baldíos son una tierra dura.",
    ["O"] = "Habla con Tonga Tótem de Runa en El Cruce.",
    ["T"] = "Los Oasis de Los Baldíos",
  },
  [887] = {
    ["D"] = "Seguro que desearía que el Vicealmirante Grezzlik hiciera un mejor trabajo manteniendo los mares seguros para nuestros buques comerciales. ¡Con todo el oro que los príncipes mercantes han invertido en las Flotas Comerciales, me molesta ver a tantos piratas corriendo por ahí, asaltando mis barcos y robando mis bienes!$B$BHe oído que los Bucaneros del Mar del Sur han establecido un campamento justo al sur de aquí. Han sido una verdadera espina en mi costado, y si Grezzlik no se ocupa de ellos, bueno, tal vez tú puedas ayudarme a deshacerme de ellos.",
    ["O"] = "Mata 12 Brigantes de los Mares del Sur y 6 Artilleros de los Mares del Sur para Gazlowe en Ratchet.",
    ["T"] = "Bucaneros de los Mares del Sur",
  },
  [888] = {
    ["D"] = "Aunque me gustaría recuperar todos mis bienes, estoy seguro de que no es posible. Aún así, ¡hay algunas cosas que absolutamente debo tener!$b$bComo ves, mi observatorio está listo, pero necesita la más pequeña de las dos lentes. La primera llegó en caravana desde Durotar, pero la segunda fue enviada por barco y nunca llegó. Además, ¡nunca recibí mi envío de botas de Drizzlik en Bahía del Botín!$b$bDirígete al campamento pirata y ve si puedes encontrarlas.",
    ["O"] = "Recupera el Envío de Botas y el Lente Telescópico para Gazlowe en Ratchet.",
    ["T"] = "Botín Robado",
  },
  [889] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Espíritu del Viento",
  },
  [890] = {
    ["D"] = "¡He estado esperando una eternidad mi último envío de mercancías desde Bahía del Botín! Estoy bastante seguro de que debe haber sido robado por los Piratas, pero solo para asegurarme, ¿podrías ir al muelle y preguntarle a Dizzywig si mis bienes ya fueron puestos en mi almacén sin mi conocimiento?$b$bAquí, lleva mi libro de contabilidad a Dizzywig y haz que revise mis registros de inventario contra sus registros.",
    ["O"] = "Lleva el Libro de Cuentas de Gazlowe al Capataz del Muelle Dizzywig.",
    ["T"] = "El Envío Perdido",
  },
  [891] = {
    ["D"] = "¡Otro barco de mi flota ha sido perdido por esos humanos excesivamente celosos en el Fuerte del Norte!$B$BEsos bastardos deben ser tratados. Su paranoia los tiene en un estado de frenesí. Disparan a todo lo que ven.$B$BViaja hacia el sur a lo largo de la Costa Mercante hasta el Fuerte y asedia sus fuerzas. Tráeme las medallas de sus soldados como prueba. Y por el amor de mi flota, ¡mata al Capitán Fairmount y a sus cañoneros excesivamente celosos!",
    ["O"] = "Capitán Thalo\'thas Brisa del Sol de Ratchet quiere que recolectes 10 Medallas de Theramore y mates al Capitán Fairmount, Artillero Whessan y Artillero Smythe.",
    ["T"] = "Las Armas de Vigía del Norte",
  },
  [892] = {
    ["D"] = "Todo parece estar en orden según mis registros, $n. Regresa y dile a Gazlowe que no tiene suerte. No me sorprendería que fueran los piratas los que se llevaron sus cosas.$b$bNo hay nada más que pueda hacer por él. Oh... aunque podrías decirle que tengo algunas cosas de Rémol para él, cuando tenga tiempo de venir a recogerlas.",
    ["O"] = "Devuelve el Libro de Cuentas de Gazlowe a Gazlowe en Ratchet.",
    ["T"] = "El Envío Perdido",
  },
  [893] = {
    ["D"] = "Los quilboars Crines de Acero al sur, más allá del Campo de Gigantes, no tienen herreros hábiles según me han dicho, pero aparentemente han comenzado a desarrollar armas más resistentes. Me gustaría conseguir algunos tipos diferentes para aprender sus técnicas si es posible.$B$BSi deseas ayudar a traer honor a la Horda, entonces tráeme ejemplos de sus armas. Encuéntrame una daga de sus acechadores, una varita de sus videntes y un escudo de sus frenéticos. ¡Eso debería servir!",
    ["O"] = "Lleva un Apuñalador Crestafuria, una Varita Crestafuria Carbonizada y un Escudo de Guerra Crestafuria a Tatternack Forjacero en Campamento Taurajo en los Baldíos.",
    ["T"] = "Armas de Elección",
  },
  [894] = {
    ["D"] = "Ventura y Cía. ha montado un pequeño centro de investigación muy hacia el norte desde aquí, al suroeste de El Fangal. No tengo muy claro qué hacen allí pero logré descubrir que están haciendo pruebas con algo llamado \"churumbele\".$B$BY digo yo, ¿qué leches es un churumbele? Bueno, sea lo que sea, quiero examinarlo, así necesito que alguien me lo consiga.$B$BConseguí una copia de su manual de instrucciones del sistema de control, gracias al cual deberías poder encontrar la forma de desconectar el churumbele.",
    ["O"] = "Accede a la consola de control en el sitio de investigación de Venture Co.",
    ["T"] = "El Churumbele",
  },
  [895] = {
    ["D"] = "¡SE BUSCA!$b$bBarón Longshore, Capitán del Heedless$b$bEl Capitán Longshore de Gilneas comanda barcos de los Corsarios de los Mares del Sur, y es buscado por cargos de piratería. ¡Los restos deben estar en condiciones identificables!$b$b-- Gazlowe",
    ["O"] = "Lleva la cabeza del Barón Longshore a Gazlowe en Ratchet.",
    ["T"] = "SE BUSCA: Barón Costasur",
  },
  [896] = {
    ["D"] = "Siendo el Capataz del Muelle de un puerto tan ocupado como Ratchet, mantengo mi dedo en el pulso de la información. Sé todo sobre el intercambio de bienes y dinero entre aquí y Bahía del Botín.$B$BLa última noticia que he escuchado es sobre la mina Veta de Venture Co., al noreste del Fangal de la Mugre. Uno de los mineros descubrió una esmeralda del tamaño de tu puño. Conozco a algunos compradores que estarían interesados en conseguir algo así, y estaría dispuesto a dividir las ganancias de su venta.",
    ["O"] = "Recupera la Esmeralda Ojo de Gato de uno de los Capataces o Ejecutores de Venture Co. para el Capataz del Muelle Mareviento en Ratchet.",
    ["T"] = "Fortuna del Minero",
  },
  [897] = {
    ["D"] = "Esta extraña criatura parecida a un insecto parece estar fuera del orden natural, como si no perteneciera a los Baldíos.",
    ["O"] = "Lleva la Cabeza del Segador a Jorn Skyseer.",
    ["T"] = "El Cosechador",
  },
  [898] = {
    ["D"] = "¡Finalmente! ¡Alguien que me rescate!$b$bNo puedo creer que hayas pasado a los guardias. Estos fanáticos de Theramore están fuera de sus cabales. Hundieron nuestro barco y me encarcelaron, el único superviviente. Me llamaron enemigo y amenaza para la Alianza.$b$b¡Yo! ¿Una amenaza para su gran farsa de Alianza? Yo era un marinero en un transporte de licor entre Ratchet y Bahía del Botín.$b$bBasta de charla. ¿Me ayudarás a volver a Ratchet? Avísame cuando estés listo y haremos nuestra escapada.",
    ["O"] = "Escolta a Gilthares Ramafuego de vuelta con el Capitán Brisa del Sol en Ratchet.",
    ["T"] = "Liberado de la Prisión",
  },
  [899] = {
    ["D"] = "Quizás conozcas el dolor de la incertidumbre, quizás no, $c. Pero sabe esto: estoy aquí todos los días, desde el amanecer hasta el ocaso, escudriñando el horizonte en busca de más de esos monstruos. He matado a cada hombre cerdo que me he cruzado desde entonces, pero mi sed de su sangre está lejos de saciarse. Quizás en lugar de dirigirme al norte, a El Cruce, debería haber ido a Taurajo.$B$BTú, $c, podrías ayudarme.$B$BMátalos. Mata a tantos como puedas. Tráeme sus colmillos, y celebraremos sus muertes juntos.",
    ["O"] = "Lleva 60 Colmillos de Jaqualope de Cerdeespina a Mankrik en El Cruce.",
    ["T"] = "Consumido por el Odio",
  },
  [900] = {
    ["D"] = "Las tres válvulas de control están actualmente abiertas. Las diversas lecturas en el panel de control te llevan a presumir que deben cerrarse antes de que el aparato pueda apagarse.",
    ["O"] = "Cierra la Válvula de Control de Combustible, la Válvula Reguladora y la Válvula de Control Principal, luego usa la consola de control nuevamente.",
    ["T"] = "Churumbele",
  },
  [901] = {
    ["D"] = "Con el aparato desactivado, el panel de control puede abrirse y el churumbele puede ser retirado de la consola. Con todo lo demás en su lugar, lo único que queda es adquirir la llave para desbloquear la consola.",
    ["O"] = "Consigue la Llave de la Consola de Tiritijón para usarla en la consola de control.",
    ["T"] = "El Churumbele",
  },
  [902] = {
    ["D"] = "Al abrir la cara de la consola de control, se revela un denso enredo de cables, tubos y otros objetos mecánicos extraños, la mayoría de los cuales parecen no tener ningún propósito práctico. Al empujarlos a un lado y excavar más profundamente en la consola, encuentras el samophlange. Un tirón lo libera de su alojamiento.",
    ["O"] = "Devuelve el Samoplex a Valvulería en Ratchet.",
    ["T"] = "Churumbele",
  },
  [903] = {
    ["D"] = "Ahora es el momento de ir tras algo que realmente muerde. Los merodeadores de Los Baldíos son una raza resistente. Resistentes, ingeniosos, astutos... y mortales. Los encontrarás entre las altas hierbas. Encontrarás muchos al suroeste de El Cruce.$B$BTen cuidado mientras los cazas, $n, o podrías encontrarte no como el depredador, sino como la presa.",
    ["O"] = "Recolecta 7 Garras de Merodeador de los Merodeadores de la Sabana para Sergra Espina Oscura en El Cruce.",
    ["T"] = "Merodeadores de Los Baldíos",
  },
  [904] = "_",
  [905] = {
    ["D"] = "Ahora, $N, la cacería se profundiza. Ahora debes derrotar a tu presa y luego encontrar el camino a su guarida.$B$BCaza a los raptores solescama al sur. Mátalos y quita las plumas que llevan. Coloca las plumas en los nidos de los garragil al suroeste del Oasis Estancado. ¡Muéstrales a sus hermanos que no les temes!",
    ["O"] = "Mata a los raptores Solescama y recolecta sus plumas. Usa las plumas en los 3 nidos de Garrafilada. Regresa con Sergra Espina Oscura en El Cruce.",
    ["T"] = "Los Sablefuria Enojados",
  },
  [906] = {
    ["D"] = "Llévale la cabeza de Lok a Thork en El Cruce, $r. <snort> Debería saber lo que le ha pasado a mi tribu sin necesidad de palabras: ja, no es que vaya a creer que Mangletooth ayudó en <snort> tales cosas. Pero estoy seguro de que te recompensará por llevar a cabo una gran hazaña. <snort>$B$BContinuaré bendiciéndote con el poder de Agamaggan mientras permanezca en esta jaula, $r. Hasta entonces, adiós.",
    ["O"] = "Lleva el Cráneo de Lok a Thork en El Cruce en los Baldíos.",
    ["T"] = "Traición desde Dentro",
  },
  [907] = {
    ["D"] = "Ahora, $N, debes enfrentarte a los grandes lagartos del trueno. Tan grandes son las bestias que la tierra tiembla cuando pisan las llanuras. Ten cuidado al luchar contra ellos, porque un paso en falso y podrías encontrarte bajo sus pies.$B$BEncontrarás a los lagartos del trueno en el sur de Los Baldíos. Se les conoce como Hocicotormenta, Pieltormenta y Cabezatrueno. Regresa a mí después de haberlos vencido.",
    ["O"] = "Lleva 3 Sangres de Lagarto del Trueno a Jorn Skyseer en Campamento Taurajo.",
    ["T"] = "Lagartos de Trueno Enfurecidos",
  },
  [908] = {
    ["D"] = "El Martillo Crepuscular se ha mudado a las Ruinas del Santuario Lunar de las Cavernas de Brazanegra. Su presencia solo puede servir para coaccionar a los elementos a trabajar en nuestra contra. Si no se controla, esta región será suya para siempre.$B$B$N, entra en Brazanegra y encuentra la piedra abisal de las ruinas, debería estar cerca en el agua. En ella hay un núcleo abisal, un dispositivo que, cuando se lee correctamente, relatará una historia de toda la actividad elemental. ¡Si lo tengo, yo y el Anillo de la Tierra podríamos hacer algo para detenerlos!",
    ["O"] = "Lleva el Núcleo de las Profundidades a Je\'neu Sancrea en el Campamento Zoram\'gar, Vallefresno.",
    ["T"] = "Entre las Ruinas",
  },
  [909] = {
    ["D"] = "El elemental de agua derrotado ha dejado atrás un extraño globo de agua. Supones que el objeto de alguna manera alimentaba de energía a la bestia. Dentro del globo, una forma de agua de aspecto pútrido se agita. El globo en sí parece ser impermeable a cualquier tipo de fuerza física.$B$BSi alguien puede darle sentido a este objeto, sería Je\'neu Sancrea del Anillo de la Tierra. Se encuentra en el puesto de avanzada de Zoram\'gar de la Horda en Ashenvale.",
    ["O"] = "Lleva el Globo de Agua Extraño a Je\'neu Sancrea en el Campamento Zoram\'gar, Vallefresno.",
    ["T"] = "Barón Aquanis",
  },
  [910] = {
    ["D"] = "He escuchado historias de los poderosos barcos que navegan entre Ratchet y Bahía del Botín. ¡Sería genial navegar en el océano y luchar en muchas grandes batallas navales! ¡Quizás sea un poderoso marinero de la Horda cuando crezca! ¡Sí!$B$B¿Me llevarías a ver los muelles de Ratchet, por favor? Prometo que no me meteré en el agua ni causaré problemas, ¡solo quiero ver los barcos grandes y oler el agua salada del mar! ¡Sí!",
    ["O"] = "Lleva a tu protegido a los muelles de Ratchet en los Baldíos.",
    ["T"] = "Abajo en los Muelles",
  },
  [911] = {
    ["D"] = "Dicen que Ashenvale es la nueva frontera, y que el Mor\'shan Rampart en Los Baldíos es la puerta de entrada a ella. ¡Sería genial ser un pionero, tallando todo lo que necesitas en las tierras salvajes! Dicen que aquí es donde la Horda y la Alianza chocarán más en el futuro, ¡y quiero estar allí, $N!$B$B¿Me llevarías al Mor\'shan Rampart para que pueda ver cómo es la frontera, por favor? ¡Quiero ver dónde la Horda está expulsando a esos apestosos elfos nocturnos!",
    ["O"] = "Lleva a tu protegido al Baluarte Mor\'shan en la parte norte de los Baldíos, justo antes de entrar en Vallefresno.",
    ["T"] = "Portal a la Frontera",
  },
  [912] = "_",
  [913] = {
    ["D"] = "El halcón trueno, $N, es una bestia feroz. Es hora de que te enfrentes a ellos. Debes encontrar donde vaga y traerme sus alas como prueba de tu caza exitosa.$B$BHaz esto, y tu tiempo conmigo estará cerca de su fin.",
    ["O"] = "Encuentra y mata a un Halcón de Trueno, devuelve sus alas a Jorn Vidente del Cielo en Campamento Taurajo.",
    ["T"] = "Grito del Halcón del Trueno",
  },
  [914] = {
    ["D"] = "Los druidas en las Cuevas de los Lamentos, los Druidas del Colmillo, son una aberración. Formaban parte de una orden de nobles druidas cuyo plan era sanar los Baldíos, pero ahora buscan remodelar esa tierra para que coincida con sus propios sueños retorcidos.$B$BLos Druidas del Colmillo tienen cuatro líderes, y cada uno posee una gema de ensueño. ¡Incluso ahora sus rostros me atormentan! Derrota a los líderes y tráeme sus gemas, y los Baldíos pueden volver a conocer la paz.$B$BVe, $N. Los encontrarás al acecho en lo profundo de las Cuevas de los Lamentos.",
    ["O"] = "Lleva las Gemas de Cobrahn, Anacondra, Pythas y Serpentis a Nara Wildmane en Cima del Trueno.",
    ["T"] = "Líderes del Colmillo",
  },
  [915] = {
    ["D"] = "¡HELADO! ¡Oh, por favor, por favor, podrías conseguirme un poco de helado?! ¡El de fresa es mi sabor favorito, y no hay mejor helado de fresa en el mundo que el Helado de Fresa de Tigule y Foror! ¡Es mi helado favorito en todo el mundo!$B$BLo probé una vez hace mucho tiempo cuando estaba en el circuito de carreras de Los Baldíos, ¡pero escuché que ahora podrían estar vendiéndolos en el pueblo! ¿Por favor? ¿Por favor? ¿Con Helado de Fresa de Tigule y Foror encima??!?!",
    ["O"] = "Consigue un poco de Helado de Fresa para tu pupilo. El chico parece preferir la marca de helado Tigule y Foror.",
    ["T"] = "Tú Gritas, Yo Grito...",
  },
  [916] = {
    ["D"] = "Vine a Claros de la Noche para observar a las arañas telarañas que habitan en la Cueva del Hilo de las Sombras. Son primas de una variedad mucho más pequeña de araña, creo que el árbol del mundo ha tenido un profundo efecto en ellas, y me gustaría tener especímenes para estudiarlas y confirmar esto.$B$BPrimero, me gustaría un poco de su veneno. Recolecta sacos de veneno de las arañas dentro y alrededor de la Cueva del Hilo de las Sombras, al norte. Luego puedo examinarlos en busca de similitudes con el veneno de su prima más pequeña.",
    ["O"] = "Lleva 10 Sacos de Veneno de Telaarboleda a Gilshalan Windwalker en Aldrassil.",
    ["T"] = "Veneno del Bosque de los Tronadores",
  },
  [917] = {
    ["D"] = "Ahora que tengo el veneno de las arañas, me gustaría tener algunos especímenes vivos para estudiar. Desafortunadamente, capturar una araña gigante viva es más de lo que puedo pedirte, joven $c. ¡Y una araña gigante es más de lo que yo podría manejar!$B$BPero si puedes encontrar un huevo sin eclosionar, entonces entregar especímenes será mucho más fácil, y luego puedo organizar que las arañas sin eclosionar sean contenidas.$B$BDebe haber un nido en lo profundo de la Cueva del Hilo de las Sombras. Por favor, busca un huevo en el nido y tráemelo.",
    ["O"] = "Lleva un Huevo de Tuercespina a Gilshalan en Aldrassil.",
    ["T"] = "Huevo del Bosque de los Tronadores",
  },
  [918] = {
    ["D"] = "Los leñadores de Teldrassil son elementales de la naturaleza. De alguna manera reflejan el orden natural de las plantas y los animales en nuestro gran árbol.$B$BAsí que es perturbador ver lo enojados que se han vuelto los leñadores.$B$BCreo que tiene algo que ver con el suelo. He estado trabajando en diferentes métodos para nutrir las plantas y me gustaría probarlos en semillas de leñadores. Por favor, ¿puedes recolectar semillas de leñadores alrededor del Lago Al\'Ameth y traérmelas?",
    ["O"] = "Lleva 8 Semillas de Leñaterral a Denelan en el Lago Al\'Ameth.",
    ["T"] = "Semillas de Leñatezón",
  },
  [919] = {
    ["D"] = "Los pequeños tronquitos están brotando alrededor de las aguas del Lago Al\'Ameth. Me temo que estos brotes están más allá de la ayuda, deberíamos intentar limpiarlos de la tierra antes de que crezcan lo suficiente como para causar problemas.$B$BCuando estés vagando por el lago, si ves algún brote de tronquito, por favor tómalos. ¡Ayuda a mantener nuestra tierra limpia!",
    ["O"] = "Lleva 12 Brotes de Leñador a Denalan en el Lago Al\'Ameth.",
    ["T"] = "Brotes de Leñatezón",
  },
  [920] = {
    ["D"] = "$n, ¿verdad? Escuché que Tenaron ha estado preguntando por ti. Lo encontrarás en la habitación más alta en la cima de Aldrassil.$B$BPodemos vivir vidas largas, pero es mejor que no lo hagas esperar demasiado.",
    ["O"] = "Habla con Tenaron Tormentagarra en la cima de Aldrassil en el Bosque de la Umbría.",
    ["T"] = "Llamado de Tenaron",
  },
  [921] = {
    ["D"] = "Es hora de que salgas a buscar tu destino, $n. Pero antes de que estés listo para aventurarte en el mundo más allá de nuestros bosques encantados, hay mucho que debes aprender sobre nuestra historia reciente.$B$BMucho ha cambiado con nuestro pueblo desde la Batalla del Monte Hyjal. Nordrassil yace como una sombra pálida de lo que alguna vez fue, su poder usado para derrotar a Archimonde y rechazar a la Legión Ardiente...$B$BHay una tarea que debes realizar. Ve al pozo de la luna al norte de Aldrassil y tráeme un frasco de su agua.",
    ["O"] = "Llena el Frasco de Cristal y llévaselo a Tenaron Agarre del Trueno en la cima de Aldrassil.",
    ["T"] = "Corona de la Tierra",
  },
  [922] = {
    ["D"] = "$N, ¿puedes llevar una de las semillas que me trajiste a mi amigo, Rellian Greenspyre? Es un druida en Darnassus, y la última vez que hablamos reveló su interés en mi trabajo con los Leñadores. Tenía algunas ideas propias, y apreciará una semilla de muestra para trabajar.$B$BGracias, $N. Has sido de gran ayuda para mí. Espero que, algún día, veas el fruto de mis labores.$B$BNormalmente encontrarás a Rellian caminando por los senderos del Enclave Cenarion en Darnassus.",
    ["O"] = "Lleva una Semilla de Leñaterral a Rellian Verdespira en Darnassus.",
    ["T"] = "Rellian Brotaveta",
  },
  [923] = {
    ["D"] = "Hay una maldad creciente en los leñadores. Estamos tratando de encontrar la fuente, pero hasta que lo hagamos... para mantener Teldrassil a salvo debemos cortar a los leñadores que están más allá de la ayuda. Los que deambulan por el Lago Primigenio en el norte de Teldrassil son los más contaminados. ¡Deben ser eliminados!$B$BDestruye a los leñadores que encuentres allí y recoge los tumores musgosos que crecen sobre ellos. Tráeme los tumores para que puedan ser quemados.",
    ["O"] = "Lleva 5 Tumores Musgosos a Rellian Greenspyre en Darnassus.",
    ["T"] = "Tumores",
  },
  [924] = {
    ["D"] = "La Semilla Demoníaca es una herramienta poderosa para la Legión Ardiente. Descansa en una cueva en la Cima de la Niebla Temerosa, sobre un altar demoníaco de fuego.$B$BHe preparado piedras de poder que, cuando se colocan cerca de la Semilla, interrumpirán su conexión con la Legión.$B$BToma una piedra de la mesa y llévala al altar de fuego en la cima de la Cima de la Niebla Temerosa. Viaja hacia el oeste, luego hacia el norte cuando el camino se divida. La cima estará a tu izquierda. La subida más segura a la cima es por su cara norte.$B$BDate prisa, el poder de la piedra se consumirá con el tiempo.",
    ["O"] = "Toma una Piedra de Poder Defectuosa. Llévala al Altar de Fuego antes de que la piedra expire, luego regresa con Ak\'Zeloth.",
    ["T"] = "La Semilla del Demonio",
  },
  [925] = {
    ["D"] = "Dicen que Cairne Pezuña de Sangre es uno de los mayores héroes que la Horda ha tenido. ¡Cuando crezca, quiero ser un héroe de la Horda también!$B$B¿Conoces a Cairne, $N? ¿Podrías hacerme un favor, por favor? ¿Podrías conseguirme su huella? No sé cómo preguntarle, y probablemente estaría demasiado ocupado para alguien como yo... ¡pero tú! ¡Eres un aventurero como él! ¡Apuesto a que te daría su huella fácilmente!$B$B¿Por favor?",
    ["O"] = "Viaja a Cima del Trueno para conseguir la huella de Cairne Pezuña de Sangre para tu pupilo.",
    ["T"] = "La Huella de Cairne",
  },
  [926] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Piedra de Poder Defectuosa",
  },
  [927] = {
    ["D"] = "El corazón de Musgondo el Fétido está cubierto de un musgo oscuro y aceitoso. De hecho, la única forma de saber que esto es un corazón es por su lento y rítmico latido... Un latido que continúa incluso ahora.$B$BQuizás Denalan quiera ver este corazón.",
    ["O"] = "Lleva el Corazón Enredado en Musgo a Denalan en el Lago Al\'Ameth.",
    ["T"] = "El Corazón Entrelazado de Musgo",
  },
  [928] = {
    ["D"] = "Aunque hay más cosas de las que podría hablarte sobre los pozos lunares y Teldrassil, debo enviarte. Corithras Lunardiente estará esperándote. He vertido la botella de agua que me trajiste en este recipiente para llevárselo.$b$bBusca a Corithras. Lo encontrarás en el pozo lunar en Dolanaar. Sigue el camino al sur de Aldrassil fuera de la Arboleda de la Sombra, y continúa siguiendo los adoquines mientras el camino gira hacia el oeste.$b$bPero asegúrate de mantenerte en el camino, $n. Últimamente hay bestias peligrosas en los bosques.",
    ["O"] = "Lleva el Recipiente Parcialmente Lleno a Corithras Moonrage en Dolanaar.",
    ["T"] = "Corona de la Tierra",
  },
  [929] = {
    ["D"] = "Primero, déjame contarte más sobre la tarea que debes completar. Los druidas en Darnassus usan el agua de los pozos de la luna de Teldrassil, y su pozo de la luna debe ser repuesto de vez en cuando. Usando estas fiolas especialmente elaboradas, puedes recolectar el agua de los pozos de la luna.$b$bLleva este recipiente al pozo de la luna fuera de la Aldea Brisa Estelar al este, y llénalo con algunas de sus aguas, luego regresa conmigo. Cuando hayas completado tu tarea, continuaré la historia donde Tenaron la dejó...",
    ["O"] = "Llena el Frasco de Jade y llévaselo a Corithras Lunar iracundo en Dolanaar.",
    ["T"] = "Corona de la Tierra",
  },
  [930] = {
    ["D"] = "Colgando de las frondas de esta planta brillante hay una fruta grande y redonda.$B$BEstás seguro de que Denalan querría estudiar esta fruta.",
    ["O"] = "Lleva la fruta brillante a Denalan en el Lago Al\'Ameth.",
    ["T"] = "La Fruta Brillante",
  },
  [931] = {
    ["D"] = "Los frondas de esta planta brillan con la luz del bosque, dándole un aura retorcida y pulsante.$B$BCrees que Denalan querría una muestra de esto.",
    ["O"] = "Lleva un Frondaluz a Denalan en el Lago Al\'Ameth.",
    ["T"] = "El Frondio Reluciente",
  },
  [932] = {
    ["D"] = "Debo advertirte, $n, este asunto debe quedar entre nosotros. Los sátiros ya son una vergüenza suficiente para nosotros, y este está demasiado cerca de casa.$B$BSe llama Lord Melenas. Reside en la cueva cercana de Roca Vil, donde ha reunido a un gran grupo de guerreros grell. Su corazón es negro como la noche y planea algo muy oscuro.$B$BDebes encontrarlo dentro de su cueva, justo al norte de aquí, y traerme su cabeza.",
    ["O"] = "Mata a Lord Melenas y lleva su cabeza a Tallonkai Raízveloz en Dolanaar.",
    ["T"] = "Odio Retorcido",
  },
  [933] = {
    ["D"] = "Hay otro pozo lunar al sureste de la entrada a Darnassus, en las orillas de las Pozas de Arlithrien. Las Centinelas están teniendo problemas para patrullar el área debido a los ataques y al creciente mal humor de los furbolgs Zarpa Parda.$B$BTen cuidado mientras buscas el pozo, y mantén tus armas cerca.",
    ["O"] = "Llena el Frasco de Turmalina y llévaselo a Corithras Lunar iracundo en Dolanaar.",
    ["T"] = "Corona de la Tierra",
  },
  [934] = {
    ["D"] = "Sin embargo, no todo estaba bien con Teldrassil. Los planes cuidadosamente elaborados de Corzocelada para el nuevo Árbol del Mundo habían dado fruto como él esperaba, pero había un pequeño problema, un problema al que se pueden atribuir muchos de los problemas en Teldrassil.$B$BSin embargo, no entraré en eso todavía. Debes visitar el último pozo lunar, al noroeste en la Arboleda del Oráculo. Bajo las ramas del Árbol del Oráculo se encuentra el primero y más poderoso de nuestros pozos. Recupera un frasco de su agua y regresa conmigo.",
    ["O"] = "Llena el Frasco Amatista y llévaselo a Corithras Lunar iracundo en Dolanaar.",
    ["T"] = "Corona de la Tierra",
  },
  [935] = {
    ["D"] = "Sin las bendiciones de Alexstrasza la Protectora de la Vida y Nozdormu el Atemporal, el crecimiento de Teldrassil no ha estado exento de fallas. Se han reportado extrañas bestias surgiendo del mismo suelo del árbol, y furbolgs enloquecidos atacan a los viajeros que pasan.$B$BSolo puedo esperar que la solución que busca el Archidruida se encuentre rápidamente. Verteré todas las fiolas que trajiste en este recipiente, para que lo entregues a Darnassus.$B$BLlévaselo a Fandral Staghelm, lo encontrarás en la arboleda de los druidas.",
    ["O"] = "Lleva el Recipiente Lleno al Archidruida Fandral Corzocelada en Darnassus.",
    ["T"] = "Corona de la Tierra",
  },
  [936] = {
    ["D"] = "Disculpa $c, pero me gustaría un momento de tu tiempo si es posible.$B$BEl Círculo Cenarion en Cima del Trueno está muy interesado en aventureros experimentados como tú para que les brinden ayuda en un proyecto de investigación vital. Aunque no conozco los detalles, puedo informarte que nada menos que Hamuul Runetotem está al frente de esta investigación.$B$BPor favor, si estás interesado, habla con él directamente en el Alto de los Ancestros dentro de Cima del Trueno.",
    ["O"] = "Habla con el Archidruida Hamuul Tótem de Runa en Cima del Trueno.",
    ["T"] = "Asistiendo al Archidruida Runatótem",
  },
  [937] = {
    ["D"] = "Fui enviado con un pequeño grupo de Centinelas aquí para proteger el Árbol del Oráculo de las arpías que han hecho nidos por todo el claro. Poco a poco estamos intentando hacerlas retroceder.$B$BCuando el Árbol del Oráculo intentó enviar un mensajero a Darnassus con un informe, el mensajero fue atacado y asesinado por un grupo de arpías.$B$BSi te sientes capaz, ve a sus nidos, mátalas y devuélveme sus cinturones como prueba de tus hazañas.",
    ["O"] = "Consigue 6 Cinturones Plumavil y llévaselas a la Centinela Arynia Rompenubes en la Arboleda del Oráculo.",
    ["T"] = "La Arboleda Encantada",
  },
  [938] = {
    ["D"] = "El pelaje del sabueso es de un distintivo color gris, mezclándose con el bosque circundante. Mientras yace allí, observas que está gravemente herida, con profundas laceraciones en su espalda y estómago.$B$BLevanta la cabeza y te mira, una brillante inteligencia brilla en sus ojos azules. Parece querer seguirte.",
    ["O"] = "Escolta a Niebla hasta la Centinela Arynia Rompenubes en el pozo de la luna cerca del Árbol del Oráculo.",
    ["T"] = "Niebla",
  },
  [939] = {
    ["D"] = "Estudias la simple flauta de madera que recogiste después de derrotar a Xavaric.$B$BLa madera nudosa se siente sólida, pero parece que las espirales y los patrones en la superficie comienzan a girar y moverse mientras la giras en tus manos. De repente, sientes una sensación de temor...$B$BSacudes la cabeza, seguro de que tu mente te está jugando una mala pasada.$B$BEs posible que otros sátiros lleven pistas sobre lo que has encontrado. Lleva tus hallazgos al druida, Eridan Bluewind, que vive en el sur de Felwood.",
    ["O"] = "Lleva la Flauta de Xavaric y 5 muestras de Vínculo de Fuego de Jade a Eridan Bluewind en el sur de Frondavil.",
    ["T"] = "Flauta de Xavaric",
  },
  [940] = {
    ["D"] = "El bosque susurra sobre tus hazañas de valor, $n. Mientras sentía que las arpías eran expulsadas de sus nidos, una mayor calma se extendió por el bosque, sus criaturas sintiéndose seguras una vez más. Temía enviar otro mensajero al Archidruida, pero sé que llevarás mi mensaje de manera segura a él.$b$bEntrégale esto y sabe que el bosque te protegerá en sus caminos hacia el bosque de piedra.",
    ["O"] = "Entrega el informe del Árbol Oráculo al Archidruida Fandral Staghelm en Darnassus.",
    ["T"] = "Teldrassil",
  },
  [941] = {
    ["D"] = "He eliminado ese musgo vil del corazón, pero sigue estando contaminado...$B$BIntenta colocar el corazón en mi maceta. Está llena de un suelo nutritivo que puede limpiar y sanar el corazón.",
    ["O"] = "Planta el Corazón Contaminado en la Maceta de Denalan.",
    ["T"] = "Plantando el Corazón",
  },
  [942] = {
    ["D"] = "Este fósil tiene un sorprendente parecido con uno que vi una vez en Forjaz. Un arqueólogo llamado Flagongut lo llevó a nuestra conferencia anual de la Liga de Expedicionarios. Creía que el fósil tenía un poder que podía ser desbloqueado de alguna manera.$b$b Lo último que supe es que Flagongut se había refugiado en la Taberna del Agua Profunda en el Puerto de Menethil, esperando escolta al Sitio de Excavación de Whelgar. Búscalo y muéstrale el fósil de Remtravel. Quizás pueda revelar los secretos del pasado.$b$b El barco a Menethil sale de Theramore.",
    ["O"] = "Lleva el fósil misterioso al Arqueólogo Flagongut en Puerto de Menethil.",
    ["T"] = "El Prospector Distraído",
  },
  [943] = {
    ["D"] = "El Prospector Whelgar ahora tiene el fósil original en su sitio en los Humedales. Cuando descubrí la Piedra de Relu, creí que era la clave para desbloquear el misterio del fósil.$b$bMientras viajaba por el camino para encontrarme con Whelgar, fui atacado por raptores y la reliquia se perdió. No sé cuál de las bestias moteadas se tragó la reliquia, pero si puedes recuperarla, puedo desbloquear el poder de estos artefactos.$B$BTráeme tanto la Piedra de Relu como el fósil original que él tiene.",
    ["O"] = "El Arqueólogo Flagongut en Puerto de Menethil quiere que le traigas la Piedra de Relu y el Fósil de Flagongut.",
    ["T"] = "El Prospector Distraído",
  },
  [944] = {
    ["D"] = "Es extraño que los naga estén aquí, en Costa Oscura. Pero aún más extraño... es el Martillo Crepuscular. Ese culto adora a los antiguos, antiguos señores de la tierra. Señores derrotados hace mucho tiempo.$B$BEl Glave del Maestro, al sur, es un lugar donde cayó uno de esos antiguos señores.$B$BVe allí y busca más cultistas del Martillo Crepuscular.$B$BToma este frasco de adivinación. Úsalo para crear un cuenco de adivinación después de buscar en el Glave del Maestro. Luego habla conmigo a través del cuenco.",
    ["O"] = "Reúne información, luego usa el Frasco de Videncia para crear un Cuenco de Videncia. Usa el cuenco para hablar con Onu.",
    ["T"] = "La Espada del Maestro",
  },
  [945] = {
    ["D"] = "¡Ayuda!$B$BEstaba vagando por la Espada del Maestro, y estos sucios cultistas me rodearon. ¡¡Por suerte soy bueno escondiéndome!!$B$B¿Puedes ayudarme a salir de aquí? Y necesito enviarle un mensaje a mi hermana Therysil para decirle que estoy bien. Ella está en Vallefresno, al sur, en el Santuario de Aessina.",
    ["O"] = "Ayuda a Therylune a escapar, luego dile a Therysil en el Santuario de Aessina que su hermana está a salvo.",
    ["T"] = "El Escape de Therylune",
  },
  [946] = "_",
  [947] = {
    ["D"] = "Estoy ideando una poción que requiere hongos raros, hongos que solo crecen en una cueva en particular. La cueva se encuentra detrás de las Cataratas de Salto del Risco, al este y un poco al norte a lo largo de las montañas.$B$BIría allí yo mismo, pero me aconsejaron desde la Arboleda de los Ancianos que me mantuviera alejado de ese lugar. Nuestros venerables aliados sienten que la cueva es el escondite de un nuevo mal en Costa Oscura.$B$BPor favor, $N, recoge los hongos para mí. Y mientras lo haces, explora la cueva para confirmar los temores de los Ancianos.",
    ["O"] = "Lleva 5 Tallos de Costra y 1 Soma de la Muerte a Barithras Moonshade en Auberdine.",
    ["T"] = "Hongos de la Cueva",
  },
  [948] = {
    ["D"] = "Onu, un Ancestro del Conocimiento en la Arboleda de los Ancestros, sabe de tu viaje a las Cataratas Aguaescarpada y desea hablar contigo. La arboleda se encuentra al sur, cerca de las montañas.$B$BLos Ancestros son pacientes y sabios, $N. Pero si Onu busca tu consejo sobre lo que viste en las cataratas, entonces temo que es urgente.",
    ["O"] = "Habla con Onu en la Arboleda de los Ancianos.",
    ["T"] = "Onu",
  },
  [949] = {
    ["D"] = "El propósito del Martillo Crepuscular en el Glaive del Maestro no se conoce. Pero debe ser oscuro.$B$BBusca en su campamento en el Glaive... Busca algo que pueda enseñarnos sus intenciones.$B$BPorque debemos aprender sus intenciones antes de que se haga un plan.",
    ["O"] = "Encuentra una pista en el campamento del Martillo Crepuscular en la Enclava del Maestro.",
    ["T"] = "El Campamento del Crepúsculo",
  },
  [950] = {
    ["D"] = "Mirar dentro del Tomo Crepuscular es enloquecedor. El texto comienza a nadar y bailar después de solo un momento de mirarlo, y una vez que los hermosos adornos se retuercen en formas grotescas.$B$BArrancar una página de garabatos insanos es todo lo que es posible antes de que un desmayo te sobrecoja.",
    ["O"] = "Lleva a Onu los Garabatos Dementes recuperados.",
    ["T"] = "Regreso con Onu",
  },
  [951] = {
    ["D"] = "El pergamino que encontraste en la Espada del Maestro es una página de un texto antiguo... ¡escrito por elfos! Está muy maldito y corrompido por el Martillo Crepuscular, pero parece que el culto está usando magia élfica antigua para traer a sus antiguos amos de regreso a nuestro mundo.$B$BViaja a las ruinas de Mathystra al noreste y busca reliquias antiguas entre la hierba y las piedras rotas. A través de su estudio, podemos descubrir cómo el Martillo Crepuscular pretende explotar la magia de los elfos.",
    ["O"] = "Lleva 6 Reliquias de Mathystra a Onu en la Arboleda de los Ancianos.",
    ["T"] = "Reliquias de Mathystra",
  },
  [952] = {
    ["D"] = "Si tuviste tiempo suficiente para llevar mensajes para el Árbol del Oráculo, estoy seguro de que puedo presionarte para que entregues un mensaje a la Arboleda de los Ancestros en Costa Oscura, al sur de Auberdine.$b$bLo más probable es que tengas que asegurar el transporte en un hipogrifo, pero tengo suficiente fe en ti para que puedas manejar eso. Lleva esto a Onu, el Ancestro del Conocimiento. Ha estado esperando noticias mías, incluso como yo esperaba noticias de la Arboleda del Oráculo.",
    ["O"] = "Entrega el mensaje de Fandral a Onu en la Arboleda de los Ancianos en Costa Oscura, al sur de Auberdine.",
    ["T"] = "Arboleda de los Ancianos",
  },
  [953] = {
    ["D"] = "Al este encontrarás las ruinas de Ameth\'Aran. Ahora está habitada por los espíritus inquietos de los Altonato que una vez vivieron dentro de sus muros, pero una vez fue un lugar donde los sirvientes de Azshara practicaban libremente sus poderosas magias.$b$bFui enviado a explorar las ruinas y me encontré con dos grandes tablillas, garabateadas con las historias de Ameth\'Aran y su caída. Mientras leía las runas, fui atacado por los espíritus y huí.$b$bPor favor, si puedes, aventúrate en las ruinas y descifra las tablillas en mi lugar.",
    ["O"] = "Estudia las tablillas que hablan de Ameth\'Aran y de su caída, luego regresa con la Centinela Tysha Filoluna en Costa Oscura.",
    ["T"] = "La Caída de Ameth\'Aran",
  },
  [954] = {
    ["D"] = "Las ruinas de Bashal\'Aran al este están infestadas de esbirros demoníacos. Los espíritus y sátiros que han tomado residencia en el área se alimentan de las energías mágicas del lugar, sus poderes creciendo con la exposición continua.$B$BAún con eso, he notado que hay un santuario al que no se acercan. En el lado oeste de las ruinas, sobre un pequeño acantilado, un extraño aura azul impregna... Debe haber una explicación para la renuencia de los demonios.$B$BMe gustaría que lo investigaras.",
    ["O"] = "Encuentra la fuente de la extraña aura azul en las ruinas de Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [955] = {
    ["D"] = "Si tuviera que relatar la historia de mi vida, no tengo dudas de que superaría los límites de tu paciencia. Digamos que la mía ha sido una vida larga y dolorosa, y esta forma espectral es quizás el peor tormento de todos.$b$bMe mantienen aquí por medios mágicos. Aunque mis palabras pueden parecer insinceras, te aseguro que estaría eternamente agradecido si me ayudaras a encontrar los medios de mi encarcelamiento. Un sello me ata, y al examinar las piezas de los grells y los espíritus, puedo encontrar un rastro de él.",
    ["O"] = "Consigue 8 Aretes de Grell para Asterion en Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [956] = {
    ["D"] = "Si los grells han entrado en contacto cercano con el sello que ata mi prisión eterna, sospecho que conozco la causa. Sin duda, el sello ha caído en posesión del sátiro que los lidera.$b$bSiento firmemente que esto debe ser cierto, $n. Uno de los sátiros seguramente lo posee. Si puedes obtenerlo, me acercarías tanto a superar las barreras de mi prisión que las lágrimas brotarían de mis ojos.",
    ["O"] = "Consigue el Sello de Piedra Lunar Antiguo y llévaselo a Asterion en Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [957] = {
    ["D"] = "Fue el arte de uno de los más poderosos de los Altonato lo que creó el sello que formó mi prisión. En Ameth\'Aran, las ruinas al sur que son gemelas a estas, persiste incluso hoy una antigua llama, de color azul. En esta llama, este sello podría ser destruido.$b$bTen cuidado en las ruinas, $n...",
    ["O"] = "Destruye el Sello de Piedra Lunar Antiguo en la llama antigua en Ameth\'Aran, luego regresa con Asterion en Bashal\'Aran.",
    ["T"] = "Bashal\'Aran",
  },
  [958] = {
    ["D"] = "$C. Tengo una tarea para ti.$B$B¿Has visto las ruinas de Ameth\'Aran? Si no, las encontrarás en el lado este del camino principal, un poco hacia el sur. Una vez fueron el hogar de muchos Altonato poderosos, ahora son un testimonio de la destrucción que sus experimentos produjeron.$B$BMe han dicho los Centinelas que los espíritus de los Altonato persisten, y que manejan sus antiguos implementos mágicos. Esas reliquias deben ser apropiadas para que podamos destruirlas.",
    ["O"] = "Recupera 7 Reliquias Altonato para Thundris Tejeviento en Auberdine.",
    ["T"] = "Herramientas de los Altonatos",
  },
  [959] = {
    ["D"] = "¡Ups!$b$b¡Tuve un pequeño accidente, como puedes ver! El envío de carga de la mañana desde Bahía del Botín fue enorme. ¡Enorme, digo! Nunca había visto tantas cajas. Bueno, estaba tratando de agilizar las cosas, así que comencé a descargar un poco más de lo que podía manejar. Fue entonces cuando todo se vino abajo.$b$bAntes de que me diera cuenta de lo que estaba pasando, vi a esa amenaza, Mad Magglish, corriendo con una botella de vino de 99 años. Lo perseguí hasta las Cuevas de los Lamentos. No había forma de que entrara allí.$b$b¿Tal vez seas lo suficientemente valiente como para encontrarlo?",
    ["O"] = "El Operador de Grúa Bigglefuzz en Ratchet quiere que recuperes la botella de Oporto Añejo de 99 Años de Mad Magglish, quien se esconde en las Cuevas de los Lamentos.",
    ["T"] = "Problemas en los Muelles",
  },
  [960] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Onu está meditando",
  },
  [961] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Onu está meditando",
  },
  [962] = {
    ["D"] = "La Sociedad Real de Boticarios, con sede en la gran Entrañas de Lordaeron, me ha enviado aquí para un servicio muy específico, $n. Quizás desees ayudarme, y a su vez a la Dama Oscura en nuestros esfuerzos por avanzar a los No-Muertos.$B$BRecientemente estudié un raro espécimen de flora llamado Flor Serpentina. Creo que en mayores cantidades esta hierba tiene un gran potencial.$B$BDesafortunadamente, la Flor Serpentina solo se puede encontrar en los recovecos más oscuros de las Cuevas de los Lamentos, un peligroso sistema de cuevas ubicado en los Baldíos.",
    ["O"] = "El Boticario Zamah en Cima del Trueno quiere que recolectes 10 Flores de Serpiente.",
    ["T"] = "Flor de Serpiente",
  },
  [963] = {
    ["D"] = "En el resplandor de las batallas en el Pozo de la Eternidad, escuché que Ameth\'Aran había sido destruida, su gente muerta, incluyendo a mi amor, Anaya.$b$bNunca hubiera pensado, miles de años después, que los recuerdos de Anaya todavía acecharían mis sueños. Deambulando por los bosques de Costa Oscura en un estupor, me encontré en las ruinas de Ameth\'Aran... donde vi el espíritu atormentado de mi amada.$b$bDebe ser liberada, pero me falta el corazón para hacerlo. Su espíritu debe ser destruido.",
    ["O"] = "Libera el espíritu de Anaya Correalba y lleva su colgante de vuelta a Cerellean Garra Blanca en Auberdine.",
    ["T"] = "Por Amor Eterno",
  },
  [964] = {
    ["D"] = "La llave de Scholomance se llama Llave Esquelética. Debe forjarse a partir de los restos de un esqueleto, varios en realidad, y endurecida solo con los metales más fuertes dentro de un molde adecuado. Un sello de poder de un ser que naturalmente puede abrir el portal a Scholomance hará que la llave funcione finalmente.$B$BPrimero lo primero, $N. Necesitaremos fragmentos esqueléticos para la forja de la llave. Los esqueletos dentro de Andorhal deberían darnos lo que necesitamos, pero los que están fuera de los muros también podrían servir.",
    ["O"] = "Lleva 15 Fragmentos de Esqueleto al Boticario Dithers en El Baluarte, Tierras de la Peste del Oeste.",
    ["T"] = "Fragmentos de Esqueleto",
  },
  [965] = {
    ["D"] = "Saludos, joven $r. Soy Elissa Starbreeze, y mi deber es proteger a Auberdine del daño.$b$bCon este fin, envié a Balthule Shadowstrike a observar los extraños sucesos alrededor de la Torre de Althalaxx al noreste.$b$bYa es hora de que haya regresado. Me preocupa que haya encontrado algún peligro imprevisto en el bosque. Te agradecería mucho si lo encontraras y te aseguraras de que está bien.",
    ["O"] = "Encuentra a Balthule Golpesombra cerca de la Torre de Althalaxx en Costa Oscura.",
    ["T"] = "La Torre de Althalaxx",
  },
  [966] = {
    ["D"] = "Un grupo de brujos se ha establecido alrededor y dentro de la torre. Habría regresado a Auberdine para informar a Elissa antes, pero tenía miedo de perderme algo mientras estaba fuera.$B$BDelgren sospechaba que tal compañía podría estar reuniéndose en la torre, pero no sabía por qué.$B$BAlgunos fragmentos de pergamino que llevan los brujos han llegado a mis manos, pero necesito más para completar el rompecabezas. Te advierto que no entres en la torre, sin embargo, ¡los brujos allí son mucho más poderosos!",
    ["O"] = "Recolecta 4 Pergaminos Gastados para Balthule Golpe de las Sombras cerca de la Torre de Althalaxx.",
    ["T"] = "La Torre de Althalaxx",
  },
  [967] = {
    ["D"] = "Mi maestro, Delgren el Purificador, es un paladín que amablemente ha ofrecido su ayuda para defender nuestros bosques de las fuerzas de los demonios y los no muertos. Me ha enseñado mucho sobre la Luz Sagrada y el arte de la batalla.$b$bDelgren debe saber de las operaciones del culto de inmediato.$b$bLo encontrarás muy al sur de aquí, en el Puesto de Maestra en el Bosque de Argénteos. Sé rápido, temo que la amenaza de la Orden Oscura crece con cada hora que pasa.",
    ["O"] = "Entrega la carta de Balthule a Delgren el Purificador en el Bosque de Vallefresno.",
    ["T"] = "La Torre de Althalaxx",
  },
  [968] = {
    ["D"] = "Aunque está terriblemente desgastado y rayado, el título de este libro aún puede leerse en su portada:$B$BLos Poderes de Abajo$B$BDentro del libro hay una lista de nombres impronunciables con títulos siniestros, métodos de adoración y sacrificios preferidos... muchos de los cuales incluyen criaturas humanoides vivas.$B$BEn la portada interior del libro están las palabras: \"Runas y Destinos de Bonegrip, La Caverna Abandonada, Forjaz. Propietario: Gerrig Bonegrip.\"$B$BSi Bonegrip vende libros como este, quizás compre este de vuelta.",
    ["O"] = "Lleva el Libro: Los Poderes Inferiores a Gerrig Bonegrip en la Caverna Forlorn, Forjaz.",
    ["T"] = "Los Poderes de las Profundidades",
  },
  [969] = {
    ["D"] = "Ah, puedo decir con solo mirarte que quieres hacer un amuleto de la suerte. ¡Mau\'ari siempre lo sabe!$B$BAl sur, encontrarás un cañón, y gigantes hechos de hielo sólido. Los gigantes a veces los llevan, pero siempre puedes recolectar los fragmentos tú mismo del cañón. Estos fragmentos tienen una poderosa cualidad reflectante.$B$BTráeme 10 fragmentos de Golpe de Escarcha, y mi amuleto de la suerte será tuyo.",
    ["O"] = "Recolecta 10 Fragmentos de Machacahielo para el Doctor Brujo Mau\'ari en Everlook.",
    ["T"] = "Que la Suerte te Acompañe",
  },
  [970] = {
    ["D"] = "La carta de Balthule es grave. Este Culto de la Orilla Oscura es una espina en mi costado que debe ser eliminada. He estado lidiando con algunos de los canallas de la Orilla Oscura al noreste de aquí en Ordil\'Aran. Uno de ellos posee una gema de alma que creo que contiene el secreto del poder del culto.$b$bTráemela, y podré descifrar los secretos que contiene.",
    ["O"] = "Recolecta una Gema de Alma Resplandeciente y llévasela a Delgren el Purificador en el Puesto de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [971] = {
    ["D"] = "Como sabrás, colecciono historias. Historias antiguas. Historias poderosas. Historias que abren portales y que pueden despertar a Aquellos que Duermen.$B$BHay rumores de una antigua historia, el Manuscrito de Lorgalis, en el fondo de las Profundidades de Brazanegra en Vallefresno. Ese lugar es la antigua morada de elfos muertos hace mucho tiempo. Elfos que poseían un gran conocimiento antes de que su ciudad cayera en ruinas.$B$BBusca en las Profundidades de Brazanegra el manuscrito. Haz esto, y no lo olvidaré. Ni siquiera después de... los días finales.",
    ["O"] = "Lleva el Manuscrito de Lorgalis a Gerrig Bonegrip en la Caverna Forlorn en Forjaz.",
    ["T"] = "Conocimiento en las Profundidades",
  },
  [972] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sapta de Agua",
  },
  [973] = {
    ["D"] = "Ilkrud Magthrull. Sí, tengo conocimiento de él. Es un poderoso brujo orco que reside en el Santuario de la Cicatriz de Fuego, un lugar de gran mal en el suroeste de Vallefresno.$b$bHabía sido poco más que una molestia hasta ahora, y no me había ocupado de él, pero parece que su tiempo ha llegado.",
    ["O"] = "Lleva el Tomo de Ilkrud Magthrull a Delgren el Purificador en el Puesto de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [974] = {
    ["D"] = "Las aguas termales aquí son una fuente de misterio para mí. ¿De dónde viene el calor?$B$BMirando lo cerca que están las piscinas de la Cresta del Penacho de Fuego, realmente me pregunto si el volcán podría tener algo que ver con eso. Creé un termómetro para tomar lecturas de las temperaturas en el volcán, y me gustaría encontrar la parte más caliente. ¡Da las mediciones en grados Kraklenheit!$B$BAquí, tómalo y toma lecturas de las temperaturas en la cresta cada vez que veas un punto caliente.",
    ["O"] = "Krakle en el Cráter de Un\'Goro quiere que encuentres el área más caliente de la Cresta del Penacho de Fuego.$B$BCada vez que encuentres un punto caliente, haz clic derecho en el termómetro para comprobar la temperatura. Sigue buscando hasta encontrar el más caliente.",
    ["T"] = "Encontrando la Fuente",
  },
  [975] = {
    ["D"] = "Ahora, antes de darte este amuleto, mejor te explico cómo usarlo.$B$BSi llevas el amuleto contigo, encontrarás que ciertos objetos caerán de las criaturas que luches. Si no tienes el amuleto contigo, no los encontrarás. ¿Entiendes?$B$BSabrás que estos objetos especiales por su nombre: se llaman E\'ko. Si encuentras alguno de estos objetos, simplemente tráelos a la vieja Mau\'ari, y ella te ayudará.$B$BEspera aquí mientras arreglo tu amuleto.",
    ["O"] = "Habla con el Doctor Brujo Mau\'ari de nuevo en un momento para recibir el amuleto de la suerte.",
    ["T"] = "Alijo de Mau\'ari",
  },
  [976] = {
    ["D"] = "Delgren me ha pedido que entregue mensajes y suministros a la Centinela Astarbreeze en Auberdine, pero me da miedo ir solo. Enemigos peligrosos operan en el bosque, y no deseo arriesgarme a que estos suministros e información caigan en las manos equivocadas.$B$BSi tú y otros pudierais actuar como mis guardias, podría irme ahora. Con vuestra protección, no tengo dudas de que podremos llevar estos bienes sanos y salvos a Auberdine.",
    ["O"] = "Habla con Delgren el Purificador en el Puesto de Maestra después de escoltar a Feero sano y salvo a través del Bosque de Vallefresno.",
    ["T"] = "Suministros a Auberdine",
  },
  [977] = {
    ["D"] = "Ahora que tengo lo que necesito para cubrir las partes móviles mecánicas, solo necesito una cosa más. Bueno, en realidad dos. ¡Dos cuernos!$B$BLos patriarcas y matriarcas de los cardos de hielo tienen los cuernos más grandes, así que, por supuesto, esos son los que quiero. Ese tipo de yeti habita en una cueva al sureste. Hay muchos de ellos allí, no deberías tener problemas para encontrarlos.$B$BRecuerda, solo quiero los que se vean mejor: ¡no cuernos rotos o dañados, por favor!",
    ["O"] = "Recolecta 2 Cuernos de Yeti Pristinos para Umi Rumplesnicker en Everlook.",
    ["T"] = "¿Ya Llegamos, Yeti?",
  },
  [978] = {
    ["D"] = "Las plumas de lechúcico de las Tierras del Interior parecen contener rastros de cualidades mágicas. Estos poderes... no parecen ser manejados por las criaturas, es más como si fueran inherentes a la especie, parte de su esencia misma.$B$BDurante mis estudios, escuché que una gran población de lechúcicos habita en las tierras nevadas de Cuna del Invierno. ¿Podrían estas criaturas tener los mismos poderes? Por favor, ayúdame a encontrar una respuesta, $N. Ve a Cuna del Invierno y recoge plumas que hayan caído al suelo, luego regresa conmigo.",
    ["O"] = "Recolecta 10 Plumas Toque Lunar de Cuna del Invierno, luego regresa con Erelas Cielo Ámbar en Aldea Rut\'theran.",
    ["T"] = "Lechúcico Lunar",
  },
  [979] = {
    ["D"] = "Sí, sí, estas plumas parecen tener el mismo encantamiento mágico que noté anteriormente... Aunque no estoy seguro de lo que podríamos encontrar, te insto a que continúes buscando respuestas, $N.$B$BUn colega mío viajó recientemente a Cuna del Invierno para observar a los lechúcicos salvajes en su hábitat natural. ¿Por qué no te reúnes con ella y ves qué ha descubierto?",
    ["O"] = "Encuentra a Ranshalla en el este de Cuna del Invierno.",
    ["T"] = "Encuentra a Ranshalla",
  },
  [980] = {
    ["D"] = "He oído hablar de otros manantiales calientes en un lugar llamado Cuna del Invierno, muy al norte de aquí. Lo extraño es que no hay volcanes cerca de ellos. Interesante, lo sé.$B$BBueno, si quieres saber más, deberías dirigirte a Cuna del Invierno y hablar con una amiga mía. Su nombre es Donova Snowden, y actualmente se encuentra cerca de los manantiales calientes allí.$B$B¡Gracias de nuevo por ayudarme con mi termómetro! ¡Nos vemos luego!",
    ["O"] = "Viaja a Cuna del Invierno y habla con Donova Snowden.",
    ["T"] = "Los Nuevos Manantiales",
  },
  [981] = {
    ["D"] = "Por todo el trabajo que has hecho, mereces una buena recompensa. Regresa con Delgren y dile que Athrikus Narassin yace derrotado por tu mano, y que el Culto de la Orden Oscura está vencido y disperso.",
    ["O"] = "Habla con Delgren el Purificador en el Puesto de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [982] = {
    ["D"] = "Al norte de Costa Oscura, frente a la costa, hay dos barcos naufragados: el Alba Plateada y el Velo de Niebla. Hace algún tiempo, ambos barcos se toparon con los malditos múrloc mientras navegaban por el vasto mar hacia Auberdine. Ahora yacen en el fondo del océano como trofeos para esos demonios.$B$BAmbos capitanes no lograron salir esa noche, y sus registros y otros efectos todavía están abajo en cajas fuertes. Me gustaría que los recuperaras para nosotros, significaría mucho para los miembros de la tripulación que todavía están por aquí.",
    ["O"] = "Recupera el Cofre del Alba Argenta y el Cofre del Velo de Niebla para Gorbold Mano de Acero en Auberdine. Ambos objetos deberían encontrarse a bordo de los restos de los barcos al norte del pueblo.",
    ["T"] = "Océano Profundo, Mar Vasto",
  },
  [983] = {
    ["D"] = "¡Oye! Ayúdame con mi último invento, el buzzbox. ¡Puedes hablar con personas lejanas!$B$BTal vez ya los hayas visto, son cajas con palancas. El único problema es que necesitan mantenimiento constante.$B$BCada uno sufre de un problema diferente, pero tomé una decisión muy inteligente. Coloqué cada uno cerca de criaturas que tienen las partes adecuadas para arreglar esa máquina en particular. En este momento, el Buzzbox 827 está fallando. Está justo al sur de Auberdine, muy cerca. Se necesitan 6 Patas de Rastreador para arreglarlo. Te pagaré...",
    ["O"] = "Recolecta 6 Patas de Rastrero y colócalas en la Caja de Bombas 827.",
    ["T"] = "Cajabón 827",
  },
  [984] = {
    ["D"] = "Algunos de mis hermanos fueron rescatados de un furbolg corrupto en Teldrassil, y he jurado detener más atrocidades antes de que más de nuestra especie sean heridos... o algo peor.$B$BHe visto un par de indicios de corrupción en Costa Oscura ya, pero aún no he encontrado signos generalizados. Creo que sería lógico si la investigación continuara con los furbolgs. ¿Encontrarías uno de sus campamentos y me informarías si ves algún signo de corrupción?",
    ["O"] = "Encuentra un campamento de furbolgs corruptos en Costa Oscura y regresa con Terenthis en Auberdine.",
    ["T"] = "¿Qué Tan Grande es la Amenaza?",
  },
  [985] = {
    ["D"] = "Ya has demostrado ser hábil en el reconocimiento de nuestro enemigo, $N. ¿Tienes lo que se necesita para luchar contra ellos también?$B$BNo todos los aventureros prefieren el combate directo al arte del sigilo y la evasión.$B$BSi crees que estás a la altura de la tarea, entonces el campamento furbolg al sur de Auberdine es actualmente la mayor amenaza para nuestra gente. Encontrarás algunos de la tribu Peloescarcha allí. Mata a 8 exploradores y 5 habladores del viento, y regresa conmigo aquí.",
    ["O"] = "Mata 8 Exploradores del Bosque Negro y 5 Hablavientos y regresa con Terenthis en Auberdine.",
    ["T"] = "¿Qué Tan Grande es la Amenaza?",
  },
  [986] = {
    ["D"] = "$N, tus habilidades ya me han ayudado en mi empresa. ¿Podría imponerme de nuevo para que ayudes a Zarparrío y a su maestro Volcor? Además, puedo confeccionarte una capa mágica que te permitirá caminar sin ser molestado por las criaturas de Costa Oscura mientras lo buscas.$B$BPara crear la capa, necesitaré cinco pieles finas de acechador lunar de un acechador lunar macho o hembra para tener suficiente material. Los gatos se pueden encontrar cerca del Río Doblez Salvaje al sur, o incluso más al sur cerca de Vallefresno.",
    ["O"] = "Encuentra 5 Pieles Finas de Acechador Lunar y llévaselas a Terenthis en Auberdine.",
    ["T"] = "Un Maestro Perdido",
  },
  [987] = "_",
  [990] = {
    ["D"] = "$N, mi señora, Raene Correlobo, te espera en la ciudad de Astranaar dentro de Vallefresno. Con tu ayuda, quizás podamos aliviar algo de la corrupción que se ha apoderado de allí.$B$BDirígete al sur desde aquí y mantente cerca del camino, lo encontrarás sin problemas si sigues esas direcciones.",
    ["O"] = "Encuentra a Raene Correlobo en Vallefresno.",
    ["T"] = "Travesía a Vallefresno",
  },
  [991] = {
    ["D"] = "$N, un viejo amigo mío también está ayudando a los Centinelas aquí en Vallefresno, pero aún no ha regresado.$B$BTenía pistas sobre encontrar un objeto que pensó que podría ralentizar los ataques de los furbolgs contra nuestro pueblo: una vara creada por un mago malvado ya muerto.$B$BAntes de irse, mencionó buscar una gema para la vara.$B$BMencionó que la gema podría estar escondida en el santuario del Lago Falathim, al pie de la montaña al oeste. La gema estaba allí antes de que fuera invadido.$B$BEncuentra a mi amigo, $N, por favor.",
    ["O"] = "Encuentra a Teronis en Vallefresno.",
    ["T"] = "La Purificación de Raene",
  },
  [992] = {
    ["D"] = "¡Tenemos una posición abierta para un topógrafo temporal de grado junior en la Compañía de Agua de Gadgetzan, si estás interesado!$B$B¡Con los nómadas tomando todos nuestros pozos, necesitamos explotar más fuentes de agua gratuitas! Hay agua en el desierto, pero solo para aquellos lo suficientemente inteligentes como para inspeccionarla primero.$B$BToma este artilugio de radiestesia y toma una muestra del agua cerca del Mirador de las Arenas Afligidas. Está justo a la vista de los trolls por allí. ¡Tráeme el artilugio muestreado cuando hayas terminado!",
    ["O"] = "Usa el artefacto de radiestesia sin usar cerca del estanque de agua junto a la Atalaya de los Penas de Arena. Una vez que hayas recolectado la muestra, devuelve el artefacto de radiestesia usado al Topógrafo Jefe Fizzledowser en Gadgetzan.",
    ["T"] = "Estudio del Agua de Gadgetzan",
  },
  [993] = {
    ["D"] = "La capa está completa, $N. Ha llegado el momento de encontrar a Volcor. Solo espero que estemos a tiempo.$B$BLa magia de la capa, una vez invocada, no durará mucho, quizás cinco minutos. Dependerá de ti saber cuándo usarla: te sugiero que esperes hasta que encuentres a Garraguja.$B$BSi lo que me ha dicho es cierto, encontrarás a su maestro en una cueva al sur de aquí. Garraguja estará junto al camino esperándote. Hazle una señal agitando la mano cuando lo veas, y él te indicará el camino hacia su maestro.",
    ["O"] = "Encuentra a Volcor en Costa Oscura y entrégale la Capa de Acechador Lunar Encantada.",
    ["T"] = "Un Maestro Perdido",
  },
  [994] = {
    ["D"] = "Muy bien, $N, la fuerza es la opción. Salgamos de aquí tan pronto como estés listo. Lucharemos hasta llegar al camino, Garramala debería seguir allí. Una vez allí, podemos separarnos.$B$BTerenthis querrá escuchar tu versión de la historia de inmediato, así que dirígete a Auberdine después de que salgamos de aquí.",
    ["O"] = "Protege a Volcor hasta que llegues al camino, luego habla con Terenthis en Auberdine.",
    ["T"] = "Escape por la Fuerza",
  },
  [995] = {
    ["D"] = "Muy bien entonces... si estás seguro, nos escaparemos de aquí. Salgamos cuando estés listo.$B$BRecuerda, después de que hayas escapado, reúnete con Terenthis de nuevo en Auberdine. Yo iré allí por mi cuenta, ya que probablemente me moveré más rápido que tú.",
    ["O"] = "Escapa de la cueva Furbolg y reúnete con Terenthis en Auberdine.",
    ["T"] = "Escape por Sigilo",
  },
  [996] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Vientoflor Corrupto",
  },
  [997] = {
    ["D"] = "¿Te diriges al sur? ¿Al Lago Al\'Ameth? Si es así, entonces tengo una tarea que podrías hacer por mí...$B$BMi colega Denalan tiene un campamento en el extremo oriental del lago, donde está estudiando y experimentando con la vida vegetal de Teldrassil. Él solicitó un paquete de tierras raras de Darnassus y llegó tarde, solo recientemente llegó aquí a Dolanaar.$B$B¿Podrías llevar el paquete a él?",
    ["O"] = "Lleva el paquete de Tierra Rara a Denalan en el Lago Al\'Ameth.",
    ["T"] = "La Tierra de Denalan",
  },
  [998] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Vientoflor Corrupto",
  },
  [999] = "_",
  [1000] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Hamuul Tótem de Runa en Cima del Trueno.",
    ["T"] = "La Nueva Frontera",
  },
  [1001] = {
    ["D"] = "Una pequeña voz crepita desde lo profundo de la máquina.$B$B\"¡Wizbang aquí! El siguiente Buzzbox está al norte de Auberdine, en la playa$B$BEntonces, ese Buzzbox es el número 411 y necesita 3 Ojos de Zarpador para sus reparaciones. ¡Están justo en la costa! Los zarpadores de Costa Oscura... *Hic*... Lo siento por eso...$B$BComo la última vez, cuando le des los objetos al Buzzbox, escupirá tu recompensa. *Hic*... ¿Qué? No, estoy bien!\"",
    ["O"] = "Recolecta 3 Ojos de Triturador de los Trituradores de Costa Oscura en el mar profundo cerca de la Caja de Bombas 411.",
    ["T"] = "Caja de zumbidos 411",
  },
  [1002] = {
    ["D"] = "El Buzzbox emite una ráfaga de estática mientras escuchas a Wizbang comenzar a hablar.$B$B\"El siguiente Buzzbox es el número 323. Está al norte de Auberdine pero... ¿dónde está la cerveza? ¿Qué? Oh, está en el camino, así que no deberías tener problemas para encontrarlo cerca del puente.\"$B$BWizbang murmura algo incomprensible antes de que escuches un fuerte sonido de trago.$B$B\"Este necesita Colmillos de Acechador Lunar... 6 de ellos.\"",
    ["O"] = "Recolecta 6 Colmillos de Acechador Lunar y colócalos en la Caja de Bombas 323.",
    ["T"] = "Caja de zumbidos 323",
  },
  [1003] = {
    ["D"] = "Sobre los ruidos hirvientes y los sonidos de goteo que provienen de la máquina, escuchas la voz temblorosa de Wizbang llamar.$B$B\"Los próximos están más al sur por el camino. No debería ser difícil de encontrar... justo al lado... cerca de un pantano. Creo que lo escondí en unos arbustos... Arbustos. Jejeje... Sí, la Caja de Zumbidos 525 toma cueros cabelludos de esos osos cardo grisáceos. Vaya, eso es un trabalenguas. Grisáceos... Jejeje...\"$B$BEscuchas más sonidos de trago mientras la máquina se apaga. Luego, de repente, vuelve a la vida.$B$B\"¡4!\"",
    ["O"] = "Recolecta 4 Cueros Cabelludos Canosos de los Osos Cardo Canosos al sur de Auberdine y colócalos en la Caja de Bombas 525.",
    ["T"] = "Caja de zumbidos 525",
  },
  [1004] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Hamuul Tótem de Runa en Cima del Trueno.",
    ["T"] = "La Nueva Frontera",
  },
  [1005] = "_",
  [1006] = "_",
  [1007] = {
    ["D"] = "Te encargaría una tarea, $N.$B$BEstaba en mi pequeña embarcación, navegando sobre las ruinas sumergidas de Zoram, cuando los naga me atacaron, surgiendo del agua y desgarrando mis carnes con sus garras. Huí, llevando los suministros que pude para hacer este modesto campamento.$B$BPero cuando llegué a la orilla y corrí... mi posesión más preciada se perdió.$B$BPor favor, $N, encuentra el lugar de mi emboscada, a lo largo de la costa al norte, y busca una antigua estatuilla. Es la razón por la que me he aventurado en los peligros de la Ensenada de Zoram.",
    ["O"] = "Lleva la Estatuilla Antigua a Talen, en su campamento cerca de la Ensenada de Zoram.",
    ["T"] = "La Estatua Antigua",
  },
  [1008] = {
    ["D"] = "Hay mal acechando en nuestra costa noroeste, conocida como la Ensenada de Zoram.$B$BEs el lugar de descanso de la ciudad condenada de Zoram, destruida durante el Cataclismo y sumergida bajo los mares. Ha estado perdida para los elfos nocturnos durante eras. Perdida, y casi olvidada.$B$BAhora, los naga han regresado, y por qué razón no lo sabemos. Pero la razón importa poco, debemos matar a estos demonios y arrojarlos de vuelta a las profundidades.$B$B¡Regresa a mí cuando hayas completado tu misión!",
    ["O"] = "Lleva 20 Cabezas de Colafuria a Shindrell Swiftfire en Astranaar.",
    ["T"] = "La Ensenada de Zoram",
  },
  [1009] = {
    ["D"] = "Temo que los secretos de la antigua estatuilla permanecerán ocultos para siempre a menos que encuentres la clave de su antiguo candado.$B$BEsa clave es un anillo, el Anillo de Zoram. Perteneció a los gobernantes de esta ciudad antes de que las olas del Cataclismo la ahogaran.$B$BDurante años, el anillo estuvo perdido, pero los naga lo han encontrado recientemente.$B$BSu líder, Ruuzel, habita en una isla en el borde norte de Zoram. Por favor, $N, ¡recupera el anillo para mí!",
    ["O"] = "Lleva el Anillo de Zoram a Talen cerca de la Ensenada de Zoram.",
    ["T"] = "Ruuzel",
  },
  [1010] = {
    ["D"] = "Hay una planta que una vez creció en las antiguas ruinas de la Mansión de Bathran al norte. La planta se llamaba Cabello de Bathran y era conocida por curar dolencias del espíritu.$B$BHay un niño enfermo en el pueblo, y creo que es más que una enfermedad física. ¿Irás a la Mansión de Bathran y buscarás Cabello de Bathran? Puede que lo necesite para tratar adecuadamente al niño.$B$BLa Mansión de Bathran se encuentra al norte del Puesto de Maestra y justo al sur de la frontera con Costa Oscura.",
    ["O"] = "Lleva 5 Cabellos de Bathran a Orendil Broadleaf en Vallefresno.",
    ["T"] = "Cabello de Bathran",
  },
  [1011] = {
    ["D"] = "Los No-Muertos, el brazo no-muerto de la Horda, se han infiltrado en Vallefresno, merodeando en nuestras ruinas y túmulos. Creemos que su objetivo es esparcir enfermedades entre las tierras de los elfos nocturnos. ¡Debemos confirmar esto!$B$BLos No-Muertos tienen un campamento al sur de aquí, cerca de la Guarida de Dor\'Danil, donde crean venenos y enfermedades y los colocan en botellas. Lucha hasta llegar a ese campamento y roba una botella de enfermedad. Regresa con dicha botella. La probaré y descubriré su propósito.",
    ["O"] = "Lleva una Botella de Enfermedad a Kayneth Vientosuave en Canción del Bosque.",
    ["T"] = "Enfermedades de los No-Muertos",
  },
  [1012] = {
    ["D"] = "Cuando luchaba contra los venenos de los No-Muertos, tuve una visión. Tuve una visión de los druidas en la Cámara de Dor\'danil, envenenados... ¡asesinados por los no muertos! Ahora son fantasmas errantes, separados de sus cuerpos y enloquecidos.$B$BAsí que para que los espíritus de los druidas puedan algún día encontrar paz, debes entrar en la Cámara de Dor\'danil y destruir a sus antiguos grandes líderes: Taneel Bosqueoscuro, Uthil Clamaluna y Mavoris Rompenubes.$B$BHaz esto, y luego regresa conmigo.",
    ["O"] = "Mata a los druidas: Taneel Bosqueoscuro, Uthil Clamaluna, Mavoris Rompenubes, luego regresa con Kayneth Vientosereno en Canción del Bosque.",
    ["T"] = "Druidas Insanos",
  },
  [1013] = {
    ["D"] = "$N, la Fortaleza de los Picos de las Sombras guarda un libro, el Libro de Ur, que sería muy apreciado en mi colección. Ur fue un gran mago de Dalaran antes de la llegada de la Plaga, sus estudios en otros mundos son de gran valor para... ciertos grupos entre los No-Muertos.$B$BEntra en la Fortaleza de los Picos de las Sombras y encuentra el libro. Tráemelo, y reportaré tu servicio a nuestra Dama Oscura...",
    ["O"] = "Lleva el Libro de Ur a Guardián Bel\'dugur en el Apothecarium en Entrañas.",
    ["T"] = "El Libro de Ur",
  },
  [1014] = {
    ["D"] = "Arugal todavía reside en el Castillo de Colmillo Oscuro. No podemos reclamar Argente como un bastión estratégico para la Dama Oscura hasta que Arugal sea asesinado.$b$bMe aseguraré de que su magia sea erradicada, $n. Pero dejo en tus manos asegurarte de que Arugal encuentre la muerte que tanto merece.$b$b¡Viaja al Castillo de Colmillo Oscuro y pon fin a los hechizos vil de Arugal de una vez por todas! ¡Tráeme la cabeza del vil mago!",
    ["O"] = "Mata a Arugal y lleva su cabeza a Dalar Tejealba en el Sepulcro.",
    ["T"] = "Arugal debe morir",
  },
  [1015] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Fandral Corzocelado en el Enclave Cenarion de Darnassus sobre el llamado del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La Nueva Frontera",
  },
  [1016] = {
    ["D"] = "Toma este pergamino de adivinación: puede determinar el creador de estos elementales. Pero necesitarás algo de ellos como reactivo para enfocar la magia del pergamino: sus brazaletes servirán.$B$BUnos pocos brazaletes intactos de las criaturas deberían ser suficientes. Los otros brazaletes serán inútiles para nuestros propósitos, pero pueden valer algo para un comerciante si deseas venderlos. Una vez que tengas los brazaletes intactos, intenta usar el pergamino. Regresa a mí con los resultados y procederemos desde allí.",
    ["O"] = "Recolecta 5 Brazales Elementales Intactos y usa el Pergamino de Adivinación en ellos. Después, lleva el Pergamino Adivinado a la Centinela Velene Estrella Fugaz en el Refugio Brisa de Plata.",
    ["T"] = "Brazales Elementales",
  },
  [1017] = {
    ["D"] = "¡Solo hay un mago no muerto en Los Baldíos capaz de profanar tantos elementales de agua! También es el único lo suficientemente tonto como para intentarlo. Sarilus Foulborne probablemente esté sentado en la cima del Pico Nublado, riéndose de mí.$B$B$n, debes adentrarte en territorio enemigo y destruir a este malhechor. Ya he tenido suficiente de su idiotez y debemos mostrarle lo que les pasa a aquellos que molestan a las Centinelas.$B$BDestrúyelo y tráeme su cabeza. Pero ten cuidado, la Horda podría estar vigilándote.",
    ["O"] = "Mata a Sarilus Foulborne y lleva su cabeza a la Centinela Velene Golpe Estelar en el Refugio Brisa de Plata.",
    ["T"] = "Invocador de Magos",
  },
  [1018] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Hamuul Tótem de Runa en Cima del Trueno.",
    ["T"] = "La Nueva Frontera",
  },
  [1019] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Fandral Corzocelado en el Enclave Cenarion de Darnassus sobre el llamado del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La Nueva Frontera",
  },
  [1020] = {
    ["D"] = "$N, he creado una cura que creo que ayudará al niño afectado. Llévala a Astranaar y entrégasela al padre del niño, Pelturas Lunablanca.$B$BPara llegar a Astranaar, sigue el camino hacia el sur, luego hacia el este.",
    ["O"] = "Lleva la Cura de Orendil a Pelturas Lunablanca en Astranaar.",
    ["T"] = "La Cura de Orendil",
  },
  [1021] = {
    ["D"] = "Escucha, no sé quién eres, pero necesito tu ayuda. Mis hermanas fueron a recuperar la Rama de Cenarius de los sátiros. Dijeron que volverían pronto, ¡pero han pasado dos días enteros!$B$B$n, ¡tienes que encontrarlas! Estaban buscando las ruinas de un templo elfo nocturno en Xavian, al noreste. No sé qué pensar. Busca a Anilia, ella las estaba liderando en la búsqueda.",
    ["O"] = "Encuentra a Anilia en Xavian.",
    ["T"] = "¡Sátiros Viles! ¡Dríades en Peligro!",
  },
  [1022] = {
    ["D"] = "Aunque hemos puesto muchos recursos y esfuerzo en expulsar a los demonios restantes del Frondavil al norte, nuestros éxitos han sido pocos. Hemos podido mantener gran parte de la presencia demoníaca fuera de Ashenvale.$b$bAl norte, cerca de la frontera con Frondavil, el santuario en ruinas de Mel\'Thandris ha sido tomado por misteriosos hombres lobo. Sus escalofriantes llamados han llevado a que el área sea conocida como el Valle Aullador. El Tomo de Mel\'Thandris guardado en el santuario podría arrojar algo de luz sobre por qué estos hombres lobo han llegado.",
    ["O"] = "Ve al Valle Aullante y estudia el Tomo de Mel\'Thandris, luego regresa con la Centinela Melyria Sombríscuro en el Santuario de Aessina.",
    ["T"] = "El Valle Aullador",
  },
  [1023] = {
    ["D"] = "Mientras revisas las pertenencias de Teronis, encuentras un diario que todavía está en buenas condiciones.$B$BTomas el diario y dejas el cadáver de Teronis como está.$B$BLa única cosa que queda por hacer es encontrar la gema que Teronis estaba buscando. Raene mencionó que estaba en el área. Quizás los múrlocs la tengan...",
    ["O"] = "Encuentra la Gema Brillante y llévasela a Raene Correlobo en Astranaar junto con el Diario de Teronis.",
    ["T"] = "La Purificación de Raene",
  },
  [1024] = {
    ["D"] = "Tendrás que investigar por tu cuenta, pero según las notas que Teronis ha hecho, parece que tu próximo curso de acción es encontrar las otras piezas de la vara que Dartol creó. Comenzaría yendo al pozo de la luna al este del Lago Iris. Cuando estés listo, sigue el camino y gira al norte cuando llegues a un pequeño área de descanso a tu izquierda.$B$BUna dríada llamada Shael\'dryn está mejor preparada para guiarte. Búscala.$B$BMantén la gema contigo, Shael\'dryn la necesitará si va a ayudarte a recrear la vara.",
    ["O"] = "Encuentra a Shael\'dryn en el pozo de la luna al noreste.",
    ["T"] = "La Purificación de Raene",
  },
  [1025] = {
    ["D"] = "Antes de que viajes al pozo lunar, tengo otra tarea que ayudará a la gente de Vallefresno si deseas poner a prueba tus habilidades.$B$BA lo largo del camino que va hacia el este antes del Río Falfarren, los furbolgs han estado atacando a los viajeros. Sus mentes torturadas se alimentan del miedo, y sus apetitos se sacian con la sangre de los inocentes.$B$BAyuda a las Centinelas a proteger las tierras mostrando a esos monstruos la misma misericordia que han mostrado a nuestra gente.$B$BCreo que sus campamentos están al sur del camino... ten cuidado.",
    ["O"] = "Mata 1 Vigía de la Guarida, 2 Ursas, 10 Totémicos y 12 Guerreros de la tribu Pellejo Sucio, y luego regresa con Raene Correlobo en Astranaar.",
    ["T"] = "Una Defensa Agresiva",
  },
  [1026] = {
    ["D"] = "Lamento que tus noticias no sean mejores.$B$BRaene tiene razón, sé sobre la creación de Dartol y los trémulos en el área, al menos lo sabía una vez. Ahora se han corrompido, su naturaleza retorcida.$B$BSi tu objetivo es encontrar la siguiente pieza de la vara, entonces tendrás que encontrar la llave para abrir el cofre en el que está.$B$BLa llave de madera está en uno de los trémulos al noreste de aquí, cerca de Frondavil. Una vez que la tengas, busca un pequeño claro al oeste del camino cerca del límite de Frondavil... el cofre debería estar escondido por allí.",
    ["O"] = "Encuentra la Llave de Madera y una pieza de la Vara de Dartol antes de regresar con Shael\'dryn en el pozo de la luna.",
    ["T"] = "La Purificación de Raene",
  },
  [1027] = {
    ["D"] = "La última pieza de la vara fue entregada a los druidas de Dor\'danil para su custodia. No tengo idea de dónde la escondieron. Lamento no poder ayudarte más que eso.$B$BTodo lo que puedo decirte es que sigas el camino que conduce hacia los Baldíos. Los druidas se refugiaron al este de ese camino, pero no serán de mucha ayuda para ti: fueron asesinados recientemente... es algo en lo que no me gusta pensar mucho.$B$BCuando encuentres la última pieza, tráemela y te ayudaré a reconstruir la vara.",
    ["O"] = "Encuentra la pieza final de la Vara de Dartol y llévasela a Shael\'dryn en el pozo de la luna.",
    ["T"] = "La Purificación de Raene",
  },
  [1028] = {
    ["D"] = "Aquí tienes la vara completada, $N. Reconstruirla fue simple... lo difícil será encontrar el santuario que tiene el poder para recargarla.$B$BCon un poco de enfoque y esfuerzo, no debería ser demasiado difícil para alguien como tú, que ya ha logrado tanto.$B$BDirígete al sureste de aquí—hay muy pocos caminos, así que ten cuidado—y mantén tus ojos al norte. El santuario está escondido dentro de las montañas que rodean este claro.$B$BUna vez allí, coloca la vara dentro del santuario... el resto es magia. Jeje.",
    ["O"] = "Encuentra el santuario escondido y vuelve a encantar la Vara de Dartol.",
    ["T"] = "La Purificación de Raene",
  },
  [1029] = {
    ["D"] = "Te sugiero que encuentres a Raene y le hagas saber que la vara está terminada. Estoy seguro de que ella sabrá cuál será tu próximo paso.$B$B¡Buena suerte, $N!",
    ["O"] = "Regresa con Raene Correlobo en Astranaar.",
    ["T"] = "La Purificación de Raene",
  },
  [1030] = {
    ["D"] = "Por desgracia, no estoy completamente seguro de lo que tenía en mente.$B$BSi lo que dijo sobre el poder de la vara es cierto, entonces te permitirá tomar otra forma por completo. No sé cuál forma.$B$BTal vez pretendía usar sus poderes para acercarse a los fúrbolgs corrompidos, pero incluso las criaturas no corrompidas lo matarían si no tuviera cuidado. Transformado por la vara, Dartol tendría que hablar con un fúrbolg que estuviera a salvo de los efectos del Musgo Vil.$B$BEmpezaría tu búsqueda al sureste del Lago Mystral.",
    ["O"] = "Encuentra un furbolg no corrupto y, usando el poder de la Vara de Transformación de Dartol, habla con él.",
    ["T"] = "La Purificación de Raene",
  },
  [1031] = {
    ["D"] = "Anilia pone sus manos sobre tus hombros y te acerca. Jadeando, señala hacia el arco del norte, antes de que sus ojos se cierren y su último aliento abandone su cuerpo. \"Él se lo llevó... Geltharis...\" Sabes en tu corazón que el último deseo de Anilia es ver la Rama de Cenarius de vuelta en manos de las dríadas.",
    ["O"] = "Mata a Geltharis y devuelve la Rama de Cenarius a Illiyana en el Santuario de Aessina.",
    ["T"] = "La Rama de Cenarius",
  },
  [1032] = {
    ["D"] = "No puedo creer que Anilia se haya ido... Solíamos jugar juntas en los bosques todo el tiempo. Casi me siento... triste.$B$BSé algo que podría hacerme sentir mejor. Por favor, mata a los sátiros que se llevaron a Anilia de mí. Tráeme sus cuernos como prueba de tus acciones y tendrás mi gratitud para siempre.",
    ["O"] = "Lleva 16 Cuernos de Sátiro a Illiyana en el Santuario de Aessina.",
    ["T"] = "¡Matanza de Sátiros!",
  },
  [1033] = {
    ["D"] = "Esperaba que la cura de Orendil salvara a Relara, pero temo que solo ralentizó la enfermedad que corre por mi hijo. ¡Debemos encontrar una medicina más poderosa! Afortunadamente, Orendil me habló de más remedios para probar si el primero fallaba.$B$BLa Lágrima de Elune es un tipo de piedra que descansa en un solo lugar: la isla en el Lago Iris, al este de Astranaar. Orendil dijo que la piedra lavará la corrupción... por favor, $N, tráeme la Lágrima de Elune y reza para que termine la enfermedad de mi hijo.",
    ["O"] = "Lleva la Lágrima de Elune a Pelturas en Astranaar.",
    ["T"] = "Lágrima de Elune",
  },
  [1034] = {
    ["D"] = "La Lágrima de Elune alivió parte de la enfermedad de mi hija. Sus pesadillas han disminuido y agradezco a la diosa por su bendición. Pero... la fiebre aún arde dentro de ella.$B$BOrendil me habló de un polvo helado que se encuentra entre las Ruinas de Polvo Estelar. Tal vez eso alivie la fiebre de Relara. Reúne un poco del polvo y luego regresa conmigo.$B$BLas Ruinas de Polvo Estelar se encuentran al sur, $N. Que Elune acelere tu viaje.",
    ["O"] = "Lleva 5 Puñados de Polvo Estelar a Pelturas en Astranaar.",
    ["T"] = "Las Ruinas del Polvo Estelar",
  },
  [1035] = {
    ["D"] = "La fiebre de Relara ha pasado, pero todavía está muy débil. No creo que el tiempo por sí solo le devuelva la fuerza que ha perdido. Creo que tomará el poder de una piedra lunar.$B$BSolíamos recolectar piedras lunares en el Lago del Cielo Caído al sureste... hasta que llegaron los Sombrarboleda.$B$BSu líder, un Oráculo Sombrarboleda, reunió todas las piedras lunares caídas y las acumula. Ahora, debes recuperar una piedra lunar del Oráculo.$B$B Y para hacer eso, me temo que debes matarlo.",
    ["O"] = "Recupera una Piedra Lunar Caída del Oráculo Mantoespeso y llévasela a Pelturas en Astranaar.",
    ["T"] = "Lago del Cielo Caído",
  },
  [1036] = {
    ["D"] = "$N... sí, he oído hablar de ti. He oído que no eres amigo de Bahía del Botín, al igual que nosotros, los Bucaneros Velasangre. El Maestro de Flota Firallon está buscando $ghombres:mujeres, fuertes y vigorosos como tú para una misión especial.$B$BSi estás interesado, dirígete al Velo Carmesí y habla con él bajo cubierta. Está anclado frente a la Costa Salvaje al este de aquí, junto al Rápidas y la Suerte de la Doncella.",
    ["O"] = "Habla con el Maestre de Flota Firallon a bordo del Velo Carmesí en la costa de la Vega de Tuercespina.",
    ["T"] = "¡Alto ahí, bribón!",
  },
  [1037] = {
    ["D"] = "Velinde Starsong fue mi predecesora aquí en el Bosque de Argénteos. Al principio parecía que tenía la situación en el Bosque de Argénteos bajo control, pero poco a poco sus esfuerzos flaquearon. Un día, simplemente desapareció.$B$BFui enviada aquí para continuar su trabajo. Sin embargo, no sé nada más sobre la sacerdotisa. Quizás Thyn\'tel Bladeweaver, uno de los comandantes de las Centinelas, sepa más detalles sobre su desaparición que yo no conozco.$B$BSeguramente ella entenderá la importancia de tal información.",
    ["O"] = "Habla con Thyn\'tel Tejespada en la Terraza de los Guerreros en Darnassus.",
    ["T"] = "Velinde Starsong",
  },
  [1038] = {
    ["D"] = "¿El Tome of Mel\'Thandris te mostró esto? Supongo que habría poco daño en permitirte examinar sus pertenencias. Esta llave te permitirá abrir el cofre donde guardamos sus cosas en el bunkhouse de las Centinelas. Ella llevaba un diario de sus deberes, si hay algo que aprender, será de eso.$B$BDebería decirte, las Centinelas creen que ella tenía sus propias razones para irse, y esperan que pueda regresar en cualquier momento. La sacerdotisa ha hecho mucho en el pasado para ganarse nuestra confianza.",
    ["O"] = "Busca en el cofre de Velinde su diario, luego devuélvelo junto con la llave a Thyn\'tel Tejhoja en Darnassus.",
    ["T"] = "Los Efectos de Velinde",
  },
  [1039] = {
    ["D"] = "Ratchet es el único puerto en Los Baldíos. Lo más probable es que Velinde encontrara un barco mercante en Ratchet para llevarla a la Cala del Agua Negra en Azeroth. Hemos tenido tratos limitados con los goblins que manejan el puerto, pero el maestro del muelle debería tener información sobre los pasajeros de los barcos.$b$bSigue el camino al sureste a través de Argénteos y te encontrarás en Los Baldíos. Vigila tus pasos, $n, los guerreros de la Horda patrullan la tierra. Sin embargo, estarás seguro en el puerto.",
    ["O"] = "Habla con el Maestro de Muelles Dizzywig en Ratchet.",
    ["T"] = "El Puerto de Los Baldíos",
  },
  [1040] = {
    ["D"] = "Ah, sí, finalmente lo encontré. Debería haberme dicho que ella pasó por aquí hace tanto tiempo. Veamos.$B$BVelinde. Reservó pasaje a Bahía del Botín en el Halcón Negro. No tengo nada aquí que diga lo contrario, así que asumo que llegó al puerto de manera segura.$B$BNo puedo ser de mucha más ayuda, pero ella preguntó sobre viajes terrestres en ese lado del mundo, y mencioné a Ruzzgot, un conductor de caravanas con base en Bahía del Botín. Podría ser que esta Velinde viajó con él.$B$BSigue adelante, ahora. No tengo todo el día para ti.",
    ["O"] = "Toma un barco a Bahía del Botín y habla con Caravaneer Ruzzgot.",
    ["T"] = "Pasaje a Bahía del Botín",
  },
  [1041] = {
    ["D"] = "Resulta que me equivoqué contigo, y eso no es algo que pase todos los días.$b$bResulta que recuerdo a esta Velinde que estás buscando. No todos los días una sacerdotisa elfa de la noche quiere viajar con una sucia y vieja—¡pero excelente para tus necesidades de envío!—caravana como la mía.$b$bNos separamos en el camino al norte, ella se dirigía a Villa Oscura. El empleado allí guarda todo tipo de registros. Podría saber algo útil.$b$BTen cuidado en la jungla, es un lugar mortal incluso en los mejores momentos.",
    ["O"] = "Habla con el Escribiente Daltry en Villa Oscura.",
    ["T"] = "El Camino de la Caravana",
  },
  [1042] = {
    ["D"] = "No, no tengo registros de una Velinde Starsong alojándose en Villa Oscura... aunque, si no te importa que te lo diga, apenas puedo imaginar a una sacerdotisa elfa de la noche tomando una habitación en la posada, ¿entiendes?$b$bSin embargo, estos hombres lobo que mencionaste, eso es algo de lo que he oído hablar. Justo el otro día, Calor llegó al pueblo con una cadena de sus cabezas. Trabaja con la familia Carevin. Cazadores de demonios, no muertos y otras monstruosidades. Habla con Jonathan, él es el cabeza de familia.",
    ["O"] = "Habla con Jonathan Carevin en Villa Oscura.",
    ["T"] = "La Familia Carevin",
  },
  [1043] = {
    ["D"] = "Tu historia suena cierta... No entiendo del todo tus motivos, pero si tu negocio aquí en Bosque del Ocaso implica deshacerte de los huargos en cualquier número, entonces puedo prescindir de entender por resultados.$b$bHay una mina al sur que ha sido invadida por huargos... Aparecieron de la nada, pero por lo que sabemos, fue allí donde se los encontró por primera vez.$b$bSigue con tu negocio, pero te pediría que si encuentras algo importante, me lo compartas. Aceptaremos cualquier ayuda en nuestra guerra contra el mal.",
    ["O"] = "Busca señales de la Guadaña de Elune y luego regresa con Jonathan Carevin en Villa Oscura.",
    ["T"] = "La Guadaña de Elune",
  },
  [1044] = {
    ["D"] = "Contendremos el problema de los huargen, no te preocupes por eso. Este mal que tu amiga introdujo en nuestros bosques será contenido, y no le guardo rencor por sus acciones. Eventos extraños están ocurriendo en estos tiempos, $n, y la oscuridad no da tregua.$b$bNo te retendré más. Sospecho que hay otros que deberían escuchar lo que has encontrado en las minas de Bosque del Ocaso.",
    ["O"] = "Regresa con Thyn\'tel Hojaafilada en Darnassus.",
    ["T"] = "Preguntas Respondidas",
  },
  [1045] = {
    ["D"] = "Los elfos nocturnos son tan antiguos como el tiempo, pero su arrogancia destruye su propio mundo. Y ahora también destruye nuestras vidas. Pero eso ya no se puede controlar...$B$BCulpo a los elfos por mucho, pero aún somos responsables de nuestras acciones. Incluso más allá de la corrupción, somos capaces de gran maldad.$B$BRan Colmillo de Sangre es una de esas maldades. Hambriento de poder, lujurioso... la corrupción solo lo ha hecho más fuerte. Mi odio por los elfos arde, pero no deseo el mal a sus hijos, así que debemos vigilarnos a nosotros mismos.",
    ["O"] = "Mata a Ran Colmillo de Sangre y a 4 Guardias Colmillo de Sangre y regresa con Krolg cerca del Lago Mystral.",
    ["T"] = "La Purificación de Raene",
  },
  [1046] = {
    ["D"] = "Regresa a tu propia tribu y hazles saber de tu victoria aquí.$B$BMuéstrales tu trofeo y disfruta de la gloria...",
    ["O"] = "Lleva el Cráneo de Ran Colmillo de Sangre y la Vara de Transformación de Dartol a Raene Correlobo en Astranaar.",
    ["T"] = "La Purificación de Raene",
  },
  [1047] = {
    ["D"] = "¡Atención, héroes!$B$B¡El Círculo Cenarion busca individuos capaces para ayudarlos a explorar las vastas nuevas fronteras de Kalimdor! Un yermo prohibido de peligros inimaginables se encuentra al oeste de Tanaris y Gadgetzan, y el Círculo necesita representantes dispuestos y honorables para actuar en su nombre.$B$B¡Todos los interesados deben buscar audiencia con el Círculo Cenarion. Habla con el Archidruida Hamuul Runetotem en la Cima de los Ancestros de Cima del Trueno para más información!",
    ["O"] = "Habla con el Archidruida Fandral Corzocelado en el Enclave Cenarion de Darnassus sobre el llamado del Círculo Cenarion para explorar las fronteras de Kalimdor.",
    ["T"] = "La Nueva Frontera",
  },
  [1048] = {
    ["D"] = "Mientras la Dama Oscura se ocupa del apremiante avance del Rey Exánime hacia el norte, me aseguro de que los asuntos más cercanos a casa se estén manejando adecuadamente.$b$bSin duda, las plagas humanas que se hacen llamar la Cruzada Escarlata son la espina más afilada en nuestro costado doméstico. Los Claros de Tirisfal están literalmente infestados de esas pequeñas hormigas rojas.$b$bViaja al Monasterio, $c, y arrasa con la Suma Inquisidora Whitemane y sus principales lugartenientes.",
    ["O"] = "Mata a la Gran Inquisidora Canisbia, al Comandante Escarlata Mograine, a Herod el Campeón Escarlata y al Maestro de canes Loksey, luego informa a Varimathras en Entrañas.",
    ["T"] = "Al Monasterio Escarlata",
  },
  [1049] = {
    ["D"] = "El Anillo de la Tierra tenía intenciones puras hacia nuestros hermanos plagados. Pero, ¿quién sabe las motivaciones de aquellos que susurran en los oídos del Consejo de Ancianos?$B$BLos No-Muertos con los que nos hemos aliado tienen una historia llena de engaños. Demasiado apresurado fue nuestro pacto. Quizás Cairne habría sido sabio al escuchar las advertencias de Orgrimmar.$B$BEn lo profundo del Monasterio Escarlata se encuentra el Compendio de los Caídos, custodiado por fanáticos humanos enloquecidos. Su método está forjado en la locura, pero su investigación podría resultar útil.",
    ["O"] = "Recupera el Compendio de los Caídos del Monasterio en los Claros de Tirisfal y llévaselo al Sabio Buscador de la Verdad en Cima del Trueno.",
    ["T"] = "Compendio de los Caídos",
  },
  [1050] = {
    ["D"] = "¡El futuro es inevitable! Nos lanzamos hacia él como gotas de lluvia hacia el Gran Mar.$b$bPero quizás, al estudiar el pasado, podamos saber qué esperar y estar mejor preparados para lo desconocido.$b$bAntes de que cayeran graves problemas sobre Lordaeron, había un bastión de conocimiento escondido en las Claros de Tirisfal. Se rumorea que un libro llamado Mitología de los Titanes estaba alojado en la biblioteca del antiguo Monasterio.$b$bTráemelo y quizás juntos podamos desbloquear los secretos del pasado.",
    ["O"] = "Recupera la Mitología de los Titanes del Monasterio y llévasela a la Bibliotecaria Mae Polvovana en Forjaz.",
    ["T"] = "Mitología de los Titanes",
  },
  [1051] = {
    ["D"] = "Aunque mi voluntad es libre, ya no puedo servir a la Dama Oscura. Siento que mi tiempo en Azeroth ha llegado a su fin. Mi fuerza vital está destrozada.$b$b¡El interrogador Vishas hizo esto conmigo! ¡Me torturó durante semanas! ¡Se llevó mi anillo de bodas y se lo dio a su esposa! ¡El bastardo!$b$b¡Venganza... quiero venganza!$b$b¡El pomposo idiota se jactó de su preciosa esposa, Nancy, que vivía cerca del Lago Lordamere! Rastrea a la bruja, mátala y devuelve mi anillo de bodas a mi esposa, Monika, en Molino Tarren.$b$b¡Atiende mis últimos deseos...",
    ["O"] = "Devuelve el anillo de bodas de Vorrel Sengutz a Monika Sengutz en Molino Tarren.",
    ["T"] = "La Venganza de Vorrel",
  },
  [1052] = {
    ["D"] = "Nosotros de la Cruzada Escarlata reclamamos fortalezas desde Hearthglen hasta los Claros de Tirisfal. Estamos muy orgullosos de nuestros bastiones de purificación por todo Lordaeron.$b$bTe has probado contra los no muertos en Desolace. Pero la verdadera amenaza de la plaga yace en Lordaeron.$b$bViaja al pueblo de Costasur, en los Reinos del Este. Busca a un cruzado llamado Raleigh el Devoto. Entrégale esta carta de recomendación con mi sello y él te escoltará a un lugar de honor en nuestro Monasterio Escarlata.",
    ["O"] = "Lleva la Carta de Recomendación del Hermano Anton a Raleigh el Devoto en Costasur.",
    ["T"] = "Por el Sendero Escarlata",
  },
  [1053] = {
    ["D"] = "Una vez serví a la Cruzada Escarlata con honor, lealtad y orgullo. Creía que su causa era noble: librar a Azeroth de los no muertos.$B$BPero a medida que pasé tiempo en el Monasterio de las Tierras de la Peste del Tirisfal, me di cuenta de que su percepción de la realidad se estaba desvaneciendo. Ahora piensan que todos están plagados si no llevan el tabardo de la Cruzada. Hombres y mujeres inocentes fueron torturados porque supuestamente estaban plagados. $B$BLa Cruzada Escarlata debe ser aplastada en nombre de la Luz. Tú, $n, debes destruir el régimen demente.",
    ["O"] = "Mata a la Gran Inquisidora Canisbia, al Comandante Escarlata Mograine, a Herod el Campeón Escarlata y al Maestro de canes Loksey, luego informa a Raleigh el Devoto en Costasur.",
    ["T"] = "En el Nombre de la Luz",
  },
  [1054] = {
    ["D"] = "Directamente al norte de Astranaar están los furbolgs Pelopelo. Mis exploradores informan que su número ha crecido enormemente en los últimos meses. Si crecen demasiado, podrían encontrar el coraje para atacar Astranaar directamente.$B$BMata a tantos de su especie como puedas, pero tráeme el cráneo de su jefe como prueba de que sus esfuerzos han sido frenados... por ahora.$B$BMe quedaré aquí en Astranaar y esperaré noticias tuyas. Que Elune te acompañe.",
    ["O"] = "Lleva el Cráneo de Dal Garragil a Raene Correlobo en Astranaar.",
    ["T"] = "Eliminando la Amenaza",
  },
  [1055] = {
    ["D"] = "La vara, ahora completa, yace en tus manos. Shael\'dryn querrá escuchar sobre tu éxito.",
    ["O"] = "Regresa con Shael\'dryn en el pozo de la luna.",
    ["T"] = "La Purificación de Raene",
  },
  [1056] = {
    ["D"] = "Los espíritus del bosque me dicen que eres valiente y estás dispuesto a viajar.$b$bAl sur, no muy lejos del Lago Mystral, hay un túnel llamado El Sendero Talondeep. A través de este túnel llegarás a un área conocida como el Risco Cortaviento en las Montañas de Trabalomas. Una vez allí, viaja hacia el suroeste más allá del Lago Cragpool y luego hacia el norte, subiendo la empinada pendiente hasta llegar a la Cima de Trabalomas.$b$bAllí te espera el Guardián Albagorm. Sigue sus órdenes, $r.$b$bEl viaje será peligroso.",
    ["O"] = "Viaja al Guardián Albagorm en la Cima de los Espolones.",
    ["T"] = "Viaje a la Cima de Stonetalon",
  },
  [1057] = {
    ["D"] = "Como protector de los espíritus del bosque, sin duda puedes adivinar por qué estoy alarmado.$b$bHas viajado a través de estas montañas para llegar a este pico. Seguramente has visto la tragedia que ha caído sobre la tierra. La codicia ha llevado a la destrucción de estas sagradas colinas.$b$bEl Valle Carbonizado al sur fue incendiado por el codicioso ejército mecanizado de industrialistas. Peor aún, las Furiasangre que han invadido la tierra nos niegan el paso para comenzar la regeneración.$b$b¡Expulsa a las miserables arpías, $n!",
    ["O"] = "El Guardián Albagorm en la Cima del Espolón quiere que mates 7 Arpías Sangre Furiosa, 7 Emboscadores Sangre Furiosa, 7 Asesinos Sangre Furiosa y 7 Plumavil Sangre Furiosa.",
    ["T"] = "Recuperando el Valle Carbonizado",
  },
  [1058] = {
    ["D"] = "¡Estoy tan emocionado!$b$b¡Tres pequeñas jaulas para tres pequeños elfos! Pero, ¿qué haremos con nuestros encantadores amigos abrazadores de árboles? ¡Pues, démosles un gusto de... cómo lo llaman... magia del bosque!$b$bNecesitaré una buena cantidad de savia de Espinazo de Piedra. De esos horribles felinos, tráeme algunos bigotes crepusculares. Esto requiere muchos ojos de corredor, por supuesto: nunca se pueden tener suficientes ojos. Oh, y no olvidemos, una preciosa escama de un dragón feérico.$b$b¡Date prisa a la Cumbre de Espinazo de Piedra, $n, para que pueda preparar algo de magia del bosque!",
    ["O"] = "El Doctor Brujo Jin\'Zil en Malaka\'jin quiere 5 porciones de Savia de Tuercespina, 5 Bigotes Crepusculares, 30 Ojos de Corcel y una Escama de Dragón Feérico.",
    ["T"] = "La Magia del Bosque de Jin\'Zil",
  },
  [1059] = {
    ["D"] = "Los Sangre Furia han sido sometidos. Pero se reagruparán pronto si no se toman medidas.$B$BMuy al sur de los Baldíos se encuentra una tierra conocida como las Mil Agujas. Si viajas al oeste en las Mil Agujas hasta llegar al borde del frondoso bosque de Feralas, encontrarás a un druida llamado Falfindel Waywarder.$B$BEste gran y sabio druida es capaz de invocar grandes espíritus del bosque que pueden ayudar en la reclamación del Valle Carbonizado.$B$B¡Envía un mensaje de nuestra necesidad! ¡Búscalo de inmediato!",
    ["O"] = "Viaja a Falfindel Waywarder en la frontera entre las Mil Agujas y Feralas.",
    ["T"] = "Recuperando el Valle Carbonizado",
  },
  [1060] = {
    ["D"] = "Has hecho muy bien por mí, $n. Eres una verdadera pata de conejo de la suerte y hay algo que quiero que veas.$B$BLas arpías no son de Los Baldíos. Vienen de un lugar llamado Sierra Espolón, muy al noroeste de aquí. Aunque probablemente sea un lugar peligroso para ti, necesito que entregues esta carta a un viejo amigo mío que vive allí, un médico brujo llamado Jin\'Zil.$B$BEstará feliz de saber que Serena Plumofuria está muerta, y creo que encontrarás Sierra Espolón un lugar interesante.",
    ["O"] = "Entrega la carta de Darsok a Jin\'Zil dentro de su cueva en Malaka\'Jin, en las Montañas Filospada.",
    ["T"] = "Carta para Jin\'Zil",
  },
  [1061] = {
    ["D"] = "Los espíritus de las Montañas de Stonetalon están enfurecidos, ya que los goblins y sus sirvientes están saqueando y quemando la tierra. Así que la tierra ha clamado, y aquellos que escuchan tales cosas... han oído.$B$BSeereth Rompepedras, una prometedora chamán de la Horda, fue enviada a Stonetalon. Ella informa de una deforestación masiva a manos de los goblins y solicita ayuda.$B$BReúnete con Seereth en Stonetalon. Para llegar a ella, sigue el camino al oeste de Las Encrucijadas en Los Baldíos. Ella está acampada cerca del Gran Valle del Bosque.",
    ["O"] = "Habla con el Vidente Rompepedras en las Montañas de Trabalomas.",
    ["T"] = "Los Espíritus de Stonetalon",
  },
  [1062] = {
    ["D"] = "Los goblins de Venture Co. se han mudado a las Montañas de Trabalomas, talando árboles y quemando grandes extensiones de bosque. Los espíritus de este lugar están casi ciegos de dolor y rabia. ¡Debemos detener a Venture Co.!$B$BDirígete al noroeste, más allá del Gran Valle y hacia el Risco Cortaviento. Encontrarás a los goblins y sus sirvientes trabajando, muéstrales que la Horda no permitirá la explotación de Trabalomas. Muéstrales en el lenguaje que mejor entienden...$B$B¡Violencia!",
    ["O"] = "Mata 15 Taladores de Venture Co., luego regresa con el Vidente Rompepedras en la frontera del Espolón y los Baldíos.",
    ["T"] = "Invasores Goblins",
  },
  [1063] = {
    ["D"] = "Para realmente atender a los espíritus de Stonetalon debemos consultar a los ancianos tauren.$B$BNo conozco muchos de los nombres de sus líderes, pero sí sé de uno: Magatha. He oído que es muy vieja, y sabe mucho. Si alguien puede movilizar ayuda espiritual de los tauren, es ella.$B$BMagatha reside en la capital tauren de Cima del Trueno, en el Ascenso de los Ancestros: el risco noreste de la ciudad. Habla con Magatha y cuéntale sobre el peligro de las Montañas de Stonetalon.",
    ["O"] = "Habla con Magatha en Cima del Trueno.",
    ["T"] = "La Anciana Bruja",
  },
  [1064] = {
    ["D"] = "Los tauren tienen un vínculo con la tierra, y me duele escuchar sobre la difícil situación de Stonetalon. Pero temo que para sanar la tierra, primero debemos eliminar la enfermedad que la aflige.$B$B¿No es afortunado, entonces, que los No-Muertos estén aliados con nosotros? Ellos saben mucho sobre enfermedades. Creo que pueden ayudarnos, y al hacerlo fortalecerán la confianza entre nuestros pueblos.$B$BHabla con la Boticaria Zamah. Ella es una erudita y una emisaria de los No-Muertos. La encontrarás en las Pozas de la Visión debajo del Ascenso de los Espíritus.",
    ["O"] = "Habla con el Boticario Zamah en las Pozas de las Visiones en Cima del Trueno.",
    ["T"] = "Ayuda de los No-Muertos",
  },
  [1065] = {
    ["D"] = "Para librar a Stonetalon de Venture Co., debemos tomar medidas extremas. Extremas, pero sin derramamiento de sangre.$B$BUn colega mío puede tener justo la herramienta que necesitamos. Su nombre es Lydon, habita en el pueblo de Molino Tarren en las Foothills de Hillsbrad, en la lejana tierra de Lordaeron.$B$BPara llegar a Hillsbrad, viaja en zepelín a nuestra capital, Entrañas. Luego ve al norte a los Claros de Tirisfal, al suroeste a Bosque de Argente, y al sur a Hillsbrad.$B$BBusca a Lydon y entrégale esta nota. Detalla nuestras necesidades para él.",
    ["O"] = "Lleva la Nota de Zamah a Boticario Lydon en Molino Tarren.",
    ["T"] = "Viaje a Molino Tarren",
  },
  [1066] = {
    ["D"] = "Crearé una toxina para drenar la violencia de Venture Co., haciéndolos débiles y dóciles. Eso debería solucionar el problema de la Horda en el Pico del Espolón, ¿no?$B$BPero para crear la toxina, necesitaré la sangre de los inocentes, un ingrediente difícil de obtener.$B$BAfortunadamente, el Sindicato, un grupo de bandidos humanos en esta área, emplea a magos de las sombras... y esos magos recolectan sangre inocente para sus propios fines desagradables.$B$BPuedes encontrar a los magos de las sombras del Sindicato en el Castillo de Durnholde, al sureste.",
    ["O"] = "Lleva 5 Viales de Sangre Inocente al Boticario Lydon en Molino Tarren.",
    ["T"] = "Sangre de Inocentes",
  },
  [1067] = {
    ["D"] = "Aquí tienes la toxina. Si la usas contra Venture Co., debería producir resultados favorables, quizás incluso contagiosos. ¡Debo decir que es bastante emocionante ver mi trabajo aplicado en el campo!$B$BPero antes de usarla, llévasela a la Boticaria Zamah. Ella debe crear un método de entrega para la toxina para maximizar su efecto.",
    ["O"] = "Lleva la Toxina de Lydon al Boticario Zamah en Cima del Trueno.",
    ["T"] = "Regreso a Cima del Trueno",
  },
  [1068] = {
    ["D"] = "Hay una relación entre la magia espiritual de los chamanes y la magia natural de los druidas. Es por eso que nosotros los chamanes podemos escuchar la súplica de los espíritus de Stonetalon. Muchos de sus susurros se pierden para mí, pero un mensaje, una visión, es clara...$B$BVi una visión de dos máquinas con forma de hombre en Cortaviento, al noroeste del Gran Valle, talando árboles con una velocidad imposible. ¡Estas bestias de metal deben ser detenidas!$B$BEn mi visión, las máquinas estaban marcadas con símbolos extraños:$B$BXT:4 y XT:9.",
    ["O"] = "Mata a las trituradoras XT:4 y XT:9, luego regresa con Vidente Rompepedras en la frontera entre las Montañas Filospada y los Baldíos.",
    ["T"] = "Máquinas Destructoras",
  },
  [1069] = {
    ["D"] = "¡Los omelettes de huevos de araña son la nueva moda en Bahía del Botín! El problema es... Bahía del Botín no tiene huevos.$B$B¡Huelo una oportunidad!$B$BEn el Risco Cortaviento y el Cañón Sishir, en las Montañas de Espinazo de Piedra al noroeste, vive la araña musgohondo. ¡Tráeme sus huevos y te pagaré un montón!$B$BEn el Risco Cortaviento, los huevos estarán agrupados bajo los árboles que quedan.$B$BEn el Cañón Sishir... bueno, si tienes un estómago débil, tal vez deberías solo recolectar los huevos en el Risco Cortaviento...",
    ["O"] = "Lleva 15 Huevos de Musgondo a Mebok Mizzyrix en Ratchet.",
    ["T"] = "Huevos de Araña Musgondo",
  },
  [1070] = {
    ["D"] = "Hola, $N.$B$BPareces estar lleno de espíritu aventurero por tu apariencia. ¿Has estado en las Montañas de Petravista? Es una tierra plagada por Venture Co. y otros enemigos de los elfos nocturnos.$B$BRecientemente, la Centinela Correlobo envió a mi amiga Kaela Lanzasombría allí para investigar lo que está sucediendo en la tierra, pero aún no he recibido noticias suyas. Estoy aún más preocupado porque la Alianza envió a una mago gnoma con ella... una señal segura de problemas.$B$B¿Podrías encontrarla e informarme?",
    ["O"] = "Encuentra a Kaela Lanzanegra mirando hacia el valle que entra en la Garganta Cortaviento en las Montañas Filospada.",
    ["T"] = "De Guardia en Stonetalon",
  },
  [1071] = {
    ["D"] = "Verás, $N, soy miembro de la Asamblea Iluminada de Arcanología, Alquimia y Ciencias de la Ingeniería, y es mi objetivo... ¡no, mi DEBER! mostrar a los elfos nocturnos que una mezcla de magia, mezclas y mecanismos puede ayudar a salvar sus bosques.$B$BPero es una guerra difícil, y los goblins se reproducen como conejos, parece. ¡Ni siquiera he tenido tiempo de construir mis nuevos inventos!$B$BNecesito algo de tiempo para planificar y organizar. Pero para hacer eso, necesito tu ayuda para mantener a raya a los goblins en el Risco de los Vientos al este...",
    ["O"] = "Mata 10 Taladores de Venture Co. y 10 Deforestadores de Venture Co. y regresa con Gaxim Silvabín en el Espolón.",
    ["T"] = "El Descanso de un Gnomo",
  },
  [1072] = {
    ["D"] = "El dispositivo en el que estoy pensando es mi versión más avanzada hasta la fecha. Pero necesitaremos una poción especial si va a funcionar. Estoy pensando que podríamos conseguir lo mejor ya que esta misión podría ser tu última.$B$BPara eso, vamos a necesitar algunos explosivos potentes: Nitromirgliceronio.$B$BLa única persona que puede hacer NG-5 es un viejo amigo mío: Lomac Gearstrip. Búscalo en la Instalación de Reclamación de Gnomeregan.$B$BConvéncelo de que nos haga un poco de NG-5, yo me encargaré de la colocación de mis dispositivos.",
    ["O"] = "Habla con Lomac Tiraengranajes en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "Un Viejo Colega",
  },
  [1073] = {
    ["D"] = "Jajaja... ¿necesitas Nitromirgliceronio para evitar que los goblins destruyan un bosque?$B$BNo me importa si conoces a Gaxim o no... Te daré el material solo para verte a ti o a ellos explotar en pedacitos.$B$BHay un problema: me he quedado sin.$B$BPero te diré qué, tráeme las pociones correctas, y prepararé algo para ti de inmediato.$B$BLo menos que puedes hacer es demostrar que tienes algo de habilidad como alquimista antes de que te dé una muestra de mi mayor creación.",
    ["O"] = "Lleva 4 Pócimas de Maná Menores y 2 Elixires de Fortaleza a Lomac Gearstrip en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "Ineptitud + Químicos = Diversión",
  },
  [1074] = {
    ["D"] = "Aquí tienes, extraño. Ten cuidado con esto... es algo volátil.$B$BSi realmente eres amigo de Gaxim, entonces dile que le mando saludos. Si no lo eres, entonces diviértete volándote a ti mismo.",
    ["O"] = "Lleva el Nitromirgliceronio de vuelta a Gaxim Rustfizzle en Tuercespina.",
    ["T"] = "Ineptitud + Químicos = Diversión",
  },
  [1075] = {
    ["D"] = "Vale, ahora para la parte de detonación remota de mi plan... oh, ¿no te lo he explicado todavía?$B$BVale, en resumen es esto: con un poco de magia, puedo hacer que los explosivos estallen cuando no estés cerca. Estoy armando un dispositivo que te permitirá presionar un solo botón y hacer que el NG-5 explote cuando estés listo.$B$BPero necesito un hechizo de un amigo en Ventormenta: Collin Mauren es su nombre. Puedes encontrarlo en el barrio de los magos allí. Es humano, así que no debería ser difícil de encontrar.",
    ["O"] = "Encuentra a Collin Mauren en Ventormenta.",
    ["T"] = "Un Pergamino de Mauren",
  },
  [1076] = {
    ["D"] = "Ah, creo que lo tengo. Un hechizo de mensajería... Creo que es el efecto que está buscando.$B$BTendré que crear un pergamino con el hechizo impreso en él. Para hacer eso, necesitaré escombros de un diablo de polvo.$B$BMientras recojo el resto de los componentes, ¿por qué no te diriges a Páramos de Poniente y encuentras una de esas criaturas despreciables? Son peligrosas, pero estoy seguro de que es algo que puedes manejar si estás dispuesto a someterte a uno de los experimentos de Gaxim.$B$BEstaré aquí cuando regreses.",
    ["O"] = "Lleva los Restos de Diablo de Polvo a Collin Mauren en Ventormenta.",
    ["T"] = "Diablillos en Poniente",
  },
  [1077] = {
    ["D"] = "Aquí tienes, $N. Esto debería servir.$B$BPor favor, dale mis saludos a Gaxim la próxima vez que lo veas. Espero escuchar sobre esta aventura cuando se complete.$B$B¡Que te vaya bien!",
    ["O"] = "Lleva el Pergamino de Mensajería a Gaxim Rustfizzle en Tuercespina.",
    ["T"] = "Entrega Especial para Gaxim",
  },
  [1078] = {
    ["D"] = "Los viajeros siguen preguntándome sobre las Montañas de Stonetalon. Parece ser un lugar popular para la aventura—no importa si buscas a los wyvern, a los elementales o tienes negocios con Venture Co.$B$BDentro del Valle Carbonizado, en lo profundo de Stonetalon, solía haber una especie de basiliscos cuyas escamas, cuando se molían hasta convertirlas en polvo, hacían un maravilloso reactivo para algunos hechizos que he creado.$B$BSi esos basiliscos todavía viven, me encantaría tener algunas de sus escamas.$B$BTómate tu tiempo, no hay prisa, pero puedo pagar bien.",
    ["O"] = "Lleva 8 Escamas Cristalizadas a Collin Mauren en Ventormenta.",
    ["T"] = "Recuperación para Mauren",
  },
  [1079] = {
    ["D"] = "Esto es, $N. Presta mucha atención. Los dispositivos están listos y preparados para ser usados.$B$BToma estas páginas: explicarán más sobre los detonadores y los explosivos, y dónde debes colocarlos.$B$BRecuerda, el éxito de tu misión no depende de cuánta destrucción causes. El éxito del Plan Alfa depende de que traigas de vuelta los planes de ingeniería de Venture Co. desde dentro del aserradero al sur del Lago Cragpool.",
    ["O"] = "Recupera los Planes de Ingeniería de Venture Co. del aserradero en el Risco Cortaviento.",
    ["T"] = "Operación Encubierta - Alfa",
  },
  [1080] = {
    ["D"] = "El Plan Beta es como el Plan Alfa, $N.$B$BCausamos una distracción y luego robamos sus preciosas cartas.$B$BLa clave es obtener sus cartas... recuérdalo.$B$BLas páginas que ya te di te dirán dónde plantar los explosivos. Solo regresa aquí vivo y con esas cartas....",
    ["O"] = "Recupera las Cartas de Venture Co. de la Mina Cortaviento en el extremo este del Risco Cortaviento.",
    ["T"] = "Operación Encubierta - Beta",
  },
  [1081] = {
    ["D"] = "$N, estoy seguro de que la propia Tyrande Susurravientos querría agradecerte por participar en esta misión.$B$BPuede que no parezca así en la superficie, pero lo que has ayudado a comenzar podría fortalecer nuestra posición en las Montañas de Stonetalon.$B$BSi te consideras nuestro aliado contra estas atrocidades, ve a ver a Tyrande en el Templo de la Luna en Darnassus.",
    ["O"] = "Encuentra a Tyrande Susurravientos en Darnassus.",
    ["T"] = "Recepción de Tyrande",
  },
  [1082] = {
    ["D"] = "$N, has hecho una gran acción al ayudar aquí.$B$BLos Centinelas de Vallefresno te recompensarán bien si les informas que Gaxim y yo estamos vivos. Diles que estamos en camino de detener a los Venture Co. y sanar las tierras.$B$BBusca a Tara Thenysil en Astranaar, estará encantada de verte de vuelta con buenas noticias.",
    ["O"] = "Regresa con la Centinela Thenysil en Vallefresno.",
    ["T"] = "Actualización para la Centinela Thenysil",
  },
  [1083] = {
    ["D"] = "No estoy seguro de lo que hizo el Consorcio en el Valle, pero es obvio que fueron superados por las criaturas allí.$B$BIncendiaron el bosque y usaron sus inventos destructivos para diezmar tanto del Valle como pudieron.$B$BPero en su ignorancia también liberaron algo más: espíritus ardientes, elementales de fuego, piedra y furia.$B$BLa tierra no tiene oportunidad de ver la sonrisa de Elune mientras estas criaturas deambulan libremente.$B$BTráeme sus brasas humeantes para demostrar que has reducido su número.",
    ["O"] = "Lleva 8 Ascuas Humeantes de cualquiera de los elementales de fuego en el Valle Carbonizado a Kaela Lanzanegra en los Baldíos de Piedra.",
    ["T"] = "Espíritus Enfurecidos",
  },
  [1084] = {
    ["D"] = "Parece obvio para mí que los protectores del Valle, los treants, tuvieron algo que ver con que Venture Co. fuera expulsada. Desafortunadamente, incluso esos antiguos guardianes se han corrompido.$B$BGaxim cree que su corrupción está de alguna manera vinculada a las armas que los goblins usaron durante su retirada.$B$BLe dije que recolectaría una muestra de un treant herido y le permitiría estudiarla para averiguar si eso es cierto.$B$BTráeme algunos de los fragmentos para que el gnomo pueda decirme más...",
    ["O"] = "Lleva un pedazo de Metralla Corroída a Kaela Lanzanegra en los Baldíos de Piedra.",
    ["T"] = "Ancestros Heridos",
  },
  [1085] = {
    ["D"] = "La situación aquí en Sierra Espolón es obviamente grave para que un elfo nocturno y un gnomo estén trabajando juntos.$B$BLa Venture Co. ha devastado estas tierras y he pasado la mayor parte de mi tiempo poniendo fin a la destrucción.$B$BNo juzgues a Gaxim prematuramente, él entiende la necesidad de que trabajemos juntos.$B$BSi deseas ayudarnos, comienza hablando con Gaxim.$B$BLamento no poder enviarte de regreso a Tara con buenas noticias todavía. Quizás pronto.",
    ["O"] = "Habla con Gaxim.",
    ["T"] = "De Guardia en Stonetalon",
  },
  [1086] = {
    ["D"] = "La toxina está lista. Todo lo que queda es su entrega. Tú, $N, debes hacer esa entrega.$B$BLa Venture Co. debe tener un medio para enviar mensajes y cargamentos ligeros entre su sede y sus operaciones en Stonetalon. Es probable que sea un pequeño puerto aéreo, no lo suficientemente grande para un zepelín, pero quizás para una máquina voladora. Debes buscar en las áreas de Stonetalon bajo el control de Venture Co. y encontrar este puerto aéreo, si existe. Si lo encuentras, coloca la toxina allí.$B$BColócala y corre.",
    ["O"] = "Lleva el Nebulizador Tóxico al aeropuerto de Venture Co., luego regresa con el Boticario Zamah.",
    ["T"] = "El Aeropuerto de la Máquina Voladora",
  },
  [1087] = {
    ["D"] = "No puedes imaginar el odio que poseo por esta tierra miserable. La estirpe de Cenarius y los druidas kaldorei en la cima emplean sus poderes patéticos para sofocar el flujo de la magia, incluso tan al sur de sus posesiones patéticas... sus amados bosques.$B$BOjalá pudiera regresar a Jaedenar y exaltarme en el flujo de la magia, pero mi maestro no me permitirá regresar hasta que mi trabajo esté hecho.$B$BNo tengo dudas de que amas a los elfos nocturnos y sus aliados tan poco como yo. Ayúdame, y ambos podríamos estar satisfechos.",
    ["O"] = "Mata 4 Hijos de Cenarius, 4 Hijas de Cenarius y 4 Botánicos Cenarion para Braelyn Manofuego cerca del Alto de los Espíritus.",
    ["T"] = "El Legado de Cenarius",
  },
  [1088] = {
    ["D"] = "Pensaron que podrían detener el flujo de la magia. Estaban equivocados. Pensaron que podrían esconder a Ordanus, ¡pero yo tendré su cabeza!$b$bLa magia me otorga la verdadera visión. Sí... ahí está. Un cruce... En la cima de un gran árbol en el Bosque de Ashenvale. Un nombre... Raynewood. Uno de los hijos mayores del dios del bosque. Lo llevaron lejos de la cima antes de que pudiera encontrarlo, pero su tiempo se acaba.$b$bTráemelo y compartiré contigo un poderoso regalo.",
    ["O"] = "Lleva la cabeza de Ordanus a Braelyn Manofuego cerca del Retiro Roca del Sol.",
    ["T"] = "Ordanus",
  },
  [1089] = {
    ["D"] = "Los druidas construyeron el Cubil de los Talones en la Cima de Stonetalon, donde guardan muchos objetos poderosos.$B$BInventaron un esquema complejo para evitar que cayeran en las manos equivocadas.$B$BAfortunadamente, logré... extraer algunos detalles de uno de ellos.$B$BOcultada en una pequeña linterna en la entrada del Cubil, encontrarás la Llave del Guardián del Portal, con ella podrás abrir los cofres alrededor del Cubil. Los objetos de estos cofres te permitirán abrir su tesoro, ubicado en una de las áreas centrales.",
    ["O"] = "Viaja a la Guarida en la Cima de los Espolones. Usando la Llave del Guardián, obtén los objetos ocultos de los druidas. Usa estos objetos para abrir el Tesoro de la Guarida del Espolón.",
    ["T"] = "La Guarida",
  },
  [1090] = {
    ["D"] = "Sabes, una vez fui la segunda persona más importante aquí en este sitio. Todo cambió cuando Gerenzo se hizo cargo. Despidió a mi jefe, Ziz Fizziks, y me envió a trabajar en las minas. Ziz me dijo que me quedara aquí y recopilara información. Su nuevo jefe nos pagará bien si podemos descubrir cuáles son los planes de Gerenzo.$b$bDe todos modos, lo que sí sé es que Gerenzo ha ordenado que se extraigan grandes cantidades de este mineral verde dorado. Puedo conseguirte una muestra para que se la lleves a Ziz, pero tendrás que estar atento a las patrullas mientras la consigo.",
    ["O"] = "Consigue el mineral misterioso de Piznik después de que termine de minarlo.",
    ["T"] = "Órdenes de Gerenzo",
  },
  [1091] = {
    ["D"] = "$N, lleva estas cartas a Kaela. Estoy seguro de que querrá saber sobre esto. O al menos los Centinelas lo harán.$B$BAsegúrate de decirle cuán exitosos fuimos y cómo no podríamos haberlo hecho sin un poco de magia.",
    ["O"] = "Lleva las Cartas de Venture Co. a Kaela Shadowspear.",
    ["T"] = "Actualización de Kaela",
  },
  [1092] = {
    ["D"] = "Supongo que debería pensar en salir de aquí también. No tardará mucho en que las noticias de lo que pasó aquí lleguen a los superiores, y estoy seguro de que no les agradaría volver a verme.$b$bMe escabulliré y me reuniré con Ziz. ¡Con suerte, él podrá encontrarme un nuevo trabajo!$b$bIntentaré limpiar un poco este desastre, pero deberías llevar la muestra de mineral a Ziz de inmediato. Me dijo que había establecido un campamento en algún lugar del lado occidental de la Cresta Cortaviento. Buena suerte.",
    ["O"] = "Entrega el Mineral Sin Identificar a Ziz Fizziks en las Montañas de Tuercespina.",
    ["T"] = "Órdenes de Gerenzo",
  },
  [1093] = {
    ["D"] = "Terrible lo que la Corporación Venture ha hecho aquí, ¿eh? ¿En qué estaban pensando? ¡Una compañía goblin contratando a un gnomo!$b$bPor suerte para mí, Sputtervalve reconoce a un genio cuando lo ve. ¡Me contrató justo después de que me echaran!$b$bMe dice que construyeron una enorme máquina de cortar madera, y Sput\' quiere que le robe los planos. Desafortunadamente, Gerenzo, el maldito gnomo, quiere verme muerto al instante... ¿tal vez podrías robarme los planos? Deberías poder encontrarlos en uno de sus operarios.",
    ["O"] = "Consigue los Planos del Super Segador 6000 para Ziz Fizziks en las Montañas de Tuercespina.",
    ["T"] = "Super Segadora 6000",
  },
  [1094] = {
    ["D"] = "Este sobre tiene toda la información que pude arrebatar cuando me echaron... Documentos internos de Venture Co., algunos manuales, cosas técnicas... Oh, y por supuesto los planos del Super Segador 6000.$b$b Sputtervalve se ha establecido en Ratchet en Los Baldíos. No deberías tener problemas para encontrarlo, es un pequeño tipo verde.",
    ["O"] = "Entrega el Sobre Sellado a Sputtervalve en Los Baldíos.",
    ["T"] = "Instrucciones Adicionales",
  },
  [1095] = {
    ["D"] = "El asunto de este gnomo que ha sido contratado por Venture Co. ha causado un poco de revuelo en Undermine. Los príncipes mercantes no aprueban que Razdunk se asocie con este Gerenzo.$B$BRiddlevox me ha pedido que me encargue del problema, y estoy seguro de que fue instruido por uno o más de los príncipes mercantes. Por supuesto, el Director no me lo dijo. Digamos que tengo una corazonada.$B$BSea como sea, Gerenzo debe ser eliminado, y sería una prueba adecuada que Fizziks se encargue de él.",
    ["O"] = "Lleva las nuevas órdenes a Ziz Fizziks en las Montañas de Tuercespina.",
    ["T"] = "Instrucciones Adicionales",
  },
  [1096] = {
    ["D"] = "Conocí a Gerenzo una vez, pero déjame decirte, no es agradable a la vista. Su carne parece estar pudriéndose, y ha injertado metal y engranajes en su cuerpo, reemplazando algunas de sus extremidades con unas mecánicas.$B$BAl noreste de aquí, muy por encima del Lago Cragpool, cerca de la rueda de agua, es donde Gerenzo vigila todo el lugar. No hay forma de que yo pueda abrirme camino hasta allí, y mucho menos enfrentarme a Gerenzo... Pero tú pareces capaz de lograrlo.$B$BSi lo matas por mí, te lo haré valer.",
    ["O"] = "Lleva el Brazo Mecánico de Gerenzo Llavesilba a Ziz Fizziks en los Baldíos de Piedra.",
    ["T"] = "Gerenzo Llavewhistle",
  },
  [1097] = {
    ["D"] = "Hay un herrero de armas enano en Ventormenta, Grimand Elmore, que envió un mensaje diciendo que necesita ayuda con una entrega. Creo que quiere enviar un paquete a su tierra natal en el norte.$B$B¡Tienes un par de piernas fuertes! Así que si estás interesado en hacer un poco de trabajo, habla con Grimand. Podríamos usarte aquí abajo, pero también debemos mantener nuestros lazos fuertes con los enanos.$B$BPuedes encontrar a Grimand Elmore en la tienda de armas en el Distrito Enano de Ventormenta, en la sección noreste de la ciudad.",
    ["O"] = "Habla con Grimand Elmore.",
    ["T"] = "La Tarea de Elmore",
  },
  [1098] = {
    ["D"] = "En preparación para un asalto a Colmillo Oscuro, se enviaron dos acechadores de la muerte a la fortaleza para reunir inteligencia. Han pasado muchos días y no han regresado, ¡pero debemos recibir su informe si queremos tener alguna esperanza de un ataque exitoso!$B$BAsí que aquí está tu tarea, $N. Ve a la Fortaleza de Colmillo Oscuro al sur y encuentra a los Acechadores de la Muerte Adamant y Vincent. Encuéntralos, y si necesitan ayuda, dásela.$B$BEsta misión es tan peligrosa como crítica, $N. No falles.",
    ["O"] = "Encuentra a los Acechamuerte Adamant y Acechamuerte Vincent.",
    ["T"] = "Mortacechadores en Castel Darrow",
  },
  [1099] = "_",
  [1100] = {
    ["D"] = "Abres lentamente el diario desgastado de Henrig Lonebrow....",
    ["O"] = "Lee el Diario de Henrig Ceja Solitaria.",
    ["T"] = "El Diario de Lonebrow",
  },
  [1101] = {
    ["D"] = "¡Pobre Heralath! Y este enano... Lonebrow. Qué alma valiente fue.$b$bNo debemos dejar que su valentía sea en vano. Esta bruja... Charlga Filonavaja... debe ser detenida.$b$bSeguramente, infiltrarse en La Cárcel será peligroso. No hay tiempo para enviar palabra a Darnassus. $n, forma un grupo para matar a la bruja.$b$bTráeme el Medallón de Filonavaja como prueba de su muerte.",
    ["O"] = "Lleva el Medallón de Filocorto a Falfindel Errante en Thalanaar.",
    ["T"] = "La Bruja del Horado",
  },
  [1102] = {
    ["D"] = "Cairne es un líder noble por unir a nuestra gente aquí en Cima del Trueno.$b$bPero no puedo perdonar tan fácilmente a aquellos que nos expulsaron de nuestras tierras ancestrales como algunos. Mi gente habitó el sur de Los Baldíos durante décadas. La tierra era sagrada para nosotros. Pero fuimos expulsados por numerosos enemigos.$b$bUn destino vengativo espera a la bruja, Charlga Filonaval, que reúne un ejército vil dentro de Horado Rajacieno.$b$bTráeme el corazón de Filonaval y podré morir en paz, $n.",
    ["O"] = "Lleva el Corazón de Filocorto a Auld Pico de Piedra en Cima del Trueno.",
    ["T"] = "Un Destino Vengador",
  },
  [1103] = {
    ["D"] = "¡Saludos, $ghermano:hermana;! ¿También has venido a purificar las aguas aquí y demostrar tu comprensión a Islen?$B$BAcabo de derrotar al espíritu de agua corrupto, y estoy a punto de poner las brazaleras y las gotas restantes de agua en el brasero.$B$B¿Qué es eso? Ahhh. Tiev entiende demasiado bien cuando algo sale mal. Bueno, Tiev puede ayudarte mucho si necesitas ver al espíritu de agua puro nuevamente.$B$BTráeme un sapta de agua de Islen como prueba de que estás listo, y esperaré para entregar mis partes.",
    ["O"] = "Lleva un Sapta de Agua a Tiev Mordune en Bosque de Argénteos si no lograste hablar con la Manifestación de Agua Menor.",
    ["T"] = "La Llamada del Agua",
  },
  [1104] = {
    ["D"] = "¡Tengo tantos diseños para nuestro coche cohete! ¡Tantos! ¡Y debo probarlos todos!!$B$BEstoy trabajando en un nuevo tipo de combustible. ¡Uno que arde muy, muy caliente! No he logrado la mezcla perfecta, pero creo que sé lo que necesito...$B$BLos escórpidos que deambulan por los Llanos de Sal tienen un veneno con mucha, mucha sal. ¡Es muy único! ¡Y es justo lo que necesito!$B$B¡Tráeme veneno y confía en mí, nuestro coche volará!",
    ["O"] = "Lleva 6 Venenos de Escórpido Salado a Fizzle Tornillos en Los Baldíos Brillantes.",
    ["T"] = "Veneno de las Salinas",
  },
  [1105] = {
    ["D"] = "Mi hermano está trabajando en un nuevo combustible para su coche cohete. Es brillante, realmente lo es. Tan brillante que... ¡creo que ese nuevo combustible que está diseñando es lo suficientemente fuerte como para comerse el chasis del coche!$B$BQuiero reforzar sus tanques de combustible antes de que eso suceda.$B$BLas tortugas en las llanuras tienen caparazones muy duros. Quiero usarlos para crear nuevos tanques para nuestro coche cohete. Tanques que puedan contener el nuevo combustible de mi hermano.$B$BPareces lo suficientemente responsable. ¿Me conseguirás esos caparazones?",
    ["O"] = "Lleva 9 Caparazones de Tortuga Endurecidos a Wizzle Tornillos en Los Baldíos Brillantes.",
    ["T"] = "Caparazones Endurecidos",
  },
  [1106] = {
    ["D"] = "Estoy desarrollando un nuevo motor que hará que el coche vaya tan rápido. ¡Tan rápido que volará, lo sé! Solo necesito hacer pistones que puedan soportar un estrés muy muy pesado. ¡Todos mis intentos han fallado porque no puedo hacer pistones lo suficientemente duros!$B$BPero hay alguien que podría saber cómo.$B$BSe llama Martek el Exiliado. Es un gran herrero y sabe más sobre metal que nadie. ¡Llévale esta carta, sé que puede ayudar!$B$B¡Pero está muy lejos, en Azeroth, en las Tierras Inhóspitas, en un campamento, con un goblin!$B$B¡Encuéntralo!",
    ["O"] = "Lleva la Carta de Fizzle a Martek el Exiliado en las Tierras Inhóspitas.",
    ["T"] = "Martek el Exiliado",
  },
  [1107] = {
    ["D"] = "¡Nuestro coche cohete es lo más rápido en los llanos! Tan rápido que... ¡me temo que la fricción lo va a destrozar!$B$BPara evitar eso, necesito crear un compuesto para recubrir las partes móviles del coche. Debe ser duro pero no quebradizo, y debe ser resistente a la corrosión por sal, o el tiempo pasado en los Llanos Brillantes lo corroerá.$B$B¿Has oído hablar del Arrecife Vil, $N? Está frente a la costa de Vega de Tuercespina. Los múrloc escamasalada viven allí, y sus aletas de cola son un ingrediente crucial de mi compuesto.",
    ["O"] = "Lleva 10 Aletas de Cola Incrustadas a Wizzle Brassbolts en el Desierto Brillante.",
    ["T"] = "Aletas de Cola Incrustadas",
  },
  [1108] = {
    ["D"] = "No estoy aquí solo para alejarme de la gente. Hay una gran cantidad de metal en las Tierras Inhóspitas, $N, si tienes el valor para conseguirlo.$B$BEl metal se llama indurio y, por suerte, se rumorea que posee una alta resistencia al calor y al estrés. Podría funcionar para el auto de Fizzle.$B$BPero asegurémonos. Tráeme muestras de indurio y probaré sus propiedades.$B$BEl verdadero mineral de indurio se encuentra en las profundidades de Uldaman, pero los troggs Criptatorre de las Tierras Inhóspitas a veces tienen escamas de él. ¡Tráeme esas escamas!",
    ["O"] = "Lleva 10 Escamas de Indurio a Martek el Exiliado en las Tierras Inhóspitas.",
    ["T"] = "Indurio",
  },
  [1109] = {
    ["D"] = "Como puedes ver, $n, estamos realizando algunos experimentos muy... interesantes en la Sociedad Real de Boticarios.$b$bComo Maestro aquí, es mi trabajo supervisar los intentos alquímicos más ambiciosos. ¡Tanto trabajo y tan poco tiempo!$b$bPareces bien viajado para un $c. Quizás puedas ayudarme.$b$bNecesito una sustancia rara. Es un material repugnante, proviene de una especie rara de murciélagos que solo se encuentra en el agujero fangoso conocido como Zarzal Rajacieno. Tráeme el guano de los murciélagos de Rajacieno para que podamos comenzar nuestro trabajo...",
    ["O"] = "Lleva 1 montón de Guano de Kraul al Maestro Boticario Faranell en Entrañas.",
    ["T"] = "Guano del Horado",
  },
  [1110] = {
    ["D"] = "Je je, con todas las carreras que hacen aquí, no es de extrañar que ocurran algunos accidentes en el camino. Si miras alrededor de los Llanos Brillantes, verás evidencia de choques pasados—¡trozos de partes de coches cohete están esparcidos por todas partes!$B$B¡Y esas partes valen dinero para los gnomos y los goblins! Siempre están buscando más artilugios para añadir a sus coches.$B$BAsí que sal y consígueme partes. Tráeme un montón y te pagaré bien.",
    ["O"] = "Lleva 30 Piezas de Cohete a Kravel Koalbeard en el Desierto Brillante.",
    ["T"] = "Partes de Coche Cohete",
  },
  [1111] = {
    ["D"] = "Me encargo de conseguirle a la gente lo que necesita, y los gnomos de allí necesitan que se complete un pedido de piezas. Sus piezas fueron enviadas a Ratchet hace una semana, pero no he podido hacer el viaje.$B$BEntonces, ¿estás listo para un poco de trabajo? Ve a Ratchet y entrega este pedido de piezas al Capataz del Muelle Dizzywig, probablemente estará en los muelles.$B$BHaz esto por mí y tendré más trabajos para ti.$B$BTrabajos, amigo mío, con un potencial de ganancias serias...",
    ["O"] = "Lleva el Pedido de Piezas de Kravel al Capataz del Muelle Dizzywig en Ratchet.",
    ["T"] = "Maestro del Muelle Dizzywig",
  },
  [1112] = {
    ["D"] = "Aquí está el paquete de Kravel, $N. Y cuando se lo entregues, dile... que si quiere más pedidos especiales, ¡tendrá que pagar su deuda!",
    ["O"] = "Lleva las Piezas de Kravel a Kravel Barbabronce en Los Baldíos Brillantes.",
    ["T"] = "Partes para Kravel",
  },
  [1113] = {
    ["D"] = "¿Por qué necesito un tipo raro de guano, podrías preguntar? Bueno, para este experimento quiero ver qué efecto pueden tener dos sustancias químicas diametralmente opuestas entre sí, especialmente cuando ambas se combinan con un agente siempre tóxico para asegurar los resultados más... espantosos.$b$bEn los picos del noreste de los Claros de Tirisfal se encuentra el Monasterio Escarlata. Allí se congregan los discípulos locos de la Cruzada Escarlata. Mátalos y tráeme sus corazones fanáticos.$b$B¡Nuestra poción será la más malvada!",
    ["O"] = "El Maestro Boticario Faranell en Entrañas quiere 20 Corazones de Celador.",
    ["T"] = "Corazones de Celos",
  },
  [1114] = {
    ["D"] = "Ahora que tengo lo que necesito, dale estas partes a Fizzle Brassbolts. Ha estado esperando mucho tiempo por ellas, estoy seguro de que no echará de menos la pequeña pieza que quité de su pedido...$B$BY no te molestes en mencionárselo. Era pequeña y sin valor.$B$BConfía en mí.",
    ["O"] = "Lleva las Piezas de Automóvil Delicadas a Fizzle Brassbolts en el Desierto de Sal.",
    ["T"] = "Entrega a los Gnomos",
  },
  [1115] = {
    ["D"] = "Ahora que hemos hecho un servicio para los gnomos, ¡es hora de hacerle un favor a los goblins!  $B$BHe oído que el piloto de carreras goblin, Nazz Steamboil, no siempre usó ese nombre.  Me pregunto por qué lo cambió.  Me pregunto si es un secreto, y... me pregunto si valora ese secreto.$B$BSi alguien sabe la verdad, entonces es Krazek de Bahía del Botín, en el extremo sur de Vega de Tuercespina.  Habla con Krazek y pregúntale sobre nuestro piloto goblin.$B$BSi puedes obtener información útil de él, entonces podríamos sacar una ganancia de ello.",
    ["O"] = "Habla con Krazek en Bahía del Botín.",
    ["T"] = "El Chismoso",
  },
  [1116] = {
    ["D"] = "Obtener información sobre Nazz es un gran favor, $N. Sí, un gran favor. Pero has venido desde los Llanos Brillantes para obtenerla... ¿estás dispuesto a viajar un poco más?$B$BMientras contacto a mis fuentes, ¿por qué no recolectas algo para mí, eh?$B$BHm... ¿qué será? ¿Qué tal polvo de sueño, de los dragones menores del Pantano de las Penas? Sí, ¡me encantaría un poco de polvo de sueño para mi bolsa de rapé!$B$BTráemelo, $N, y te daré la información que buscas.",
    ["O"] = "Lleva 10 Partículas de Polvo de Ensueño a Krazek en Bahía del Botín. El Polvo de Ensueño se obtiene de los vástagos de dragón de los Pantanos de las Penas.",
    ["T"] = "Polvo de Ensueño en el Pantano",
  },
  [1117] = {
    ["D"] = "Me las arreglé para usar algunos de mis favores, regalé una botella rara de vino élfico e hice algunas deducciones pesadas yo mismo. ¡Pero encontré la información que buscas!$B$BAquí, escribí una carta con algunos datos muy interesantes sobre nuestro amigo, Nazz Steamboil. Haz con ella lo que quieras, es demasiado peligroso para mí usarla.$B$B ¡Solo no le digas a nadie de dónde la obtuviste!",
    ["O"] = "Lleva los Rumores de los Goblins a Kravel Koalbeard en el Desierto de Sal.",
    ["T"] = "Chismes para Kravel",
  },
  [1118] = {
    ["D"] = "$N, eres un $c en quien puedo confiar, tanto para hacer el trabajo como para mantener la boca cerrada mientras lo haces.$B$BHe estado tramando un nuevo plan. Es arriesgado, pero si funciona, ¡ambos podríamos ganar mucho! Y creo que eres justo la persona para llevarlo a cabo.$B$BPara empezar, debes volver a Bahía del Botín. Lo siento, buen $N. Sé que mi última tarea te llevó allí. Pero esta vez, habla con Crank Fizzlebub.$B$BEntrégale esta carta. Y si requiere ayuda, haz lo posible por ayudarlo. Valdrá la pena el esfuerzo.",
    ["O"] = "Lleva el Plan de Kravel a Crank Fizzlebub en Bahía del Botín.",
    ["T"] = "De Regreso a Bahía del Botín",
  },
  [1119] = {
    ["D"] = "Descubrí cómo usar la mezcla de Zanzil con cerveza negra para hacer una poderosa bebida. Una Cerveza del Tonto. Creo que será justo lo que Kravel necesita, pero pruébala y déjame saber cómo funciona.$B$BY $N, no guardes esto por mucho tiempo sin usarlo. La mezcla de Zanzil tiene un efecto extraño en la levadura de la cerveza negra, acelerándola. Así que no se mantendrá fresca para siempre.",
    ["O"] = "Lleva la Cerveza del Tonto de vuelta a Kravel en el Desierto de Sal.",
    ["T"] = "La Mezcla de Zanzil y el Stout del Tonto",
  },
  [1120] = {
    ["D"] = "Necesitamos probar la Cerveza del Tonto en uno de los equipos de boxes.  Si quieres probarla en los gnomos, entonces aquí - toma la cerveza y dásela al jefe de boxes gnomo.$B$BY no esperes demasiado.  Esta cerveza se echará a perder eventualmente.",
    ["O"] = "Lleva la Cerveza del Tonto al Capataz Gnomo.",
    ["T"] = "Embriagar a los Gnomos",
  },
  [1121] = {
    ["D"] = "Necesitamos probar la Cerveza del Tonto en uno de los equipos de boxes.  Si quieres probarla en los goblins, entonces aquí - toma la cerveza y dásela al jefe de boxes goblin.$B$BY no esperes demasiado.  Esta cerveza se echará a perder eventualmente.",
    ["O"] = "Lleva la Cerveza del Tonto al Capataz Goblin.",
    ["T"] = "Embriagar a los Goblins",
  },
  [1122] = {
    ["D"] = "¡Esa Cerveza del Tonto funcionó de maravilla! Hice una apuesta en la próxima carrera, y... ¡esperemos que la tripulación a la que emborrachaste haya hecho de las suyas con su auto!$B$BSolo queda una tarea más para ti: lleva esta nota de vuelta a Crank Fizzlebub en Bahía del Botín. Querrá saber sobre la prueba exitosa.$B$B¡Y con suerte podremos conseguir más de esa Cerveza del Tonto para futuras carreras!",
    ["O"] = "Lleva el informe de la Cerveza del Tonto a Crank Fizzlebub en Bahía del Botín.",
    ["T"] = "Informar a Fizzlebub",
  },
  [1123] = {
    ["D"] = "Para esta tarea, el mayordomo de Keeper Remulos en Claro de la Luna, Rabine Saturna, busca ayuda para explorar las vastas tierras baldías al oeste de Tanaris y Un\'Goro. Se encuentra en Refugio de la Noche, el pueblo principal de Claro de la Luna.$B$BQuiero recalcar la importancia de tu presencia. Hay tensiones crecientes entre los elfos nocturnos de Darnassus y el Círculo Cenarion en Claro de la Luna. Mira más allá de los límites raciales y aprovecha la sabiduría de tus ancestros en tus tratos con el Círculo allí, $N. Buena suerte.",
    ["O"] = "Habla con Rabine Saturna en la aldea de Refugio de la Noche, en Claro de la Luna.",
    ["T"] = "Rabine Saturna",
  },
  [1124] = {
    ["D"] = "Al oeste del Cráter de Un\'Goro, a través de la cresta noroeste, hay un desierto árido: Silithus. Pocos saben que allí se libró una guerra antigua entre los elfos nocturnos y un poder maligno y alienígena. El mal fue sellado, pero ahora sospechamos que hay fuerzas trabajando para despertar este... horror indescriptible.$B$BBusca a Layo Starstrike, uno de nuestros agentes en Silithus, búscalo en el Reposo del Valor, el lugar de descanso final para los caídos de los desiertos. Entrégale esta carta, y él te dará su confianza en consecuencia.",
    ["O"] = "Habla con Layo Golpe Estelar cerca del cementerio del Reposo del Valor en Silithus, mostrándole la Carta de Rabine.",
    ["T"] = "Tierra Baldía",
  },
  [1125] = {
    ["D"] = "Al suroeste se encuentra un lugar trágico: las ruinas de la Aldea del Viento del Sur. Aquí es donde nuestro trabajo debería comenzar, $N.$B$BSegún lo que hemos aprendido, esta aldea fue una vez una base de operaciones de los elfos nocturnos para Silithus. Desde entonces ha sido invadida por los enjambres de silítidos que infestan los yermos. Solo puedo imaginar el horror del desmembramiento inicial de la aldea...$B$BLos espíritus torturados de druidas y centinelas caídos vagan por las ruinas sin rumbo. Explora este lugar, liberando las almas de estos seres desdichados según sea necesario.",
    ["O"] = "Libera los espíritus de 8 Druidas Atormentados y 8 Centinelas Atormentadas en la Aldea del Viento del Sur, y luego regresa con Layo Golpe Estelar en el cementerio del Reposo del Valor en Silithus.",
    ["T"] = "Los Espíritus del Viento del Sur",
  },
  [1126] = {
    ["D"] = "El enjambre debe ser abordado en la Aldea Brisa del Sur, y creo que sé una manera de llegar a ellos. Ha sido mi experiencia que los enjambres de silítidos son muy sensibles a su entorno, esto explica sus ataques al escuchar los lamentos de muerte de los espíritus.$B$BLos silítidos son más fuertes en la torre, la que tiene una masa de avispas rodeándola. Escala la torre y agita el enjambre para activarlo. Informa lo que aprendas, ¡pero ten cuidado! Si descubres algo que pueda ayudar, tráemelo.",
    ["O"] = "Escala la torre de la Aldea del Viento del Sur y localiza un medio para agitar la colmena silítida. Devuelve cualquier cosa inusual que puedas descubrir al hacerlo a Layo Golpe Estelar en el cementerio del Reposo del Valor en Silithus.",
    ["T"] = "Colmena en la Torre",
  },
  [1127] = {
    ["D"] = "¿Así que quieres un poco de Cerveza del Tonto, eh?  ¿Pensando en volver a las carreras...?$B$BMuy bien, tráeme más de la mezcla de Zanzil y haré más cerveza para ti.",
    ["O"] = "Lleva 12 Mezclas de Zanzil a Crank Fizzlebub en Bahía del Botín.",
    ["T"] = "Stout del Tonto",
  },
  [1130] = {
    ["D"] = "$N, Melor Pezuña Pétrea ha oído hablar de tus hazañas y ha trazado un camino para ti.$B$BSi deseas tomarlo, ve a Cima del Trueno y habla con Melor. Lo encontrarás en el Ascenso del Cazador.",
    ["O"] = "Habla con Melor Pezuña Pétrea en Cima del Trueno.",
    ["T"] = "Melor Envía un Mensaje",
  },
  [1131] = {
    ["D"] = "A veces cazamos por comida. A veces cazamos por honor. Y a veces cazamos para ganar las enseñanzas de la Madre Tierra.$B$BPero la caza que ahora te encomiendo no es ninguna de estas cosas. Es matar a una criatura fuera de la bendición de la Madre Tierra.$B$BEl hiena Steelsnap merodea por las Mil Agujas, al sur de los Baldíos. Es un tirano, atrayendo a otros hienas hacia él y esparciendo miedo y derramamiento de sangre entre las bestias más pacíficas.$B$BEncuentra a Steelsnap, $N. Encuéntralo y derrótalo.",
    ["O"] = "Lleva la Costilla de Muerdefrío a Melor Pezuña de Piedra en Cima del Trueno.",
    ["T"] = "Dentacerada",
  },
  [1132] = {
    ["D"] = "¡Oh, estar en el mar una vez más!  ¡Sentir el beso del viento, y tener las olas meciéndome como mi bendita madre, hace mucho tiempo!$B$B¡Oh, desearía tener tu fortuna, buen $c, porque veo el mar en tu futuro!$B$B¡Es mi trabajo contarles a las almas ansiosas sobre la tierra de Kalimdor, la tierra de la oportunidad!  Si estás dispuesto a probar suerte al otro lado del mar, entonces toma un barco desde aquí hasta el encantador puerto de Theramore.  Habla allí con mi compañera, la elfa, Fiora Longears.$B$B¡Ella te iniciará en tu aventura en Kalimdor!",
    ["O"] = "Habla con Fiora Orejas Largas en Theramore.",
    ["T"] = "Fiora Longears",
  },
  [1133] = {
    ["D"] = "Si acabas de llegar en barco desde Menethil, lo primero que deberías hacer es... ir a Astranaar. Estoy seguro de que un miembro entusiasta de la Alianza como tú puede hacer un verdadero bien allí. Habla con Shindrell Fuegorápido y ofrece tus servicios.$B$BPero no te molestes en mencionar quién te envió. Shindrell no me conoce...",
    ["O"] = "Habla con Shindrell Fuegorápido en Astranaar.",
    ["T"] = "Viaje a Astranaar",
  },
  [1134] = {
    ["D"] = "No estamos en guerra abierta con la Horda, pero nuestra paz es frágil. A menudo encontramos a sus agentes actuando en nuestra contra, pero lo hacen de manera encubierta.$B$BUn avance de este tipo nos fue revelado por fuentes ocultas en Theramore, afirmando que los orcos recolectan veneno de los alerones cerca del Lago Mirkfallon, en las Montañas de Stonetalon, al sur. ¡Los asesinos orcos luego usan el veneno en sus incursiones secretas contra la Alianza!$B$BSi esto es cierto, entonces debemos detenerlos, $N. Debemos negarles su fuente de veneno.",
    ["O"] = "Lleva 12 Sacos de Veneno de Orgulloalado a Shindrell Swiftfire en Astranaar.",
    ["T"] = "Alas de Orgullo de Stonetalon",
  },
  [1135] = {
    ["D"] = "Como sabrás, los wyverns a menudo son domesticados por los orcos para usarlos como monturas. Pero lo que es menos conocido es que el veneno de un wyvern, en algunos círculos, es más valioso que la bestia misma. Así que si no tienes miedo de cazar un poco de wyverns, entonces... tengo un trabajo para ti.$B$BHay un lugar llamado Altozano en la tierra de las Mil Agujas, al oeste de aquí. Los wyverns de Altozano tienen un veneno potente. Potente y valioso.",
    ["O"] = "Lleva 10 Sacos de Veneno de Altoperch a Fiora Longears en Theramore.",
    ["T"] = "Veneno de Altozano",
  },
  [1136] = {
    ["D"] = "$N, es hora de que viajes a tierras lejanas. Estás listo.$B$BAhora cazarás a Fauzgelida, un yeti en las Montañas de Alterac. Es escurridizo, su pelaje blanco se mezcla con las nieves de su hogar montañoso. Rara vez se muestra.$B$BTe mostraré cómo atraer a una criatura como él. Aquí, toma este pergamino. En él he escrito cómo se puede atraer a Fauzgelida.$B$BAhora ve, $N. Tu viaje es largo y muchos desafíos te esperan. Cuando regreses con la melena de Fauzgelida, solo entonces tu tarea estará completa.",
    ["O"] = "Lleva la Melena de Fauzhelada a Melor Pezuña de Piedra en Cima del Trueno.",
    ["T"] = "Faucescarcha",
  },
  [1137] = {
    ["D"] = "El indurio tiene una resistencia asombrosa al calor. Estoy seguro de que funcionará para los pistones de Fizzle. Pero necesitará auténtico mineral de indurio, no solo las escamas.$B$BPara obtener el mineral, debe ser extraído de las profundidades de Uldaman, en las fronteras norte de las Tierras Inhóspitas. Esa es la verdadera fuente del indurio.$B$BEnvía mis saludos a Fizzle Brassbolts y dile lo que te he dicho.$B$B¡Buen viaje, $N! Y no me sorprendería si nos volvemos a encontrar, una vez que Fizzle sepa del indurio, probablemente te enviará a Uldaman para conseguirlo.",
    ["O"] = "Habla con Fizzle Pernolatón en el Desierto Brillante.",
    ["T"] = "Noticias para Fizzle",
  },
  [1138] = {
    ["D"] = "Me encantan los cangrejos. ¡Los cangrejos son el fruto del mar! Puedes hornearlos, asarlos, hervirlos y cocerlos al vapor. Se fríen en sartén, se fríen en profundidad y se saltean. Hay ensalada de cangrejo, sopa de cangrejo, estofado de cangrejo, cangrejo a la pimienta, cangrejo al limón, cangrejo con raíz látigo y cangrejo sorpresa de Forjaz. Eso es... eso es todo.$B$BEstoy aquí pescando ahora mismo, así que no puedo conseguir trozos finos de cangrejo. Puedes sacarlos de los trepadores de arrecife y los trepadores de marea incrustados. Puedes hacer muchas cosas con ellos. Puedes hornearlos, asarlos, hervirlos...",
    ["O"] = "Recolecta 6 Trozos de Cangrejo Fino para Gubber Blump en Auberdine.",
    ["T"] = "Fruto del Mar",
  },
  [1139] = {
    ["D"] = "Hemos ralentizado al clan Forjatiniebla al cortarlos de la Cumbre de Roca Negra, pero la misión de Hammertoe sigue sin cumplirse. Su muerte no debe ser en vano. Soñaba con encontrar la Tabla de la Voluntad: un fragmento de piedra inscrito con magia por los mismos titanes para ayudar a infundir vida a sus creaciones, dándoles una voluntad propia, o eso dicen las leyendas.$B$BTodavía debemos recuperar la Tabla antes de que el clan Forjatiniebla la encuentre.$B$BRegresa a Uldaman, encuentra la tabla y regresa aquí de inmediato.",
    ["O"] = "Encuentra la Tablilla de la Voluntad y llévasela al Consejero Belgrum en Forjaz.",
    ["T"] = "Las Tablas Perdidas de la Voluntad",
  },
  [1140] = {
    ["D"] = "La escritura de los orcos es poco mejor que los garabatos de un niño. Muchos de mi orden se burlaron cuando elegí aprender su lengua, pero he encontrado beneficioso obtener un mayor entendimiento de mis enemigos.$B$BDe los garabatos de Ilkrud, parece que Athrikus todavía posee dos gemas de alma, y están siendo guardadas en Carrera Nocturna y Satyrnaar, áreas controladas por sátiros en el noreste de Ashenvale.$B$BTen cuidado ahí fuera, $n, los templos empapados de sangre de los sátiros pueden ser inquietantes incluso para un $c experimentado como tú.",
    ["O"] = "Libera las almas Altonato atrapadas en Carrera Nocturna y Satyrnaar, y luego regresa con Delgren el Purificador en el Puesto de Maestra.",
    ["T"] = "La Torre de Althalaxx",
  },
  [1141] = {
    ["D"] = "Me encanta pescar. La familia Blump es conocida por la pesca. Mi nombre es Gubber Blump, yo pesco.$B$BHay un tipo de pez que me gusta pescar por aquí llamado mero de Costa Oscura. Solía salir en un bote para pescar meros, pero ningún bote ha salido desde que esos malditos múrlocos se mudaron. Apuesto a que se han estado comiendo todos los meros.$B$BOye, ¿quieres ayudarme a pescar meros de Costa Oscura? ¡Te daría una auténtica caña de pescar de la familia Blump si lo hicieras! Es una caña muy buena para pescar.",
    ["O"] = "Captura 6 Mero de Costa Oscura para Gubber Blump en Auberdine.",
    ["T"] = "La Familia y la Caña de Pescar",
  },
  [1142] = {
    ["D"] = "No me queda mucho tiempo de vida... tan... tan débil.$b$b...el valiente enano, Ceja Solitaria, ha sido enviado para advertir a Falfindel...$b$bPero antes de que... m-m-muera...$b$b...mi esposa, Treshala... su colgante de unión conmigo... robado... por uno de los malditos agresores...$b$bEncuéntralo, p-p-por favor... y devuélveselo a Treshala en Darnassus...$b$b...junto con la n-n-noticia de mi solitaria... muerte.",
    ["O"] = "Encuentra y devuelve el Colgante de Treshala a Treshala Pardoluna en Darnassus.",
    ["T"] = "La Mortalidad Disminuye",
  },
  [1143] = {
    ["D"] = "Veo. Bueno, si lo que dices es cierto, entonces ahora es el momento de atacar. Creo que Athrikus reside en la cima de la torre. Ten cuidado, está rodeado de muchos brujos poderosos, mucho más mortales que los que enfrentaste antes.",
    ["O"] = "Mata a Athrikus Narassin y lleva su cabeza a Balthule Golpe Sombrío cerca de la Torre de Althalaxx.",
    ["T"] = "La Torre de Althalaxx",
  },
  [1144] = {
    ["D"] = "¡Por fin, ha llegado la ayuda!$b$bNo tienes idea de cuánto tiempo he estado prisionero aquí. No recuerdo la última vez que vi el exterior de este lugar.$b$bVerás, soy un empresario. Siempre buscando una moneda de cobre, como dicen. Me enteré de que Mebok Mizzyrix pagaba bien por los tubérculos de hojarroja de este corral de cerdos. Pensé que sería un asunto fácil, entrar y salir. ¡Como puedes ver, me equivoqué un poco!$b$bDe todos modos, si fueras tan amable de ayudarme a salir de aquí, te lo compensaré.",
    ["O"] = "Escolta a Willix el Importador fuera de Rajacieno.",
    ["T"] = "Willix el Importador",
  },
  [1145] = {
    ["D"] = "Muchos viajeros que han pasado por El Cruce me ayudaron a recolectar huevos del Campo de los Gigantes, y muchos de ellos tienen historias de lo mortales que son las criaturas. He escuchado lo suficiente como para considerarlas una amenaza mucho mayor de lo que mis maestros pensaron inicialmente.$B$BNecesito un mensajero para viajar a Orgrimmar y encontrarse con Belgrom Rocamartillo. No es un asunto simple, y alguien que tome la tarea en serio debería ir.$B$BBelgrom suele estar afuera de la casa del gremio de guerreros.",
    ["O"] = "Entrega la Nota Sellada de Korran a Belgrom Machacarroca en Orgrimmar.",
    ["T"] = "El Enjambre Crece",
  },
  [1146] = {
    ["D"] = "Así que Korran ha llegado a una conclusión similar sobre estos insectos: parecen ser una amenaza mayor de lo que asumimos.$B$BHace unos días, un cazador regresó a mí desde las Mil Agujas casi muerto. Estaba con un grupo de asalto enviado para expulsar a un equipo de excavación enano de los Llanos Brillantes. Durante el ataque, más de estos insectos surgieron de una cueva en el sitio de excavación y casi matan al grupo.$B$BFueron liderados por Moktar Krin, y se han refugiado en el borde de los Llanos Brillantes. Tráele esta nota.",
    ["O"] = "Entrega la Nota Sellada de Belgrom a Moktar Krin en las Mil Agujas.",
    ["T"] = "El Enjambre Crece",
  },
  [1147] = {
    ["D"] = "Hmm. Parece que Belgrom cree que esos monstruos son una amenaza. Quiere que los empujen de vuelta a las profundidades de donde salieron.$B$BSupongo que matar a los enanos enviados por el Explorador",
    ["O"] = "Mata 5 Buscadores Silítidos, 5 Zánganos de Colmena Silítidos y 5 Invasores Silítidos y regresa con Moktar Krin en las Mil Agujas.",
    ["T"] = "El Enjambre Crece",
  },
  [1148] = {
    ["D"] = "Inspeccionas el caparazón con cuidado. Al girarlo en tus manos, te das cuenta de que las placas quitinosas están entrelazadas de una manera que las hace increíblemente fuertes. Es diferente a todo lo que has visto antes. Inspeccionas el resto del cadáver y te das cuenta de que estas criaturas son muy poderosas y altamente evolucionadas.$B$BMirando de nuevo el caparazón, piensas que a Korran le gustaría estudiar más de estas criaturas. Podría ayudarlo a descubrir qué son estas criaturas y de dónde vienen.",
    ["O"] = "Lleva 1 Corazón de Silítido, 5 Garras de Silítido y 3 Caparazones de Silítido intactos a Korran en El Cruce.",
    ["T"] = "Partes del Enjambre",
  },
  [1149] = {
    ["D"] = "Así que me has buscado, o tal vez el destino te trae a mí? No importa. Estás aquí ahora, y así tu viaje comenzará de nuevo.$B$BMuchos aspectos conforman a un ser: el espíritu, el cuerpo, la mente. Algunos de estos pueden medirse por las tradiciones de una cultura. Otros solo podemos esperar que existan dentro de nosotros. Si estás de acuerdo, pondré a prueba algunos de los aspectos dentro de ti.$B$BSi pasas, te volverás más fuerte. Si fallas, entonces te darás cuenta de la vastedad del Vacío.$B$BEl primero es una Prueba de Fe.",
    ["O"] = "Si tienes fe, salta desde las tablas que dominan las Mil Agujas.",
    ["T"] = "Prueba de Fe",
  },
  [1150] = {
    ["D"] = "Me complace ver que tu valor sigue firme a pesar de lo que ya has hecho. Como tu fe sigue intacta, es hora de poner a prueba tu cuerpo.$B$BEl primero es una prueba de resistencia. Ve con amigos a la Guarida de los Plumasbrujas, al oeste de Puesto Viento Libre. Adéntrate en las profundidades de su guarida y destruye sus provisiones. Para entonces deberías haber provocado su furia, y si logras sobrevivir el tiempo suficiente, la propia Grenka Bloodscreech vendrá por ti. Mátala y tráeme su garra para completar tu prueba.",
    ["O"] = "Lleva la Garra de Grenka a Dorn Acechallanuras en Las Mil Agujas.",
    ["T"] = "Prueba de Resistencia",
  },
  [1151] = {
    ["D"] = "Durante la época de los Dioses Antiguos, los elementales habitaban gran parte de este mundo. Adoraban a los Dioses Antiguos, y los Dioses, a través de su poder, mantenían a los elementales atados a este mundo. Cuando los Dioses Antiguos fueron desterrados, los elementales desaparecieron de nuestro mundo y ahora solo regresan por orden de poderosos magos.$B$BSin embargo, hay excepciones. Algunos elementales eran lo suficientemente poderosos como para permanecer aquí por su cuenta. Uno de esos elementales es Rok\'Alim.$B$BPara probar tu fuerza, encuentra a Rok\'Alim en las Mil Agujas y mátalo.",
    ["O"] = "Lleva los Fragmentos de Rok\'Alim a Dorn Acechallanuras en Las Mil Agujas.",
    ["T"] = "Prueba de Fuerza",
  },
  [1152] = {
    ["D"] = "Tu tiempo conmigo está a punto de terminar. Soy conocedor, pero la Prueba de la Sabiduría no es mía para darla. Hay lecciones que aprender y lugares que visitar que otros reclaman como su dominio.$B$BSi estás preparado, busca a Braug Espíritu Oscuro, es un chamán de gran sabiduría. Él te pondrá a prueba más, así que presta atención a sus palabras, $N.$B$BCuando estés listo, encuentra el túnel que conecta las Montañas de Trabalomas con Vallefresno: Braug habita cerca de allí. Y ten cuidado, $N, los elfos nocturnos pueden querer impedir tu prueba.",
    ["O"] = "Encuentra a Braug Espíritu Tenue cerca de la entrada al Sendero Talondeep en las Montañas Filospada.",
    ["T"] = "Prueba de Sabiduría",
  },
  [1153] = {
    ["D"] = "$C, has hecho bien al ayudarme en el pasado. El jefe de guerra está satisfecho hasta ahora con lo que he logrado, y me pide que continúe con renovada determinación. Mi honor dicta que te dé el reconocimiento adecuado al jefe de guerra por todo lo que has hecho, pero primero te pediría tu ayuda nuevamente.$B$BRecientemente, unos viajeros llegaron del sur desde un lugar llamado Las Mil Agujas. Allí mataron a un kóbold que tenía una muestra de mineral inusual, pero no quisieron venderla. Quiero una muestra de ese mineral para examinarlo.",
    ["O"] = "Encuentra una Muestra de Mineral sin Refinar en un Excavador o Topógrafo Morrobarro y llévasela a Tatternack Forjaferro en Campamento Taurajo en Los Baldíos.",
    ["T"] = "Una Nueva Muestra de Mineral",
  },
  [1154] = {
    ["D"] = "Has pasado las pruebas de Dorn, y ahora buscas pasar tu siguiente prueba. Muy bien. Pero hasta ahora, tus desafíos han sido infantiles. Es fácil probar tu cuerpo, pero tu mente es otra cosa completamente diferente.$B$BTe lo explicaré de manera simple: encuentra el libro que nombre, estúdialo y responde mi pregunta sobre su contenido.$B$B¿Qué libro, te estarás preguntando? El Legado de los Aspectos. Se puede encontrar en el fondo de Dor\'danil en el sur de Frondavil, protegido por los druidas muertos y sus asesinos.",
    ["O"] = "Encuentra el Legado de los Aspectos y llévaselo a Braug Espíritu Tenue cerca de la entrada al Paso Talondeep en los Baldíos de los Espolones.",
    ["T"] = "Prueba de Sabiduría",
  },
  [1155] = "_",
  [1156] = "_",
  [1157] = {
    ["D"] = "TODO",
    ["O"] = "POR HACER",
    ["T"] = "Alexstrasza",
  },
  [1158] = {
    ["D"] = "TODO",
    ["O"] = "POR HACER",
    ["T"] = "Malygos",
  },
  [1159] = {
    ["D"] = "Bien hecho, $N. Has pasado la primera parte de la Prueba del Conocimiento, pero todavía hay otra pregunta por responder. Pero esa pregunta no es mía para hacerla.$B$BBusca a Parqual Fintallas en la Ciudad de Lordaeron. Él fue un gran historiador para los humanos antes de que la Plaga lo alcanzara. Ahora continúa buscando la iluminación, pero de una naturaleza diferente.$B$BÉl tendrá otro requisito para ti antes de preguntarte algo, pero lo has hecho magistralmente hasta ahora, estoy seguro de que superarás su obstáculo.",
    ["O"] = "Encuentra a Parqual Fintallas en Entrañas.",
    ["T"] = "Prueba de Sabiduría",
  },
  [1160] = {
    ["D"] = "Te diré lo que debes buscar, e incluso te haré mi pregunta, pero no porque desees demostrar tu valía. El conocimiento es mucho más importante que la fuerza. Incluso cuando otros parecen superarte en habilidad, tu conocimiento del mundo será más valioso.$B$BVe al Monasterio en el norte de los Claros de Tirisfal. En la biblioteca allí, reside un libro llamado \"Los Inicios de la Amenaza de los No Muertos\". Fue utilizado por los ahora corruptos paladines allí para entrenarlos en la lucha contra mi especie.$B$BTráemelo.",
    ["O"] = "Encuentra Los Inicios de la Amenaza No Muerta y llévaselo a Parqual Fintallas en Entrañas.",
    ["T"] = "Prueba de Sabiduría",
  },
  [1161] = "_",
  [1162] = "_",
  [1163] = "_",
  [1164] = {
    ["D"] = "¡La ramera! ¡La cerda! Kenata todavía vive, su familia sana y próspera mientras yo sufro. ¡Renegada, de hecho!$B$BMi familia se ha ido, tomada por la plaga. Nuestra finca también confiscada, saqueada y quemada durante la guerra. Y después de todo eso, ella y sus miserables hijos tuvieron la audacia de robar las únicas pertenencias preciosas que me quedaban.$B$B¡Ya no me importan las cosas que robaron! Lo que quiero ahora son sus cabezas.$B$B¡Mátalos por mí! Ve a la Granja Dabyrie en Arathi, al noreste del Refugio de la Zaga.",
    ["O"] = "Lleva las Cabezas de Kenata, Fardel y Marcel Dabyrie a Genavie Callow en Entrañas.",
    ["T"] = "Robar a los Ladrones",
  },
  [1166] = {
    ["D"] = "¡Arrodíllate ante el Señor Supremo Mok\'Morokk!$B$B¡Rexxar me hizo líder poderoso del Clan Quebrantarrocas!$B$BYo buen líder. Yo mantener clan seguro.$B$BGrandes dragones venir y hacer fuego en viejo hogar. Yo ogro inteligente. Encontrar nuevo hogar para clan aquí.$B$BDejar viejo hogar tan rápido, yo olvidar cosas. Pequeño $r, ir a buscar mis cosas. Ir al sur y encontrar mi grog sabroso, mi rapé y mi cofre fuerte. Yo gustar de ti después de hacer eso. Mirar en la Guarida de la Llama y las Ruinas Quebrantarrocas... estar allí, yo seguro.",
    ["O"] = "El Señor Supremo Mok\'Morokk en la Aldea Murohelecho quiere que recuperes su grog, rapé y cofre fuerte en los Marjal Revolcafango.",
    ["T"] = "La Preocupación del Señor de la Guerra Mok\'Morokk",
  },
  [1167] = {
    ["D"] = "Antes de enfrentarte a Athrikus, probablemente sería prudente que consultaras con Balthule. No tengo dudas de que ha estado vigilante en su vigilancia de la Torre, y puede tener información sobre los desarrollos recientes.",
    ["O"] = "Habla con Balthule Golpe Umbrío cerca de la Torre de Althalaxx.",
    ["T"] = "La Torre de Althalaxx",
  },
  [1168] = {
    ["D"] = "Mok\'Morokk fue nombrado Señor Supremo por Rexxar. Le importa más el poder que los ogros.$b$bLos dragones negros mataron a muchos ogros. Mataron a la esposa de Tharg. Mok\'Morokk nos dijo que corriéramos. Nos hizo venir aquí.$b$bLos ogros deberían luchar. Los ogros no son buenos corriendo. Mok\'Morokk solo se preocupa por Mok\'Morokk.$b$bYo llamo hogar a las Ruinas de los Quebrantarrocas. Me gusta en el sur. Este lugar no es hogar.$b$bAyuda a los ogros a luchar contra el ejército del dragón negro. Quiero venganza. Quiero volver a casa.",
    ["O"] = "Tharg en la Aldea Pantoque quiere que mates a 15 Exploradores Crin de Fuego, 10 Colas de Ceniza Crin de Fuego y 5 Guardias Crin de Fuego.",
    ["T"] = "El Ejército del Dragón Negro",
  },
  [1169] = {
    ["D"] = "Mientras ese cobarde tonto, Mok\'Morokk, se deleita en el poder y Tharg lucha contra los demonios del dolor y la venganza y compite por el liderazgo del clan, parezco ser el único preocupado por identificar la fuente de la agresión en nuestro antiguo hogar.$b$b¿Por qué esa mirada de perplejidad, $n? ¿Esperabas que hablara como un rufián grosero solo porque soy un ogro?$b$bAhora, volvamos al asunto, tráeme los corazones y lenguas de los crías y retoños. Rastrearé la raíz de este mal...",
    ["O"] = "Draz\'Zilb en la Aldea Murohele quiere que le traigas 15 Lenguas Abrasadoras y 15 Corazones Abrasadores.",
    ["T"] = "Identificando a la Progenie",
  },
  [1170] = {
    ["D"] = "La Aldea Machacamiedo fue invadida por la prole de Onyxia. Pero, ¿por qué la hija del señor de los dragones negros, Alamuerte, descendería sobre nuestras tierras?$b$bEsto es muy preocupante. Seguramente, Onyxia fue llevada aquí con un propósito más allá de sitiar a una pequeña banda de ogros.$b$b¡Notifica a Mok\'Morokk de inmediato! Se debe tomar acción.",
    ["O"] = "Habla con el Señor de la Guerra Mok\'Morokk en la Aldea Pantoque.",
    ["T"] = "La Progenie de Onyxia",
  },
  [1171] = {
    ["D"] = "Déjame en paz ahora. Ve y dile a Draz\'Zilb que nos quedamos aquí. No hay dragón negro aquí.",
    ["O"] = "Habla con Draz\'Zilb en Poblado Murohelecho.",
    ["T"] = "La Progenie de Onyxia",
  },
  [1172] = {
    ["D"] = "La cría de Onyxia ha sido dispersada por el Pantano del Dragón. Es imperativo que no se permita que más crías eclosionen.$b$bDate prisa hacia el Pantano del Dragón en el sur de Marjal Revolcafango. Seguramente, ella ha hecho su guarida allí. Rastrea los huevos del malvado dragón y destrúyelos.$b$bNunca recuperaremos la Aldea Quebrantarrocas si el área circundante sigue siendo un criadero.$b$bEn cuanto a Mok\'Morokk... tengo otros planes para el imbécil.",
    ["O"] = "Draz\'Zilb en la Aldea Murohele quiere que destruyas 5 Huevos de Onyxia.",
    ["T"] = "La Progenie de Onyxia",
  },
  [1173] = {
    ["D"] = "¿Te atreves a desafiar al Señor de la Guerra Mok\'Morokk?$B$B¡Ja!$B$B¡Yo aplastaré a pequeño $r!$B$BDime cuando estés listo para una buena paliza.",
    ["O"] = "Derrota a Mok\'Morokk e informa las noticias a Draz\'Zilb en Poblado Murohelecho.",
    ["T"] = "Desafiar al Señor de la Guerra Mok\'Morokk",
  },
  [1174] = "_",
  [1175] = {
    ["D"] = "Después del gran choque con el corredor Trueno Rojo de los goblins y un basilisco, Kronkrider me contrató para cuidar la pista. No sé de qué se alimentan todos los animales en las llanuras para crecer tanto, pero debemos tener cuidado de reducir su número para que no deambulen por la pista durante una carrera.$B$BTe diré algo. Si quieres salir y ganar un poco de dinero, ve y elimina a los basiliscos del área.",
    ["O"] = "Mata 10 Basilisco Salpiedra, 10 Cristaloides Salpiedra y 6 Miradores Salpiedra para el Maestro de Pistas Zherin en el Desierto Brillante.",
    ["T"] = "Un Bache en el Camino",
  },
  [1176] = {
    ["D"] = "Cuando se trata de carreras, ¡cada detalle cuenta! Ya sea un pequeño ajuste en la salida de potencia, un ligero aflojamiento en las bobinas de tensión, o aligerar el peso del marco, tienes que tomar todo en cuenta.$B$BUsamos huesos de buitre para muchos de los ajustes estructurales menores que hacemos a nuestros corredores, que ofrecen el equilibrio perfecto entre fuerza y peso. Incluso mejor, es fácil para nosotros conseguir más, ya que las salinas están llenas de buitres.$B$BTráeme algunos y te daré un buen precio por ellos.",
    ["O"] = "Consigue 10 Huesos de Buitre Huecos para Pozzik en el Desierto Brillante.",
    ["T"] = "Aligerar la Carga",
  },
  [1177] = {
    ["D"] = "Mud ogro grande.$B$BMu hambriento.$B$BVe a golpear peces. Trae comida a Mud.$B$BTú alimentas a Mud o Mud te come a ti.",
    ["O"] = "Machacieno Piesucio en el norte de Marjal Revolcafango quiere 12 Cabezas de Aletapantano.",
    ["T"] = "¡Hambre!",
  },
  [1178] = {
    ["D"] = "Nuestro equipo de carreras es el orgullo de los goblins en todas partes. Te sorprenderías de algunas de las personas que contribuyen de una forma u otra a nuestro equipo.$b$b¿Conoces a Gazlowe? Él rastrea piezas de todo el mundo para nosotros. ¡Incluso vino a trabajar en uno de nuestros vehículos una vez!$b$bHace unas semanas, dijo que nos enviaría un nuevo regulador de combustible prototipo, pero aún no ha llegado. Por lo general, es muy puntual... ¿Tal vez no te importaría ir a Ratchet para preguntarle sobre eso? No sería tan insistente, pero los gnomos nos están ganando terreno.",
    ["O"] = "Habla con Gazlowe en Ratchet.",
    ["T"] = "Patrocinio Goblin",
  },
  [1179] = {
    ["D"] = "¿Alguna vez has oído hablar de los Llanos Vibrantes? Es una extensión de salinas muy lejos en Kalimdor, justo al sur del cañón de las Mil Agujas. ¡Es un gran lugar para carreras porque, bueno, es tan plano!$B$BUn par de hermanos gnomos están allí, amigos míos, probando su nuevo coche cohete, y por lo que escuché, ¡es rápido! Tan rápido que sus pilotos siguen estrellándose. Dándose conmociones cerebrales.$B$BAsí que me pidieron algunos cascos de motor de asedio, pero tengo deberes aquí. ¡No puedo hacer el viaje!$B$B¿Puedes tú?",
    ["O"] = "Lleva la caja de cascos de choque a Wizzle Brassbolts en el Desierto de Sal.",
    ["T"] = "Los Hermanos Pernolatón",
  },
  [1180] = {
    ["D"] = "Volviendo a lo que viniste aquí. Sé que le prometí a Pozzik que le entregaría un nuevo regulador de combustible, pero todavía no lo he recibido. Revilgaz, allá en Bahía del Botín, dijo que lo enviaría, pero podría ser que se haya perdido en el camino.$b$bSi eso es cierto, no hay mucho que podamos hacer al respecto. Al menos podemos averiguar si fue despachado desde Bahía del Botín.$b$bConsulta con el Capataz del Muelle Lozgil allá, él debería poder revisar los registros de carga.",
    ["O"] = "Habla con el Maestro de Muelles Lozgil en Bahía del Botín.",
    ["T"] = "Patrocinio Goblin",
  },
  [1181] = {
    ["D"] = "Me temo que no puedo ser de mucha más ayuda para ti, $n. Si te gustaría seguir investigando, te sugiero que vayas a hablar con el Barón mismo. Si hay alguien que conoce los negocios de Revilgaz, es Revilgaz.$B$BLo encontrarás en la taberna al otro lado del puerto, en el piso superior.",
    ["O"] = "Habla con el Barón Revilgaz en Bahía del Botín.",
    ["T"] = "Patrocinio Goblin",
  },
  [1182] = {
    ["D"] = "Mira, las trituradoras de Venture Co. en el Lago Nazferiti (en el norte de Tuercespina) han sido equipadas con un nuevo modelo de regulador de combustible. Pensé que Pozzik podría aprovecharlo si obtuviera algunos detalles. Desafortunadamente, Cozzle, el capataz de allí, parece ser un poco más astuto de lo que le había dado crédito.$b$bParece que tú podrías tener más suerte encontrando los planos. Sé que los guarda en su casa, pero casi seguro que están bajo llave.",
    ["O"] = "Lleva los Planos del Regulador de Combustible al Barón Revilgaz en Bahía del Botín.",
    ["T"] = "Patrocinio Goblin",
  },
  [1183] = {
    ["D"] = "Deberías estar listo para partir. Dale mis saludos a Pozzik y dile que si alguna vez necesita algo, que no dude en preguntar. ¡Estaré esperando las noticias de que rompa el récord de la pista pronto!",
    ["O"] = "Entrega los Planos del Regulador de Combustible a Pozzik en el Llano Salobre.",
    ["T"] = "Patrocinio Goblin",
  },
  [1184] = {
    ["D"] = "Regresa con Belgrom en Orgrimmar y dile que Moktar está haciendo su voluntad en las Mil Agujas. Dile también que mis estudios progresan, y espero haber aprendido más pronto. Eso debería complacerlo enormemente.",
    ["O"] = "Regresa con Belgrom Rocamartillo en Orgrimmar.",
    ["T"] = "Partes del Enjambre",
  },
  [1185] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Debajo del Caparazón...",
  },
  [1186] = {
    ["D"] = "Voy a estar ocupado trabajando en la integración de este nuevo regulador de combustible en nuestros diseños de corredores, pero si te gustaría ayudar más al equipo, habla con Razzeric. Es nuestro piloto, bueno, nuestro decimoctavo piloto, para ser exactos, y le gusta agregar sus propias pequeñas modificaciones a los corredores.$b$bEstá un poco loco, así que trata de no actuar demasiado sorprendido.",
    ["O"] = "Habla con Razzeric en el Desierto Brillante.",
    ["T"] = "El Decimoctavo Piloto",
  },
  [1187] = {
    ["D"] = "Pozzik es un gran mecánico, lo sé, pero tiene miedo de simplemente cargar el poder de fuego y agarrarse para el viaje. Yo no, sin embargo.$b$bDespués de que Pozzik termina con un corredor, tomo un poco de tiempo para poner mis propias modificaciones: ya sabes, aflojar los controles, quitar partes del marco y agregar más jugo.$b$bPedí un reforzador de seforio a Shreev en el Gizmorium en Gadgetzan, pero aparentemente el zepelín en el que lo enviaban se estrelló en Marjal Revolcafango! ¡Lo necesito, $n!",
    ["O"] = "Recupera el Propulsor de Seforio para Razzeric en el Desierto de Sal.",
    ["T"] = "El Ajuste de Razzeric",
  },
  [1188] = {
    ["D"] = "Pero espera, ¿qué es esto? Shreev dejó el seguro puesto. Eso limitará la cantidad de energía que puedo extraer del potenciador en cualquier momento. ¡Le dije que lo quería desactivado! Esto no es bueno.$b$bTienes que llevar esto al Gizmorium de Gadgetzan en el Desierto de Tanaris y hacer que Shreev lo quite.",
    ["O"] = "Lleva el Potenciador de Seaforium a Shreev en Gadgetzan.",
    ["T"] = "Seguridad Primero",
  },
  [1189] = {
    ["D"] = "Ahí, hice lo que Razzeric quería. Solo dile que tenga cuidado cuando active esto. Si comete el más mínimo error, no avanzará, solo será una mancha quemada en la pista.$b$bSería una pena, también, si eso sucediera. ¿Quién sabe si nuestro decimonoveno conductor sería bueno?",
    ["O"] = "Devuelve el Propulsor de Seforio Modificado a Razzeric en el Desierto de Sal.",
    ["T"] = "Seguridad Primero",
  },
  [1190] = {
    ["D"] = "Los hermanos Brassbolts han descubierto un nuevo material de construcción, esa es la única explicación que tiene sentido. Sus autos han estado funcionando más rápido que antes, pero sus diseños, y todos los demás datos que hemos recopilado de sus carreras, son idénticos.$B$BLo que sí sabemos es que Rizzle Brassbolts se ha centrado casi constantemente en los planes de este nuevo descubrimiento.$B$BZamek puede crear una distracción para ti, para que puedas robar esos planes de ellos.",
    ["O"] = "Haz que Zamek cree una distracción, luego roba los planes de Rizzle para Pozzik en el Deslinde Brillante.",
    ["T"] = "Manteniendo el Ritmo",
  },
  [1191] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Distracción de Zamek",
  },
  [1192] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Mineral de Indurio",
  },
  [1193] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Una Trampa Rota",
  },
  [1194] = {
    ["D"] = "Los esquemas de Rizzle describen el proceso de refinación del mineral de indurio. De sus garabatos, es evidente que el mineral de indurio se está obteniendo del sitio de excavación enano de Uldaman en las Tierras Inhóspitas.$b$bMuchas de las otras notas se relacionan con las propiedades resistentes al calor.$b$bUsado como pisapapeles para los planos hay un trozo de lo que parece ser mineral de indurio sin refinar.",
    ["O"] = "Lleva la Muestra de Mineral de Indurium a Pozzik en el Desierto de Sal.",
    ["T"] = "Los Esquemas de Rizzle",
  },
  [1195] = {
    ["D"] = "Los centauros Galak de Las Mil Agujas custodian un artefacto desde el comienzo de sus tiempos.%b%bNos gustaría arrebatárselo pero para ello necesitamos una ampolla de agua de una de las pozas de la luna de los elfos nocturnos.$b$bPara conseguir el agua, primero tendrás que hacerte con una de las ampollas que portan los dríades cerca del Refugio de la Algaba en el corazón del Bosque de Vallefresno. Hay una poza de la luna cerca de la orilla occidental del Río Falfarren, al sur de la carretera principal, donde podrás llenar la ampolla de agua.",
    ["O"] = "Devuelve un Frasco Grabado Lleno a Zangen Pezuñapétrea en Cima del Trueno.",
    ["T"] = "La Llama Sagrada",
  },
  [1196] = {
    ["D"] = "Lleva el frasco lleno a Rau Cliffrunner en el Puesto de Viento Libre en el sureste de las Mil Agujas. Puedes llegar a las Mil Agujas tomando el Gran Elevador en el borde sur de los Baldíos.$b$bNo me sorprendería si puedes ayudarnos más.",
    ["O"] = "Entrega la Fiola Grabada Llena a Rau Correcostas en el Poblado Viento Libre.",
    ["T"] = "La Llama Sagrada",
  },
  [1197] = {
    ["D"] = "Usando el agua que trajiste, podrás apagar temporalmente la llama que sostiene la reliquia de los centauros. No funcionará permanentemente, sin embargo, así que tendrás que ser rápido.$b$bEncontrarás el Peñasco Pezuña Hendida, el bastión de los centauros, al norte de aquí. La llama se guarda en el Bastión Pezuña Hendida, una parte fuertemente custodiada del peñasco.$b$bTen cuidado, sin embargo, los centauros Galak tienen grandes números aquí, y estarán bastante reacios a rendir algo tan querido para ellos.",
    ["O"] = "Recupera la Pezuña Hendida para Rau Correcuestas en el Puesto Viento Libre.",
    ["T"] = "La Llama Sagrada",
  },
  [1198] = {
    ["D"] = "Muchos rumores circulan estos días sobre la unión del Martillo Crepuscular y los naga en las Profundidades de Brazanegra en las costas de Argénteos.$b$bMi papel aquí con el Alba Argenta es recopilar tanta información como pueda, para que sepamos cuándo y dónde se necesita ayuda.$b$bHan pasado semanas desde que envié a un explorador a verificar la actividad en las Profundidades de Brazanegra. El Guardia Argenta Thaelrid aún no ha informado.$b$bMis otros exploradores están ocupados con asuntos urgentes.$b$bBusca y ayuda a Thaelrid por mí, $n.",
    ["O"] = "Busca al Guardia Argenta Thaelrid en las Cavernas de Brazanegra.",
    ["T"] = "En Busca de Thaelrid",
  },
  [1199] = {
    ["D"] = "Empoderados por los antiguos ritos de los caóticos Dioses Antiguos, los miembros del Martillo Crepuscular se esfuerzan por traer de vuelta a sus oscuros patrones y esparcir la muerte y el terror por el mundo.$b$bCreemos que el Martillo Crepuscular se ha aliado con los naga y se están reuniendo en las Profundidades de Brazanegra en la costa de Vallefresno.$b$bCualquier plan malvado que se esté gestando en las profundidades del antiguo templo debe ser frustrado.$b$bDeclara la guerra al Martillo y trae pruebas de tus hazañas. Tus esfuerzos serán generosamente recompensados.",
    ["O"] = "Lleva 10 Colgantes Crepusculares al Guardia Argenta Manados en Darnassus.",
    ["T"] = "Cae el Crepúsculo",
  },
  [1200] = {
    ["D"] = "La fuerza me ha abandonado. ¡Necesito tu ayuda!$b$bHace mucho tiempo, este lugar era un gran templo de Elune. Pero la desgracia lo llevó a la ruina cuando la corrupción de un Dios Antiguo se filtró desde las profundidades de la tierra y contaminó el Sagrado Pozo de la Luna.$b$bAku\'Mai, sirviente del Dios Antiguo, surgió de las aguas.$b$bLos cultistas del Martillo Crepuscular se han aliado con los naga para ocupar estos terrenos. Los cultistas, liderados por Kelris, sacrifican inocentes a Aku\'Mai para obtener poder.$b$bMata a Kelris y tráeme su cabeza a Selgorm en Darnassus, $N, por favor.",
    ["O"] = "Lleva la cabeza del Señor Crepuscular Kelris a Dawnwatcher Selgorm en Darnassus.",
    ["T"] = "Villanía de Foso Negro",
  },
  [1201] = {
    ["D"] = "La Horda y la Alianza mantienen una tregua inestable. Escaramuzas y acciones encubiertas son llevadas a cabo por un bando contra el otro, y es nuestro deber asegurarnos de que la Horda esté en la cima.$B$BAhora es la oportunidad de hacer precisamente eso.$B$BInfiltradores de la Alianza han tomado posiciones al este de la Aldea Murohelecho. Espían la aldea y reportan nuestros movimientos a Theramore, lo cual no podemos permitir.$B$BCaza a los infiltradores y mátalos. Envía un mensaje claro a sus superiores de que no toleraremos la presencia de sus espías.",
    ["O"] = "Mata 9 Infiltrados de Theramore, luego regresa con Nazeer Sangrepúa en el Poblado Murohelecho.",
    ["T"] = "Espías de Theramore",
  },
  [1202] = {
    ["D"] = "$N, dos de nuestros espías fueron enviados a robar documentos de un barco en los muelles de Theramore. Obtuvieron los papeles pero fueron descubiertos y se vieron obligados a arrojar los documentos al mar.$B$BUno de los espías escapó más tarde, pero no pudo recuperar el premio. Me informó de su fracaso y ahora te encomiendo la tarea.$B$BLos documentos están en un baúl, en las aguas debajo de los muelles de Theramore. Encuentra esos documentos, $N, y tráemelos.",
    ["O"] = "Lleva los Documentos del Capitán a Nazeer Bloodpike en la Aldea Murohelecho.",
    ["T"] = "Los Muelles de Theramore",
  },
  [1203] = {
    ["D"] = "Tengo que cortar. No puedo decirte qué voy a cortar, pero... necesito una espada. Una espada en particular...$B$BAlgunas personas la llaman una espada ancha de acero lunar. Yo la llamo una espada de golpe.$B$B ¿Me ayudas? Tal vez la encuentres en el pueblo, tal vez hables con un herrero y él la hará especial.$B$BPero no te duermas en esto. Lo que voy a cortar no se mantendrá para siempre...",
    ["O"] = "Lleva una Espada Ancha de Acero Lunar a Jarl en Los Baldíos de Polvo.",
    ["T"] = "Jarl Necesita una Hoja",
  },
  [1204] = {
    ["D"] = "Estoy trabajando en un plato que llamo \"Sopa de Mudrock y bichos\". Todavía necesito los ingredientes principales, y... bueno, ¡no creo que sobreviva mucho tiempo recorriendo Marjal Revolcafango!$B$BAsí que, $N, ¿podrías cazar algo por mí? Primero, necesito las lenguas de las tortugas Mudrock. Puedes encontrar Mudrocks a lo largo de la costa, principalmente alrededor de la bahía.$B$B¡Pero no quiero cualquier lengua! Escuché que algunas Mudrocks tienen lenguas bifurcadas...$B$BDicen que las lenguas bifurcadas tienen un sabor distintivo, y quiero ese sabor en mi plato...",
    ["O"] = "Lleva 8 Lenguas Bifurcadas de Fangorroca a Morgan Stern en Theramore.",
    ["T"] = "Sopa de Fangorroca y Bichos",
  },
  [1205] = {
    ["D"] = "Deadmire es un antiguo crocolisco en los Pantanos de Polvareda. Y es hora de que muera.$B$BSus viejos huesos crujen mientras arrastra su enorme cuerpo a través del pantano, y aunque todavía se mueve con la fuerza y la velocidad de la juventud, su cuerpo envejecido tortura al gran espíritu dentro de él, un espíritu cuya llama no vacilará.$B$BPero ahora vive en un dolor constante y enloquecedor. Debes terminar con la vida de esta noble criatura, $N. Debes llevar a Deadmire a la paz.",
    ["O"] = "Lleva el Diente de Mortalga a Melor en Cima del Trueno.",
    ["T"] = "Muertopantano",
  },
  [1206] = {
    ["D"] = "El jugo de ojo es un buen jugo para beber. Lástima que la mayoría de las criaturas solo tengan dos ojos.$B$B¡Pero las arañas tienen muchos ojos! Muchos y muchos. Y el jugo de ojo de los Oscurbruma es delicioso. ¡Suave y salado! Ojos de araña sin reventar... ya los estoy saboreando.$B$BLas arañas Oscurbruma están al oeste de aquí, en la Caverna Oscurbruma. ¿Te imaginas que las arañas recibieron su nombre por la cueva... o es al revés?",
    ["O"] = "Lleva 40 Ojos de Niebla Oscura sin Reventar a \"Ojo del Pantano\" Jarl en la Mansión Pantanosa.",
    ["T"] = "Jarl Necesita Ojos",
  },
  [1218] = {
    ["D"] = "Me gusta cocinar. Buena cocina de pantano. Piernas de rana verde fritas y cosas así.$B$BY para cocinar, necesito especias. Especias reconfortantes. Luego haré un estofado, lo estofaré, lo sorberé y te daré un poco a ti también.$B$B¿Te gusta el estofado?",
    ["O"] = "Lleva 3 Especias Relajantes a \"Ojo del Pantano\" Jarl en Los Baldíos.",
    ["T"] = "Especias Calmantes",
  },
  [1219] = {
    ["D"] = "Al tamizar la tierra suelta, se encuentra la mano cortada de un orco. Y en la mano hay un tubo tallado en hueso. Y en el tubo hay un pergamino manchado de tierra.$B$BEl pergamino está escrito en orco... y parece importante.",
    ["O"] = "Lleva el Informe del Espía a un Teniente de Theramore.",
    ["T"] = "El Informe del Orco",
  },
  [1220] = {
    ["D"] = "El Capitán Vimes debería ver el informe orco que encontraste, $N. Él conoce el idioma orco mejor que la mayoría. ¡Y debería saber que hay espías de la Horda cerca de Theramore!$B$BEl capitán está en los barracones, en el lado noreste de la Isla de Theramore.",
    ["O"] = "Lleva el Informe del Espía al Capitán Garran Vimes.",
    ["T"] = "Capitán Vimes",
  },
  [1221] = {
    ["D"] = "Los tubérculos de hoja azul son un manjar en todo el mundo. ¡Pero son raros, muy raros! El único lugar donde se encuentran es aquí en los Baldíos, en lo profundo de la tierra, en Horado Rajacieno.$B$B¡Y aún así, son imposibles de encontrar a menos que sepas exactamente dónde buscar! Por eso he entrenado a estos topos hociconariz para que los encuentren por mí. Tienen narices excelentes y pueden oler un tubérculo a cincuenta pasos de distancia.$B$BNo será fácil, pero si me consigues algunos tubérculos, te pagaré generosamente.",
    ["O"] = "Toma una Caja con Agujeros.$BToma un Bastón de Mando de Hocicono.$BToma y lee el Manual del Dueño de Hocicono.$B$BEn Zanja Cardizal, usa la Caja con Agujeros para invocar un Hocicono, y usa el Bastón de Mando en el hocicono para hacer que busque Tubérculos.$B$BTrae 6 Tubérculos Hoja Azul, el Bastón de Mando de Hocicono y la Caja con Agujeros a Mebok Mizzyrix en Ratchet.",
    ["T"] = "Los Tubérculos Hojazul",
  },
  [1222] = {
    ["D"] = "¡Genial, finalmente algo que no quiere comerme!$B$BNecesito ayuda para manejar las criaturas por aquí, y tú encajas en el molde, $gcompañero:compañera;.$B$BEl cocinero Morgan Stern está investigando una nueva receta, y me envió aquí para recolectar hojas de bogbean. Supongo que piensa que son sabrosas.$B$BDe todos modos, mientras buscaba una planta de bogbean... ¡me rodeé! ¿Puedes ayudarme a salir de aquí y cuidar mi espalda mientras busco el bogbean?",
    ["O"] = "Escolta a \"Apestoso\" Ignatz, luego habla con Morgan Stern en Theramore.",
    ["T"] = "El Escape de Stinky",
  },
  [1238] = {
    ["D"] = "Al tamizar la tierra suelta, se encuentra la mano cortada de un orco. Y en la mano hay un tubo tallado en hueso. Y en el tubo hay un pergamino manchado de tierra.$B$BEl pergamino es un informe hecho por un orco que pasó semanas espiando la ciudad de Theramore.$B$BDespués de escanear el pergamino, está claro que debe ser llevado al maestro de espías orco en el Poblado Murohelecho.",
    ["O"] = "Lleva el Informe del Espía a Nazeer Bloodpike en la Aldea Murohelecho.",
    ["T"] = "El Informe Perdido",
  },
  [1239] = {
    ["D"] = "Una excavación vigorosa revela la cabeza de un orco macho, cortada de manera desigual a la altura del cuello. Sus labios están congelados en un gruñido, y sus oscuros ojos miran con desafío.",
    ["O"] = "Lleva la Cabeza de Orco Desafiante a Nazeer Bloodpike en la Aldea Murohelecho.",
    ["T"] = "La Cabeza Cortada",
  },
  [1240] = {
    ["D"] = "¡Debemos saber los eventos que rodearon la muerte de Marg!  Y la única forma de hacerlo, $N, es a través de la magia de brujería de los trolls.$B$BLleva la cabeza de Marg a Kin\'weelay en Vega de Tuercespina, en el Campamento Grom\'gol.$B$BKin\'weelay es el brujo más poderoso de los trolls Lanzanegra.  Si alguien puede hablar con el espíritu de Marg Ojo Nocturno, es él.",
    ["O"] = "Lleva la Cabeza de Orco Desafiante a Kin\'weelay en el Campamento Base Grom\'gol en la Vega de Tuercespina.",
    ["T"] = "El Brujo Troll",
  },
  [1241] = {
    ["D"] = "Recientemente se envió un diplomático a Theramore para reunirse con Jaina Valiente. Ese diplomático nunca llegó.$B$BCreo que los Defias están involucrados en este complot, pero no estoy seguro de cómo. La desaparición del diplomático aún escapa a la atención pública, pero no puede permanecer así por mucho tiempo.$B$BLos agentes del rey ya están revisando la ciudad en busca de pistas, pero tengo mis propios contactos que me gustaría involucrar.$B$BEn el Valle de los Héroes hay un viejo amigo mío llamado Jorgen. Encuéntralo, dale esta nota y sigue sus instrucciones.",
    ["O"] = "Encuentra a Jorgen en la Ciudad de Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1242] = {
    ["D"] = "Llévale esta nota a Elling Trias. No le digas nada más. Solo sé educado y espera a que responda. Y no te preocupes, lo hará.$B$BSolo te lo voy a recalcar una vez más, $N: toma este asunto en serio y mantén la boca cerrada.$B$BEncontrarás a Trias en su tienda de queso, Quesos Trias, en el Distrito de Mercaderes. Buena suerte.",
    ["O"] = "Entrega la Nota Sellada de Jorgen a Elling Trias en Ciudad de Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1243] = {
    ["D"] = "Jorgen, viejo hijo de.... Así que así es como va a ser, ¿eh?$B$BMuy bien, puedo lidiar con eso. ¡Espero que encuentre el Vacío Abisal mucho más rápido!$B$BAquí está lo que va a pasar, $N. Comenzaré a usar mis contactos aquí, pero tú harás la mayor parte del trabajo para que ahorre algo de dinero.$B$BDirígete a Villa Oscura en Bosque del Ocaso y encuentra al Vigía Backus: generalmente patrulla el camino norte justo afuera de la ciudad. Solo dile que estás investigando cualquier actividad de los Defias por mí y él te ayudará con cualquier información que pueda.",
    ["O"] = "Dale la Nota Sellada al Vigía Backus en Bosque del Ocaso.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1244] = {
    ["D"] = "¿Actividad de los Defias? Bueno, siempre hay algo de actividad, incluso si se mantienen al margen, los consideramos una amenaza.$B$BPero ahora que lo pienso, hubo un avistamiento reciente que pareció extraño. Debe haber sido hace un par de semanas, pero algunos de sus agentes se reunieron alrededor de la Granja de Addle. Según el informe, supuse que algo grande había sucedido.$B$B¿Por qué no revisas allí primero y me traes cualquier cosa que encuentres?$B$BLa granja está al sur del camino de Poniente cuando entras en Bosque del Ocaso.",
    ["O"] = "Encuentra el Expediente Defias y llévaselo al Vigía Backus en Bosque del Ocaso.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1245] = {
    ["D"] = "¡Vaya, esto parece demasiado complejo para ser algo que solo te lleve por mal camino! Ni siquiera debería estar mirando estas cosas. Llévaselo a Trias, él podrá darle sentido. Y NO dejes que nadie se interponga en tu camino. Esto es más grande que yo. Incluso podría ser más grande que él, pero ya veremos.$B$BY hazme un favor, $N, no le digas a nadie que te ayudé. Si mis superiores descubren que sabía algo de esto y no se lo conté, probablemente me colgarían.",
    ["O"] = "Lleva el Expediente Defias a Elling Trias en Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1246] = {
    ["D"] = "Sí, ahora que lo pienso... Dashel... Dashel algo... ¿Cómo se llamaba? ¡PUÑO! Dashel Puño de Piedra. Lo llaman Puño en algunos círculos, probablemente porque le gusta meterse en peleas.$B$BPuedes encontrarlo en el Casco Antiguo, justo en el centro. Ve a hablar con él. Si es necesario, usa una espada o algo pesado y contundente para persuadirlo de que hable. Y no lo dudes. Si está metido en esto tan profundamente como sugieren estos papeles, no te dará información voluntariamente.",
    ["O"] = "Habla con Dashel Puño de Piedra en Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1247] = {
    ["D"] = "Así que sí... um... podría saber algo sobre esa reunión en la Granja de Addle. Ocurrió hace unas semanas, creo. Realmente no tuve ninguna participación en eso. Mi parte terminó cuando el plan A falló... malditos aventureros.$B$BEl tipo que tenemos dentro para el plan B vino de Menethil, un tipo llamado Slim. Eso es todo lo que sé sobre su plan de respaldo.",
    ["O"] = "Habla con Elling Trias en Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1248] = {
    ["D"] = "Un contacto mío se retiró en Los Humedales, en la ciudad portuaria de Menethil. Debería estar dispuesto a ayudarte solo con tu palabra si le dices que trabajas para mí. Su nombre es Mikhail. Mencionó convertirse en bartender antes de dejar Ventormenta hace unos años.$B$B¡Habla con él, encuentra a este Slim y averigua si hay algo más que saber sobre este diplomático secuestrado! Necesitamos saber dónde lo han llevado, o al menos quién es. El documento no menciona nada sobre sus planes después de capturarlo.",
    ["O"] = "Habla con Mikhail en los Humedales.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1249] = {
    ["D"] = "¿Dices que Elling te envió aquí? Bueno, entonces la hospitalidad que expresé ahora es diez veces mayor. Cualquier cosa que necesites, solo pregunta. Haré todo lo posible para ayudarte.$B$BHmm, ¿Slim, dices? No estoy seguro de que el nombre me suene familiar, pero estaré atento a él.$B$BNo mires ahora, $N, pero el caballero cerca de la salida está mirando hacia aquí. Está escuchando nuestra conversación un poco demasiado atentamente, ¿entiendes? Si fuera tú, haría lo posible por someterlo antes de que intente escapar por la puerta.",
    ["O"] = "Somete a Tapoke Jahn antes de que escape, y luego regresa con Mikhail en Menethil.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1250] = {
    ["D"] = "Los Defias no me pagaron lo suficiente como para morir por su causa. Se llevaron al rey. Sí, al rey de Ventormenta, no a un simple diplomático. Estaba de camino a Theramore para reunirse con Jaina Proudmoore, al parecer era un gran asunto y un gran secreto. Pero no son solo los Defias los involucrados. Están trabajando con alguien. Creo que sabían que esto era más grande que ellos.$B$BMi parte era solo conseguir que su hombre subiera al barco del rey mientras estaba en el puerto. El nombre del tipo era Hendel. Eso es todo lo que sé. ¡Lo juro!",
    ["O"] = "Habla con Mikhail en los Humedales.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1251] = {
    ["D"] = "Un escudo de hierro, ennegrecido por el fuego que arrasó la posada, está fijado a la chimenea derruida, una de las pocas partes restantes de la estructura que una vez fue la Posada Reposo Umbrío.$B$BEl escudo puede ser removido del ladrillo de la chimenea.",
    ["O"] = "Lleva el Escudo de Hierro Ennegrecido a Krog en la Aldea Murohelecho.",
    ["T"] = "El Escudo Negro",
  },
  [1252] = {
    ["D"] = "Un destello de luz en el suelo llama tu atención debajo de los escombros. Al quitar los escombros y la ceniza incrustada en el objeto, se revela el emblema de un ancla dorada sobre esmalte blanco en la superficie: el signo de Theramore.$B$BDebajo del ancla está grabado el nombre \"Teniente Paval Reethe\".",
    ["O"] = "Lleva la Insignia de Reethe al Capitán Garran Vimes en Theramore.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1253] = {
    ["D"] = "Un escudo de hierro, ennegrecido por el fuego que arrasó la posada, está fijado a la chimenea derruida, una de las pocas partes restantes de la estructura que una vez fue la Posada Reposo Umbrío.$B$BEl escudo puede ser removido del ladrillo de la chimenea.",
    ["O"] = "Lleva el Escudo de Hierro Ennegrecido al Capitán Garran Vimes en Theramore.",
    ["T"] = "El Escudo Negro",
  },
  [1258] = {
    ["D"] = "Otro componente de mi nueva receta es la pata con caparazón de un cangrejo gigante. Y los cangrejos que tienen en Marjal Revolcafango, los llaman Caparazones de Fango, ¡simplemente no sirven! Me temo que los únicos cangrejos adecuados viven lejos, en el Pantano de las Penas.$B$BEl caparazón de la pata del cangrejo es un elemento clave en la presentación del plato, así que necesito patas impecables, sin cicatrices. Un cangrejo adulto podría tener solo una o dos patas así, y necesito muchas para investigar mi plato.$B$BEntonces, $N... ¿estás dispuesto a viajar?",
    ["O"] = "Lleva 12 Patas de Rondador Inmaculadas a Morgan Stern en Theramore.",
    ["T"] = "... y Bichos",
  },
  [1259] = {
    ["D"] = "No reconozco el nombre del Teniente Paval Reethe. Tu mejor opción sería consultar con el Ayudante Tesoran. Él lleva un registro de todos los archivos de la guardia de Theramore, desde el personal hasta su equipo.$B$BÉl debería poder decirte quién es este Reethe con una rápida revisión de sus libros.",
    ["O"] = "Infórmate más sobre Reethe con el Adjunto Tesoran en Theramore.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1260] = {
    ["D"] = "Estoy desarrollando un nuevo menú para el restaurante. Estará lleno de platos rurales con un tema de \"pantano\". ¡Creo que mi clientela en el Recluse Azul los amará!$B$BEnvié a mi sobrino Morgan a Theramore, una ciudad al borde del Pantano de las Mareas. Está allí recopilando recetas de los lugareños, y debo saber qué ha encontrado hasta ahora.$B$BEl viaje a Theramore es largo. Demasiado largo para mí. Pero apuesto a que un joven $c como tú saltaría a la oportunidad de viajar, ¿verdad?",
    ["O"] = "Habla con Morgan Stern en Theramore.",
    ["T"] = "Morgan Stern",
  },
  [1261] = {
    ["D"] = "¡Maldito caparazón de fango! ¡No puedes matar a Marg! Ah, pero estoy muerto. Así que debe haberme matado...$B$B¡Pero mi misión! ¡Mi misión! Tú, $N, debes completarla.$B$BMientras estaba cerca de Theramore, vi un grupo de soldados de la Alianza acampados en la playa. Y un soldado llevaba un colgante... ¡un colgante lleno de más gemas de lo que cualquier soldado podría permitirse!$B$BEsa noche me colé en su campamento y robé el colgante, pero en mi regreso a la base un hombre-cangrejo de caparazón de fango me mató.$B$B$N, ¡debes encontrar ese caparazón de fango y recuperar el colgante!",
    ["O"] = "Lleva el Colgante Enjoyado a Nazeer en la Aldea Murohelecho.",
    ["T"] = "Marg Habla",
  },
  [1262] = {
    ["D"] = "Es extraño que el colgante que Marg robó estuviera en manos de soldados comunes. ¡Quizás su dueño estaba disfrazado, o quizás los soldados mismos lo robaron!$B$BSea cual sea la verdad, este es un colgante de noble y debemos descubrir su verdadero dueño si queremos desentrañar su misterio.$B$BLleva el colgante a Zor Lonetree en Orgrimmar. Él está bien versado en la política de la Alianza y sabrá si este colgante tiene una historia entre los nobles de Ventormenta.",
    ["O"] = "Lleva el Colgante Enjoyado a Zor Lonetree en Orgrimmar.",
    ["T"] = "Informar a Zor",
  },
  [1263] = "_",
  [1264] = {
    ["D"] = "Al comienzo de cada mes, los Marines de Kul Tiras reciben su paga y suelen dejar Theramore para ir a otro lugar. Muchos de ellos vienen aquí a buscar mujeres, a emborracharse y simplemente a pasarlo bien. Uno de esos grupos lo considero habitual aquí en la taberna, y creo que este Hendel está entre ellos.$B$BEl Comandante Samaul en Theramore podría ayudarte a encontrarlo si Hendel todavía está estacionado allí. Es completamente leal a Valiente, pero si mencionas el nombre de Elling, debería ayudarte... discretamente.",
    ["O"] = "Encuentra al Comandante Samaul en los Pantanos de Viennegato.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1265] = {
    ["D"] = "¿Hendel? Sí, conozco al hombre. ¿Pero por qué necesitarías a uno de mis soldados?$B$B¿Elling te envió a buscarlo? Hmm, no me gusta cómo suena eso. Elling no te habría enviado a mí primero si tu asunto no fuera problemático. Entiendo si no puedes decirme de qué se trata, pero ten en cuenta esto: tengo el deber de proteger estas tierras. Si vas a causar disturbios, te sugiero que me informes de lo que puedas.$B$BCreo que Hendel estaba estacionado en la primera torre fuera de Theramore. Ten cuidado.",
    ["O"] = "Busca en el Puesto de Vigilancia cualquier señal del Soldado Hendel.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1266] = {
    ["D"] = "El soldado Hendel está al oeste de aquí. Actualmente está de guardia protegiendo a algunos de los infiltrados de Theramore mientras descansan. Han estado vigilando la base de la Horda, Poblado Murohelecho, y buscando cualquier otra cosa que pueda amenazar nuestras fronteras.$B$BNo deberías tener problemas para encontrarlo, solo busca las tiendas a lo largo del camino.$B$BTengo otro asunto que atender, pero me reuniré contigo pronto. Siempre puedes encontrarme en la torre de la Lady Valiente si tienes algún problema.",
    ["O"] = "Encuentra al Soldado Hendel en Marjal Revolcafango.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1267] = {
    ["D"] = "",
    ["O"] = "Habla con Jaina Valiente en los Pantanos de Veleno.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1268] = {
    ["D"] = "Filas de huellas de cascos se alejan del esqueleto aún humeante de la Posada Reposo Umbrío. Hay al menos algunas impresiones visibles, con más filas de huellas casi imperceptibles en el barro blando del pantano.",
    ["O"] = "Informa sobre las huellas sospechosas en la Posada Reposo Umbrío a Krog en la Aldea Murohelecho.",
    ["T"] = "Huellas de Pezuñas Sospechosas",
  },
  [1269] = {
    ["D"] = "Un destello de luz en el suelo llama tu atención debajo de los escombros. Al quitar los escombros y la ceniza incrustada en el objeto, se revela el emblema de un ancla dorada sobre esmalte blanco en la superficie: el signo de Theramore.$B$BDebajo del ancla está grabado el nombre \"Teniente Paval Reethe\".",
    ["O"] = "Lleva la Insignia de Reethe a Krog en Poblado Murohelecho.",
    ["T"] = "Teniente Paval Reethe",
  },
  [1270] = {
    ["D"] = "¡Genial, finalmente algo que no quiere comerme!!$B$BNecesito ayuda para manejar las criaturas por aquí, y tú encajas en el molde, $gcompañero:compañera;.$B$BUn goblin de Ratchet llamado Mebok Mizzyrix necesita algunas hojas de bogbean para una poción, o algo así... Honestamente no me importa por qué: su dinero era bueno y conozco este pantano, así que aquí estoy.$B$B¡Lástima que me rodeé antes de encontrar el bogbean! ¿Puedes ayudarme a salir de aquí y cuidar mi espalda mientras busco la planta de Mebok?",
    ["O"] = "Escolta a \"Apestoso\" Ignatz, luego habla con Mebok Mizzyrix en Ratchet.",
    ["T"] = "El Escape de Stinky",
  },
  [1271] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Festín en el Recluse Azul",
  },
  [1273] = {
    ["D"] = "Tomó mucho tiempo, pero encontré a Reethe. Se esconde bien para ser un humano.$b$bOgron está preocupado de que pueda estar loco después de tanto tiempo en el pantano. Ven conmigo para que obtengamos respuestas de él.",
    ["O"] = "Ve con Ogron a hablar con Reethe, luego regresa con Krog en la Aldea Murohelecho.",
    ["T"] = "Interrogando a Reethe",
  },
  [1274] = {
    ["D"] = "Disculpa, $gseñor:señora;. El Obispo DeLavey me pidió que me acercara a aventureros que pudieran ayudarlo con un asunto delicado.$B$BSi pudieras dirigirte discretamente al Castillo de Ventormenta y hablar con él a la mayor brevedad, estoy seguro de que apreciaría tu ayuda. Nuevamente, por favor, sé discreto. Es un asunto de cierta importancia.",
    ["O"] = "Encuentra al Obispo DeLavey en la Fortaleza de Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1275] = {
    ["D"] = "Los elfos de sangre tontos trabajan con magia demoníaca. ¿No han visto lo que sucedió con los naga y con los sátiros de Kalimdor?$b$bTemo que los elfos de sangre sufrirán una deformación similar. Azeroth no puede permitirse el nacimiento de otra raza vil de monstruos.$b$bDeseo estudiar los tallos cerebrales de los sátiros y los naga para poder deducir hacia qué destino se dirigen los elfos. En las Profundidades de Brazanegra, en la costa de Vallefresno, encontrarás tanto sátiros como naga.$b$bEs un viaje largo, $n.",
    ["O"] = "Gershala Susurro Nocturno en Auberdine quiere 8 Tallos Cerebrales Corruptos.",
    ["T"] = "Investigando la Corrupción",
  },
  [1276] = {
    ["D"] = "Ah, ¡ahora lo recuerdo! Mosarn es un respetado herrero en Cima del Trueno. Sabía que había escuchado su nombre en algún lugar antes.$B$BComo agradecimiento, el Anillo de la Tierra me otorgó un conjunto de armadura elaborada por Mosarn. Muy buen trabajo.$B$BTal vez él pueda decirte más sobre el escudo que forjó.$B$BNo es mucha información, pero ya que no obtuvimos nada útil de Reethe, es todo lo que tenemos.",
    ["O"] = "Muéstrale el Escudo de Hierro Ennegrecido a Mosarn en Cima del Trueno.",
    ["T"] = "El Escudo Negro",
  },
  [1277] = "_",
  [1278] = "_",
  [1279] = "_",
  [1280] = "_",
  [1281] = "_",
  [1282] = {
    ["D"] = "No puedo evitar sentir pena por el pobre Jim. No siempre fue así. Dirigía una posada en el pantano por un tiempo, un lugar maravilloso.$B$BMira, realmente no quiero hablar de eso. Habla con el Capitán Vimes si realmente tienes que hacerlo.",
    ["O"] = "Habla con el Capitán Garran Vimes en Theramore.",
    ["T"] = "Lo Llaman Jim Sonriente",
  },
  [1283] = "_",
  [1284] = {
    ["D"] = "Filas de huellas de cascos se alejan del esqueleto aún humeante de la Posada Reposo Umbrío. Hay al menos algunas impresiones visibles, con más filas de huellas casi imperceptibles en el barro blando del pantano.",
    ["O"] = "Informa sobre las huellas sospechosas en la Posada Reposo Umbrío al Capitán Garran Vimes en Theramore.",
    ["T"] = "Huellas de Pezuñas Sospechosas",
  },
  [1285] = {
    ["D"] = "Reddon... Redpath... ¡Ah, aquí está! Teniente Paval Reethe.$b$bSe unió a la guardia de Theramore junto con la mayoría de los reclutas que cruzaron el mar con la Lady Valiente. Se alistó con los marines de Kul Tiras a los dieciséis años. Servicio distinguido, promociones...$b$bAparece como desaparecido.",
    ["O"] = "Devuelve la información sobre Reethe al Capitán Garran Vimes en Theramore.",
    ["T"] = "Los Hombres de Daelin",
  },
  [1286] = {
    ["D"] = "Ha sido una lucha mantener una presencia en el pantano, pero gracias a la fuerza de nuestra determinación, hemos podido mantener varias torres de vigilancia defensivas en toda el área.$b$bLa última información y los informes de exploración que he recibido indican que un grupo de desertores se ha instalado en nuestra torre abandonada en Punta Perdida, al sureste de la posada.$b$bEl líder de los desertores es un hombre llamado Jacken. A ver si puedes obtener alguna información de él.$b$bTen cuidado, $n. Puede ser un hueso duro de roer.",
    ["O"] = "Consigue información sobre Reethe de Balos Jacken y los desertores en Marjal Revolcafango.",
    ["T"] = "Los Desertores",
  },
  [1287] = {
    ["D"] = "¿Reethe? ¿Ese cobarde?$b$bLo expulsamos del campamento porque insistía en atacar los suministros de los ogros en Poblado Murohelecho. No somos tantos en número como para enfrentarnos a esos brutos sin mente.$b$bCruzó la línea una vez demasiado, así que lo dejamos a su suerte.$b$bAhora, te he ayudado... Entonces, ¿por qué no nos ayudas y olvidas que te encontraste con nosotros?",
    ["O"] = "Habla con el Capitán Garran Vimes en Theramore sobre Reethe.",
    ["T"] = "Los Desertores",
  },
  [1288] = {
    ["D"] = "Has hecho un buen trabajo, $n, el mejor que he visto de un aficionado.$b$bLady Valiente pidió que la mantuviera informada sobre esta delicada situación, y no he hablado con ella desde hace algún tiempo.$b$bIba a entregarle mi informe más tarde, pero parece más apropiado que seas tú quien hable con ella al respecto. Probablemente puedas responder sus preguntas mejor que yo.",
    ["O"] = "Lleva el Informe de Vimes a Lady Jaina Valiente en Theramore.",
    ["T"] = "El Informe de Vimes",
  },
  [1289] = {
    ["D"] = "Bueno, juzgando por el informe del Capitán Vimes, has sido de gran ayuda para su investigación, y por eso te agradezco.$B$BPuedo estar más tranquilo sabiendo que el asunto está en manos tan capaces. Si pudieras, te pediría que regreses con el Capitán Vimes y lo ayudes a llegar al fondo de este misterio.",
    ["O"] = "Regresa con el Capitán Garran Vimes en Theramore.",
    ["T"] = "El Informe de Vimes",
  },
  [1293] = "_",
  [1294] = "_",
  [1295] = "_",
  [1296] = "_",
  [1297] = "_",
  [1299] = "_",
  [1300] = "_",
  [1301] = {
    ["D"] = "Antes de trabajar aquí en el Recluse Azul, fui entrenado por uno de los mejores chefs y posaderos que he conocido. Su nombre era James Hyal. Todo lo que tengo y todo lo que he logrado en la vida se lo debo a él.$B$BMe pregunto cómo estará. Su hermano Vincent y él fueron a trabajar en una taberna en el Puerto de Menethil, pero no he sabido de él recientemente.$B$BSi alguna vez pasas por allí, ¿podrías saludarlo por mí?",
    ["O"] = "Habla con Vincent Hyal en el Puerto de Menethil.",
    ["T"] = "James Hyal",
  },
  [1302] = {
    ["D"] = "Mi hermano James fue con Lady Valiente a través del mar. Lo último que supe es que los combates se habían calmado por allí, y James estaba estableciendo una posada en algún lugar del continente. Dondequiera que esté, estoy seguro de que está haciendo buenos negocios. Era un maldito buen cocinero.$B$BHace tiempo que no tengo noticias suyas, demasiado ocupado con su posada, supongo, pero el empleado en Theramore probablemente pueda indicarte dónde está.",
    ["O"] = "Habla con el Escribiente Lendry en Theramore.",
    ["T"] = "James Hyal",
  },
  [1318] = {
    ["D"] = "Ahora eres el rey, así que tal vez puedas recuperar el preciado artefacto Gordok de ese elfo espeluznante que se hace llamar Príncipe.  ¡El rey antes que tú falló, como todos los otros reyes desde que Tortheldrin, el elfo espeluznante, lo robó del antiguo rey Gordok!$B$BEl artefacto se llama el Guantelete del Poder Gordok.  Las historias antiguas dicen que solo el verdadero rey de los Gordok puede recuperarlo.  La historia antigua también dice que el elfo espeluznante todavía está por aquí: ¡encuéntralo y se convertirá en un poderoso guantelete que usarás para siempre!  ¡Sí, te lo mostraré!",
    ["O"] = "Encuentra el Guantelete del Poderío de Gordok y llévaselo al Capitán Kromcrush en La Masacre.$B$BSegún Kromcrush, la \"historia antigua\" dice que Tortheldrin, un elfo \"espeluznante\" que se hacía llamar príncipe, lo robó de uno de los reyes Gordok.",
    ["T"] = "Asuntos Inconclusos de Gordok",
  },
  [1319] = {
    ["D"] = "Lamento no poder ser de mucha ayuda, $n, pero puedo remitirte a alguien que sí puede. Caz Twosprocket, uno de nuestros mejores herreros, trabaja en la forja de la herrería.$b$bLleva el escudo a él y verás qué puede decirte.",
    ["O"] = "Muéstrale el Escudo de Hierro Ennegrecido a Caz Dosengranaje en Theramore.",
    ["T"] = "El Escudo Negro",
  },
  [1320] = {
    ["D"] = "Ah, casi me perdí esto... La correa de cuero en la parte posterior debe haberse quemado, pero parece que estaba hecha para ajustarse a una persona grande. Supongo que un tauren, en realidad.$B$BEso es todo lo que puedo decirte. Dile al Capitán Vimes que lamento no poder ser de más ayuda.",
    ["O"] = "Habla con el Capitán Garran Vimes en Theramore.",
    ["T"] = "El Escudo Negro",
  },
  [1321] = {
    ["D"] = "Hmm... No veo nada fuera de lo común con este escudo. Deberíamos hacer que Do\'gol, el herrero, lo revise y vea si puede decirnos más sobre él, pero no mantendría muchas esperanzas...",
    ["O"] = "Muéstrale el Escudo de Hierro Ennegrecido a Do\'gol en la Aldea Murohelecho.",
    ["T"] = "El Escudo Negro",
  },
  [1322] = {
    ["D"] = "Hmmm, hay una inscripción justo aquí en el borde del escudo, pero es difícil de leer debido a toda la quemadura.$B$BNecesito ácido para aclararlo. El veneno de las arañas Colmillo Oscuro en el pantano serviría. Consígueme algunos de sus sacos y podré limpiar esto.",
    ["O"] = "Consigue 6 Sacos de Veneno Ácido para Do\'gol en la Aldea Murohelecho.",
    ["T"] = "El Escudo Negro",
  },
  [1323] = {
    ["D"] = "Oh, creo que puse demasiado ácido en el escudo. Déjame frotar un poco...$b$bHm, es un poco difícil de leer, pero parece que el nombre es Mosarn. Nunca he oído hablar de él. Tal vez Krog lo conoce. Krog conoce a muchas personas.",
    ["O"] = "Habla con Krog en la Aldea Pantoque.",
    ["T"] = "El Escudo Negro",
  },
  [1324] = {
    ["D"] = "¡¿Qué?! ¡Vienes aquí acusándome de aliarme con los Defias, y crees que me quedaré aquí parado mientras me insultas?! Algo me dice que esto te lo tienes merecido desde hace tiempo.",
    ["O"] = "Derrota al Soldado Hendel en Marjal Revolcafango.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1338] = {
    ["D"] = "Hay un herrero enano cuya obra admiro mucho. Su nombre es Furen Longbeard, y su habilidad es insuperable. Necesito un nuevo escudo, ¡y debe ser uno de los suyos!$B$BEl problema es... Furen está muy al sur, en el distrito enano dentro de Ventormenta. ¡En tierras humanas! No puedo imaginar por qué viviría allí, tan lejos de Forjaz. ¡Los humanos deben pagarle una fortuna para mantenerlo allí!$B$BAsí que, si quieres viajar y no te importa ganar algo de dinero, ¿podrías enviarle mi solicitud de un escudo?",
    ["O"] = "Lleva la Petición de Pico Tormenta a Furen Barbalarga en Ventormenta.",
    ["T"] = "La Orden de Stormpike",
  },
  [1339] = {
    ["D"] = "Escuché que el Montaraz Stormpike está buscando un mensajero. Alguien que haga un poco de viaje por él. ¿Qué tal? ¿Eres el $r para el trabajo?$B$BSi es así, entonces encontrarás a Stormpike en la parte superior de la torre de vigilancia del norte.",
    ["O"] = "Habla con el Montaraz Stormpike.",
    ["T"] = "La Tarea del Montañero Stormpike",
  },
  [1358] = {
    ["D"] = "Un colega en Kalimdor, el Boticario Helbrim, está investigando los efectos de las toxinas de Lordaeron en las bestias de Kalimdor. Estaría muy interesado en una muestra de los corazones de lobo que Renferrel me envió.$B$BHelbrim está estacionado en Los Baldíos, en un pueblo conocido como El Cruce. Para llegar a él, debes viajar en zepelín a la capital orca de Orgrimmar, luego viajar al sur, a pie, a Durotar. Dirígete al oeste hacia Los Baldíos y pronto llegarás a El Cruce.",
    ["O"] = "Lleva la Muestra de Corazón de Lobo al Boticario Helbrim en Los Baldíos.",
    ["T"] = "Muestra para Helbrim",
  },
  [1359] = {
    ["D"] = "Los corazones de lobo que adquiriste poseen una calidad nunca vista en especímenes similares. Hay una toxicidad en ellos que debería ser mortal para los mismos lobos en los que los corazones laten.$B$BEs bastante increíble, y debemos estudiarlo más a fondo. Guardé algunas muestras de los corazones adquiridos. Llévalas a mi colega, la Boticaria Zinge. La encontrarás en nuestra sede en Entrañas.",
    ["O"] = "Lleva las Muestras de Corazón de Lobo al Boticario Zinge en Entrañas.",
    ["T"] = "Entrega de Zinge",
  },
  [1360] = {
    ["D"] = "Tenía un buen trabajo como excavador en el sitio de excavación de Uldaman en las Tierras Inhóspitas... hasta que aparecieron esos malditos troggs. Escapé con mi piel apenas intacta, y nada más. En el caos, dejé mi posesión más preciada. Está a salvo de los troggs en mi cofre, seguro, pero está justo en medio de ellos.$B$B¡Estaría en deuda contigo, tanto en honor como en monedas, si pudieras recuperarlo! Un cofre con mi nombre está al final del Pasillo Norte Común. Ese es el que necesitas abrir.",
    ["O"] = "Consigue la posesión más preciada de Krom Brazorrecio de su cofre en la Sala Común Norte de Uldaman, y llévasela a él en Forjaz.",
    ["T"] = "Tesoros Recuperados",
  },
  [1361] = {
    ["D"] = "Un guerrero de la Horda, Regthar Puertamuerte, está reclutando agentes para una misión en las tierras de Desolace. Habla con él si deseas ser voluntario. Está estacionado en Los Baldíos, en una serie de búnkeres al oeste de El Cruce.$B$BY afila tus armas. Desolace es un lugar desgarrado por la guerra.",
    ["O"] = "Habla con Regthar Puerta de la Muerte.",
    ["T"] = "Regthar Puertamuerte",
  },
  [1362] = {
    ["D"] = "Los centauros Kolkar viven en Desolace, y sus fuerzas se han extendido a los Baldíos. Este es un hecho conocido y tenemos agentes de la Horda ocupándose de ellos aquí.$B$BPero mientras los centauros sean fuertes en Desolace, siempre representarán una amenaza. Es hora de deshacernos de esa amenaza.$B$BHabla con Felgur Dosgajos en el Puesto del Caminante Fantasma en Desolace. Fue enviado para reunir inteligencia sobre los centauros. Él sabrá cómo lidiar mejor con los Kolkar de Desolace.",
    ["O"] = "Habla con Felgur Doshojas en el Puesto del Espíritu Errante en Desolace.",
    ["T"] = "Los Kolkar de Desolace",
  },
  [1363] = {
    ["D"] = "Como cuidador de las obras aquí en la biblioteca de la Academia de Artes y Ciencias Arcanas, es mi responsabilidad asegurarme de que todos los tomos y pergaminos mágicos estén contabilizados en todo momento. ¿Quién sabe qué locura se desataría si algunos de estos textos encantados cayeran en las manos equivocadas?$b$bLo cual me recuerda... mi asistente, Dellis, cometió un terrible error la semana pasada. Es bastante capaz en la biblioteca, pero incompetente cuando sale de las puertas de Ventormenta. Sé un héroe y ayuda al viejo Dellis, ¿quieres?",
    ["O"] = "Habla con el Acólito Dellis en Ventormenta.",
    ["T"] = "La Petición de Mazen",
  },
  [1364] = {
    ["D"] = "Las instrucciones del Maestro Mazen eran bastante claras: entregar un ensayo muy importante al Vigía Mahar Ba en el Castillo de Nethergarde.$b$b¡Pero nunca había estado en las Tierras Devastadas antes! ¡El viaje fue aterrador!$b$bCuando llegué al Pantano de las Penas, tomé un giro equivocado y terminé rodeado de horribles criaturas de pantano parecidas a plantas. Se movieron como si fueran a atacarme, así que corrí. Pero en mi prisa, dejé caer el ensayo. Estoy seguro de que una de las bestias lo tomó.$b$b¿Podrías intentar recuperarlo y entregarlo a Mahar Ba?",
    ["O"] = "Recupera los Ensayos de Khadgar sobre Convergencia Dimensional y entrégaselos al Vigía Mahar Ba en el Castillo de Nethergarde.",
    ["T"] = "La Petición de Mazen",
  },
  [1365] = {
    ["D"] = "Los Kolkar no son los únicos centauros de Desolace. Muchos clanes vagan por estas áridas llanuras, luchando y muriendo entre sí. Y es bueno que luchen entre ellos, porque son fuertes y brutales. Si los centauros se unieran contra nosotros, serían un enemigo feroz.$B$BDebemos lidiar con ellos antes de que se unan.$B$BTu primera tarea es interrumpir al clan Kolkar. Haz esto matando a su líder, Khan Dez\'hepah.$B$BLa Aldea Kolkar está al noreste de aquí. Ve, y que la fortuna te acompañe.",
    ["O"] = "Lleva la Cabeza de Khan Dez\'hepah a Felgur Dosgajas en Desolace.",
    ["T"] = "Khan Dez\'hepah",
  },
  [1366] = {
    ["D"] = "Ahora que asustaste a los clanes de centauros, es hora de hacerlos sangrar.  Cuando viajes por Desolace, caza centauros.  No me importa a qué clan caces.  Solo recolecta orejas.  Un montón de orejas.$B$BAl debilitar sus números, debilitarás su resolución y eliminarás cualquier pensamiento de expansión.$B$BBuena caza, $N.",
    ["O"] = "Lleva 15 Orejas de Centauro a Felgur Twocuts en Desolace.",
    ["T"] = "Recompensa por Centauros",
  },
  [1367] = {
    ["D"] = "Para vencer a los centauros, debemos usar su odio contra ellos. Nos aliaremos con uno de los clanes y a través de ellos, aprenderemos cómo derrotar a todos los clanes.$B$BSi eliges aliarte con los Magram, entonces mata a sus enemigos, los Gelkis. Esto te ganará respeto entre los Magram. Cuando hayas ganado suficiente, reúnete con el portavoz Magram Warug, en las afueras de la Aldea Magram, en el sureste de Desolace.",
    ["O"] = "Gana reputación Amistosa con los Magram y luego habla con Warug.",
    ["T"] = "Alianza con los Magram",
  },
  [1368] = {
    ["D"] = "Para vencer a los centauros, debemos usar su odio contra ellos. Nos aliaremos con uno de los clanes y a través de ellos, aprenderemos cómo derrotar a todos los clanes.$B$BSi eliges aliarte con los Gelkis, entonces mata a su rival más odiado, los Magram. Después de ganar el respeto de los Gelkis, reúnete con el chamán Gelkis Uthek el Sabio, en las afueras de la Aldea Gelkis, en el suroeste de Desolace.",
    ["O"] = "Gana reputación Amistosa con los Gelkis y luego habla con Uthek el Sabio.",
    ["T"] = "Alianza con los Gelkis",
  },
  [1369] = {
    ["D"] = "Los Gelkis piensan que nuestra madre centauro, Theradras, nos protege. Son estúpidos. Nadie protege a los centauros. ¡Los Magram no necesitamos protección! ¡Somos los más fuertes!$B$BMuéstrales a los Gelkis que son estúpidos. Ve a la cueva en su aldea. Acumulan cristales en esa cueva, las lágrimas de Theradras. Destruye las lágrimas y tráeme los pedazos rotos.",
    ["O"] = "Mantén tu reputación con los Magram y lleva 3 Lágrimas Rotas a Warug en la Aldea Magram.",
    ["T"] = "Lágrimas Rotas",
  },
  [1370] = {
    ["D"] = "Cuando no estamos en guerra, cazamos carne. La carne nos da fuerza, y los estómagos llenos luchan más duro que los vacíos.$B$BEs lo mismo con los Magram. Almacenan carne extra para poder luchar más tiempo entre cacerías. Si asaltas su aldea al este y tomas sus reservas de carne, entonces su fuerza fallará.",
    ["O"] = "Lleva 6 Bolsas de Carne Secada Tosca a Uthek el Sabio en la Aldea Gelkis.",
    ["T"] = "Robando Suministros",
  },
  [1371] = {
    ["D"] = "Hace mucho tiempo, un pequeño gnomo llegó a Desolace.  Tenía caballos y un carro, y el carro contenía cosas extrañas.  Cosas divertidas.  Cosas que ardían de diferentes colores.  Cosas que se movían y no estaban vivas.  Maté al pequeño gnomo, pero antes de eso... me hizo reír.$B$BGuardé mi cosa favorita de su carro.  Era un humano hecho de madera y me saludaba hasta que lo golpeé y lo rompí.$B$B¡Quiero uno nuevo!  ¡Consígueme uno nuevo!",
    ["O"] = "Mantén tu reputación con los Magram y lleva un Muñeco Diana Avanzado a Warug en la Aldea Magram en Desolace.",
    ["T"] = "Artilugio para Warug",
  },
  [1372] = {
    ["D"] = "Nuestra fuerza expedicionaria fue enviada por orden de Varimathras para inspeccionar el sur de Azeroth.$b$bMientras exploraba, noté a un Infiltrador humano espiando en el Refugio del Mendigo. Antes de que pudiera capturarlo, algunos de nuestros hermanos orcos... lo capturaron y lo llevaron a Rocal, su fortaleza en el Pantano de las Penas.$b$bQuiero... ayudar... a nuestros aliados orcos. No vieron nuestro campamento aquí, pero de todos modos quiero echar una mano.$b$bTrabaja con Faustin aquí para desarrollar un... suero de la verdad para nuestro amigo en Rocal.",
    ["O"] = "Habla con el Boticario Faustin en el Refugio del Mendigo.",
    ["T"] = "Nada Más Que la Verdad",
  },
  [1373] = {
    ["D"] = "Tienes más que demostrar, y tu próxima tarea te llevará lejos, así que por ahora los Gelkis lucharán contra los Magram sin ti.$B$BLa tierra susurra muchos secretos a quienes escuchan.  Un secreto que escucho es de un cristal rojo.  Se llama draenetista, y es nuevo en nuestra tierra.  Proviene de un lugar donde las montañas vuelan y los demonios gobiernan.$B$B¡Tráeme un fragmento de draenetista, y hará que mi magia sea más fuerte!  El fragmento está en un lugar llamado el Pantano de las Penas, en posesión del perdido Ongeku.",
    ["O"] = "Mantén tu reputación con los Gelkis y lleva un Fragmento de Draenetista a Uthek el Sabio en la Aldea Gelkis en Desolace.",
    ["T"] = "Ongeku",
  },
  [1374] = {
    ["D"] = "La tierra me habla, y dice que los Magram se debilitan. ¡Ahora es el momento de matar a su Khan!$B$B¡Tráeme la cabeza del Khan Magram, Jehn!",
    ["O"] = "Lleva la Cabeza del Khan Jehn a Uthek el Sabio en la Aldea Gelkis en Desolace.",
    ["T"] = "Khan Jehn",
  },
  [1375] = {
    ["D"] = "Ayudas a los Magram, y me agradas. ¡Ahora, mata al Khan de los Gelkis! Él es Khan Shaka, ¡y quiero su cabeza! ¡Quiero mirar su cabeza y reírme de él!",
    ["O"] = "Mantén tu reputación con los Magram y lleva la Cabeza de Khan Shaka a Warug en la Aldea Magram en Desolace.",
    ["T"] = "Khan Shaka",
  },
  [1380] = {
    ["D"] = "$N, nos ayudaste contra los Magram. ¡Ahora, ayúdanos a vencer a los Maraudine! ¡Mata al jefe de su clan, Khan Hratha!$B$BPara enfrentarte a él, toca el Cuerno de Guerra Maraudine. Está en lo alto de un acantilado en el Valle de las Lanzas.$B$BPero para tocar el cuerno, debes obtener una boquilla de cuerno de guerra. Estas son raras, solo las llevan los centauros Maraudine de confianza.$B$BDespués de tocar el cuerno, los centauros vendrán. Mátalos hasta que aparezca Hratha.$B$BKhan Hratha tiene una pieza de... una llave. Tráemela...",
    ["O"] = "Mantén tu reputación con los Gelkis y lleva el Fragmento de Llave Maraudine a Uthek el Sabio en la Aldea Gelkis.",
    ["T"] = "Khan Hratha",
  },
  [1381] = {
    ["D"] = "¡El Khan Gelkis está muerto, y ahora los Maraudine caerán! ¡Mata al Khan Maraudine, Hratha!$B$BPara enfrentarlo, toca el cuerno en el alto risco en el Valle de las Lanzas. Pero para tocar el cuerno, necesitas una boquilla. Para obtener una boquilla, mata a los centauros Maraudine. Sigue matando hasta que encuentres una boquilla. Luego toca el cuerno.$B$BHaz esto y los centauros Maraudine vendrán a la torre. Mátalos hasta que aparezca Hratha. Hratha tiene una pieza de una llave. Dame esa pieza...",
    ["O"] = "Mantén tu reputación con los Magram y lleva el Fragmento de Llave Maraudine a Warug en la Aldea Magram en Desolace.",
    ["T"] = "Khan Hratha",
  },
  [1382] = {
    ["D"] = "Somos afortunados de que los clanes de centauros luchen entre sí, porque si se unieran en una sola fuerza, serían imparables. Sí, somos afortunados, y deberíamos presionar nuestra ventaja.$B$BUno de los clanes de centauros, el clan Gelkis, está en una guerra brutal con el clan Magram. Si atacamos a los Magram, quizás se pueda forjar una alianza con los Gelkis.$B$BHabla con Uthek el Sabio, en las afueras de los campamentos Gelkis en el suroeste de Desolace. A través de ella podríamos forjar una alianza con los Gelkis.",
    ["O"] = "Gana reputación Amistosa con los Gelkis y luego habla con Uthek el Sabio.",
    ["T"] = "Alianza Extraña",
  },
  [1383] = {
    ["D"] = "Tengo justo el suero adecuado en mente. Se encargará de la verdad de la manera en que la verdad debería ser tratada.$B$BPara esta poción necesitaré varios corazones de pantera de las sombras del Pantano. También requiero el hongo encantado del Señor del Pantano que reside allí. Estoy seguro de que alguien tan capaz como tú, $N, puede persuadirlo para que te dé algo.$B$BAhora, la parte difícil será localizar un tumor de zancudo de las profundidades de Desolace. Muy raramente, los gigantes allí se enferman y se forma un tumor.$B$B¡Ahora, ve!",
    ["O"] = "El Boticario Faustin en el Refugio de los Mendigos necesita 5 Corazones de Pantera de las Sombras, Hongo del Señor del Lodo y un Tumor de Zancudo de las Profundidades.",
    ["T"] = "Nada Más Que la Verdad",
  },
  [1384] = {
    ["D"] = "Los centauros Kolkar piensan que su magia es fuerte. ¡No conocen la magia! ¡No reconocerían la magia si los mordiera!$B$BTú les mostrarás que su magia es débil. Ve al noreste y mátalos. Toma sus amuletos. Tráeme los amuletos y los arrojaré al fuego y reiré.",
    ["O"] = "Mantén tu reputación con los Gelkis y lleva 10 Amuletos Crudos a Uthek el Sabio en la Aldea Gelkis en Desolace.",
    ["T"] = "Incursión en los Kolkar",
  },
  [1385] = {
    ["D"] = "Desolace es una tierra en guerra. Los centauros luchan entre sí, y creemos que el vencedor entre ellos se desbordará y amenazará nuestras fortalezas. Eso no podemos permitirlo.$B$BDebemos aprender a vencer a los centauros, y para hacerlo... debemos aliarnos con uno de los clanes. Los centauros Magram luchan contra los Gelkis. Si luchas contra los Gelkis, los Magram podrían considerar una alianza.$B$BGánate el favor de los Magram y luego habla con Warug, en la aldea Magram en el sureste de Desolace.",
    ["O"] = "Gana reputación Amistosa con los Magram y luego habla con Warug.",
    ["T"] = "Política Brutal",
  },
  [1386] = {
    ["D"] = "Los Kolkar piensan que son fuertes, ¡pero nos reímos de ellos! ¡Nos reímos y los matamos! Tú también los matarás. Muéstrame que puedes matar Kolkar como nosotros. ¡Dirígete al norte y mátalos a todos!",
    ["O"] = "Mantén tu reputación con los Magram. Mata a 12 Centauros Kolkar, 12 Exploradores Kolkar y 6 Aplastadores Kolkar, luego regresa con Warug en la Aldea Magram en Desolace.",
    ["T"] = "Asalto a los Kolkar",
  },
  [1387] = {
    ["D"] = "Muchos clanes conforman la raza de los centauros. Cada uno tiene su propio líder y agenda, pero todos los clanes tienen una cosa en común. Crueldad.$B$BSi alguna vez vamos a encontrar la paz, entonces los centauros deben ser eliminados. Y por eso te pongo en la tarea de hacerlo.$B$BMientras vagas por Desolace, caza centauros y toma sus orejas. Tráeme muchas, muchas orejas.$B$BHaz esto en servicio de la Alianza, $N, y estaremos un paso más cerca del fin de la guerra.",
    ["O"] = "Lleva 15 Orejas de Centauro al Cabo Melkins en el Puesto de Nijel en Desolace.",
    ["T"] = "Recompensa por Centauros",
  },
  [1388] = {
    ["D"] = "Todo está procediendo según el plan, $n. Quiero que le des este suero de la verdad a Acechador de la Muerte Zraedus y él te mostrará cómo administrarlo... adecuadamente.$b$b¡Jaja!",
    ["O"] = "Dale el Suero de la Verdad de Faustin a Mortacechador Zraedus en el Refugio del Mendigo.",
    ["T"] = "Nada Más Que la Verdad",
  },
  [1389] = {
    ["D"] = "Los perdidos que viven en el Santuario de la Barbecha se parecen a nosotros, pero son monstruos... enloquecidos por las dificultades de nuestro viaje a Azeroth. Una vez nuestros hermanos, ahora viven en el Santuario de la Barbecha al este, capturando o matando a cualquiera que se acerque.$B$BY han robado algo sagrado para nosotros: cristales de draenetista.$B$BNo tenemos la fuerza para entrar en el Santuario de la Barbecha y reclamar nuestros cristales de draenetista robados de entre sus chozas y fogatas, pero sin esos cristales... nosotros también podríamos perder el rumbo.",
    ["O"] = "Lleva 6 Cristales Draenethyst a Magtoor en El Puerto en los Pantanos de las Penas.",
    ["T"] = "Cristales Draenethyst",
  },
  [1390] = {
    ["D"] = "Texto faltante",
    ["O"] = "Texto faltante",
    ["T"] = "Ups, Los Matamos Otra Vez.",
  },
  [1391] = {
    ["D"] = "Necesitamos operar con cierta discreción. Después de todo, no es como si fueras a entrar allí y convencer al tonto de que beba la poción de Faustin aquí, ¿verdad?$b$bPero si hay algo que esos humanos no pueden negar es el alcohol. Tomaré el suero de Faustin y lo mezclaré en esta cerveza. Te garantizo que el pobre tonto no mostrará ninguna reticencia cuando se trate de beber.$b$bDate prisa a Rocal en el Pantano de las Penas, $n. Y mantén nuestra pequeña misión en silencio...",
    ["O"] = "Lleva la Cerveza de Zraedus al Infiltrador Marksen en Rocal.",
    ["T"] = "Nada Más Que la Verdad",
  },
  [1392] = {
    ["D"] = "Noboru ha plagado a los draenei del Refugio durante meses, y su maza es un símbolo de la brutalidad que han sufrido a manos de los perdidos.",
    ["O"] = "Lleva el Garrote de Noboru al líder de El Puerto en los Pantanos de las Penas.",
    ["T"] = "Noboru el Mazo",
  },
  [1393] = {
    ["D"] = "¡Por favor, ayuda!  ¡Estas criaturas me van a comer!$B$B¡Ayúdame a salir de aquí, y te daré un gran tesoro!  Está en mi campamento en el pantano al norte de Rocal, pero está encerrado en una caja fuerte que solo se puede abrir con un truco que solo yo sé.$B$BSi me ayudas a escapar, entonces te lo enseñaré.",
    ["O"] = "Escolta a Galen fuera de peligro, luego encuentra su campamento en los Pantanos de las Penas y abre su caja fuerte.",
    ["T"] = "El Escape de Galen",
  },
  [1394] = {
    ["D"] = "Has hecho bien, $N. Has pasado mi prueba, y las pruebas anteriores a la mía. Regresa con Dorn en las Mil Agujas. Él sentirá el cambio dentro de ti, y verá que tu mente, cuerpo y espíritu son lo suficientemente fuertes como para que seas recompensado por tus esfuerzos.$B$BMantente fiel al equilibrio que has mostrado a lo largo de estas pruebas, $N. Dorn te pondrá a prueba nuevamente en el futuro si lo haces.",
    ["O"] = "Habla con Dorn Hablallanuras en las Mil Agujas.",
    ["T"] = "Pasaje Final",
  },
  [1395] = {
    ["D"] = "Esta caja de piedras de poder debe ir al Castillo de Nethergarde, en las Tierras Devastadas. No mantendrán su poder por mucho tiempo, así que no podemos enviarlas con la próxima caravana.$B$BDebes llevarlas, y debes llevarlas rápidamente.$B$BLlévalas al Intendente Lungertz en el Castillo de Nethergarde. Para llegar al castillo, debes viajar al este a través del Paso de la Muerte, luego al este a través del Pantano de las Penas, luego al sur a través de las Tierras Devastadas.$B$BTen cuidado, $N. Viajarás por tierras peligrosas, pero no debes fallar.",
    ["O"] = "Lleva el Crate de Piedras de Poder al Intendente Lungertz, antes de que las piedras de poder expiren.",
    ["T"] = "Suministros para Nethergarde",
  },
  [1396] = {
    ["D"] = "Mi caravana estaba destinada a la costa este, pero no llegamos a nuestro objetivo. Los Perdidos nos atacaron, matando a los guardias y destrozando los suministros.$B$BSobreviví solo a su asalto. He jurado proteger los suministros que quedan, pero las criaturas salvajes merodean cerca. ¡Por favor, ayúdame a defender mi campamento de las bestias!",
    ["O"] = "Mata 8 Cocodrilos Dientes de Sierra Jóvenes, 10 Hiladores de la Pesadumbre y 10 Jaguares del Pantano, luego regresa con el Vigía Biggs en los Pantanos de las Penas.",
    ["T"] = "Fauna Invasora",
  },
  [1398] = {
    ["D"] = "Te debo mucho, $N. Un corazón honorable late dentro de ti. Y ahora tengo otra tarea para la cual humildemente pido tu ayuda.$B$BSi alguna vez voy a llegar a Nethergarde, entonces debo reparar este carro. Y para eso... necesitaré madera. Madera fuerte y seca, no la madera esponjosa y enredada de los árboles del pantano.$B$B$N, ve al este hacia la costa y busca madera flotante que haya sido secada por el sol. Tráemela.",
    ["O"] = "Lleva 8 trozos de Madera Flotante Secada al Sol al Vigía Biggs en los Pantanos de las Penas.",
    ["T"] = "Madera a la Deriva",
  },
  [1418] = {
    ["D"] = "Los Baldíos son un lugar duro y están cerca de las tierras de la Alianza en Loch Modan, por lo que la comunicación entre Rocal y el puesto de avanzada de Kargath en los Baldíos a menudo se corta.$B$BDemuestra tu valía, $N. Viaja a los Baldíos y habla con Neeka Sangrescara, la exploradora principal del puesto de avanzada de Kargath. Habla con ella y luego envía su informe de vuelta a mí.",
    ["O"] = "Habla con Neeka Cicatriz de Sangre.",
    ["T"] = "Neeka Cicatriz de Sangre",
  },
  [1419] = {
    ["D"] = "¿Has pasado muchos días en las Tierras Inhóspitas, $c? ¿Has sentido su calor? ¿Te has enfrentado a las bestias que deambulan por sus polvorientas llanuras? Es hora de que lo hagas.$B$BLos coyotes de las cumbres son una raza resistente. Resistentes y lo suficientemente audaces como para colarse en Kargath y robar nuestros suministros de comida. ¡Ayuda a deshacer el puesto de estos ladrones!$B$BCaza coyotes de las cumbres, $N. Cázalos y tráeme sus mandíbulas. Tráeme suficientes, y tal vez te crea cuando digas que conoces las Tierras Inhóspitas.",
    ["O"] = "Lleva 30 Mandíbulas de Coyote a Neeka Bloodscar en Kargath.",
    ["T"] = "Ladrones de Coyotes",
  },
  [1420] = {
    ["D"] = "Escribí un nuevo informe para Helgrum en Rocal. Tráeselo.$B$BY si te consideras un correcaminos, no pierdas el camino. O caigas ante un enemigo.",
    ["O"] = "Lleva el Informe de Neeka a Helgrum el Veloz en Rocal.",
    ["T"] = "Informar a Helgrum",
  },
  [1421] = {
    ["D"] = "Quedé aturdido durante el ataque de los Perdidos, pero antes de que mi vista se oscureciera, vi uno de nuestros carromatos siendo arrastrado hacia el Santuario Fértil, la aldea de los perdidos al norte.$B$BUn cofre de caravana con valiosos reactivos estaba en ese carromato, reactivos vitales para los estudios de los magos del Castillo de Nethergarde. Encuentra el cofre y recupera esos reactivos, $N. Recúperalos y tráemelos.",
    ["O"] = "Encuentra el Cofre de la Caravana en el Santuario Fértil, obtén los Reagentes de los Magos y llévaselos al Vigía Biggs.",
    ["T"] = "La Caravana Perdida",
  },
  [1422] = {
    ["D"] = "Mientras comienzo a trabajar en tu arma, ¿por qué no hablas con Katar? Mencionó que necesita ayuda con las patrullas a lo largo de la playa, y prefiero que mantengas tu ingenio y armas preparadas en lugar de emborracharte con algunos de los otros holgazanes con los que comparto este campamento.",
    ["O"] = "Habla con Katar en los Pantanos de las Penas.",
    ["T"] = "Amenaza desde el Mar",
  },
  [1423] = {
    ["D"] = "Este barril es pequeño pero resistente. Sus costuras están selladas con una cera amarilla y picante. Estampado en la parte inferior del barril están las palabras:$B$BQUARTERMASTER LUNGERTZ$B$BNETHERGARDE KEEP",
    ["O"] = "Lleva los Suministros Perdidos al Intendente Lungertz.",
    ["T"] = "Los Suministros Perdidos",
  },
  [1424] = {
    ["D"] = "El Jefe de Guerra nos envió a investigar sucesos extraños en el Templo de Atal\'Hakkar.$B$BLos trolls Atal\'ai son una tribu de magos oscuros, conocidos por practicar magia prohibida. Puedes imaginar nuestra sorpresa cuando llegamos aquí y descubrimos que el templo había sido hundido.$B$BLos dragones verdes ahora custodian el sitio, afirmando ser protectores de Azeroth.$B$BNecesitamos descubrir qué sucedió. Explora alrededor del Estanque de las Lágrimas y recoge artefactos Atal\'ai. A partir de ellos puedo comenzar a armar este rompecabezas.",
    ["O"] = "Fel\'zerul en Rocal quiere que reúnas 10 Artefactos Atal\'ai.",
    ["T"] = "El Charco de Lágrimas",
  },
  [1425] = {
    ["D"] = "Mientras arreglo este vagón, $N, tengo una última tarea para ti.$B$BHe empacado los suministros más vitales salvados de los ataques de los ogros. Llévalos al Castillo de Nethergarde. El castillo está enclavado en las colinas del norte de las Tierras Devastadas, vigilando el Portal Oscuro.$B$BPara llegar a Nethergarde, sigue el camino hacia el este. Cuando se divida, dirígete al sur hacia las Tierras Devastadas, luego gira a la izquierda en la siguiente bifurcación. Sigue el camino, $N, y mantente alerta.",
    ["O"] = "Lleva el Envío a Nethergarde al Intendente Lungertz en el Castillo de Nethergarde.",
    ["T"] = "Entregar el Envío",
  },
  [1426] = {
    ["D"] = "No tengo amor por el mar, ni por ninguna criatura que haya visto salir de sus profundidades. Es una amante poderosa pero ciega, insensible y brutal. Me daría un gran placer matar a los hombres pez por cientos si estuviera en mi poder. Pero obtengo algo de placer al ver sus muertes a manos de cualquiera que cumpla la voluntad de la Horda.$B$BLo que nos lleva a ti, $c. ¿Cuántos puedes matar? ¿Te pondrás a prueba contra los más poderosos de su especie?",
    ["O"] = "Mata 10 Múrloc de los Pantanos, 10 Escupecharcos de los Pantanos y 10 Comecarne de los Pantanos, luego regresa con Katar en los Pantanos de las Penas.",
    ["T"] = "Amenaza desde el Mar",
  },
  [1427] = {
    ["D"] = "Antes de que descanses, habla con Tok\'Kar nuevamente. Mientras estabas fuera, creo que terminó algunas armas. Por tu ayuda aquí, te ofrecería una de ellas. Harías bien en probar su fuerza contra los hombres pez cuando estés listo... pero solo para complacerme aún más.",
    ["O"] = "Habla con Tok\'Kar en los Pantanos de las Penas.",
    ["T"] = "Amenaza desde el Mar",
  },
  [1428] = {
    ["D"] = "Me complace ver que tu odio por los hombres pez crece junto con el mío.$B$BEncuentra más de su especie a lo largo de la playa, y cuando hayas tenido suficiente combate, regresa a mí. Entonces hablaremos de una recompensa por tu diligencia.",
    ["O"] = "Mata 10 Escupecharcos de los Pantanos, 10 Comecarne de los Pantanos y 10 Oráculos de los Pantanos, luego regresa con Katar en los Pantanos de las Penas.",
    ["T"] = "Amenaza Continua",
  },
  [1429] = {
    ["D"] = "Mientras te enfrentabas a los peligros de la Poza de las Lágrimas, uno de mis exploradores regresó con un pergamino escrito en sangre sobre piel seca. Parece ser una sentencia judicial contra un sacerdote de la tribu Atal\'ai.$b$bEste sacerdote fue sentenciado a un destino peor que la muerte entre los Atal\'ai.$b$bFue exiliado para vivir lejos al norte, en las Tierras del Interior, con los trolls Witherbark.$b$bEmpaquetaré estos artefactos. Debes buscar al exiliado y descubrir su verdadero uso. Regresa a mí una vez que tu viaje esté completo.",
    ["O"] = "Lleva el Fardo de Artefactos Atal\'ai al Exiliado Atal\'ai en las Tierras del Interior.",
    ["T"] = "El Exiliado Atal\'ai",
  },
  [1430] = {
    ["D"] = "Hay una gran demanda por el sabor suculento de la carne de cangrejo entre mis clientes, especialmente la carne de los cangrejos monstruosos.$B$BSi planeas dirigirte a la costa, entonces podría hacer que valga la pena que me traigas algunas de sus patas para transportarlas de regreso a Orgrimmar.$B$BLa costa está al este de aquí, y no deberías tener problemas para encontrar a las criaturas correteando por las playas y bajo el mar.",
    ["O"] = "Lleva 10 Patas de Rondador Monstruoso a Dar en los Pantanos de las Penas.",
    ["T"] = "Carne Fresca",
  },
  [1431] = {
    ["D"] = "Quédate quieto, $c, y permíteme compartir contigo algo importante para nuestra gente, pero lejos de ser obvio.$B$BSomos una raza orgullosa, y la guerra con los humanos puso a prueba incluso la gran paciencia de Thrall. Verás, nuestro Jefe de Guerra es sabio y prudente, y siempre pone a su gente primero. En última instancia, es un diplomático, y en su tiempo con los humanos aprendió mucho de sus formas.$B$BSi la historia que he comenzado despierta tu interés, entonces encuentra a Keldran cerca de la salida occidental de la ciudad. Su choza está allí.",
    ["O"] = "Habla con Keldran en Orgrimmar.",
    ["T"] = "Relaciones con la Alianza",
  },
  [1432] = {
    ["D"] = "A medida que la Alianza avanza más y más en Kalimdor, se pueden sacar conclusiones incorrectas sobre que la Horda está haciendo pactos con la Legión Ardiente. El Jefe de Guerra no puede permitir que eso suceda. Y con su bendición, estoy tomando el control de supervisar su eliminación.$B$BTengo agentes en el área de Desolace en el Puesto del Caminante Fantasma, al norte del Gran Cementerio de Kodos. Dirígete al sur del Valle Carbonizado en las Montañas de Stonetalon y lo encontrarás. Cuando llegues, busca a Takata Hojacero.",
    ["O"] = "Habla con Takata Hojacero en Desolace.",
    ["T"] = "Relaciones con la Alianza",
  },
  [1433] = {
    ["D"] = "La Hermandad de la Hoja tiene muchas agendas, pero sabe que son malvados hasta la médula, sus espíritus están corrompidos, y su destrucción es la única manera de frustrar sus objetivos.$B$BKeldran envió a Maurin conmigo debido a su conocimiento de la Hoja y sus formas de brujos.$B$BÉl tiene el control de nuestros planes contra la Hoja mientras yo me ocupo de las otras amenazas demoníacas aquí en Desolace.$B$BHabla con él, y te guiará en sus diseños.",
    ["O"] = "Habla con Maurin Huesorrado en Desolace.",
    ["T"] = "Relaciones con la Alianza",
  },
  [1434] = {
    ["D"] = "Una infestación de demonios no ocurre sin otras influencias: algo más permite que el mal gane terreno.$B$BPuede comenzar con un espíritu debilitado, un deseo innecesario de poder, tal vez sea una sed de venganza o el atractivo de la justicia final. Cuando estamos afligidos por el dolor, el mal se aprovecha de nosotros, mal como el de los sátiros.$B$BSe han establecido vínculos entre ellos y la Hoja Negra, y Thrall quiere que sean eliminados. Búscalos al noreste de aquí y regresa a mí cuando los hayas repelido.",
    ["O"] = "Mata 7 Pícaros Furiahate, 7 Malditos Furiahate, 7 Traidores Furiahate y 7 Clama Inferno Furiahate, y regresa con Takata Hoja de Acero en Desolace.",
    ["T"] = "Corrompido por los Sátiros",
  },
  [1435] = {
    ["D"] = "La muerte es lo más deseable para esas criaturas despreciables que dan la espalda a su señor por razones tan egoístas. Pero la muerte no es suficiente cuando se trata de aquellos que se han entregado a la oscuridad.$B$BToma esta gema. Cuando uno de los Espada de Fuego esté cerca de la muerte, activa su magia. El hechizo extraerá el espíritu corrompido de su víctima, dejando una gema infundida.$B$BTráeme estas gemas infundidas para que pueda deshacerme de ellas adecuadamente.$B$BVe ahora, y que evites la muerte.",
    ["O"] = "Devuelve la Gema Ardiente y 15 Gemas Ardientes Infundidas a Maurin Rompehuesos en Desolace.",
    ["T"] = "La Quema de los Espíritus",
  },
  [1436] = {
    ["D"] = "La lucha va bien, $N. Quizás el Jefe de Guerra nos permita regresar a nuestros hogares antes de que cambien las estaciones si continuamos haciendo grandes avances en nuestra batalla contra los demonios y sus secuaces.$B$BRegresa con Keldran y hazle saber lo que hemos hecho. Estoy seguro de que te recompensará por tus esfuerzos aquí. Su causa es importante para él, y se sentirá orgulloso de saber que el Jefe de Guerra respeta sus preocupaciones.",
    ["O"] = "Habla con Keldran en Orgrimmar.",
    ["T"] = "Relaciones con la Alianza",
  },
  [1437] = {
    ["D"] = "Soy Vahlarriel, $N. He venido aquí para descubrir el paradero de dos nobles desaparecidos de una familia de comerciantes de Darnassus, la Casa Malem. El padre de la casa teme lo peor, y debo hacer todo lo posible para encontrar a sus hijos desaparecidos. Todavía no he comenzado mi búsqueda de Tyranis o Dalinda, pero tengo la esperanza de no estar aquí por mucho tiempo.$B$BLa última noticia dice que pasaron por las Montañas de Stonetalon hace unas semanas.$B$BSi encuentras su carro, por favor, ven a buscarme de inmediato.",
    ["O"] = "Encuentra y registra el carromato de Tyranis y Dalinda Malem en Desolace.",
    ["T"] = "La Búsqueda de Vahlarriel",
  },
  [1438] = {
    ["D"] = "Encuentra tanto a Dalinda como a Tyranis si puedes y regresa a mí con tus hallazgos. Comienza tu búsqueda en esta fortaleza. Contactaré de inmediato a mis superiores en Darnassus y les informaré de la situación.$B$B$N, hay algo más que deseo recalcar. Mis superiores han mostrado cierta preocupación por los motivos de Tyranis para venir aquí. Para ellos es más importante que Dalinda sea encontrada y traída de vuelta a salvo. Tyranis debe ser... evaluado antes de ser llevado a casa.",
    ["O"] = "Encuentra a Dalinda Malem en Desolace.",
    ["T"] = "La Búsqueda de Vahlarriel",
  },
  [1439] = {
    ["D"] = "Nos trajeron aquí después de que los orcos masacraran a todos nuestros guardias y sirvientes. ¡Oh, las cosas terribles que les hicieron! Pensé que seguramente nos matarían allí mismo, pero se hizo obvio que tenían otros planes.$B$BTyranis intentó hablar con ellos. Creo que quería impresionarlos con su conocimiento de lo arcano... No estoy seguro de por qué.$B$BPor favor, no puedo irme de aquí hasta que sepa qué le ha pasado a mi hermano. Encuéntralo, luego regresa por mí cuando sepas su destino.",
    ["O"] = "Encuentra a Tyranis Malem en Desolace.",
    ["T"] = "En Busca de Tyranis",
  },
  [1440] = {
    ["D"] = "¿Así que el momento es ahora? Aprecio tus esfuerzos, y si la situación no lo permite, permíteme agradecerte ahora por todo lo que has hecho por mí y mi familia. Si estás listo, entonces no hay nada más para mí aquí - podemos irnos cuando sientas que es el momento adecuado.",
    ["O"] = "Escolta a Dalinda Malem desde la Fortaleza Hacha de Trueno y luego regresa con Vahlarriel Matademonios en Desolace.",
    ["T"] = "Regreso con Vahlarriel",
  },
  [1441] = "_",
  [1442] = {
    ["D"] = "Saludos, $N. ¿Qué puedo hacer por alguien que sigue un camino sagrado?$B$BAh, sí, Stilwell, el herrero... de hace mucho tiempo. Lo recuerdo bien, aunque no he escuchado su nombre en algún tiempo.$B$BNunca me pareció un hombre insolente, así que solo puedo asumir que su deseo de una gema Kor es noble.$B$BSi realmente deseas una de las gemas, entonces debes dirigirte al noroeste de Vallefresno. Encontrarás las Cavernas de Brazanegra allí dentro de algunas ruinas antiguas. Las naga que aún usan las gemas están abajo. No vayas solo, $c.",
    ["O"] = "Lleva una Gema Kor a Thundris Tejeviento en Costa Oscura.",
    ["T"] = "Buscando la Gema Kor",
  },
  [1444] = {
    ["D"] = "Jammal\'an el Profeta me condenó al exilio del Templo de Atal\'Hakkar. Una vez creí que el sumo sacerdote era un líder digno.$b$bSu fe inquebrantable en su profecía llevará a la destrucción de los Atal\'ai. Si no lo ha hecho ya.$b$bCuando expresé mis preocupaciones, me etiquetaron como infiel y me expulsaron del templo.$b$bEsos artefactos que me trajiste son inofensivos. Pero si Jammal\'an ha encontrado el fetiche adecuado para el ritual de invocación, el mundo está en gran peligro....",
    ["O"] = "Regresa con Fel\'Zerul en Rocal.",
    ["T"] = "Regreso con Fel\'Zerul",
  },
  [1445] = {
    ["D"] = "¿Así que habló de la profecía de Jammal\'an?$b$bEl líder espiritual de los Atal\'ai cree que una vez que Hakkar regrese a Azeroth desde el Vacío, el dios les concederá la inmortalidad a la tribu Atal\'ai.$b$bUn engaño tonto para provocar un apocalipsis prematuro, si me preguntas.$b$b¿Pero dices que el exiliado habló de fetiches encantados? Esto me preocupa mucho. Si estos son de hecho la clave del ritual Atal\'ai que causó este desastre, debemos entender sus poderes intrínsecos.$b$b¡Aventúrate en el Templo y toma los fetiches!",
    ["O"] = "Recolecta 20 Fetiches de Hakkar y llévaselos a Fel\'Zerul en Rocal.",
    ["T"] = "El Templo de Atal\'Hakkar",
  },
  [1446] = {
    ["D"] = "Mis compañeros Witherbark no son amables con los extraños cerca de su hogar.$b$b¿Yo? Ya no tengo un hogar. Fui exiliado de mi gente, la gran tribu Atal\'ai del Pantano de las Penas.$b$bNuestro líder espiritual, Jammal\'an, tuvo lo que llamó una profecía. Cree que la invocación del dios Hakkar les dará a los Atal\'ai la inmortalidad.$b$bPero yo pedí precaución. ¿Y si la profecía no era más que manipulación?$b$bQuiero venganza por mi exilio. Tráeme la cabeza de Jammal\'an. Quizás entonces mi gente sea libre.",
    ["O"] = "El Exiliado Atal\'ai en las Tierras del Interior quiere la Cabeza de Jammal\'an.",
    ["T"] = "Jammal\'an el Profeta",
  },
  [1447] = {
    ["D"] = "Sí, obviamente no eres el $c más inteligente que ha llegado a Ventormenta, pero estás a punto de ser uno de los más tontos en nunca irse.$B$B¿Has conocido a mis amigos?",
    ["O"] = "Derrota a Dashel Puño de Piedra en Ventormenta.",
    ["T"] = "El Diplomático Desaparecido",
  },
  [1448] = {
    ["D"] = "Hubo un tiempo en que era joven y lleno de vigor como tú, $c.$b$bPero ahora soy viejo y no puedo explorar el mundo como lo hacía antes. ¡Ah, las cosas que vi!$b$bPero hubo un misterio que me eludió. Para mi última misión, la Liga de Expedicionarios me envió en busca del Templo de Atal\'Hakkar. Se rumoreaba que estaba ubicado en el Pantano de las Penas.$b$bDebido a mi estado frágil, viajé por los cielos en grifo en su búsqueda, pero nunca lo encontré.$b$b¿Ayudas a un viejo enano? Quizás tengas mejor suerte a pie.",
    ["O"] = "Brohann Barriga de Barril en Ventormenta quiere que busques el Templo de Atal\'Hakkar.",
    ["T"] = "En Busca del Templo",
  },
  [1449] = {
    ["D"] = "Como mencioné anteriormente, intenté explorar el templo por mi cuenta desde el aire. Bueno, no estaba exactamente solo.$b$bViajé al renombrado aviario de grifos en Pico Nidal, en Las Tierras del Interior. Allí recluté la ayuda de uno de sus mejores pilotos. El piloto cayó de su montura, hacia el pantano...$b$bEn mi vejez, el nombre del pobre hombre se me escapa. Sin duda, el maestro del aviario lo recordaría.$b$bViaja allí y busca a Falstad Martillo Salvaje.$b$bNecesitamos averiguar qué le pasó a ese piloto.",
    ["O"] = "Habla con el Alto Señor Falstad Martillo Salvaje en Pico Nidal en las Tierras del Interior.",
    ["T"] = "A las Tierras del Interior",
  },
  [1450] = {
    ["D"] = "¿Así que eres un $g chico:chica, glorificado de recados para algún viejo del Explorers\' League, eh?$b$bBueno, escúchame, $c. No sé con quién volaba tu viejo amigo por Azeroth. ¿Te parezco el Maestro de grifos Talonaxe? ¿Eh?$b$bSi estás tan interesado en las asignaciones de vuelo, ¿por qué no le preguntas al maestro de grifos? Si te veo holgazaneando aquí por mucho más tiempo, te pondré a trabajar de verdad.$b$bAhora, muévete.",
    ["O"] = "Habla con el Maestro de Grifos Hachatalón dentro del Aviario de Pico Nidal.",
    ["T"] = "Maestro de Grifos Talonaxe",
  },
  [1451] = {
    ["D"] = "A Rhapsody le gustaba mucho el alcohol. No me malinterpretes, todos los enanos aquí en el aviario amamos una buena cerveza cuando es el momento adecuado.$b$bPero el viejo Shindigger se aficionó al licor como un grifo al cielo. Y era una maldita pena, nadie podía volar como Rhapsody.$b$bEl primer signo de problemas fue cuando regresó tambaleándose de esa misión con tu amigo, Brohann. Todo fue cuesta abajo después de eso. Tuve que despedirlo.$b$bLo último que supe es que montó un campamento de cerveza al este de aquí...",
    ["O"] = "Busca a Rhapsody Machacacopas en Las Tierras del Interior.",
    ["T"] = "Rhapsody Shindigger",
  },
  [1452] = {
    ["D"] = "¡Hip!$B$B¿Así que ese loco de Brohann te envió? Vaya, tuvimos toda una aventura... ¡Hip!$B$BSi quieres que te cuente lo que pasó en el pantano, necesitaré algo fuerte para beber. No fue una escena bonita, ya verás. ¡Hip!$B$BTe diré qué... tráeme lo que necesito para preparar un Cóctel de Kalimdor y te contaré todo al respecto.$B$BVoy a necesitar algunas mollejas de Roc de Tanaris... y veamos... algunos hígados de Groddoc y Feropeludo de Feralas.$B$BUn buen Cóctel de Kalimdor bien cargado... eso me hará hablar.",
    ["O"] = "Rhapsody Shindigger en Las Tierras Altas quiere que le traigas 3 Mollejas de Roc, 3 Hígados de Groddoc y 3 Hígados de Peloférreo.",
    ["T"] = "El Cóctel Kalimdor de Rhapsody",
  },
  [1453] = {
    ["D"] = "Hay trabajo por hacer en Desolace, $N. Es un lugar atroz, contaminado por centauros y otras criaturas desagradables. Pero si estás buscando una buena semana de trabajo honesto, entonces dirígete allí, reúnete con Kreldig Ungor y dile que te envié.$B$BPuedes encontrarlo refugiado en el Punto de Nijel, un pequeño campamento acogedor escondido en las montañas del norte de Desolace. Solo dirígete al este si entras desde las Montañas de Trabalomas. Es fácil de pasar por alto, así que mantén los ojos abiertos.",
    ["O"] = "Habla con Kreldig Ungor en Desolace.",
    ["T"] = "Negocios de los Reclamadores en Desolace",
  },
  [1454] = {
    ["D"] = "Mi primer encargo es para el mago Karnitol, un noble de algún tipo en Ventormenta. Parece que su barco fue atacado por gigantes marinos mientras estaba en la costa noroeste de Desolace. Él y sus sirvientes intentaron llevar uno de sus cofres más valiosos a tierra, pero cuando los gigantes marinos atacaron en masa, decidió abandonarlo y teletransportarse a un lugar seguro junto con sus sirvientes.$B$BQuiere que encontremos el cofre y su contenido, y que se lo devolvamos en Ventormenta.",
    ["O"] = "Encuentra el Cofre de Karnitol en Desolace.",
    ["T"] = "El Naufragio del Karnitol",
  },
  [1455] = {
    ["D"] = "Tomas unos momentos para averiguar qué abrió el cofre, y notas a lo largo de los lados y la parte superior del cofre, arañazos largos y profundos que revelan que algo más que un hombre o un gigante se interesó en él. Las marcas no parecen haber sido causadas por una daga o una espada.$B$BDespués de revisar el cofre, lo encuentras completamente vacío.$B$BNo teniendo otro curso de acción, cierras el cofre y te preparas para partir. Kreldig estará decepcionado.",
    ["O"] = "Regresa con Kreldig Ungor en Desolace.",
    ["T"] = "El Naufragio del Karnitol",
  },
  [1456] = {
    ["D"] = "Intrigante.$B$BBueno, quizás no estemos completamente sin esperanza.$B$BA lo largo de esa misma costa, pero hacia el norte cerca de la Isla Ranazjar, hay ruinas élficas en el fondo del océano. Una tribu de naga, los Filoescamos, hacen de ese lugar su hogar. Creo que esa sería nuestra mejor apuesta.$B$BSi un grupo de exploración llegó tan al sur, entonces debían ser bastante poderosos. Si vas a encontrar las pertenencias de Karnitol, empezaría con los naga más poderosos: los cazadores de mareas o las brujas del mar.",
    ["O"] = "Encuentra la Bolsa de Karnitol y devuélvesela a Kreldig Ungor en Desolace.",
    ["T"] = "El Naufragio del Karnitol",
  },
  [1457] = {
    ["D"] = "Llévale las cosas de Karnitol a Roetten en Forjaz y te pagará por un trabajo bien hecho.$B$BJaja, si no tienes cuidado, estará tan contento contigo que te pedirá que te unas a nosotros a tiempo completo.$B$BOtra vez, gran trabajo, $N.$B$BTen cuidado en tu regreso, y no tardes demasiado: Karnitol no es el más paciente de los hombres, pero es muy generoso de todos modos.",
    ["O"] = "Lleva la Bolsa de Karnitol a Roetten Martillopétreo en Forjaz.",
    ["T"] = "El Naufragio del Karnitol",
  },
  [1458] = {
    ["D"] = "Uno de mis encargos aquí implica recoger algunos reactivos para Leftwitch, un mago gnomo en Kharanos. Roetten parece pensar que es dinero fácil en el banco ya que ya estamos aquí. Realmente no le importa que estemos cortos de personal.$B$BLos primeros elementos de su lista deberían ser fáciles de conseguir, y sería una buena cantidad de dinero para cualquiera que me ayude.$B$BNecesito algunos cuernos y garras de los sátiros Hatefury al este de aquí. Tráemelos y comenzaremos con el resto de la lista.",
    ["O"] = "Lleva 10 Garras Furia de Odio y 10 Cuernos Furia de Odio a Kreldig Ungor en Desolace.",
    ["T"] = "Reactivos para Reclamadores S.A.",
  },
  [1459] = {
    ["D"] = "Lo siguiente en la lista de Leftwitch es... veamos... hmm. Ah, aquí está.$B$BSencillo, pero bastante explícito en sus instrucciones. Necesita veneno de los scorpashi y piel de algún kodo. La letra pequeña dice que ambos deben ser de criaturas específicas: el veneno debe provenir de cualquier scorpashi, y la piel debe ser de un kodo envejecido, no de los moribundos o antiguos.$B$BLos magos, ya sabes, algunas de las cosas que quieren... son simplemente raras.",
    ["O"] = "Lleva 7 viales de Veneno de Scorpashi y 3 Pieles de Kodo Añejas a Kreldig Ungor en Desolace.",
    ["T"] = "Reactivos para Reclamadores S.A.",
  },
  [1460] = "_",
  [1461] = "_",
  [1462] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sapta de Tierra",
  },
  [1463] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Sapta de Tierra",
  },
  [1464] = {
    ["D"] = "",
    ["O"] = "Habla con Telf Joolam por otro Sapta de Fuego.",
    ["T"] = "Sapta de Fuego",
  },
  [1465] = {
    ["D"] = "Mientras continúas tu búsqueda en el vagón, encuentras no solo un colgante, sino también huellas que se dirigen hacia el sur. A lo lejos puedes ver humo elevándose desde un gran fuerte. El humo proviene de un fuego mantenido, no de la quema de edificios, y tus instintos te dicen que los Malem, si aún están vivos, fueron llevados en esa dirección.$B$BCrees que Vahlarriel en el Punto de Nijel no solo debería ver el colgante que has encontrado, sino también saber sobre los signos de vida hacia el sur.",
    ["O"] = "Devuelve el Colgante Malem a Vahlarriel Matademonios en Desolace.",
    ["T"] = "La Búsqueda de Vahlarriel",
  },
  [1466] = {
    ["D"] = "Este es el último de los artículos, y no voy a mentir, no es bonito. También espero que esto te tome un poco más de tiempo que los otros artículos.$B$BAsí que, basta con las advertencias, comencemos. Leftwitch dice que necesita: diez cerebros de devoradores de maná, cazadores de magos o cazadores de leyes. Diez alas abisales de una doncella abisal, hermana o hechicera. Y... diez viales de sangre de señor del destino de un capitán o señor del destino.$B$BBastante simple, ¿eh? Caray... si quieres pasar de esto, lo entendería.",
    ["O"] = "Lleva 10 Cerebros de Can manáfago, 10 Alas abisales y 10 viales de Sangre de maldición a Kreldig Ungor en Desolace.",
    ["T"] = "Reactivos para Reclamadores S.A.",
  },
  [1467] = {
    ["D"] = "Para cobrar, tendrás que volver a Forjaz y hablar con el propio Roetten. Le encantará sacarse el paquete de Leftwitch de encima de todos modos. Solo dale esto y debería ser suficiente para obtener tu botín.$B$BY no te preocupes, todavía voy a poner una buena palabra por ti cuando regrese. No debería estar en Desolace mucho más tiempo.",
    ["O"] = "Lleva el Paquete de Leftwitch de vuelta a Roetten Martillopétreo en Forjaz.",
    ["T"] = "Reactivos para Reclamadores S.A.",
  },
  [1468] = {
    ["D"] = "¿Estás dispuesto a ayudarnos aquí en el orfanato? Bendito seas, amigo, tu ayuda durante la Semana de los Niños será invaluable.$B$BToma este silbato. Solo podrás usarlo por un corto período de tiempo, generalmente no más que la propia Semana de los Niños. Cuando lo uses, podrás hablar con el niño que has aceptado cuidar. Usarlo nuevamente despedirá al niño.$B$B¡Por todos los medios, $N, usa el silbato y conoce a tu pupilo! Estoy seguro de que el niño estará ansioso por conocerte.",
    ["O"] = "Usa el Silbato de Huérfano Humano para hablar con el niño que cuidarás durante la Semana de los Niños.",
    ["T"] = "Semana de los Niños",
  },
  [1469] = {
    ["D"] = "Antes del vuelo, me bebí un barril entero de cerveza negra. ¡Hipo!$B$B¡Y qué vuelo fue! Volando por ahí con Brohann siguiéndome. No vi ningún templo, sin embargo. ¡Hipo!$B$BBueno, no vi ningún templo hasta que me puse un poco demasiado borracho y me caí de mi montura. Me sumergí directamente en el agua y me hundí. Me encontré rodeado de un montón de trolls espeluznantes. ¡Nadé rápidamente para salir de allí! Incluso me llevé este recuerdo de troll. Aquí, llévaselo a Brohann y hazle saber que su templo está ahí abajo, de verdad. ¡Hipo!",
    ["O"] = "Lleva el Fragmento de Tablilla Atal\'ai a Brohann Caskbelly en Ventormenta.",
    ["T"] = "El Cuento de Rhapsody",
  },
  [1470] = {
    ["D"] = "Saludos, niño. Puedo ver que todavía eres joven en los caminos del brujo, el poder aún elude tu alcance. Puedo sentir tu entusiasmo por aprender.$b$bY aprenderás.$b$bNo hay fuerza en la ignorancia. El conocimiento es nuestro mayor poder. A través de su aplicación, podemos controlar las magias y seres caóticos del vacío, doblegándolos a nuestra voluntad.$b$bTe mostraré cómo atar a un sirviente a tu voluntad, pero primero debes traerme tres cráneos de los esqueletos traquebones cerca de la herrería abandonada.",
    ["O"] = "Lleva a Venya Marthand 3 Cráneos de Jaula de Cascabel.",
    ["T"] = "Traspasando el Velo",
  },
  [1471] = {
    ["D"] = "¿Seguro que has visto el círculo de invocación debajo de nosotros? Solo allí podrás invocar a un abisario no vinculado.$B$BUsando estas runas, podrás invocarlo, y luego debes dominarlo mediante la fuerza de las armas y la magia.$B$BCuando esté sometido, estará bajo tu control. Ven a mí entonces, y te explicaré el hechizo de invocación, para que pueda servirte para cualquier fin que desees.",
    ["O"] = "Usando las Runas de Invocación, invoca y somete a un abisario, luego devuelve las Runas de Invocación a Carendin Halgar en el Barrio de la Magia de Entrañas.",
    ["T"] = "El Vínculo",
  },
  [1472] = {
    ["D"] = "Una súcubo es una devoradora de almas, destructora de corazones, tentadora de hombres. Una criatura de profundo mal y de mente singular.$B$BNo puede ser traída a nuestro mundo sin un estímulo.$B$BAsí que usamos su propia fuerza en su contra. Con los corazones de hombres buenos, hombres puros, hombres amorosos, la súcubo no podrá resistirse y cruzará a nuestro mundo. Entonces puedes dominarla y hacerla tuya.$B$BHabla con mi asistente, Godrick Farsan, dentro del Templo. Él te ayudará a encontrar tu cebo.",
    ["O"] = "Habla con Godrick Farsan en el Templo de los Condenados.",
    ["T"] = "Devorador de Almas",
  },
  [1473] = {
    ["D"] = "Parece que el consenso es que estás listo para aprender a invocar un abisario.$b$bUn ser creado del caos que yace más allá, si invocas uno pero no puedes controlarlo, serás destrozado.$b$bEl primer abisario fue invocado por Egalin, y en su grimorio se explica el proceso.$b$bDesafortunadamente, nos fue robado por un agente de la Cruzada Escarlata que lo tiene escondido en su base en una torre al oeste. Tráemelo y te impartiré el conocimiento.",
    ["O"] = "Recupera el Grimorio de Egalin y llévaselo a Carendin Halgar en el Templo del Damnado.",
    ["T"] = "Criatura del Vacío",
  },
  [1474] = {
    ["D"] = "Usando estos corazones, lanza un hechizo en el círculo de invocación para abrir un portal para que la súcubo pase. Tus poderes son mayores ahora, y necesitarás todos ellos para derrotar a la súcubo. $b$bCuidado con sus encantos, $n, muchas mentes fuertes han sido atadas a su voluntad. Cuando la hayas vencido, la súcubo será devuelta al vacío, y te enseñaré el hechizo para invocarla a tu voluntad.",
    ["O"] = "Usando los Corazones Puros, invoca y somete a una súcubo, luego devuelve los Corazones Puros a Carendin Halgar en el Barrio de la Magia de Entrañas.",
    ["T"] = "El Vínculo",
  },
  [1475] = {
    ["D"] = "Según la leyenda, el Templo de Atal\'Hakkar es un santuario sagrado dedicado al antiguo dios, Hakkar el Desgarrador de Almas. Mantenido por una tribu viciosa de trolls llamados los Atal\'ai, se rumorea que el templo es impenetrable.$B$BAhora hundido bajo el Lago de las Lágrimas, recuperar artefactos del templo es aún más difícil. Pero eso es lo que necesito que hagas, $n.$B$BAyúdame recolectando las Tablillas Atal\'ai intactas. Tráemelas y regresaré al Gremio de Expedicionarios como un héroe. Juntos compartiremos la gloria.",
    ["O"] = "Reúne 10 Tablillas Atal\'ai para Brohann Barriga de Barril en Ventormenta.",
    ["T"] = "En el Templo de Atal\'Hakkar",
  },
  [1476] = {
    ["D"] = "¿Hombres de honor y bondad? ¿Existe tal hombre?$b$bTomó mucha investigación, pero a través de fragmentos de historias escuchadas, logré encontrar dos hombres que deberían servir.$b$bEl primero es Dalin Forgewright, quien se ha dedicado al cuidado de los refugiados de Lordaeron, a quienes sus parientes rechazan en el Muro de Greymane.$b$bEl otro es Comar Villard, un hombre que permanece fiel a su amor perdido, vagando por los Humedales en busca de una señal de su esposa.$b$bEntrega sus corazones a Carendin. Pero primero, déjame mostrarte tu presa.",
    ["O"] = "Lleva los corazones de Dalin Forgewright y Comar Villard a Carendin Halgar en el Templo del Damnado.",
    ["T"] = "Corazones de los Puros",
  },
  [1477] = {
    ["D"] = "Nuestros magos están realizando investigaciones en el Castillo de Nethergarde en las Tierras Devastadas, investigaciones vitales para nuestra comprensión de los Portales Oscuros. Requieren suministros casi constantes, pero nuestra línea de suministro a Nethergarde es tenue.$B$BDebes ayudar a asegurar que los suministros lleguen a Nethergarde. Informa al Vigilante Sorigal en Villa Oscura y haz lo que te pida. Esta tarea te llevará a tierras hostiles, $N. Prepárate.",
    ["O"] = "Habla con el Vigía Maestro Sorigal.",
    ["T"] = "Suministros Vitales",
  },
  [1478] = {
    ["D"] = "Ah, $n! Acabo de recibir noticias de Carendin Halgar de que le gustaría verte para más entrenamiento.$B$BDeberías estar orgulloso de ti mismo, $n. No todos los días el Maestro Halgar se toma la molestia de instruir personalmente a un joven brujo.$B$BPodrás encontrarlo en el Barrio de la Magia de Entrañas, cerca del Templo de los Condenados.",
    ["O"] = "Habla con Carendin Halgar en Entrañas.",
    ["T"] = "La Llamada de Halgar",
  },
  [1479] = {
    ["D"] = "He oído historias sobre cómo los elfos nocturnos tienen su banco en un árbol—¡uno que parece un oso! ¡Eso es genial! Los elfos nocturnos son geniales por cómo les gusta la naturaleza y todo eso, pero los osos son simplemente increíbles.$B$B¿Me llevarías a ver el árbol-oso en Darnassus, por favor? Hay un barco que nos llevará cerca de allí, y no debería tomar mucho tiempo. ¡Por favor, por favor, por favor!",
    ["O"] = "Lleva al huérfano al banco de Darnassus. El banco en sí está ahuecado en un árbol conocido como la Rama de los Eternos.",
    ["T"] = "La Rama de los Eternos",
  },
  [1480] = {
    ["D"] = "La piel desgarrada huele a podrido y es sorprendentemente flexible para algo que parece seco y frágil.$B$BMientras la desenrollas, puedes ver claramente las marcas rojas de algún tipo de lenguaje en un lado. En la parte inferior de la piel hay un símbolo muy claro que adivinas que es una firma de algún tipo.$B$BTal vez Maurin podría decirte más al respecto.",
    ["O"] = "Lleva la Piel de Demonio Desollado a Maurin Bonesplitter en Desolace.",
    ["T"] = "El Corruptor",
  },
  [1481] = {
    ["D"] = "Creo que puedo encontrar a este señor demonio con los reactivos adecuados.$B$BSi es tan poderoso como sospecho, necesitaré objetos recolectados de criaturas mortales. Pero actúas en nombre del Jefe de Guerra, así que estoy seguro de que aceptarás mi tarea.$B$BPrimero, tendré que romper cualquier protección que tenga contra la adivinación. Para esto, necesitaré el cuero cabelludo de un sátiro, pero no de cualquier sátiro. El cuero cabelludo debe provenir de un acechador sombrío Furiaodio.$B$BBúscalos entre las ruinas antiguas en el noreste de Desolace.",
    ["O"] = "Lleva un Cuero Cabelludo de Acechasombras a Maurin Rompehuesos en Desolace.",
    ["T"] = "El Corruptor",
  },
  [1482] = {
    ["D"] = "Un cristal de oráculo serviría bien. Son creados por los naga para que sus oráculos los usen.$B$BAl noroeste de aquí, a lo largo de la costa, hay un vasto arrecife de coral que los naga han tomado como su hogar cuando están cerca de la orilla. Puedes encontrar a sus oráculos allí. Los Filoescama son mortales y no reaccionarán amablemente a tus intrusiones, así que ten cuidado y date prisa.$B$BCuando regreses, veremos cómo encontrar a este señor demonio Azrethoc.",
    ["O"] = "Lleva un Cristal de Oráculo a Maurin Rompehuesos en Desolace.",
    ["T"] = "El Corruptor",
  },
  [1483] = {
    ["D"] = "La Venture Co. se llevó a muchas de las mejores mentes goblins, pero de vez en cuando tenemos la oportunidad de recuperar algunas.$B$BHace un tiempo, la sucursal de Venture Co. en las Montañas de Stonetalon contrató a Gerenzo Wrenchwhistle, ¡un gnomo! Su otro ingeniero, Ziz Fizziks, estaba tan sorprendido por esto que me rogó que le encontrara un nuevo empleo con el Sindicato de Manitas. Lo puse en algunas tareas, pero podría necesitar ayuda.$B$B¿Tal vez podrías echarle un vistazo por mí? Está estacionado en el borde occidental del Desfiladero Cortaviento.",
    ["O"] = "Habla con Ziz Fizziks en la Cresta Cortaviento.",
    ["T"] = "Ziz Fizziks",
  },
  [1484] = {
    ["D"] = "Esta criatura es mucho más peligrosa de lo que había anticipado. Sin duda, Takata cuestiona mis habilidades para enfrentarme a tal criatura, y sería un insulto a su honor si no fuera informado sobre lo que acaba de suceder.$B$BHabla con el espadachín, dile que estás dispuesto a matar al demonio si él lo permite, pero no presiones demasiado. Es mejor si él piensa que es su propia idea.",
    ["O"] = "Habla con Takata Hojacero en Desolace.",
    ["T"] = "El Corruptor",
  },
  [1485] = {
    ["D"] = "Esos brujos que vinieron antes que nosotros han ganado la desconfianza de la Horda, y aunque somos tolerados, no somos amados.$b$bEsto no ha unido a los brujos, sin embargo, ya que muchos eligen vivir recluidos, jugando con sus magias en soledad. No trataré de guiarte por un camino u otro, sino solo ayudarte en tu maduración.$b$bAntes de enseñarte el arte de la invocación, tráeme seis cabezas de los viles familiares de la cueva al noreste.",
    ["O"] = "Lleva 6 Cabezas de Familiar Vil a Ruzan.",
    ["T"] = "Familiares Viles",
  },
  [1486] = {
    ["D"] = "Mientras Naralex descendía más profundamente en su pesadilla, una extraña raza de bestias surgió desde las profundidades de Los Baldíos hacia las Cuevas de los Lamentos.$b$bEstas criaturas desviadas tienen propiedades extrañas y de otro mundo. Aunque son de naturaleza maligna, en mi opinión, algo bueno puede surgir de su existencia aquí en Kalimdor. Creo que sus pieles serán de particular utilidad en el arte del peletero.$b$bSi te sientes capaz, aventúrate en las cuevas y recupera algunas pieles desviadas.",
    ["O"] = "Nalpak en las Cuevas de los Lamentos quiere 20 Pieles Desviadas.",
    ["T"] = "Pieles Desviadas",
  },
  [1487] = {
    ["D"] = "Naralex tenía un objetivo noble.$b$bNuestro gran líder aspiraba a entrar en el Sueño Esmeralda y ayudar a regenerar estas tierras áridas en el frondoso bosque que alguna vez fue. Pero algo salió terriblemente mal.$b$bEl sueño de Naralex se convirtió en una pesadilla y criaturas corruptas comenzaron a habitar las cavernas.$b$bMientras algunos Discípulos de Naralex buscan despertar a nuestro maestro, mi preocupación es deshacer estas cuevas de las bestias malignas.$b$bAventúrate en las cavernas, $n, y erradica las crías desviadas.",
    ["O"] = "Ebru en las Cuevas de los Lamentos quiere que mates 7 Devastadores Descarriados, 7 Víbora Descarrriada, 7 Vagantes Descarrriados y 7 Colmillos del Terror Descarrriados.",
    ["T"] = "Erradicación Desviada",
  },
  [1488] = {
    ["D"] = "Aunque no tengo conocimiento de este Lord Azrethoc, estoy de acuerdo con Maurin, debe ser detenido, especialmente si está intentando ganar influencia sobre la Hojargenta para sus propios fines.$B$BYa has demostrado que deseas ayudar al Jefe de Guerra, y confiaría en ti para reunir un grupo y matar al señor demonio y a su sirviente brujo.$B$BCuando estés listo, dirígete directamente al sur y regresa conmigo cuando la amenaza haya terminado.",
    ["O"] = "Mata a Lord Azrethoc y Jugkar Grim\'rod y regresa con Takata Hojacero en Desolace.",
    ["T"] = "El Corruptor",
  },
  [1489] = {
    ["D"] = "Las conchas que me trajiste tienen una mancha, una que nunca antes había visto. Estoy seguro de que el agua de los Oasis es la causa, y puedo sentir la confusión que causan en esta tierra. Es como si la tierra soñara y no pudiera despertar.$B$BLas Cuevas de los Lamentos deben ser la fuente de la mancha. Pero antes de que vayas allí, habla con Hamuul Runatótem, el druida anciano. Le envié un mensaje sobre los acontecimientos en los Baldíos. Él es sabio y puede ofrecerte consejo.$B$BLo encontrarás en Cima del Trueno, en el Alto de los Ancestros.",
    ["O"] = "Habla con Hamuul Runatótem.",
    ["T"] = "Hamuul Runetotem",
  },
  [1490] = {
    ["D"] = "Entré en la tierra de los sueños y soñé con un culto de druidas que habitan en las Cuevas de los Lamentos. Vi que sus objetivos alguna vez fueron nobles, pero han perdido su camino. Ahora son sirvientes de la corrupción que se filtra a través de los Baldíos.$B$BSoy viejo y mi fuerza falla. No pude permanecer mucho tiempo en ese lugar de ensueño y sé poco más sobre los druidas corruptos. Pero Nara es joven y su espíritu es feroz. Ella permaneció en el sueño cuando yo huí.$B$BHabla con Nara. Ella está en la tienda, allá.",
    ["O"] = "Habla con Nara Crin Salvaje en Cima del Trueno.",
    ["T"] = "Nara Pezuña Salvaje",
  },
  [1491] = {
    ["D"] = "Bien, así que los cuernos de raptor no funcionaron tan bien. Pero está bien, ¡nadie se hizo rico en su primer intento! Y además, creo que encontré algo que funcionará aún mejor en mis bebidas inteligentes.$B$BHay una sustancia llamada Esencia Lamentosa, y creo que es la causa de las extrañas plantas y animales en los Baldíos. ¡Consígueme algo y lo probaré en mi bebida!$B$BHe oído que la esencia se acumula en criaturas ectoplásmicas en las Cuevas de los Lamentos al oeste. Caza esas criaturas y recoge la esencia.",
    ["O"] = "Lleva 6 Porciones de Esencia Lamentosa a Mebok Mizzyrix en Ratchet.",
    ["T"] = "Bebidas Inteligentes",
  },
  [1492] = {
    ["D"] = "Necesito enviar una caja de pociones, reactivos y otros artículos a mis asociados en el Viejo Mundo. Tengo poco tiempo para llevarlos personalmente al muelle de Ratchet, y la última caravana ya se ha ido.$b$bSi pudieras, por favor, llévalo al Capataz Dizzywig. Él se asegurará de que se coloque en el próximo barco a la Cala Agua Negra.",
    ["O"] = "Lleva el Crate Seguro a Wharfmaster Dizzywig en Ratchet.",
    ["T"] = "Maestro del Muelle Dizzywig",
  },
  [1498] = {
    ["D"] = "Como guerrero, una de nuestras tareas más cruciales es... la protección. Somos el escudo de la Horda, y mantenemos a salvo a nuestros hermanos más débiles. Si vas a unirte a nuestras filas, entonces debes demostrarme tu valía.$B$BVe a Cresta del Trueno en Durotar, donde viven los ocultos del relámpago y los lagartos del trueno. ¡Caza a las bestias! ¡Muéstrame que eres más duro que ellas! Enfrenta sus ataques y tráeme un montón de sus escamas chamuscadas como prueba.$B$BHaz esto, y entonces te enseñaré.",
    ["O"] = "Lleva 5 Escalas Chamuscadas a Uzzek en el Puesto de Vigilancia Lejano en Los Baldíos.",
    ["T"] = "Camino de la Defensa",
  },
  [1499] = {
    ["D"] = "Zureetha ha estado enviando novatos como tú para ayudar a reducir el número de criaturas en la cueva cerca de donde luchaste contra los familiares viles.$b$bCreo que sería bueno para ti ganar experiencia ayudándola. Asegúrate de decirle que ayudaste con el problema de los diablillos, probablemente tendrá una recompensa para ti.",
    ["O"] = "Habla con Zureetha Fargaze.",
    ["T"] = "Familiares Viles",
  },
  [1500] = "_",
  [1501] = {
    ["D"] = "Quedan tan pocos brujos... Arriesgamos mucho, pero los riesgos están justificados. Debes alcanzar rápidamente la madurez en tu habilidad si quieres ser útil a la Horda y al Jefe de Guerra.$b$bEs hora de que aprendas un nuevo hechizo de invocación.$b$bPrimero, debes recuperar la Tablilla de Verga, que fue robada por brujos de la Hojargenta y ahora se guarda en su guarida en Roca del Cráneo al este de la ciudad. Con la tablilla, te mostraré las runas necesarias para invocar un abisario.",
    ["O"] = "Recupera la Tablilla de Verga para Gan\'rul Ojosangre en Orgrimmar.",
    ["T"] = "Criatura del Vacío",
  },
  [1502] = {
    ["D"] = "Es vital que el arma de un guerrero sea de calidad. Y si hay un orco que pueda forjar armas de calidad, ese orco es Thun\'grim Fuegogaze. Es un ermitaño que vive en lo alto de las colinas al este de El Cruce.$B$BSi buscas un arma, guerrero, entonces deberías buscar a Thun\'grim. Para encontrarlo, primero viaja a El Cruce hacia el oeste, luego dirígete al este hacia las colinas. Su campamento está en lo alto de una colina.",
    ["O"] = "Habla con Thun\'grim Mirada de Fuego en los Baldíos.",
    ["T"] = "Thun\'grim Mirallamas",
  },
  [1503] = {
    ["D"] = "Te daré un arma, $N, pero primero debes ayudarme. Los Colmillarrantes al noroeste vinieron a mi campamento hace unas noches. No quisieron enfrentarme en combate, pero saben que mi vista es mala: ¡algunos me distrajeron con gruñidos mientras otros me robaban! ¡Los cobardes!$B$BSe llevaron un cofre de hierro, y dentro había algunas barras de acero forjado. Si puedes recuperarlas, te daré el arma que buscas.$B$BBusca el cofre de hierro dentro de los campamentos de los jabalíes y regresa con las barras de acero forjado.",
    ["O"] = "Lleva las Barras de Acero Forjado a Thun\'grim Firegaze en Los Baldíos.",
    ["T"] = "Acero Forjado",
  },
  [1504] = {
    ["D"] = "Ahora, observa la Tablilla de Verga, ya que contiene los glifos que necesitarás para la invocación. Llévalos al círculo en la tienda de Neeru Hojafuego al otro lado de la caverna. Allí podrás llamar al abisario.$b$bDebes derrotarlo usando cualquier medio físico y mágico que tengas a tu disposición. Cuando esto se complete, regresa a mí con los glifos de invocación y te mostraré cómo puedes atar a la criatura a tu voluntad.",
    ["O"] = "Usando los Glifos de Invocación, invoca y somete a un abisario, luego devuelve los Glifos de Invocación a Gan\'rul Bloodeye en Orgrimmar.",
    ["T"] = "El Vínculo",
  },
  [1505] = {
    ["D"] = "La Horda honra a sus guerreros porque su fuerza es incuestionable. Pero los verdaderamente grandes guerreros también son grandes estudiantes.$B$BBusca a Uzzek. Es un veterano de muchas batallas y ahora instruye a jóvenes guerreros en las formas del combate. Lo encontrarás al sur del Puesto de Vigilancia, la guarnición orca en la frontera entre los Baldíos y Durotar.",
    ["O"] = "Habla con Uzzek en el Puesto de Vigilancia Lejano.",
    ["T"] = "Veterano Uzzek",
  },
  [1506] = {
    ["D"] = "Tú allí. $n, ¿verdad?$b$bEspero que tu horario no esté ocupado, porque Gan\'rul quiere verte de inmediato. Lo encontrarás con los brujos mayores en la Grieta de las Sombras.$b$bBasado en mis tratos pasados con Gan\'rul, tu convocatoria es algo muy bueno o algo muy malo.",
    ["O"] = "Habla con Gan\'rul Ojosangre en Orgrimmar.",
    ["T"] = "La Llamada de Gan\'rul",
  },
  [1507] = {
    ["D"] = "Ha llegado el momento de que aprendas más sobre las magias de invocación. Aprenderás a invocar a una súcubo, una criatura de increíbles poderes mentales. Puede adormecer a aquellos de mente débil en su servidumbre, ¡pero no temas! Estoy seguro de que estarás a la altura del desafío.$B$BUna súcubo solo puede ser invocada cuando se la atrae con un objeto que representa gran devoción y amor. Te dejo a ti encontrar tal objeto.$B$B¿Una sugerencia? Habla con Cazul... Parece que hay poco que el antiguo brujo no sepa.",
    ["O"] = "Habla con Cazul en la Grieta de la Sombra.",
    ["T"] = "Devorador de Almas",
  },
  [1508] = {
    ["D"] = "Hmm... Pero, por supuesto, no es por eso que has venido a hablar conmigo.$B$BSi Gan\'rul te ha enviado a mí, debe ser porque vas a invocar a una súcubo y necesitas un objeto para usarlo como incentivo.$B$BHmm... Si confías en que un ciego te guíe, $n, entonces escucha. En los acantilados de Orgrimmar, al oeste de la torre de los jinetes del viento, vive Zankaja. Ella espera el regreso de su compañero.$B$BVe a verla.",
    ["O"] = "Habla con Zankaja en Orgrimmar.",
    ["T"] = "Cazul el Ciego",
  },
  [1509] = {
    ["D"] = "Mi compañero, Dogran, fue enviado recientemente a El Cruce por deber. Era un ascenso, y estaba muy orgulloso.$b$bYo estaba triste, por supuesto, pero no se lo dejé saber. Y también preocupada. Los Baldíos son un lugar más peligroso que Orgrimmar, y temía lo peor.$b$bAhora ha pasado una semana desde que debería haber regresado, y no he recibido noticias de él...$b$b¿Quizás podrías ir a El Cruce y ver si tienen alguna noticia? Trabajaba con Gazrog, quien debería estar estacionado allí.",
    ["O"] = "Habla con Gazrog en El Cruce.",
    ["T"] = "Noticias de Dogran",
  },
  [1510] = {
    ["D"] = "Estaba estacionado en el Campamento Taurajo, donde hemos estado tratando de erradicar a los quilboar. Lideró a unos cuantos grunts a uno de sus campamentos, donde resultó gravemente herido. Los otros regresaron e intentaron rescatarlo. Estaba en mal estado, sus heridas se infectaron, así que temían moverlo sin curarlo primero.$b$bEnviaron un mensaje aquí, solicitando una poción curativa de los trolls en Malaka\'jin al pie de las Montañas de Petravista. Enviamos a alguien a buscarla, pero aún no ha regresado.",
    ["O"] = "Habla con Ken\'zigla en Malaka\'jin.",
    ["T"] = "Noticias de Dogran",
  },
  [1511] = {
    ["D"] = "Imaginé que podría venir otro a buscar la poción, así que la preparé de todos modos.$b$bAquí tienes, $n. ¡Ten en cuenta que solo debes darle la mitad! Eso le dará suficiente fuerza para moverse sin desangrarse. Espera una noche y luego dale la otra mitad.$b$bDe todos modos, querrás llevar esto a Logmar en Campamento Taurajo. Por lo que me contó el primero, debería tener una idea de dónde está este Dogran.",
    ["O"] = "Lleva la Poción de Ken\'zigla al Grunt Logmar en Campamento Taurajo.",
    ["T"] = "El Brebaje de Ken\'zigla",
  },
  [1512] = {
    ["D"] = "Zankaja es joven y hermosa. Estar encadenada a un inválido... No permitiré que sufra esa injusticia.$b$bDéjame, $n. Y gracias. Puede que termine mi vida en una gloriosa batalla, en lugar de en un cuerpo sin vida.$b$bY por favor, te pediría que no le cuentes a mi pareja sobre esto... No quiero que tenga remordimientos en su conciencia.",
    ["O"] = "Lleva el Colgante Cubierto de Tierra a Gan\'rul Bloodeye en Orgrimmar.",
    ["T"] = "El Regalo del Amor",
  },
  [1513] = {
    ["D"] = "Llévale esto al círculo de invocación en la tienda de Neeru, $n. Invoca a una súcubo vil, luego demuéstrale tu dominio. Creo en la fuerza de tu magia. Como siempre, sin embargo, sé cauteloso, porque el orgullo y la arrogancia han superado a muchos brujos prometedores.$b$bCuando la hayas derrotado, devuélveme el colgante para que pueda darle la noticia a Zankaja.",
    ["O"] = "Usando el Colgante de Dogran, invoca y somete a una súcubo, luego devuelve el Colgante de Dogran a Gan\'rul Bloodeye en Orgrimmar.",
    ["T"] = "El Vínculo",
  },
  [1514] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Vientoflor Corrupto",
  },
  [1515] = {
    ["D"] = "Has llegado hasta aquí, así que si quieres ver esto hasta el final, no te detendré. No puedo darte más mano de obra, estarás solo.$b$bDogran está siendo retenido en un campamento de jabalíes al norte de aquí. Busca un grupo de dos chozas. Estará fuertemente custodiado, es decir, si no lo han matado todavía, o si no ha sucumbido a sus heridas.$b$bBuena suerte.",
    ["O"] = "Lleva la Poción de Ken\'zigla a Dogran en los Baldíos.",
    ["T"] = "El Cautiverio de Dogran",
  },
  [1516] = {
    ["D"] = "El momento es ahora, joven $c. Has crecido fuerte y tu espíritu perdura como la tierra. Seguir el camino del chamán muestra que tienes sabiduría incluso antes de demostrarlo.$B$BEl elemento de la tierra guiará tu destino, volviéndose parte de ti si estás listo. Pero debes pararte ante la tierra misma. Si estás listo, entonces verás cosas que solo los chamanes conocen.$B$BBusca a los Acechadores Vil al norte, y toma de ellos dos de sus pezuñas. Regresa conmigo entonces, y hablaremos más sobre tu futuro.",
    ["O"] = "Lleva 2 Pezuñas de Acechador Vil a Canaga Earthcaller en el Valle de los Retos.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1517] = {
    ["D"] = "Un sapta es una bebida creada para unir nuestros espíritus a los elementos. Fusionada con magia, la poción permitirá a los de voluntad fuerte ver los elementos como nadie más puede. Tu espíritu estará conectado al elemento para el que se creó el sapta, y solo unos pocos chamanes conocen la receta.$B$BEl sapta siempre se bebe en un lugar sagrado, y nunca en ningún otro lugar, recuérdalo.$B$BBusca la Roca del Espíritu al suroeste de aquí, se encuentra al final del Sendero Oculto. Bebe tu sapta allí, y... bueno, ya verás.",
    ["O"] = "Encuentra la Roca de los Espíritus y bebe la Sapta Terrenal.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1518] = {
    ["D"] = "Toma este cuarzo áspero de mí y llévaselo a Canaga Clamor de Tierra como prueba de nuestro encuentro. Él creará un tótem para ti, y esta piedra será su corazón. Por pequeño que parezca, descubrirás que el tamaño no importa, y que incluso las cosas más pequeñas pueden durar más que muchas otras.$B$BSé paciente, $N. Mantente fuerte. Y busca sabiduría. Esto es lo que la tierra te pide. Al final, te convertirás en uno con la tierra: respétala como ella te respetará a ti.",
    ["O"] = "Lleva el Cuarzo áspero a Canaga Earthcaller en el Valle de los Retos.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1519] = {
    ["D"] = "Ha llegado el momento, joven $c. Tu cuerpo envejece y se fortalece, y tu espíritu perdura como la tierra. Lo veo dentro de ti: ya tienes sabiduría.$B$BLa tierra guía a nuestra gente, y nos habla, como lo hará contigo en su momento. Pero debes ser probado. Si estás listo, entonces verás cosas que solo nuestra clase conoce.$B$BBusca a los chamanes Jabaespina en su barranco al este, y toma de ellos un ungüento que usan en sus rituales. Regresa conmigo solo cuando tengas suficiente para tu propia poción.",
    ["O"] = "Lleva 2 aplicaciones de Ungüento Ritual al Vidente Ravenfeather en Campamento Narache.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1520] = {
    ["D"] = "Un sapta es una bebida creada para unir nuestros espíritus a los elementos. Fusionada con magia, la poción permitirá a los de mente abierta ver los elementos como nadie los ha visto antes. Tu espíritu estará conectado para siempre al elemento para el que se creó el sapta, y solo unos pocos de nuestra clase conocen los secretos de su creación.$B$BEl sapta siempre se bebe en un lugar sagrado, y nunca en ningún otro lugar.$B$BBusca el santuario chamánico al este de aquí y bebe del sapta.",
    ["O"] = "Encuentra Kodo Rock y bebe la Sapta Terrenal.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1521] = {
    ["D"] = "Toma este cuarzo áspero de mí y llévaselo a la Vidente Pluma de Cuervo como prueba de nuestro encuentro. Ella creará un tótem para ti, y esta piedra será su corazón. Por pequeño que parezca, descubrirás que el tamaño no importa, y que incluso las cosas más pequeñas pueden durar más que montañas.$B$BSé paciente, $N. Mantente fuerte. Y busca sabiduría. Esto es lo que la tierra te pide. Al final, te convertirás en uno con la tierra: respétala como ella te respetará a ti.",
    ["O"] = "Lleva el Cuarzo áspero al Vidente Ravenfeather en Campamento Narache.",
    ["T"] = "La Llamada de la Tierra",
  },
  [1522] = {
    ["D"] = "El fuego está contigo, mon. Veo que ya eres fuerte en los caminos de la tierra, y creo que estás listo para hablar con Kranal.$B$BKranal vive al norte de El Cruce en Los Baldíos. Es fuerte en los caminos de los elementos y puede enseñarte los caminos del fuego... si crees que estás listo.$B$BEs fácil de encontrar, solo no vayas demasiado lejos, si vas demasiado lejos, te encontrarás en las tierras de los elfos.",
    ["O"] = "Encuentra a Kranal Fiss en Los Baldíos.",
    ["T"] = "La Llamada del Fuego",
  },
  [1523] = {
    ["D"] = "Ha pasado mucho tiempo desde que fuiste probado por última vez, $N. Has mostrado paciencia hasta ahora en tus estudios, pero ha llegado el momento de que aprendas más. Ha llegado el momento de que aprendas sobre la destrucción... y el caos. El elemento del fuego es tuyo para entenderlo, si estás preparado para pagar por ello.$B$BBusca a Kranal Fiss en los Baldíos. Vive en una pequeña vivienda al norte de El Cruce, pero ten cuidado, si has llegado a las tierras de los elfos nocturnos, has ido demasiado lejos.",
    ["O"] = "Encuentra a Kranal Fiss en Los Baldíos.",
    ["T"] = "La Llamada del Fuego",
  },
  [1524] = {
    ["D"] = "Eres lo suficientemente poderoso para manejar el tótem de fuego, pero eso no significa que estés listo.$B$BA lo largo del Río Furia del Sur en Durotar, antes del Gran Mar, hay un sendero oculto que lleva a uno de los picos más altos de la tierra. Encuentra el camino hacia la cima y encontrarás uno de nuestros santuarios sagrados vigilado por Telf Joolam: el Santuario de la Llama Durmiente.$B$BLleva esta antorcha a él: marca que intentas domar el fuego. Devuélvela encendida por el brasero en el santuario y veré que te den el tótem de fuego.",
    ["O"] = "Lleva la Antorcha de la Llama Latente a Telf Joolam en Durotar.",
    ["T"] = "La Llamada del Fuego",
  },
  [1525] = {
    ["D"] = "Pero no viniste aquí para una lección de historia, ¿verdad? Viniste por la Llama Eterna y el tótem de fuego.$B$BEl primer paso es crear un sapta y unirte a la llama.$B$BPuedo crear el sapta de fuego para ti si me traes dos objetos. El primero es alquitrán de fuego, un objeto simple que suelen llevar los lanzadores de hechizos Razormane en el este de Los Baldíos.$B$BEl segundo objeto será más peligroso: una bolsa de componentes de uno de los cultistas de la Espada de Fuego. Puedes encontrarlos en una cueva sobre los barrancos al noreste de Colina Cortazar.",
    ["O"] = "Lleva 1 Alquitrán de Fuego y 1 Bolsa de Componentes a Telf Joolam en Durotar.",
    ["T"] = "La Llamada del Fuego",
  },
  [1526] = {
    ["D"] = "El fuego sapta está terminado. Ha llegado el momento de que enfrentes la llama. Usa el sapta en el santuario y dirígete a la cima de la montaña para enfrentar a la manifestación que allí se encuentra. No te atacará hasta que estés listo, pero se dará cuenta de que puedes verlo.$B$B¡Acaba con él rápidamente, $N, porque solo crecerá en poder cuanto más tardes en matarlo! Una vez que lo hayas derrotado, coloca sus brasas ardientes dentro del brasero y enciende la antorcha.",
    ["O"] = "Derrota a la Manifestación Menor del Fuego y coloca la Ascua Ardiente en el brasero en la cima del Santuario de la Llama Eterna.",
    ["T"] = "La Llamada del Fuego",
  },
  [1527] = {
    ["D"] = "Con la antorcha firmemente en la mano, te alejas del brasero y miras hacia Durotar. Sientes una emoción que tira de tu espíritu mientras el viento se levanta y obliga a la llama a bailar violentamente.$B$BComienzas a bajar por el camino de regreso a los Baldíos en busca de Kranal Fiss.",
    ["O"] = "Lleva la Antorcha de la Llama Eterna a Kranal Fiss en Los Baldíos.",
    ["T"] = "La Llamada del Fuego",
  },
  [1528] = {
    ["D"] = "El agua está en tu futuro, mon. Veo que ya eres fuerte en las formas del fuego, y creo que estás listo para hablar con Islen.$B$BIslen vive a lo largo de la costa de Los Baldíos. Si te diriges al sur a lo largo del Río Furia del Sur, no puedes perderte: ¡busca el gran tiburón! Ella es fuerte en las formas de los elementos y puede enseñarte las formas del agua... si crees que estás listo.$B$BEs fácil de encontrar, solo no vayas demasiado al sur: está justo más allá de Ratchet.",
    ["O"] = "Encuentra a Islen Vidente de las Aguas en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [1529] = {
    ["D"] = "Ha pasado mucho tiempo desde que fuiste probado por última vez, $N. Has mostrado paciencia hasta ahora en tus estudios, pero ha llegado el momento de que aprendas más. Ha llegado el momento de que aprendas sobre la pureza del agua.$B$BBusca a Islen Waterseer en los Baldíos. Está a lo largo de la costa este, al sur del Río Furia del Sur y Ratchet. La encontrarás allí en su cabaña de pesca. Buena suerte, $N.",
    ["O"] = "Encuentra a Islen Vidente de las Aguas en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [1530] = {
    ["D"] = "Si hay una lección que debes aprender sobre el agua, es esta: el agua significa renacimiento.$B$BSu poder fluye, erosiona y limpia todo tipo de cosas. Encontrarás que su capacidad para sanar y reponer es incomparable, pero solo cuando es pura.$B$BCuando entiendas que el agua, cuando está contaminada, puede devastar todo lo que toca, apreciarás cuán vital es como recurso.$B$BSi deseas el sapta de agua, encuentra a Brine en lo profundo de los Baldíos del Sur. Su hogar es constantemente molestado por los quilboar allí.",
    ["O"] = "Encuentra a Brine en los Baldíos del Sur.",
    ["T"] = "La Llamada del Agua",
  },
  [1531] = {
    ["D"] = "Estás progresando, mon. Estás listo para demostrar que eres digno del tótem de aire, pero primero, debes encontrar a Prate Vidente de las Nubes en las Mil Agujas. Ella estará dispuesta a decirte cómo obtener tu tótem de aire si la tratas con suficiente respeto.$B$BVe hacia el sur a través de los Baldíos, y después de llegar a las Mil Agujas, dirígete al este. La encontrarás escondida en un agujero con su novio, jaja, no le digas que la estoy criticando, no se lo tomará muy bien.",
    ["O"] = "Encuentra a Prate Vidente de las Nubes en las Mil Agujas.",
    ["T"] = "La Llamada del Aire",
  },
  [1532] = {
    ["D"] = "Los vientos de Kalimdor corren a tu encuentro y serás contado entre algunos de nuestros más poderosos $ghermanos:hermanas;. Estoy orgulloso de ver que has llegado tan lejos y tienes un futuro tan prometedor.$B$BEl elemento del aire es difícil de controlar, pero tengo fe en que tus habilidades superarán cualquier obstáculo que se te presente.$B$BDirígete al extremo sur de Los Baldíos y entra en Las Mil Agujas. Allí, muy al este, encontrarás a Prate Cloudseer. Ella te enseñará más.",
    ["O"] = "Encuentra a Prate Vidente de las Nubes en las Mil Agujas.",
    ["T"] = "La Llamada del Aire",
  },
  [1534] = {
    ["D"] = "Toma este odre y adéntrate en el frondoso bosque que los elfos nocturnos llaman Vallefresno. Está muy al norte de Los Baldíos.$B$BUna vez que hayas entrado en el bosque, viaja hacia el oeste. A lo largo de las montañas que separan Las Cumbres Tormentosas y Vallefresno, y más allá del Lago Mystral, encontrarás un lugar sagrado: las Ruinas de Polvo Estelar. En el centro del lago encontrarás una pequeña fuente.$B$BTráeme una muestra del agua de la fuente. Será el componente final que necesitarás antes de que te envíe de vuelta con Islen.",
    ["O"] = "Llena el Odre de Agua Azul Vacío en las Ruinas de Polvo Estelar en Vallefresno y regresa con Brine en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [1535] = {
    ["D"] = "Para encontrar comprensión y obtener el sapta de mí, debes buscar solo las formas más puras de agua. Mientras recoges las muestras para mí, toma nota de tu entorno. Sé consciente de las criaturas que dependen del agua, que la protegen. Observa cómo afecta a los bosques y desiertos. Sé testigo de cómo la sustancia misma que sostiene la vida obligará a las criaturas a tomar vidas.$B$BComenzaremos con una tarea simple: debajo de mi choza hay un estanque del preciado líquido. Llena este odre con él y regresa a mí.",
    ["O"] = "Llena el Odre de Agua Marrón Vacío en el abrevadero debajo de la choza de Brine y llévaselo a ella en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [1536] = {
    ["D"] = "Hace mucho tiempo, seguí a algunos compañeros a las Foothills de Trabalomas. Nos defendimos de algunos humanos tenaces que hacen su hogar allí y nos encontramos en un pequeño pueblo que los No-Muertos habían tomado: Molino Tarren.$B$BFue allí donde encontré algo muy peculiar: en el centro del pueblo había un pequeño pozo de agua fresca. No estoy seguro de que los ciudadanos plagados del pueblo sepan su valor - es un pequeño vestigio sagrado en un mar profanado.$B$BTráeme una muestra de esa agua en este cuero de agua.",
    ["O"] = "Llena el Odre de Agua Rojo Vacío en el pozo de Molino Tarren y regresa con Brine en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [1558] = {
    ["D"] = "Forjaz es donde viven todos los enanos y gnomos. Cerca de allí hay un lugar llamado Loch Modan, y los enanos construyeron una ENORME presa allí. Dicen que es una de las cosas más grandes que alguien haya hecho. ¡Guau!$B$B¿Podrías llevarme a Loch Modan y ver la presa allí, por favor? Creo que es la presa de... ¿Piedra... eh... Presa de Piedra? ¡Es grande, eso es seguro! Me gustaría mirar por encima de la gran cascada. ¡Prometo que no me asomaré demasiado!",
    ["O"] = "Lleva al huérfano a la Presa Boulderbram en Loch Modan. Debes llevarlo al medio de la presa para que pueda ver la gigantesca cascada.",
    ["T"] = "La Presa de Petrapunta",
  },
  [1559] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Receta de Bomba Cegadora",
  },
  [1560] = {
    ["D"] = "¡Estoy tan perdido!$B$BMi esposa, Torta, me envió a buscar la cena y debí haber tomado un camino equivocado en algún lugar.$B$B¿Serías tan amable de llevarme de vuelta con Torta? Ella me está esperando justo al sur de Puerto Bonvapor.",
    ["O"] = "Guía a Tooga de vuelta con su esposa, Torta.",
    ["T"] = "La Misión de Tooga",
  },
  [1578] = {
    ["D"] = "Los montañeros de Loch Modan están luchando contra troggs, ¡y están muy ocupados! Necesitan un suministro constante de armaduras y armas nuevas si queremos controlar a esos troggs.$B$B¡Por eso mi colega Thorvald está allí, tratando de mantener a los montañeros bien equipados! Si hicieras algunas hachas de cobre y cinturones de malla de cobre y se los llevaras a Thorvald, él estaría agradecido. Incluso podría compartir su conocimiento contigo.$B$BThorvald está en la torre de vigilancia del sur de Loch Modan.",
    ["O"] = "Lleva 6 Hachas de Cobre y 6 Cinturones de Anillas de Cobre a Thorvald en la torre de vigilancia sur de Loch Modan.",
    ["T"] = "Suministros para el Frente",
  },
  [1579] = {
    ["D"] = "Vine a Auberdine por la tranquilidad. Quiero inventar mis inventos donde haya silencio, porque después de una noche de bebida no me gusta todo el ruido de Forjaz.$B$BAsí que tomé un barco a Auberdine y bebí un poco demasiado en el camino. ¡Dejé caer mi caja de gaffer jacks por la borda! ¡Necesito mis gaffer jacks!$B$BNo sé exactamente dónde los dejé caer, pero si pescas en el océano, podrías tener suerte...",
    ["O"] = "Lleva 8 Gafas de Electricista a Wizbang Torcoliz en Auberdine.",
    ["T"] = "Gaffer Jacks",
  },
  [1580] = {
    ["D"] = "Sabemos que tienes suerte, pero... ¿qué tan suerte eres, $N?$B$BUna noche estaba vagando por los caminos de Costa Oscura. Estaba buscando un buen río o lago para probar mi nuevo señuelo de pato autopropulsado y explosivo, y creo que estaba un poco borracho... porque me desmayé y desperté horas después en Auberdine. ¡Mi cabello estaba chamuscado y mi bolsa de electropalas había desaparecido! ¡Debí haberlas dejado caer en el agua!$B$B¡Necesito esas electropalas! Quizás puedas pescarlas de algún lago o arroyo en Costa Oscura...",
    ["O"] = "Lleva 12 Electropaletas a Wizbang Cranktoggle en Auberdine.",
    ["T"] = "Electrohélices",
  },
  [1581] = {
    ["D"] = "Nuestra tienda de hierbas y alquimia está en auge, pero mi esposo pasa tanto tiempo mezclando pociones que nunca tenemos tiempo el uno para el otro.$B$B¿Puedes ayudarnos?$B$BSi puedes traerme un suministro de elixires previamente preparados, estaré feliz de intercambiar contigo. Tengo algunas hierbas raras que podrían serte útiles.",
    ["O"] = "Lleva 6 Elixires de Fuerza de León y 2 Elixires de Defensa Menor a Syral Bladeleaf en Dolanaar.",
    ["T"] = "Elíxires para los Hojaverde",
  },
  [1582] = {
    ["D"] = "Nosotros en Darnassus, especialmente los artesanos, estamos bastante ocupados. Los jóvenes aventureros están ansiosos por probarse en la naturaleza y necesitan suministros constantemente.$B$BSi puedes ayudarme a completar mis pedidos de artículos de cuero, quizás pueda enseñarte una técnica avanzada de peletería.",
    ["O"] = "Lleva 1 Guante de Cuero con Grabados, 5 Botas de Cuero con Grabados y 5 Capas de Cuero con Grabados a Lotherias en Darnassus.",
    ["T"] = "Chaleco Brillo Lunar",
  },
  [1598] = {
    ["D"] = "¡Tú allí! $n, ¿verdad?$b$bEspero que estés seguro de convertirte en un brujo, porque si no puedes valerte por ti mismo, es probable que te hundas.$b$bTengo que enseñarte, pero primero necesito asegurarme de que seas lo suficientemente fuerte.$b$bHabía un novicio estudiando en la Abadía que huyó para unirse a la Hermandad Defias. ¿Por qué me importa? Bueno, robó algunos libros importantes.$b$bUno de esos resulta ser un libro que he querido para mí, los Poderes del Vacío. Robátelo para mí, lo tienen escondido en su tienda en los Viñedos.",
    ["O"] = "Recupera los Poderes del Vacío para Drusilla La Salle.",
    ["T"] = "El Tomo Robado",
  },
  [1599] = {
    ["D"] = "Debes ser el nuevo brujo del que todos murmuran. Alguien debe haberte tomado cariño si me han pedido que aparte tiempo de mi investigación para comenzar tu entrenamiento.$b$bNo te preocupes, no te lo tendré en cuenta.$b$bEl más simple de los hechizos de invocación que aprenderás es el del diablillo. Sin embargo, antes de impartírtelo, debes demostrar que tienes la capacidad mágica y física necesaria.$b$bTráeme tres amuletos de plumas de los novicios frostmane de la cueva al suroeste.",
    ["O"] = "Lleva 3 Amuletos de Pluma a Alamar Grimm en Yunque de Mar.",
    ["T"] = "Comienzos",
  },
  [1618] = {
    ["D"] = "Los enanos no somos los únicos que luchamos. ¡Nuestros aliados humanos también tienen las manos llenas!$B$BCrestagrana está bajo ataque periódico de los orcos de Roca Negra, y su suministro de equipo siempre es bajo. Si los ayudas, estoy seguro de que su herrero residente, Verner Osgood, te enseñará un truco o dos.",
    ["O"] = "Tormus Forjahonda quiere que le lleves 4 Cinturones de Cobre Rúnicos y 4 Mazos de Cobre Pesados a Verner Osgood en Crestagrana.",
    ["T"] = "Equipando Crestagrana",
  },
  [1638] = {
    ["D"] = "Has demostrado ser un guerrero competente, $N. Pero aún eres joven y tienes mucho que aprender.$B$BHabla con Harry Burlguard, usualmente está en la taberna El Cerdo y el Silbato, en la Ciudad Vieja de Ventormenta. Harry es un veterano y un buen maestro. Él puede enseñarte algunos movimientos que te mantendrán respirando por más tiempo.",
    ["O"] = "Habla con Harry Guardarrama.",
    ["T"] = "Entrenamiento de un Guerrero",
  },
  [1639] = {
    ["D"] = "Sí, puedo enseñarte. Pero primero quiero ver qué movimientos ya tienes.$B$B¿Ves a ese borracho al final de la barra? ¿Bartleby? Quiero que consigas su jarra.$B$BJa, y si conoces a Bartleby, sabes que no te la dará así como así.",
    ["O"] = "Habla con Bartleby.",
    ["T"] = "Bartleby el Borracho",
  },
  [1640] = {
    ["D"] = "¿Quieres qué?? ¡Estás loco!$B$B¡La única manera de obtener esta jarra es arrancándomela de mis fríos y muertos dedos...",
    ["O"] = "Vence a Bartleby, luego habla con él.",
    ["T"] = "Derrotar a Bartleby",
  },
  [1641] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Tomo de Divinidad",
  },
  [1642] = {
    ["D"] = "En todas las cosas, un paladín debe reflejar la Luz que complementa nuestra fuerza. Esforzarse por ser divino para uno de los nuestros no significa que nos esforcemos por la divinidad—nos esforzamos por ser buenos en todas las acciones.$B$BAunque se nos llama a abatir el mal en estos tiempos difíciles, siempre debes recordar que es ayudar a los demás lo que realmente te distinguirá de los ciudadanos de Azeroth. Compasión, paciencia, valentía—estas cosas significan tanto para un paladín como la fuerza en la batalla.$B$BSabe esto bien, y nunca lo olvides.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1643] = {
    ["D"] = "Tu tarea entonces, $N, es encontrar a alguien aquí en Ventormenta que necesite ayuda. Alguien que sea puro de corazón y motivo. Quizás alguien que esté ayudando a otros. Quizás alguien que necesite una guía. En cualquier caso, la persona debe tener intenciones nobles y estar confiando en tu bondad.$B$BHaz esto y regresa a mí cuando hayas terminado. Entonces, discutiremos más sobre tu camino y lo que significa ser un paladín de la Luz.",
    ["O"] = "Busca en el Distrito de Mercaderes de Ventormenta a Stephanie Turner.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1644] = {
    ["D"] = "Benditos sean sus corazones, la mayoría de ellos no tienen familias debido a los ataques de la Plaga en la última década.$B$BMi esposo John recolecta los materiales para que yo haga camisas y pantalones para los niños, pero ni siquiera he tenido tiempo para hacer eso. He estado demasiado concentrada en mantener la comida en sus platos y un techo sobre sus cabezas.$B$B¿Qué dices? ¿Estarías dispuesto a ayudar? Oh, gracias a la Luz, y gracias a ti, $N.$B$BSi pudieras traerme un poco de tela de lino para que pueda coser ropa para los niños, eso me ayudaría mucho.",
    ["O"] = "Lleva 10 Paño de Lino a Stephanie Turner en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1645] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Tomo de Divinidad",
  },
  [1646] = {
    ["D"] = "En todas las cosas, un paladín debe reflejar la Luz que complementa nuestra fuerza. Esforzarse por ser divino para uno de los nuestros no significa que nos esforcemos por la divinidad—nos esforzamos por ser buenos en todas las acciones.$B$BAunque se nos llama a abatir el mal en estos tiempos difíciles, siempre debes recordar que es ayudar a los demás lo que realmente te distinguirá de los ciudadanos de Azeroth. Compasión, paciencia, valentía—estas cosas significan tanto para un paladín como la fuerza en la batalla.$B$BSabe esto bien, y nunca lo olvides.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1647] = {
    ["D"] = "Tu tarea entonces, $N, es encontrar a alguien aquí en Forjaz que necesite ayuda. Alguien que sea puro de corazón y motivo. Quizás alguien que esté ayudando a otros. Quizás alguien que necesite una guía. En cualquier caso, la persona debe tener intenciones nobles y estar confiando en tu bondad.$B$BHaz esto y regresa a mí cuando hayas terminado. Entonces, discutiremos más sobre tu camino y lo que significa ser un paladín de la Luz. Me quedaré aquí en el templo para tu regreso. Otros paladines también necesitarán mi guía.",
    ["O"] = "Busca en el anillo exterior de Forjaz a John Turner.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1648] = {
    ["D"] = "Mi esposa y yo dirigimos un orfanato en Ventormenta, principalmente para niños que han perdido a sus padres debido a los ataques de la Plaga en la última década. Vengo a Forjaz cada mes más o menos para ver si puedo recolectar más suministros de los ciudadanos aquí, y hablar con varios grupos para ver si pueden hacer algo para ayudar.$B$BEste viaje ha sido agonizantemente lento y no muy productivo.$B$BSi pudiera conseguir un poco más de lino para que mi esposa haga algo de ropa, finalmente podría enviar un cargamento a casa.",
    ["O"] = "Lleva 10 Paño de Lino a John Turner en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1649] = {
    ["D"] = "El valor, y todas las virtudes que lo acompañan, son como minerales raros: debes apreciarlos cuando los encuentres por su valor, pero también debes tomarte el tiempo para cosecharlos... refinarlos. Cuando se mezclan correctamente, se forma una aleación fuerte, a veces irrompible incluso por los golpes más poderosos.$B$BEsta fuerza, tu fe, tu valentía, deben ser puestas a prueba. Debes aprender a nunca perder la fe y darte cuenta de que con el poder de la Luz, puedes superar cosas que la mayoría de los demás no pueden.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Valentía",
  },
  [1650] = {
    ["D"] = "¡Bien! Tu falta de vacilación es una señal segura de que estás preparado y ansioso por ponerte a prueba. Que así sea.$B$BEn los mismos extremos del Litoral de Poniente, una pequeña casa domina el océano. Allí vive una pareja, los Stilwell. Jordan es un herrero increíble, y se ha ido a Forjaz para reunirse con los enanos por un tiempo. Pero esto ha dejado a Daphne, su esposa, sola y desprotegida.$B$BLos informes sugieren que los Defias han tomado el control del área, Daphne puede necesitar algo de protección contra los villanos. Ve allí.",
    ["O"] = "Encuentra a Daphne Stilwell en Páramos de Poniente.",
    ["T"] = "El Tomo de Valentía",
  },
  [1651] = {
    ["D"] = "Ahora, volverán pronto, apostaría, así que prepárate.$B$BTengo un arma en la casa. La tomaré y estaré listo para ayudarte, pero en su mayor parte, estarás solo.$B$BNo tengo idea de cuántos enviarán, pero estoy seguro de que serán más que los pocos que enviaron antes.$B$BPídele a la Luz las bendiciones que puedas porque... ¿escuchaste eso? Creo que están llegando.$B$B¡Haremos nuestra última resistencia cerca de la casa!",
    ["O"] = "Defiende a Daphne Stilwell del ataque de los Defias.$B$BNinguno de vuestros espíritus debe ser liberado de sus envolturas mortales si deseas tener éxito.$B$BDespués de que tengas éxito, habla con Daphne Stilwell nuevamente.",
    ["T"] = "El Tomo de Valentía",
  },
  [1652] = {
    ["D"] = "Por favor, regresa con Duthorian y hazle saber que estoy a salvo por ahora. Te tengo a ti y a la Iglesia que agradecer por eso.$B$BÉl enviará un mensaje a Forjaz para que mi Jordan sepa que no tiene nada que temer.$B$BOh, cómo extraño a mi esposo. No puedo esperar a que regrese.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Valentía",
  },
  [1653] = {
    ["D"] = "De hecho, no tengo dudas de que al propio Jordan le gustaría agradecerte, y como necesito un mensajero para enviar noticias de que su esposa está a salvo, creo que serías la elección perfecta.$B$BJordan iba a ser un paladín de la Luz antes de que su dedicación al oficio de su padre lo llevara por otro camino, está íntimamente familiarizado con nuestra forma de vida y es considerado uno de los más fieles.$B$BTodavía debería estar en Forjaz, le gusta trabajar fuera de las puertas durante el día, algo sobre el aire fresco de la montaña.",
    ["O"] = "Habla con Jordan Stilwell en Forjaz.",
    ["T"] = "La Prueba de la Rectitud",
  },
  [1654] = {
    ["D"] = "Me gustaría recompensarte si estás dispuesto a aceptar algo así. No me malinterpretes, mi generosidad depende de cuánto puedas ayudar, pero veo que ambos nos beneficiaremos enormemente de tal alianza.$B$BTe forjaré un arma digna de lo que has logrado, un símbolo del paladinismo y la Luz, pero debes reunir los objetos que necesito para crearla. Ya he comenzado a tomar notas sobre los objetos, solo requiere que alguien valiente y hábil los recupere. ¿Qué dices, $c?",
    ["O"] = "Usando las Notas de Armas de Jordan, encuentra algo de Madera de Roble Piedrablanca, el Envío de Mineral Refinado de Bailor, el Martillo de Herrero de Jordan y una Gema Kor, y entrégaselos a Jordan Stilwell en Forjaz.",
    ["T"] = "La Prueba de la Rectitud",
  },
  [1655] = {
    ["D"] = "¡Ah, me has asustado, $Ghombre:mujer;! ¿No ves que estaba perdido en mis pensamientos?$B$B¿Qué dices? El envío de Jordan... para Forjaz. Ahhh, sí, sé de lo que hablas. Los ogros Mo\'grosh... esos son los que se llevaron el cargamento de mineral de mi viejo amigo. La caravana se dirigía aquí desde los Humedales y fue emboscada. El guardia que sobrevivió al ataque acaba de fallecer hace unos días, el sanador no pudo llegar a tiempo.$B$BSi quieres el cargamento, tendrás que dirigirte al noreste, cruzando el Lago. Debe estar allí en algún lugar.",
    ["O"] = "Lleva el Envío de Mineral de Jordan a Bailor Petramano en Loch Modan.",
    ["T"] = "El Envío de Mineral de Bailor",
  },
  [1656] = {
    ["D"] = "$N, por favor, te lo ruego, ¿puedes ayudarme? No he terminado mis tareas aquí en la Mesa, estoy en medio de mi Rito de Fuerza, y mi padre me pidió que entregara estas pieles en la posada de Sangre de Buey.$B$BNo estoy lo suficientemente descansado para completar tanto mi rito como esta tarea para mi padre. ¿Te importaría continuar por mí?$B$BLa posada es agradable y un lugar perfecto para descansar durante tus largos viajes. Estoy seguro de que estarás de acuerdo una vez que la veas.",
    ["O"] = "Lleva el Fardo de Pieles a Tabernero Kauth en Poblado Pezuña de Sangre.",
    ["T"] = "Una Tarea Inconclusa",
  },
  [1657] = {
    ["D"] = "Celebra el Festival de la Cosecha con estilo... ¡llevando el dolor a nuestros enemigos en Costasur!$B$BToma una de estas bombas apestosas especialmente elaboradas. Está llena de un hedor fétido que ningún humano, o cualquier bestia de voluntad débil, puede soportar. Necesitarás lanzarla justo en el corazón de Costasur para que haga su trabajo, así que prepárate para una pelea contra la Alianza!$B$BCuando hayas completado esta tarea... este tributo a nuestra liberación, ¡regresa a mí!",
    ["O"] = "Lanza tres Bombas Fétidas de los No-Muertos en el corazón de Costasur en Trabalomas, y regresa con Clamador Sombrío Yanka en el Festival del Hombre de Mimbre en la Vega de Tirisfal.",
    ["T"] = "Apestando Costasur",
  },
  [1658] = {
    ["D"] = "Necesito individuos valientes que se dirijan a la Vega de Tirisfal y revisen el Festival del Hombre de Mimbre. No voy a mentir: ir allí será peligroso. Los guardias en el festival serán excepcionalmente viciosos. Aún así, necesitamos saber qué tan grande es el festival este año y cuántos problemas nos causarán los No-Muertos durante la Noche de los Difuntos.$B$BSi estás dispuesto, dirígete a Tirisfal y explora el festival. Regresa con la información y me aseguraré de que seas bien recompensado.",
    ["O"] = "Localiza el Festival del Hombre de Mimbre de los No-Muertos en los Claros de Tirisfal. Regresa con el Sargento Hartman en Costasur una vez que lo hayas hecho.",
    ["T"] = "Estrellando el Festival del Hombre de Mimbre",
  },
  [1659] = "_",
  [1660] = "_",
  [1661] = {
    ["D"] = "Te saludo una vez más, $N. Estoy más orgulloso que nunca de llamarte $ghermano:hermana; -- tu dedicación es un tributo para todos nosotros.$B$BHa llegado el momento de otorgarte un gran regalo. Pero ten en cuenta que, por ahora, te lo doy libremente, pero más adelante, cuando el tiempo lo permita, esto será un desafío mucho mayor para todos los paladines que elijan emprender mi misión.$B$B¡Háblame de nuevo y te concederé una gran bendición.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Nobleza",
  },
  [1665] = {
    ["D"] = "Bien, aquí está mi jarra. ¡Espera! Déjame terminar lo que hay dentro primero... ahí vamos.",
    ["O"] = "Lleva la Jarra de Bartleby a Burlguard.",
    ["T"] = "La Jarra de Bartleby",
  },
  [1666] = {
    ["D"] = "Has aprendido algunos movimientos, pero aún podrías usar un arma decente. Apuesto a que si hablas con el Mariscal Haggard, él podría ayudarte.$B$BEl Mariscal Haggard vive en una mansión en el Bosque de Elwynn, en el Campamento del Valle del Este. Está retirado ahora, pero recolectó mucho equipo durante su carrera.$B$BHazle saber que te envié, y con suerte tendrá un arma de repuesto para ofrecerte.",
    ["O"] = "Habla con el Mariscal Haggard.",
    ["T"] = "Mariscal Haggard",
  },
  [1667] = {
    ["D"] = "¿Burlguard te envió, verdad? Sí, tengo armas de sobra por aquí, pero primero tengo una tarea para ti...$B$BHace un tiempo, un villano Defias, Jack Dientemuerto, me engañó. Estaba oscuro y llevaba una tabarda de Ventormenta, y pensé que era el Mariscal Dughan. Dijo que necesitaba mi vieja insignia de Mariscal de Ventormenta y se la di... ¡y luego se rió y huyó antes de que pudiera atraparlo!$B$B¡Recupera mi insignia! Jack Dientemuerto probablemente la tenga guardada en su campamento al sur.",
    ["O"] = "Recupera la Insignia de Haggard del campamento de Jack Dientemuerto y llévasela al Mariscal Haggard.",
    ["T"] = "Jack Dientemuerto",
  },
  [1678] = {
    ["D"] = "Si quieres ascender en las filas de los guerreros, entonces será mejor que seas duro. Puedo enseñarte nuevos movimientos, pero antes de eso, demuéstrame que puedes enfrentarte a enemigos reales.$B$BVejrek es el troll más duro que encontrarás en estas partes, y es a quien tienes que vencer. Tiene una choza en las estribaciones justo al sur de la Fortaleza Peloescarcha.$B$BTráeme la cabeza de Vejrek y sabré que tienes agallas.",
    ["O"] = "Lleva la Cabeza de Vejrek a Muren Stormpike en Forjaz.",
    ["T"] = "Vejrek",
  },
  [1679] = {
    ["D"] = "¡Hola, guerrero! Muestras mucho potencial, pero aún necesitas aprender algunos trucos, y Muren Pico Tormenta es el enano indicado para enseñarte.$B$BPuedes encontrar a Muren en el Salón de las Armas en el Distrito Militar de Forjaz. Habla con él cuando estés listo para aprender lo que significa ser un guerrero.",
    ["O"] = "Habla con Muren Stormpike.",
    ["T"] = "Muren Pico Tormenta",
  },
  [1680] = {
    ["D"] = "Ahora que has aprendido algunos movimientos, es hora de conseguirte un arma decente. Habla con Tormus Forjahonda. Él puede hacerte un arma que estoy seguro de que te gustará.$B$BEncontrarás a Tormus aquí en Forjaz, en la Gran Forja.",
    ["O"] = "Habla con Tormus Forjahonda.",
    ["T"] = "Tormus Forjahonda",
  },
  [1681] = {
    ["D"] = "Mi mentor, Sturgy Ironband, tenía un complejo al sur del Lago Helm\'s Bed en el este de Dun Morogh. Allí enseñaba herrería y era el mejor.$B$BPero un día, uno de sus estudiantes le trajo un cargamento de mineral extraño. El Maestro Ironband lo llamó mineral umbral y estaba ansioso por trabajar con él, pero poco después de que llegara... una banda de enanos Hierro Negro atacó su complejo. ¡Mataron a todos!$B$BEntra en el complejo de Ironband, $N, y venga la muerte de mi maestro. Y tráeme el cargamento de mineral umbral que lo causó.",
    ["O"] = "Lleva una carga de Mineral Umbral a Tormus Forjahonda en Forjaz.",
    ["T"] = "El Complejo de Ironband",
  },
  [1682] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Armas Hierro Negro",
  },
  [1683] = {
    ["D"] = "Antes de que pueda enseñarte, debes mostrarme tu determinación.$B$BEl sátiro Vorlus Pezuña Vil ha encontrado uno de nuestros pozos lunares remotos, al sureste de la Guarida de Ban\'ethil. Ahora chapotea dentro de él, profanando sus aguas puras con su inmundicia.$B$BEncuentra a Vorlus en el pozo lunar y destrúyelo. Tráeme su cuerno como prueba y comenzaré tu entrenamiento avanzado como guerrero.$B$BEl camino al pozo lunar está bien escondido, $N, pero mantente enfocado y alerta, y lo encontrarás.",
    ["O"] = "Lleva el Cuerno de Vorlus a Elanaria en Darnassus.",
    ["T"] = "Vorlus Pezuña Vil",
  },
  [1684] = {
    ["D"] = "Saludos, guerrero. Tu habilidad crece, pero hay más en tu profesión de lo que puedes creer. Para progresar más, pronto debes encontrar un maestro.$B$BLa guerrera Elanaria habita en Darnassus, en la Terraza de los Guerreros. Ella puede instruirte.",
    ["O"] = "Habla con Elanaria.",
    ["T"] = "Elanaria",
  },
  [1685] = {
    ["D"] = "¿No es así como siempre funciona? Te estuve buscando todo el día y no pude encontrarte, y ahora, cuando no lo espero, apareces y te acercas a mí.$b$bHe perdido suficiente tiempo tratando de encontrarte, así que no te retendré más. El maestro Gakin te ha convocado para un entrenamiento adicional. Parece que deberías buscarlo lo antes posible.$b$bLo encontrarás en el sótano de la taberna El Cordero Degollado. Por cierto, quizás quieras mantener ese lugar en secreto. No querrás que se corra la voz.",
    ["O"] = "Preséntate ante Gakin el Oscuro en el Distrito de los Magos de Ventormenta.",
    ["T"] = "La Llamada de Gakin",
  },
  [1686] = {
    ["D"] = "Has aprendido mucho, $N. Ahora te forjaremos un arma. Un arma de guerrero.$B$BPara hacer esto, debes viajar a Costa Oscura.$B$BHace mucho tiempo, un barco se estrelló contra las rocas cerca del faro de Auberdine, esparciendo muchos cajones de mineral de elunita por el fondo del océano.$B$BPrimero, debes derrotar al guardián de la elunita, la Sombra de Elura. Antes la capitana del barco, se maldijo a sí misma para vagar por las profundidades y proteger su cargamento por toda la eternidad. Derrota a la Sombra de Elura, obtén su medallón y recoge los cajones de elunita perdida.",
    ["O"] = "Lleva 8 cargas de Mineral Elunita y el Medallón de Elura a Elanaria en Darnassus.",
    ["T"] = "La Sombra de Elura",
  },
  [1687] = {
    ["D"] = "A veces, tarde en la noche, las matronas del orfanato nos cuentan historias de miedo. ¡Esas son GENIALES! Hay una sobre un fantasma que acecha un faro en los Páramos de Poniente. Dicen que era un capitán pirata al que otros piratas le quitaron su barco. ¡Vaya!$B$B¿Podrías llevarme al faro en los Páramos de Poniente? ¡Quiero ver si el fantasma de ese capitán está allí! Prometo quedarme cerca de ti; ¡no me escaparé ni nada!",
    ["O"] = "Lleva al huérfano a ver el faro en la costa de los Páramos de Poniente.",
    ["T"] = "El Faro Embrujado",
  },
  [1688] = {
    ["D"] = "Tenía una estudiante llamada Surena Caledon. Ella estaba donde tú estás ahora, ansiosa por aprender sobre la magia de brujos, y poseía no poco talento. Más que eso, era joven y agradable a la vista. ¡Si lo hubiera visto entonces, la traicionera ramera!$b$b¡La ladrona huyó con uno de los Defias, Erlan Drudgemoor! Aunque su pérdida es de poca importancia, le regalé un collar de piedra de sangre que debo recuperar.$b$bSu vida ya no significa nada para mí. La encontrarás en el Campo de Calabazas de Brackwell. Recupera lo que es mío.",
    ["O"] = "Recupera el Collar de Surena para Gakin el Oscuro en Ventormenta.",
    ["T"] = "Surena Caledon",
  },
  [1689] = {
    ["D"] = "Al recuperar mi collar y vencer a Surena, has demostrado que eres al menos tan capaz como ella.$b$bDicho esto, puedo decir que necesitas desesperadamente entrenamiento. Puedes manejar los flujos de la magia, sí, pero ser un brujo es mucho más que eso.$b$bTe mostraré cómo comandar a un caminante del vacío, pero antes de poder hacerlo, debes invocar uno en un círculo de invocación y someterlo usando cualquier arma, física o mágica, que tengas a tu disposición. Usa la magia de este collar en el círculo de invocación de abajo.",
    ["O"] = "Usando el Collar de Piedrasangre, invoca y somete a un abisario, luego devuelve el Collar de Piedrasangre a Gakin el Oscuro en Ventormenta.",
    ["T"] = "El Vínculo",
  },
  [1690] = {
    ["D"] = "¡Tú allí! Es hora de que seas un gran ayudante para la Compañía de Agua de Gadgetzan.$B$B¡Los nómadas Merodeadores han tomado casi todos los pozos de agua en el desierto de Tanaris! Sin acceso a ellos, nuestro suministro de agua se secará en poco tiempo. Si quieres ganarte nuestra buena voluntad, ¡entonces nos ayudarás a traer justicia a esos nómadas!$B$BVe al este de aquí y derriba a diez Bandidos y Ladrones Merodeadores, luego repórtame de inmediato. ¡La justicia no espera a nadie... goblin... bah, solo hazlo!",
    ["O"] = "Elimina a 10 Bandidos y Ladrones de los Merodeadores al este de Gadgetzan, luego informa al Ingeniero Jefe Burbagea.",
    ["T"] = "Justicia de los Merodeadores",
  },
  [1691] = {
    ["D"] = "Ahora necesitamos que te enfrentes a los rangos más fuertes de los nómadas Merodeadores. Has hecho un buen trabajo hasta ahora, pero si vamos a recuperar esos pozos de agua, ¡solo tiene sentido hacerlo contra la menor cantidad de nómadas posible!$B$BEsta vez necesitamos que derrotes a sus pícaros, asesinos y magos de las sombras. Los encontrarás al este y sureste de aquí.$B$BNo voy a mentirte - son un grupo desagradable. Haz esto por nosotros, y estaremos en camino de recuperar lo que legítimamente nos pertenece!",
    ["O"] = "Elimina a 10 Magos Sombríos de los Merodeadores, 8 Pícaros de los Merodeadores y 6 Asesinos de los Merodeadores para el Ingeniero Jefe Burbagea en Gadgetzan.",
    ["T"] = "Más Justicia de los Merodeadores",
  },
  [1692] = {
    ["D"] = "He colocado el elunita que recolectaste en un estuche.  Llévalo al herrero Mathiel, y él te fabricará un arma de elunita.$B$BMathiel está al sur, al otro lado de este edificio.",
    ["O"] = "Lleva el Estuche de Elunita a Herrero Mathiel.",
    ["T"] = "Herrero Mathiel",
  },
  [1698] = {
    ["D"] = "Yorus Barleybrew ha lanzado un desafío a todos los guerreros capaces de la Alianza. Espera en la Posada de Villa del Lago, en las Crestagrana, y promete una gran recompensa a cualquiera que pueda pasar su prueba.",
    ["O"] = "Habla con Yorus Cebadiz.",
    ["T"] = "Yorus Cebadiz",
  },
  [1699] = {
    ["D"] = "Llamo a mi desafío el Gauntlet de Rethban. Para pasar la prueba, todo lo que debes hacer es entrar en las Cavernas de Rethban, hasta la primera bifurcación, y luego regresar aquí. ¡Fácil! Excepto que hay algunos obstáculos...$B$BDebes regresar en una hora.$B$BHay montones de gnolls de Crestagrana en el camino.$B$BAntes de comenzar, debes tomar un trago de la bebida insignia de mi familia: ¡el Escaldador de Cebada!$B$BEntonces, ¿qué dices? Las Cavernas de Rethban están en las estribaciones al norte de Villa del Lago. ¿Estás listo para correr el Gauntlet de Rethban?",
    ["O"] = "Entra en las Cavernas de Rethban, llega hasta la primera bifurcación y regresa con Yorus Barleybrew dentro del tiempo permitido.$B$BNo debes morir y liberar tu espíritu.",
    ["T"] = "El Guantelete de Rethban",
  },
  [1700] = {
    ["D"] = "Hice una copia de mis notas sobre las técnicas que aprendí mientras hacía tu armadura, y necesito que se las envíen a Grimand Elmore. Está aquí en el Distrito de los Enanos, en la armería.$B$BY agáchate cuando se las des. Es muy orgulloso de sus habilidades y no le gustará la idea de aprender algo de mí...",
    ["O"] = "Lleva las Notas de Furen a Grimand Elmore.",
    ["T"] = "Grimand Elmore",
  },
  [1701] = {
    ["D"] = "Así que te di un buen escudo, pero no tenemos que detenernos ahí. Conozco los secretos para hacer armadura de malla extremadamente dura. No es fácil y requiere materiales muy raros, pero si estás dispuesto a hacer el trabajo, haré una pieza para ti.$B$B¿Qué dices, $N? Si estás listo para el desafío, entonces te daré una lista de lo que necesitas y dónde conseguirlo.",
    ["O"] = "Reúne los materiales que Furen Barba Larga requiere y llévaselos a él en Ventormenta.",
    ["T"] = "Malla Endurecida por el Fuego",
  },
  [1702] = {
    ["D"] = "Realmente demostraste de qué estabas hecho cuando corriste el Gauntlet de Rethban, $N. Y soy fiel a mi palabra. Te daré la recompensa que te prometí...$B$BExcepto que está en Ventormenta.$B$BHabla con mi amigo, Furen Longbeard, en el Distrito de los Enanos de Ventormenta. Es el mejor herrero de escudos que jamás conocerás, y me debe un gran favor.$B$BAquí, lleva a Furen este barril de Barleybrew Scalder y pídele un escudo. Te dará uno bueno.",
    ["O"] = "Lleva el Barril de Escaldador a Furen Longbeard.",
    ["T"] = "El Forjador de Escudos",
  },
  [1703] = {
    ["D"] = "Hice una copia de mis notas sobre las técnicas que aprendí mientras hacía tu armadura, y necesito que se las envíen al herrero Mathiel. Vive en Darnassus, que está bastante lejos, pero estoy seguro de que estará muy agradecido de aprender lo que yo he aprendido.",
    ["O"] = "Lleva las Notas de Furen a Mathiel.",
    ["T"] = "Mathiel",
  },
  [1704] = {
    ["D"] = "Hice una copia de mis notas sobre las técnicas que aprendí mientras hacía tu armadura, y necesito que alguien se las lleve a Klockmort Spannerspan. Es un herrero gnomo en la Instalación de Reclamación de Gnomeregan, y cuando aprenda lo que yo he aprendido... apuesto a que se arrancará la barba de la emoción.",
    ["O"] = "Lleva las Notas de Furen a Klockmort Palanqueta.",
    ["T"] = "Klockmort Palanqueta",
  },
  [1705] = {
    ["D"] = "Odio admitir que Furen podría haber encontrado algo realmente importante. Pero para estar seguro, necesitaré probar sus técnicas en una nueva pieza de armadura. Si me ayudas con los materiales, $N, entonces puedes quedarte con la armadura.$B$BPara hacer esta armadura, necesito muestras de sangre ardiente y una roca ardiente.$B$BEl único lugar que conozco para conseguirlos es en el Bosque del Ocaso. Los huargos Nocturnia basados en la mina de la Perdición de Roland tienen sangre ardiente, y su líder, Gutspill, tendrá la roca.$B$BQue la fortuna te dé velocidad, $N.",
    ["O"] = "Lleva 20 viales de Sangre Ardiente y 1 Roca Ardiente a Grimand Elmore en Ventormenta.",
    ["T"] = "Sangre Ardiente",
  },
  [1706] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Armadura de Grimand",
  },
  [1707] = {
    ["D"] = "Saludos, $n, ¡me llamo Luglunket y tenemos un trabajo importante para ti si estás dispuesto! ¡Hurra!$B$BNo hay nada que me complazca más como un operador de grifo experimentado que tomar cualquier bolsa de agua Wastewander que encuentres en el desierto. ¡Esa es nuestra agua de todos modos, los nómadas solo la han robado! La Compañía de Agua de Gadgetzan te ofrecerá un paquete de cuidado oficial por cada cinco que entregues. Lo más importante es que estás ayudando a Gadgetzan a ayudarse a sí misma.",
    ["O"] = "Lleva 5 Odres de Agua de los Merodeadores de las Dunas al Operador de la Espita Luglunket en Gadgetzan.",
    ["T"] = "Recompensa por Bolsas de Agua",
  },
  [1708] = {
    ["D"] = "Quiero probar las nuevas técnicas de Furen Barbalarga en mi propia pieza de armadura. Y como ayudaste a Furen y él me ayudó a mí, ¡eres bienvenido a la armadura después de que la haga!$B$BPara terminar la pieza, necesito un coral especial, coral abrasador. Solo crece en un lugar: a lo largo de la costa al sur de Puerto Menethil, en los Humedales.$B$BTráeme suficiente coral abrasador y me pondré a trabajar en la armadura. Y date prisa, $N. Estoy tan ansioso por empezar que se me retuercen los dedos de los pies.",
    ["O"] = "Lleva 20 cargamentos de Coral Abrasador a Klockmort Spannerspan en la Instalación de Reclamación de Gnomeregan.",
    ["T"] = "Coral de Hierro",
  },
  [1709] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Creación de Klockmort",
  },
  [1710] = {
    ["D"] = "Mi colega, Furen Barbalarga, hizo un descubrimiento que estoy ansioso por probar. Deseo probarlo en una nueva armadura que estoy investigando, ya que creo que las técnicas de Furen producirán resultados superiores.$B$B¿Me ayudarás? Si reúnes lo que necesito para hacer la armadura, entonces haré una pieza extra para ti.$B$BNecesito las cáscaras de huevo quemadas por el sol de los wyverns de Altopercha que habitan en Altopercha, en las Mil Agujas. Busca las cáscaras debajo de sus nidos y regresa a mí cuando tengas una cantidad suficiente.",
    ["O"] = "Lleva 20 Caparazones Quemados por el Sol a Mathiel en Darnassus.",
    ["T"] = "Conchas Abrasadas por el Sol",
  },
  [1711] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Armadura de Mathiel",
  },
  [1712] = {
    ["D"] = "He pasado siglos observando este sitio. Observo, para que algún día pueda ver caer al gran Cicloniano. Pero aún no he encontrado un campeón que pueda derrotar al señor del viento.$B$BQuizás tú seas ese campeón.$B$BDemuestra que lo eres. Reúne los artículos que necesito para invocar a Cicloniano, y sabré que eres un gran guerrero. Quizás entonces puedas derrotarlo y obtener su corazón de torbellino.$B$BSi lo haces, infundiré el poder del corazón en un arma, y te daré esa arma.$B$BAquí tienes un pergamino con los artículos que necesito.",
    ["O"] = "Lleva los objetos en el Pergamino de Bath\'rah a Bath\'rah el Vigía del Viento en Alterac.",
    ["T"] = "Ciclónico",
  },
  [1713] = {
    ["D"] = "Estoy listo para realizar el ritual para invocar a Ciclonian. Cuando sea invocado, debes derrotarlo y tomar su corazón de torbellino. Lo usaré para infundir un arma con el poder de Ciclonian.$B$B$N, ¿estás listo para enfrentarte al señor del viento?",
    ["O"] = "Lleva el Corazón del Torbellino a Bath\'rah el Vigía del Viento.",
    ["T"] = "La Invocación",
  },
  [1714] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Esencia del Exiliado",
  },
  [1715] = {
    ["D"] = "Hmph. En tiempos pasados, todos los brujos de Khaz Modan eran entrenados bajo los auspicios de los maestros de Gnomeregan, pero ahora que todos hemos sido obligados a reubicarnos en este agujero de rata en Forjaz, y Magni ha decidido vigilarnos de cerca, nos vemos obligados a enviar a nuestros novicios a Ventormenta para su entrenamiento.$B$BAfortunadamente, Gakin no es ningún inepto, así que aún aprenderás lo que necesitas saber, ¡pero me hierve la sangre!$B$BDe todos modos, encontrarás a Gakin en el sótano del Cordero Degollado en Ventormenta.",
    ["O"] = "Preséntate ante Gakin el Oscuro en el Distrito de los Magos de Ventormenta.",
    ["T"] = "El Cordero Degollado",
  },
  [1716] = {
    ["D"] = "Una súcubo es una devoradora de almas, destructora de corazones y dominadora de mentes. No conoce nada más que el mal.$B$BEsta será la criatura que aprenderás a invocar y controlar, pero no puede ser traída a nuestro mundo sin un estímulo.$B$BCon un símbolo de amor como cebo, seguramente responderá a tu llamado. Entonces puedes derrotarla y hacerla tu herramienta.$B$BSerá difícil encontrar algo de esta naturaleza, sin embargo. Deberías hablar con Takar el Vidente. Lo encontrarás en Los Baldíos de Kalimdor, cerca del Campamento Taurajo.",
    ["O"] = "Habla con Takar el Vidente en los Baldíos.",
    ["T"] = "Devorador de Almas",
  },
  [1717] = {
    ["D"] = "$n! No esperaba verte por aquí, pero me dijeron que te buscara.$B$BEs hora de que recibas más entrenamiento, así que deberías visitar a Gakin la próxima vez que estés en Ventormenta. Él te estará esperando.",
    ["O"] = "Preséntate ante Gakin el Oscuro en el Distrito de los Magos de Ventormenta.",
    ["T"] = "La Llamada de Gakin",
  },
  [1718] = {
    ["D"] = "Tu fama como guerrero crece, $N. Ahora es el momento de enfrentarte a tus compañeros y ver dónde te encuentras.$B$BHay una isla frente a la costa de Los Baldíos, al sur de Trinquete. Se llama Isla de la Disputa y es un lugar donde los guerreros se reúnen.$B$BSe reúnen, y luchan.$B$BHabla con el jefe guerrero de la Isla de la Disputa, Klannoc Macleod. Si te considera digno, Klannoc te guiará más allá en el camino del guerrero.$B$BPara llegar a la Isla de la Disputa, busca los bajos de la barra de arena a lo largo de la Costa Mercante.",
    ["O"] = "Habla con Klannoc Macleod.",
    ["T"] = "El Isleño",
  },
  [1719] = {
    ["D"] = "Si quieres aprender de mí, primero debes pasar El Enfrentamiento. Es un desafío que en la Isla del Enfrentamiento nos gusta ofrecer a los guerreros en ascenso que creen que son duros, y si puedes pasar esta prueba, sabré que lo eres.$B$BPara comenzar, camina hacia el medio de esa multitud allá y pisa la rejilla. Eso le dirá a Twiggy Flathead que estás listo, y él enviará a los retadores contra ti.$B$BDerrota a todos los retadores y él llamará a Big Will. Mata a Big Will y regresa a mí en el tiempo que permitimos.",
    ["O"] = "Mata a Big Will, luego habla con Klannoc Macleod en la Isla de la Disputa.",
    ["T"] = "La Pelea",
  },
  [1738] = {
    ["D"] = "En preparación para tu llegada, usé mi vista para mirar el mundo y a través de las brumas del tiempo. He encontrado lo que buscas.$b$bEn la Guerra de los Ancestros, dos amantes cayeron juntos en batalla. Donde tomaron su último aliento, creció un árbol conocido como el leñocorazón, un símbolo de su amor eterno.$b$bEso debería ser suficiente para tentar a una súcubo. Lo encontrarás en las ruinas de Ordil\'Aran en la parte noroeste del bosque de Vallefresno.",
    ["O"] = "Recupera el Duramen de Vallefresno y llévaselo a Gakin el Ligoscuro en el Distrito de los Magos de Ventormenta.",
    ["T"] = "Duramen",
  },
  [1739] = {
    ["D"] = "Usando el núcleo de madera de corazón, podrás lanzar un hechizo en el círculo de invocación en la cripta de abajo para abrir un portal para que la súcubo pase.$b$bTen cuidado, $n, las súcubos han dominado incluso las mentes más fuertes.$b$bUna vez que la hayas vencido y la hayas devuelto al vacío, regresa conmigo y te mostraré cómo llamarla y controlarla.",
    ["O"] = "Usando el Núcleo de Leñoscuro, invoca y somete a una súcubo, luego devuelve el Núcleo de Leñoscuro a Gakin el Oscuro en el Cordero Degollado.",
    ["T"] = "El Vínculo",
  },
  [1740] = {
    ["D"] = "Cuando los demonios de la Legión caen en desgracia, sus espíritus son aprisionados dentro de orbes y distribuidos a sus magos y brujos.$B$BMuchos fueron llevados por sus sirvientes en Azeroth. Incluso fragmentados, todavía poseen los poderes de los demonios atrapados dentro.$B$BEl orbe de Soran\'ruk fue dividido en dos piezas, una fue rota aún más para ser usada por los acólitos del Martillo Crepuscular en las Cavernas de Brazanegra. La otra pieza, más grande, estaba en posesión de un mago en el Castillo de Colmillo Oscuro. Encuéntralas y podré reformarlo.",
    ["O"] = "Encuentra 3 Fragmentos de Soran\'ruk y 1 Fragmento Grande de Soran\'ruk y llévaselos a Doan Karhan en Los Baldíos.",
    ["T"] = "El Orbe de Soran\'ruk",
  },
  [1758] = {
    ["D"] = "Se necesitará más que un simple círculo de invocación para traer un abisario para que lo desafíes.$b$bMis tres acólitos y yo ayudaremos a crear los círculos mágicos. Necesitas una copia del Tome de la Cábala, que se ha perdido para nosotros por algún tiempo. Fue escrito por el primer grupo de brujos que invocó un abisario sin ayuda.$b$bQuizás Krom Stoutarm pueda ayudarte con esto. Un brujo itinerante estos días, pasa sus días acumulando textos antiguos y merodeando por la biblioteca en Forjaz.",
    ["O"] = "Habla con Krom Brazofuerte en Forjaz.",
    ["T"] = "Tomo del Cábala",
  },
  [1778] = {
    ["D"] = "Voy a ver sobre hacer planes para regresar a casa pronto. Espero que Stephanie esté bien en Ventormenta. No puedo esperar a verla de nuevo. Hasta la próxima, $c.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1779] = {
    ["D"] = "Quiero que tomes esto, $N. Se llama el Símbolo de la Vida.$B$BHas demostrado ser caritativo y paciente, y ahora quiero que demuestres cuán hábil te has vuelto tanto en combate como en poder.$B$BMi esposo Muiredon regresó recientemente de un encuentro con algunos enanos Hierro Negro, y apenas escapó con vida, está allí siendo curado por uno de nuestros sacerdotes.$B$BToma el Símbolo y habla con él. Si puedes ayudarlo, entonces hablaremos de nuevo pronto. Que la Luz te ilumine.",
    ["O"] = "Toma el Símbolo de Vida y habla con Muiredon Forjabatalla en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1780] = {
    ["D"] = "Llevaré estos a los niños este mismo día. Por favor, ten cuidado, buen $c. No es frecuente que $G hombres:mujeres, de tu carácter muestren sus rostros en la ciudad, pero has demostrado que los paladines aquí en Ventormenta nunca darán la espalda a los necesitados.$B$BAdiós.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1781] = {
    ["D"] = "Toma esto, $N. Se llama el Símbolo de la Vida.$B$BHas demostrado ser caritativo y paciente, y ahora ha llegado el momento de ver si eres lo suficientemente poderoso como para enfocar uno de los mayores dones de la Luz.$B$BMi amigo Gazin regresó recientemente de un encuentro con algunos Defias en Elwynn cerca de la Vigilia de los Héroes: apenas escapó con vida. Está cerca del altar, donde los sacerdotes curan sus heridas.$B$BToma el Símbolo y habla con él. Si puedes ayudarlo, entonces hablaremos de nuevo pronto. Que te bañes en la gloria de la Luz.",
    ["O"] = "Lleva el Símbolo de Vida a Gazin Tenorm en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1782] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "La Armadura de Furen",
  },
  [1783] = {
    ["D"] = "¡Estábamos espiando a algunos de los enanos Hierro Negro que se han atrincherado al sur del Lago Lecho de Helm! Sabíamos que estaban tramando algo malo, así que Narm y yo pensamos en echar un vistazo más de cerca... logramos ver a su líder dando órdenes a algunos de sus hombres.$B$BNarm y yo aprovechamos para matar a algunos de ellos, pero resultaron más duros de lo que pensábamos, y más de ellos nos sorprendieron. Narm me dijo que corriera a buscar apoyo, así que aquí estoy.$B$BNarm necesita ayuda, y no descansaré hasta que la consiga.$B$B¿Puedes ayudar, $N?",
    ["O"] = "Toma el Símbolo de Vida y resucita a Narm Faulk en Dun Morogh.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1784] = {
    ["D"] = "Antes de caer en batalla, Muiredon y yo casi habíamos derrotado a uno de los espías Hierro Negro cerca de esa pequeña casa. Vimos a su capitán pasando órdenes a ellos, y buscamos matarlo y tomar las órdenes.$B$BAlgunos del consejo en Forjaz aún no creen en la presencia de los enanos Hierro Negro en Dun Morogh. Al obtener uno de esos escritos, no solo tendremos pruebas de sus movimientos, sino posiblemente una idea de lo que están planeando.$B$BObtén uno de esos escritos y llévaselo a Muiredon, $N.",
    ["O"] = "Recupera un Guión Hierro Negro de los enanos Hierro Negro cerca de la Cama de Helm y llévaselo a Muiredon Forjabatal en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1785] = {
    ["D"] = "Puedo ver a mi esposa allí, radiante de orgullo. Mejor que te apresures a llegar allí. Creo que ella tendrá algunas palabras para ti.$B$BQue la Luz esté contigo, poderoso $c. Te estaré esperando en el campo de batalla contra la Plaga y esos Hierro Negro cuando llegue el momento.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1786] = {
    ["D"] = "Henze y yo llegamos a la Vigilia de los Héroes, una isla en medio del Lago de la Piedra del Cuervo en el noreste de Elwynn. Habíamos oído que los Defias habían infiltrado nuestras fronteras y queríamos pruebas para llevarlas a los consejeros del rey.$B$BVimos a un par de esa escoria revisando algunos documentos y los atacamos. Desafortunadamente, rápidamente nos superaron más magos.$B$BHenze los contuvo para que yo escapara... el tonto valiente, siendo tan valiente.$B$BÉl está allá afuera ahora, Henze. ¿Puedes ayudarnos, $N?",
    ["O"] = "Toma el Símbolo de Vida y resucita a Henze Faulk en Elwynn.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1787] = {
    ["D"] = "Tendremos tiempo para hablar más sobre el Símbolo más adelante. Lo importante ahora es conseguir esos papeles que vimos revisando a los Defias. Si podemos poner nuestras manos en ellos, podremos probar que los Defias han infiltrado Elwynn y, con suerte, obtener alguna idea de cuáles son sus planes.$B$BNo estoy en condiciones de pelear ahora mismo, $N. ¿Crees que eres lo suficientemente fuerte como para conseguir esos papeles de los Defias aquí en la isla?$B$BSi logras conseguirlos, tráelos de vuelta a Gazin y hazle saber que estoy bien.",
    ["O"] = "Recupera un Guión Defias de los Magos Pícaros Defias en Elwynn y llévaselo a Gazin Tenorm en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1788] = {
    ["D"] = "Llevaré el guion a los asesores del Rey después de que Duthorian lo haya revisado, pero mientras tanto, deberías ir a hablar con Duthorian ahora. ¡Seguramente querrá darte una palmada en la espalda!$B$BGracias de nuevo, $N. Espero que nos volvamos a encontrar. Seguramente eres un aliado del que me enorgullezco de llamar amigo.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "El Tomo de Divinidad",
  },
  [1791] = {
    ["D"] = "Ahora que has aprendido tu postura de rabia, querrás un buen arma para usarla.$B$BHabla con Bath\'rah el Vigía del Viento, un ermitaño troll que vive entre las Montañas de Alterac y las Tierras de la Peste del Oeste. Es un hábil fabricante de armas y un poderoso chamán.$B$BPara encontrar a Bath\'rah, ve al río que fluye junto al flanco este de Molino Tarren. Síguelo hacia el norte - Bath\'rah está acampado cerca de unas ruinas que descansan a lo largo de la orilla este del río.",
    ["O"] = "Habla con Bath\'rah el Vigía del Viento.",
    ["T"] = "El Vigía del Viento",
  },
  [1792] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Arma del Torbellino",
  },
  [1793] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Tomo de Valentía",
  },
  [1794] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Tomo de Valentía",
  },
  [1795] = {
    ["D"] = "Si estás suficientemente preparado, entonces deberíamos comenzar. Déjame explicarte el proceso.$B$BComenzaré creando el círculo de invocación básico. Cuando termine, mis acólitos usarán las varas de canalización para crear el círculo mayor.$B$BUna vez hecho eso, lee la invocación del Tomo del Cabal, y el manáfago será convocado. Ten cuidado, $N. Un manáfago es un enemigo poderoso, y me gustaría que te mantuvieras con la cabeza sobre los hombros.",
    ["O"] = "Usando el Tomo del Cabal, invoca y somete a un manáfago, luego devuelve el Tomo del Cabal a Strahad Farsan en Ratchet.",
    ["T"] = "El Vínculo",
  },
  [1796] = {
    ["D"] = "El primer artículo que te ayudaré a crear es una túnica. Reunirás los materiales para mí, y los juntaremos. Luego, asumiendo que tengas éxito, tendrás el primero de muchos artículos que te harán más poderoso y te ayudarán en tus luchas.$B$BPara comenzar, debes haber confeccionado las Túnicas de Arcana. Un sastre puede hacerlas por ti, o quizás puedas comprárselas a alguien más, estaría muy impresionado si pudieras hacerlas tú mismo, pero en cualquier caso, tráeme las túnicas y continuaremos a partir de ahí.",
    ["O"] = "Lleva las Togas del Arcana a Menara Voidrender en los Baldíos.",
    ["T"] = "Componentes para la Toga Dorada Encantada",
  },
  [1798] = {
    ["D"] = "Es hora de que aprendas a invocar un manáfago, $n. No tengo los reactivos y objetos necesarios para enseñarte aquí, así que tendrás que buscar a otro, Strahad Farsan, otro de nuestro círculo interno.$B$BÉl reside en el puerto goblin de Ratchet. Enviaré un mensaje por delante para informarle de tu llegada.",
    ["O"] = "Habla con Strahad Farsan en los Baldíos.",
    ["T"] = "Buscando a Strahad",
  },
  [1799] = {
    ["D"] = "Bien, entonces comenzaremos.$B$BLa primera cosa que debes hacer es hablar con mis acólitos adentro. Elige uno de sus caminos para seguir, ellos te explicarán más.$B$BSegundo, debes viajar a Desolace y encontrar un Orbe Infernal de uno de los brujos del Coven Mannoroc. Es ese orbe el que limpiarás con la ayuda de una maga llamada Tabetha en lo profundo del sur del Pantano de Tuercespina. Ella te ayudará debido a nuestra historia juntos.$B$BOtra vez, no regreses hasta que hayas hecho ambas cosas que te he pedido.",
    ["O"] = "Habla con los acólitos de Menara dentro de la torre sobre Ratchet y elige uno de sus caminos a seguir. Luego, lleva un Orbe Infernal a Tabetha en los Pantanos de Veleno.",
    ["T"] = "Fragmentos del Orbe de Orahil",
  },
  [1800] = {
    ["D"] = "Las matronas de los huérfanos en mi hogar cuentan poderosas historias de batallas y leyendas por la noche si somos buenos y hacemos nuestras tareas. Una historia hablaba de cómo Lordaeron cayó, y de cómo el malvado Arthas tomó la vida de su propio padre para convertirse en gobernante.$B$B¡No tenía honor, $N! Cuando crezca y me convierta en un poderoso guerrero de la Horda, mi espada estará guiada por el honor, ¡nunca por el mal!$B$B¿Me llevarías a ver la sala del trono, por favor? Está justo antes de donde bajarías a Entrañas.",
    ["O"] = "Lleva a tu protegido a la antigua Sala del Trono de Lordaeron que se encuentra justo antes de descender a Entrañas.",
    ["T"] = "Sala del Trono de Lordaeron",
  },
  [1801] = {
    ["D"] = "Se necesitará más que un simple círculo de invocación para traer un abisario para que lo desafíes.$b$bMis tres acólitos y yo ayudaremos a crear los círculos mágicos. Necesitas una copia del Tome de la Cábala, que se ha perdido para nosotros por algún tiempo. Fue escrito por el primer grupo de brujos que invocó un abisario sin ayuda.$b$bQuizás Jorah Annison pueda ayudarte con esto. Está empleado como cartógrafo en Entrañas estos días, pero aún sabe más de textos antiguos que cualquiera que yo conozca.",
    ["O"] = "Habla con Jorah Annison en Entrañas.",
    ["T"] = "Tomo del Cábala",
  },
  [1802] = {
    ["D"] = "Se perdió, pero hay alguna idea de dónde podría encontrarse.$b$bSolo se hizo una copia y fue vandalizada en algún momento, una gran parte de ella arrancada del encuadernado. Se rumorea que de alguna manera pasó a las manos de los centauros de las Mil Agujas.$b$bEl resto se perdió frente a la costa al oeste de Costasur, donde iba a ser transportado a Puerto Menethil en barco. Nunca llegó.$b$bA cambio de este conocimiento, todo lo que pido es que si lo encuentras, me lo traigas para verlo.",
    ["O"] = "Recupera el Tome Mohoso y el Manuscrito Andrajoso para Krom Brazorrecio en Forjaz.",
    ["T"] = "Tomo del Cábala",
  },
  [1803] = {
    ["D"] = "¡Ah! Veo que eres un brujo de gustos similares a los míos. De hecho, adquirir el Tomo del Cábala es una aspiración digna. ¿Sabías que una vez estuvo en las murallas de Lordaeron? Fue vandalizado, casi la mitad de sus páginas fueron arrancadas. ¿Quién cometería un acto tan atroz?$B$BEsa mitad está perdida, aunque los rumores dicen que cayó en manos de los centauros de las Mil Agujas.$B$BEl resto del texto iba a ser transferido a Forjaz, pero se perdió en el tránsito en la costa al oeste de Costasur.",
    ["O"] = "Recupera el Tome Mohoso y el Manuscrito Andrajoso para Jorah Annison en Entrañas.",
    ["T"] = "Tomo del Cábala",
  },
  [1804] = {
    ["D"] = "Bueno, ah, es difícil dejarlo ir. Pero tú lo encontraste, y tienes cosas más importantes que hacer con él, por supuesto. Solo, por favor, asegúrate de mencionarle a Strahad que yo quería una copia.$B$BMientras estabas fuera, él envió un mensaje para ti. Quería que te dijera que para que sus acólitos ayuden con la invocación, tendrás que adquirir tres varas de canalización.$B$BÉl podría no saber dónde encontrarlas, ¡pero yo sí! Fueron usadas por última vez por los orcos del clan Faucedraco en los Humedales. ¡Yo revisaría a sus hechiceros!",
    ["O"] = "Lleva el Tomo Reconstruido y 3 Varas de Canalización a Strahad Farsan en Ratchet.",
    ["T"] = "Tomo del Cábala",
  },
  [1805] = {
    ["D"] = "Este es uno de los textos más interesantes que he hojeado en los últimos años. La selección aquí es escasa, principalmente libros sobre geografía, y ya los he leído todos. Espero que Strahad me envíe una copia de este tomo cuando tenga la oportunidad.$b$bHablando de él, me envió un mensaje mientras estabas fuera. Para tu tarea, debes obtener tres varas de canalización.$b$bPor permitirme mirar este texto, compartiré un secreto: puedes encontrar las varas en los orcos del Clan Faucedraco en los Humedales.",
    ["O"] = "Lleva el Tomo Reconstruido y 3 Varas de Canalización a Strahad Farsan en Ratchet.",
    ["T"] = "Tomo del Cábala",
  },
  [1806] = {
    ["D"] = "¿Por qué no vamos a la forja y comenzamos con esto? He estado preparándome para tu llegada por algún tiempo, así que no debería tomar mucho.$B$BCuando terminemos, estarás mucho más preparado para defender la Luz de lo que has estado, te lo juro.$B$BSeré un poco honesto, $N, ver los resultados de tus hazañas me da un poco de envidia. Pero saber que puedo contribuir a tus aventuras de mi propia manera significativa me trae algo de alegría.",
    ["O"] = "Espera a que Jordan Stilwell termine de forjar un arma para ti.",
    ["T"] = "La Prueba de la Rectitud",
  },
  [1818] = {
    ["D"] = "Me dijeron que el Guardia de la Muerte Dillinger tiene un trabajo para alguien como tú. Si estás dispuesto a demostrar tu valor a las fuerzas de la Dama Oscura, entonces repórtate con Dillinger de inmediato.$B$BEl puesto de Dillinger está en el extremo oeste de Rémol, cerca del cementerio.",
    ["O"] = "Habla con el Exterminador Dillinger en Rémol.",
    ["T"] = "Hablar con Dillinger",
  },
  [1819] = {
    ["D"] = "Cuando el Azote arrasó los Claros de Tirisfal, capturamos a uno para estudiarlo. Tenía una mente libre y se hacía llamar Ulag el Cortador. Pero, aunque su voluntad era libre, todavía estaba cautivado por el Rey Exánime.$B$BEsperábamos encontrar una manera de romper el control del rey sobre Ulag, pero fallamos. Ahora, finalmente, es hora de liberarlo. $B$BLibéralo, y mátalo.$B$BActiva el disparador de la piedra de cráneo cerca del mausoleo al norte. Esto levantará el sello mágico que lo ata.$B$BLuego... prepárate, $N.",
    ["O"] = "Mata a Ulag el Descuartizador, luego habla con Guardia de la Muerte Dillinger.",
    ["T"] = "Ulag el Destripador",
  },
  [1820] = {
    ["D"] = "Has demostrado tu valor ante la Dama Oscura, ahora quizás puedas demostrarte ante uno de sus súbditos.$B$BColeman Farthing espera a un guerrero que esté dispuesto a ser su instrumento de venganza contra los enemigos de los No-Muertos.$B$BLo encontrarás en la Taberna del Fin del Mundo en el este de Rémol.",
    ["O"] = "Habla con Coleman Farthing.",
    ["T"] = "Hablar con Coleman",
  },
  [1821] = {
    ["D"] = "La familia Agamand fue mi empleadora en vida, ¡y fueron la causa de mi muerte! Es apropiado que los Agamand ahora sean siervos sin mente del Rey Exánime.$B$BAhora quiero sus últimos vestigios de honor. Entra en su cripta familiar y róbales sus reliquias familiares: una espada, un hacha, una daga y una maza. ¡Tráemelas!$B$BLa cripta de la familia Agamand está en el extremo norte de los molinos Agamand, protegida por el Azote y los Agamand muertos.",
    ["O"] = "Lleva a Coleman Farthing el Hacha de la Familia Agamand, la Espada de la Familia Agamand, la Maza de la Familia Agamand y la Daga de la Familia Agamand.",
    ["T"] = "Reliquias de los Agamand",
  },
  [1822] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Arma Heredada",
  },
  [1823] = {
    ["D"] = "Eres un guerrero hábil y de creciente renombre. ¿Quizás estás listo para la prueba de Ruga Ragetotem? Si es así, entonces encuentra a Ruga en el Campamento Taurajo en los Baldíos.$B$BÉl espera a aquellos guerreros que respondan a su llamado.",
    ["O"] = "Habla con Ruga Tótem de Ira.",
    ["T"] = "Hablar con Ruga",
  },
  [1824] = {
    ["D"] = "Para pasar esta prueba, debes viajar al sur al Campo de los Gigantes. Allí encontrarás una multitud de criaturas insectoides que se arrastran. Son nuevas en los Baldíos y no me gusta lo que presagian.$B$BTráeme sus antenas aún retorciéndose, y no pierdas tiempo, $N, porque las antenas cosechadas no se retorcerán por mucho tiempo. Tráeme las antenas en el tiempo permitido, y pasarás la prueba.",
    ["O"] = "Lleva 5 antenas espasmódicas a Ruga Ragetotem en Campamento Taurajo.",
    ["T"] = "Prueba en el Campo de los Gigantes",
  },
  [1825] = {
    ["D"] = "Thun\'grim Firegaze, un herrero orco de renombre, ha oído hablar de tus continuas hazañas de valor al servicio de la Horda. Le gustaría confeccionarte un traje de armadura y te pide que hables con él de inmediato.$B$BThun\'grim está acampado al este de las Cruces en los Baldíos.",
    ["O"] = "Habla con Thun\'grim Mirada de Fuego en los Baldíos.",
    ["T"] = "Hablar con Thun\'grim",
  },
  [1838] = {
    ["D"] = "He escuchado historias de tu fuerza y valor, y deseo hacerte una armadura, un compañero que te ayude mientras las historias de tus grandes hazañas crecen.$B$BLa creación de esta armadura será una misión en sí misma, $N. Necesitaré tu ayuda y la ayuda de otros herreros de la Horda.$B$BToma este pergamino. Pasé muchas noches, luchando con ojos casi ciegos, escribiendo lo que necesito para la armadura. Tráeme los artículos en este pergamino y crearé para ti la primera pieza del conjunto.",
    ["O"] = "Lleva a Thun\'grim Firegaze 15 Lingotes de Hierro Ahumado, 10 Azurita en Polvo, 10 Barras de Hierro y un Frasco de Flogisto.",
    ["T"] = "Armadura Brutal",
  },
  [1839] = {
    ["D"] = "El herrero troll Ula\'elek te fabricará los guantes brutales. Vive en Durotar, en la Aldea Sen\'jin con sus hermanos Lanza Negra, y espera tu llegada.",
    ["O"] = "Habla con Ula\'elek en Durotar.",
    ["T"] = "Ula\'elek y los Guanteletes Brutales",
  },
  [1840] = {
    ["D"] = "Orm Pezuñapétrea es muy respetado por su habilidad. Él te hará el yelmo brutal.$B$BBúscalo en Cima del Trueno, cerca del estanque junto al gran tótem central de la ciudad.",
    ["O"] = "Habla con Orm Pezuñapétrea en Cima del Trueno.",
    ["T"] = "Orm Pezuña de Piedra y el Yelmo Brutal",
  },
  [1841] = {
    ["D"] = "Se dice que, antes de su muerte, Velora Nitely era una de las mejores armaduras de Lordaeron. La muerte, al parecer, no ha disminuido sus talentos. Habla con Velora: ella hará tus grebas brutales.$B$BVelora ejerce su oficio en la Ciudad de Entierro de Lordaeron, en el barrio comercial.",
    ["O"] = "Habla con Velora Nitely en Entrañas.",
    ["T"] = "Velora Nitely y las Leotardas Brutales",
  },
  [1842] = {
    ["D"] = "Puedo hacerte un par de guantes brutales, y contendrán una gran magia. Pero primero, debes recolectar esta magia y traérmela.$B$BLos sátiros habitan en el noreste de Vallefresno debido al poder del bosque. Se alimentan de su pureza, chupando y corrompiendo. Puedes saber qué sátiros han absorbido más magia de Vallefresno por sus pezuñas hinchadas y no hendidas.$B$BEstas pezuñas contienen un gran poder. Las necesitaré para la fabricación de tus guantes.",
    ["O"] = "Lleva 7 Pezuñas de Sátiro no Hendidas a Ula\'elek en la Aldea Sen\'jin en Durotar.",
    ["T"] = "Pezuñas de Sátiro",
  },
  [1843] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Guanteletes Brutales",
  },
  [1844] = {
    ["D"] = "Te haré el yelmo, pero debes demostrarme que eres digno de él.$B$BEn los confines noroccidentales de las Montañas de los Baldíos se encuentra el Valle Carbonizado. Una vez fue un lugar verde, pero ahora está profanado y cubierto de cenizas, y las criaturas que quedan allí están locas de dolor y desesperación.$B$BEse es el lugar al que te envío, $N.$B$BDebes ir al Valle Carbonizado y encontrar a una matriarca quimera. Mátala, libérala de su miseria. Tráeme su cuerno galvanizado como prueba, y te haré el yelmo.",
    ["O"] = "Lleva un Cuerno Galvanizado a Orm Pezuña de Piedra en Cima del Trueno.",
    ["T"] = "Cuerno Quimérico",
  },
  [1845] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Yelmo Brutal",
  },
  [1846] = {
    ["D"] = "Para hacer las grebas brutales, necesito un par de tibias resistentes. Los orcos Faucedraco de los Humedales tienen tibias bonitas y gruesas. Necesitaré las suyas.$B$BPero deben ser resistentes. Asegúrate de probar las tibias mientras las recolectas. Si puedes romperlas con tus propias manos, entonces no serán lo suficientemente resistentes para trabajar con ellas.",
    ["O"] = "Lleva 8 Tibias Faucedraco Robustas a Velora Nitely en Entrañas.",
    ["T"] = "Huesos de Faucedraco",
  },
  [1847] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Leotardas Brutales",
  },
  [1848] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "El Coselete Brutal",
  },
  [1858] = {
    ["D"] = "Te dejaré a ti abrir el bolso de Tazan y regresar a mí con su contenido. El mecanismo de bloqueo es complejo, por lo que probablemente sería más fácil si obtuvieras la llave.$B$BComo no la encontraste en el cuerpo de Tazan, solo puedo asumir que dejó la llave al cuidado de otro... Ah, sí, eso es. Tenía un asistente llamado Gamon. Un borracho, lo más probable es que lo encuentres en la taberna. Tal vez podrías quitársela discretamente.",
    ["O"] = "Roba la llave de Tazan, luego úsala para abrir su alforja y entregar su contenido a Therzok en la Grieta de la Sombra en Orgrimmar.",
    ["T"] = "La Mano Destrozada",
  },
  [1859] = {
    ["D"] = "Baja la voz, $n. Sí, conozco tu nombre. Me dijeron que estuviera atento a un orco con tu descripción.$B$BPor lo que hemos oído de ti, puedes defenderte en combate y no te opones a un poco de sigilo de vez en cuando.$B$B¿Estoy en lo cierto?$B$BDe todos modos, deberías ir a ver a Therzok en Orgrimmar. Tiene una oferta para ti.",
    ["O"] = "Habla con Therzok en la Grieta de la Sombra en Orgrimmar.",
    ["T"] = "Therzok",
  },
  [1860] = {
    ["D"] = "Los magos tenemos una tarea que necesita hacerse. Una que es vital para la seguridad de Ventormenta y el bosque de Elwynn.$B$BJennea Cannon tiene los detalles. Ella estudia en el sanctum del mago, en el distrito de los magos de Ventormenta. Deberías dirigirte a ella con prisa.",
    ["O"] = "Habla con Jennea Cannon en Ventormenta.",
    ["T"] = "Hablar con Jennea",
  },
  [1861] = {
    ["D"] = "Este mundo ha sido víctima de grandes luchas místicas, que arrancaron las corrientes mágicas de los canales una vez estables. Luchamos por determinar los efectos que estos cambios han causado, ¡y algunos de nosotros tememos lo peor!$B$BPero revelaré más de esto más tarde. Ahora mismo, necesitamos que reúnas información.$B$BAquí, toma este frasco. Ve a la base de la cascada en el Lago Espejo, al suroeste de las puertas de Ventormenta, y recupera una muestra del agua allí. Devuélvemela para que pueda probarla en busca de contaminaciones mágicas.",
    ["O"] = "Lleva una muestra del Lago Espejo a Jennea Cannon en Ventormenta.",
    ["T"] = "Lago Espejo",
  },
  [1878] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Recompensa por Bolsas de Agua",
  },
  [1879] = {
    ["D"] = "$N! Bink, mi colega gnoma en Forjaz, envía noticias. ¡Ella tiene una tarea que necesita hacerse y no confiará en nadie más que en un mago para hacerla! ¿Eres tú ese mago, $N?$B$BSi es así, habla con Bink. Pasa su tiempo en el Salón de los Misterios en el Barrio Místico de Forjaz.",
    ["O"] = "Habla con Bink en Forjaz.",
    ["T"] = "Hablar con Bink",
  },
  [1880] = {
    ["D"] = "¡Los magos hacemos más que solo mover los dedos y hacer chispas bonitas! ¡Controlamos la magia, la base misma de nuestro mundo! Y eso requiere mucho cuidado, determinación... y conocimiento... ¡tanto conocimiento!$B$B¡Uno de mis dispositivos, el gizmonitor mágico, recolecta y almacena ese conocimiento en grandes cantidades! ¡Pero está perdido! Cuando huimos de nuestro hogar en Gnomeregan, lo dejé en un cofre en una de nuestras cabañas fuera de la ciudad, ¡y ahora el lugar está lleno de gnomos leprosos!$B$B¡Por favor, $N, recupera mi gizmonitor!",
    ["O"] = "Lleva a Bink su Gizmonitor Mágico.",
    ["T"] = "Gizmonitor Mágico",
  },
  [1881] = {
    ["D"] = "$N, Anastasia Hartwell tiene una tarea para alguien de tus talentos. Habla con ella en el Barrio de la Magia de Entrañas, y prepárate para tu primera tarea para los magos de los No-Muertos.",
    ["O"] = "Habla con Anastasia Hartwell.",
    ["T"] = "Hablar con Anastasia",
  },
  [1882] = {
    ["D"] = "Las Tierras de la Peste al este son una aberración. Diseñadas por el Rey Exánime, extienden su magia al suelo, corrompiendo y matando todo lo que vive. Aunque hay un cierto encanto en las fuerzas destructivas de la plaga, nosotros, los magos de los No-Muertos, creemos que es descontrolada y debe ser contenida.$B$BPero primero, debemos medir cuánto se ha extendido en Tirisfal. Ve a la Granja Balnir en el este de Tirisfal. En el jardín hay flores: dragones. Recógelas y regresa.",
    ["O"] = "Lleva Dragonantes de Balnir a Anastasia Hartwell en el Barrio de los Magos de Entrañas.",
    ["T"] = "La Granja de los Balnir",
  },
  [1883] = {
    ["D"] = "Un\'thuwa, mago tribal de la Aldea Sen\'jin, detecta una perturbación en las corrientes mágicas de Durotar y pide ayuda. Habla con Un\'thuwa y haz lo que puedas para ayudarlo.$B$BHaz esto, y con el tiempo se te revelarán más secretos.",
    ["O"] = "Habla con Un\'thuwa.",
    ["T"] = "Hablar con Un\'thuwa",
  },
  [1884] = {
    ["D"] = "El brujo Zalazane de las Islas del Eco ha interrumpido el flujo normal de la magia en Durotar. Lo hace a través de fetiches contaminados, montones de ju-ju, que ha colocado entre sus chozas en la isla principal de las Islas del Eco.$B$BEstos montones corrompen nuestra vida silvestre y dan poder a los brujos y criaturas infernales que acechan en los rincones oscuros de la tierra.$B$BDebes destruir los montones de ju-ju, $N, antes de que el flujo de la magia se altere para siempre.",
    ["O"] = "Destruye 4 Montones de Ju-Ju y luego regresa con Un\'thuwa en la Aldea Sen\'jin.",
    ["T"] = "Montones de Ju-Ju",
  },
  [1885] = {
    ["D"] = "$n? No es exactamente lo que esperaba, considerando que el propio Carkad ha mostrado interés en ti. Las apariencias engañan, ¿verdad?$B$BDe cualquier manera, deberías ir a verlo lo antes posible. No querrás saber qué le pasó a los últimos que eligieron ignorar su llamada.",
    ["O"] = "Habla con Mennet Carkad en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Mennet Carkad",
  },
  [1886] = {
    ["D"] = "Si deseas unirte a los Acechadores de la Muerte, la guardia de élite de Lord Varimathras, tendrás que demostrar tu valía.$b$bNo te mentiré, $n, esta tarea podría significar tu muerte.$b$bTengo algunas cosas que necesitan atención. Necesito los efectos personales de Astor Hadren, un mensajero que viaja entre el Bosque del Ocaso y Entrañas. Dejaré el método de obtención a tu discreción.$b$bSerá mejor para ti si sabes menos sobre la naturaleza de este asunto. Ten éxito y te contaré más.",
    ["O"] = "Consigue la Carta de Presentación de Astor y devuélvesela a Mennet Carkad en el Barrio de los Pícaros.",
    ["T"] = "Los Mortacechadores",
  },
  [1898] = {
    ["D"] = "La carta de presentación debería ser suficiente para convencer a Andron de que eres el mensajero que ha estado esperando. He conocido a Andron antes, no tiene la capacidad mental para ser tan sospechoso.$B$BLord Varimathras sospecha que Andron ha estado proporcionando ayuda a algún otro grupo. Él desea saber qué organización es esta y requiere pruebas de ello.$B$BQuiero que hables con Andron y obtengas esta información. Lo encontrarás en su tienda cerca del Antro de los Apothecarios.",
    ["O"] = "Habla con Andron Gant en Entrañas.",
    ["T"] = "Los Mortacechadores",
  },
  [1899] = {
    ["D"] = "Encontrarás el libro mayor con la información que buscan tus empleadores en la estantería detrás de mí. Entrégalo con mis saludos, y diles que preferiría que te enviaran a ti, si encuentran suficiente valor como para desear continuar nuestro acuerdo.",
    ["O"] = "Lleva el Libro de Cuentas de Astor a Mennet Carkad en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Los Mortacechadores",
  },
  [1918] = {
    ["D"] = "El elemental asesinado ha producido un globo de agua que brilla intensamente. El contenido del globo es, en el mejor de los casos, repugnante. Sería seguro asumir que lo que ha contaminado el agua del Lago Mystral probablemente esté presente en el agua del globo.$B$BNo es exactamente de conocimiento común quién podría estar interesado en tal hallazgo, quizás la persona que originalmente te envió al lago, Mastok Wrilehiss en el Puesto del Hachazo, podría tener una idea.",
    ["O"] = "Lleva el Globo de Agua Contaminado a Mastok Wrilehiss en el Campamento de la Trama Esmeralda, Vallefresno.",
    ["T"] = "El Elemento Corrupto",
  },
  [1919] = {
    ["D"] = "Tu conocimiento del oficio crece, joven $N. ¿Estás listo para comenzar tus verdaderas lecciones? ¿Estás listo para tomar tu lugar en la guerra secreta que libramos?$B$BSi es así, habla con Jennea Cannon, en el santuario de los magos en el distrito de los magos de Ventormenta. Ella tiene una tarea para ti que te abrirá tu primera ventana al mundo invisible.",
    ["O"] = "Habla con Jennea Cannon en Ventormenta.",
    ["T"] = "Informar a Jennea",
  },
  [1920] = {
    ["D"] = "Se ha reportado una perturbación en la taberna El Recluse Azul en el Barrio de los Magos. Extrañas criaturas invisibles ahora deambulan por la taberna, y temo que se haya formado una grieta de maná allí.$B$BQuiero que investigues la taberna y captures a esas criaturas. Usa el cántico de manifestación en la taberna para hacer visibles a las criaturas, somételas y luego usa un cofre de contención para capturarlas. Cuando hayas capturado suficientes, regresa a mí con tus cofres llenos, tus cofres vacíos y el cántico.",
    ["O"] = "Consigue una Cantación de Manifestación y un Cofre de Arquetas de Contención detrás de Jennea Cannon. Lleva 3 Arquetas de Contención Llenas a Jennea en el Sagrario del Mago.",
    ["T"] = "Investigar el Recluse Azul",
  },
  [1921] = {
    ["D"] = "Has servido bien a tus compañeros magos, y ahora es el momento de vestir tu túnica de mago. Wynne Larson puede hacer tu túnica, pero debes traerle los materiales necesarios.$B$BLa túnica estará hecha de lino, que es fácil de encontrar, pero también debes obtener gemas de grieta cargadas, y el único lugar conocido donde se encuentran estas gemas es en la Mina del Arroyo de Plata de Loch Modan, dejadas en cajas por la liga de mineros enanos.$B$BReúne el lino y las gemas y llévaselas a Wynne Larson en la Sastrería Larson en el Distrito de los Magos.",
    ["O"] = "Lleva 10 Lino y las 6 Gemas de Falla Cargadas a Wynne Larson en Ventormenta.",
    ["T"] = "Reuniendo Materiales",
  },
  [1938] = {
    ["D"] = "Hace mucho tiempo, tres magos estudiaron en esta torre hasta que uno cayó en las artes oscuras. Se le instó a alejarse de esa magia maligna, pero no pudo resistirse. Y así fue desterrado.$B$BEl nuevo dominio del mago caído es la torre de Ilgalar en Crestagrana, y se llevó consigo un poderoso libro: el Tratado de Ur sobre la Magia de las Sombras.$B$BNosotros, los magos de Ventormenta, queremos que ese libro sea devuelto. Entra en la torre de Ilgalar, encuentra el tomo en su biblioteca y tráemelo.",
    ["O"] = "Lleva el Tratado de Ur sobre Magia de las Sombras al Alto Hechicero Andromath en Ventormenta.",
    ["T"] = "Tratado de Ur sobre la Magia de las Sombras",
  },
  [1939] = {
    ["D"] = "El Sumo Mago Andromath desea hablar contigo, $N. Está en la cima del santuario de los magos, en el distrito de los magos de Ventormenta.$B$BSería prudente hablar con él, ya que Andromath es uno de los magos más poderosos que no están encerrados en Dalaran. Su consejo es de gran importancia.",
    ["O"] = "Habla con el Alto Hechicero Andromath.",
    ["T"] = "Alto Hechicero Andromath",
  },
  [1940] = {
    ["D"] = "Tu servicio ha sido inquebrantable, $N. Creo que, tal vez, es hora de que tengas una prenda más fina, una que se ajuste más a tu estación como mago adepto. Si reúnes los materiales necesarios, entonces arreglaremos que te hagan la túnica.$B$BDebes entrar en Bosque del Ocaso y cazar arañas reclusas carroñeras. Extrae su seda, pero solo la seda más fina y prístina servirá. Cuando tengas suficiente, llévasela a Wynne Larson, aquí en el distrito de los magos en la Sastrería Larson. Ella te confeccionará la túnica.",
    ["O"] = "Lleva 8 Seda de Araña Pristina a Wynne Larson en Ventormenta.",
    ["T"] = "Seda de Araña Inmaculada",
  },
  [1941] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Toga de Tejido de Maná",
  },
  [1942] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Vestimenta de Nudo Astral",
  },
  [1943] = {
    ["D"] = "La maga troll Deino necesita tu ayuda, $N. Reside en Orgrimmar, en la Logia Darkbriar.",
    ["O"] = "Habla con Deino.",
    ["T"] = "Hablar con Deino",
  },
  [1944] = {
    ["D"] = "La legendaria ciudad de Xavian se encuentra escondida en lo profundo del este de Vallefresno, y los relatos de su antiguo conocimiento son bien conocidos entre los eruditos. Se dice que las aguas de Xavian contienen vastos poderes mágicos.$B$BMe gustaría tener una muestra de esas aguas, de su fuente.$B$BToma este frasco y ve a Xavian. Recolecta agua del fondo de su cascada más alta y regresa conmigo. Y ten cuidado mientras te mueves por esa antigua ciudad: los sátiros ahora poseen las ruinas y no les gusta que los magos husmeen en sus asuntos.",
    ["O"] = "Lleva la Muestra de Agua de Xavian a Deino en Orgrimmar.",
    ["T"] = "Aguas de Xavian",
  },
  [1945] = {
    ["D"] = "Por tus servicios, he organizado la creación de tus nuevas túnicas, siempre que puedas reunir los materiales necesarios...$B$BPara estas túnicas, debes regresar a Vallefresno por los mechones de cabello de las hermanas risueñas, dríadas que se reúnen cerca del Refugio de Raynewood.$B$BCuando tengas su cabello, llévaselo a Kil\'hala, un sastre troll en El Cruce en Los Baldíos. Él te estará esperando y te confeccionará las túnicas.",
    ["O"] = "Lleva 12 Cabellos de Hermana Risueña a Kil\'hala en El Cruce.",
    ["T"] = "Hermanas Riendo",
  },
  [1946] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Vestimenta de Encaje Abisal",
  },
  [1947] = {
    ["D"] = "Ahora es el momento de que ganes tu varita de mago. Para comenzar esta misión, habla con la ermitaña humana Tabetha en los Baldíos de Dustwallow. Encontrarás su choza en lo profundo del pantano, al oeste de Theramore.$B$BHabla con ella, pues su conocimiento es vasto. Encontrarás la cabaña de Tabetha al oeste de Theramore, y justo al norte de las Ruinas de los Quebrantamuelas.",
    ["O"] = "Habla con Tabetha en los Pantanos de Veleno.",
    ["T"] = "Viaje al Pantano",
  },
  [1948] = {
    ["D"] = "La construcción de la varita de un mago no es fácil. Se requieren sustancias raras, y se debe hacer un recipiente que pueda almacenar grandes cantidades de energía mágica. Debes reunir estas cosas y traérmelas.$B$BHe escrito en este pergamino lo que necesitas y las instrucciones sobre cómo conseguirlo. Tráeme exactamente lo que está en esta lista y sigue mis instrucciones escritas al pie de la letra, porque si no lo haces, cuando hagamos tu varita, las cosas podrían salir... mal.",
    ["O"] = "Lleva 1 Jade y la Zarza Cargada de Rayo a Tabetha en Los Baldíos.",
    ["T"] = "Objetos de Poder",
  },
  [1949] = {
    ["D"] = "Para hacer tu varita, hay pasajes clave en un cierto libro que debo leer. El libro se llama Rituales de Poder, escrito por el Magus Tirth.$B$BNo tengo una copia de su libro, y ninguna de las grandes bibliotecas del mundo la posee. Así que debes hablar con Tirth y obtenerla de él.$B$BBuena suerte. Tirth solía ser un gran mago y erudito, pero ahora pasa su tiempo en las carreras de gnomos y goblins en los Llanos de Sal, bebiendo y apostando.",
    ["O"] = "Habla con el Magus Tirth en el Desierto Brillante.",
    ["T"] = "Secretos Ocultos",
  },
  [1950] = {
    ["D"] = "Puedo ayudarte con el libro, ¡pero primero tienes que ayudarme!$B$BEl tomo que buscas está en mi caja fuerte mágicamente cerrada. Para abrirla, se debe pronunciar una frase especial, ¡y no puedo recordar la frase! Normalmente mi asistente me ayuda a recordar estas cosas, pero no está por ningún lado.$B$B¡Si quieres mi libro, entonces encuentra a mi aprendiz! Pregunta por la pista de carreras, si tienes suerte, alguien lo habrá visto. O tráelo de vuelta aquí, o al menos consigue mi frase mágica de él para que podamos abrir mi caja.",
    ["O"] = "Encuentra la frase del cofre fuerte de Tirth y luego regresa con Tirth.",
    ["T"] = "Obtener la Primicia",
  },
  [1951] = {
    ["D"] = "Hmm... ahora que lo pienso, ¡no tengo mi libro! Verás, tenía algunas deudas de juego y para pagarlas vendí la última copia de Rituales de Poder a un miembro del clero...$B$BUn miembro de la Hermandad Escarlata. Debe haberlo llevado a su biblioteca en el Monasterio Escarlata en Tirisfal. ¡Si quieres ese libro, tendrás que ir al monasterio a buscarlo! Y lo siento por todos los problemas. No estoy en mi mejor momento estos días...",
    ["O"] = "Lleva el libro Rituales de Poder a Tabetha en los Pantanos de Dustwallow.",
    ["T"] = "Rituales de Poder",
  },
  [1952] = {
    ["D"] = "Iré a hacer las varitas ahora.",
    ["O"] = "Habla con Tabetha después de su ritual.",
    ["T"] = "Varita del Mago",
  },
  [1953] = {
    ["D"] = "Es hora, $N, de que hables con Tabetha... la ermitaña de Marjal Revolcafango. Ella te instruirá en la creación de un poderoso implemento de mago.$B$BLa choza de Tabetha se encuentra en lo profundo de Marjal Revolcafango, al oeste de Theramore.",
    ["O"] = "Habla con Tabetha en los Pantanos de Veleno.",
    ["T"] = "Regreso al Pantano",
  },
  [1954] = {
    ["D"] = "El primer paso es adquirir un orbe con poder. Los brujos de la Espada de Fuego tienen orbes de buena calidad... ¡es una pena que hayan vinculado demonios en ellos! Pero podemos arreglar eso, sí podemos.$B$BVe a Desolace y arrebátale un orbe infernal a un invocador de la Espada de Fuego. Encontrarás invocadores en el Aquelarre de Mannoroc.$B$BCuando tengas el orbe, regresa conmigo.",
    ["O"] = "Lleva un Orbe Infernal a Tabetha en Los Baldíos de Polvo.",
    ["T"] = "El Orbe Infernal",
  },
  [1955] = {
    ["D"] = "Ahora que tenemos un orbe de invocador, debemos exorcizar al demonio que hay dentro. Yo puedo hacer eso. Esa es la parte fácil.$B$BTú eres el que tiene el trabajo difícil. Tú eres el que tiene que matar al demonio una vez que esté fuera.$B$BY tendrás que ser rápido. Si tardas demasiado, volverá a meterse en el orbe y tendremos que empezar de nuevo.$B$BAsí que prepárate. Prepara tus hechizos más poderosos y ten tus pociones a mano, y consigue un amigo si tienes uno. Porque esto no va a ser bonito.",
    ["O"] = "Mata al Demonio del Orbe, luego habla con Tabetha.",
    ["T"] = "El Exorcismo",
  },
  [1956] = {
    ["D"] = "Ahora que el demonio ha sido removido, debemos obtener una fuente de poder. Desafortunadamente, las mejores fuentes de poder suelen ser las más antiguas y las más difíciles de conseguir. Es curioso cómo el destino a veces te da un mordisco, ¿no?$B$BAsí que tendrás que ir a Uldaman y derrotar al centinela de obsidiana que hay allí. ¡Es grande, es duro, y su fuente de energía tiene un golpe poderoso!$B$BTe deseo la mejor de las suertes, $N. Y si no tienes suerte, bueno... fue un placer conocerte.",
    ["O"] = "Recupera una Fuente de Poder de Obsidiana y llévasela a Tabetha en Los Pantanos de Dustwallow.",
    ["T"] = "Poder en Uldaman",
  },
  [1957] = {
    ["D"] = "Estamos casi terminados, pero este último paso es el más peligroso, $N.$B$BTengo el orbe del invocador vacío y estoy listo para potenciarlo. Lo haremos aprovechando la energía de una grieta de maná. Lanzaré un hechizo que abrirá temporalmente una grieta, de la cual saldrán criaturas, oleadas de maná. Debes derrotar a las oleadas de maná y su poder será liberado, el cual recolectaré.$B$BMata suficientes oleadas de maná antes de que la grieta se cierre y colocaré toda su energía recolectada en tu orbe.",
    ["O"] = "Mata 12 Oleadas de Maná en el tiempo límite permitido.",
    ["T"] = "Oleadas de Maná",
  },
  [1958] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Poder Celestial",
  },
  [1959] = {
    ["D"] = "Los magos de Entrañas han detectado una perturbación mágica dentro de la ciudad y han enviado un mensaje para que los magos de la Horda investiguen.$B$BAnastasia Hartwell puede darte más información. Habla con ella en el Barrio de la Magia en Entrañas.",
    ["O"] = "Habla con Anastasia Hartwell.",
    ["T"] = "Informar a Anastasia",
  },
  [1960] = {
    ["D"] = "La tienda de alquimia de Algernon en el Apothecarium es el centro de una extraña perturbación. Criaturas invisibles, hechas únicamente de magia, deambulan por la tienda, debe haber una razón, ¡y debemos encontrarla!$B$BInvestiga la perturbación y captura a esas criaturas. Toma un cofre de arcas y un pergamino detrás de mí. Usa el pergamino en la tienda para hacer visibles a las criaturas, somételas con tus hechizos y luego lanza un arca para capturarlas. Regresa con las arcas llenas, las arcas vacías y el cántico.",
    ["O"] = "Consigue una Cantación de Manifestación y un Cofre de Arquetas de Contención detrás de Anastasia Hartwell. Lleva 3 Arquetas de Contención Llenas, el Cofre de Arquetas de Contención y la Cantación de Manifestación a Anastasia en Entrañas.",
    ["T"] = "Investigar la Tienda del Alquimista",
  },
  [1961] = {
    ["D"] = "Es hora de que vistas tu primera túnica de mago, $N. Son un signo de tu estación y un testimonio de tu valía.$B$BPara obtener las túnicas, debes recolectar las gemas de maná de los magos de Dalaran de Bosque de Argénteos. Sus creencias son similares a las nuestras, por lo que matarlos es algo desagradable, pero si carecen de la fuerza para resistirnos, entonces no merecen sus vidas.$B$BTráeme las gemas y un suministro de lino a Josef Gregorian en la tienda de sastrería del Barrio de la Magia.",
    ["O"] = "Lleva 10 Lino y 6 Gemas de Maná de Dalaran a Josef Gregorian.",
    ["T"] = "Reuniendo Materiales",
  },
  [1962] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Togas de Fuego de Hechizo",
  },
  [1963] = {
    ["D"] = "El Jefe de Guerra es demasiado sutil en sus métodos. Es cauteloso al desatar el poder de la Horda. Una vez fuimos más fuertes, conquistadores poderosos. Ese orgullo debemos recuperar, y la Mano Destrozada lo hará.$B$BConstruimos fuerza desde las sombras y golpeamos a los expuestos, derribando secretamente a nuestros enemigos. Una tarea que debes completar para mí, para demostrar tu valía.$B$BEl troll Tazan comercia con los piratas cerca de Ratchet. Tiene información que debo tener. Mátalo y tráeme su bolsa.",
    ["O"] = "Mata a Tazan y lleva su Alforja a Therzok en La Grieta de la Sombra en Orgrimmar.",
    ["T"] = "La Mano Destrozada",
  },
  [1978] = {
    ["D"] = "¿El Sindicato? Nunca lo habría sospechado. Parece que los señores de Alterac han caído mucho en los años desde su traición.$B$BEl señor Varimathras estará complacido de saber esto, y como fuiste tan instrumental en este descubrimiento, sería apropiado que fueras tú quien se lo entregue.$B$BAh, ¿y cómo podría olvidarlo?$B$BBienvenido a los Acechadores de la Muerte.",
    ["O"] = "Entrega el Libro de Cuentas de Andron a Varimathras en el Salón de la Dama Oscura en Entrañas.",
    ["T"] = "Los Mortacechadores",
  },
  [1998] = {
    ["D"] = "El Lord Varimathras acaba de enviarme un mensaje sobre una amenaza para los No-Muertos que debe ser eliminada de inmediato. Su nombre es Fenwick Thatros, uno a quien el Lord Varimathras cree que es un líder de los no muertos que han echado raíces en el Bosque de Argénteos.$B$BTu nombre vino a mi mente de inmediato, $n. Tengo suficiente fe en tus habilidades para que lleves a cabo esta misión sin fallos.$B$BLos últimos informes de los Acechadores de la Muerte en Argénteos indican que Thatros puede ser encontrado cerca del muelle decrépito en Lordamere.",
    ["O"] = "Mata a Fenwick Thatros y lleva su cabeza de vuelta a Mennet Carkad en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Fenwick Thatros",
  },
  [1999] = {
    ["D"] = "Los magos del Kirin Tor en Molino Ámbar han guardado documentos importantes relacionados con el estado actual de Dalaran. Esto lo sabemos por las confesiones de uno de nuestros prisioneros. Lord Varimathras ha puesto la tarea de recuperarlos en manos de los Acechadores de la Muerte.$B$BEsta tarea cae sobre ti, $n. Necesitarás algo de equipo, sin embargo, y el conocimiento de cómo abrir cerraduras. Habla con Estelle Gendry, nuestra proveedora, y pídele un juego de herramientas antes de partir.",
    ["O"] = "Encuentra el Informe de Estado de Dalaran y llévaselo a Mennet Carkad en el Barrio de los Pícaros de Entrañas.",
    ["T"] = "Herramientas del Oficio",
  },
  [2038] = {
    ["D"] = "¡Como si estrellarme en el Loch no fuera suficiente, los troggs de la isla cercana me atacaron y robaron mis herramientas y los explosivos especiales Blastencapper de Blastenheimer! Sin girocóptero y sin explosivos, ¡el movimiento no tiene ninguna posibilidad!$B$BRecupera mis herramientas y encuentra mis explosivos Blastencapper, $N. ¡Por Gnomeregan!",
    ["O"] = "Encuentra y devuelve los suministros de Bingles:$B$BLa Llave de Bingles, el Destornillador de Bingles, el Martillo de Bingles y el Detonador de Bingles.",
    ["T"] = "Los Suministros Perdidos de Bingles",
  },
  [2039] = {
    ["D"] = "Hemos perdido contacto con Bingles. Fue enviado en una misión de reconocimiento sobre Gnomeregan y no ha reportado en más de una semana. Sé que se ha volado a sí mismo.$B$BFue visto por última vez volando sobre el Loch. Quizás puedas investigar, $N.",
    ["O"] = "Encuentra a Bingles Blastenheimer en Loch Modan.",
    ["T"] = "Encontrar a Bingles",
  },
  [2040] = {
    ["D"] = "¡Gnomeregan ha caído bajo el control de esos malvados troggs! La situación es grave, pero tal vez puedas ayudar, $N.$B$BEn lo profundo de las Minas de la Muerte hay una trituradora goblin funcional. Encuentra esa trituradora y trae de vuelta la fuente de energía intacta. Con la fuente de energía de la trituradora, podemos darle a nuestros excavadores girodrilmáticos el poder que necesitan para atravesar los bordes rocosos subterráneos de Gnomeregan, abriendo el camino para un asalto gnomo.",
    ["O"] = "Recupera el Gnoam Sprecklesprocket de las Minas de la Muerte y llévaselo a Shoni la Silenciosa en Ventormenta.",
    ["T"] = "Asalto Subterráneo",
  },
  [2041] = {
    ["D"] = "Quizás... Quizás puedas ayudarnos en la batalla por Gnomeregan.$B$BEn Ventormenta encontrarás al comandante de nuestro equipo de asalto subterráneo, Shoni la Silenciosa. Shoni necesita ayuda con sus excavadores girodrilmáticos.$B$BProbablemente la encuentres entre los enanos en Ventormenta.$B$BBuena suerte, $N.",
    ["O"] = "Habla con Shoni la Silenciosa en Ventormenta.",
    ["T"] = "Hablar con Shoni",
  },
  [2078] = {
    ["D"] = "Marinero, necesitas encontrar a mi primer oficial.$B$BToma esta llave, ponla en su cabeza y tráelo de vuelta aquí para que sirva al Capitán.$B$B¡Esos múrlocs y su jefe no estarán muy contentos una vez que mi primer oficial se ocupe de ellos!",
    ["O"] = "Encuentra al Primer Oficial de Gelkak, el Machacador 4100, y llévalo de vuelta a Gelkak.",
    ["T"] = "La Venganza de Gyromast",
  },
  [2098] = {
    ["D"] = "La vida de un pirata no es fácil, terrestre. ¡No se trata solo de saquear y beber! Por desgracia, mi barco se hundió cerca de la costa gracias a esos molestos Threshadons.$B$B¡Lo que necesito es que mi primer oficial vuelva a estar en orden!$B$BCuando llegué a la orilla, la llave de mi primer oficial se rompió en las rocas y fue robada por las bestias de Costa Oscura. Por el rabillo del ojo vi a algunos de esos Ragin\' Crawlers, Ferocious Foreststriders y múrlocs revisando los restos....",
    ["O"] = "Encuentra y devuelve las tres partes de la Llave de Gelkak a Gelkak Giromástil.",
    ["T"] = "La Recuperación de Gyromast",
  },
  [2118] = {
    ["D"] = "Una enfermedad se extiende por Costa Oscura, afectando las mentes y los cuerpos de todos los que toca.$B$BLos Osos Cardo han sido los más afectados por esta plaga. Las nobles bestias son ahora un instrumento de destrucción: rabiosas y frenéticas. Puede que tenga una cura para esta enfermedad.$B$BToma esta trampa al bosque y colócala en el suelo. Cualquier Oso Cardo Rabioso que cruce la luz se volverá dócil por un corto tiempo. Una vez que el oso esté dócil, te seguirá. Tráelo de vuelta aquí, $N.",
    ["O"] = "Captura un Oso Cardo Rabioso vivo y llévalo de vuelta a Tharnariun.$B$BSi fallas en capturar un Oso Cardo Rabioso y pierdes tu trampa, regresa con Tharnariun Cuidador de Árboles y pide otra trampa.",
    ["T"] = "Tierras Contaminadas",
  },
  [2138] = {
    ["D"] = "Como temía, la cura no funciona.$B$BMe entristece enormemente ordenar la muerte de cualquiera de las bestias de la naturaleza, pero los osos cardenal infectados y contaminados deben ser sacrificados.$B$BRegresa al bosque y destruye 20 osos cardenal rabiosos, $N. Ciertamente no pondrá fin a la plaga, pero ralentizará el daño que los animales están causando a nuestro bosque y sus habitantes por un tiempo.",
    ["O"] = "Purifica el bosque de 20 Osos Cardo Rabiosos y regresa con Tharnariun Cuidador de Árboles en Costa Oscura.",
    ["T"] = "Limpieza de los Infectados",
  },
  [2139] = {
    ["D"] = "La supervivencia de nuestra gente y tierras tiene costos desafortunados, $N.$B$BHas derramado la sangre de los sirvientes de la naturaleza, muchos animales enfermos has derribado. La gente de Auberdine está en deuda contigo, porque el dolor del bosque se ha aliviado, aunque sea solo por un momento.$B$BTengo una última tarea para ti, pero ten cuidado, tendrás que matar de nuevo. Espero que esta sea la última vez que te llame, $N.$B$BEn una cueva al noreste, cerca de Bashal\'Aran, reside la Madre de la Guarida. Mátala.",
    ["O"] = "Encuentra y mata a la Madre de la Manada.",
    ["T"] = "La Esperanza de Tharnariun",
  },
  [2158] = {
    ["D"] = "Cada aventurero debería descansar cuando llega el agotamiento, ¡y no hay mejor lugar para descansar y relajarse que en la Posada El Orgullo del León!$B$BMi mejor amigo, el tabernero Farley, dirige El Orgullo del León. Si le dices que te envié yo, puede que te dé tarifas especiales en comida y bebida.$B$BPara encontrar la Posada El Orgullo del León, viaja hacia el sur por el camino desde aquí, ¡no te la puedes perder!",
    ["O"] = "Habla con el Tabernero Farley en la Posada El Orgullo del León.",
    ["T"] = "Descanso y Relajación",
  },
  [2159] = {
    ["D"] = "Saludos, joven $c. ¿Puedes ofrecerme ayuda? Tengo un paquete de hierbas que debo entregar en el pueblo de Dolanaar. Pero todavía tengo negocios con los druidas de Cañada Umbría y no puedo irme todavía.$B$B¿Puedes entregar este paquete por mí? Debe ser enviado al posadero Keldamyr, en la posada de Dolanaar. Está a lo largo del camino, hacia el sur.",
    ["O"] = "Lleva el Paquete de Dolanaar al Tabernero Keldamyr.",
    ["T"] = "Entrega a Dolanaar",
  },
  [2160] = {
    ["D"] = "¡Oye! Pareces un aventurero robusto. Si planeas atravesar el paso, ¿podrías llevar un paquete a la posada en Kharanos?$B$B¿Planeabas detenerte en la posada, verdad? Si logras atravesar el paso, definitivamente querrás tomar un respiro allí.$B$BDe todos modos, lleva esto a Tannok Martillo Frío, el asistente del posadero. No puedo atravesar el paso yo mismo, y pasarán días hasta que llegue un escolta de los Montaraces.",
    ["O"] = "Entrega el Cofre de Suministros de la Taberna a Tannok Martillo de Escarcha en Kharanos.",
    ["T"] = "Suministros para Tannok",
  },
  [2161] = {
    ["D"] = "Disculpa, ¿puedes ayudarme?$B$BTengo una carga de comida aquí. Caminé por el camino y me enfrenté a escórpidos, arañas y ¡peores cosas! Traje la comida al Valle de los Retos porque pensé que la necesitaban y me gusta ayudar. ¡Pero no necesitan comida! Ahora tengo que llevarla de vuelta a Cerro Cortez y tengo miedo de todas las bestias en el camino...$B$B¿Puedes llevarla por mí? Solo soy un peón, pero tú eres un héroe. ¡No temes a nada!$B$BLlévala al tabernero Grosk en la posada de Cerro Cortez. ¡Gracias!",
    ["O"] = "Lleva la Carga de Ukor al Tabernero Grosk en Cerro del Cuervo.",
    ["T"] = "La Carga de un Peón",
  },
  [2178] = {
    ["D"] = "¿Hueles ese delicioso aroma? Es mi receta secreta, $N: estofado de zancudo fresco. ¡Delicioso!$B$B¿Quizás estarías interesado en aprender la receta?$B$BAntes de enseñarte la receta, tendrás que demostrar que puedes manejar a las bestias.$B$BAhora, no espero que domes uno y lo montes de regreso a mí, pero si puedes matar a algunos y traerme un poco de su jugosa carne, podría estar persuadido a mostrarte los caminos del estofado de zancudo.",
    ["O"] = "Lleva 5 Carnes de Zancudo a Alanndarian Noctecanto en Auberdine.",
    ["T"] = "Vida Tranquila",
  },
  [2198] = {
    ["D"] = "Has encontrado un collar deslustrado, aunque el deslustre oculta una artesanía intrincada y mágica. El collar ha sido destrozado, ya que su engaste parece estar faltando tres gemas. En la parte inferior del collar hay una leve impresión del sello de la ciudad de Forjaz. Debajo del sello hay tres pequeñas letras grabadas: \"TdK\".$B$BSi hay algo que se pueda sacar del collar, tendrás que aprender sobre sus orígenes. Quizás el sello y las iniciales sean un comienzo...",
    ["O"] = "Busca al creador original del collar destrozado para conocer su valor potencial.",
    ["T"] = "El Collar Destrozado",
  },
  [2199] = {
    ["D"] = "Soy un joyero de gran renombre aquí en Forjaz. Bueno, antiguo renombre, de todos modos... He caído en tiempos difíciles debido a las artimañas de un competidor. Una mala reputación en este negocio significa un desastre financiero. Como resultado, me encuentro sin un cobre a mi nombre, aunque actualmente manchado.$B$BQuiero que me consigas cinco barras de plata para terminar un pedido que recibí, el primero en lo que parece una eternidad.$B$BConsígueme esas cinco barras de plata y responderé tus preguntas sobre el collar.",
    ["O"] = "Lleva cinco Barras de Plata a Talvash del Kissel en Forjaz.",
    ["T"] = "Conocimiento por un Precio",
  },
  [2200] = {
    ["D"] = "Un competidor mío comenzó un rumor de que mi collar maldijo al paladín. No hace falta decir que nadie quiere comprar objetos malditos a un joyero.$B$B$N, te haré un trato: encuentra las gemas y reensamblaré el collar, siempre y cuando anuncies que ¡NO hago objetos malditos!$B$B¡Intenta buscar pistas en Uldaman, tal vez veas si el cuerpo del paladín está allí, ya que es el que sabemos que lo tuvo por última vez! Cuando encuentres las gemas, usa esto, te permitirá contactarme aquí en Forjaz.",
    ["O"] = "Busca pistas sobre el paradero actual del collar de Talvash dentro de Uldaman. El paladín asesinado que mencionó fue la última persona que lo tuvo.",
    ["T"] = "Regreso a Uldaman",
  },
  [2201] = {
    ["D"] = "El diario del paladín ha proporcionado quizás las únicas pistas que Uldaman ofrecerá sobre la ubicación de las gemas del collar. Si las anotaciones del paladín siguen siendo precisas, entonces los enanos Forjatiniebla y los troggs que compiten por el control del sitio de excavación deberán ser tratados.$B$BArmado con este conocimiento, la búsqueda de las gemas comienza en serio. Cuando todas las gemas hayan sido adquiridas, Talvash en Forjaz debe ser notificado a través del Frasco de Visión según sus instrucciones.",
    ["O"] = "Encuentra el rubí, el zafiro y el topacio que están esparcidos por Uldaman. Una vez adquiridos, contacta a Talvash del Kissel remotamente usando el Frasco de Videncia que te dio anteriormente.$B$BDel diario, sabes...$B* El rubí ha sido escondido en un área de los Forjatiniebla bloqueada.$B* El topacio ha sido escondido en una urna en una de las áreas de los Troggs, cerca de algunos enanos de la Alianza.$B$B* El zafiro ha sido reclamado por Grimlok, el líder de los troggs.",
    ["T"] = "Encontrar las Gemas",
  },
  [2202] = {
    ["D"] = "He oído hablar de una planta recién descubierta llamada hongos magenta. Crecen en grupos, en lo profundo del sitio de excavación de Uldaman, pueden estar en otros lugares, pero por ahora ese es el único lugar donde sé que están. Quiero estudiar su uso potencial en la alquimia, y ahí es donde entras tú.$B$BBusca los grupos de hongos magenta y tráeme una docena de tapas. Ten cuidado, los grupos pueden expulsar esporas venenosas si los agitas.$B$BHaz esto por mí, y te prepararé un lote de uno de mis famosos elixires restauradores.",
    ["O"] = "Lleva 12 Sombreros de Hongo Magenta a Jarkal Mossmeld en Kargath.",
    ["T"] = "Recolección de Reagentes en las Tierras Inhóspitas",
  },
  [2203] = {
    ["D"] = "Has sido muy útil para mí, $c. Te recompensaría con la receta de la cerveza que te di antes... pero te pediría una tarea bastante peligrosa. Escucha mi petición.$B$BEsta caja fuerte contiene tres vasijas de taumaturgia vacías, están imbuidas con un aura de sintonización que drenará la sangre de un dragón guardián abrasado. Usar una en la bestia la enfurecerá mucho, así que ten cuidado. Una vez que las hayas llenado todas, tráemelas.$B$BHaz esto, y la receta será tuya.",
    ["O"] = "Usa los recipientes de taumaturgia vacíos en los dragones guardianes chamuscados que se encuentran en las Tierras Inhóspitas. Una vez que los hayas llenado, llévaselos a Jarkal Mossmeld en Kargath.",
    ["T"] = "Recolección de Reagentes en las Tierras Inhóspitas II",
  },
  [2204] = {
    ["D"] = "Vas a necesitar encontrar algo de \"energía\" para el collar, y Uldaman es probablemente el mejor lugar para ello, en realidad. Supongo que hay muchos constructos corriendo por allí, ¿no? Bueno, ¡mata al constructo más grande y malo de ese lugar y toma su fuente de energía! Estoy bastante seguro de que puedo fusionar esa fuente en el collar.$B$BSin esa fuente de energía, arreglar el collar será imposible. Regresa conmigo aquí a Forjaz cuando la tengas, ¡y lo arreglaremos todo para ti!",
    ["O"] = "Consigue una fuente de poder del constructo más poderoso que puedas encontrar en Uldaman y entrégasela a Talvash del Kissel en Forjaz.",
    ["T"] = "Restaurando el Collar",
  },
  [2205] = {
    ["D"] = "¿La placa? Oh, no es nada, pez, nada que tú pudieras manejar de todos modos. Por tu apariencia, diría que Mathias probablemente te confundirá con el repartidor de queso en lugar de un pícaro adecuado. De todos modos, me han asignado un trabajo y he prometido cumplirlo.$B$BHablando de eso, entrega este paquete a Mathias y puede que te dé una pequeña tarea o dos.$B$BPonte en marcha.",
    ["O"] = "Entrega el paquete de Keryn Sylvius al Maestro Mathias Shaw en los Cuarteles de Ventormenta.",
    ["T"] = "Buscar a SI: 7",
  },
  [2206] = {
    ["D"] = "Nuestros operativos en el campo informan de una intensa actividad de los Defias al sur de Villadorada. Los informes indican que los Defias han tomado el Desembarco de Jerod y ahora lo usan como una forma de contrabandear grandes cantidades de mercancías ilegales desde Crestagrana a Páramos de Poniente.$B$BNecesito que infiltres la casa del muelle y me consigas ese horario de envíos, $N. Probablemente lo encontrarás enterrado en lo profundo de los bolsillos del Maestro del Muelle.$B$B...y $N, si te descubren, el SI:7 negará cualquier conocimiento de tu existencia.",
    ["O"] = "Encuentra al Jefe de Muelles Defias y recupera el Horario de Envíos para el Maestro Mathias Shaw.",
    ["T"] = "Arrebatar y Agarrar",
  },
  [2218] = {
    ["D"] = "Otra alma perdida buscando orientación, ¿eh? Supongo que quieres encontrar un propósito, tu propósito. Lo he visto mil veces, otro héroe buscando trabajo.$B$BBueno, hoy es tu día de suerte, amigo. Hogral tiene la cura para tu mal.$B$BAquí está lo que debes hacer: Toma este camino hacia Forjaz y dirígete a la Caverna Abandonada. Una vez allí, localiza el gremio de ladrones y habla con Hulfdan Barbanegra. Él te pondrá en el camino hacia la salvación.",
    ["O"] = "Habla con Hulfdan Barbanegra en Forjaz.",
    ["T"] = "Camino a la Salvación",
  },
  [2238] = {
    ["D"] = "Verás, el Rey está explotando la cantera para extraer minerales y gemas. Más riquezas de las que cualquier enano debería ver en diez vidas.$B$BEl Círculo Oculto tiene valores, $N. Valoramos los objetos valiosos. ¿Entiendes?$B$BAhora creemos que el Rey debería compartir algo de ese botín, pero no queremos que el viejo \"Barbapeste\" sepa que está compartiendo, ¿entiendes?$B$BNecesitamos que hables con Onin MacHammar cerca de Gnomeregan y traigas un informe. El astuto enano se ha escondido entre los leprosos. Ten cuidado.",
    ["O"] = "Encuentra a Onin MacHammar cerca de Gnomeregan.",
    ["T"] = "Subterfugio Simple",
  },
  [2239] = {
    ["D"] = "¿La Cantera? Sí, veo esos monstruosos tanques blindados pasar por este puesto casi todos los días.$B$BMis contactos al este me dicen que el tanque comienza en Forjaz, hace una recogida en la Cantera de Gol\'Bolar, se detiene en Kharanos para repostar y luego, por alguna maldita razón, toma el camino largo pasando por Gnomeregan, antes de llegar a Forjaz para dejar el cargamento.$B$BTodo está en el informe, $r. Llévaselo a Hulfdan para que pueda planificar el próximo paso.",
    ["O"] = "Lleva el Informe de Onin de vuelta a Hulfdan en Forjaz.",
    ["T"] = "El Informe de Onin",
  },
  [2240] = {
    ["D"] = "Ahora para la tarea que nos ocupa.$B$BHay una cámara secreta, la Cámara de Khaz\'mul, que debe ser explorada. El problema es que la cámara está detrás de una enorme puerta cerrada. Por suerte, sé cómo abrirla.$B$BHe descrito todo en mi diario, aquí en la mesa. Mi banda y yo estamos atascados aquí por ahora... así que tú, $N, ¡debes encontrar la cámara de Khaz\'mul por nosotros!$B$BLee mi diario, explora la cámara, luego informa al Prospector Stormpike en la Liga de Expedicionarios en Forjaz.",
    ["O"] = "Lee el Diario de Baelog, explora la cámara oculta, luego informa al Prospector Pico Tormenta.",
    ["T"] = "La Cámara Oculta",
  },
  [2241] = {
    ["D"] = "Dicen que estoy \"enamorado\", lo que sea que eso signifique.$B$BClaro, paso cada momento despierto pensando en Syurna. Claro, mi casa está llena de pinturas y dibujos de Syurna. Claro, a menudo paso días sin comer, dormir o beber, mientras lamento el amor que podríamos haber tenido. ¿Eso está tan mal?$B$B¡Ahora ella no me quiere ver! ¡A MÍ! He intentado enviarle mensajes, pero ni siquiera hablará con otra persona a menos que sea un pícaro.$B$B¿Podrías entregarle esta flor? No olvides decirle que es de Jannok.",
    ["O"] = "Lleva la Rosa de Jannok a Syurna en Darnassus.",
    ["T"] = "La Manzana Cae",
  },
  [2242] = {
    ["D"] = "Creemos que hay un ser que podría ayudarnos a entender lo que está sucediendo en los bosques de Teldrassil. Desafortunadamente, lograr que la criatura coopere ha sido desastroso.$B$BSe esconde en una rama en las afueras de la Arboleda del Oráculo. Todos los que se han acercado al sátiro, ya sea por la fuerza o la diplomacia, han encontrado su perdición. Quizás el sigilo sea la respuesta, $N.$B$BEncuentra lo que Sethir el Antiguo lleva en sus bolsas e infórmame.",
    ["O"] = "Encuentra a Sethir el Antiguo y trae cualquier pista que descubras a Syurna.",
    ["T"] = "El Destino Llama",
  },
  [2258] = {
    ["D"] = "$N, podría usar tu ayuda si estás dispuesto. Soy un alquimista de cierta reputación, y busco tu ayuda para adquirir algunos reactivos que se encuentran en las Tierras Inhóspitas.$B$BNecesito los siguientes artículos para reponer mis existencias: cinco mollejas de buitre, diez colmillos de coyote de risco y cinco fragmentos de elemental de roca. Obviamente, puedes obtenerlos de las bestias mismas, si no eres del tipo combativo, entonces encuentra amigos que lo sean.$B$BAdquiere estos artículos para mí, y obtendrás algo de moneda a cambio. ¿Qué dices?",
    ["O"] = "Lleva 5 Mollejas de Buitre, 10 Colmillos de Coyote de Riscos y 5 Fragmentos de Elemental de Roca a Jarkal Mossmeld en Kargath, Tierras Inhóspitas.",
    ["T"] = "Recolección de Reagentes en las Tierras Inhóspitas",
  },
  [2259] = {
    ["D"] = "No te preocupes, $N, no te estoy enviando a ver a Syurna. Erion requiere tu presencia en el Enclave Cenarion. ¡Date prisa!",
    ["O"] = "Contacta a Erion Susurros de las Sombras en Darnassus.",
    ["T"] = "Erion Sombramurmu",
  },
  [2260] = {
    ["D"] = "La noticia de tus hazañas se ha extendido lejos y cerca, $N. Tan lejos, de hecho, que la Inteligencia de Ventormenta te ha pedido por nombre.$B$BEl viaje es agotador y no está exento de peligros, pero una oportunidad para entrenar bajo SI:7 no debe tomarse a la ligera.$B$BUn nuevo mundo te espera, $N. Dirígete al Casco Antiguo en Ventormenta y habla con Renzik \"El Chuchillo\" en la sede de SI:7.",
    ["O"] = "Viaja a Ventormenta y busca a Renzik \"El Chafarote\".",
    ["T"] = "La Petición de Erion",
  },
  [2278] = {
    ["D"] = "Las piezas centrales de esta cámara del tesoro son cuatro enormes discos de platino, cada uno adornado con numerosos sigilos de origen desconocido. Tocarlos hace que tu mano hormiguee. Justo después de tocar los discos, la visión de un gran vigilante de piedra surge.$B$B\"Saludos. Soy un guardián del conocimiento. La síntesis de los Creadores de los Earthen ha sido documentada y almacenada aquí para este mundo en los Discos de Norgannon.\"$B$B¡Has descubierto una gran cantidad de conocimiento mundial previamente desconocido!",
    ["O"] = "Habla con el vigía de piedra y aprende la antigua sabiduría que guarda. Una vez que hayas aprendido lo que tiene para ofrecer, activa los Discos de Norgannon.",
    ["T"] = "Los Discos de Platino",
  },
  [2279] = {
    ["D"] = "Tocar los Discos de Norgannon una vez más hace que tu mano hormiguee. Esta vez, sin embargo, se escucha un extraño ruido de molienda desde dentro de los discos.$B$BTu mochila de repente se vuelve más pesada de lo que era antes. Dentro, ¡descubres una versión en miniatura de los discos!$B$BSin duda, un artículo de este tipo sería invaluable para alguien interesado en la historia antigua de Azeroth. La Liga de Expedicionarios en Forjaz es un grupo así, conocido en toda la Alianza, quizás puedan hacer buen uso de él.",
    ["O"] = "Lleva la versión en miniatura de los Discos de Norgannon a la Liga de Expedicionarios en Forjaz.",
    ["T"] = "Los Discos de Platino",
  },
  [2280] = {
    ["D"] = "Tocar los Discos de Norgannon una vez más hace que tu mano hormiguee. Esta vez, sin embargo, se escucha un extraño ruido de molienda desde dentro de los discos.$B$BTu mochila de repente se vuelve más pesada de lo que era antes. Dentro, ¡descubres una versión en miniatura de los discos!$B$BSin duda, un artículo de este tipo sería invaluable para alguien interesado en la historia antigua de Azeroth. Los sabios de Cima del Trueno son historiadores así, conocidos en toda la Horda, quizás puedan hacer buen uso de él.",
    ["O"] = "Lleva la versión en miniatura de los Discos de Norgannon a uno de los sabios en Cima del Trueno.",
    ["T"] = "Los Discos de Platino",
  },
  [2281] = {
    ["D"] = "Enviarte al campo ahora sería un suicidio. Renzik no necesita otro pícaro muerto en sus manos. No te preocupes, $N, Renzik te pondrá al día.$B$BDirígete a las Montañas Crestagrana y habla con un asociado de SI:7 llamado Lucius. Lo encontrarás merodeando por los muelles de Villa del Lago con los demás vagabundos.",
    ["O"] = "Dirígete a Villa del Lago en las Montañas Crestagrana y habla con Lucius.",
    ["T"] = "Encuentro en Crestagrana",
  },
  [2282] = {
    ["D"] = "Siempre hemos entrenado ladrones aquí durante años. Después de todo, no todos los granujas nacen con la gracia y el refinamiento del viejo Lucius.$B$BSubiendo por el camino aquí encontrarás el Molino de Alther. Tengo un cofre allí con algo importante dentro. ¡Ve a buscarlo! Tráeme ese token y recibirás tu Certificado de Ladrón.",
    ["O"] = "Abre el Cofre de Lucius, recupera la Ficha de Ladrón y entrégasela a Lucius en Villa del Lago.",
    ["T"] = "El Molino de Alther",
  },
  [2283] = {
    ["D"] = "¡Tenemos trabajo para ti, si tienes el valor de hacerlo!$B$BEn las profundidades de las Tierras Inhóspitas está el sitio de excavación de Uldaman. Hemos escuchado un rumor sobre algún idiota paladín que murió allí recientemente. La razón por la que incluso nos importa es que llevaba un collar muy valioso. Supuestamente tiene tres grandes gemas: un rubí, un zafiro y un topacio.$B$BEl trato: lo queremos, y estamos pagando PLATA DE PRIMERA por conseguirlo. Puede estar dañado, pero eso no importa, arreglaremos cualquier rasguño que tenga.",
    ["O"] = "Busca un collar valioso dentro del sitio de excavación de Uldaman y llévaselo a Dran Droffers en Orgrimmar. El collar puede estar dañado.",
    ["T"] = "Recuperación del Collar",
  },
  [2284] = {
    ["D"] = "Tan seguro como que Malton es un maniquí, esas gemas todavía están en Uldaman en algún lugar. Tendrás que volver allí y buscarlas.$B$BComo dije antes, si la información que tenemos es correcta, entonces hay un rubí, un zafiro y un topacio que van con el collar. Encuéntralos y colócalos en el collar.$B$BEn cuanto a dónde están las gemas, ¿quién sabe? Tal vez tengas suerte y encuentres el cadáver del Paladín. Si no lo han despojado por completo, tal vez las tenga consigo.",
    ["O"] = "Encuentra una pista sobre el paradero de las gemas en las profundidades de Uldaman.",
    ["T"] = "Recuperación del Collar, Toma 2",
  },
  [2298] = {
    ["D"] = "Así que allí estábamos, a punto de comenzar a planificar la fase III cuando un par de cabezas cuadradas de Magni irrumpen aquí hablando de algún tipo de robo y arrestando a algunos del Círculo Oculto.$B$B¡Bueno, nadie le pone hierros al viejo Barbanegra! La discusión se puso pesada y espesa y de alguna manera ¡tu nombre salió a relucir!$B$B¿Mi consejo? Toma el primer grifón que salga de Forjaz y ve a Ventormenta. Dirígete a Villa Oscura y una vez allí, habla con un viejo amigo mío llamado Renzik \"El Chafarote\".",
    ["O"] = "Viaja a Ventormenta y busca a Renzik \"El Chafarote\".",
    ["T"] = "Sacudida Real",
  },
  [2299] = {
    ["D"] = "¿Dónde has estado, $N? Hulfdan te ha estado buscando por todas partes. Pensamos que el rey podría haberte encarcelado.$B$B¡Ve a Forjaz lo antes posible y habla con Hulfdan!",
    ["O"] = "Viaja a Forjaz y habla con Hulfdan Blackbeard.",
    ["T"] = "¡A Hulfdan!",
  },
  [2300] = {
    ["D"] = "Renzik te estaba buscando, pececillo.$B$BSi fuera tú, no haría esperar al segundo al mando de Mathias.",
    ["O"] = "Viaja a Ventormenta y busca a Renzik \"El Chafarote\".",
    ["T"] = "SI:7",
  },
  [2318] = {
    ["D"] = "En un punto muerto de algún tipo, sabes que necesitarás que el libro sea traducido a un idioma que puedas entender. Ciertamente hay varias personas en el mundo que podrían hacerlo, la ubicación más cercana que podría tener a alguien que pueda hacerlo, sin embargo, es el puesto de Kargath en las Tierras Inhóspitas. Quizás ese sea un buen lugar para comenzar.",
    ["O"] = "Encuentra a alguien que pueda traducir el diario del paladín. El lugar más cercano donde podría haber alguien es Kargath, en las Tierras Inhóspitas.",
    ["T"] = "Traduciendo el Diario",
  },
  [2338] = {
    ["D"] = "Droffers e Hijos Recuperación no son lo suficientemente inteligentes como para restaurar la magia en el collar, pero yo sí. Todo lo que quiero hacer es estudiar el collar para poder aprender a hacerlos yo mismo. Para hacer eso, necesitaré tu collar. Déjamelo prestado, y a cambio del tiempo que tengo para estudiarlo, traduciré el diario para ti. Mientras buscas las gemas, debería tener suficiente tiempo para aprender lo que necesito.$B$BEs un trato simple, y nadie más lo sabrá. ¿Qué quieres hacer?",
    ["O"] = "Deja que Jarkal tome prestado el collar. A cambio, traducirá el diario para ti.",
    ["T"] = "Traduciendo el Diario",
  },
  [2339] = {
    ["D"] = "Aquí tienes el diario, traducido para que lo leas por tu cuenta. Lo esencial dice que los enanos escondieron el rubí, los troggs escondieron el topacio y un trogg llamado Grimlok tiene el zafiro.$B$BMira, vas a necesitar una fuente de poder para que el collar vuelva a funcionar. Dran Droffers no sabe esto. Para encontrar una, esto es lo que haría: ve tan profundo como puedas en Uldaman y mata al constructo más fuerte que haya. Sin duda, lo que sea tendrá lo que necesitas.$B$BCuando tengas todo recolectado, ¡regresa aquí!",
    ["O"] = "Recupera las tres gemas y una fuente de poder para el collar de Uldaman, y luego llévaselas a Jarkal Musgofusión en Kargath. Jarkal cree que una fuente de poder podría encontrarse en el constructo más fuerte presente en Uldaman.$B$BDel diario, sabes que...$B* El rubí ha sido escondido en un área de los Forjatiniebla bloqueada.$B* El topacio ha sido escondido en una urna en una de las áreas de los Troggs, cerca de algunos enanos de la Alianza.$B* El zafiro ha sido reclamado por Grimlok, el líder de los troggs.",
    ["T"] = "Encontrar las Gemas y la Fuente de Poder",
  },
  [2340] = {
    ["D"] = "Cualquier engaste de collar y gema funcionará, siempre que sean del mismo material. Sin embargo, esta fuente de poder es lo que devolverá la magia al collar... espero.$B$BCon gusto te proporcionaré las gemas necesarias para tu versión del collar, siempre que la fuente de poder funcione realmente.$B$BDirígete a Orgrimmar y cumple tu parte del trato con Droffers e Hijos Salvamento. Solo mantén la boca cerrada sobre nuestro trato, y cuando regreses, podría tener una maravillosa sorpresa para ti.",
    ["O"] = "Entrega el Collar y los Restos de Gema a Dran Droffers en Orgrimmar.",
    ["T"] = "Entregar las Gemas",
  },
  [2341] = {
    ["D"] = "Mientras Dran y Malton Droffers comienzan a discutir por su nuevo botín, la mitad del trato ya está completa. Ahora es necesario viajar a las Tierras Inhóspitas para terminar el trabajo, ya que Jarkal Mossmeld en Kargath podría tener una recompensa especial esperándote allí.",
    ["O"] = "Visita a Jarkal Mossmeld en Kargath para ver si tuvo éxito.",
    ["T"] = "Recuperación del Collar, Toma 3",
  },
  [2342] = {
    ["D"] = "¡Barrer los corrales de murciélagos con esta escoba no es digno de un Garrett! He ganado un poco de dinero con este maldito trabajo, y estoy dispuesto a gastarlo en ti para mejorar mi situación.$B$BEl arma preferida de la familia Garrett está encerrada en nuestro cofre en el Salón Común Sur de Uldaman, desde que los Garrett trabajamos allí como excavadores. Sin ella, estaré atrapado aquí para siempre, literalmente, barriendo después de estos infernales murciélagos.$B$B¡Recupera el tesoro de mi familia y tráemelo! ¡Ayúdame a restaurar mi herencia!",
    ["O"] = "Consigue el tesoro familiar de Patrick Garrett de su cofre familiar en la Sala Común Sur de Uldaman, y llévaselo a él en Entrañas.",
    ["T"] = "Tesoros Recuperados",
  },
  [2358] = {
    ["D"] = "Hace muchos años, los Cuernos de Nez\'ra fueron robados de la mansión de Ravenholdt. A través de continentes y océanos he buscado, solo para descubrir que estas insignificantes bestias los han tenido en su posesión todo este tiempo.$B$BMata a cada orco Roca Negra que cruces hasta que los Cuernos de Nez\'ra sean recuperados. Si recuperas los cuernos, encuéntrame en Villa del Lago.$B$BHaz esto por mí y tu nombre será conocido por Ravenholdt, $N.",
    ["O"] = "Devuelve los Cuernos de Nez\'ra a la Sombra de Arantir en Villa del Lago.",
    ["T"] = "Cuernos de Nez\'ra",
  },
  [2359] = {
    ["D"] = "He estado vigilando esta torre durante semanas, $N. Recientemente llegó un gran cargamento desde Bosque del Ocaso, y con él vinieron esos guardias.$B$B¿Qué son? Tu conjetura es tan buena como la mía. Una cosa te puedo decir—no son humanos.$B$BEn cualquier caso, el cargamento tenía un cofre—que es lo que supongo que están protegiendo esas abominaciones. El zombi con los pinchos tiene una llave que necesitaremos.$B$B¡Consigue esa llave, entra en la torre y descubre lo que hay en ese cofre! Lleva tus hallazgos de vuelta a Mathias.",
    ["O"] = "Roba la Llave de la Torre de los Defias, entra en la Torre de los Defias y descubre el contenido del Cofre de Bosque del Ocaso. Lleva cualquier información que encuentres de vuelta a Mathias Shaw en Ventormenta. Lee el Diario de Kearnen para obtener información sobre la torre.",
    ["T"] = "La Torre de Klaven",
  },
  [2360] = {
    ["D"] = "No podías llegar en mejor momento, $N. Los reconocimientos de SI:7 reportan actividad inusualmente intensa de los Defias en Páramos de Poniente.$B$BParece que los Defias han tomado una torre de vigilancia de la Alianza abandonada al sureste de la Colina del Centinela y la usan como base de operaciones para contrabandear bienes de la ruta de suministros de Crestagrana.$B$BVe a Páramos de Poniente y contacta a la Agente Kearnen para tu informe. La encontrarás escondida cerca de la torre.$B$B...y $N, si te descubren, SI:7 negará cualquier conocimiento de tu existencia.",
    ["O"] = "Viaja a Poniente y encuentra al Agente Kearnen para tu informe.",
    ["T"] = "Mathias y los Defias",
  },
  [2361] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Restaurando el Collar",
  },
  [2378] = {
    ["D"] = "Has servido bien a los Acechadores de la Muerte, $N, pero quizás es hora de que te aventures más allá. Aunque has mostrado aptitud en las formas del sigilo y el engaño, aún requieres mucho entrenamiento.$B$BLos Acechadores de la Muerte tienen aliados dentro de la Mano Destrozada. Busca a aquellos que nos ayudarían: encuentra a Shenthul en Orgrimmar.",
    ["O"] = "Viaja a Orgrimmar y habla con Shenthul en la Grieta de las Sombras.",
    ["T"] = "Encontrar a la Mano Destrozada",
  },
  [2379] = {
    ["D"] = "Tu determinación por sí sola no es suficiente para superar los obstáculos que tenemos por delante.$B$BHabla con Zando\'zan para comenzar tu entrenamiento como operativo de la Mano Destrozada.$B$BSolo cuando hayas dominado las prácticas del sigilo y el hurto podrás dar el siguiente paso hacia tu destino.",
    ["O"] = "Habla con Zando\'zan en la Grieta de la Sombra.",
    ["T"] = "Zando\'zan",
  },
  [2380] = {
    ["D"] = "Un maestro del sigilo y el engaño no eres, sin embargo, todo eso puede y cambiará. Obviamente has impresionado a los que están a cargo con tus habilidades modestas, ya que el gran Shenthul te ha pedido por nombre.$B$BToma el camino al norte de aquí hacia Orgrimmar y habla con Shenthul en la Grieta de la Sombra.",
    ["O"] = "Habla con Shenthul en la Grieta de la Sombra en Orgrimmar.",
    ["T"] = "¡A Orgrimmar!",
  },
  [2381] = {
    ["D"] = "No quiero asustarte, $N, pero los últimos cinco ladrones que envié a robar el tesoro de los Mares del Sur regresaron a mí en pedazos.$B$BAl sur a lo largo de la costa encontrarás a Navaja de Marea, el barco de los piratas de los Mares del Sur. En la bodega está el botín: el tesoro de los Mares del Sur. En el segundo nivel de ese barco está la guarida de los piratas, un lugar donde entrenan a todos sus ladrones.$B$BAborda ese barco, roba el tesoro y tráemelo. Usa la guarida de los piratas si necesitas practicar tu habilidad de robo, ¡pero ten cuidado!",
    ["O"] = "Lleva el Tesoro de los Mares del Sur a Wrenix el Desdichado en Ratchet. No olvides conseguir un E.C.A.C. y Herramientas de Ladrón del Aparato Gizmótico de Wrenix. Necesitarás ambos objetos para completar tu misión.$B$B¡Si te atacan loros inusualmente hostiles, usa tu E.C.A.C.!",
    ["T"] = "Saquear a los Saqueadores",
  },
  [2382] = {
    ["D"] = "¡Zando\'zan lo ha visto! Sí, ¡el día se acerca! La muerte de Thrall... el puñal en su espalda, el veneno filtrándose por todas sus venas... Zando\'zan ha dicho demasiado.$B$B$N! ¡Zando\'zan tiene una tarea para $N! $N debe viajar a Trinquete, al este de El Cruce, y hablar con Wrenix el Desdichado. ¡Ve!",
    ["O"] = "Viaja a Ratchet y habla con Wrenix el Desdichado.",
    ["T"] = "Wrenix de Trinquete",
  },
  [2383] = {
    ["D"] = "Ah, mientras estabas fuera, llegó un pergamino para ti, $N.$B$BLéelo cuando tengas tiempo. Si no me equivoco, vino del entrenador de guerreros Frang. Él tendría palabras para ti cuando estés listo. Lo encontrarás fuera de la Guarida, refugiándose en la sombra.",
    ["O"] = "Lee el Pergamino Simple y habla con Frang en el Valle de los Retos.",
    ["T"] = "Pergamino Simple",
  },
  [2398] = {
    ["D"] = "El sitio de excavación en Uldaman no siempre estuvo infestado. Nosotros, los enanos de Forjaz, solíamos controlar el área, pero... eso fue antes de que llegaran los troggs. ¡Vinieron tan rápido y en tal número que ni siquiera el poder enano pudo detenerlos!$B$BDe hecho, su ataque fue tan repentino que temo que algunos de nuestros compañeros puedan estar atrapados detrás de las líneas enemigas.$B$BMi amigo Baelog lideró un pequeño grupo de enanos profundamente dentro del sitio justo antes de que aparecieran los troggs. Si él y su compañía viven, entonces quiero que los encuentres.",
    ["O"] = "Encuentra a Baelog en Uldaman.",
    ["T"] = "Los Enanos Perdidos",
  },
  [2399] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Los Frondas Brotados",
  },
  [2418] = {
    ["D"] = "Hace un tiempo, tuve un incidente con los enanos Forjatiniebla. Me llevaron a la Fortaleza de Angor para estudiar dos tipos de piedras extrañas de Uldaman. Descubrí que las piedras eran fuentes de poder estables y sostenidas si se trataban correctamente. ¡Es increíble las cosas que se pueden encontrar en ese viejo sitio de excavación!$B$BDe todos modos, después de que les conté el secreto de las piedras, me echaron de la Fortaleza de Angor y comenzaron a cavar en busca de más en Uldaman.$B$B¡Quiero esas piedras para mí! Tráeme ambos tipos y te pagaré generosamente.",
    ["O"] = "Lleva 8 Piedras de Poder Dentrium y 8 Piedras de Poder An\'Alleum a Rigglefuzz en las Tierras Inhóspitas.",
    ["T"] = "Piedras de Poder",
  },
  [2438] = {
    ["D"] = "Una vez me dieron un atrapasueños esmeralda de Gaerolas Talvethen, el guardián de los druidas en la Cámara de Ban\'ethil. Este poderoso amuleto es capaz de absorber energía del Sueño Esmeralda, otorgando suerte a quienes lo llevan.$B$BDesafortunadamente, no he podido recuperarlo de mi cómoda en Aldea Brisa Estelar... Aunque Brisa Estelar fue una vez un lugar tranquilo, ahora ha sucumbido a la corrupción de los furbolgs que residen allí.$B$B¿Quizás estarías dispuesto a recuperar mi atrapasueños, $c?",
    ["O"] = "Lleva el Atrapasueños Esmeralda a Tallonkai Swiftroot en Dolanaar.",
    ["T"] = "El Atrapasueños Esmeralda",
  },
  [2439] = {
    ["D"] = "Este objeto es un hallazgo increíble. Los antiguos Titanes, o los Creadores como se refieren estos discos, son el vínculo con nuestra creación encantada, si la historia almacenada aquí es correcta. ¡Necesitaremos invertir tanto tiempo de investigación como sea posible en esto!$B$BEn cuanto a ti, heroico $c, lleva este vale a Dinita Mantopiedra en la bóveda. Te otorga lo que creo que es una recompensa adecuada para un hallazgo tan tremendo.$B$BRegresa directamente a mí cuando estés listo; ¡hay mucho más por hacer para la Liga de Expedicionarios!",
    ["O"] = "Lleva tu vale de recompensa a Dinita Mantopétreo en la Cámara de Forjaz.",
    ["T"] = "Los Discos de Platino",
  },
  [2440] = {
    ["D"] = "Me concentraré en estos discos, revelarán su verdadero funcionamiento en el momento adecuado.$B$BEn cuanto a ti, $N, ahora te recompensaré por tu diligencia al traer esto a mi atención. Lleva este vale a Bena Pezuña Invernal, la instructora de alquimia de Cima del Trueno. Ella te dará un objeto especial que creo que te resultará atractivo, y te proporcionará una selección de pociones de mi stock personal.$B$BRegresa pronto, pues siento que hay mucho más por hacer respecto a este hallazgo.",
    ["O"] = "Lleva el vale de recompensa a Bena Pezuña Invernal en Cima del Trueno.",
    ["T"] = "Los Discos de Platino",
  },
  [2458] = {
    ["D"] = "La Venture Co... ¿Quizás has oído hablar de ellos?$B$BDurante un año hemos trabajado para plantar un agente profundamente dentro de sus filas. A través de mucho trabajo duro y planificación cuidadosa, tuvimos éxito.$B$BRecientemente recibimos noticias de que Venture Co. está planeando algo a gran escala. Ve al norte del Fangal de la Ponzoña a la torre de Venture Co. y contacta a nuestro agente encubierto, el Capataz Fizzule.$B$BCuando encuentres la torre, usa esta pistola de bengalas, luego acércate a Fizzule y muéstrale el saludo de la Mano Destrozada.",
    ["O"] = "Viaja a la Torre de Venture Co., al norte del Pantano de Lodazal y contacta al operativo encubierto de la Mano Destrozada, Maestro de tareas Fizzule.$B$BCuando la torre esté a la vista, usa la Pistola de Bengala para señalar tu llegada a Maestro de tareas Fizzule. Dispara DOS bengalas, luego acércate a Maestro de tareas Fizzule y realiza el /salute de la Mano Destrozada.$B$BEstas acciones deben realizarse en el orden correcto, ya que Fizzule atacará y eliminará a cualquier persona que no reconozca.",
    ["T"] = "Cobertura Profunda",
  },
  [2459] = {
    ["D"] = "La esmeralda... ¡Ha desaparecido! Mi atrapasueños ha sido dañado.$B$BHay una banda de místicos Gnarlpine ubicada al norte de Arroyoceleste. He escuchado informes de que su líder, Ferocitas el Devorasueños, ha estado usando un collar que brilla en verde por la noche. Ahora, al ver mi atrapasueños, estoy seguro de que ha robado mi esmeralda... Nunca se daría cuenta de que su poder es inútil para él.$B$BEncuentra esta joya perdida, $N. Y, mientras estés allí, elimina a algunos de los místicos corruptos también.",
    ["O"] = "Tallonkai Raízveloz en Dolanaar quiere que mates a 7 Místicos Pino Torcido y encuentres la Joya Perdida.",
    ["T"] = "Ferocitas el Devorador de Sueños",
  },
  [2460] = {
    ["D"] = "Como pícaro de la Mano Destrozada, es imperativo que aprendas el Saludo Destrozado. Es la única forma de identificar verdaderamente a otro miembro de la Mano.$B$BObserva, aprende, repite.",
    ["O"] = "Realiza el Saludo Quebrado ante Shenthul.",
    ["T"] = "El Saludo Destrozado",
  },
  [2478] = {
    ["D"] = "Necesitarás robar la llave de la torre del Capataz Silixiz, $N. Una vez que tengas la llave en tu poder, guárdala bien, puede que la necesite más tarde.$B$BRecuerda, el veneno ha debilitado todo en esta torre. Usa tus habilidades para sembrar el caos y la destrucción entre sus habitantes. ¡Mátalos a todos, no muestres piedad!$B$BUna vez que todo dentro yazca en un montón ensangrentado, abre la caja fuerte de Gallywix y roba el alijo de la mezcla de Zanzil que contiene. Lleva tanto la cabeza de Gallywix como la mezcla a Shenthul en Orgrimmar.",
    ["O"] = "Roba la Llave de la Torre de Silixiz del Capataz Silixiz, luego mata a dos Zánganos de Venture Co. mutados, dos Patrulleros de Venture Co. y dos Vigías de Venture Co. Asesina al Gran Capataz Gallywix y lleva su cabeza, luego abre el candado de la caja fuerte de Gallywix y roba el Alijo de la Mezcla Alterada de Zanzil. Lleva el Alijo y la Cabeza de Gallywix a Shenthul en Orgrimmar para más instrucciones.",
    ["T"] = "Misión: Posible Pero No Probable",
  },
  [2479] = {
    ["D"] = "Toma una muestra de la mezcla y llévasela a Serge Hinott en Molino Tarren.$B$BSi alguien puede curarte, es Serge. Vive lo suficiente y Serge te mostrará las formas de creación y uso de venenos.",
    ["O"] = "Viaja a Molino Tarren en las Tierras Altas de Trabalomas y entrega la Muestra de la Mezcla de Zanzil a Serge Hinott.$B$B¡Para llegar a Molino Tarren, toma el Zepelín a Entrañas y sigue el camino hacia el sur a través de los Bosques de Argénteos y hacia Trabalomas. ¡Sigue las señales!",
    ["T"] = "La Asistencia de Hinott",
  },
  [2480] = {
    ["D"] = "Nunca había visto resultados tan maravillosos de un veneno. ¿Zanzil, dices? Nunca había oído hablar de él, sin embargo, debo felicitarlo por la creación de esta poción.$B$BEntonces, ¿debo curarte de esto y luego enseñarte cómo hacer uso del veneno?$B$BDame unos momentos para examinar la toxina, $N.",
    ["O"] = "Espera a que Serge Hinott complete la cura.",
    ["T"] = "La Asistencia de Hinott",
  },
  [2498] = {
    ["D"] = "Acabo de recibir noticias de que Denalan en el Lago Al\'Ameth ha descubierto lo que podría ser la causa de los tumores que plagan a los leñadores. Por favor, habla con él y dile que te envié yo.$B$BDate prisa, $N, necesita nuestra ayuda.",
    ["O"] = "Rellian Verdespira quiere que hables con Denalan en el Lago Al\'Ameth.",
    ["T"] = "Regreso con Denalan",
  },
  [2499] = {
    ["D"] = "En una cueva a lo largo de la orilla sur del lago, un leñador llamado Robleceño está esparciendo corrupción a todas las criaturas que hacen del Lago Al\'Ameth su hogar.$B$BNo me atreví a acercarme demasiado, pero incluso desde la distancia, es obvio: Robleceño está siendo envenenado por el tumor más grande que he visto... Lo llamaría gigantesco, incluso.$B$B$N, ya has hecho mucho para ayudar en mis esfuerzos, pero te pido una tarea más. Caza a Robleceño y recolecta el tumor, elimina esta fuente de corrupción de mi hogar.",
    ["O"] = "Denalan en el Lago Al\'Ameth quiere que recolectes el Tumor Gigantesco de Robleceja.",
    ["T"] = "Robleceja",
  },
  [2500] = {
    ["D"] = "$C, haré uso de tus servicios. Soy un alquimista de cierta reputación, y busco tu ayuda para adquirir algunos reactivos que se encuentran en las Tierras Inhóspitas.$B$BNecesito los siguientes artículos para reponer mis existencias: cinco mollejas de buitre, diez colmillos de coyote de risco y cinco fragmentos de elemental de roca. Obviamente, puedes obtenerlos de las bestias mismas, si no eres del tipo combativo, entonces encuentra amigos que lo sean.$B$BAdquiere estos artículos para mí, y obtendrás algo de moneda a cambio.$B$BBueno, ¿qué dices?",
    ["O"] = "Consigue los componentes que Ghak Sanador necesita de las Tierras Inhóspitas, luego regresa con él en Thelsamar.",
    ["T"] = "Recolección de Reagentes en las Tierras Inhóspitas",
  },
  [2501] = {
    ["D"] = "Has sido muy útil para mí, $N. Me gustaría recompensarte con la receta de la cerveza que te di antes, pero debo pedirte un favor bastante peligroso.$B$BEsta caja fuerte contiene tres vasijas de taumaturgia vacías, están imbuidas con un aura de sintonización que drenará la sangre de un dragón guardián abrasado. Usar una en la bestia la enfurecerá mucho, así que ten cuidado. Una vez que las hayas llenado todas, tráemelas.$B$BHaz esto, y la receta será tuya.",
    ["O"] = "Usa los recipientes de taumaturgia vacíos en los dragones guardianes chamuscados que se encuentran en las Tierras Inhóspitas. Una vez que los hayas llenado, llévaselos a Ghak Healtouch en Thelsamar.",
    ["T"] = "Recolección de Reagentes en las Tierras Inhóspitas II",
  },
  [2518] = {
    ["D"] = "Lady Sathrah fue una vez amada por Elune. Gracosa y pura, la araña tejía sus hilos plateados a través de la luz de la luna, atrapando la niebla de la tarde. El rocío plateado tenía fuertes poderes curativos y se guardaba aquí en el templo.$B$BPero recientemente, Sathrah ha caído en la locura. Sus futuras generaciones también están ahora amenazadas.$B$BEncuentra a Lady Sathrah, $N, y termina su sufrimiento. Habita en los límites norte de Teldrassil, cerca del Lago Primigenio. Recolecta sus hileras plateadas y tráelas de vuelta a mí.",
    ["O"] = "La Sacerdotisa A\'moora en el Templo de la Luna en Darnassus quiere que le lleves las Hilanderas Plateadas de Lady Sathrah.",
    ["T"] = "Lágrimas de la Luna",
  },
  [2519] = {
    ["D"] = "Una vez, una gran criatura del bosque necesita ayuda, $r.$B$BHay una tarea difícil que debe hacerse.$B$BPor favor, busca a la Sacerdotisa A\'moora, ella te explicará las cosas. Encuéntrala al sureste de aquí, dentro del Templo de la Luna.",
    ["O"] = "La Hermana Aquinne quiere que hables con la Sacerdotisa A\'moora en el Templo de la Luna.",
    ["T"] = "El Templo de la Luna",
  },
  [2520] = {
    ["D"] = "Ahora debes terminar la tarea para la que has sido designado. Toma las hileras y ofrece el sacrificio de Lady Sathrah a Elune, da descanso a la gran araña.$B$BDentro de este templo, encuentra la fuente central. Allí es donde puedes dejar el sacrificio. Las aguas sagradas purificarán y limpiarán la corrupción que enloqueció a Sathrah.$B$BCuando hayas completado esto, regresa conmigo.",
    ["O"] = "La Sacerdotisa A\'moora quiere que coloques las hilanderas plateadas de Lady Sathrah en la fuente dentro del templo, y luego regreses con ella.",
    ["T"] = "El Sacrificio de Sathrah",
  },
  [2521] = {
    ["D"] = "$R, ¿puede tu diminuto cerebro comprender la rareza de una esfera perfecta de draenethyst? Los draenei han pasado vidas enteras en busca de solo uno de estos cristales, solo para encontrar destinos horribles, su propósito incumplido.$B$BAsí que estás ante Kum\'isha, sabiendo lo que Kum\'isha ha pedido y aún deseas ayudar? Muy bien, $r... En cualquier momento durante tus viajes por estas tierras, si encuentras una esfera perfecta de draenethyst, devuélvemela y te recompensaré con riquezas más allá de tus sueños más salvajes.",
    ["O"] = "En tus viajes por las Tierras Devastadas, si alguna vez encuentras una Esfera Draenetista Impecable, llévasela a Kum\'isha el Coleccionista.$B$BTen cuidado, esta gema es uno de los cristales más raros de todo Azeroth. Cualquier criatura en estas tierras podría tener una Esfera Draenetista Impecable.$B$BSerás recompensado por cada Esfera Draenetista Impecable que hayas recolectado.",
    ["T"] = "Servir a Kum\'isha",
  },
  [2522] = {
    ["D"] = "Aunque la invocación del portal fue un fracaso, sigo esperanzado de que mi próximo intento sea exitoso.$B$BLa esperanza de los draenei es tan ferviente como tu codicia por baratijas, $r. Regresa a estas tierras malditas y recupera otra esfera de draenetista impecable.$B$BPor supuesto, la decisión de aceptar esta tarea es completamente tuya *sonrisa*.",
    ["O"] = "Encuentra y devuelve una Esfera Draenethyst Impecable a Kum\'isha el Coleccionista.",
    ["T"] = "Los Esfuerzos de Kum\'isha",
  },
  [2523] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Flor de Canción Corrupta",
  },
  [2541] = {
    ["D"] = "Los chamanes Gnarlpine que habitan este lugar han descubierto una forma de separar el espíritu de un druida dormido de su cuerpo físico. Los furbolgs han animado mi forma física y la están usando para atacar a cualquiera que intente explorar la Guarida de Ban\'ethil. Ahora estoy atrapado en el Sueño Esmeralda, impotente para detener esto.$B$BDebes ayudarme. Los chamanes Gnarlpine llevan un amuleto extraño que se usa para realizar este ritual, y me gustaría examinarlo. Por favor, $N, tráeme uno.",
    ["O"] = "Lleva un Amuleto Vudú de Chamán a Oben Garraguja en la Guarida Ban\'ethil.",
    ["T"] = "El Druida Durmiente",
  },
  [2561] = {
    ["D"] = "Después de examinar este amuleto, $N, ahora veo lo que debe hacerse. Por favor, tómalo y haz lo que te pido.$B$BDebes explorar las áreas más profundas de la Guarida de Ban\'ethil. Allí encontrarás mi cuerpo sin alma... Aunque lamento lo que estoy a punto de decirte, no veo otra manera de liberarme del control de los Tuercepinos.$B$BPara que yo pueda escapar de ellos, debes matar mi forma física. Una vez hecho eso, usa el amuleto vudú en mi cuerpo caído. Después de que hayas completado esta tarea, por favor regresa conmigo.",
    ["O"] = "Oben Garraguja quiere que mates su cuerpo sin alma y luego uses el Amuleto Vudú.",
    ["T"] = "Druida de la Garra",
  },
  [2581] = {
    ["D"] = "Es un hecho bien conocido que las hienas poseen una fuerza tremenda en sus mandíbulas, pero ¿la fuerza para romper tres pulgadas de acero de torio? Imposible, improbable, y sin embargo lo he visto demostrado con mis propios ojos.$B$BAventúrate en las Tierras Devastadas y tráeme tres mandíbulas de Snickerfang. Mientras buscas al Snickerfang, mantente atento a los jabalíes y escorpoks: el ensayo inicial también requiere dos pulmones de jabalí devastado y una pinza de escorpok para completarse.",
    ["O"] = "Lleva tres Quijadas de Snickerfang, dos Pulmones de Jabalí Devastado y una Pinza de Scorpok al Magus de Sangre Drazial.",
    ["T"] = "Mejillas de Snickerfang",
  },
  [2582] = {
    ["D"] = "Ah, $N, bienvenido de vuelta. Parece que disfrutaste el efecto del imbuido robusto y operativo, derivado del snickerfang. Ten cuidado, aunque el efecto inmediato es una explosión titánica de fuerza, los efectos a largo plazo en tu espíritu e inteligencia podrían ser peligrosos.$B$BDicho esto, ¿necesitas más? Si es así, tráeme otras tres quijadas de snickerfang, dos pulmones de jabalí destrozados y una pinza de escorpok.",
    ["O"] = "Lleva tres Quijadas de Snickerfang, dos Pulmones de Jabalí Devastado y una Pinza de Scorpok al Magus de Sangre Drazial.",
    ["T"] = "La Ira de las Edades",
  },
  [2583] = {
    ["D"] = "Dentro de estas tierras devastadas existen dos especies de jabalíes: el jabalí de crin de ceniza y el helboar.$B$BParece que la resistencia de estas bestias las hace casi inmunes al daño, ya que los otros animales las encuentran demasiado difíciles de matar y, en consecuencia, no vale la pena el esfuerzo.$B$BNecesitamos realizar más análisis, $N. Si me traes tres pulmones de jabalí devastado, dos pinzas de escorpok y un cerebro de basilisco, crearé un elixir divino a partir del residuo del ensayo. ¡Serás mi primer sujeto de prueba!",
    ["O"] = "Lleva tres Pulmones de Jabalí Devastado, dos Pinzas de Scorpok y un Cerebro de Basilisco al Magus de Sangre Drazial.",
    ["T"] = "La Vitalidad del Jabalí",
  },
  [2584] = {
    ["D"] = "Cuando administré por primera vez el elixir, $N, pude decir que una o dos aplicaciones no saciarían tu apetito. Incluso ahora, puedo ver el hambre en tus ojos. La poción tiene propiedades adictivas de las cuales, aparentemente, incluso los más fuertes de voluntad no pueden resistirse.$B$B¡Así sea! Si requieres más del cóctel de jugo de pulmón, aventúrate de nuevo en el páramo y encuentra para mí tres pulmones de jabalí devastado, dos pinzas de escórpido y un cerebro de basilisco.",
    ["O"] = "Lleva tres Pulmones de Jabalí Devastado, dos Pinzas de Scorpok y un Cerebro de Basilisco al Magus de Sangre Drazial.",
    ["T"] = "El Espíritu del Jabalí",
  },
  [2585] = {
    ["D"] = "¿Alguna vez los has visto en batalla, $N? Los escorpoks, quiero decir. ¿Has visto la gracia y agilidad con la que se mueven? ¿La decisión de sus golpes?$B$BSi pudiéramos aislar esa agilidad y encapsularla, seríamos ricos más allá de nuestra... quiero decir... sería una bendición para la comunidad científica.$B$BAyúdame, $N, y te permitiré probar la muestra.$B$BPara crear una muestra estable, necesitaré tres pinzas de escorpok, dos mollejas de buitre y un pulmón de jabalí destrozado.",
    ["O"] = "Lleva tres Pinzas de Scorpok, dos Mollejas de Buitre y un Pulmón de Jabalí Devastado al Magus de Sangre Drazial.",
    ["T"] = "El Golpe Decisivo",
  },
  [2586] = {
    ["D"] = "¡Estás vivo! Esta es una buena noticia, de hecho. Supongo que has vuelto para obtener más del ensayo de escórpido, ¿no?$B$BMuy bien, $N, regresa al páramo y no vuelvas hasta que hayas recolectado tres pinzas de escórpido, dos mollejas de buitre y un pulmón de jabalí destrozado.",
    ["O"] = "Lleva tres Pinzas de Scorpok, dos Mollejas de Buitre y un Pulmón de Jabalí Devastado al Magus de Sangre Drazial.",
    ["T"] = "La Sal del Escórpido",
  },
  [2601] = {
    ["D"] = "Estas tierras baldías están plagadas de basiliscos de piedra roja, $N. Estos basiliscos se han adaptado y florecido, más que cualquier otra vida silvestre en la región.$B$BHe observado desde lejos mientras cazan y realizan sus actividades diarias, las bestias exhiben un proceso de pensamiento más complejo que incluso algunos humanoides.$B$B¡Debo estudiar sus cerebros!$B$BObtén diez de sus cerebros para mí, $N. Sin embargo, el compuesto no estará completo sin dos mollejas de buitre para formar la base de la solución.",
    ["O"] = "Lleva diez Cerebros de Basilisco y dos Mollejas de Buitre a la Maga de sangre Lynnore.",
    ["T"] = "La Mordedura del Basilisco",
  },
  [2602] = {
    ["D"] = "¿Tu concentración enfocada ha disminuido tan pronto? ¡Se debe hacer más investigación!$B$BOh sí, el precio sigue siendo el mismo, $N. Aventúrate en las Tierras Devastadas y regresa con diez cerebros de los redstone y dos mollejas de buitre.",
    ["O"] = "Lleva diez Cerebros de Basilisco y dos Mollejas de Buitre a la Maga de sangre Lynnore.",
    ["T"] = "Mente Infalible",
  },
  [2603] = {
    ["D"] = "La muerte tiene alas en estas malditas tierras, $r. He sido testigo de cómo los asesinos negros y los picahuesos se alimentan de los cadáveres de muchos exploradores caídos, a veces alimentándose de los cadáveres más viles y plagados de enfermedades. Es un milagro, entonces, cómo sus sistemas internos superan la pestilencia que asola estas tierras.$B$B¡Asísteme en mis estudios, $N! ¡Necesito mollejas! También se requerirán dos mandíbulas de Snickerfang para la base de la poción.$B$BSerás recompensado doblemente por tus esfuerzos.",
    ["O"] = "Lleva diez Mollejas de Buitre y dos Mandíbulas de Dentefino a la Maga de sangre Lynnore.",
    ["T"] = "El Vigor del Buitre",
  },
  [2604] = {
    ["D"] = "Veo que el fuego interior se ha apagado. ¿Dirías que la euforia espiritual duró una hora?$B$BSi es más de la goma de molleja lo que deseas, entonces necesito más mollejas. Regresa a los páramos, $N. No regreses hasta que hayas recuperado diez mollejas y dos mandíbulas.",
    ["O"] = "Lleva diez Mollejas de Buitre y dos Mandíbulas de Dentefino a la Maga de sangre Lynnore.",
    ["T"] = "Dominación Espiritual",
  },
  [2605] = {
    ["D"] = "Tengo una idea, verás. He oído hablar de unas criaturas extrañas llamadas recolectores de rocío que viven al suroeste... y que son capaces de almacenar agua en glándulas especiales.$B$BCreo que si pudiera conseguir una de esas glándulas, podría crear una bebida que saciaría mi terrible sed. ¿Crees que podrías ayudarme, $N?$B$BBusca el jardín de cactus, $N, ahí es donde encontrarás a los recolectores de rocío.",
    ["O"] = "Recolecta una Glándula de Rocío Cargada y llévasela a Marin Noggenfogger en Gadgetzan.",
    ["T"] = "El Goblin Sediento",
  },
  [2606] = {
    ["D"] = "¡Puaj! ¡Esto sabe terrible! Bueno, ahí se fue esa idea...$B$BEspera, ¿qué es eso? Oh, ya sé, ya sé, crees que no debería rendirme con esto. Bueno, está bien entonces.$B$BUhhhh, ¡tengo una idea! ¡Brillante! Lleva este rocío a mi amiga Sprinkle, ella es una excelente chef. Dile lo que estamos haciendo aquí y que queremos que sea un poco más fácil de tragar.$B$BQuiero decir, si va a saber tan mal, al menos debería tener un poco de chispa... Y simplemente no lo siento...",
    ["O"] = "Marin Noggenfogger quiere que hables con Sprinkle en Gadgetzan.",
    ["T"] = "De Buen Gusto",
  },
  [2607] = {
    ["D"] = "Parece que has traído más información de la que incluso yo esperaba. Este es sin duda un giro extraño de los acontecimientos, $N.$B$B¿Una cura? Hmm... Ve al sótano y habla con Doc Mixilpixil, él podría ayudarte.",
    ["O"] = "Habla con Doc Mixilpixil en el sótano del Cuartel.",
    ["T"] = "El Toque de Zanzil",
  },
  [2608] = {
    ["D"] = "¡Oh, cielos, pareces la muerte misma! Corrección: he visto a la muerte, y tú pareces peor que ella. Tú, $g señor:señorita;, serías lo que la muerte tosió en su último examen físico.$B$B¡Ja! ¿Oíste eso, Noarm? ¡Apúntalo!$B$B*Tos* Mmm sí, para que pueda ayudarte, necesitaré extraer algo de sangre. No te preocupes, solo duele las primeras... cincuenta veces más o menos... /lay cuando estés listo para el tratamiento, $N.",
    ["O"] = "Usa /lay para ser examinado por Doc Mixilpixil. ¿Cómo puede encontrar la cura si no encuentra la causa?",
    ["T"] = "El Toque de Zanzil",
  },
  [2609] = {
    ["D"] = "Es unánime, $N. Tienes el \'itis\'.$B$BAfortunadamente, tengo una cura para el \'itis\'.$B$BAquí está lo que necesito: un ramo de flores silvestres simples, un vial emplomado, un tubo de bronce y, por último pero no menos importante, un carrete de hilo de seda verde claro. La vieja Emma dejó un carrete para mí en \'El Mejor Hilo\'. Puedes encontrar la tienda a lo largo de los canales.$B$BDate prisa ahora, $N, y trata de no tocar a nadie hasta que estés curado. Ah, y el Dr. Montgomery dice que necesitas una ducha, sus palabras, no las mías.",
    ["O"] = "Lleva a Doc Mixilpixil un ramo de Flores Silvestres Simples, un Frasco con Tapón de Plomo, un Tubo de Bronce y un Carrete de Hilo de Seda Verde Claro. La \"itis\" no se cura sola, joven $g muchacho:muchacha;.",
    ["T"] = "El Toque de Zanzil",
  },
  [2621] = {
    ["D"] = "Arrepentimiento. Es todo lo que tengo ahora. Una mala decisión que resultó en varios resultados horribles. Si tan solo hubiera esperado esas órdenes. ¡Quizás habrían detallado los horrores que nos esperaban en esas tierras malditas! ¿Quizás habrían sido para redirigirnos a otra misión? Al final, fue mi culpa. Todo fue mi culpa.$B$BAsí que aquí estoy, esperando órdenes. Debo saberlo. Debo tener esas órdenes.$B$BEl Comandante Ruag reside en Rocal. Encuéntralo y descubre qué pasó con las órdenes.",
    ["O"] = "Habla con el Comandante de Despachos Ruag en Rocal.",
    ["T"] = "El Deshonrado",
  },
  [2622] = {
    ["D"] = "¿Las órdenes? ¿Qué podrías querer con las órdenes?$B$B¿Qué estás insinuando, $N? ¡Por supuesto que transmití esas órdenes! Los soldados a los que envié las órdenes fueron emboscados y brutalmente golpeados por los múrlocs. Solo uno sobrevivió, si es que se le puede llamar sobrevivir. Apenas puede hablar, no puede caminar y debe ser alimentado a través de un tubo. Si quieres saber qué pasó con esas órdenes, habla con Bengor. Estoy seguro de que está por aquí en algún lugar... y, $N, ten cuidado con lo que metes tu nariz, podrían morderte.",
    ["O"] = "Habla con Bengor.",
    ["T"] = "Las Órdenes Perdidas",
  },
  [2623] = {
    ["D"] = "Las órdenes fueron dadas por el comandante del despacho. Obedecimos... obedecimos las órdenes. De camino a las Tierras Devastadas, nos emboscaron.$B$BMúrlocs... muchos múrlocs... uno llamado Hablador del Pantano. Tomó las órdenes de mis manos mientras una lanza me atravesaba el cráneo. Este... cueva....$B$B<Bengor se desmaya.>",
    ["O"] = "Recupera las Órdenes del Jefe de Guerra y devuélveselas al Héroe Caído de la Horda.",
    ["T"] = "El Hablador del Pantano",
  },
  [2641] = {
    ["D"] = "Cuando era niño, mi hermana solía hacer toda la cocina. ¡Era horrible! Apenas podía soportar algunas de sus mezclas—hasta que encontré lo que me gusta llamar mi ingrediente \"secreto\". ¡Agrégalo a cualquier cosa, y... WOW! Sabe genial.$B$B¿Qué es, preguntas? Es un hongo llamado Violet Tragan. Si quieres que te ayude a convertir este horrible brebaje en algo comestible, dirígete a las Tierras del Interior y busca un pequeño lago allí.$B$BOh, y recuerda, el Violet Tragan solo se puede encontrar bajo el agua.",
    ["O"] = "Sprinkle en Gadgetzan quiere que recolectes un Tragan Violeta y se lo devuelvas.",
    ["T"] = "El Ingrediente Secreto de Sprinkle",
  },
  [2661] = {
    ["D"] = "Aquí está el Polvo Violeta, $N. Llévaselo a Marin y dile que me debe un favor.",
    ["O"] = "Sprinkle quiere que lleves el Polvo Violeta a Marin Soplagarfio en Gadgetzan.",
    ["T"] = "Entrega para Marin",
  },
  [2662] = {
    ["D"] = "Ahora, para mezclar este polvo con el rocío.$B$BVuelve y habla conmigo en un minuto... ¡después de que haya perfeccionado el fabuloso Elixir Noggenfogger!",
    ["O"] = "Marin Noggenfogger quiere que hables con él nuevamente después de que cree su elixir.",
    ["T"] = "El Elixir Noggenfogger",
  },
  [2681] = {
    ["D"] = "Se necesitará más que tú solo para superar las fuerzas de Razelikh y sus subordinados.$B$BReúne un grupo adecuado de aventureros y libera a mis hombres de su tortura eterna. Debes destruir dieciocho piedras.$B$BLucha con fuerza y muere con honor, $N.",
    ["O"] = "Libera a nueve Sirvientes de Razelikh, tres Sirvientes de Sevine, tres Sirvientes de Allistarj y tres Sirvientes de Grol. Regresa con el Héroe Caído cuando tu tarea esté completa. Debes permanecer cerca de las piedras o el proceso fallará.",
    ["T"] = "Las Piedras que Nos Atan",
  },
  [2701] = {
    ["D"] = "Por favor, toma de esto una reliquia del pasado. Ya no tenemos uso para el objeto.$B$BQue te sirva bien en tu lucha contra la Legión.",
    ["O"] = "Abre el cofre y reclama tu recompensa.",
    ["T"] = "Héroes de Antaño",
  },
  [2702] = {
    ["D"] = "Me has honrado con tu nobleza y heroísmo, $N.$B$BTambién los has honrado a ellos...",
    ["O"] = "Habla con el Cabo Thund Pezuña Hendida.",
    ["T"] = "Héroes de Antaño",
  },
  [2721] = {
    ["D"] = "Mientras un capítulo de tu aventura se cierra, otro comienza, $N.$B$BUno de mis hombres aún está desaparecido, perdido en algún lugar de las Tierras Devastadas. Hablo, por supuesto, del Teniente Kirith.$B$BComo mencioné antes, ambos fuimos encarcelados y torturados por Allistarj, pero el pobre Kirith, su fuerza de voluntad flaqueó. Podía escuchar los experimentos desde mi jaula, los gritos de tortura resonaban por los pasillos de la cueva. Esa fue la última vez que vi o escuché de Kirith.",
    ["O"] = "Averigua qué fue del Teniente Kirith.",
    ["T"] = "Kirith",
  },
  [2741] = {
    ["D"] = "",
    ["O"] = " ",
    ["T"] = "El Super Huevo-O-Matic",
  },
  [2742] = {
    ["D"] = "¡Oye, $r!$B$B¡Rin\'ji perdió su hacha y fue capturado! Por favor, ayuda a Rin\'ji a escapar. Sé que a $r como tú no les gusta la gente de Rin\'ji, ¡pero Rin\'ji te lo suplica! ¡Debe salir de aquí!$B$BTengo algo para el $Gbuen $r si $Gél me ayuda a salir de aquí:ella me ayuda a salir de aquí;;$B$BRin\'ji esconde algo secreto en los Acantilados del Mirador.",
    ["O"] = "Escolta a Rin\'ji fuera del Refugio Quel\'Danil, luego encuentra su secreto oculto en los Acantilados del Mirador al este.",
    ["T"] = "¡Rin\'ji está Atrapado!",
  },
  [2743] = {
    ["D"] = "No andemos con rodeos, $r, mi tiempo aquí es limitado.$B$BDebes destruir el trío de poder que protege al señor demonio, Razelikh. Hacerlo no será una tarea tan simple como liberar un alma condenada.$B$B<Kirith sonríe.>$B$BVerás, cada uno de los subordinados de Razelikh lleva un tercio de un amuleto de la creación de Razelikh alrededor de su cuello. El mismo amuleto que necesitarás para invocar al archidemonio.$B$BVuelve con Trebor y dile lo que se requiere para invocar a Razelikh. Él podrá instruirte más.",
    ["O"] = "Regresa con el Héroe Caído de la Horda con tus hallazgos.",
    ["T"] = "La Cobertura de la Oscuridad",
  },
  [2744] = {
    ["D"] = "La tríada está protegida por Razelikh.$B$BA cambio de su servidumbre, Razelikh les otorgó a cada uno un poder casi ilimitado dentro de las Tierras Devastadas. Las piezas del amuleto están cada una sintonizadas con la fuerza vital de su dueño, otorgándoles inmortalidad. Mientras permanezcan dentro de las Tierras Devastadas, no pueden ser asesinados, sus vidas se extienden hasta el fin de los días.$B$BHay alguien que podría ayudarte más, $N. Viaja a Azshara y encuentra al cazador de demonios, Loramus Thalipedes. Busca en las islas.",
    ["O"] = "Habla con Loramus Thalipedes en Azshara.",
    ["T"] = "El Cazador de Demonios",
  },
  [2745] = {
    ["D"] = "Hmm, una historia interesante, y no una que deba tomarse a la ligera. Sí, puedo ayudarte, pero lo que necesitas hacer es impartir una forma de justicia que no sea... legal, si quieres ver esto hasta el final.$B$BLescovar y Marzon deben ser tratados, y rápidamente. ¿Crees que eres capaz de algo así?$B$BTú decides. Si es así, entonces date prisa y ve al castillo y busca a un viejo amigo mío, un gnomo llamado Tyrion. Ha estado vigilando a Lescovar durante un par de semanas. Él debería poder ayudarte a terminar esto.",
    ["O"] = "Habla con Tyrion en el Castillo de Ventormenta.",
    ["T"] = "Infiltración en el Castillo",
  },
  [2746] = {
    ["D"] = "Trias quiere que el horario se adelante un poco, ¿eh? Está bien, podemos hacer eso, pero no estoy listo. Y sabe esto, yo estoy haciendo la parte difícil, pero tú eres el que va a ensuciarse las manos, ¿entiendes? Bien.$B$BEstá bien, necesitaré que me consigas algunas cosas antes de que pueda poner mi plan en acción. Tengo el disfraz perfecto que hará que Spybot pase esos guardias.$B$BTráeme algo de seda y un par de manzanas. La seda depende de ti, pero la pequeña granja de Clara justo fuera de la ciudad tiene las manzanas del tamaño perfecto.",
    ["O"] = "Lleva 3 Paños de Seda y 2 Manzanas Frescas de Clara a Tyrion en Ventormenta.",
    ["T"] = "Objetos de Alguna Consecuencia",
  },
  [2747] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un Huevo Extraordinario",
  },
  [2748] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un Huevo Fino",
  },
  [2749] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un Huevo Ordinario",
  },
  [2750] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Un Huevo Malo",
  },
  [2751] = {
    ["D"] = "Veinte generaciones de Omosh han sostenido este martillo, $N. Cien mil toneladas de metal hemos moldeado. Trágicamente, todo termina en Orokk, el fracaso de los Omosh: Sin parientes e incapaz de producir descendencia.$B$B¡No puedo dejar que las recetas secretas de Omosh mueran conmigo! ¡Alguien debe continuar el legado, Omosh o no!$B$BPrueba a Orokk que eres capaz, $N. Tráele lo siguiente: Dos brazales de bronce con patrones, dos espadas grandes de bronce y dos garras afiladas. Tráeselas a Orokk y continuaremos.",
    ["O"] = "Lleva dos Brazales de Bronce con Diseño, dos Espadas Grandes de Bronce y dos Garras Afiladas a Orokk Omosh en Orgrimmar.",
    ["T"] = "Baluartes Barbáricos",
  },
  [2752] = {
    ["D"] = "Orokk ve el entusiasmo en tus ojos, $N. Como una esponja, absorbes la sabiduría del Omosh.$B$B¡Tres lecciones te enseñaré!$B$BVuelve al yunque, sumérgete en el calor de la forja, crea para Orokk cuatro hachas de batalla de bronce y cuatro martillos de guerra de bronce. Solo entonces aprenderás sobre las hombreras de hierro.",
    ["O"] = "Lleva cuatro Hachas de Batalla de Bronce y cuatro Martillos de Guerra de Bronce a Orokk Omosh en Orgrimmar.",
    ["T"] = "Sobre Hombreras de Hierro",
  },
  [2753] = {
    ["D"] = "Solo dos lecciones más Orokk enseñará, $N. Tus músculos duelen, lo sé, pero continúa por el Omosh!$B$BTráele a Orokk cuatro yelmos de hierro verde, cuatro brazales de hierro verde y dos leotardos de hierro verde; ¡por esto aprenderás y tus enemigos sufrirán!",
    ["O"] = "Lleva cuatro Yelmos de Hierro Verde, cuatro Brazales de Hierro Verde y dos Leotardos de Hierro Verde a Orokk Omosh en Orgrimmar.",
    ["T"] = "Pisoteado",
  },
  [2754] = {
    ["D"] = "La lección final que Orokk te enseña, $N. ¡Regresas al yunque sagrado y a la forja! Trae para Orokk dos mazas de hierro macizo, dos botas de bronce plateado y dos guanteletes de bronce plateado.$B$B¡Verás el miedo en sus ojos cuando te acerques!",
    ["O"] = "Lleva dos Mazas de Hierro Sólidas, dos Botas de Bronce Plateadas y dos Guanteletes de Bronce Plateados a Orokk Omosh en Orgrimmar.",
    ["T"] = "Cuernos de Frenesí",
  },
  [2755] = {
    ["D"] = "Orokk no está llorando, $N. Hay hollín en el ojo.$B$B<Orokk se seca una lágrima.>$B$BHay un último secreto de la familia Omosh que Orokk te enseñará. Orokk morirá feliz, gracias a $N.$B$B¡Aprende el baile de alegría de Omosh y luego aprenderás el puño de hierro!",
    ["O"] = "Observa y aprende el Baile de la Alegría de Omosh.",
    ["T"] = "Las Alegrías de Omosh",
  },
  [2756] = {
    ["D"] = "En los tiempos de las leyendas, Aturk aplastaba los cráneos de sus enemigos y molía los huesos hasta convertirlos en un polvo fino para usarlo en filamentos. Hace mucho que pasaron esos días, $r. El Jefe de Guerra exige paz entre los clanes. ¡Los viejos enemigos ahora solo son observados con ojos cansados!$B$BIncreíble, lo sé, pero tal es la voluntad de Thrall.$B$B¡Basta de lamentaciones! Estás aquí para aprender, ¡y aprenderás! Tráeme cuatro petos de acero y cuatro yelmos de acero y obtendrás una probada de la guerra.",
    ["O"] = "Lleva cuatro Petos de Acero y cuatro Yelmos de Acero a Aturk el Yunque en Orgrimmar.",
    ["T"] = "Las Viejas Costumbres",
  },
  [2757] = {
    ["D"] = "Escucha, chico, no vas a aprender nada colgando por aquí.$B$BConozco a un tipo que conoce a otro tipo. Él frecuenta la herrería en Bahía del Botín. Si le muestras esta insignia, te contará todo sobre el mitril.$B$B¿Qué dices? Deja estas cosas de niños a los bebés en pañales, $N, es hora de seguir adelante.$B$BSi estás interesado, su nombre es McGavan. Muéstrale esta chuchería y reconocerá tu compromiso.",
    ["O"] = "Habla con McGavan en Bahía del Botín.",
    ["T"] = "¡Bahía del Botín o Nada!",
  },
  [2758] = {
    ["D"] = "Cuando tenía más o menos tu edad, un enano llamado Galvan me tomó bajo su ala y me entrenó en las formas de la herrería de armaduras. Antes de irse a Bahía del Botín, me hizo prometer que entrenaría a todos aquellos con el deseo y la determinación de avanzar en el oficio de la herrería—así que aquí estoy—todos estos años después.$B$BAsí que ahora es el momento de que aprendas un poco. Hazme seis brazales de escamas doradas y aprenderás de dónde viene la herrería, $N.",
    ["O"] = "Lleva seis Brazales de Escamas Doradas a Hank el Martillo en Ventormenta.",
    ["T"] = "Los Orígenes de la Herreria",
  },
  [2759] = {
    ["D"] = "Aunque Galvan dejó Bahía del Botín hace muchos años, los miembros de la orden aún permanecen en la ciudad, esperando encontrar a alguien digno del tutelaje de Galvan.$B$BToma esta insignia y preséntasela a McGavan en Bahía del Botín.$B$BSi realmente llegas tan lejos, él sabrá que eres digno. ¡Tengo fe, $N!",
    ["O"] = "Habla con McGavan en Bahía del Botín.",
    ["T"] = "En Busca de Galvan",
  },
  [2760] = {
    ["D"] = "La mayoría de nuestros reclutas nunca llegan a Bahía del Botín, $N. El brillo de la insignia es suficiente para despertar la codicia incluso en los herreros más firmes. ¡Son unos tontos, ya que la insignia es una gota en el océano comparada con la riqueza de objetos que verás!$B$BEncuentra a Galvan el Antiguo y muéstrale esta insignia. Su residencia está muy al norte, entre Zul\'Gurub y el montículo ogro Mosh\'Ogg.",
    ["O"] = "Habla con Galvan el Ancestro en la Vega de Tuercespina.",
    ["T"] = "La Orden de Mithril",
  },
  [2761] = {
    ["D"] = "Muchos herreros creen que la minería y la herrería son trabajos separados e independientes, pero ¿puede haber herrería sin minería? ¡Ah, ja, es imposible! El herrero consumado también debe ser un minero consumado.$B$B¡Demuéstrale a Galvan que eres capaz, $N! Tráeme cuarenta barras de hierro y cuarenta barras de mitril. Por esto, Galvan te enseñará el camino de los pantalones de mitril ornamentados!",
    ["O"] = "Lleva cuarenta Barras de Mitril y cuarenta Barras de Hierro a Galvan el Antiguo en Vega de Tuercespina.",
    ["T"] = "Fundir, Fundir, Fundir",
  },
  [2762] = {
    ["D"] = "Se dice que el veraplata es más fuerte y duradero que el mitril. Galvan no está en desacuerdo con esta evaluación de los minerales, pero contrario a su nombre, el veraplata no es verdadero. ¡El mitril es un mineral mucho más noble! ¡JA! Sin embargo, para esta lección necesitarás encontrar al gran engañador plateado, $N.$B$BTráeme cuarenta barras de mitril y cinco barras de veraplata. A cambio, se te enseñará el secreto de los guantes de mitril ornamentados y, lo que es más importante, ¡aprenderás que el veraplata es un mentiroso!",
    ["O"] = "Lleva cuarenta Barras de Mitril y cinco Barras de Veraplata a Galvan el Antiguo en Vega de Tuercespina.",
    ["T"] = "El Gran Engañador de Plata",
  },
  [2763] = {
    ["D"] = "La cosecha de gemas preciosas es de suma importancia para nuestro oficio, $N. Pocas gemas son más preciosas que la citrina. Las propiedades mágicas que infundimos en nuestros bienes se amplifican enormemente con la presencia de citrina.$B$BPara esta lección, fortaleceremos cuarenta barras de mitril con cuatro citrinas. El resultado será una mejor comprensión de la gemología mágica y el conocimiento para crear hombreras de mitril ornamentadas.",
    ["O"] = "Lleva cuarenta Barras de Mitril y cuatro Citrinas a Galvan el Antiguo en Vega de Tuercespina.",
    ["T"] = "El Arte de la Imbue",
  },
  [2764] = {
    ["D"] = "Si estás listo para concluir tu entrenamiento con Galvan, debes viajar a Gadgetzan.$B$BAllí encontrarás a mi mejor alumno: Trenton Lighthammer. Él tiene mucho que enseñarte sobre el mithril ornamentado. De lo contrario, ¡quédate aquí y aprende!$B$BOh, Gadgetzan está en Tanaris, por supuesto.",
    ["O"] = "Habla con Trenton Martilloluz en Gadgetzan.",
    ["T"] = "El Mejor Alumno de Galvan",
  },
  [2765] = {
    ["D"] = "Cuando viniste a mí por primera vez, sabía que te convertirías en uno de mis mejores alumnos. ¡Sin dudarlo, renunciaste a un objeto de cierto poder para McGavan! Lo hiciste solo para poder conocerme. Tal fe ciega y devoción por el oficio debería ser recompensada.$B$BHas alcanzado la maestría en herrería, $N. ¡Recoge las recompensas!",
    ["O"] = "¡Reclama tu recompensa de Galvan el Antiguo!",
    ["T"] = "¡Herrero Experto!",
  },
  [2766] = {
    ["D"] = "Has descubierto una especie de dispositivo extraño en forma de huevo hecho de metal. Jugando con una de sus perillas, el huevo cobra vida y se abre en una especie de artilugio robótico gnomo. Una voz desde dentro del huevo robótico cobra vida.$B$B\"¡Mi nombre es Oglethorpe Obnoticus, y mi robot de localización se ha estrellado! Te recompensaré por su recuperación; ¡por favor lleva este faro al robot!\"$B$B\"¡Ahora he calculado las coordenadas del robot para ti, está en una cueva cerca del Valle de la Cicatriz Feral!\"",
    ["O"] = "Lleva el Distress Beacon al robot guía de Oglethorpe en una cueva cerca del Valle Feral Scar.",
    ["T"] = "Encontrar a OOX-22/FE!",
  },
  [2767] = {
    ["D"] = "La voz del gnomo resuena nuevamente desde el robot:$B$B\"Necesito mover a OOX-22/FE a un lugar abierto y seguro para que pueda comenzar un largo procedimiento de despegue. Tiene camuflaje incorporado, pero necesito tiempo de mi parte para volver a hacerlo operativo. Acompaña al robot desde su ubicación actual hasta, digamos, el muelle a lo largo de la Costa Olvidada. ¡Ese debería ser un lugar perfecto, y lo suficientemente largo, para poner las cosas en marcha!\"$B$B\"¡Acompaña al robot de manera segura hasta el muelle y luego ven a hablar conmigo en Bahía del Botín! Oglethorpe Obnoticus, fuera.\"",
    ["O"] = "Escolta a OOX-22/FE hasta el muelle a lo largo de la Costa Olvidada, luego informa a Oglethorpe Obnoticus en Bahía del Botín.",
    ["T"] = "Rescatar a OOX-22/FE!",
  },
  [2768] = {
    ["D"] = "¡El Sargento Bly me robó! Dijo que solo lo tomaría prestado, pero ¡se llevó mi querida vara divino-mática! ¡Sin esa vara, cómo sabré dónde cavar nuevos pozos de agua?$B$B¡Encuentra a Bly y tráeme mi vara! Escuché que llevó a su banda de aventureros de mala reputación a cazar tesoros en Zul\'Farrak, al oeste. Apuesto a que lo encontrarás allí.$B$BEsperemos que los trolls se hayan ocupado de él, porque si tienes que luchar contra él por la vara, te espera una pelea seria.",
    ["O"] = "Lleva la Vara Divino-mática al Jefe Ingeniero Bilgewhizzle en Gadgetzan.",
    ["T"] = "Vara Divino-Mática",
  },
  [2769] = {
    ["D"] = "¿Has conocido a los hermanos Brassbolts? Están en los Llanos Brillantes, en Kalimdor, corriendo su coche cohete contra los goblins. ¡Suelen ganar, lo cual no es sorpresa—el coche goblin explota tanto que casi nunca cruza la línea de meta!$B$BPero incluso entonces, los hermanos Brassbolts siempre están buscando nuevas formas de hacer que su coche vaya más rápido, y Wizzle me dijo que necesita un componente muy raro para su próximo experimento.$B$BProbablemente necesite ayuda para conseguir esa parte. Habla con él y averigua.",
    ["O"] = "Habla con Wizzle Pernolatón en el Desierto Brillante.",
    ["T"] = "Los Hermanos Pernolatón",
  },
  [2770] = {
    ["D"] = "En lo profundo de Zul\'Farrak, la ciudad troll de arena en Tanaris, hay un estanque sagrado. ¡Desde ese estanque los trolls invocan a una bestia enorme! ¡Gahz\'rilla! Es tan feroz que incluso sus escamas crepitan con energía. ¡Es esa energía la que quiero aprovechar para mi auto!$B$B¡Tráeme la escama electrificada de Gahz\'rilla!$B$B¡Pero la invocación de Gahz\'rilla es un secreto bien guardado de los trolls! Para enfrentarlo, primero debes arrebatarles el secreto.",
    ["O"] = "Lleva la Escala Electrificada de Gahz\'rilla a Wizzle Tornillos en Los Baldíos Brillantes.",
    ["T"] = "Gahz\'rilla",
  },
  [2771] = {
    ["D"] = "Parece que el maestro tenía esto planeado desde el principio. No me enseñaron a crear los artículos que tú aprendiste a crear, y tú no aprendiste los artículos que yo aprendí a crear.$B$BEl maestro es sabio, $N. ¡Nos enseñaremos el uno al otro!$B$BMuéstrame cómo hacer un yelmo de mitril y hombreras de mitril ornamentadas, y yo te mostraré cómo crear un yelmo de mitril ornamentado.",
    ["O"] = "Lleva dos Cofias de Mithril y un Hombrera Ornada de Mithril a Trenton Lighthammer.",
    ["T"] = "Una Buena Cabeza sobre Tus Hombros",
  },
  [2772] = {
    ["D"] = "En el Campeonato de Herreros del Valle del año pasado, todos los que observaron vieron cómo mi pierna fue barrida por un cobarde perro de la Fundición de la Empresa. Lo que no sabían, sin embargo, era la razón por la que pude continuar y finalmente ganar el torneo: ¡Eso es correcto, botas de mitril ornamentadas!$B$BSin embargo, este año necesitaré aprender a fabricar pantalones de mitril ornamentados para el torneo, ya que una trituradora de la Empresa es un participante. ¡Enséñame el camino de los pantalones y aprende el camino de la bota!",
    ["O"] = "Lleva dos Botas de Mithril Pesadas y un Pantalón Ornado de Mithril a Trenton Lighthammer.",
    ["T"] = "El Mundo a Tus Pies",
  },
  [2773] = {
    ["D"] = "Por estos lugares me conocen como el Chico de Mithril. Suministro mithril ornamentado a aventureros de todo el mundo.$B$B<Trenton susurra.>$B$BLo que no saben es que no conozco la técnica para fabricar guantes de mithril ornamentados. ¡Shhhh! Este será nuestro pequeño secreto.$B$BEnséñame la técnica para crear guantes de mithril ornamentados, $N, y a cambio, te mostraré el resultado de la culminación de mi entrenamiento bajo Galvan: El Peto de Mithril Ornamentado.",
    ["O"] = "Lleva dos Petos de Mithril Pesados y un Guante Ornado de Mithril a Trenton Lighthammer.",
    ["T"] = "El Chico de Mithril",
  },
  [2781] = {
    ["D"] = "¡SE BUSCA: Caliph Scorpidsting!$B$BLa Compañía de Agua de Gadgetzan de Gadgetzan, Tanaris pagará una alta recompensa por la cabeza de Caliph Scorpidsting, líder de los forajidos de los Merodeadores del Desierto. Sus crímenes incluyen:$B$B1. ¡Asesinar empleados de la Compañía de Agua de Gadgetzan!$B2. ¡Robar recursos de la Compañía de Agua de Gadgetzan!$B3. ¡Tomar propiedad de la Compañía de Agua de Gadgetzan!$B$B¡Trae prueba de la muerte de Caliph Scorpidsting al Ingeniero Jefe Bilgewhizzle para recibir una recompensa inmediata!",
    ["O"] = "Lleva la cabeza del Califa Colmillo Escorpión al Jefe Ingeniero Bilgewhizzle en Gadgetzan.",
    ["T"] = "SE BUSCA: Califa Picavena",
  },
  [2782] = {
    ["D"] = "La tableta está cubierta con marcas de troll Secacorteza. El secreto de Rin\'ji es realmente un misterio...$B$BEs posible que tengas algo bastante valioso en tus manos aquí. Por otro lado, también es posible todo lo contrario. Un troll Secacorteza en apuros probablemente haría cualquier cosa para salir de problemas...",
    ["O"] = "Oran Serpescrita, una conocida erudita en asuntos relacionados con los trolls Secacorteza, puede ser capaz de descifrar la tablilla. Se sabe que reside en el Barrio de la Magia de Entrañas.",
    ["T"] = "El Secreto de Rin\'ji",
  },
  [2783] = {
    ["D"] = "Luchamos contra el enemigo equivocado, $N.$B$BEl verdadero poder en estas tierras continúa creciendo en fuerza y número mientras discutimos por tierra quemada con los orcos.$B$B¿Continuaremos luchando en una guerra en estas tierras contra la Horda, solo para ser arrasados un día por los ejércitos de la Legión Ardiente? Es inútil.$B$BDeja de lado tus diferencias, $N, habla con el héroe caído de la Horda. Él conoce la verdad detrás de las Tierras Devastadas.",
    ["O"] = "Habla con el Héroe Caído de la Horda en la frontera de las Tierras Devastadas y el pantano.",
    ["T"] = "Pequeñas Disputas",
  },
  [2784] = {
    ["D"] = "Escucha mi historia, $r.",
    ["O"] = "Escucha al Héroe Caído de la Horda contar su historia.",
    ["T"] = "Caída en Desgracia",
  },
  [2801] = {
    ["D"] = "Antes de que podamos continuar, debes escuchar mi historia.",
    ["O"] = "Escucha al Héroe Caído de la Horda contar su historia.",
    ["T"] = "Un Cuento de Tristeza",
  },
  [2821] = {
    ["D"] = "Mi filosofía sobre peletería es que el trabajo debe ser de la mejor calidad posible—sin compromisos. ¡Cualquiera que diga que la calidad no importa está tratando de venderte propiedades frente al mar en Dun Morogh!$B$BEn mi última búsqueda de materiales de alta calidad, me he topado con pieles gruesas de yeti. Los Yetis son nativos de Feralas, con muchos encontrados en el Valle Cicatriz Feral. Quiero algunos, y te daré a elegir uno de mis productos de cuero por un paquete de diez. Si eso te parece justo, entonces estamos en negocios.",
    ["O"] = "Lleva 10 Pieles de Yeti Gruesas a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "La Marca de Calidad",
  },
  [2822] = {
    ["D"] = "Me enorgullezco de mi trabajo en cuero, y creo que se nota, la calidad nunca debe comprometerse. ¡Cualquiera que diga que la calidad no importa también te haría creer que la tierra no guarda secretos!$B$BEn mi última búsqueda de materiales de alta calidad, he descubierto pieles gruesas de yeti. Los yetis son nativos de Feralas, a menudo se encuentran al oeste de aquí, en el Valle de la Cicatriz Feral. Te daré a elegir uno de mis productos de cuero por una pila de diez. Si eso te parece bien, ¡entonces estamos listos!",
    ["O"] = "Lleva 10 Pieles de Yeti Gruesas a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "La Marca de Calidad",
  },
  [2841] = {
    ["D"] = "La tecnología gnoma: hace dos años, su uso más común era como un chiste. Los tiempos ciertamente han cambiado. Ahora tienen un nuevo equipo construido que es mejor que todos nuestros modelos de trituradoras combinados. ¡Necesito los planos, $N!$B$BDesafortunadamente, los planos están guardados en una caja fuerte cerca de Mekgineer Thermaplugg. Para empeorar las cosas, la combinación de la caja fuerte solo la conoce Mekgineer Thermaplugg.$B$BHaz lo que tengas que hacer para conseguir esa combinación, $N. Luego abre esa caja fuerte y tráeme esos planos.",
    ["O"] = "Recupera los Planos de la Plataforma y la Combinación de la Caja Fuerte de Thermaplugg de Gnomeregan y llévaselos a Nogg en Orgrimmar.",
    ["T"] = "Guerras de Plataformas",
  },
  [2842] = {
    ["D"] = "Tan pronto como recibimos noticias de Kernobee sobre el nuevo aparato gnómico, Scooty y yo comenzamos a trabajar en el Transpoliportador 6000: La forma más rápida y confiable de llegar a Gnomeregan.$B$BFinalmente está listo para que los pasajeros lo usen, $N. Si quieres probarlo, ve a Bahía del Botín y habla con Scooty en el puesto de los jinetes del viento. Para llegar a Bahía del Botín, toma el barco desde Ratchet.$B$B¡Por supuesto, también podrías intentar llegar a Gnomeregan a través de las montañas infestadas de enanos de Dun Morogh!",
    ["O"] = "Habla con Scooty en Bahía del Botín.",
    ["T"] = "Ingeniero Jefe Scooty",
  },
  [2843] = {
    ["D"] = "Mientras tengas el transpondedor en tu posesión, todo lo que necesitas hacer es pararte en la plataforma y serás teletransportado a Gnomeregan.$B$BPuedes experimentar algunos efectos secundarios inusuales, pero ninguno de ellos debería ser permanente... ya.$B$BCasi olvido la parte más importante: cómo regresar. Cuando estés listo para volver, ¡solo pisa la plataforma de transpoliportación en Gnomeregan!$B$BAhora a calibrar el transpondedor a tu tamaño y peso.",
    ["O"] = "Espera a que Scooty calibre el Transpondedor Goblin.",
    ["T"] = "¡Gnomer-gooooone!",
  },
  [2844] = {
    ["D"] = "No soy bueno, $N. Se suponía que debía cuidar a mi pequeña amiga, Shay, y de alguna manera, se me escapó. Realmente no estoy seguro de lo que sucedió, pero la última vez que la vi, estábamos caminando por las ruinas justo al norte de aquí.$B$BTodo iba bien, pero luego me di la vuelta y... ella había desaparecido.$B$BAhora no sé muy bien qué hacer aquí. ¿Tienes alguna idea, $N? Estoy muy preocupado.$B$BOye, ¿qué tal si vas a buscar a Shay? Tus piernas pueden viajar mucho más rápido que las mías.",
    ["O"] = "Localiza a la niña elfa de la noche perdida.",
    ["T"] = "El Guardián Gigante",
  },
  [2845] = {
    ["D"] = "¿Crees que podrías ayudarme a encontrar el camino de regreso a mi amigo, Rockbiter? Se suponía que él me estaba vigilando... Pero tengo la tendencia a alejarme de vez en cuando. Hay tantas cosas que ver, y me distraigo fácilmente.$B$BSin embargo, hay una cosa que siempre funcionará para llamar mi atención.$B$BDentro de este cofre, encontrarás una campana que es muy preciada para mí. Simplemente me encanta el sonido de ella. Te doy mi palabra, si me alejo, todo lo que tienes que hacer es hacerla sonar y volveré corriendo.",
    ["O"] = "Recoge la Campana de Shay del cofre.$B$BEscolta a Shay Corredor de Hojas hasta el campamento de Roedoroca.",
    ["T"] = "Shay Errante",
  },
  [2846] = {
    ["D"] = "Hace mucho tiempo poseía una hermosa pieza de joyería, la Tiara de las Profundidades. Y no solo era bonita, sino que contenía un gran poder para aquellos con el conocimiento para usarla.$B$BAsí que cuando la noticia de la tiara llegó a la Hidromántica Velratha, ella tuvo que tenerla. Envió agentes a mi hogar y la robaron mientras yo estaba fuera. ¡Los ladrones!$B$B¡Quiero mi tiara de vuelta! Ve a Zul\'Farrak, la ciudad troll en Tanaris, encuentra a Velratha y arranca la tiara de ella. Devuélvemela y ganarás mi favor.",
    ["O"] = "Lleva la Tiara de las Profundidades a Tabetha en los Pantanos de Dustwallow.",
    ["T"] = "La Tiara de las Profundidades",
  },
  [2847] = {
    ["D"] = "$n, ¡te estás volviendo bastante hábil como peletero! Conozco algunos patrones que podrían interesarte... ¡armadura de cuero salvaje! El cuero salvaje aprovecha las propiedades potentes y caóticas de las enredaderas salvajes, que se encuentran en áreas de vegetación exuberante y en algunas de las criaturas allí. La armadura hecha con él es insuperable para personas de tu habilidad, garantizado.$B$BTe enseñaré esto, pero trabajarás para mí por un tiempo. El primer costo es simplemente diez piezas de cuero grueso. Después de esto, hablaremos de detalles.",
    ["O"] = "Lleva 10 Cuero Grueso a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Armadura de Cuero Salvaje",
  },
  [2848] = {
    ["D"] = "Los hombros de Cuero Salvaje soportan muy bien el marco del usuario, ofreciendo una protección superior de la parte superior de los brazos y los hombros. Como con toda la armadura de Cuero Salvaje, una mejora aleatoria pero muy potente se imbuye en el artículo a medida que se cose la vid salvaje.$B$BPara este patrón, necesitaré seis kits de armadura gruesa y una vid salvaje. No debería ser un pedido demasiado alto para ti.",
    ["O"] = "Lleva 6 Kits de Armadura Gruesa y una Vid Salvaje a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Hombreras de Cuero Salvaje",
  },
  [2849] = {
    ["D"] = "Los chalecos de Cuero Salvaje son la base de cualquier conjunto de armadura de Cuero Salvaje. El marco del chaleco se mezcla con la vid salvaje para producir un producto que ofrece la máxima protección del torso mientras permite que la magia potente fluya a través de la prenda.$B$BPara este patrón, necesitaré dos petos de Escama de Tortuga, dos pares de guantes de Escama de Tortuga y una vid salvaje. Es un pedido alto, pero deberías tener poca dificultad en cumplirlo para mí.",
    ["O"] = "Lleva 2 Petos de Escama de Tortuga, 2 Guantes de Escama de Tortuga y una Vid Salvaje a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Jubón de Cuero Salvaje",
  },
  [2850] = {
    ["D"] = "Un casco de cuero salvaje ofrece al usuario una protección superior en las áreas vitales de la cabeza sin sacrificar la comodidad. Como con toda la armadura de cuero salvaje, la vid salvaje integrada proporciona una mejora poderosa pero aleatoria al marco del yelmo. ¡Los usuarios de cuero definitivamente querrán esta pieza!$B$BPara este patrón, necesitaré dos túnicas Noctávida, dos cintas Noctávida y una vid salvaje. ¡Completa este pedido y el patrón será tuyo!",
    ["O"] = "Lleva 2 Túnicas Nocturnas, 2 Cintas Nocturnas y una Vid Salvaje a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Yelmo de Cuero Salvaje",
  },
  [2851] = {
    ["D"] = "Has aprendido bastante ahora, $n, sin embargo, hay más por aprender. Lo siguiente son las botas de cuero salvaje, la piedra angular de cualquier conjunto.$B$BEstas botas están diseñadas para acunar el pie con comodidad mientras lo protegen de los peligros de la aventura. Las suelas suaves son una bendición para aquellos que buscan moverse en silencio.$B$BPara este patrón, necesito que completes un pedido de dos pantalones Nochefuga y dos botas Nochefuga. También necesitaré dos vides salvajes... no solo una. Cuando tengas todo esto, avísame.",
    ["O"] = "Lleva 2 Pantalones Nocturnos, 2 Botas Nocturnas y 2 Vid Salvaje a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Botas de Cuero Salvaje",
  },
  [2852] = {
    ["D"] = "Con lo básico aprendido, estás listo para dominar cosas más complejas, $n. Las perneras de cuero salvaje son algunas de las prendas de cuero más resistentes y poderosas que existen. La cintura, los muslos, las pantorrillas y las partes vitales están todas protegidas de manera tremenda. Junto con la mejora que proporciona la vid salvaje, los aventureros que las posean te agradecerán cada vez que te vean.$B$BPara este patrón, necesito dos yelmos de escamas de tortuga, dos brazales de escamas de tortuga y dos vides salvajes.",
    ["O"] = "Lleva 2 Yelmos de Escama de Tortuga, 2 Brazales de Escama de Tortuga y 2 Vides Salvajes a Pratt McGrubben en el Bastión Plumaluna.",
    ["T"] = "Leotardas de Cuero Salvaje",
  },
  [2853] = {
    ["D"] = "Bueno $n, eres la definición de diligencia.  Solo queda un patrón para la armadura de cuero salvaje, has dominado los demás.  No te haré trabajar innecesariamente por el patrón.  Lo has ganado directamente, y más... pero no puedo dártelo yo mismo.  Mi maestro, el que me introdujo en el oficio, es quien debe recompensarte.$B$BLleva esta carta a Telonis, él enseña peletería en Darnassus.  Muéstrale la carta y deja que te reconozca como un igual, como yo lo hago.",
    ["O"] = "Dale la Carta de Pratt a Telonis, el maestro peletero de Darnassus.",
    ["T"] = "Maestro del Cuero Salvaje",
  },
  [2854] = {
    ["D"] = "$n, tu habilidad como peletero te precede. Poseo conocimientos de peletería que serías sabio en adquirir... ¡armadura de cuero salvaje! El cuero salvaje aprovecha las propiedades potentes y caóticas de las enredaderas salvajes, que se encuentran en áreas de vegetación exuberante y en algunas de las criaturas allí. La armadura hecha con él es insuperable para un artesano de tu habilidad, garantizado.$B$BTe enseñaré, pero trabajarás para mí por un tiempo. El primer costo es simplemente diez piezas de cuero grueso. Después de esto, hablaremos de detalles.",
    ["O"] = "Lleva 10 Cuero Grueso a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "Armadura de Cuero Salvaje",
  },
  [2855] = {
    ["D"] = "Los hombros de Cuero Salvaje soportan muy bien el marco del usuario, ofreciendo una protección superior de la parte superior de los brazos y los hombros. Como con toda la armadura de Cuero Salvaje, una mejora aleatoria pero muy potente se imbuye en el artículo a medida que se cose la vid salvaje.$B$BPara este patrón, necesitaré seis kits de armadura gruesa y una vid salvaje. No debería ser un pedido demasiado alto para ti.",
    ["O"] = "Lleva 6 Kits de Armadura Gruesa y una Vid Salvaje a Jangdor Zancoveloz en Campamento Mojache.",
    ["T"] = "Hombreras de Cuero Salvaje",
  },
  [2856] = {
    ["D"] = "Los chalecos de Cuero Salvaje son la base de cualquier conjunto de armadura de Cuero Salvaje. El marco del chaleco y el tejido de vid salvaje en su interior producen un producto que ofrece la máxima protección del torso mientras permite que la magia potente fluya a través de la prenda.$B$BPara este patrón, necesitaré dos petos de Escama de Tortuga, dos pares de guantes de Escama de Tortuga y una vid salvaje. Esta no es una lección fácil, pero ciertamente tienes la habilidad para completarla.",
    ["O"] = "Lleva 2 Petos de Escama de Tortuga, 2 Guantes de Escama de Tortuga y una Vid Salvaje a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "Jubón de Cuero Salvaje",
  },
  [2857] = {
    ["D"] = "Un casco de cuero salvaje ofrece al usuario una protección superior en las áreas vitales de la cabeza sin sacrificar la comodidad. Como con toda la armadura de cuero salvaje, la vid salvaje integrada proporciona una mejora poderosa pero aleatoria al marco del yelmo. ¡Los usuarios de cuero definitivamente querrán esta pieza!$B$BPara este patrón, necesitaré dos túnicas Noctávida, dos cintas Noctávida y una vid salvaje. ¡Completa este pedido y el patrón será tuyo!",
    ["O"] = "Lleva 2 Túnicas Nocturnas, 2 Cintas Nocturnas y una Vid Salvaje a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "Yelmo de Cuero Salvaje",
  },
  [2858] = {
    ["D"] = "Tu conocimiento sobre la armadura de cuero salvaje crece, $n, sin embargo, aún hay más por aprender.$B$BLas botas de cuero salvaje son la piedra angular de cualquier conjunto. Estas botas están diseñadas para acunar el pie con comodidad mientras lo protegen de los peligros de la aventura. Las suelas suaves son una bendición para aquellos que buscan moverse en silencio.$B$BPara este patrón, necesito dos pantalones Nochefuga y dos botas Nochefuga. También necesitaré dos vides salvajes... no solo una. Cuando tengas estos artículos, procederemos.",
    ["O"] = "Lleva 2 Pantalones Nocturnos, 2 Botas Nocturnas y 2 Vid Salvaje a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "Botas de Cuero Salvaje",
  },
  [2859] = {
    ["D"] = "Estás listo para dominar lecciones más complejas, $n. Las perneras de cuero salvaje son algunas de las prendas de cuero más resistentes y poderosas que existen. La cintura, los muslos, las pantorrillas y las partes vitales están todas protegidas de manera tremenda. Junto con la mejora que proporciona la vid salvaje, los aventureros que las posean te agradecerán cada vez que te vean.$B$BPara que obtengas este patrón, necesito dos yelmos de escamas de tortuga, dos brazales de escamas de tortuga y dos vides salvajes.",
    ["O"] = "Lleva 2 Yelmos de Escama de Tortuga, 2 Brazales de Escama de Tortuga y 2 Vides Salvajes a Jangdor Swiftstrider en Campamento Mojache.",
    ["T"] = "Leotardas de Cuero Salvaje",
  },
  [2860] = {
    ["D"] = "$n, has aprendido todo lo que puedo enseñarte. Sin embargo, queda un patrón más para la armadura de cuero salvaje. No te haré trabajar innecesariamente por esto, te lo has ganado de sobra. Este patrón, sin embargo, es uno que no puedo compartir contigo libremente.$B$BMi maestro, quien me introdujo en el oficio, es quien debe recompensarte. Lleva esta nota a Una, ella enseña peletería en Cima del Trueno. Muéstrale la carta y deja que te reconozca como un igual, como yo lo hago.",
    ["O"] = "Dale la Carta de Jangdor a Una, la maestra peletera de Cima del Trueno.",
    ["T"] = "Maestro del Cuero Salvaje",
  },
  [2861] = {
    ["D"] = "Se rumorea que Tabetha, una colega maga que vive en las profundidades de los Pantanos de Veleno, tiene un asunto pendiente con cierto troll de arena en Zul\'Farrak. Si hablas con Tabetha, ella te puede contar más.$B$BY lleva algunos amigos contigo, $N. Si Tabetha te manda a Zul\'Farrak, no querrás ir solo.$B$BEncontrarás la cabaña de Tabetha al oeste de Theramore y justo al norte de las Ruinas de los Quebrantarrocas.",
    ["O"] = "Habla con Tabetha en los Pantanos de Veleno.",
    ["T"] = "La Tarea de Tabetha",
  },
  [2862] = {
    ["D"] = "Los gnolls... son más que una molestia. Son una amenaza constante para la existencia misma de este campamento. Es irrelevante si son indígenas de Feralas o no, si no fortalecemos nuestra resolución contra ellos, nos encontraremos expulsados de toda la región.$B$BLos ataques de los gnolls contra el campamento se han vuelto más frenéticos últimamente. Necesitamos tu ayuda para reducir su número, tráeme diez de sus crines y te recompensaré generosamente por tu esfuerzo.$B$B¡Ve!",
    ["O"] = "Lleva 10 Crines de Gnoll Zarpa Lenhosa a Hadoken Swiftstrider en Campamento Mojache.",
    ["T"] = "Guerra contra los Zarpaleña",
  },
  [2863] = {
    ["D"] = "El plan es derribar a varios líderes de los gnolls, llamados Alfas, en rápida sucesión. Si podemos ejecutar este tipo de devastación rápida y precisa contra ellos, causaría el caos en sus filas. ¡Quizás también les enviaría un mensaje a través de sus cabezas llenas de pulgas de que podemos y los destruiremos!$B$BQuiero que seas el mensajero de mi voluntad, elimina a cinco de sus Alfas dentro de una hora para que esto funcione, $N, ¡o seguiremos en este punto muerto!",
    ["O"] = "Mata 5 Alfa Zarpa de Madera y regresa con Hadoken Zancoveloz en una hora.",
    ["T"] = "Golpe Alfa",
  },
  [2864] = {
    ["D"] = "Mi primo Tran\'rek está en un aprieto. Me prometió una carga de caparazones de escarabajos hace semanas y no ha cumplido. Es mi primo, así que odiaría tener que enviar a alguien a Gadgetzan para romperle algo.$B$B¿Puedes hablar con Tran\'rek por mí? Hazle saber que necesita reunir esa entrega antes de que haga algo imprudente.$B$BGadgetzan está en el norte de Tanaris, en Kalimdor.",
    ["O"] = "Habla con Tran\'rek en Gadgetzan.",
    ["T"] = "Tran\'rek",
  },
  [2865] = {
    ["D"] = "Los escarabajos de Tanaris tienen caparazones muy duros. ¡Lo suficientemente duros como para usarlos como material de construcción para muchas cosas! ¡Tantas cosas!$B$BDe hecho, esos caparazones son tan útiles... ¡que los escarabajos fueron cazados casi hasta la extinción!$B$B¡Sé dónde hay más escarabajos, y si prometes traerme sus caparazones, te diré dónde están! ¿Prometes?$B$B¡Bien, los escarabajos tienen una colonia en Zul\'Farrak! Supongo que los trolls no los cazan por sus caparazones.$B$B¡Pero tú puedes! ¡Ve a Zul\'Farrak y consígueme caparazones sin grietas!",
    ["O"] = "Lleva 5 Caparazones de Escarabajo sin Romper a Tran\'rek en Gadgetzan.",
    ["T"] = "Caparazones de Escarabajo",
  },
  [2866] = {
    ["D"] = "Al sur de nosotros se encuentran las Ruinas de Solarsal, esta es un área que data de tiempos mucho más antiguos. Exploramos las ruinas inicialmente y no encontramos nada de interés, como tal, las dejamos en paz.$B$BRecientemente, los naga Cresta de Odio han ocupado estas ruinas en números alarmantes. Al principio, lo consideramos como el precursor de un ataque. Aunque esa amenaza sigue siendo posible, creemos que podría estar sucediendo algo más.$B$BVe allí, explora las ruinas y busca cualquier cosa que te llame la atención. Buena suerte, $n.",
    ["O"] = "Explora las Ruinas de Solarsal e investiga la presencia de los naga allí.",
    ["T"] = "Las Ruinas de Solarsal",
  },
  [2867] = {
    ["D"] = "Con el descubrimiento del extraño gazebo en el corazón de las Ruinas de Solarsal, ha llegado el momento de regresar al Bastión Plumaluna e informar a la General Shandris Plumaluna lo que se ha descubierto aquí.",
    ["O"] = "Regresa con Shandris Plumaluna en el Bastión Plumaluna e informa de tus hallazgos.",
    ["T"] = "Regreso al Bastión Plumaluna",
  },
  [2868] = "_",
  [2869] = {
    ["D"] = "El mejor curso de acción en este punto es asumir que, independientemente de lo que estén haciendo los naga, eventualmente se moverán para asegurar la isla para ellos. Necesitamos tomar medidas agresivas para contrarrestar esa amenanza antes de que sea demasiado tarde.$B$BTráeme diez Escamas Cresta de Odio que estén en buen estado, debería ser obvio para ti cómo adquirirlas. Cortarás tus dientes en los naga por primera vez, y tendremos una presencia debilitada de naga en la Isla Sardor, ambas perspectivas que juegan a nuestro favor.",
    ["O"] = "Lleva 10 Escamas de Naga Cresta de Odio a Latronicus Moonspear en el Bastión Plumaluna.",
    ["T"] = "Contra los Cresta de Odio",
  },
  [2870] = {
    ["D"] = "$n, nuevos informes de exploración nos han dado una buena idea de la cadena de mando de los naga. La realeza está presente entre sus filas, y son ellos quienes dan las órdenes.$B$BEspecíficamente, hemos identificado a uno de los miembros más crueles de la realeza como Lord Shalzaru. Los exploradores informan que él mismo ha descubierto una reliquia extraña durante una reciente excavación naga en una cueva al sur de las ruinas principales, al otro lado del agua en la Isla del Terror.$B$BPonle fin a esto acabando con él, y tráeme su reliquia para que podamos estudiarla.",
    ["O"] = "Elimina a Lord Shalzaru y luego lleva la Reliquia Misteriosa que descubrió a Latronicus Lunaspear en el Bastión Plumaluna.",
    ["T"] = "Contra el Señor Shalzaru",
  },
  [2871] = {
    ["D"] = "Si fueras tan amable de entregar la reliquia a mi esposa Vestia, te lo agradecería. Ella no solo es una excelente sacerdotisa para nuestra comunidad, sino que también es una de las eruditas más versadas que encontrarás en cualquier lugar. No podría darle sentido a este dispositivo incluso si hubiera una apuesta involucrada. Mi esposa, sin embargo, si alguien puede descubrir qué es esta cosa, ella podría... ¡y necesitamos saberlo!$B$BElla está justo a la vuelta de la esquina, sin duda estudiando. Gracias, $n.",
    ["O"] = "Entrega la Reliquia Misteriosa a Vestia Hablaluna en el Bastión Plumaluna.",
    ["T"] = "Entrega de la Reliquia",
  },
  [2872] = {
    ["D"] = "Mi viejo amigo Stoley me debe una botella de ron muy especial. Atiende el bar en el Puerto Bonvapor en Tanaris, Kalimdor, y me prometió que el ron de allí es más fuerte que cualquier cosa que podamos encontrar en Bahía del Botín. ¡Está tan seguro que dice que si pruebo ese ron y no estoy de acuerdo con él, entonces me duplicará el dinero que me debe!$B$BBueno, creo que no está tan seguro como cuando hablamos por última vez, y ahora tiene miedo de darme ese ron. Pero no me echo atrás en las apuestas, así que ¿por qué no hablas con él por mí?",
    ["O"] = "Habla con Stoley en Puerto Bonvapor.",
    ["T"] = "La Deuda de Stoley",
  },
  [2873] = {
    ["D"] = "Le debo a \"Lobo Marino\" MacKinley en Bahía del Botín algo de ron, para saldar una apuesta, ¡pero me he quedado sin ron! El Capitán Cuergo robó mi último cargamento, y lo tiene escondido en esa ciudad pirata amurallada al sur.$B$BSi puedes encontrar mi cargamento de ron y devolvérmelo, entonces podré demostrarle a MacKinley que el licor de Kalimdor supera con creces al de Bahía del Botín.",
    ["O"] = "Lleva el Envío de Stoley a Stoley en Puerto Bonvapor.",
    ["T"] = "El Envío de Stoley",
  },
  [2874] = {
    ["D"] = "Gracias de nuevo por tu ayuda, $N. Aquí tienes una botella de ron de sentina de los Catalejos. Llévasela a MacKinley y finalmente podrá dejar de lado sus locas creencias sobre el alcohol de Bahía del Botín.",
    ["O"] = "Lleva la Botella de Stoley a \"Lobo de Mar\" MacKinley en Bahía del Botín.",
    ["T"] = "Entregar a MacKinley",
  },
  [2875] = {
    ["D"] = "El vil pirata Andre Firebeard es buscado por crímenes contra Bonvapor y Gadgetzan.$B$BPor decreto del consejo gobernante, cualquiera que lea esto está autorizado a usar fuerza letal durante la captura de Andre y sus cómplices.$B$BAdemás, si Andre está muerto o de alguna manera incapacitado durante el momento de su audiencia, su cabeza puede actuar como sustituto, y la entrega de dicha cabeza al Jefe de Seguridad Bilgewhizzle constituye un contrato vinculante y canjeable.",
    ["O"] = "Lleva la Cabeza de Andre al Jefe de Seguridad Bilgewhizzle en Puerto Bonvapor.",
    ["T"] = "SE BUSCA: Andre Barbafuego",
  },
  [2876] = {
    ["D"] = "Este pergamino contiene los horarios de los envíos entre el Puerto Bonvapor y Bahía del Botín. Se incluyen tanto los envíos pasados como los futuros, junto con los nombres de los barcos, con grandes \"X\" junto a algunos viajes de barcos y anotaciones como \"GRAN BOTÍN\" y \"DURO COMBATE\" junto a otros.$B$BAlguien en el Puerto Bonvapor querría ver este documento.",
    ["O"] = "Informa de los Horarios de los Barcos a una autoridad en Puerto Bonvapor.",
    ["T"] = "Horarios de los Barcos",
  },
  [2877] = {
    ["D"] = "Nosotros los Yelmo Fiero recordamos el pasado, más que nuestros hermanos en Forjaz. Si quieres hacer amigos aquí, ¡tendrás que ensuciarte las manos por nosotros!$B$BLa Horda abandonó sus posiciones en las Tierras del Interior alrededor de Roca del Merodeador, ahora está infestado de asquerosos lodos y moco. Tenemos planes para esa área, pero primero necesitamos un equipo de limpieza para abrirnos paso.$B$B¡Ahí es donde entras tú, Sol! Ve allí y elimina diez lodos verdes y diez mocos de jade, luego informa aquí. ¡Muévete!",
    ["O"] = "Mata 10 Fangos Verdes y 10 Mocos de Jade, y luego informa a Fraggar Mantormenta en Pico Nidal.",
    ["T"] = "Limpieza de Roca Skulk",
  },
  [2878] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Flor de Canto Corrupta",
  },
  [2879] = {
    ["D"] = "Este báculo podría ser el Báculo de Equinex. ¡El Báculo de Equinex es en realidad una llave, utilizada para desbloquear el Monolito de Equinex en las Ruinas de Ravenwind, en el continente al oeste del Soto del Sueño.$B$BEncuentra las cuatro llamas que aún arden en esas ruinas: Samha, Imbel, Byltan y Lahassa. Recupera su esencia y luego, mientras estás junto al Monolito de Equinex, usa las esencias para energizar el báculo. Si esto es realmente el Báculo de Equinex, podrás desbloquear el Monolito y recolectar un artefacto sagrado de él.",
    ["O"] = "Energiza el Bastón de Troyas y encuentra el Monolito Equinex.",
    ["T"] = "El Bastón de Equinex",
  },
  [2880] = {
    ["D"] = "Entiende esto bien, $r: ¡NO somos parte de la Alianza! Los Martillo Salvaje somos independientes, confiamos en hechos y acciones para demostrar el valor de uno, ¡en lugar de las tonterías de aquellos que usan palabrería para reforzar sus actitudes de no hacer nada!$B$BSi estás buscando amigos aquí, ¡primero demuestra tu valía! Los malditos trolls que infestan las laderas de las Tierras del Interior a veces llevan collares tribales. Tráeme cinco de ellos y lo marcaremos como un paso en la dirección correcta para ti.",
    ["O"] = "Lleva 5 Collares Tribales de Troll a Fraggar Thundermantle en Pico Nidal.",
    ["T"] = "Recompensa por Collares de Troll",
  },
  [2881] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Recompensa por Collares de Troll",
  },
  [2882] = {
    ["D"] = "Este mapa muestra un tramo de playa con una cara sur. Debajo del mapa hay palabras, elegantemente escritas...$B$B$BAl sur de Wheedle$BAl sur de casa$BEncuentra un mástil y una bandera y huesos$BCava allí, si eres valiente$B¡Cava y reclama el oro de Cuergo!$BUna llave necesitarás y una llave verás$BLos hombres que enfrentes tomarán sangre como pago.",
    ["O"] = "¡Encuentra el oro de Cuergo!",
    ["T"] = "El Oro de Cuergo",
  },
  [2902] = {
    ["D"] = "Los gnolls no son las herramientas más afiladas del cobertizo, pero sin duda tendrán algún tipo de estrategia documentada sobre su actividad reciente. Si encontráramos esos planes, tendríamos una idea más clara de lo que los tiene tan alterados.$B$BQuiero que regreses a los Woodpaw y encuentres cualquier cosa que se parezca a sus planes de batalla. Tienen que tener algo, sus ataques han sido demasiado coordinados como para haber sido realizados sin ellos. Encuéntralos y regresa conmigo cuando los tengas.",
    ["O"] = "Encuentra los planes de batalla de los gnolls en algún lugar de los campamentos gnolls al sur del Campamento Mojache.",
    ["T"] = "Investigación de los Zarpaleña",
  },
  [2903] = {
    ["D"] = "Con los planes de batalla de los gnolls descubiertos, todo lo que queda es llevar los planes de vuelta a Hadoken Zancoveloz en el Campamento Mojache. Quizás él o alguien allí pueda darle más sentido a lo que los gnolls están tramando.",
    ["O"] = "Lleva los Planes de Batalla de los Zarpaleña a Hadoken Swiftstrider en Campamento Mojache.",
    ["T"] = "Los Planes de Batalla",
  },
  [2904] = {
    ["D"] = "<Kernobee gime.> ¿Qué... qué pasó?$B$BLa última cosa que recuerdo es ser capturado por un grupo de enanos Hierro Negro, golpeado hasta casi la muerte y luego arrojado aquí con estos sucios troggs.$B$B¡Sácame de aquí, $N!$B$BAquí está el plan: Llévame a la salida, si salimos vivos, ve a Bahía del Botín y dile a Scooty que fui a Ratchet para advertirle a Tinkerwiz sobre la participación de los Hierro Negro en la construcción de esa super máquina.$B$B¡Vamos!",
    ["O"] = "Escolta a Kernobee hasta la salida de la Ruta Mecánica y luego informa a Scooty en Bahía del Botín.",
    ["T"] = "Un Buen Lío",
  },
  [2922] = {
    ["D"] = "Cuando nuestra amada ciudad Gnomeregan cayó, una chispa rebelde debió haber sacudido a mi pobre creación, Techbot. Una vez me ayudó a mí y a mis asociados con sus innumerables artilugios y su ilimitado almacén de información, pero su cerebro positrónico se ha polarizado. Ahora es negatrónico... ¡y los comportamientos de Techbot están invertidos! ¡Vaga por Gnomeregan en un frenesí!$B$BPero tal vez pueda ser salvado. Tal vez, si puedes recuperar el núcleo de memoria de Techbot y traérmelo, pueda descubrir la causa de la polarización y arreglar a mi pobre Techbot.",
    ["O"] = "Lleva el Núcleo de Memoria de Tecnoide a Maestro Tragaldabas en la Instalación de Reclamación de Gnomeregan.",
    ["T"] = "¡Salvar el Cerebro de Techbot!",
  },
  [2923] = {
    ["D"] = "Tinkmaster Overspark está muy molesto por el destino de su creación mecánica, Techbot. Recuerdo a Techbot como una herramienta amable y útil para los gnomos, pero Overspark me dice que está mal funcionado.$B$BMuy triste. ¡Pero con suerte tal vez pueda ser reparado!$B$BHabla con Tinkmaster Overspark, en la Instalación de Reclamación de Gnomeregan en Dun Morogh. Él agradecería tu ayuda.",
    ["O"] = "Habla con el Maestro Tinker Overspark en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "Tinkmaster Overspark",
  },
  [2924] = {
    ["D"] = "Un componente mecánico casi universal, el artificial esencial, fue de gran utilidad para los ingenieros y herreros de Gnomeregan. Pero durante la frenética huida de nuestra ciudad, ¡nadie recordó llevarse estos dispositivos! Y necesito una gran cantidad de ellos para el trabajo que pronto emprenderé.$B$BPor favor, entra en Gnomeregan y consígueme algunos artificiales esenciales. Los encontrarás en las áreas más profundas de nuestra ciudad, guardados en contenedores llamados extrapoladores artificiales.$B$BGracias, $N, ¡y por favor, date prisa!",
    ["O"] = "Lleva 12 Artificiales Esenciales a Klockmort Spannerspan en la Instalación de Reclamación de Gnomeregan.",
    ["T"] = "Artificiales Esenciales",
  },
  [2925] = {
    ["D"] = "Un amigo gnomo, Klockmort Spannerspan, me envió un mensaje desde Forjaz. Me dice que necesita ayuda en una misión peligrosa pero esencial a Gnomeregan, la otrora gran ciudad gnoma ahora contaminada y llena de gnomos locos y sus dispositivos.$B$BDebería quedarme aquí, pero mi corazón anhela ayudar a mi amigo. Si puedes ir en mi lugar, te estaría muy agradecido.$B$BPuedes encontrar a Klockmort en la Instalación de Reclamación de Gnomeregan, en Dun Morogh.",
    ["O"] = "Habla con Klockmort Palancabielas en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "Esenciales de Klockmort",
  },
  [2926] = {
    ["D"] = "Desde el accidente, todo en lo que Gnoarn piensa o habla es en limpiar Gnomeregan de la infestación de troggs y encontrar una cura para la enfermedad que la plaga, $N.$B$BAfortunadamente, Razzle y yo estamos cerca de algún tipo de cura, o eso o la receta de un ron realmente potente. ¡No lo sabremos hasta que lo probemos! Naturalmente, para probar nuestra teoría, vamos a necesitar algunas cosas, principalmente, residuos de los troggs irradiados en Gnomeregan. Llena este frasco con el brillo verde y tráemelo.",
    ["O"] = "Usa el Frasco de Colección de Plomo Vacío en Invasores Irradiados o Saqueadores Irradiados para recolectar lluvia radiactiva. Una vez esté lleno, llévaselo a Ozzie Togglevolt en Kharanos.",
    ["T"] = "Gnogaine",
  },
  [2927] = {
    ["D"] = "¿No creerás que siempre fui calvo, verdad? ¡No, $gseñor:señora;! Fue la fuga de radiación en Gnomeregan lo que causó esta trágica pérdida de cabello. Antes de ese día, yo tenía el único afro gnomo en todo Azeroth.$B$B<Gnoarn sacude su puño en el aire.>$B$B¡Malditos troggs malditos y sucios, malditos sean todos!!$B$BQuizás no sea demasiado tarde, $N. Todavía podríamos salvarla, ¡me refiero a la ciudad! Habla con Ozzie Togglevolt en Kharanos. Él y su compañero Razzle están trabajando en una solución.",
    ["O"] = "Habla con Ozzie Voltiodo en Kharanos.",
    ["T"] = "El Día Después",
  },
  [2928] = {
    ["D"] = "Es terrible, $N. No vamos a tener suficientes piezas para llevar nuestra flota de excavadoras giroperforadoras a Dun Morogh.$B$BNecesitamos piezas de repuesto, $N. ¿Qué mejor lugar para conseguir las piezas de repuesto para nuestros vehículos que Gnomeregan? Escuché que los robots y los mechs deambulan por ese lugar como ganado en un campo abierto.$B$BLo que necesito que hagas es ir a Gnomeregan y desarmar esos robots. Tráeme sus entrañas de robot. Dos docenas deberían ser suficientes.",
    ["O"] = "Lleva veinticuatro Tripas Robo-mecánicas a Shoni en Ventormenta.",
    ["T"] = "Excavadores Girodrillmatic",
  },
  [2929] = {
    ["D"] = "Confié en Thermaplugg, $r. Nunca esperé que me traicionara a mí y a todo el pueblo gnomo. ¿Y para qué? ¿Poder? ¿Riqueza? Todo lo que habría tenido con el tiempo. Ahora hemos sido desplazados de nuestro hogar y ese loco está al mando. El rey de la nada....$B$BRecuperaremos Gnomeregan, $N. No nos detendremos hasta que la ciudad vuelva a estar bajo nuestro control. Si deseas unirte a nuestra lucha, te pido una simple tarea: Mata al traidor. Destruye al Mecánico maestro Thermaplugg.",
    ["O"] = "Viaja a Gnomeregan y mata al Mecánico Termochufe. Regresa con el Alto Tóquer Mekkatorque cuando hayas completado la tarea.",
    ["T"] = "La Gran Traición",
  },
  [2930] = {
    ["D"] = "Cuando huimos de Gnomeregan, ¡dejamos tantos datos atrás! ¡Datos de vital importancia!$B$BNecesito los datos de una tarjeta perforada prismática, de un terminal de alta seguridad Matrix Punchograph 3005-D, en las profundidades de Gnomeregan. Debes acceder al terminal, pero para hacerlo primero necesitas autorización... y para obtenerla debes acceder a terminales de menor seguridad: los modelos -A, -B y -C. Y para hacer ESO necesitas tarjetas de seguridad perforadas, comenzando con la blanca... y dejamos todas las tarjetas perforadas en la ciudad.$B$B¡Es una catástrofe! ¿Puedes ayudarnos?",
    ["O"] = "Lleva una Tarjeta Perforada Prismática al Maestro Mecánico Castpipe en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "Rescate de Datos",
  },
  [2931] = {
    ["D"] = "¡El Maestro Mecánico Castpipe en la Instalación de Reclamación de Gnomeregan necesita aventureros! Está organizando grupos para reingresar a Gnomeregan en una misión de recuperación de datos de máxima seguridad e importancia.$B$BCastpipe puede que no sea miembro de la Asamblea Iluminada de Arcanología, Alquimia y Ciencias de Ingeniería como yo, pero sigue siendo un gnomo inteligente, así que si él cree que es importante, ¡según mis cálculos hay un ochenta y cuatro por ciento de probabilidad de que realmente lo sea!$B$B¡Esas son buenas probabilidades!",
    ["O"] = "Habla con el Maestro Mecánico Castpipe en la Instalación de Reclamación de Gnomeregan en Dun Morogh.",
    ["T"] = "La Tarea de Castpipe",
  },
  [2932] = {
    ["D"] = "Los trolls Secacorteza alguna vez fueron aliados de la Horda, pero traicionaron a Thrall... ¡y eso debe ser castigado!$B$BAquí, toma mi pica y ve a las Tierras del Interior, muy al norte. Lo encontrarás al noreste de Molino Tarren, un pueblo en manos de nuestros aliados, los No-Muertos.$B$BCuando entres en las Tierras del Interior, caza trolls Secacorteza. Reúne sus cráneos y clava mi pica en el suelo en uno de sus pueblos más pequeños, Hiri\'watha o Zun\'watha.$B$B¡Muéstrales que sus acciones viles tienen consecuencias!",
    ["O"] = "Recoge Cráneos Secacorteza y colócalos en la Pica de Nimboya. Coloca la Pica Cargada de Nimboya en una de las Aldeas Secacorteza en las Tierras del Interior, luego regresa con Nimboya en Tuercespina.",
    ["T"] = "Mensaje Siniestro",
  },
  [2933] = {
    ["D"] = "Estas botellas contienen un veneno vil y verde.$B$BTeniendo en cuenta la cantidad de botellas en este pueblo, los Witherbarks deben considerarlo muy importante. Quizás un boticario en el cercano Molino Tarren quiera una muestra.",
    ["O"] = "Lleva una Botella de Veneno a un boticario en Molino Tarren.",
    ["T"] = "Botellas de Veneno",
  },
  [2934] = {
    ["D"] = "Necesito una muestra fresca del veneno que me trajiste. Las arañas de las que fue recolectado deben habitar en las Tierras del Interior, cázalas hasta que encuentres un saco de veneno intacto y tráemelo. Encontrarás muchas arañas cerca de la ciudad troll de Shadra\'Alor.$B$BNo te demores, $N, porque el saco de veneno debe estar fresco cuando me lo entregues.",
    ["O"] = "Lleva un Saco de Veneno Intacto al Boticario Lydon en Molino Tarren.",
    ["T"] = "Saco de Veneno Intacto",
  },
  [2935] = {
    ["D"] = "El veneno de los guardianes de la cría no era tan potente como el veneno que trajiste originalmente. Debe haber venido de una araña diferente.$B$BQuiero una muestra fresca de ese veneno. Para descubrir de dónde vino, debes consultar con un troll que conozca bien a los Secacorteza: el Maestro Gadrin en la Aldea Sen\'jin. Viaja a Durotar y habla con Gadrin. Con suerte, sabrá de dónde obtienen su veneno los Secacorteza.",
    ["O"] = "Habla con el Maestro Gadrin en la Aldea Sen\'jin.",
    ["T"] = "Consultar al Maestro Gadrin",
  },
  [2936] = {
    ["D"] = "Los Secacorteza adoran a un dios araña, y de ella obtienen su veneno más potente. Así que si quieres el veneno, ¡debes invocar a su dios!$B$BPara hacer eso, debes saber su verdadero nombre, y ese nombre está en una tableta enterrada en Tanaris, en la ciudad troll de Zul\'Farrak, en una tumba custodiada por Theka el Mártir.$B$BDerrota a Theka y encuentra y lee su tableta para descubrir el nombre del dios araña. Cuando lo sepas, regresa conmigo.",
    ["O"] = "Lee de la Tablilla de Theka para aprender el nombre del dios araña Secacorteza, luego regresa con el Maestro Gadrin.",
    ["T"] = "El Dios Araña",
  },
  [2937] = {
    ["D"] = "Toma su pergamino y léelo en el altar de Shadra\'Alor, en lo profundo del reino Witherbark en las Tierras del Interior. Esto invocará a Shadra, el dios araña.",
    ["O"] = "Derrota a Shadra en el altar de Shadra\'Alor, luego lleva su veneno al Boticario Lydon en Molino Tarren.",
    ["T"] = "Invocando a Shadra",
  },
  [2938] = {
    ["D"] = "El veneno que recolectaste del dios araña de los Secacorteza es un hallazgo bastante bueno. He podido matar a casi dos docenas de conejos con solo una gota de la sustancia. Claro, algunos de esos conejos eran muy jóvenes y lo suficientemente pequeños como para caber en la palma de la mano, así que estoy seguro de que su resistencia a las toxinas era baja, pero no se puede negar que este veneno es poderoso.$B$BMe gustaría enviar una muestra a mi superior, el Boticario Faranell en el Boticarium de Entrañas. Estoy seguro de que querría probarlo él mismo.",
    ["O"] = "Lleva el Paquete de Faranell al Maestro Boticario Faranell en Entrañas.",
    ["T"] = "Veneno para Entrañas",
  },
  [2939] = {
    ["D"] = "Aunque las ruinas de Feralas pueden ser bastante peligrosas, tienen mucho que contar sobre lo que ha sucedido aquí.$B$BBuscando entre los escombros al sur hace unos días, descubrí lo que parece ser un bastón normal. Sin embargo, no puedo quitarme la sensación de que hay algo más en él. Angelas y yo hemos estado revisando nuestros libros aquí, pero no encontramos nada al respecto.$B$BTengo una colega en Darnassus que podría decirnos qué es esto, $N. ¿Por qué no vas a hablar con ella y ves si puede ayudarnos?",
    ["O"] = "Habla con Daryn Brisaligera en la Aldea Rut\'theran.",
    ["T"] = "En Busca de Conocimiento",
  },
  [2940] = {
    ["D"] = "Este libro parece como si nadie lo hubiera abierto durante bastante tiempo. Sus cubiertas están bastante desgastadas y sus páginas amarillentas, pero después de examinarlo, te das cuenta de que podría ser justo lo que Troyas está buscando.$B$BLo recoges, pero te das cuenta de que probablemente deberías preguntar antes de tomarlo prestado.",
    ["O"] = "Pregunta a Daryn Brisaligera si puedes tomar prestado su libro.",
    ["T"] = "Feralas: Una Historia",
  },
  [2941] = {
    ["D"] = "He estudiado muchos temas en mi tiempo, y mi última fascinación es con los mordedores que ocupan la playa en las Tierras del Interior. Hay uno en particular que me gustaría ver, un mordedor gigante llamado Gammerita. Me gustaría ir yo mismo, pero mi investigación me mantiene aquí. Creo que una imagen de ella sería lo mejor.$B$BToma esta carta para Curgle Cranklehop en Tanaris. Ella ha creado un invento para mí que puede capturar una imagen. Lo llamó una \"instantánea\", creo...",
    ["O"] = "Lleva la carta a Curgle Cranklehop en Tanaris.",
    ["T"] = "El Prestatario",
  },
  [2942] = {
    ["D"] = "En el momento en que el artefacto es removido del Monolito, sientes que la energía en el Bastón de Equinex comienza a desvanecerse.",
    ["O"] = "Devuelve la Piedra Centelleante y el Bastón de Equinex a Troyas Brisaluna en el Bastión Plumaluna.",
    ["T"] = "La Piedra de Morrow",
  },
  [2943] = {
    ["D"] = "Aquí tienes, $N. Por favor, cuida mi libro.$B$BAhora, date prisa. Estoy seguro de que Troyas está ansioso por tu regreso.",
    ["O"] = "Entrega el libro a Troyas Brisaluna en el Bastión Plumaluna.",
    ["T"] = "Regreso con Troyas",
  },
  [2944] = {
    ["D"] = "Echa un vistazo a mi nuevo invento, $N. Todo lo que necesitas hacer es apuntar a lo que quieras fotografiar y presionar el botón.$B$B¿Qué era esa criatura de las Tierras del Interior que mencionaste? ¿Gammerita? Bueno, buena suerte encontrándola. Estoy seguro de que Daryn estará muy contenta con la instantánea que le traigas.$B$BEn cualquier caso, aquí tienes el Super Snapper. ¡Diviértete!",
    ["O"] = "Usa la Super Tortuga FX para tomar una instantánea de Gammerita, luego regresa con Daryn Lightwind en la Aldea Rut\'theran.",
    ["T"] = "El Super Capturador FX",
  },
  [2945] = {
    ["D"] = "Has descubierto lo que parece ser un anillo del cadáver de uno de los enanos Hierro Negro. Sin embargo, es difícil saber qué tipo de anillo es, la suciedad y los residuos endurecidos lo cubren por completo, haciendo que sea imposible usarlo. Crees que puedes distinguir el ligero brillo de un metal precioso y tal vez incluso el destello de una gema bajo los residuos.$B$BSi vas a hacer uso de este anillo, tendrás que encontrar algo para quitar los residuos endurecidos.",
    ["O"] = "Encuentra una manera de quitar la mugre del Anillo Cubierto de Mugre.",
    ["T"] = "Anillo Cubierto de Mugre",
  },
  [2946] = {
    ["D"] = "Queremos que lleves los discos en miniatura que encontraste a las puertas selladas de Uldum. Nosotros... no estamos seguros de qué pasará cuando lo hagas.$B$BEsto suena arriesgado, claro, lo reconocemos. Sin embargo, podría no hacer nada. Podrías necesitar usar los discos en algo. Quizás las puertas se desbloquean cuando tienes los discos en tu posesión. Estate abierto a intentar cualquier cosa y prepárate para que pase cualquier cosa.$B$BCuando hayas descubierto qué pasa, regresa a Forjaz. ¡Tendremos mucho que analizar cuando hayas terminado!",
    ["O"] = "Lleva los discos de platino en miniatura a las puertas de Uldum en Tanaris y busca pistas sobre la conexión entre los dos.",
    ["T"] = "Ver Qué Pasa",
  },
  [2947] = {
    ["D"] = "Abrir la caja producida por la Destellamática 5200 revela lo que sospechabas: ¡un anillo brillante! Al ponértelo, sientes que el poder mágico fluye por tu cuerpo.$B$BDentro de la banda hay una pequeña pero discernible impresión del sello de Forjaz. Junto a eso, hay tres pequeñas iniciales grabadas: \"TdK\". Esto puede significar las iniciales del dueño anterior, o quizás las iniciales del artesano del anillo. Quédate el anillo... o devuélveselo a esta persona. Decisiones, decisiones...",
    ["O"] = "Puedes quedarte con el anillo, o puedes encontrar a la persona responsable de la impresión y grabados en el interior de la banda.",
    ["T"] = "El Regreso del Anillo",
  },
  [2948] = {
    ["D"] = "Escucha $n, ese anillo es tuyo, claro... ¡pero puedo hacerte uno mejor! Ese diseño, como dije, es realmente simple. Ahora soy mucho más hábil, y por la módica suma de treinta monedas de plata lo mejoraré para ti. Bueno, un poco más de treinta, en realidad...$B$BEstoy en un aprieto en este momento, todo mi stock de materiales se ha secado. Si me traes una barra de plata y un ágata musgosa, agregaré un enrejado de plata y ágata al anillo que le dará un poco más de brillo.$B$B¡Confía en mí!",
    ["O"] = "Lleva el Anillo de Oro Brillante, una Barra de Plata, un Ágata Musgosa y 30 monedas de plata a Talvash del Kissel en Forjaz.",
    ["T"] = "Mejora de Gnomos",
  },
  [2949] = {
    ["D"] = "Abrir la caja producida por la Destellamática 5200 revela un anillo brillante. Al ponértelo, sientes que el poder mágico fluye por tu cuerpo.$B$BDentro de la banda hay una pequeña pero discernible impresión del sello de Orgrimmar. Hay letras grabadas junto a él, pero solo las primeras tres destacan incluso después de limpiarlas: \"NOG...\"$B$BEsto puede significar las iniciales del dueño anterior, o quizás las iniciales del artesano del anillo. Quédate el anillo... o encuentra a \"NOG\"; decisiones, decisiones...",
    ["O"] = "Puedes quedarte con el anillo, o puedes encontrar a la persona responsable de la impresión y grabados en el interior de la banda.",
    ["T"] = "El Regreso del Anillo",
  },
  [2950] = {
    ["D"] = "No, no, no—no estoy diciendo de repente que el anillo es mío. Normalmente no hago esto, pero tu información sobre los Hierro Negro fue muy útil. Mejoraré tu anillo para ti, mejor de lo que quien haya hecho esta cosita simple podría haberlo hecho. Probablemente algún gnomo...$B$BSi me traes una barra de plata, un ágata musgosa y treinta monedas de plata por mi molestia, agregaré un enrejado de plata y ágata al anillo que fortalecerá la magia dentro de él. ¿Interesado?",
    ["O"] = "Lleva el Anillo de Oro Brillante, una Barra de Plata, un Ágata Musgosa y 30 monedas de plata a Nogg en Orgrimmar.",
    ["T"] = "El Anillo de Nogg Redo",
  },
  [2951] = {
    ["D"] = "Este artilugio destaca entre los gnomos que resisten la locura fuera de la habitación. Una pequeña placa en el dispositivo dice \"El Sparklematic 5200\", seguido de algún texto:$B$B\"¿El polvo y los residuos están arruinando tus brillantes? ¡El Sparklematic 5200 devuelve el brillo a lo brillante! Inserta el objeto que deseas limpiar y deposita tres monedas de plata en la ranura. Dale al Sparklematic 5200 un momento para que opere, y ¡voilá! ¡Tus objetos valiosos saldrán limpios y brillantes!\"",
    ["O"] = "Inserta un Objeto Cubierto de Mugre en el Destellamatic 5200, y asegúrate de tener tres monedas de plata para iniciar la máquina.",
    ["T"] = "¡El Sparklematic 5200!",
  },
  [2952] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "¡El Sparklematic 5200!",
  },
  [2953] = {
    ["D"] = "",
    ["O"] = "",
    ["T"] = "Más Acción Sparklematic",
  },
  [2954] = {
    ["D"] = "Saludos, soy un guardián de la entrada. A menos que ya tengas las Placas de Uldum integradas con tu conjunto de discos, no permitiré la entrada al complejo de Uldum.",
    ["O"] = "Aprende el propósito del Vigía de Piedra de Norgannon, y luego interactúa con el Pedestal de Uldum en el desierto de Tanaris.",
    ["T"] = "El Vigilante de Piedra",
  },
  [2962] = {
    ["D"] = "No solo tu muestra fue insuficiente en cantidad, también era impura.$B$B Lo que necesitamos es más brillo verde. Claramente los troggs no están suficientemente irradiados para satisfacer nuestras necesidades. Tendrás que ir a Gnomeregan y traer algo de lluvia radiactiva, directo de la fuente. Hablo de los limos, merodeadores y horrores irradiados. Advertencia, contener este tipo de lluvia es difícil. Quizá necesites varios intentos con el frasco hasta obtener una muestra adecuada.",
    ["O"] = "Viaja a Gnomeregan y trae de vuelta Lluvia Radiactiva de Alta Potencia. Ten cuidado, la lluvia es inestable y colapsará rápidamente.$B$BOzzie también requerirá tu Frasco de Colección de Plomo Pesado cuando la tarea esté completa.",
    ["T"] = "La Única Cura es Más Resplandor Verde",
  },
  [2963] = {
    ["D"] = "Saludos una vez más, $n! Tu descubrimiento dentro de Uldaman ha puesto a la Liga de Expedicionarios en un estado de emoción. La Liga ha estado trabajando para desbloquear los secretos de los pequeños discos de platino que encontraste. ¡Tu afortunada llegada aquí al Salón me permite compartir contigo que posiblemente estamos en algo!$B$BEl Historiador Karnik ha descubierto algunas pistas importantes sobre los verdaderos orígenes del Valle de los Vigilantes en el desierto de Tanaris. ¡Le gustaría hablar contigo lo antes posible!",
    ["O"] = "Habla con el Historiador Karnik en Forjaz.",
    ["T"] = "Presagios de Uldum",
  },
  [2964] = {
    ["D"] = "Bueno $n... parece que estamos en un callejón sin salida temporal aquí.  Sabemos lo que necesitamos - las Planchas de Uldum - pero no tenemos idea de dónde podrían estar.  Tendremos que investigar bastante para idear un plan de ataque sólido.  Tengo un par de ideas por dónde empezar a buscar, tengo grandes esperanzas de que pronto te pediremos que salgas a recuperarlas.$B$BEl Alto Explorador necesita ser informado sobre esto, $n, sin duda, también quiere agradecerte personalmente por una vez más ayudar a la Liga.",
    ["O"] = "Habla con el Alto Explorador Magellas en Forjaz.",
    ["T"] = "Una Tarea Futura",
  },
  [2965] = {
    ["D"] = "Ah, $n, es fortuito que hayas venido a mí de nuevo. Tus revelaciones en el sitio de excavación de Uldaman ciertamente nos han dado mucho de qué hablar a los sabios. Estaba haciendo precisamente eso con una de las druidas, Nara Wildmane, cuando me di cuenta de que sus experiencias pasadas de aventuras podrían tener una gran importancia en cuanto al siguiente paso que debemos tomar.$B$BMe gustaría que hablaras con ella sobre sus viajes al Valle de los Vigías en el desierto de Tanaris. Ella te espera en el Alto de los Ancestros.",
    ["O"] = "Habla con Nara Crin Salvaje en Cima del Trueno.",
    ["T"] = "Presagios de Uldum",
  },
  [2966] = {
    ["D"] = "El Sabio Buscaverdad y yo queremos que lleves los discos miniatura que encontraste a las puertas selladas de Uldum. Nosotros... no estamos seguros de qué pasará cuando lo hagas.$B$BSuena arriesgado, claro, lo reconocemos. Sin embargo, podría no hacer nada. Quizás necesites usar los discos en algo. Tal vez las puertas se desbloquean cuando tienes los discos en tu posesión. Estate abierto a intentar cualquier cosa y prepárate para que pase cualquier cosa.$B$BAprende lo que puedas y luego regresa conmigo. Tendremos mucho que discutir.",
    ["O"] = "Lleva los discos de platino en miniatura a las puertas de Uldum en Tanaris y busca pistas sobre la conexión entre los dos.",
    ["T"] = "Ver Qué Pasa",
  },
  [2967] = {
    ["D"] = "Accedes al pedestal nuevamente, los discos de platino aparecen de repente en tu mochila.$B$BCon el conocimiento del vigía de piedra aprendido y los discos de vuelta en tu posesión, es hora de regresar a Cima del Trueno e informar a Nara Wildmane de tu descubrimiento.",
    ["O"] = "Habla con Nara Crin Salvaje en Cima del Trueno.",
    ["T"] = "Regreso a Cima del Trueno",
  },
  [2968] = {
    ["D"] = "Bueno $n... parece que estamos en un desafortunado impasse.  Sabemos lo que necesitamos - las Planchas de Uldum - pero no tenemos idea de dónde podrían estar.  Investigaremos esto, y luego idearemos un plan de ataque sólido.  Tengo un par de ideas por dónde empezar a buscar, tengo grandes esperanzas de que pronto te pediremos que te unas nuevamente a nuestros esfuerzos para descubrir la verdad.$B$BEl Sabio Buscador de la Verdad necesita ser informado sobre esto, $n, sin duda, también quiere agradecerte personalmente por una vez más atender el llamado.",
    ["O"] = "Habla con el Sabio Buscador de la Verdad en Cima del Trueno.",
    ["T"] = "Una Tarea Futura",
  },
  [2969] = {
    ["D"] = "El repugnante clan Tótem Siniestro está capturando dragones feéricos, $N. Jer\'kai y yo no tenemos idea de lo que planean hacer con ellos, pero obviamente no es nada bueno.$B$BNo somos lo suficientemente poderosos para abrirnos paso hasta el campamento y liberar a los dragones capturados, pero quizás con tu ayuda...$B$BSi estás dispuesto, y tienes algunos amigos fuertes, libera a las criaturas de su jaula con esta llave y asegúrate de que escapen con vida. Estaré a tu lado todo el tiempo. Jer\'kai permanecerá aquí.",
    ["O"] = "Usando la Llave de la Jaula de Bambú, libera a los Dracohechiceros del corral en el campamento de los Tótem Siniestro. Al menos 6 Dracohechiceros y Kindal Tejeluna deben sobrevivir.$B$BTienes 6 minutos antes de que Kindal decida que es hora de retirarse y fallen la misión.",
    ["T"] = "Libertad para Todas las Criaturas",
  },
  [2970] = {
    ["D"] = "Como ella ya te ha dicho, Kindal y yo llevaremos la lucha a los Totem Siniestro. Juntos, aún deberíamos poder eliminar a algunos de los tauren sin mucha amenaza para nosotros, pero nos encantaría un poco de ayuda si estás dispuesto y eres capaz.$B$BHas visto el campamento debajo de nosotros, pero hay más al este. Usando tus propias tácticas, causa tanto daño como puedas a los tauren. Podemos reunirnos aquí después de que hayan sentido el aguijón de las flechas de Kindal y tus propias armas.",
    ["O"] = "Mata 12 Naturalistas Tótem Siniestro, 10 Maleantes Tótem Siniestro y 6 Chamán Tótem Siniestro antes de regresar con Jer\'kai Tejeluna en Feralas.",
    ["T"] = "Repartiendo Justicia",
  },
  [2971] = "_",
  [2972] = {
    ["D"] = "Aquí en la naturaleza, Kindal y yo tenemos medios modestos para recompensar tu ayuda, pero no queremos que te sientas subestimado. Toma mi anillo de sello. Si alguna vez te encuentras en las ramas de Teldrassil, ve al Templo de la Luna en el corazón de Darnassus. Allí encontrarás a mi Suma Sacerdotisa Tyrande Susurravientos. El anillo le hará saber que estamos bien y dará crédito a tu historia.$B$BAmbos te agradecemos, $N.",
    ["O"] = "Lleva el Sello de Jer\'kai a la Suma Sacerdotisa Tyrande Susurravientos en Darnassus.",
    ["T"] = "Repartiendo Justicia",
  },
  [2973] = {
    ["D"] = "Bienvenido, $c. He viajado con mis compañeros hasta Feralas en una misión para nuestros amos de Orgrimmar.$B$BMi compañera Korrin encuentra gran placer en la ropa nueva, así que planeo hacerle una nueva capa adornada con las alas de los dragones feéricos. ¿Podrías ayudarme? Los destelladores con hermosas alas que quiero están al oeste de Campamento Mojache.",
    ["O"] = "Lleva 10 Alas de Draco Iris a Krueg Skullsplitter en Campamento Mojache.",
    ["T"] = "El Brillo de una Nueva Capa",
  },
  [2974] = {
    ["D"] = "¡¿Qué?! ¿Te encontraste con tauren en el área? ¡¿Estás bromeando?! ¿Cómo se veían?$B$B¡¿El clan Tótem Siniestro?! ¿Encontraste algunos de ellos? Belgrom, uno de nuestros maestros, tiene interés en su especie... incluso ha puesto una recompensa por ellos.$B$BTienes que volver, $c. Vuelve y tráeme sus cuernos... montones de ellos si puedes. Los Tótem Siniestro no son para tomarlos a la ligera o subestimarlos. Vuelve y mátalos, deja que los ríos en Feralas corran rojos con su sangre oscura.",
    ["O"] = "Lleva 20 Cuernos de Tótem Siniestro a Krueg Skullsplitter en Campamento Mojache.",
    ["T"] = "Un Descubrimiento Siniestro",
  },
  [2975] = {
    ["D"] = "Los efectos de la tribu ogra Gordunni difícilmente pasan desapercibidos en esta área. Mira a tu alrededor, $N, su mera presencia cicatriza esta tierra. Debemos rechazarlos antes de que se expandan aún más en Feralas.$B$BSigue el río hacia el norte y encontrarás su puesto avanzado. Reduce su número, $N. Debemos mostrarles que su clase no es bienvenida aquí.",
    ["O"] = "Rok Orhan en Campamento Mojache quiere que mates 10 Ogros Gordunni, 10 Magos Ogros Gordunni y 5 Brutos Gordunni.",
    ["T"] = "Los Ogros de Feralas",
  },
  [2976] = {
    ["D"] = "¡¿Qué?!$B$BAh, ¿así que mencioné una recompensa, no? Bueno, eso me enseñará a abrir la boca, ¿no?$B$BHiciste el trabajo, así que supongo que es justo que recibas la mayor recompensa. Toma los cuernos, los pondré en una caja para ti. Encuentra a Belgrom Machacaroca en el Valle del Honor, querrá saber lo que viste y te recompensará por tu arduo trabajo.$B$BQue te vaya bien, $c, y gracias de nuevo por todo lo que has hecho.",
    ["O"] = "Lleva el Crate de Cuernos de los Totem Siniestro a Belgrom Rockmaul en Orgrimmar.",
    ["T"] = "Un Descubrimiento Siniestro",
  },
  [2977] = {
    ["D"] = "Accedes al pedestal nuevamente, los discos de platino aparecen de repente en tu mochila.$B$BCon el conocimiento del vigía de piedra aprendido y los discos de vuelta en tu posesión, es hora de regresar a Forjaz e informar a la Liga de Expedicionarios de tu descubrimiento.",
    ["O"] = "Habla con el Historiador Karnik en Forjaz.",
    ["T"] = "Regreso a Forjaz",
  },
  [2978] = {
    ["D"] = "Al recoger el pergamino manchado, notas que parece representar algún tipo de reunión o ceremonia. Rok Orhan estaría interesado en este pergamino y en lo que podría significar para los Gordunni.",
    ["O"] = "Lleva el Pergamino Gordunni a Rok Orhan en Campamento Mojache.",
    ["T"] = "El Pergamino Gordunni",
  },
  [2979] = {
    ["D"] = "Estos parecen ser planes para algún tipo de ceremonia oscura, $N. Por lo que puedo entender, este ritual es realizado por los señores de la magia Gordunni.$B$BEncuentra a uno de estos señores de la magia y obtén un orbe de él.$B$BEl orbe de un mago contiene un gran poder y nos ayudará a descubrir más sobre esta ceremonia y la magia que los Gordunni manejan.",
    ["O"] = "Rok Orhan en Campamento Mojache quiere que encuentres un Orbe Gordunni.",
    ["T"] = "Ceremonia Oscura",
  },
  [2980] = {
    ["D"] = "Ahora que has demostrado que eres un verdadero luchador, confío en que posees el coraje para continuar con la tarea que tienes entre manos.$B$BViaja a la Alta Naturaleza, al suroeste de aquí. Allí yacen algunas estructuras abandonadas y destruidas, en tierras que una vez pertenecieron a los elfos nocturnos. Busca estas ruinas, y cuando veas los árboles marchitos y el terreno profanado, encontrarás a los Gordunni. Hazlos retroceder.$B$BEstos ogros son mucho más fuertes que los que enfrentaste anteriormente, $N. Ten cuidado.",
    ["O"] = "Rok Orhan en Campamento Mojache quiere que mates 10 Chamán Gordunni, 10 Brujos Gordunni y 5 Aplastadores Gordunni.",
    ["T"] = "Los Ogros de Feralas",
  },
  [2981] = {
    ["D"] = "Feralas es un lugar ideal para que un $c experimentado demuestre $gsu valía:su valía;. Una tribu de ogros llamada los Gordunni es la fuerza militar dominante allí, y sus números están creciendo. Deben ser detenidos antes de que amenacen nuestra presencia en Feralas.$B$BUna guerrera que conozco llamada Rok Orhan reside en el Campamento Mojache en Feralas, ve a ella. Ella te contará más sobre la situación cuando llegues.",
    ["O"] = "Belgrom Rockmaul quiere que hables con Rok Orhan en Campamento Mojache.",
    ["T"] = "Una Amenaza en Feralas",
  },
  [2982] = {
    ["D"] = "Aunque es peligroso, Troyas y yo hemos estado investigando las ruinas que cubren los bosques de Feralas.$B$BÚltimamente, sin embargo, Troyas ha estado yendo por su cuenta a la Alta Naturaleza, y realmente me preocupa. Los ogros Gordunni han tomado esa área, y no son amables con nadie que entre en su territorio.$B$BEsperaba que pudieras estar dispuesto a viajar a las ruinas al sureste y eliminar a algunos de los ogros. Hazlo un poco más seguro para mi Troyas, y estaré extremadamente agradecido.",
    ["O"] = "Angelas Moonbreeze quiere que mates a 8 Brujos Gordunni, 8 Chamán Gordunni y 8 Brutos Gordunni.",
    ["T"] = "Las Tierras Altas Salvajes",
  },
  [2983] = {
    ["D"] = "Ha pasado algún tiempo desde que fuiste probado por última vez, $N. Has sido paciente hasta ahora en tus estudios, pero es hora de que aprendas sobre poderes mayores... es hora de que aprendas el poder real. El elemento del fuego es tuyo si estás preparado para pagar por él.$B$BBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña morada al norte de El Cruce, pero ten cuidado, si has llegado a las tierras de los elfos nocturnos, has ido demasiado lejos.",
    ["O"] = "Encuentra a Kranal Fiss en Los Baldíos.",
    ["T"] = "La Llamada del Fuego",
  },
  [2984] = {
    ["D"] = "Hace mucho tiempo que no te ponen a prueba, $N. Has mostrado paciencia hasta ahora en tus estudios, pero ha llegado el momento de que aprendas más. Ha llegado el momento de que aprendas sobre la destrucción... y el caos. El elemento del fuego es tuyo para entenderlo, si estás preparado para pagar por él.$B$BBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña morada al norte de El Cruce, pero ten cuidado, si has llegado a las tierras de los elfos nocturnos, has ido demasiado lejos.",
    ["O"] = "Encuentra a Kranal Fiss en Los Baldíos.",
    ["T"] = "La Llamada del Fuego",
  },
  [2985] = {
    ["D"] = "Pareces listo para tu próxima prueba, $N. Has sido paciente en tus estudios, pero ha llegado el momento de que aprendas más. Ha llegado el momento de que aprendas sobre la pureza del agua.$B$BBusca a Islen Waterseer en Los Baldíos. Está a lo largo de la costa este, al sur del Río Furia del Sur y Ratchet. La encontrarás allí en su cabaña de pesca. Buena suerte, $N.",
    ["O"] = "Encuentra a Islen Vidente de las Aguas en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [2986] = {
    ["D"] = "Hace mucho tiempo que no te ponen a prueba, $N. Has mostrado paciencia hasta ahora en tus estudios, pero ha llegado el momento de que aprendas más. Ha llegado el momento de que aprendas sobre la pureza del agua.$B$BBusca a Islen Waterseer en Los Baldíos. Está a lo largo de la costa este, al sur del Río Furia del Sur y Ratchet. La encontrarás allí en su cabaña de pesca. Buena suerte, $N.",
    ["O"] = "Encuentra a Islen Vidente de las Aguas en Los Baldíos.",
    ["T"] = "La Llamada del Agua",
  },
  [2987] = {
    ["D"] = "Acabo de llegar aquí a Feralas, y he escuchado que los ogros Gordunni que habitan estas tierras salvajes tienen algunos minerales interesantes. ¿Quién sabe qué están tramando, pero seguro que son protectores de ese material! No lo dejan tirado por ahí, no, lo entierran para proteger a gente como yo de ponerle las manos encima.$B$BSi estás dispuesto, te daré mi pala. Ve al Puesto Gordunni al norte de aquí y cava un poco de cobalto Gordunni. ¡Busca el brillo azul y luego comienza a cavar!",
    ["O"] = "Orwin Gizzmick en Campamento Mojache quiere 12 muestras de Cobalto Gordunni.",
    ["T"] = "Cobalto Gordunni",
  },
  [2988] = {
    ["D"] = "¡El hijo de nuestro grifo más poderoso ha desaparecido! El heredero de Alaveloz, Picofino, se perdió mientras cazaba con sus padres hace unos días, y creemos que fueron los trolls quienes lo tomaron.$B$B¡Debes ayudarnos! Busca en las jaulas de las dos aldeas troll Secacorteza para ver si Picofino está allí.",
    ["O"] = "Revisa las jaulas en las dos aldeas Secacorteza, luego regresa con el Maestro de grifos Hachax.",
    ["T"] = "Jaulas de los Secacorteza",
  },
  [2989] = {
    ["D"] = "Sharpbeak no estaba con los trolls Witherbark. Quizás los trolls Vilebranch se lo llevaron. Ve al Altar de Zul en el borde occidental del territorio Vilebranch y busca al pequeño grifo.",
    ["O"] = "Revisa la cima del Altar de Zul, luego regresa con el Maestro de grifos Hachax.",
    ["T"] = "El Altar de Zul",
  },
  [2990] = {
    ["D"] = "Llévale esta pluma a Thadius Grimshade, el adivino. Dejó el Pico Nidal hace años para perseguir conocimientos más oscuros, pero en su corazón sigue siendo un Martillo Salvaje!$B$BY quizás sus talentos puedan ayudarnos a encontrar Picoafilado.$B$BEsta pluma fue tomada del nido donde duerme el niño grifo. Si se la das a Thadius, con suerte, podrá usar sus magias de adivinación para encontrar a Picoafilado.$B$BBuena suerte, $N. Encontrarás a Thadius en las Tierras Devastadas, en la torre de magos de la Fortaleza de Nethergarde.",
    ["O"] = "Lleva la Pluma de Picofino a Thadius Grimshade en las Tierras Devastadas.",
    ["T"] = "Thadius Grimshade",
  },
  [2991] = {
    ["D"] = "Averiguaré dónde está tu grifo, pero primero tienes que hacer algo por mí. Hay un troll en Zul\'Farrak, Nekrum Cometripas, que posee un medallón que me gustaría mucho estudiar. Tráeme ese Medallón y usaré mis poderes para encontrar Afilapico.$B$BZul\'Farrak está al otro lado del océano hacia el oeste, en el desierto de Tanaris.",
    ["O"] = "Lleva el Medallón de Nekrum a Thadius Grimshade en las Tierras Devastadas.",
    ["T"] = "El Medallón de Nekrum",
  },
  [2992] = {
    ["D"] = "Dame espacio mientras realizo un ritual de adivinación. Encontraré la conexión entre la pluma que me trajiste y su dueño, y seguiré esa conexión hasta el propio Picoafilado.$B$BEs decir... lo haré si todo sale según el plan. Si no es así, entonces... mejor cierra los ojos.",
    ["O"] = "Espera hasta que Grimshade realice el ritual de adivinación.",
    ["T"] = "La Adivinación",
  },
  [2993] = {
    ["D"] = "Sharpbeak está encerrado en una jaula, en lo alto de un acantilado en la cima de Jintha\'Alor en las Tierras del Interior. La llave de esta jaula la posee la suma sacerdotisa de los trolls Vilebranch, la Sacerdotisa Vile Hexx.$B$BAhí. He hecho mi parte. Te deseo suerte en tu búsqueda, $N.",
    ["O"] = "Habla con el Maestro de Grifos Hachatalón dentro del Aviario de Pico Nidal.",
    ["T"] = "Regreso a las Tierras del Interior",
  },
  [2994] = {
    ["D"] = "¡Debemos salvar a Picoagudo!$B$B¡Lucha hasta la cima de Jintha\'Alor en el corazón del territorio Branquiavil, obtén la llave de la jaula de Picoagudo de la vil Sacerdotisa Hexx, y luego encuentra y salva a Picoagudo!",
    ["O"] = "Lucha hasta la cima de Jintha\'Alor, mata a la Vil Sacerdotisa Hexx por la llave de la jaula de Picoafilado y salva a Picoafilado.",
    ["T"] = "Salvando a Picofino",
  },
  [2995] = {
    ["D"] = "El Refugio Quel\'Danil es un centro de comunicación para los altos elfos de las Tierras del Interior. Sus embajadores están trabajando para establecer relaciones con otras razas, con quienes comparten su conocimiento sobre los trolls Secacorteza que habitan en el área sur de las Tierras del Interior. Todos los documentos relacionados con las comunicaciones se guardan en el refugio.$B$BNo necesitamos que los Alto Valle formen alianzas con otros, $N.$B$BDestruye los documentos de Alto Valle. Necesitamos quemar sus líneas de comunicación... literalmente.",
    ["O"] = "Oran Serpescrita en Entrañas quiere que viajes a las Tierras del Interior y quemes los Registros de Vallealto, el Informe de Vallealto y las Notas de Vallealto.",
    ["T"] = "Líneas de Comunicación",
  },
  [2996] = {
    ["D"] = "Te he enseñado habilidades en el pasado, pero ha llegado el momento de que otro te enseñe. Está en Ratchet, y su nombre es Strahad. Es humano, pero no dejes que eso te engañe haciéndote pensar que es débil. Es poderoso, aunque pueda parecer pequeño. Puede enseñarte sobre invocaciones más poderosas, siento que estás listo. Ve a él ahora. Está en Ratchet... sobre el pueblo portuario, junto a su torre.",
    ["O"] = "Habla con Strahad Farsan en Ratchet.",
    ["T"] = "Buscando a Strahad",
  },
  [2997] = {
    ["D"] = "Hola, $N. Sé que estás ocupado ayudando a la gente de Dun Morogh, pero cuando tengas tiempo, deberías dirigirte a Forjaz para hablar con uno de los nuestros más respetados: Tiza Forjabatalla. Ella es a quien los jóvenes paladinos acuden cuando es hora de aprender más sobre la Luz y lo que se espera de ti.$B$BPuedes encontrarla arriba en el Salón de los Misterios. Si nunca has estado allí, está en el Distrito Místico. Buena suerte, $N.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "Tomo de Divinidad",
  },
  [2998] = {
    ["D"] = "Saludos, $Ghermano:hermana;. Duthorian Rall, uno de nuestros paladines más influyentes, estaba hablando de ti recientemente. Lo escuché decir que era hora de que te pusieran a prueba... junto con algunas cosas muy halagadoras sobre lo bien que te has desempeñado hasta ahora.$B$BSi yo fuera tú, iría a Ventormenta y hablaría con él cuando tengas tiempo. Estoy seguro de que está en la Catedral de la Luz, donde pasa la mayor parte de su tiempo.",
    ["O"] = "Habla con Duthorian Rall en Ventormenta.",
    ["T"] = "Tomo de Divinidad",
  },
  [2999] = {
    ["D"] = "Hola, $N. Sé que estás ocupado ayudando a la gente de Dun Morogh, pero cuando tengas tiempo, deberías subir y hablar con uno de los nuestros más respetados: Tiza Forjabatalla. Ella es a quien los jóvenes paladinos acuden cuando es hora de aprender más sobre la Luz y lo que se espera de ti.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "Tomo de Divinidad",
  },
  [3000] = {
    ["D"] = "Hola, $N. Sé que estás ocupado ayudando a la gente de Azeroth, pero cuando tengas tiempo, deberías regresar a Forjaz y hablar con una de las paladinas más respetadas de tu hogar: Tiza Forjabatalla. Ella es a quien los jóvenes paladinos enanos acuden cuando es hora de aprender más sobre la Luz y lo que se espera de todos nosotros.$B$BPuedes encontrarla arriba en el Salón de los Misterios, ubicado en el Distrito Místico.",
    ["O"] = "Habla con Tiza Forjabatalla en Forjaz.",
    ["T"] = "Tomo de Divinidad",
  },
  [3001] = {
    ["D"] = "Te he enseñado habilidades en el pasado, pero ha llegado el momento de que otro te enseñe. Está en Ratchet, y su nombre es Strahad. Es humano, pero no dejes que eso te engañe haciéndote pensar que es débil, ni que es tu enemigo... al menos por ahora. Es poderoso y debe ser respetado. Puede enseñarte sobre invocaciones más poderosas, invocaciones que siento que estás preparado para aprender. Ve a él ahora.$B$BPuedes encontrarlo en Ratchet... sobre el pueblo portuario, junto a la torre allí.",
    ["O"] = "Encuentra a Strahad Farsan en Ratchet.",
    ["T"] = "Buscando a Strahad",
  },
  [3002] = {
    ["D"] = "Aunque conozco bien a mi enemigo, soy un guerrero, $N. No puedo comenzar a decirte de qué es capaz este orbe.$B$BConozco a un mago en Orgrimmar que podrá decirnos más sobre el orbe. Llévaselo.",
    ["O"] = "Lleva el Orbe Gordunni a Uthel\'nay en la Cabaña Darkbriar en Orgrimmar.",
    ["T"] = "El Orbe Gordunni",
  },
  [3022] = {
    ["D"] = "$N, puedo ver que anhelas dedicarte a mi causa valiosa. Sí, por supuesto, ¡puedes hacer algo por mí!$B$BLleva este cajón de huevos a Erelas Ambersky. Es un druida que cuida de estos huevos, ayudándolos a eclosionar y luego entrenando a los jóvenes hipogrifos. Lo encontrarás en Teldrassil, en un pequeño pueblo al sur llamado Rut\'theran.",
    ["O"] = "Lleva el Crate Empacado con Cuidado a Erelas Ambersky en la Aldea Rut\'theran.",
    ["T"] = "Manejar con Cuidado",
  },
  [3042] = {
    ["D"] = "Los trolls Arenafuria de Zul\'Farrak hacen un agente de templado a partir de areniscas, y puedo usar eso para templar los bienes que elaboro, ¡sí que puedo! Es muy apreciado por los conocedores, así que si me traes un buen suministro del templado, ¡te recompensaré bien!$B$BZul\'Farrak está al noroeste de Gadgetzan. ¡Buena suerte!",
    ["O"] = "Lleva 20 Viales de Temperamento de Troll a Trenton Lighthammer en Gadgetzan.",
    ["T"] = "Temperamento de Troll",
  },
  [3062] = {
    ["D"] = "Edana Garramelena es la reina de las arpías de Fuente del Norte que residen en las ruinas al oeste. Ella es pura maldad, he escuchado que su corazón es de cristal. No tiene más emoción que el odio que siente hacia todos los demás. Debes enfrentarte a Edana y traerme su oscuro corazón.$B$BHe escuchado de un cuerno que llevan las arpías: tocarlo cerca de las piedras Garramelena, en la parte norte de las ruinas, convocará a la reina.$B$BImagina su sorpresa cuando no sea su familia quien la llame, sino su muerte.",
    ["O"] = "Talo Pezuña de Espina en el Campamento Mojache quiere el Corazón Oscuro de Edana Garraodio.",
    ["T"] = "Corazón Oscuro",
  },
  [3063] = {
    ["D"] = "En mis días, yo era un cazador experimentado. No había ninguna criatura a la que temiera. Hasta que...$B$BEstaba en el bosque, afilando mi espada después de una dura batalla, cuando fui emboscado. Las arpías me rodearon, y apenas logré arrastrarme a un lugar seguro.$B$BAún ahora, todavía puedo escuchar sus gritos resonando...$B$BNunca fui el mismo después de eso. Pero el odio hierve dentro de mí—debo vengarme, después de todos estos años.$B$BLas arpías de Fuente del Norte se encuentran al oeste de aquí. $N, ¡haz que se haga justicia!",
    ["O"] = "Talo Pezuña de Espina quiere que mates a 4 Arpías de Manantial Norte, 4 Plumavil de Manantial Norte, 4 Clamanorte de Manantial Norte y 4 Asesinos de Manantial Norte.",
    ["T"] = "Venganza en los Vientonegro",
  },
  [3064] = "_",
