  [6721] = {
    ["D"] = "Olá, jovem caçador. Você me parece habilidoso o suficiente para lidar com uma fera e treiná-la como sua companheira.$B$BVocê deve ver Dazalar em Dolanaar. Ele pode colocá-lo no caminho para ganhar seu próprio animal de estimação.",
    ["O"] = "Fale com Dazalar em Dolanaar.",
    ["T"] = "O Caminho do Caçador",
  },
  [6722] = {
    ["D"] = "Olá, jovem caçador. Você me parece habilidoso o suficiente para lidar com uma fera e treiná-la como sua companheira.$B$BVocê deve ver Dazalar em Dolanaar. Ele pode colocá-lo no caminho para ganhar seu próprio animal de estimação.",
