  [8446] = {
    ["D"] = "A corrupção escoa deste objeto.$B$BPode haver uma ligação entre este objeto e os dragões vindos do Sonho. Você deve encontrar alguém que possa decifrar o significado deste item. Talvez os druidas da Clareira da Lua ou da Fortaleza Cenariana possam ajudá-lo ainda mais.",
    ["O"] = "Encontre alguém capaz de decifrar o significado por trás do Objeto Engolido pelo Pesadelo.$B$BTalvez um druida de grande poder possa ajudá-lo.",
    ["T"] = "Envolto em pesadelo",
  },
  [8447] = {
    ["D"] = "As águas do Lago Elune\'ara deverão dissolver os pesadelos que envolvem este objeto. Dê-me um momento para realizar a limpeza, $N.",
    ["O"] = "Espere pelo Guardião Remulos na Clareira da Lua para limpar o Objeto Engolido pelo Pesadelo.",
    ["T"] = "Despertando Lendas",
